/*Table structure for table `comment` */

DROP TABLE IF EXISTS `comment`;

CREATE TABLE `comment` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `content` varchar(256) DEFAULT NULL COMMENT '评论内容',
  `img` varchar(256) DEFAULT NULL COMMENT '图片',
  `ufrom` int(11) DEFAULT NULL COMMENT '评论者id',
  `uto` int(11) DEFAULT NULL COMMENT '被评论者id，（回复才有）',
  `newsid` int(11) DEFAULT NULL COMMENT '评论针对的新闻id',
  `upvote` int(11) DEFAULT NULL COMMENT '点赞数量',
  `time` timestamp NULL DEFAULT NULL COMMENT '评论时间',
  `parentid` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=111 DEFAULT CHARSET=utf8;

