/*Table structure for table `news` */

DROP TABLE IF EXISTS `news`;

CREATE TABLE `news` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) DEFAULT NULL COMMENT '标题',
  `editor` varchar(128) DEFAULT NULL COMMENT '编辑',
  `source` varchar(255) DEFAULT NULL COMMENT '来源',
  `detail` text COMMENT '详情',
  `imgsrc` varchar(200) DEFAULT NULL COMMENT '图片地址(0表示没有)',
  `videosrc` varchar(200) DEFAULT NULL COMMENT '视频地址(0表示没有)',
  `browsenum` int(11) DEFAULT NULL COMMENT '浏览量',
  `time` timestamp NULL DEFAULT NULL COMMENT '时间',
  `modifiedtime` timestamp NULL DEFAULT NULL COMMENT '最后修改时间',
  `type` varchar(128) DEFAULT NULL COMMENT '类型',
  `isdelete` int(2) DEFAULT NULL COMMENT '是否删除',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3179 DEFAULT CHARSET=utf8;

/*Data for the table `news` */

insert  into `news`(`id`,`title`,`editor`,`source`,`detail`,`imgsrc`,`videosrc`,`browsenum`,`time`,`modifiedtime`,`type`,`isdelete`) values (2840,'浙江省长袁家军：浙江要努力打造新经济大省','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>杭州12月16日电 (黄慧)“浙江要力争在互联网，物联网大数据人工智能等领域成为领跑者，打造以‘一实、两集群、两中心’为主体的新经济大省。”16日，在首届钱塘江论坛上，浙江省长袁家军表示，浙江要打造新经济为引领的现代化经济体系，努力打造新经济大省。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　据袁家军介绍，“一实”指的是之江实验室，“两集群”是世界级产业，先进制造业集群和世界级港口集群，“两中心”是新型贸易中心和新兴金融中心。</p>\n<p>　　袁家军表示，浙江将努力打造新兴金融中心，充分发挥杭州移动支付之城，网商银行等互联网金融，泰隆银行等小微金融，温州民商银行等民营金融，玉皇山南基金小镇等金融特色小镇，宁波国家保险综合创新试点等诸多优势，探索建立无现金浙江，加快建设网络金融安全中心，网络金融产业中心，移动支付中心，保险创新中心与一体化新金融中心。</p>\n<p>　　据了解，本次论坛的宗旨是新金融，新科技，新经济。在袁家军看来，新金融、新经济、新科技正在深刻影响全球经济格局，也已成为中国发展的新动能。他认为去年中国电子商务交易额达3.92万亿美元，年均增长30%，占全球的39.2%；移动支付交易规模超12.2万亿美元，居全球之首；今年双十一全球购物狂欢节阿里巴巴天猫平台销售额超过254亿美元等表现，都彰显着中国新经济新动能。</p>\n<p>　　袁家军进一步指出，新金融、新经济、新科技更是浙江发展的新亮点。“中国的四大发明‘高铁、网购、支付宝、共享单车’中网购和支付宝都是在浙江这块热土诞生。”他表示，云上银行，无人超市，移动支付，互联网法院等新技术、新业态、新模式正在大量涌现，浙江移动支付总额及人均支付均额均居全国第二位，全国电子商务百强县和淘宝村的数量均居全国第一位，去年浙江全省淘宝村销售总额超47亿美元。</p>\n<p>　　据袁家军介绍，近年来浙江省把金融产业作为引领未来的八大万亿产业之一，扎实推进金融改革创新，金融强省建设迈上新台阶。“正是得益于金融的强力支撑，浙江的经济保持持续健康的发展态势，形成动力转化，结构优化，质量提升的良好局面。”</p>\n<p>　　谈及浙江省金融改革的发展，袁家军表示主要围绕四方面展开。第一是全面深化金融改革，大力推动地方金融改革。“通过深入推进温州金融综合改革，台州小微金改，宁波保险综合改革，义乌国际贸易金改，衢州、湖州绿色金改等全国金融改革试点，创造了许多浙江经验，实现了许多全国率先和全国首创。”袁家军表示，浙江在全国区域金改试点项目最多，是改革创新最活跃的地区之一。</p>\n<p>　　“浙江省金融改革发展坚持服务实体经济，牢牢把握服务实体经济的金融本质。”他表示，在保证这个第二点外，第三还要确保金融安全稳定，切实提升金融风险的防控能力。</p>\n<p>　　此外，袁家军表示还要大力推动战略的落地。“当前我们浙江省正在大力推进大湾区、大花园、大通道和大都市区四大建设，以及浙江自贸试验区的建设。”</p>\n<p>　　“大湾区的主体是杭州湾经济区，将建成为全国现代化建设新样板，全球新金融革命的重要策源地，长三角区域创新发展的新引擎，到2035年建成世界化、现代化、世界级大湾区。”袁家军如是展望大湾区的未来。</p>\n<p>　　对于大花园，他认为这是浙江的底色，目标是把浙西南生态旅游带打造成为诗画浙江的鲜活样板，把全省建设成为全国的大花园。而大通道的建设要联动推进海港、空港、路港、信息港四港建设。大都市区则是指杭州、宁波、温州和金华义乌四大都市区，需重点提高城市人居环境水平、城市功能和智慧化治理水平及国际化水平，提升人才聚集能力以及提升城市的影响力。</p>\n<p>　　而对于舟山自贸区，他认为核心任务就是构建油品全产业链，打造世界级“一中心、三基地、一示范区”，即国际油品交易中心，国际海市服务基地，国际油品储运基地，国际石化基地和大宗商品跨境贸易人民币国际示范区。</p>\n<p>　　“希望我们以钱塘江金融港湾为新的起点，紧扣新时代，抢抓新机遇，培育新动能，携手高水平打造现代化经济体系。”袁家军最后表示。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1791,'2017-12-16 20:55:00','2017-12-17 01:15:26','66',0),(2841,'2017国际智能网联汽车大会在溧水产业新城召开','陈海峰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月15日电 12月14日-15日，由中国汽车工程研究院股份有限公司主办，华夏幸福基业股份有限公司协办的“2017国际智能网联汽车大会”在溧水产业新城召开。中国汽车工程研究院与华夏幸福签署战略合作协议，双方将在智能网联汽车领域展开深度合作。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p><strong>　　打造我国最有影响力的全球化智能网联汽车年度会议品牌</strong></p>\n<p>　　作为实施“中国制造2025”的重要举措，智能网联汽车发展是国家制造强国战略的重要领航工程，将为中国建设制造强国、实现“智能制造”提供强有力的战略支撑。智能网联汽车的开发与应用将成为主要汽车国家解决道路交通安全、环境和效率的重要路径，在将来实现智能化升级、提高汽车的生态发展水平及相关行业过程中起到重要作用。</p>\n<p>　　会议以“智行天下，网联未来”为主题，包括华夏幸福基业股份有限公司副总裁顾强、中国汽车工程研究院副院长谢飞、广汽集团汽车工程研究院副院长查鸿山、滴滴智慧交通首席科学家Henry Liu、博世自动驾驶兼前瞻技术研究总监BONHMME Renaud、沃尔沃中国主动安全和自动驾驶高级精力Gavin Zhang等在内的众多海内外知名学者、专家及企业决策者，围绕智能网联汽车产业发展的现状、前景及痛点，展开观点交流和技术分享。</p>\n<p>　　会上，中国汽车工程研究院与华夏幸福签署战略合作协议，双方将整合各自优势资源，面向智能网联汽车开展深度合作，重点围绕行业研究规划、产业联盟构建、产业集群打造等三大领域推进战略合作。</p>\n<p>　　此外，主办方围绕智能网联汽车产业开展了“智能网联汽车技术发展与试验示范”“智慧交通与产业发展”分论坛，主办方致力将“国际智能网联汽车大会”打造成为我国智能网联汽车年度最盛大、最权威的会议，推动行业发展。</p>\n<p><strong>　　溧水产业新城致力成为一流的智能网联汽车产业创新基地</strong></p>\n<p>　　华夏幸福基业股份有限公司副总裁顾强向与会嘉宾介绍了此次会议举办地溧水产业新城在打造智能网联汽车产业基地方面的战略布局。2016年3月，华夏幸福与南京市溧水区政府及溧水经开区开展战略合作规划，以智能网联汽车为重点方向，致力将溧水产业新城打造成为智能网联汽车技术研发和孵化转化高地、智能网联汽车专业测试和开放试验高地、智能网联汽车国际合作高地，成为国际知名、国内一流的智能网联汽车产业创新基地。</p>\n<p>　　据了解，溧水新能源汽车基础雄厚、产业生态完善，聚集了多家大型整车制造企业，集中全省新能源客车80%整车产能和70%以上零部件生产能力。为顺应全球汽车电动化、智能化、网联化发展趋势，依托江苏省汽车产业基础和人才优势，溧水产业新城以智能网联汽车生态建设为切入点和突破口，并设定了未来十年的奋斗目标：</p>\n<p>　　一是打通创新链，突破智能网联汽车关键核心技术。掌握关键核心技术，打造复杂工况下的自动驾驶和无人驾驶环境，建成智能网联汽车自主创新体系。</p>\n<p>　　二是布局产业链，打造高能级产业体系。围绕智能网联汽车和智慧道路设施两个重点方向，促进红外传感系统、车载激光雷达、高精度地图、车载视觉系统等关键技术产业化，实现芯片、操作系统、先进传感器及配套软件开发的工程化和产业化应用，加快完善产业链布局和产业集聚发展。</p>\n<p>　　三是开放资源链，构建开放合作生态体系。加快频谱资源开放使用，争取V2X专用无线电频谱分配支持，探索合理的高精度地图使用方式方法。搭建通信网、物联网(含位置网)、智慧路网三个基础网，建成智能网联测试和示范应用区，建成国家级智能网联汽车与智慧交通的技术测试和产品认证平台，参与国家智能网联汽车相关标准的制订。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,529,'2017-12-15 16:47:00','2017-12-17 01:15:26','72',0),(2842,'台男子涉非法猎捕台湾长鬃山羊 警方查获冷冻肉块','吉翔','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月16日电 据台湾《联合报》报道，台湾嘉义县一名黄姓男子涉嫌在山区放捕兽夹，非法猎捕保育类野生动物台湾长鬃山羊，日前被警方循线逮捕，并在其住处找到冷冻肉块。但黄辩称是野猪肉，经送验后确认为台湾长鬃山羊肉，已被函送法办。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　台湾长鬃山羊，又称台湾鬣羚、台湾野山羊、台湾羚羊，是台湾特有物种，虽然称羊却是台湾唯一野生牛科动物。原生长在台湾高海拔山区，但921大地震后疑因山区林相改变、影响栖息环境，近来传闻台湾长鬃山羊常出现在低海拔浅山觅食。</p>\n<p>　　保七总队第七大队日前接获情资，在嘉义县梅山乡瑞峰村一带，有人放捕兽夹非法猎捕台湾长鬃山羊。警察上月中旬会同县府农业处绿保课稽查员执行联合稽查多次，终于查获黄男非法冰藏台湾野山羊屠体，并查获多具大型捕兽器，但黄姓男子辩称冷冻的是野猪肉。</p>\n<p>　　警方立即将冷冻屠体送“农业委员会”特有生物研究保育中心鉴定，15日确认为“二级保育类野生动物长鬃山羊”，16日依违反“野生动物保育法”移送法办。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,754,'2017-12-16 14:52:00','2017-12-17 01:15:26','70',0),(2843,'习近平总书记在江苏徐州市考察引发强烈反响','王诗尧','央视网','<p>　　<strong>紧扣新要求 奋进新时代――习近平总书记在江苏徐州市考察引发强烈反响</strong></p>\n<p>　　央视网消息(新闻联播)：习近平总书记近日在江苏徐州市考察时强调，各地区各部门要深刻理解和精准把握党的十九大精神，结合实际扎扎实实贯彻党的十九大决策部署，紧扣中国特色社会主义新时代的新要求，推动党和国家各项事业不断迈上新台阶。</p>\n<p>　　总书记的嘱托催人奋进，当地干部群众表示，要以习近平新时代中国特色社会主义思想为指引，紧扣新要求，奋进新时代。</p>\n<p>　　考察第一站，总书记来到了徐工集团重型机械有限公司，他察看装配车间，坐进起重机驾驶室。设计师朱长建，对当时和总书记交流的情景记忆犹新。</p>\n<p>　　创新是企业核心竞争力的源泉。总书记指出，必须有信心、有耐心、有定力抓好自主创新。这些话，让王民更加坚定了发展的方向。</p>\n<p>　　落实总书记要求，徐工集团计划明年，把数控化加工率从59%提高到95%，让产品覆盖的国家和地区超过200个。</p>\n<p>　　要让人民过上幸福美好的生活。这次考察，总书记还来到了徐州市贾汪区，他走进马庄村综合服务室，叮嘱干部要让群众少跑腿；观看村民的快板节目，他强调乡村振兴，不光看农民口袋里的票子，还要看农民的精神风貌。</p>\n<p>　　采煤是马庄村过去的主要产业，现在，村里已从单纯以煤为生，转型到文化、旅游等多种产业共同发展。这两天王秀英老人一直很兴奋，因为她的香包卖给了总书记。</p>\n<p>　　马庄村毗邻潘安湖，这里曾是采煤塌陷区，现在经过生态修复成为湿地景区。做好资源枯竭地区转型发展这篇大文章，贾汪区正着手完善规划。</p>\n<p>　　“紧扣新时代要求推动改革发展”，这是十九大后首次国内考察的习近平总书记向大家提出的硬任务，大家表示，要用实干苦干，走进决胜全面建成小康社会的新时代，走进奋力实现中华民族伟大复兴中国梦的新时代。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1635,'2017-12-16 20:28:00','2017-12-17 01:15:26','64',0),(2844,'报告显示：年轻移民增多致澳人老龄化速度放缓','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月15日电 据澳洲网编译报道，经济合作与发展组织(OECD)近日针对养老金问题，发布2017年度“养老金一瞥”报告(2017 edition of Pensions at a Glance)。报告指出，年轻移民加入致澳大利亚老龄化速度减缓，然而澳老人贫困率偏高。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　据报道，报告显示，澳大利亚老龄化速度比多数发达国家慢。</p>\n<p>　　OECD平均受供养人数(即每100人中超过65岁的老人人数)为28人，并将可能在2050年增加到53人。相比之下，澳大利亚数值偏低，即每100名澳人中超过65岁的老人人数为25人，并将可能在2050年增加到41人。</p>\n<p>　　虽然，2016年人口普查数据显示，澳人人口出生率在下降，致使澳大利亚出生的年轻人数量减少。但是，很多青年人从海外移居到澳大利亚，造成澳大利亚年轻人数量占比高。</p>\n<p>　　此外，因为澳大利亚工作者们需要供养的老人更少，这就给澳大利亚更多时间采取措施，以限制老龄化给养老金系统带来的影响。</p>\n<p>　　据了解，澳大利亚政府给养老金的财政支出为4%，并将在2050年保持4%；而OECD的平均水平为9%，并将在2050年提升到10%。</p>\n<p>　　对此，报告中写到：“与许多OECD国家相比，澳大利亚似乎不用怎么担心养老金系统的财务可持续性。考虑到(澳大利亚)政府在养老金方面的参与度有限，以及老龄化程度放缓，导致政府对养老金的支出不多，并将持续保持这种状态。”</p>\n<p>　　据悉，65岁以上的澳人中有3/4甚至更多人，获得了政府的养老金(Age Pension)。而OECD的平均水平仅为1/4。但却因为(政府对申请补助者的)经济情况调查，导致约42%的澳人养老金减少了。</p>\n<p>　　据了解，澳大利亚于1992年引入了养老金系统，其中有一项“强制储蓄”条例，可能降低了澳大利亚老人的贫困率(从而使得养老金减少)。</p>\n<p>　　2015年，澳大利亚老人的贫困率高达26%；而OECD平均水平为13%。对此，OECD报告中写到：“还有部分原因是，现在非常流行一次就提取所有养老金，而不是等到退休的时候，慢慢领取。”</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1549,'2017-12-15 17:03:00','2017-12-17 01:15:26','73',0),(2845,'海南四机场旅客吞吐量突破4000万人次','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>海口12月16日电 (洪坚鹏 曾凡容 胡茂君)记者16日从海航机场集团有限公司媒体沟通会获悉，由其运营的海南省内四家机场旅客吞吐量当日突破4000万人次，包括海口美兰国际机场、三亚凤凰国际机场、琼海博鳌国际机场以及三沙永兴机场。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　海航机场集团董事长兼总裁廖虹宇在媒体沟通会上介绍，近几年海南省以及各级政府从资金投入、资源配置等多方面支持省内各机场建设，先后出台多项奖励配套政策，有效促进了机场客流量的增长。</p>\n<p>　　他表示，美兰机场旅客吞吐量自2011年突破1000万人次大关以来，始终保持两位数的增长，经过短短6年的发展又实现2000万人次的跨越；12月6日，海南入境游客量突破100万人次，实现了海南入境旅游发展的阶段性突破。这些成绩的取得，正是依靠海南省以国际旅游岛建设为抓手，紧跟国家“一带一路”建设及打造“泛南海旅游经济合作圈”，充分发挥自身区位和资源优势，统筹整合国内航线资源，不断拓展国内外航空市场开发取得的结果。</p>\n<p>　　据了解，近几年，海南省委、省政府高度重视海南国际旅游市场开发工作，先后出台《关于印发海南省入境旅游市场开拓扶持办法(试行)的通知》、《海南省整合和调整有关民航客运财政补贴政策条款实施办法》等，对各家机场客流量增长产生明显拉动作用。省市两级旅游主管部门也曾多次带队参加世界航线发展大会、海南旅游市场推介等活动，多方位宣传推广海南旅游市场，不断提升海南航空旅游市场的知名度和美誉度。</p>\n<p>　　海南省旅游发展委员会相关官员表示，未来海南将大力提升交通基础设施水平尤其是民航基础设施水平建设，加快完成海口、三亚两机场扩建。</p>\n<p>　　据统计，截至目前，海南4家机场共开通航线412条，其中国内359条，国际地区航线53条，主要辐射港澳台、东南亚、日韩、俄罗斯和中亚等国家和地区共计48个城市。“空中丝路”的不断加密，提升了国际旅客赴琼旅游的热情，也为海南省未来国际航空市场的可持续发展创造了便捷的交通条件。</p>\n<p>　　廖虹宇介绍，美兰机场2017年荣获SKYTRAX五星机场，成为全球第八家、境内首家SKYTRAX五星机场，创建了中国机场的世界级品牌；凤凰机场荣获有SKYTRAX“全球最佳贵宾航站楼”、“四星机场”等荣誉称号。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,61,'2017-12-16 20:44:00','2017-12-17 01:15:26','66',0),(2846,'北京市2018年普通高考英语听力第一次考试举行','张楷欣','北京晨报','<p>　　<strong>高考英语听力首次机考</strong></p>\n<p>　　<strong>启用人脸识别系统 12月29日公布成绩</strong></p>\n<p>　　北京晨报讯(记者 王刚)昨日，北京市2018年普通高考英语听力第一次考试举行，大多数考生反映考题难度适中，没有偏题怪题。</p>\n<p>　　北京教育考试院相关负责人介绍，为了防止考场内作弊，英语听力机考启用人脸识别系统，加强考生身份核验，严禁考生携带无线通讯、电子存储、记忆录像等设备以及学习资料、纸笔等学习用具进入封闭区域。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　考试结束后，大多数考生走出考场后显得轻松自信，反映考题难度适中，没有偏题怪题，做起来比较得心应手。英语教师反映，高考英语听力机考实行一年两考，增加了考生选择权，减缓了考生的应试压力，针对机考形式平时学习过程有训练，考试过程不紧张。</p>\n<p>　　北京教育考试院相关负责人介绍，第一次高考英语听力机考安全有序。考试期间，全市和各区招考机构高度重视，认真部署，精心组织，严密实施，重点加强了试题保管、工作人员系统培训和考务培训、考生考前培训等各环节工作，确保考试安全平稳。此外，全市17个考区152个考点，为首次高考英语听力机考建设标准化考场，提供了硬件保障。重点加强考场编排，提供了良好备考和考试环境。机考软件系统通过反复测试，系统性能稳定、安全可靠。</p>\n<p>　　第一次高考英语听力机考成绩将于2017年12月29日发布，考生可通过北京教育考试院网站(网址：www.bjeea.edu.cn或www.bjeea.cn)查询。英语听力第二次考试报名工作将于2018年1月8日至10日进行，考生可登录北京教育考试院网站报名参加第二次考试。</p>\n<p>　　据悉，为贯彻落实《国务院关于深化考试招生制度改革的实施意见》，北京市从2018年起，高考英语听力与统考笔试分离，采取计算机化考试。每个学生有两次参加考试的机会，取其中一次最高分计入高考总分。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1824,'2017-12-17 00:57:00','2017-12-17 01:15:26','65',0),(2847,'移动支付普及将破线下小微企业贷款难题？','魏巍','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>北京12月15日电(夏宾)线下的小微经营者不用购置扫码枪等机具，只要一张“码”就拥有了移动互联网时代的入场券，路边的包子铺、菜市场的小摊主，甚至是没有固定店铺的上门服务经营者都能零门槛接入互联网，小微经营者们形象地将自己称为“码商”。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　移动支付不仅让“码商”收款便捷，更帮助线下商业实现数字化升级，基于二维码商业场景和数据，还能让线下经营者享受到更多维的综合金融服务。</p>\n<p>　　今年6月，网商银行依托支付宝移动支付，从服务线上“网商”延伸到了服务线下“码商”，推出专为线下小微经营者提供的“多收多贷”贷款服务，让小摊主、个体户也能通过手机贷款。</p>\n<p>　　6个月时间，已有超155万线下小微经营者获得了网商银行的贷款。在科技的助力下，金融服务首次规模化触达了这批最小微的线下经营者群体。</p>\n<p>　　与大企业满额贷款慢慢使用不同，小微经营者更为精打细算，为了节省利息，只有必要时才申请贷款，并且尽快还款，有需求再贷，“小、急、短、频”的特点明显。</p>\n<p>　　网商银行数据显示，线下小微经营者笔均贷款金额仅7615元，平均资金使用时长为50天，6个月内贷款超过3次的经营者却达到35%。</p>\n<p>　　无担保、没抵押、缺风控数据，是阻碍传统金融方式服务线下小微商家最重要的原因。</p>\n<p>　　用“码”实现线下商家的互联网化后，网商银行通过大数据和人工智能技术突破了这些限制，改变了金融风控模式。</p>\n<p>　　“随着不断的探索，我们发现线下小微经营者用上移动支付之后，能够沉淀下来的部分数据维度甚至比线上电商还要丰富，再结合我们的风控能力和经验，为他们提供贷款服务就有了基础。”网商银行资深总监胡晓东说。</p>\n<p>　　针对线下小微经营者，网商银行创建了多套针对性风控措施，能有效识别经营属性、判断交易有效性、预测商家经营能力。</p>\n<p>　　“传统的信贷风控理念往往先把人预设为坏人，但网商银行从不预设任何一个坏人，我们把每个人首先看成是好人，然后用大数据的风控技术把其中少数的‘坏人’挑出去。”网商银行行长黄浩表示，这也是网商银行能在短短半年服务这么多线下小微商家的原因。</p>\n<p>　　截止到11月底，网商银行线下经营者贷款的不良率仅0.78%，99.15%的商家都能做到按时还款。</p>\n<p>　　据国家统计局公布的经济数据，2016年中国第三产业的产值占GDP的51.6%，而发达国家第三产业的产值则超过70%。</p>\n<p>　　中国国际经济交流中心常务副理事长郑新立认为，第三产业大量的个体户、小企业得不到贷款的支持，是中国第三产业发展滞后的最重要的原因。</p>\n<p>　　“科技的发展，让金融血液能输送到实体经济的每条毛细血管，渗透到每个细胞，将助力第三产业发展，进一步优化中国经济结构。”郑新立说。</p>\n<p>　　业内人士预测，中国线下具有经营性质的小微商家及个体户总量可能有1亿户，随着移动支付的普及和随之而来的数据积累，以及科技平台跟金融机构开放合作的加深，信息不对称、小微企业贷款难的现象或将走向历史。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1432,'2017-12-15 17:23:00','2017-12-17 01:15:26','67',0),(2848,'民进党执政一年多为台民众带来啥？国民党两字回应','王诗尧','中国台湾网','<p>　　<strong>民进党执政一年多为台民众带来啥？国民党两字回应</strong></p>\n<p>　　中国台湾网11月16日讯 据台湾《中时电子报》报道，绿营智库“新台湾国策智库”昨日(15日)发布民调，台当局“行政院长”赖清德支持度下跌，只剩约4成，“行政院”发言人徐国勇认为任何改革实施后，人民才会感受到改革对社会有利，“痛苦会过去，美丽会留下来”。对此，国民党文化传播委员会副主任委员洪孟楷直言，民进党过去执政一年多时间到底为台湾民众带来些什么？只怕就是层出不穷的经典“干话”(空话、假话)，若最后民进党执政只剩“干话”被留下，恐怕也离下台不远了。</p>\n<p>　　“民调跌都在‘院长’意料之内！”徐国勇称“劳基法”、年金改革等，人民到时候都会有感，真正实施就能够化解争议，有句话说“痛苦会过去，美丽会留下来”、“我们当然了解要承受这些痛苦”。</p>\n<p>　　洪孟楷在Facebook表示，徐国勇谈赖清德民调下跌，以“痛苦会过去”来形容民调数字只是一时；然而民进党过去执政一年多时间到底为台湾民众带来些什么？只怕就是层出不穷的经典“干话”，而如果最后民进党执政只剩下一句又一句的“干话”被留下，恐怕也就离下台不远了。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　他表示，不论是“干话”之首的蔡英文曾说过的“劳工是我心中最软的一块”、“听到之后、就不要再大声”，赖清德的“功德说”，冯世宽的“100分”，陈时中的“看到百宝箱一开心就想怀孕生子”，到邱议莹“抗议那些都是放录音带”等等，真是所谓族繁不及备载的罄竹难书。每一句听在人民耳里就是刺耳，要问民调怎么提升？就从官员停止讲“干话”开始！</p>\n<p>　　洪孟楷认为，谁说改革一定让民调低？若是对人民有利的改革，全民举双手赞成！但就怕是打着改革大旗、却做清算斗争之事，看在民众眼里正事不做，民调当然低！“不当党产”、“促转条例”哪项不是针对性极高？如果民进党当局成绩单摊开只能拿对付政敌这项来说嘴，这样带给台湾的痛苦恐怕会没有过去的一天。</p>\n<p>　　他说，不然提到赖清德就想做功德、提到蔡英文就想到自立自强、提到民进党就想到资进党，只在人民心中留下“干话”形象，这将是全民皆输的局面。(中国台湾网 李宁)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,87,'2017-12-16 14:15:00','2017-12-17 01:15:26','70',0),(2849,'保监会就《保险资产负债管理监管办法》征行业意见','陈海峰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月15日电 据保监会网站消息，近日，保监会就《保险资产负债管理监管办法》(以下简称《监管办法》)征求行业意见。《监管办法》是保监会贯彻落实全国金融工作会议精神和保监会“1+4”系列文件的一项重要举措，对于推动行业回归本源、积极服务实体经济、防范资产负债错配风险、强化资产负债管理监管硬约束具有重要的意义。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　《监管办法》在前期五项具体监管规则的基础上，进一步明确了资产负债管理的基本要求、监管框架、评级方法以及对应的差别化监管措施，是资产负债管理监管的总领性文件，共5章38条。</p>\n<p>　　第一章总则，主要明确制定目的、适用范围、资产负债管理定义、监管组织架构及相应职责。第二章保险公司资产负债管理，主要针对保险公司明确资产负债管理的基本内容和要求。第三章监管评估，主要明确资产负债管理监管的评估方式、评分方法、综合评级划分标准等。第四章监管措施，主要明确根据综合评级结果实施差别化的监管措施，比如对于综合评级为D类的公司，将采取限制资金运用形式或比例、限制产品销售或业务范围等针对性措施；对于能力评估结果为低的公司，将不予备案其股权投资能力、不动产投资能力和衍生品运用能力，禁止其开展重大股票投资等。第五章附则，主要针对特殊情况和原有法规适用作出说明。</p>\n<p>　　良好的资产负债管理是保险公司可持续经营的基础，也是保险业在日益复杂的风险环境中保持稳健发展、防范系统性风险的重要保障。加强资产负债管理监管，有利于推动行业回归本源，发挥长期稳健风险管理和保障功能；有利于引导保险资金稳健配置，为实体经济发展提供有力支持；有利于防范资产负债错配风险，守住不发生系统性风险的底线；有利于弥补监管短板，增强监管体系的科学性和有效性。</p>\n<p>　　保监会将根据各方面反馈意见对监管办法予以修订完善，并将在明年正式发布实施。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,158,'2017-12-15 17:47:00','2017-12-17 01:15:26','67',0),(2850,'安理会召开部长级会议 各方吁推动朝鲜半岛无核化','孔庆玲','新华网','<p>　　<strong>联合国安理会召开部长级会议 各方呼吁推动朝鲜半岛无核化</strong></p>\n<p>　　新华社联合国12月15日电(记者 马建国)联合国安理会15日就朝鲜半岛问题召开部长级会议。联合国秘书长和中国、瑞典、俄罗斯等国代表纷纷呼吁有关各方尽快恢复对话谈判，推动实现朝鲜半岛无核化和可持续和平。</p>\n<p>　　联合国秘书长古特雷斯在会上表示，朝鲜半岛核问题是当今世界最紧张和危险的和平与安全问题。他呼吁朝鲜遵守联合国安理会有关决议，并要求朝鲜为恢复朝鲜半岛无核化和可持续和平的谈判留下空间。他还说，联合国安理会的团结是和平解决朝鲜半岛问题的最重要手段。外交介入是达成可持续和平和无核化的唯一途径。</p>\n<p>　　中国常驻联合国代表团临时代办吴海涛表示，半岛局势仍然深陷示强与对抗的恶性循环，前景不容乐观。但同时也要看到，和平的希望仍未湮灭，谈判的前景依然存在，动武的选择不可接受。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　吴海涛说，中方认为，有关各方应从维护半岛和平稳定的大局出发，冷静判断形势，作出明智选择，采取切实行动。一、立即停止一切不利于半岛无核化与和平稳定的言行，尽快缓和半岛局势；二、遵循安理会涉朝决议的规定和精神，推动半岛核问题的妥善解决；三、承担自己应有的责任，切实履行自身应尽的义务；四、坚持和平解决的大方向，尽快恢复对话谈判。</p>\n<p>　　他说，作为半岛近邻，中方始终坚持实现半岛无核化，坚持维护半岛和平稳定，坚持通过对话协商解决问题，反对半岛生乱生战。中方作出了比各方都要多的努力，承受了比各方都要大的代价，对中方努力的质疑是不负责任的。针对当前的半岛局势，中方提出了朝鲜暂停核导活动、美韩暂停大规模联合军演的“双暂停”倡议与并行推进半岛无核化和建立半岛和平机制的“双轨并行”思路。俄罗斯也提出了解决半岛问题分步走设想。在此基础上，今年7月4日，中俄发表联合声明，提出解决半岛问题的“路线图”。中俄共同倡议现实可行，旨在推动和平解决半岛核问题、维护半岛和平稳定，希望得到有关各方的响应和支持。</p>\n<p>　　俄罗斯常驻联合国代表涅边贾表示，现在朝鲜半岛问题正处于一个关键时刻。任何误判或轻率的决定都会带来让人追悔莫及的后果。涅边贾重申俄中就解决朝鲜半岛问题共同提出了“路线图”，强调中俄一直提倡的“双暂停”倡议。</p>\n<p>　　英国负责中国及亚太事务的外交国务大臣马克・菲尔德表示，希望通过联合国安理会组成一条统一战线，找到解决问题的办法。</p>\n<p>　　瑞典外交大臣玛戈特・瓦尔斯特伦指出，在朝鲜半岛核问题上“绝对没有军事解决选项”，并强调要加强外交努力，建立对话机制。</p>\n<p>　　玻利维亚、埃及、哈萨克斯坦代表都纷纷表示了要对话解决问题的愿望。</p>\n<p>　　美国国务卿蒂勒森表示美国不接受朝鲜拥核国地位的立场，强调解决朝鲜半岛危机的终极目标应该是朝鲜方面“完全的、可核实的、不可逆的”放弃核武器项目。</p>\n<p>　　朝鲜常驻联合国代表慈成男也出席了会议。他表示，朝鲜拥核是应对美国核威胁与讹诈的“不可避免的自卫”。美国和日本才是核扩散的罪魁。</p>\n<p>　　这次会议由日本驻联合国使团提议举行。会议前，担任12月联合国安理会轮值主席国的日本使团还向安理会成员国代表散发一份资料，建议成员国关注核武器、导弹开发项目和其他大规模杀伤性武器。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1914,'2017-12-16 14:31:00','2017-12-17 01:15:26','71',0),(2851,'中英两国央行首次召开联合研讨会聚焦绿色金融','魏巍','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>北京12月15日电 (夏宾)15日，首届中英央行联合研讨会在北京举行。两国专家围绕绿色投资与收益、金融机构环境信息披露等议题进行了讨论。</p>\n<p>　　中国央行副行长陈雨露、英格兰银行审慎监管局国际银行监管执行总裁Sarah Breeden出席会议并致辞。</p>\n<p>　　陈雨露表示，中英两国是全球绿色金融领域的先行者和领导者。在两国共同的推动下，全球绿色金融发展取得长足进步，双边绿色金融合作取得丰硕成果。</p>\n<p>　　中国政府先后发布了《关于构建绿色金融体系的指导意见》等一系列政策文件，建立了较为系统、完善的绿色金融政策框架。绿色项目融资渠道不断拓宽，产品和服务创新不断涌现，为绿色项目提供了有力的金融支持。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　陈雨露指出，目前，中国是全球最大的贴标绿债发行国，绿色信贷余额占全部信贷的比重稳步提高，环境信息披露要求不断提升，绿色投资者队伍不断壮大。中国的浙江、广东、新疆、贵州、江西等五个国家级绿色金融改革创新试验区正式启动，重在发挥地方的创新精神，积极探索可复制可推广的经验。</p>\n<p>　　陈雨露提醒，当前绿色金融发展还面临着一些挑战，包括绿色金融可持续发展的能力有待提高、绿色金融产品工具不够多元化、信息不对称问题比较突出、存在项目“洗绿”风险等。</p>\n<p>　　陈雨露称，十九大报告中再次强调发展绿色金融，指明了绿色发展和绿色金融发展的光明前景。未来，希望中英两国密切合作，继续推进绿色金融议题的国际主流化进程，着力提升绿色金融内生性的可持续发展水平，开展绿色金融标准体系建设工作，推动绿色金融更好服务于“一带一路”建设。</p>\n<p>　　中英两国政府部门、市场和研究机构的100余位代表参加研讨会。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,419,'2017-12-15 18:34:00','2017-12-17 01:15:26','67',0),(2852,'大陆配偶群体在台北举办两岸交流30年踩街嘉年华','张楷欣','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>台北12月16日电 (记者 刘舒凌 杨程晨)台湾中华两岸婚姻协调促进会联合全台40多家团体共同发起的“两岸交流30年踩街嘉年华”16日在台北市欢乐登场。</p>\n<p>　　当天，以“两岸一家亲，牵手心连心”“深化交流，融合发展”为诉求，来自全台多地、逾千名大陆籍配偶与家人一起踩街、表达心中愿望。</p>\n<p>　　他们冒着细雨，从四面八方聚集到市中心自由广场。下午2时许，彩车、舞狮队、腰鼓队和不同社团构成的踩街队伍由宣传车、旗队引导着，穿着盛装的参与者们举起“两岸一家亲”等彩旗，携家带眷一同出行。</p>\n<p>　　当天活动的主持人王娟萍说，30年前，两岸互不往来，许多家庭分隔多年、音讯渺茫；开放探亲后，他们当中许多人才得以骨肉重逢、共享天伦，也成为两岸重启交流的起点。如今，交流涉及经济、社会、文化，层次越来越广、不断深化，为两岸关系改善和两岸和平发展奠定好的基础。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　38万对男女在这30年中结成了两岸婚姻，王娟萍说，这样的成果非常不容易，我们要好好维护它，带动海峡两岸深化交流、融合发展。</p>\n<p>　　活动主要发起人、中华两岸婚姻协调促进会理事长黄江南说，这次活动9月开始筹备。大陆配偶是为了家庭、为了爱来到台湾，欢迎大家从全台各地一同来踊跃参加。他也感谢很多大陆配偶姐妹为使活动办得更美更好，辛苦地排练舞蹈、歌曲。</p>\n<p>　　活动总指挥钟锦明表示，两岸交流30年的成果需要大家一起维护，踩街队伍将走进台北西门町等周末人群聚集的地方，希望走慢一点，更好地向市民传达两岸应融合发展的观点。</p>\n<p>　　现场气氛欢快活泼，钟锦明说，原先报名的大陆配偶和亲属为1700人，有希望达到2000人的规模。</p>\n<p>　　来自浙江舟山的周君君和60多位同乡一同行走在踩街队伍中。她表示，自己喜欢台湾，也想念舟山，“我在大陆有亲人，在台湾也有亲人，两岸各有各的好，我现在都离不开”。</p>\n<p>　　湖南衡阳来的蒋女士20年前嫁到台北。当天和好姐妹们相约来踩街，她说，参加活动就是要表达希望台湾、大陆将来都更好，也呼吁台湾本地民众增加对大陆配偶的认识、减少排斥。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1296,'2017-12-16 22:49:00','2017-12-17 01:15:26','70',0),(2853,'渣打3年内为“一带一路”项目提供200亿美元融资支持','魏巍','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>上海12月15日电(记者 姜煜)渣打银行15日宣布，将在2020年底前为“一带一路”倡议相关项目提供总值至少200亿美元的融资支持。</p>\n<p>　　渣打银行有约85%的收入来自于亚洲、非洲和中东市场，是全球为数不多的能够在“一带一路”沿线市场促进贸易、资本和投资增长的国际性银行之一。</p>\n<p>　　渣打集团董事会主席韦浩思表示：“渣打集团策略与‘一带一路’倡议非常契合，我们拥有非常独特的全球化网络，其中超过三分之二的网点与‘一带一路’路线高度重合。在这些市场，我们有着非常悠久的历史，并长期通过贸易和投资走廊为客户提供支持。渣打银行将更好地支持‘一带一路’沿线的中国企业，并助力目的地市场客户把握这一重要倡议所带来的机遇。”</p>\n<p>　　据悉，渣打银行为“一带一路”倡议相关项目所提供的融资支持将覆盖贷款、金融市场、交易银行、资本市场和项目融资等系列产品和服务。今年，渣打银行已经参与超过50笔“一带一路”相关交易，价值总额超过100亿美元，涉及广泛的金融产品和服务。在这些交易中，大约有一半发生在非洲，四分之一在南亚，剩余部分来自其他“一带一路”沿线市场。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　渣打银行大中华区首席经济学家丁爽表示：“‘一带一路’倡议在过去4年取得重大进展，目前正稳步进入落实阶段。随着多层级融资机制的初步建立，我们认为开发性金融和商业银行将在‘一带一路’项目融资过程中发挥重要作用。” (完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,979,'2017-12-15 19:31:00','2017-12-17 01:15:26','67',0),(2854,'《心理罪之城市之光》提前点映 上演罪犯与警察拉锯战','李季','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>杭州12月15日电(胡小丽)正义有时会迟到，但从不缺席，只是这种情况往往会催生如电影《心理罪之城市之光》中江亚(阮经天饰)这样的人。</p>\n<p>　　14日，《心理罪之城市之光》在浙江杭州举行点映，由邓超饰演的神探方木与江亚在片中上演了一场智力与武力的双重比拼，获不少观众好评。</p>\n<p>　　据悉，该片改编自雷米系列犯罪小说《心理罪》最后一部《城市之光》，讲述了方木抓捕高智商变态杀人犯的故事。</p>\n<p>　　许多悬疑片，往往“我方在明，敌人在暗”，但《心理罪之城市之光》中，双方几乎知根知底，但警方苦于没有有力证据，无法将杀人犯江亚绳之以法。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　作为警察的方木在和江亚的一次次正面较量中，多次被激怒，险些欲绕过法律，手刃“仇人”，但方木深知以暴制暴，只会制造更多的暴力，他作为一名警察，可以为抓捕罪犯以身犯险，但绝不可以以身犯法。</p>\n<p>　　《心理罪之城市之光》最大的亮点即是打破了以往悬疑动作片的惯常套路，将重点由破案及抓获凶手转移到了当正义暂时得不到伸张时，该不该使用私刑的思考上。</p>\n<p>　　到底谁才是真正的“城市之光”？是自觉充满侠义之心，凌驾于法律之上的江亚，还是始终守住法律底线，维护公众秩序的警察？影片虽在结尾给出了答案，但依旧留给观众无限思考的空间。</p>\n<p>　　而除引发思考之外，在点映期间，该片剧情和人物塑造亦圈粉不少：</p>\n<p>　　“年度前十，电影主题、节奏、观赏度以及演员表现都有很高水准。”“两个主角过招非常精彩，邓超还是一如既往地稳。”“阮经天的表演确实惊艳到了我，很好地演绎了一个人的变态。”</p>\n<p>　　值得一提的是，《心理罪之城市之光》末尾还以彩蛋的方式致敬了现实生活中真正的“城市之光”，使之成为了全片最温暖有力的泪点。</p>\n<p>　　据了解，《心理罪之城市之光》由徐纪周执导，邓超、阮经天、刘诗诗、文淇等主演，林嘉欣特别演出，郭京飞、雷米友情演出，将于12月22日全国公映。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,558,'2017-12-15 15:48:00','2017-12-17 01:15:26','68',0),(2855,'香港迪士尼乐园和海洋公园宣布门票加价','魏巍','新华网','<p><strong>　　香港迪士尼乐园和海洋公园宣布门票加价</strong></p>\n<p>　　新华社香港12月15日电(记者张雅诗 王小旎)香港迪士尼乐园15日起调高入场票价，各类门票平均加幅5.8%；香港海洋公园也于日前宣布将于明年元旦起加价9%。业界和学者表示，两园加价在合理范围内，未来两园将陆续加入新元素，相信有助提升吸引力。</p>\n<p>　　具体而言，海洋公园成人和小童日间入场票价将于明年1月1日起分别调高至480港元和240港元。香港迪士尼乐园则于15日起加价，标准门票售价和小童门票分别增至619港元和458港元。</p>\n<p>　　事实上，香港海洋公园和香港迪士尼乐园已连续数年加价。香港旅游界立法会议员姚思荣表示，相信两个主题公园加价不会影响内地和海外旅客入场人次。他说，两个乐园加价反映它们在经营上有困难，加上通胀压力，加价是可以理解的。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　香港专业教育学院(黄克竞)酒店、服务及旅游学系高级讲师黄家荣表示，香港迪士尼乐园一直保持着新元素的不断加入，由开园到现在，景点数目和地方增加了，价格增长也很正常。至于海洋公园，未来也将推出新项目和酒店等设施，正在为将来扩展作好准备。</p>\n<p>　　香港旅游发展局数据显示，今年1月至10月整体访港旅客人次同比增长2.7%，超过4790万人次，其中内地旅客人次同比增长3.1%。</p>\n<p>　　姚思荣说，访港旅客人数下半年逐渐回暖，尤其在第四季，访港过夜旅客人数升幅明显，整个旅游业气氛良好。“两个主题公园在策略、产品、宣传方面，需要有新元素，加入新项目，今后才能继续保持吸引力。”</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,849,'2017-12-15 21:55:00','2017-12-17 01:15:26','69',0),(2856,'香港发生两车相撞交通事故致14人受伤 2人情况危殆','吉翔','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月16日电 据香港电台报道，香港牛池湾新清水湾道16日发生交通意外，一辆小巴与特别用途车相撞，14名男女受伤送院，其中1男1女伤者情况危殆，8人伤势稳定，另有4人已经出院。</p>\n<p>　　事发在午夜时分，一辆小巴沿新清水湾道向西贡方向行驶，驶至近彩云�时怀疑收掣不及，撞向前方慢驶中的一辆扫街车。</p>\n<p>　　小巴司机一度被困，脚部受伤，其后由消防救出。其余6男7女的伤者是小巴乘客，年龄介乎22至67岁。</p>\n<p>　　报道称，所有伤者被送往联合医院及伊利沙伯医院治理。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>',NULL,NULL,1845,'2017-12-16 14:57:00','2017-12-17 01:15:26','69',0),(2857,'“孙平化日本学学术奖励基金”在北京颁奖','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>北京12月16日电 (记者 沙晗汀)“‘孙平化日本学学术奖励基金’激励了国内年轻一代学者更加全面深入地了解日本，对促进中日民间交流，增进两国人民之间的相互了解与友谊，加深接触、实现更为坦诚的对话，具有重要意义”，孙平化之女孙晓燕在16日举行的第八届“孙平化日本学学术奖励基金”颁奖仪式后告诉<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　今年是“孙平化日本学学术奖励基金”设立以来的第20个年头。该奖项是根据已故中国日本友好协会会长孙平化遗愿，将其生前、身后所得的各类捐赠和奖酬等捐献设立的基金，旨在支持中国日本学学术研究和人才培养，奖励优秀学术成果，发展中日两国人民友好事业。</p>\n<p>　　在颁奖仪式上，日本驻华公使衔参赞川上文博表示，今年是日中邦交正常化45周年，也是孙平化先生诞辰100周年。孙平化先生毕生致力于日中友好事业发展，是深受日中两国人民敬爱的先驱，为日中两国建交作出了重要贡献。川上文博希望，通过本奖项的实施，祈愿更多年轻一代继承日中友好意愿，激励更多中青年学者为增进日中友谊贡献力量。</p>\n<p>　　中国日本友好协会会长唐家璇在贺信中说，当前中日关系正处在改善发展的难得机遇期，明年将迎来《中华人民共和国和日本国和平友好条约》缔结40周年。值此颁奖之际，不由地更加怀念孙平化先生和为中日友好事业倾注大量心血的两国老一辈先贤们。</p>\n<p>　　在中国宋庆龄基金会副主席井顿泉看来，中日关系正站在新的历史起点上，面临进一步发展的新机遇。他希望两国友好人士不忘前辈初心，秉持“以史为鉴、面向未来”的精神，坚定和平共处、世代友好的信念，共同推动中日友好事业发展。</p>\n<p>　　在本届评选中，包括《永远的唐土――日本平安朝物语文学的中国叙述》等25件作品获奖。</p>\n<p>　　日本前首相福田康夫、日本驻华使馆人员和中日友好人士等参加了颁奖典礼。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,635,'2017-12-16 20:56:00','2017-12-17 01:15:26','75',0),(2858,'《中国信托行业报告》发布：“5+4”助信托行业发展','罗攀','中国新闻网','<p>　　<strong>中国外贸信托联合BCG发布《中国信托行业报告》：“5+4”助信托行业蓬勃发展 </strong></p>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>北京12月15日电(夏宾)15日，中国对外经济贸易信托有限公司(下称“中国外贸信托”)与波士顿咨询公司(BCG)在北京联合主办“远见成就融耀”――《中国信托行业报告――回归本源、服务实体、动能转换、转型发展》(以下简称“报告”)发布会。</p>\n<p>　　发布会围绕“信托如何更好地服务实体”与“信托如何满足人民日益增长的美好生活需要”进行了深入探讨，为中国信托业发展打开了新的行业视窗。</p>\n<p>　　报告作者之一、中化集团总会计师、中化金融事业部总裁、中国外贸信托董事长杨林表示，过去十年，信托资产来源日益多样、业务结构不断优化、业务模式持续丰富，行业在支持实体方面一直发挥着重要作用。信托未来要更加主动地服务国家发展战略，聚焦社会主要矛盾变化，全力服务实体经济与人民美好生活需求。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　报告作者之一、BCG合伙人兼董事总经理、大中华区金融与保险行业负责人何大勇表示：“面对中国经济增速换挡、金融监管趋严、市场竞争加剧的新环境，信托业已经来到转型发展的关键路口，可持续发展的压力不容小觑，如何打造行业的核心竞争力，将是未来几年内信托行业发展的主题。”</p>\n<p>　　对于再次站在十字路口的信托业，报告通过分析信托业的“本源”是什么、信托业未来发展应把握的机遇是什么、信托业的核心竞争力来源和差异化竞争模式是什么三个关键问题，以探寻中国信托业的未来发展之路。</p>\n<p>　　报告作者之一、中国外贸信托总经理伊力扎提表示：“一个行业的本源，落实于需求和供给两大方面。信托所服务的需求，应源自于实体经济发展和人民美好生活的需要，而信托所履行的核心功能，应源于信托业差异化的竞争优势。上述两者之结合，为信托业之本源。</p>\n<p>　　在需求视角下，中国信托业的发展机遇集中体现在产业优化、消费升级、财富管理、新型城镇化与经济出海这五大经济发展主题上；在供给视角下，信托业下一阶段的发展重点为服务信托、私募融资、资产管理与财富管理四大业务模式。基于信托业的这四大重点业务，结合当前宏观经济转型升级、金融需求迫切、金融体系尚待健全的环境，信托业的发展有三大意义：服务实体经济、服务人民群众和推动金融改革。</p>\n<p>　　五大主题和四类信托业务的有机结合，将为信托行业带来蓬勃的发展机会。据报告预计，直接涉及的四大业务均将实现高速发展，从投融资的总规模看，融资服务、财富管理、资产管理业务市场在未来均保持年均10%以上的快速增长。</p>\n<p>　　报告指出，伴随着整体市场发展机遇的凸显以及行业自身竞争力的攀升，行业有望实现效率与质量的共同提升。</p>\n<p>　　据测算，从2016年到2021年，信托行业总收入有望达到1945亿元(人民币，下同)，其中非自营的业务收入约为1459亿元，相对2016年实现约14%的年化增长率；行业结构呈多元、均衡发展态势，规模结构方面，合作型服务信托业务规模大幅下降，能力型服务信托和投资信托(包括主动型资产管理规模和家族信托)规模占比将会从22%提升至35%；收入结构方面，私募融资一元独大格局有所改观，占比从55%降至48%，资产管理和财富管理的收入比例从26%提升至39%。</p>\n<p>　　报告指出，未来行业致胜核心在于服务，行业动能也将逐步由私募投行驱动转为服务驱动。以此为契机，信托业应秉持“服务+业务”的发展思路，可考虑三大差异化业务模式：“服务+融资”、“服务+财富”、“服务+资管”。</p>\n<p>　　报告作者之一、BCG合伙人兼董事经理陈本强表示：“顺应行业发展趋势，信托业需真正以‘客户为中心’，实现专业能力致胜，善用‘服务+’理念，打造信托业差异化核心竞争力。这在很大程度上要求信托公司全盘规划、悉心宣贯、优化架构、坚定转型，有序推动各项战略举措落地实施。”(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,566,'2017-12-15 16:56:00','2017-12-17 01:15:26','67',0),(2859,'南非执政党非国大举行大会 十年来将首次更换党主席','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>约翰内斯堡12月16日电 (记者 宋方灿)12月16日，南非执政党非洲人国民大会(非国大)举行第54次全国代表大会，将选举产生新的党主席和副主席，新任党主席也有望成为下届南非总统候选人。</p>\n<p>　　非国大成立于1912年，自1997年以来其全国代表大会每五年举行一次。因非国大党主席、南非总统祖马的党主席任满，故此次大会将选举产生新的主席人选。非国大最高层的6人执委包括主席、副主席、全国主席、秘书长、副秘书长和总司库。本次大会为期5天，执委会选举于16日举行，6名执委及党主席的选举结果有望在17日揭晓。</p>\n<p>　　本次竞选主席的共有7名候选人，南非副总统拉马福萨和祖马总统的前妻、前任非盟委员会主席德拉米尼・祖马当选主席一职呼声很高，胜者有望代表非国大竞选下届南非总统。非国大全国执委会还接受了祖马总统的提议，将党的主席和副主席选举分开，这也意味着角逐党主席失败的候选人仍可竞选副主席。</p>\n<p>　　在15日晚大会开幕前的非国大晚宴上，祖马发表了主旨演讲。他宣布，大会一切准备就绪，他将卸任已经连任10年的党主席。只要与会代表团结起来，非国大必将且终将成为获胜者。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　祖马还指出，非国大以人民为优先，热爱人民，为人民而工作，致力于建立不分种族，不分性别的平等，努力打造一个民主、繁荣的南非。非国大也有一些失误，这是因为急于解决国内的问题，即便做错了，外界也应该予以包容与谅解。非国大希望得到工商界的继续支持，帮助南非发展经济，减少贫困并创造更多就业机会。</p>\n<p>　　出于对非国大换届选举结果和权力移交前景的乐观，南非约翰内斯堡证交所的股指近日有所上涨，而南非货币兰特对美元的汇率也持续上升。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1386,'2017-12-16 19:42:00','2017-12-17 01:15:26','71',0),(2860,'“严师慈父”人设崩 汪峰跨年晚会将展现暖男柔情','高辰','沈阳晚报','<p>　<strong>　汪峰跨年晚会将展现暖男柔情</strong></p>\n<p>　　各地跨年演唱会都已经进入紧张的筹备制作阶段，嘉宾阵容也开始逐渐揭开谜底。继日前公布了唱跳全才罗志祥，新晋小生胡一天后，昨日湖南卫视正式公布，摇滚巨匠汪峰确认加盟。今年音乐公益支教节目《让世界听见》中观众看到了汪峰最柔软、最温暖的一面，据导演组透露，在节目中以“严师慈父”形象圈粉无数的汪峰，在今年的湖南卫视跨年演唱会上将继续展现柔情的一面，不但将带来自己新专辑中的新歌首唱，用歌声向他最爱的家人表白，还极有可能与来自泸沽湖畔的“向阳花”合唱团再次合作。(张宁)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>',NULL,NULL,1690,'2017-12-15 14:39:00','2017-12-17 01:15:26','68',0),(2861,'海外华裔青少年长春“寻根”:做中华文化的“传播人”','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>长春12月16日电 (肖寓隆)“我们应该顺应当下的时代优势，来传播中华文化，让海外华人华侨可以更多地认识祖(籍)国，认识中华文化。”16日，来自马来西亚砂拉越诗巫公教中学的程大卫对记者说。</p>\n<p>　　由国务院侨务办公室主办，长春市侨办及长春市教育局承办的2017海外华裔青少年“中国寻根之旅”冬令营(长春营)正在长春进行，有来自印度尼西亚、菲律宾、新加坡和马来西亚的100名海外华裔青少年参与这次为期15天的“寻根之旅”。</p>\n<p>　　此次营员的年龄在12至18周岁之间。冬令营期间，营员们将在长春学习中国地理、历史、书法、剪纸等传统文化知识，同时也将体验乒乓球、民族舞、冰雪项目、参观伪满皇宫。</p>\n<p>　　按照行程安排，16日，中共长春市委党校常务副校长吴彦杰向营员们讲述了十九大精神的内涵。吴彦杰在宣讲中谈及，未来，中国的经济、军事、科技等实力将进一步跃升。这句话给在场的海外华裔青少年带来触动。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　来自新加坡的14岁女孩刘苏楠告诉记者，她的亲人有很多都生活在中国，因此她基本上每年都会来中国探亲。而谈及中国多年来的变化，她直言令其印象最深的便是移动支付的应用。</p>\n<p>　　刘苏楠向记者介绍，在新加坡并没有移动支付的应用，人们更多的还是采用现金支付的方式，如出现出门忘带钱包的情况，买东西就会很不便。“中国的移动支付很方便，不管在哪都可以随时掏出手机扫码支付，很便捷。”</p>\n<p>　　程大卫也表示，近年来中国科技的发展，尤其是基础设施建设是非常让人惊叹的。“在马来西亚我身边很多朋友，对中国还保留着刻板印象，他们对中国发展现状并不是很了解，有机会应该让他们都来中国看看，看看中国发展的速度之快。”</p>\n<p>　　新加坡华源会副秘书长欧阳小萱认为，通过让这些海外华裔青少年学习十九大精神，了解中华文化，可以加深他们对祖(籍)国的认知。“也希望他们成为传播中华文化的使者，将在中国的所见、所闻回去分享给更多的人。”(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,867,'2017-12-16 19:21:00','2017-12-17 01:15:26','73',0),(2862,'美联储如期加息 黄金原油短期或继续“分道扬镳”','魏巍','中国证券报','<p><strong>　　美联储如期加息 美元走强几率较大</strong></p>\n<p><strong>　　黄金原油短期或继续“分道扬镳”</strong></p>\n<p>　　美联储13日宣布今年第三次加息，符合市场普遍预期。接下来美元走势对于国际大宗商品价格的影响也引发市场关注。数据显示，进入今年第四季度以来，作为两大最受关注的大宗商品，国际黄金与原油期货价格却呈现分化走势，北海布伦特原油期价累计上涨9.14%，纽约商品交易所原油期价累计上涨9.75%，同期纽约商品交易所黄金期价累计下跌1.86%，白银期价累计下跌5.35%。</p>\n<p>　　分析人士指出，虽然美联储立场不如市场想象得那么“鹰派”，美元指数13日应声下跌，但持续加息仍将导致美元后市走强概率加大，实际上美元指数从本季度初的93以下，一度回升至接近94关口，进入12月以后更是反弹明显。由于“跷跷板效应”远较原油明显，黄金期价受到美元走强预期打压。此外，欧佩克延长减产协议无疑对于油价构成提振。各种差异造成了两者走势分化。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　美元因素或仍打压金价</p>\n<p>　　分析人士指出，从目前走势来看，美元因素对于原油等大宗商品影响越来越小，但是仍对黄金等贵金属产生一定影响，而近期美元仍有望走强，这或将抑制金价涨势。</p>\n<p>　　美联储在13日的例会后指出，预计明后两年仍将保持小幅渐进的加息节奏。美联储当天发布的加息预测显示，美联储预计2018年将加息三次、2019年和2020年分别加息两次，</p>\n<p>　　嘉盛集团研究主管James Chen指出，市场此前已广泛预期美联储将今年第三次加息25个基点，目前市场更多关注的是美联储未来货币政策途径相关线索以及经济预期。美联储2018年一定程度更为激进地收紧货币政策的预期依然存在，在此情境下，美元将进一步受到提振。</p>\n<p>　　供需平衡利好油价</p>\n<p>　　业内分析师表示，与黄金不同，原油价格的主要影响因素在于供需端。一些政策的变化至关重要，相反美元因素的影响力较小。</p>\n<p>　　石油输出国组织(欧佩克)与俄罗斯等非欧佩克产油国11月30日决定，将原油减产协议延长至2018年年底，以继续缓解市场供过于求的状况。目前减产取得了成效，原油供过于求的局面得到一定缓解，国际油价有所回升，原油库存也明显下降。</p>\n<p>　　芝商所董事总经理兼首席经济学家布卢福德・帕特南(Bluford Putnam)指出，自2016年9月以来，美国WTI油价一直在每桶42-55美元的宽幅区间内波动。短期来看，交易区间有望上调至每桶48-62美元。</p>\n<p>　　他指出，从短期因素来看，一些重大的供应中断会影响油价。例如委内瑞拉经济不佳，原油产量大幅下降，近期内看不到复苏迹象。此外，沙特石油国企阿美石油将进行首次公开发行，为了提振价格，沙特将有很大动力通过减产推高短期油价。虽然短期供应中断是暂时性因素，但仍可能影响2018年或者阿美石油完成首次公开募股之前的原油市场。这些暂时性因素似乎将扩大布伦特与WTI油价的价差。</p>\n<p>　　此外，他还指出，全球经济增长温和回暖是油价的另一个利好因素。中国经济继续向好，巴西的经济衰退已经结束，经济增长逐步回归。实际上，全球的经济信心和增长速度都将有所提高，推动原油和其他工业品价格涨至其交易区间的顶端。总体而言，短期供应因素和全球经济增长回暖正在推高油价，油价甚至有可能涨至每桶60美元。记者 张枕河</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1063,'2017-12-15 15:08:00','2017-12-17 01:15:26','67',0),(2863,'警方破获“感情骗子” 诈骗单身女性上千万元','王诗尧','新华网','<p>　　<strong>警方破获“感情骗子” 以恋爱为名诈骗单身女性上千万元</strong></p>\n<p>　　新华社北京12月16日电(记者卢国强)记者16日从北京警方获悉，警方抓获一名将自己包装成“成功人士”，以谈恋爱为名诈骗单身女性的犯罪嫌疑人。据悉，仅目前警方核实的5名受害人就被骗近1200万元。</p>\n<p>　　不久前，事主李女士向北京市公安局西城分局陶然亭派出所报案称，2016年4月，她通过微信认识一自称郭某某的54岁男子，两人逐步确定恋爱关系。随后，郭某某以马上交付购房尾款、房屋装修等理由，先后向李女士借款220多万元。此后，郭某某以各种理由拒不还款，今年9月干脆手机关机，下落不明。</p>\n<p>　　民警调查发现，郭某某并没有买房、装修，而是在北京市昌平区一别墅区租了一套住房，“借”来的钱也全部用于挥霍。随后，民警将郭某某抓获。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　据交代，郭某某从2002年开始，通过媒体刊登征婚广告，与多名单身女性保持恋爱关系，并以做生意等名义骗取12名事主100余万元。2004年，郭某某因诈骗罪被判处有期徒刑13年，2012年假释出狱。</p>\n<p>　　2015年以来，郭某某通过微信、QQ等交友软件冒充“成功人士”，选择在京居住且具有一定经济基础的大龄单身女性，借谈恋爱的名义向对方借款，甚至要求对方通过抵押房产、汽车或个人信用贷款。骗来的钱一部分用来偿还郭某某在银行贷款的利息，还有一部分被用于购买豪华轿车、奢侈品，租住高档住房等。</p>\n<p>　　目前，郭某某已经被警方刑事拘留，案件正在进一步调查中。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,290,'2017-12-16 19:31:00','2017-12-17 01:15:26','65',0),(2864,'涂们新片发布“一分钟解读”预告 呈现悲喜人生','罗攀','环球网','<p align=\"center\"><img src=\"upload/image/34cf3a94-6897-40ca-8bbd-6b8d4e83354a.jpg\"></p>\n<p><strong>　　《老兽》曝“一分钟解读”版预告 阅尽悲喜人生</strong></p>\n<p>　　由王小帅监制，周子�执导，涂们、王超北、王子子等人领衔主演的电影《老兽》，目前正在中国大陆热映。</p>\n<p>　　今日，《老兽》片方发布一支“一分钟解读”版预告，内蒙古老炮儿荒诞的悲喜人生在本支预告中浓缩呈现。预告片从老杨(涂们 饰)从商经历、情人、朋友、家庭四个层面展开，而所有关系最后都落到了金钱上，现代社会的畸形价值观一览无遗。预告中孙子对老杨的漠视让人痛心，他们之间的亲情只靠玩具和金钱维系，观众也从痛恨到慢慢可怜这个老混蛋，感慨道：“我们的亲情正在崩塌，这样的悲剧正在每个人身上发生。”</p>\n<p>　　预告片开头，银行取款机屏幕赫然显示老杨银行卡上75元的余额，即便如此，这个外界看起来的老混蛋，与朋友依旧是君子之交、对亲孙子依然百般疼爱，但是，他的付出在别人看来却是伤害，而身边最亲近的家人也报之以伤害，有观众看完影片后立即联想泰戈尔的诗歌：“世界以痛吻我，要我报之以歌。” 老杨就像他曾经投资过的高耸烂尾楼一样，迷茫无助，毫无存在感。</p>\n<p>　　“看似有情的子女其实无情，看似混蛋的父亲其实最有情。父亲以和全世界对着干的方式表现孤寂却无人理解，最终和这个世界不可调和，越走越远。”这是一位观众在看完影片后的最直观反应，观众们甚至评价：“看完本片的感觉，像西北凛冽的寒风一下子灌进了脖子里，心都凉了一截。” 影片中老杨经历一场斗殴后坐在地上拭干鼻血燃起手中香烟的那一刻令人唏嘘，他那可怜可恨又有些可爱的一生像极了一代中国男人的侧写，不少观众看完影片后的第一个念头，就是给家里的老父亲拨通了电话。</p>\n<p>　　电影中，老杨放生了墙中的鸟，在导演看来，这只鸟就像老杨一样，想要逃离家庭的累赘，却犹如困兽。关雅荻导演看完《老兽》后称：“影片难得找到了人与环境的对立统一关系，视听角度的时空连续性非常好，仿佛就是发生在身边事情。” 表面上无恶不作的老杨是家庭破裂的罪魁祸首，其实，真正让这个家庭崩溃的，是钱。</p>\n<p>　　《老兽》的残酷结局并不是周子�导演想要抛给观众的一个终点，这部电影就像一部现代家庭启示录，《北京日报》评价故事背后的剧本完美诠释了海明威的冰山理论：“内容只是冰山一角，冰山下的东西更具分量。”而《老兽》也在娱乐至死的时代里凸显了它的价值，丰富了中国银幕生态。</p>\n<p>　　《老兽》已于12月11日全国上映，由冬春(上海)影业有限公司出品，方金影视文化传播(北京)股份有限公司、有我(北京)影视文化传媒有限责任公司、北京大桥唐人影视传媒有限公司、西山晴雪(北京)国际影视传媒有限公司、北京惠人新峰文化发展有限公司、广州好家伙传媒有限公司联合出品。影片目前正在热映，一起走进影院，发现身边的老兽。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/34cf3a94-6897-40ca-8bbd-6b8d4e83354a.jpg',NULL,567,'2017-12-15 14:47:00','2017-12-17 01:15:26','68',0),(2865,'马总理：消费税有助低收入群体脱贫','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>吉隆坡12月16日电 马来西亚总理纳吉布在吉隆坡表示，马来西亚推行的消费税，有助于低收入阶层脱贫。</p>\n<p>　　纳吉布15日在其博客撰文时说，低收入阶层在消费税制度下，能够获得更多好处，因为所有的必需品，都免消费税。</p>\n<p>　　马来西亚自2015年4月1日起，开征消费税，税率达到6%。但民众必需之面粉、米、白糖等均不计算在消费税内。尽管如此，政府开征消费税后，一些民众和反对党一直反对政府这一措施。</p>\n<p>　　纳吉布指出，消费税是累进税制，政府将一些必需品豁免消费税，让低收入群体不受影响，而向高收入群体征收的消费税税收也会转化为政府的各项资助计划，用以协助低收入群体提高收入。他说，如今政府的收入，通过一马援助金、农民、渔民和小园主津贴和低收入公务员特别拨款计划，直接惠及低收入群体。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　纳吉布指出，带领马来西亚走向高收入国是政府的目标，因此如何提高人民的收入也成为政府最关注的课题。</p>\n<p>　　他指出，政府的努力已经有了显著效果，40%低收入群体和40%中收入群体的收入，已有明显增加的迹象，贫穷率也在逐渐降低。</p>\n<p>　　纳吉布说，如果没有消费税，这些成果是无法取得的。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1597,'2017-12-16 19:49:00','2017-12-17 01:15:26','71',0),(2866,'近三百名侨界青年入选河北侨联青年委员会第三届委员','孔庆玲','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>石家庄12月16日电 (记者 李茜)河北省归国华侨联合会(以下简称河北省侨联)青年委员会第三次委员大会16日在石家庄召开。来自30个国家和地区的近50名海外青年侨胞与130名河北省内青年侨胞参加。</p>\n<p>　　中华全国归国华侨联合会副主席、中华全国归国华侨联合会青年委员会会长乔卫表示，广大侨界青年兼具中外文化基因，既要做“龙”的传人，也要做文明交融的使者。</p>\n<p>　　本届委员共289人，平均年龄35岁。其中，30岁以下的70人。男性195名，女性94名。硕士以上学历占总数57.4%，较上届增加41%。港澳海外委员149名，来自47个国家和地区，较上届增加34个国家，体现了人选的广泛性。</p>\n<p>　　本届委员会设会长1名，由河北省侨联分管青年委员会工作的副主席兼任；设常务副会长4名，平均年龄38岁；设副会长18名，平均年龄37岁，其中港澳海外副会长10名。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　青年侨胞王金保准备在河北固安投资兴业。他表示，近几年河北迎来京津冀协同发展、2022年冬奥会筹办、雄安新区设立等众多“好消息”，希望参与其中，同时青年侨胞有义务把中国经济的利好政策传播到海外，让更多人了解中国，共享中国发展成就。</p>\n<p>　　青年侨胞、地球村国际教育会长李月明在韩国留学后回国从事中韩教育交流。他表示，受中国经济、文化发展的影响，越来越多的韩国青年选择到中国求学，也有越来越多在韩国获得高等学历的中国青年选择回来。“中国的发展给了中国青年吸引和民族自信。”</p>\n<p>　　乔卫对当天参加大会的侨界青年表示，希望广大侨界青年多做促进民心相通的事情，使中国合作共赢，共同发展的理念深入人心，为中国发展营造良好的国际环境。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,682,'2017-12-16 14:04:00','2017-12-17 01:15:26','73',0),(2867,'中国台湾致公党举行党庆 正式更名“中华民族致公党”','王诗尧','新华网','<p>　　<strong>中国台湾致公党举行党庆 正式更名“中华民族致公党”</strong></p>\n<p>　　新华社台北12月16日电(记者张钟凯 刘刚)中国台湾致公党16日在台北举行17周年党庆活动，并正式宣布修改党徽和党名，改称“中华民族致公党”，以致力于中华民族的伟大复兴。</p>\n<p>　　中华民族致公党创建于2000年1月，前身是华侨社团洪门发起的海外组织。</p>\n<p>　　该党于11月26日召开的党代会上表决通过了更名决议。中华民族致公党主席陈柏光表示，此次改名考量了当前政治生态，有利于专注于提升社会福利、环境保护及民生经济等议题，也是为了呼应中华民族的伟大复兴。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　据陈柏光介绍，中华民族致公党一直以来都在推动两岸交流与和平发展，每个月都举办台湾青年学习营，带领岛内年轻人到大陆参访交流。“台湾年轻人如果能到外滩走一走，到故宫、长城走一走，个人的格局和胸怀就会进一步打开，认识到机会就在大陆。”</p>\n<p>　　台湾立法机构日前通过了新版“政党法”三读，对岛内政党有很多规定和限制。陈柏光表示，中华民族致公党对此会正面看待与因应，并将全面提名本党候选人，参与明年台湾地方公职人员选举。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1877,'2017-12-16 20:08:00','2017-12-17 01:15:27','70',0),(2868,'《中医四大典籍》出版首发 首次合璧中医经典','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>北京12月16日电 (记者 应妮)由中国中医科学院原院长、中华中医药学会副秘书长曹洪欣和北京理工大学生命学院传统医学中心主任武国忠主编的《中医四大典籍》16日在京出版首发。该书首次将《黄帝内经》《仲景全书》《药王全书》《本草纲目(金陵初刻版)》四种合璧出版。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　在16日的出版座谈会上，中华中医药学会副会长兼秘书长王国辰表示，这套书从出版的形式和内容上，都是致力于对中医经典的呈现。读者能从中感受到中华传统文化中的中医经典，更对中医药的发展有着积极发展和促进作用。</p>\n<p>　　国家中医药管理局科技司副司长周杰指出，在中医文化里，中医的博学是中医思维的一个基础，所以这四部经典合辑的问世在中医发展史上都是里程碑的。经典著作要传给后世，但是如何传，让后人如何来理解经典，这就需要一个“悟”字。这套书是曹洪欣和武国忠先生以及中医药学的佼佼者共同完成，两位主编在中医药领域深耕多年，见识卓著，成果频出，他们把自己的“悟”投注在这套书中，并非简单的整理故纸。</p>\n<p>　　第九届、十届全国人大常委会副委员长，北京师范大学人文宗教高等研究院院长、中国文化院院长许嘉璐对该书文献整理工作予以高度评价。他表示，在中国医学文化延绵永续的过程当中，师承的口传心授起到了很大作用，但是没有经典就没有依据，从这个意义上来说文献是基础。《中医四大典籍》重新整理出版，就是给现代和今后中国医学进一步筑基。</p>\n<p>　　《黄帝内经》托名黄帝所作，是中医理论奠基之作。现在所见的《黄帝内经》，是一部由战国至两汉不同时代的医家，搜集当时医学成果，整理加工和补充成篇，汇集而成的医学巨著。《仲景全书》即《伤寒杂病论》，约成书于公元二�五年，为世称“医圣”的东汉名医张仲景所撰。是第一部中医理、法、方、药俱备的经典著作。《药王全书》包括《备急千金要方》和《千金翼方》两部分，为世称“药王”的唐代名医孙思邈所撰。这两部书充分反映了唐初医学的高度发展水平。成书以来，《药王全书》不仅在国内深受欢迎，同时还影响到了国外，如朝鲜、日本等。《本草纲目》是明代著名医药学家李时珍编撰的医药学巨著。问世以来，《本草纲目》先后刊刻三十余次，明代胡承龙刻印的本子为《本草纲目》最早的版本，世称“金陵本”。本次整理，以上海科学技术出版社1993年影印出版的金陵版《本草纲目》为底本，以人民卫生出版社1977年出版的校点本为主校本，参考其他有关文献整理而成，力求反映金陵初刻本《本草纲目》的原貌。</p>\n<p>　　武国忠介绍，此次合璧出版全部采用简体竖排，以方便医务人员、医药院校师生及广大中医药爱好者阅读，使之能在更大范围内得到普及与应用。同时采用善品堂定制安徽泾县纯手工宣纸印刷，双色精印，湖州真丝绢面，耿绢包角，传统古法线装，金丝楠木柜装藏，突出版本甄选、古本辑复、文字校正、经典解读等方面发掘整理研究。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1812,'2017-12-16 20:42:00','2017-12-17 01:15:27','75',0),(2869,'险企股权并购“乍现”冷暖否泰','魏巍','中国证券报','<p><strong>　　险企股权并购“乍现”冷暖否泰</strong></p>\n<p><strong>　　年内上市公司收购保险股权逾470亿</strong></p>\n<p>　　“一半是海水，一半是火焰。”今年的保险业收购和退出股权事件接连登台。一方面是多家险企股东“脱手”旗下股份，意欲退出保险市场。另一方面，今年以来一些“新选手”在保险业投入的资金规模已逾470亿元。业内人士预计，在防风险、去杠杆的外部环境下，保险行业下一步的发展重点不是增加数量，而是提高发展质量，规范经营行为。</p>\n<p>　　□本报记者 程竹 实习记者 林婷婷</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　部分险企股权遭“脱手”</p>\n<p>　　日前，海航投资发布公告称，决定终止2015年度非公开发行股票事项，并向证监会撤回相关申报材料。其中，包括之前海航投资拟收购华安财产保险股份有限公司(简称“华安财险”)19.64%的股权。</p>\n<p>　　海航终止收购险企股权并不是保险业的特例。事实上，今年以来，保险股权遭“脱手”的案例屡见不鲜，据不完全统计，截至目前，今年已有约80亿元的意向资金却步保险行业，有的终止了股权收购计划，有的退出了保险公司筹建，还有部分是因为筹建申请被否。今年上半年拟涉足保险股权投资的上市公司有14家，同比减少了50%以上。</p>\n<p>　　Wind数据显示，在减持保险股权方面，12月8日，众诚保险股东――广东粤财信托有限公司于2017年12月6日在股转系统通过协议转让方式减持7500万股，股份减少5%，权益变动后持股比例为10%。近日，一汽富维挂牌转让亿安保险10%股权，公告显示，该公司拟将该股权进入产权交易中心公开挂牌转让，价格不低于165万元。本次股权转让完成后，该公司将不再持有亿安保险的股权。</p>\n<p>　　在调降投资比例方面，东吴证券将拟收购的东吴人寿股权从原计划的3.09亿股调低至1.99亿股，收购完成后，其总持股比例从原计划的7.725%下调至4.975%。</p>\n<p>　　在股权转让方面，吉祥人寿11月10日完成了一次股权变更，湖南财信投资控股有限责任公司拟将其所持8.8亿股股份中的1.13亿股无偿转让给湖南农业信贷担保有限责任公司。</p>\n<p>　　从蜂拥而入到低调退出，一位险企人士指出，资本“却步”保险的原因各不相同，既有因为合作伙伴出现危机退出者，也有因为政策环境变化转而观望的。</p>\n<p>　　保监会日前发布了《保险公司股权管理办法(第二次征求意见稿)》(简称“办法”)。国务院发展研究中心教授朱俊生表示：“股权办法修改最明显的影响是，社会资本获取保险股权的难度大幅增加。”此前各家公司对保险牌照趋之若鹜，现在大多选择观望。目前业内已经基本形成了共识：当前的监管风格短期不会改变，未来甚至有可能越来越严格。业内人士认为，这或许是不少险企股权面临的窘境，一方面部分股东面对常年亏损和不断加强的监管，想要脱手股份找到接盘者。另一方面审慎的监管环境也让一些抱着特殊目的的入场者知难而退。</p>\n<p>　　据不完全统计，截至11月中旬，保监会今年以来仅仅批准了6家保险公司的筹建，且全部发生在今年1月。而2016年全年，保监会共批准筹建22家保险公司。</p>\n<p>　　资本布局保险业未止步</p>\n<p>　　虽然热度有所退却，但资本并未停下布局的脚步，业内人士认为，2018年，保险业“并购热”仍将持续升温。从今年以来各类资本在保险业密集下注可以窥见，据Wind数据显示，今年以来共有9起涉及上市公司收购保险股权的交易，其中3起为保险经纪公司，相关交易金额超过470亿元。</p>\n<p>　　具体来看，近期，中天金融或其控股子公司拟以现金购买华夏人寿21%-25%的股权。公告显示，本次股权收购完成后，该公司将成为华夏人寿第一大股东。马云旗下云锋基金、巨人网络等6家投资者共耗资131亿元收购了万通保险全部股权。其中云锋金融收购万通保险5.376亿股，占已发行股本的60%。</p>\n<p>　　五矿资本收购工银安盛的股权交易也备受关注。12月4日，五矿资本同意公司向全资子公司五矿资本控股增资158,120万元，用于受让中国五矿集团公司持有的工银安盛人寿保险有限公司的10%股权。</p>\n<p>　　同样瞄准寿险产业的金控集团还有华业资本，该公司欲收购长城人寿5.86%的股权，坐上第三大股东的位置。此外，东吴证券计划2.21亿元收购东吴人寿4.975%的股权。联美控股对财险公司感兴趣，收购信达财险13.33%的股权，耗资6.5亿元。</p>\n<p>　　与此同时，今年共有3起涉及保险经纪公司的收购，包括达华智能收购鹏程保险经纪100%股权，融钰集团收购联合保险经纪100%股权，全新好(000007)参股并购基金收购明亚保险经纪66.67%股权。</p>\n<p>　　保险行业发展路径渐变</p>\n<p>　　尽管陆续有多家险企股东“脱手”旗下股份，意欲退出保险市场；并且社会资本对保险牌照的热情有所减退，从积极投入转向观望，但业内人士认为，在防风险、去杠杆的外部环境下，保险行业下一步的发展重点不是增加数量，而是提高其发展质量，规范其经营行为。</p>\n<p>　　联合资信在《2017年保险行业分析及展望》指出，未来一段时间，寿险行业将持续回归传统的保险保障功能，理财型保险业务将大幅放缓或下降，财险业务将平稳发展；投资方面，保险行业将继续重风控、调结构，在保障保险资金安全前提下稳定资金收益率。在经济增速放缓，去杠杆防风险的背景下，保险业监管也将持续加强。</p>\n<p>　　在投资端，中国保险资产管理业协会副秘书长陈国力表示，行业对主要保险资管产品进行梳理发现，基本符合资管新规的要求，而且资管新规的发布可能改变过去对保险资管不太公平的现状。</p>\n<p>　　国寿资管总裁助理于泳乐观地指出，保险资管行业的春天来了，资管新规整体利好保险业。负债端，保险产品的设计更加注重保障性质，产品久期更长。同时，新规出来之后，保险资管产品将与其他类型的资管产品站在同一起跑线上，这对于下一步规范管理，提升保险资管行业发展水平，将有很大帮助。</p>\n<p>　　分析人士指出，在“保险姓保”的背景下，保险业需要从负债端出发，解决负债端成本高企倒逼投资端提升风险偏好的问题，通过增量调存量的方式实现行业特殊时期的平稳过渡。</p>\n<p>　　中国保监会偿付能力监管部主任赵宇龙指出，下一步保监会准备进一步强化监管。主要是从四个方面完善监管规则：资产端、负债端、资本端、资产负债端。</p>\n<p>　　在资产端，保监会将全面实施对资产的穿透性监管，有效地抑制过度投资和激进投资。</p>\n<p>　　在负债端，要在资本中引入调控特征因子，从而鼓励和引导保险资金更多地投向国家重大战略，投向实体经济。赵宇龙表示，还有区域协调发展战略、乡村振兴战略、健康中国战略、“一带一路”、普惠金融、绿色金融等。</p>\n<p>　　在资本端，将对现在的“合格资本”的定义做出修订，遏制续增资本的乱象。“现在我们对资本的定义是按照国际标准，并不能完全适应中国的市场实际和强化监管的要求。我们下一步将针对中国市场中存在的问题，依据问题导向的原则，增加资本的真实性、合规性的要求。”赵宇龙说，还将采取更加严格的核心资本定义，进一步提升监管的稳健度。</p>\n<p>　　在资产负债端，强化资本负债管理，改进利率风险资本要求，提升行业偿付能力。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,129,'2017-12-15 15:12:00','2017-12-17 01:15:27','67',0),(2870,'澳洲网：研究发现阿尔茨海默症有望提前被检测出','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月15日电 澳洲网15日刊文称，澳大利亚昆州大脑研究所近日进行了一项测试，该测试使得阿尔茨海默症有望提前被检测出来。</p>\n<p>　　文章摘编如下：</p>\n<p>　　据报道，研究显示，在真正患上阿尔茨海默症的10多年前，人类大脑就能显示出该病症的迹象。对此，昆州大脑研究所的研究者们近日正在试图通过一种独特的记忆测试，定位早期阿尔茨海默症的迹象。</p>\n<p>　　据悉，参与测试的人员身体健康。首先，研究者们会向他们展示一系列新的故事。之后，在做磁共振扫描过程中，研究者们再次向他们展示一段新的录像。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　据了解，通过磁共振扫描可以显示出，当参与者试图进行记忆时，大脑中会产生什么变化。这就方便专家们追踪大脑的健康情况。</p>\n<p>　　对此，来自昆州大脑研究所的郭科亭(Christine Guo)向媒体透露到：“通过这项测试，我们了解大脑中发生了什么，同时我们也能了解人们在生病时的身体状况。”</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1094,'2017-12-15 16:55:00','2017-12-17 01:15:27','73',0),(2871,'《西虹市首富》开机 闫非彭大魔沈腾再聚','魏巍','新华网','<p><strong>　　《西虹市首富》开机 闫非彭大魔沈腾再聚</strong></p>\n<p>　　喜剧电影《西虹市首富》今日于厦门正式开机，导演兼编剧闫非、彭大魔，主演沈腾、张一鸣联袂亮相开机仪式。片方正式宣布电影《西虹市首富》定档2018年7月27日，大热的喜剧题材加上闫非、彭大魔、沈腾这样的黄金搭档，相信《西虹市首富》将会为2018年暑期档奉上一桌爆笑的喜剧大餐。</p>\n<p align=\"center\"><img src=\"upload/image/561f1450-96d8-49ec-85ef-8a1c43f6709b.jpg\"></p>\n<p>　　谈到“西虹市”，相信熟悉《夏洛特烦恼》的观众并不陌生，作为故事的“发生地”，“西虹市”也是电影中一个隐藏的小彩蛋。此次《西虹市首富》再次将故事放在了这座让人“特烦恼”的城市，讲述了处于低谷的王多鱼(沈腾饰)意外获得十亿资金，却必需在一个月内花光。要怎样“烧钱”才最快？王多鱼人生中第一次感到“花钱特烦恼”！而这次的“烦恼”将会发生什么样的爆笑故事，让观众十分期待。</p>\n<p>　　除了宣布电影开机以外，片方还发布了首款海报。海报中，主演沈腾攀爬在一朵用钱折叠成的玫瑰花上，寓意“有钱花”，但玫瑰有刺，也暗指了这个钱似乎也没那么容易花。同时，影片还宣布定档2018年7月27日。在2015年国庆档上，《夏洛特烦恼》口碑票房双丰收，成为黑马拿下超过14亿的票房，一举成为行业内外的焦点。而在市场更大消费潜力更高的暑期档上，《西虹市首富》凭借优秀的观众基础，相信会在该档期有优异的表现。</p>\n<p>　　自《夏洛特烦恼》大获成功之后，作为导演兼编剧的闫非和彭大魔反而从公众的视线中消失，直到两年以后，他们才带着新作回归大银幕。开机当天，他们透露，之所以间隔这么久才开拍《西虹市首富》，是希望能够沉淀心态，精心打磨故事，因此仅就剧本就打磨了两年之久。在谈到未来时，闫非和彭大魔表示，会与开心麻花共同打造全新影视品牌“西虹市”，二人将作为创作核心，力争带给观众更多的优质作品。</p>\n<p>　　作为《夏洛特烦恼》成功的另一大因素，演员的精彩演绎也是不可或缺的。此次沈腾作为领衔主演再次回到“西虹市”，也使这部电影如虎添翼。而作为闫非和彭大魔的老搭档，沈腾也表示每次和二人合作都会碰撞出很多灵感，很享受这样的创作过程。希望这次能够继续碰撞出一部好的电影作品，不负观众所望。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/561f1450-96d8-49ec-85ef-8a1c43f6709b.jpg',NULL,915,'2017-12-15 16:06:00','2017-12-17 01:15:27','68',0),(2872,'中华民族致公党拟参与台湾“九合一”选举','张楷欣','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>台北12月16日电 (记者 杨程晨 刘舒凌)中华民族致公党16日举行成立17周年党庆活动，党主席陈柏光就更改党名、党徽一事作出说明。</p>\n<p>　　中华民族致公党创建于2000年1月，前身为华侨社团洪门发起的海外组织，旨在维护海外华侨的正当权益。今年11月26日，该党全代会通过党名、党徽修正案，将中国台湾致公党更名为中华民族致公党。</p>\n<p>　　陈柏光表示，考量当前台湾政治发展现状，为专注社会福利、环境保护及民生经济等议题，避免台湾陷入统“独”争议，也是为了呼应中华民族的伟大复兴，该党作出更名的决定。</p>\n<p>　　他指出，该党一直都在推动两岸交流与和平发展，每月都会带领台湾年轻人到大陆参访、交流。“台湾年轻人如果能到上海外滩、北京故宫、长城走一走，个人的格局和胸怀就会进一步打开，认识到机会就在大陆。”</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　陈柏光称，该党还将参与2018年举行的台湾地方公职人员选举(“九合一”选举)。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1930,'2017-12-16 22:36:00','2017-12-17 01:15:27','70',0),(2873,'93岁香港参类药材加工师傅：一辈子做好一件事','吉翔','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>广州12月16日电 题：93岁香港参类药材加工师傅：一辈子做好一件事</p>\n<p>　　作者 蔡敏婕</p>\n<p>　　“严肃，做事情最重要就是严肃；只有保持药材质量标准，才可以把中药的影响延续下去。”问及如何传承中药材的传统制作工艺，93岁的参类药材加工师傅、香港天祥号第二代传人陈郁均日前在广州多次重复这句话，他已经有超过70年的参类药材制作经验。</p>\n<p>　　由于家境贫困，陈郁均16岁从江门台山前往香港谋生，在香港一家药材铺从事药材加工，一做便是70多年，“一开始什么都不会，跟着师傅做了3年‘学生’后，决定要与众不同，从那时开始，只做参类药材的加工。我这辈子就做好了这一件事情。”</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　与现代大部分以机器加工参类药材不同，陈郁均介绍，传统参类的加工制作工艺是选参、洗参、蒸参、烘干、下尾须、再烘干、包装等流程，主要是通过水蒸的方式，把原本是黄色的参类通过调节火候大小的方式加热或烘干，反复数次，再制成“参饼”。</p>\n<p>　　假如参类蒸制过程中温度和时间控制不当，会出现参类破肚开裂，导致皂苷外溢，使营养成分流失，“在蒸的过程中，遇到次品都会扔出来，只为保持好品质。”陈郁均说。</p>\n<p>　　“我从事参类加工技艺多年，最讲究的便是参类药材的产地来源。”陈郁均表示，不同产地所产出的中药材，在药效和性能方面都有所不同，因此在原料选择上最为严格，“以往选择日本产区的参为主，有时候也会选择中国产区的参，但由于受到环境保护政策的影响，耕地日渐减少，上述两个产区优良野生原料的减少，这对中药材的发展是一种威胁。”</p>\n<p>　　“野生的功效和人工种植的不同，例如野生的花旗参温补为主，不会太寒凉，而人工种植的花旗参则清润。”陈郁均说。</p>\n<p>　　“西药见效快，但是有副作用；中药见效慢，但是副作用小。”陈郁均笑称，他如今93岁的高龄，仍能思维清晰和手脚灵敏，除了日常坚持轻跑一百下等轻微运动外，还因为闻了参气，“70多年来，我都只从事参类的加工工作，每次到仓库看货，打开装有参类的盒子，都会有参的气味冲上来，可能参类真的有保养的功效，中药对人还是很有用的。”</p>\n<p>　　广州同康药业有限公司董事长沈小葵日前拜陈郁均为师父，并聘请陈郁均为该公司的技术顾问。沈小葵称，传统中医药专业加工技术已经接近失传，希望通过拜师仪式把传统技艺传承下来。</p>\n<p>　　面对中药材来源受污染并日益缺少的问题，沈小葵表示，目前该企业从加工技术、肥料等方面建立追溯体系；由于濒危中药材越来越稀少，希望实现有序开发，同时，在加工中药材方面，将推出中药饮片、滴丸剂、超微粉剂等中药制剂，迎合年轻人市场。</p>\n<p>　　“中药将来还是有市场的，因为有很多老一辈都相信中药，年轻一辈比较忙，没有这么多时间煲中药，因此研发中药饮片是将来发展的趋势。”陈郁均说：“我希望中药可以发展得越来越好，望后辈多努力创新。”(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1574,'2017-12-16 19:28:00','2017-12-17 01:15:27','69',0),(2874,'报告显示中国经济转型正重塑各行业职工养老储备水平','张楷欣','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>北京12月16日电 (王庆凯)16日发布的《2017中国大中城市职工养老储备指数报告》显示，经济转型正在重塑中国各行业职工养老储备水平。知识性和技术性较强的行业储备指数提升较快，而一些支持过去中国高速增长的行业，如房地产行业、建筑业和制造业指数增长偏慢。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　这份由中国保险行业协会、中国平安养老保险股份有限公司等部门联合发布的报告显示，2017年中国大中城市职工养老储备整体处于“基本水平”区间的下限位置，符合中国目前经济社会发展现状，未来提升空间巨大。</p>\n<p>　　“职工养老储备”是指职工在工作期间已积累和将要积累的用于养老目的的各种权益、现金流和资产。该储备水平不仅取决于已经积累水平(充足度)，也与未来的积累能力(稳定度)和积累意愿(认知度)有关。</p>\n<p>　　报告显示，中国职工养老储备充足度和稳定度指数相对2015年明显增强。这得利于近年来中国政府致力于基本养老保险的扩面工作，尤其是实施“全面参保登记计划”。</p>\n<p>　　指数的提升符合中国社会发展现状，但随着人口老龄化加速和居民生活水平提高，进一步提升中国职工养老储备水平已成为迫切要求和必然趋势。</p>\n<p>　　进入21世纪，中国人口老龄化趋势日益明显，与发达国家相比，中国人口老龄化速度和严重程度前所未有。中国保险行业协会会长朱进元在会上透露，截止到2016年底中国60岁以上的老龄人口已经超过2.3亿，占总人口的19.7%。预计2050年中国老龄人口将达到4.8亿，占亚洲人口40%，全球人口的25%，超过美国、德国和英国的人口总和。</p>\n<p>　　“然而，中国属于未富先老。”中国平安养老保险股份有限公司总经理甘为民表示，2016年中国人均GDP为8865美元，仅排名全世界第69名，与发达国家相比，中国面临的养老问题更加复杂，中国民众养老需求巨大。</p>\n<p>　　报告显示，基本养老保险充足度指数为90.6，说明基本养老保险全覆盖已基本实现，但只是“广覆盖、保基本”。此外，企业年金充足度指数和企业保障程度指数明显下降，说明随着经济全球化进一步深化，企业竞争不断加剧，对于还处于发展中国家阶段的中国而言，企业为职工提供更加健全福利保障的动力不足。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1734,'2017-12-17 00:41:00','2017-12-17 01:15:27','66',0),(2875,'港中大师生追忆余光中：诗人若近，人间似远','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>香港12月15日电 <strong>题：港中大师生追忆余光中：诗人若近，人间似远</strong></p>\n<div style=\"text-align:center\"> \n <img alt=\"余光中是中山大学外国语文系荣誉退休教授，今年并没有在校开课，他曾任中山大学外国语文学系、高雄第一科技大学应用英语系讲座教授、中山大学文学院长、香港中文大学联合书院中文系系主任、美国西密执安州立大学英文系副教授。图为2015年11月8日，余光中在开馆仪式上致辞，当日，余光中文学馆在福建永春举行开馆仪式。 <a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 吕明 摄\" src=\"upload/image/a45c1595-f116-4cbb-8fca-cde7a327894f.jpg\" title=\"余光中是中山大学外国语文系荣誉退休教授，今年并没有在校开课，他曾任中山大学外国语文学系、高雄第一科技大学应用英语系讲座教授、中山大学文学院长、香港中文大学联合书院中文系系主任、美国西密执安州立大学英文系副教授。图为2015年11月8日，余光中在开馆仪式上致辞，当日，余光中文学馆在福建永春举行开馆仪式。 <a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 吕明 摄\"> \n <input id=\"1296e2fe0e7242f1bf20997dbcb12a77\" name=\"1296e2fe0e7242f1bf20997dbcb12a77\" type=\"hidden\" value=\"http://www.chinanews.com/tp/hd2011/2017/12-14/788515.shtml\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：2015年11月8日，余光中在开馆仪式上致辞，当日，余光中文学馆在福建永春举行开馆仪式。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 吕明 摄 \n</div>\n<p>　　作者 殷田静子 洪蔚琳 何香奕</p>\n<p>　　一代文学大师、著名诗人余光中14日在台湾高雄病逝。曾在香港中文大学(中大)任教过11年的他，为中大作出不少贡献，也留下了抹不去的印记。</p>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者15日走访中大校园，采访了曾经与余光中有过交集的老师以及学生，回忆与余光中生前交往的点点滴滴。</p>\n<p>　　“第一次见余老师时，我的脑中浮现出四个字‘谦谦君子’”。中大中文系退休教授张双庆是当时负责去香港机场接余光中的“司机”，他对余光中的第一印象，就是“很客气”。</p>\n<p>　　1974年，张双庆还只是中大的讲师，而余光中已经很有名气。他回忆，余老师对每个人都是平等对待，彬彬有礼，非常客气。他笑说，“余老师从不搞麻烦，很谦逊，而且是个很念旧情的人”。</p>\n<p>　　说起余光中对现代文学发展的重要性，张双庆毫不犹豫地说，“他加入中大促进了现代文学在香港的发展”。他回忆称，余老师设计了很多新课程，与学生关系融洽，学生不仅成绩有显著提高，甚至有些同学慕余光中之名来报考中大。“在他的培养下，香港的确有了一批像黄秀莲、王良和这样的优秀作家”。</p>\n<p>　　与余光中相识约50年、共事近9年的原中大教授黄维梁有太多与余光中的回忆。</p>\n<p>　　他介绍，余光中当年和自己、梁锡华、黄国彬等文学专业的教师结下友谊，并称大家为“沙田帮”，闲时大家常有聚会，比如端午节以方言吟诵屈原作品，又或一起登凤凰山、八仙岭，参加文学活动，并带动学生养成了写文章的风气。</p>\n<p>　　与记者聊起和余光中在中大度过的时光，黄维梁有说不完的故事。</p>\n<p>　　他眼中的余光中，是一个“认真”、“积极向上”、“有联想力”、“有观察力”、“风趣”、“幽默”的好友。他回忆道，“当时学生很多，每到期末，他至少花一周时间批改学生们的论文。”</p>\n<p>　　黄维梁还记得有一次老师们要交报告，大家普遍都是一页纸，但余光中的报告有足足8页。“他有很多教学文化活动，还要接待访客，还需要时间创作，竟然可以这么认真地写报告”，黄维梁对此记忆犹新，十分敬佩。</p>\n<p>　　余光中1985年回到台湾后，仍多次重临中大，担任访问学人及国际研讨会主讲嘉宾。</p>\n<p>　　中大中文系应届毕业生袁学慧就曾听过两次余光中的讲座，最让她印象深刻的，是余光中的幽默睿智。</p>\n<p>　　“曾经他在讲座上说过‘文学是闲出来的’，我感同身受”。袁学慧说，现在中文系的学生很忙，经常会忽略身边的东西。但余先生之所以能将“中大里的雾”、“沙田的地貌”等用散文形式写出来，就是因为他很留意身边的一草一木。</p>\n<p>　　袁学慧对余光中的作品名字和内容信手拈来――《记忆像铁轨一样长》、《我的四个假想敌》、《文学的沙田》、《等你，在雨中》等。她对余光中的突然离世感到伤心，“余老师的作品是我们对童年文学的回忆，他的逝去，代表着一个时代过去了”。</p>\n<p>　　不光是中文系学生对余光中的作品着迷，中大电子工程专业本科生刘宇通也曾在2015年听了两场余光中的讲座。“老先生看起来精神不错，一读诗就很有精气神，也爱开玩笑”。</p>\n<p>　　他回忆道，余老师在读写给他妻子的《珍珠项链》时，突然停下来说道――我妻子就是坐我对面离我最近的。“本来当时很学术的氛围，突如其来的秀恩爱，令人意想不到”。</p>\n<p>　　余光中曾说，中大给他很多写作灵感，沙田、马料水的环境得天独厚，校园就是在风景区里。</p>\n<p>　　中大中文系研究助理教授胡琦与余光中并无交集，但之前在课堂上和学生一起读过《沙田山居》。“教室正好在康本，于是我索性拉起窗帘，放进吐露港的天光云影一起上课，看到对面山上云雾间悠悠然时有车辆穿行，突然就感到进入了一个别有天地的时空之中。”</p>\n<p>　　胡琦说，“这也许就是文学的一种神奇的效果吧。今天的我们和当年的诗人重逢在沙田的山头。诗人若近，人间似远。”(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/a45c1595-f116-4cbb-8fca-cde7a327894f.jpg',NULL,1063,'2017-12-16 00:25:00','2017-12-17 01:15:27','69',0),(2876,'姚文智确定选台北市长 民进党恐难礼让柯文哲','王诗尧','中国台湾网','<p><strong>　　“绿委”姚文智确定选台北市长 民进党恐难礼让柯文哲</strong></p>\n<p>　　中国台湾网11月16日讯 民进党恐将难以直接礼让柯文哲参选台北市长！据台湾《中时电子报》报道，民进党“立委”姚文智宣布将在明天(17日)下午三点，号召党内20位“立委”及逾3000位支持者在台北世贸国际会议中心，正式宣布投入参选2018台北市长，他更喊出“台北要做台湾市长”的口号。换言之，柯文哲若要顺利争取连任，首要之战将面对姚文智的挑战。</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图为现任台湾市长柯文哲。 <a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 刘舒凌 摄\" src=\"upload/image/f64060f1-711c-41b5-820e-997b84ee5b9b.jpg\" title=\"资料图为现任台湾市长柯文哲。 <a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 刘舒凌 摄\"> \n <input id=\"a91b807ffd9242c6b4e1572c740a52b4\" name=\"a91b807ffd9242c6b4e1572c740a52b4\" type=\"hidden\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图为现任台湾市长柯文哲。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>发 刘舒凌 摄 \n</div>\n<p>　　民进党日前通过2018“‘直辖市’和县市长提名特别条例”，明订非执政县市如台北市，将由选对会协调适当人选，但未规定要入党，因而被视为“柯文哲条款”。但如今，随着民进党内有明确候选人表态参选，民进党中央选对会必然得从新思考台北市提名争议。</p>\n<p>　　对于明天将宣布参选，姚文智称，他将以“地壳改造运动！”为主题，在台北国际会议中心发表台北城市愿景演讲会。(中国台湾网 李宁)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/f64060f1-711c-41b5-820e-997b84ee5b9b.jpg',NULL,111,'2017-12-16 15:45:00','2017-12-17 01:15:27','70',0),(2877,'冯绍峰新戏海报及特辑发布 揭视效制作过程','罗攀','环球网','<p>　　<strong>《今生有幸》曝海报 好莱坞视效助力人妖乱战</strong></p>\n<p>　　由陈国富监制，肖洋执导，冯绍峰、刘亦菲、李光洁、郭京飞领衔主演，焦俊艳、熊乃瑾友情出演的现代妖精电影《二代妖精之今生有幸》将于12月29日全国上映。今日，片方发布了“妖你好看”版海报及视效制作特辑，揭开奇幻人妖恋的诞生。</p>\n<p align=\"center\"><img src=\"upload/image/bea8c734-c76a-4550-aea2-83fa8462789b.jpg\"></p>\n<p>　　在“妖你好看”版海报中，妖精们混入人间，乘坐“妖精号”地铁往返人妖两界，正值地铁高峰期，狐妖白纤楚(刘亦菲 饰)、妖怪管理局(以下简称“妖管局”)局长云中鹤、猫妖洪思聪(郭京飞 饰)“幸运”的挤上同一节车厢，唯一的人类袁帅(冯绍峰 饰)误入其中，面对一车妖精面色惊恐一脸蒙圈，被群妖团团围住。车窗外，还有不少妖精没有挤上这列地铁，这辆跨越两界的列车即将到达人界，一场跨界人妖混战拉开帷幕。而特辑则对后期特效制作进行了大揭秘，妖精变身细致入微的制作过程让人大开眼界。</p>\n<p>　　“妖精号”发车最怂人类乱入，群妖原形毕露即将抵达战场</p>\n<p>　　电影《二代妖精之今生有幸》描绘了一个充满现代风的人妖共存新世界，生活在现代都市的妖，要想成功隐藏身份混入人群，除了变成人形，生活方式也要与人类一样，日常挤地铁也是现代妖精必修的一门课程，在“妖你好看”版海报中妖精和人类一样需要在上下班高峰期挤地铁。</p>\n<p>　　海报中，狐妖白纤楚和猫妖洪思聪显然是挤地铁的老手，在妖群中仍不忘保持优雅，洪思聪戏精本精对着车窗整理发型，时刻保持“妖界爱豆”的良好形象。虽然地铁中妖种繁多，但狐妖白纤楚难掩“妖界颜王”的妖设，一条毛茸茸的狐狸尾巴尽显狐妖本色。相对于两人的淡定，高冷的妖管局局长云中鹤明显是第一次坐地铁，异常狼狈，然而就算站不稳，也要保持云局长的高傲，绝不低头就怕“皇冠会掉”。在这个封闭逼仄的列车空间里，冯绍峰饰演的袁帅作为车厢中唯一一个人类被巨型妖精包围着，边上的狐妖白纤楚目光坚定地守护着这位人类以及他们之间的这段跨界人妖禁忌恋，与群妖们摩擦出一股浓浓的火药味，人妖两界会不会爆发一场乱战？请拭目以待！</p>\n<p>　　人妖混战惊现好莱坞“黑科技”，妖精变身特效精确到毛</p>\n<p>　　魔都上空撕裂两界次元壁的人妖混战即将爆发，当现代大都市惊现群妖乱战，前所未见的场面引爆观众无限遐想。作为元旦档唯一一部3D现代奇幻妖精电影，为了更好呈现片中人妖共存的奇幻世界以及人妖混战壮观场面，制片方特意邀请了萨姆・霍尔希德(Sam Khorshid)担任本片的特效指导。Sam曾为《钢铁侠》、《鬼吹灯之寻龙诀》、《敢死队3》等多部大制作担任特效指导，凭借《鬼吹灯之寻龙诀》斩获第53届台湾金马奖凭借最佳视觉效果奖，他参与特效指导的《爱丽丝梦游仙境》曾获第83届奥斯卡金像奖最佳视觉效果奖提名，好莱坞级别的特效指导极致还原本片人妖共存的现代都市全貌。在这版视效制作特辑中，Sam讲述了特效制作过程各种难点，解密电影中“巨型妖精的诞生”。</p>\n<p>　　毛发的特效制作一直是国际难题，好莱坞视效制作曾有一条铁律：“永远别碰动物和水”，但因剧情需要，电影中有一段白纤楚一点点长出狐狸毛变身的全过程。Sam坦言这是整部电影中最难的特效，为此他动用了只有好莱坞大片才会使用的MOCO(高科技交互式摄影控制系统)进行后期特效制作，最终呈现了电影中炫酷的狐妖变身镜头。不仅如此，电影中洪思聪在云中鹤的压迫下突变猫妖脸、白纤楚头部变狐狸等镜头都运用了这一黑科技。当好莱坞“黑科技”与现代妖精结合，人妖混战会有多震撼？12月29日，请到电影院“验货”！</p>\n<p>　　《二代妖精之今生有幸》由工夫影业无锡有限公司、北京京西文化旅游股份有限公司出品，上海腾讯影业文化传播有限公司、上海东威影视有限公司、北京合瑞影业文化有限公司、北京耐飞影视有限公司联合出品，12月29日《二代妖精之今生有幸》全国上映，妖精们即将抵达人界，注意这不是演习，人类请小心！</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/bea8c734-c76a-4550-aea2-83fa8462789b.jpg',NULL,1886,'2017-12-15 15:33:00','2017-12-17 01:15:27','68',0),(2878,'北京小客车猛增势头遏制 明年普通购车指标减至4万个','孙静波','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>北京12月15日电 (记者 杜燕)2011年1月至2017年11月底，北京小客车净增95.4万辆，年增速由2010年的24.3%降至2.4%。明年，北京市小客车配置指标总量将由目前的15万个减少到10万个，其中新能源指标保持6万不变，普通指标由9万个减少至4万个。</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：北京市小客车指标摇号现场。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 陆欣 摄\" src=\"upload/image/2fb2706b-b74d-4db1-8eda-0e2dea99933d.jpg\" style=\"border:px solid #000000\" title=\"资料图：北京市小客车指标摇号现场。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 陆欣 摄\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  资料图：北京市小客车指标摇号现场。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>发 陆欣 摄 \n</div>\n<p>　　15日，北京公布《关于2018年小客车指标总量和配置比例的通告》(以下简称《通告》)。根据《北京市“十三五”时期交通发展建设规划》和《北京市城市总体规划实施工作方案(2017年至2020年)》提出“2020年底全市机动车保有量控制在630万辆以内”的目标，《通告》明确，2018年年度小客车配置指标总量由15万个减少到10万个，且普通指标和新能源指标的配额都将进一步向个人倾斜。</p>\n<p>　　其中，个人普通指标占比从2017年的92%提高到95%(3.8万个)、单位指标和营运指标分别占年度配额的4%(1600个)和1%(400个)；个人新能源指标占比从85%提高到90%(5.4万个)、单位指标和营运指标各占年度配额的5%(各3000个)。</p>\n<p>　　北京交通部门指出，自2011年实施小客车数量调控政策以来，北京实现了小客车数量合理有序增长，为缓解城市道路交通拥堵和减少机动车尾气排放做出了巨大贡献。2011年1月至2017年11月底，北京小客车净增95.4万辆，年增速由2010年的24.3%降至2.4%。</p>\n<p>　　同时，指标调控政策有力推动了新能源汽车的示范应用。目前北京已注册登记纯电动小客车超过13.78万辆，居中国内地之首。</p>\n<p>　　北京市交通部门表示，从目前发展形势来看，小客车无序迅猛增长的势头得到了有效遏制，但机动车持续增长对大气质量、交通运行和城市空间所形成的压力仍然很大。因此，以承载力作为约束性条件进一步放缓机动车增速十分必要。</p>\n<p>　　记者了解到，近年北京多次修订完善小客车数量调控政策，比如于2013年11月27日修订的内容包括设置个人摇号阶梯中签率，以缓解“久摇不中”的压力；增加新能源小客车指标配置等。2016年1月22日进一步调整普通小客车指标摇号阶梯中签率进阶规则，加大对“久摇不中”申请人的照顾力度。</p>\n<p>　　北京市小客车指标调控管理办公室表示，今后还将继续听取民众呼声，坚持公平公正公开的原则，结合城市治理精细化和规范化能力的不断提升，提高机动车总量控制的管理和服务水平。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/2fb2706b-b74d-4db1-8eda-0e2dea99933d.jpg',NULL,1181,'2017-12-15 15:49:00','2017-12-17 01:15:27','72',0),(2879,'中国企业慈善公益500强名单在京发布 腾讯居榜首','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>北京12月16日电(记者 张希敏)由《企业观察报》主办的2017中国企业慈善公益500强发布16日在北京举行，腾讯控股有限公司以26.78亿元人民币高居榜首。</p>\n<p>　　据《企业观察报》副社长王少华介绍，2017中国企业慈善公益500强入围门槛为：2016年度企业在慈善公益方面的投入总金额不低于100万元。</p>\n<p>　　他说，榜单排名数据主要来自五个方面：一是中华慈善总会等各类慈善公益机构发布或提供的数据。二是中国企业联合会、中国企业家协会发布的2017中国企业500强营业收入数据。三是全国工商联发布的2017中国民营企业500强营业收入数据。四是企业发布的当年社会责任报告中的相关数据。五是企业自主申报的上年度慈善公益方面投入的数据，包括企业员工参加志愿者活动投入的总时间所折合的金额，也包括超比例按排残疾人就业折算的金额。</p>\n<p>　　王少华说，目前中国还有大批中小企业，这些企业虽然营业收入无法与中国500强企业相比，但也有一部分积极履行社会责任，热心公益事业，超比例安排残疾人就业，为推动社会持续健康发展发挥了积极作用。为展示这些企业的慈善公益形象，组委会在听取专家和社会各界人士的建议后，推选出137家为中国慈善公益好企业进行表彰。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　中国社会福利基金会原副理事长缪力在讲话中说，该活动让人们看到了中国慈善公益事业的蓬勃发展，也让中华民族在建设人类命运共同体中，挺起了高高的脊梁。它引领了中国企业向好向善向上的潮流，是对社会发展负责任的担当，推动了社会公平，关爱了弱势群体，提升了公益机构的水平，培养了公益专业人才的大军，传播了中国优秀传统文化，促进了社会的和谐。</p>\n<p>　　同时，她建议，企业做慈善公益要从企业的自身价值和使命出发，弘扬企业之优势，解决社会的痛点，填补公益产品的空白。</p>\n<p>　　主办方表示，“中国企业慈善公益500强”名单发布既是激励先进，也是鞭策后进，将督促企业主动承担企业社会责任，不断加大慈善公益投入。名单的发布既有利于中国企业的长远发展。</p>\n<p>　　中共中央对外联络部原副部长马文普，中国银监会原监事会主席孙希岳，国资委新闻中心首席专家、中国企业改革与发展研究会原副会长、《中国企业报》原总编李锦等出席活动。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1836,'2017-12-16 20:43:00','2017-12-17 01:15:27','64',0),(2880,'中国西南少数民族文献与跨境民族语言研究院云南揭牌','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>昆明12月16日电 (记者 马骞)16日，来自国内研究机构、高校相关语言专家齐聚昆明，中国西南少数民族文献与跨境民族语言研究院在云南民族大学揭牌。</p>\n<p>　　云南民族大学副校长段钢介绍了云南省少数民族语言文献资源的优势和该校中国少数民族语言文学专业开设的语种情况，以及该校在少数民族语言文献研究中所取得的成绩。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为中国西南少数民族文献与跨境民族语言研究院揭牌 钟欣 摄\" src=\"upload/image/913cae74-e3d3-4a42-9971-10d3e49076eb.jpg\" title=\"图为中国西南少数民族文献与跨境民族语言研究院揭牌 钟欣 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为中国西南少数民族文献与跨境民族语言研究院揭牌 钟欣 摄 \n</div>\n<p>　　当日，“南方少数民族小文种文献保护与整理研究”开题报告会同期举行，这是以云南民族大学校柔性引进的高层次人才黄建明教授为首席专家的2017年度国家社会科学基金重大招标项目。中国社科院民族所原副所长黄行担任报告会组长，成员由西南民族大学原副校长沙玛拉毅、云南师范大学副校长安学斌、南开大学教授石锋、中央民族大学教授张铁山、云南民族博物馆教授普卫华和云南民族大学教授刘劲荣担任。</p>\n<p>　　黄建明向参与开题报告会的专家做了整个项目的工作汇报，专家们对该项目给予了高度的评价和充分的肯定，同时也诚恳地提出了整个项目在研究中有待于修改、完善的宝贵意见和建议。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/913cae74-e3d3-4a42-9971-10d3e49076eb.jpg',NULL,407,'2017-12-16 20:41:00','2017-12-17 01:15:27','75',0),(2881,'减少污染 台中今年跨年不放烟火、台北101减量放','吉翔','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月16日电 据台湾《联合报》报道，台湾反空污大游行将于17日在台中、高雄举行。台中市长林佳龙15日宣布，为落实环保，今年起官方跨年活动不再放烟火，减少污染；高雄市府则表示，市府从未放烟火庆祝跨年，今年也不会。</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：台北101大楼跨年烟火秀。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 董会峰 摄\" src=\"upload/image/decd84b4-ee1b-4b56-a6ae-90bad82ae6bb.jpg\" title=\"资料图：台北101大楼跨年烟火秀。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 董会峰 摄\"> \n <input id=\"f1060b44b1ca475a8e7c4ee4be821a8e\" name=\"f1060b44b1ca475a8e7c4ee4be821a8e\" type=\"hidden\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：台北101大楼跨年烟火秀。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>发 董会峰 摄 \n</div>\n<p>　　跨年烟火会造成多少污染？台中市环保局统计，以去年跨年1.7万发烟火估算，不放烟火可减少碳排放量1.74吨，还有垃圾、噪音减量等；不过一场跨年活动的碳排量为98.24吨，烟火只占2%，交通运输占81%、废弃物占17%。</p>\n<p>　　近年台湾陆续有县市响应环保停放跨年烟火，包括台南市2011年起以声光秀替代，台东县、彰化县前年起取消，嘉义市去年也停放。</p>\n<p>　　林佳龙15日说，跨年烟火造成空气污染，取消放烟火响应反空气污染，市府节约100万新台币经费办活动，为改善空气质量做最大努力。</p>\n<p>　　南投县长林明溱说，空气污染问题越来越严重，为响应环保，去年跨年晚会就不放烟火，希望县民也配合减少鞭炮。另外，新竹市府今年不办跨年晚会，改为提前在23日办感恩晚会，也决定不放烟火。</p>\n<p>　　桃园市府今年续放跨年烟火，将由世大运创下亚洲标枪纪录的郑兆村，在会场中距离展演中心91.36米(即亚洲纪录)处，在跨年倒数3秒时，象征性做出掷标枪动作，透过光束启动3分钟烟火秀。</p>\n<p>　　屏东县府也持续烟火庆跨年，发言人黄建嘉表示，每年烟火都只是“意思一下”，县长潘孟安特别交代不要放太多。</p>\n<p>　　台北101强调：鉴于空气污染严重，今年跨年烟火16000发，比去年少4000发，另增加LED灯网、多媒体视觉创新。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/decd84b4-ee1b-4b56-a6ae-90bad82ae6bb.jpg',NULL,627,'2017-12-16 14:38:00','2017-12-17 01:15:28','70',0),(2882,'中国法律援助机构已入驻1700多个人民法院','王诗尧','新华网','<p>　　<strong>我国法律援助机构已入驻1700多个人民法院</strong></p>\n<p>　　新华社北京12月16日电(记者王茜)记者日前从司法部获悉，目前，法律援助机构在1700多个人民法院建立了工作站，建站率为52%。</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：人民法院。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 李慧思 摄 \" src=\"upload/image/e5b0b7e5-4074-46b5-b996-7141500a502b.jpg\" title=\"资料图：人民法院。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 李慧思 摄 \"> \n <input id=\"ad3b879aa3a54d588291c5a6ec5d3117\" name=\"ad3b879aa3a54d588291c5a6ec5d3117\" type=\"hidden\" value=\"http://www.chinanews.com/tp/hd2011/2015/07-17/542584.shtml\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：人民法院。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>发 李慧思 摄 \n</div>\n<p>　　值班律师制度是通过在人民法院、看守所派驻值班律师，使没有辩护人的犯罪嫌疑人、被告人在刑事诉讼各阶段都能获得初步法律帮助。</p>\n<p>　　数据显示，在这1700多个工作站里，上海、浙江、安徽、河南建站率为100%。同时，看守所法律援助工作站建站率为88%，其中，北京、内蒙古、吉林等13个省(区、市)实现了看守所工作站全覆盖。</p>\n<p>　　司法部法律援助工作司司长白萍表示，司法部将在全部人民法院、看守所建立法律援助工作站，并联合公安部出台规范看守所法律援助值班律师工作的意见。同时，围绕发挥工作站职能作用，开展规范化工作站创建活动，推出一批示范典型。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/e5b0b7e5-4074-46b5-b996-7141500a502b.jpg',NULL,1983,'2017-12-16 19:28:00','2017-12-17 01:15:28','64',0),(2883,'故宫珍藏皇家武备精品展在澳门揭幕','李夏君','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>澳门12月15日电 (记者 龙土有)“大阅风仪――故宫珍藏皇家武备精品展”15日在澳门艺术博物馆揭幕，澳门特区政府社会文化司司长谭俊荣、故宫博物院副院长李小城、中央政府驻澳门联络办公室宣传文化部部长万速成等嘉宾出席开幕式。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　澳门艺术博物馆馆长陈继春在开幕式上致辞时表示，将继续助力弘扬优秀中华民族文化艺术，增强市民文化归属感的工作。同时期望与故宫博物院达成更深、更广的合作，共同促进大众对中华文化的认识与认同。</p>\n<p>　　“大阅风仪”展以皇家武备为题材，展出清宫珍藏160多件套，分为“威仪”“尚武”“制度”三大专题，展品包括皇家御制品、御用品、官造器械以及各地进贡品等，从中可以多角度了解到清代帝王大阅、狩猎、军队操练及兵制等情况，以及清代八旗兵的构成、武器装备的种类等，增加对清代政治和军事制度的认识。</p>\n<p>　　展览由故宫博物院、澳门艺术博物馆、澳门基金会、澳门特区政府旅游局及澳门日报社合办，展期至2018年3月11日。期间还举办一系列配套活动，有故宫博物院专家讲座、工作坊、专题导赏以及小型中乐演奏会等。</p>\n<p>　　(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1379,'2017-12-15 21:31:00','2017-12-17 01:15:28','69',0),(2884,'报告显示：2017年中国职工养老储备处于基础水平','王诗尧','新华网','<p>　　<strong>机构报告显示：2017年我国职工养老储备处于基础水平</strong></p>\n<p>　　新华社北京12月16日电(记者谭谟晓)16日发布的《2017年中国大中城市职工养老储备指数报告》显示，2017年中国职工养老储备指数为60.8，比2015年的59.7略有提高，储备状况处于基础水平。</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：养老保险中心。 <a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 韦亮 摄\" src=\"upload/image/0d8d8789-a693-44aa-bd61-523544e4ffbe.jpg\" title=\"资料图：养老保险中心。 <a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 韦亮 摄\"> \n <input id=\"8651e39d80af4fb8b5746119082f980d\" name=\"8651e39d80af4fb8b5746119082f980d\" type=\"hidden\" value=\"http://www.chinanews.com/part/561/2017/0710/1298.html\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：养老保险中心。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 韦亮 摄 \n</div>\n<p>　　这份报告由中国保险行业协会、中国劳动和社会保障科学研究院、中国社科院世界社保研究中心、中国经济信息社、平安养老保险公司联合发布，调查了全国36个具有代表性的大中城市5300多名城镇企业单位职工。报告每两年发布一次。</p>\n<p>　　分行业看，科学研究、技术服务和地质勘测业、金融业的职工养老储备最高，排名最低的行业是住宿和餐饮业。</p>\n<p>　　报告显示，企业年金计划和商业养老保险是职工养老储备的重要组成部分，但受访者中，参加企业年金计划的职工人数比例仅为15.5%，购买个人商业养老保险的职工比例为38.5%。</p>\n<p>　　报告还显示，中国城镇职工平均拥有不动产为1.01套，比2015年的1.06套略有下降，主要是年轻人住房拥有量下降所致。从金融资产持有情况来看，36个大中城市职工平均金融资产为35.32万元，相比2015年的59.8万元明显下降。</p>\n<p>　　报告建议，强化个人自我保障是提升养老储备水平的最优选择，职工参加基本养老保险并连续足额缴费，参加企业年金，购买各种商业保险计划、个人养老保险产品或储备其他资产等是提升养老储备水平的重要手段。对政府而言，应加大对商业养老保险的税收优惠支持力度。</p>\n<p>　　职工养老储备是职工在工作期间已积累和将要积累的用于养老目的的各种权益、现金流和资产。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/0d8d8789-a693-44aa-bd61-523544e4ffbe.jpg',NULL,1826,'2017-12-16 18:06:00','2017-12-17 01:15:28','66',0),(2885,'日外相称日本将参加2022年安理会非常任理事国竞选','孔庆玲','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月16日电 综合报道，日本外相河野太郎15日下午在美国纽约的联合国总部召开记者会，表示日本将参加2022年的联合国安理会非常任理事国竞选。他强调说：“将尽可能频繁地作为理事国持续为维护国际社会的和平与安全作出贡献。”</p>\n<p>　　日本从2016年起第11次担任非常任理事国，是联合国成员国中担任次数最多的，本月底将结束为期2年的任期。</p>\n<div style=\"text-align:center\"> \n <img alt=\"日本自民党第84届大会3月5日在东京举行，正式决定将其党章中原先规定的“最多2届6年”的总裁任期，改为“最多3届9年”。藉此，现任日本首相安倍晋三，得以在明年秋季其任期届满后，连续第三次参选自民党总裁，从而打开其孜孜以求的长期执政之门。 <a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 王健 摄\" src=\"upload/image/d780ebf5-af41-414f-8fed-21533bdd8efd.jpg\" title=\"日本自民党第84届大会3月5日在东京举行，正式决定将其党章中原先规定的“最多2届6年”的总裁任期，改为“最多3届9年”。藉此，现任日本首相安倍晋三，得以在明年秋季其任期届满后，连续第三次参选自民党总裁，从而打开其孜孜以求的长期执政之门。 <a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 王健 摄\"> \n <input id=\"88ed09caebbf499b8a6dffb9c104afe6\" name=\"88ed09caebbf499b8a6dffb9c104afe6\" type=\"hidden\" value=\"http://www.chinanews.com/tp/hd2011/2017/03-05/720974.shtml\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：日本首相安倍晋三。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 王健 摄 \n</div>\n<p>　　据悉，日本此前一直主张扩大常任和非常任理事国的安理会改革。日本首相安倍晋三去年在日本加入联合国60周年纪念研讨会上，曾表现出使日本成为安理会常任理事国的意愿。</p>\n<p>　　据了解，1980年日本首次公开提出将“入常”纳入其成为“国际大国”的战略目标。2000年，时任日本首相小泉纯一郎与同样谋求提升国际地位的德国、巴西、印度组成“四国联盟”，打算以抱团的方式闯关。</p>\n<p>　　之后，四国还提议将安理会常任理事国由5个增至11个、非常任理事国由10个增至14个到15个。为此，安倍政府目前正在积极拉拢包括非洲在内的多国，意图实现这一目标。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/d780ebf5-af41-414f-8fed-21533bdd8efd.jpg',NULL,868,'2017-12-16 16:06:00','2017-12-17 01:15:28','71',0),(2886,'杭州宣布打造“国际金融科技中心”','吉翔','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>杭州12月16日电 (黄慧)16日，杭州在首届钱塘江论坛上正式宣布要打造“国际金融科技中心”。</p>\n<p>　　据浙江互联网金融联盟发布的2017金融科技中心指数显示，杭州与北京、上海、深圳、广州是全球金融科技中心的第一梯队。杭州金融科技体验指数排名第一，网贷、众筹、第三方支付、大数据征信和区块链五大行业体验均位居前列。</p>\n<p>　　“杭州的金融科技既有相当规模又有特色优势，杭州不仅有一批独角兽企业，更有一大批成长性的中小企业。”杭州市委书记赵一德现场介绍说，蚂蚁金服是中国最大的金融科技公司，在金融科技全球百强榜排名也是第一。挖财、51信用卡、爱财集团，微网贷与蚂蚁金服一起入选中国科技金融50强，杭州还有一大批新兴的中小金融科技企业，部分已经在细分领域成为单打冠军。</p>\n<p>　　赵一德表示，杭州要紧紧抓住国际金融科技大发展的机遇，着力打造全球金融科技的中心。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　除打造“国际金融科技中心”发布外，钱塘江论坛现场还发布了由中证指数公司和南方基金管理有限公司共同研发的中证杭州湾指数，该指数是国内首批湾区经济主题类指数。</p>\n<p>　　据介绍，中证杭州湾区指数是反映环杭州区大湾区上市公司整体状况的标志性指数，成分股由100家上市公司组成，目前包括浙江80家上市公司和上海20家上市公司。</p>\n<p>　　此外，现场还进行了浙江钱塘江金融港湾公益基金会、浙江钱塘江金融研修院的揭牌、全球金融科技创新大会Money20/20中国会址及重大项目落地萧山等仪式。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,5,'2017-12-16 21:17:00','2017-12-17 01:15:28','66',0),(2887,'小鹏汽车完成第三轮融资 阿里系、小米、富士康参投','姜雨薇','第一财经客户端','<p>　　<strong>阿里系、小米、富士康联合投资小鹏汽车</strong></p>\n<p>　　智能汽车融资争夺战加速。</p>\n<p>　　第一财经独家获悉，互联网电动汽车品牌小鹏汽车已经完成第三轮融资，该轮参与的投资机构有小米、富士康、IDG等。小鹏汽车将于本周内正式宣布融资消息。</p>\n<p>　　此前，阿里系参与了小鹏汽车第二轮融资。近日，作为小鹏汽车的运营实体公司，广州橙行智动汽车科技有限公司的股东中增加了“杭州阿里创业投资有限公司”，占股10%。该公司的股东分别为阿里巴巴董事局主席马云，他个人注资2.08亿元占股80%；另一股东为阿里巴巴联合创始人谢世煌，注资5200万元占股20%。在阿里巴巴完成对小鹏汽车战略投资的同时，阿里巴巴董事局副主席蔡崇信已进入小鹏汽车董事会。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　自2014年成立以来，小鹏汽车一直与互联网巨头阿里系有千丝万缕的关系。小鹏汽车核心投资人、董事长何小鹏就来自阿里系。</p>\n<p>　　在首轮融资中，小鹏汽车获得神州优车22亿元战略投资，投资主体为神州优车成立的优车产业基金，这也是优车产业基金首次对外投资。记者从接近小鹏汽车人士处获悉，包括雷军、俞永福、李学凌、傅盛等人士曾通过上述基金曲线投资小鹏汽车。</p>\n<p>　　何小鹏13日没有回应上述消息，但透露近期会发布三轮融资的情况，今年融资目标是50亿美元。</p>\n<p>　　<strong>两年计划融资50亿美元</strong></p>\n<p>　　小鹏汽车前三轮融资总额高达7亿美元。何小鹏的目标是两年内融资50亿美元。第一财经获悉，为了达成这一目标，小鹏汽车计划引入新的首席财务官。</p>\n<p>　　何小鹏曾表示，小鹏汽车一年内要完成5轮融资。对于当下的小鹏汽车来说，这个目标不是难题。一年5次的融资速度，已经超过了共享单车头部企业摩拜单车。2016年全年，摩拜单车完成了四轮融资。</p>\n<p>　　小鹏汽车以“三电一屏”(电机、电池、电控和中控屏幕)开发为切入点，进行互联网汽车核心系统研发，于2015年初开始进入造型及整车研发阶段。2016年9月，小鹏汽车发布Beta版样车，2017年2月首台试制车辆下线，已生产的100多台试制车辆通过了各项严苛的测试。至今，尚未有任何一家互联网造车企业拿到新能源车企生产牌照，小鹏汽车选择了与传统车企海马汽车合作的方式，借助海马汽车的生产资质，终于使得小鹏汽车首款产品可以在今年10月顺利下线。</p>\n<p>　　除了与海马汽车在生产等方面深度合作，小鹏汽车也在自建工厂。今年5月，小鹏汽车宣布将在广东肇庆累计投资100亿，建设总共占地3000亩的车辆生产基地。其中第一期工程计划投资40亿元，预计2019年将竣工，投产后将实现10万辆/年的产能。</p>\n<p>　　互联网造车，在科技设计、产品迭代等方面上占有优势，但汽车是仅次于飞机的最复杂制造产业，无论是特斯拉还是小鹏汽车，新造车势力在很长时间内还需要为质量努力，不断迭代完善产品，造出更好的智能互联电动车。何小鹏谈到，今年10月发布的小鹏汽车1.0版量产汽车不会大规模对公众销售，目前大约有1000辆订单。通过这些特定客户的试驾而从中发现问题并不断改善，完成对硬件迭代，小鹏2.0版本的智能电动汽车已开发出来，即将参加美国CES展，并将于2018年正式上市。</p>\n<p>　　造车是资金密集型和技术密集型的领域，而且汽车行业是一个超长周期行业，产品推出和换代周期长、产业链长、投资回报周期更长。造车新势力除了要不断攻破技术的难关，还要克服资金的难关，源源不断地投入巨额资金。乐视造车项目搁浅，与资金链断裂密不可分。威马汽车创始人、董事长兼CEO沈晖曾说过，造车是非常专业的一个领域，难度大大超过外界想象。有钱不一定能做好，没钱一定做不好。</p>\n<p>　　走在造车路上，造车新势力纷纷通过融资来继续前行。能否获得投资方的青睐以及每轮融资背后的投资方是谁、融到多少钱，这些成为衡量造车新势力实力的指标。</p>\n<p>　　<strong>BAT双边押注</strong></p>\n<p>　　近两个月来，在造车新势力中呼声较高的几家互联网造车企业推出或即将推出首款量产车时，BAT正积极参与进来。</p>\n<p>　　11月8日，造车新势力中的蔚来汽车完成新一轮超10亿美元融资，由腾讯领投。其首款量产纯电动车ES8将于12月16日上市。</p>\n<p>　　12月5日，威马汽车完成由百度资本领投、百度集团等跟投的新一轮10亿美元融资。12月11日，威马首次对外公布了未来产品规划以及旗下首款量产车EX5。目前，威马汽车的累计融资总额已经超过120亿元人民币，并又在筹备新一轮的融资计划。此前，腾讯前CTO熊明华创立的七海资本曾投资威马汽车，熊明华也加盟威马汽车担任副董事长。</p>\n<p>　　小鹏汽车继今年6月获得神州优车22亿元的战略投资之后，又获得阿里、小米的战略投资。</p>\n<p>　　借助资本的力量，造车新势力加速超电动化、智能化、网络化的方向前进。BAT等互联网巨头纷纷入局，从资金、技术等方面为造车新势力撑腰。</p>\n<p>　　三家公司造车方式不尽相同。蔚来汽车选择了“外包”，先后同江淮汽车和长安汽车达成合作，由后者作为代工厂；威马汽车和小鹏汽车选择了自己生产，威马汽车整车工厂位于温州市，小鹏汽车的生产基地位于广东肇庆。</p>\n<p>　　一位从传统车企跳到互联网造车企业的高管近日接受第一财经记者采访时谈到，在移动互联领域先行一步的手机产业，苹果以颠覆式的创新，扳倒了爱立信、诺基亚以及摩托罗拉这三个手机巨头，这一幕将可能在汽车这个新的移动终端上重演。造车新势力擅长互联网思维，打磨出更注重数据和用户体验的电动车极致产品，将从产品、商业模式以及服务等全面颠覆传统汽车产业，他跳槽到互联网造车企业，就是想接受这种新挑战。</p>\n<p>　　当前，传统车企也在加速朝智能互联电动车加速冲刺，而且与造车新势力相比，传统车企在资金、技术等方面更胜一筹。传统车企巨头丰田2016年财年虽然出现减收减益，但销售额依然高达27.6万亿日元(按当时汇率约合人民币1.67万亿元)，营业利润为1.99万亿日元(约合人民币1210亿元)。而作为中国最大、市值最高的汽车企业，上汽集团在2016年总营收高达7564.16亿元，净利润达320亿元。</p>\n<p>　　值得注意的是，BAT除了对造车新势力进行战略投资以外，也与传统车企进行跨界合作，阿里与上汽、腾讯，以及百度与长安等抱团合作。其中，早在2014年，上汽携手阿里签署战略合作协议，率先提出“跑在互联网上的汽车”的设计理念。次年，上汽、阿里共同设立10亿元“互联网汽车基金”，同时投资成立“斑马网络技术有限公司”，建立起车载硬件设施与车载互联网系统的应用开发体系，让车端有了“云”的支持。</p>\n<p>　　BAT对汽车领域布局，同时将筹码押注在传统车企和造车新势力身上。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1291,'2017-12-15 16:20:00','2017-12-17 01:15:28','72',0),(2888,'故宫钟表匠师亓昊楠获评“年度匠人精神青年榜样”','张楷欣','北京晨报','<p>　　<strong>故宫钟表匠师获评青年榜样</strong></p>\n<div style=\"text-align:center\"> \n <input id=\"97439f2aa5514d8da27198a4e526d9a2\" name=\"97439f2aa5514d8da27198a4e526d9a2\" type=\"hidden\" value=\"http://www.chinanews.com/tp/2016/12-12/8091638.shtml\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \"> \n <div style=\"text-align:center\"> \n  <img alt=\"国家级非物质文化遗产古代钟表修复技艺的第三代传承人王津师傅(右)和徒弟亓昊楠(左)在办公室内。新华社记者 吕帅 摄 图片来源：新华网\" src=\"upload/image/720e3ba2-ca53-4f8b-a013-cc3618b979ce.jpg\" style=\"border:px solid #000000\" title=\"国家级非物质文化遗产古代钟表修复技艺的第三代传承人王津师傅(右)和徒弟亓昊楠(左)在办公室内。新华社记者 吕帅 摄 图片来源：新华网\"> \n </div> \n <div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n   资料图：国家级非物质文化遗产古代钟表修复技艺的第三代传承人王津师傅(右)和徒弟亓昊楠(左)在办公室内。新华社记者 吕帅 摄 图片来源：新华网 \n </div> \n</div>\n<p>　　北京晨报讯(记者 陈琳)一部纪录片《我在故宫修文物》，让故宫文物修复师成为“网红”。因为它展现了一种浮躁社会很难有的专注，这就是文物修复师们的“工匠精神”。昨天，由《北京青年》周刊举办的2017首都航空助力“工匠精神・青年榜样”颁奖典礼在国博举行。故宫钟表匠师亓昊楠获得“年度匠人精神青年榜样”。</p>\n<p>　　现场，19个奖项共计36人(组)折桂“青年榜样”，涵盖了文化艺术、人工智能、航天科工、体育、创新创业、互联网、非遗项目、公益事业以及财经领域。一同获得“年度匠人精神青年榜样”的还有孙长胜、朱炜强、秦涛、时绍原、林进、郑阳、彭文源。此外，还有著名作家刘震云、著名表演艺术家吴刚分别摘得“年度文化青年导师”大奖和“年度表演艺术青年导师”大奖。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/720e3ba2-ca53-4f8b-a013-cc3618b979ce.jpg',NULL,1571,'2017-12-17 00:50:00','2017-12-17 01:15:28','65',0),(2889,'第九次中英经济财金对话在北京举行 深入讨论“一带一路”合作','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>北京12月16日电 (记者 蒋涛 李晓喻)中国国务院副总理马凯与英国财政大臣哈蒙德16日在北京共同主持第九次中英经济财金对话。</p>\n<div style=\"text-align:center\"> \n <img alt=\"12月16日，第九次中英经济财金对话在北京举行，中国国务院副总理马凯与英国财政大臣哈蒙德共同出席并主持对话。 <a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 李慧思 摄\" src=\"upload/image/862a6271-00d7-4e60-b722-307aeaefc3b9.jpg\" title=\"12月16日，第九次中英经济财金对话在北京举行，中国国务院副总理马凯与英国财政大臣哈蒙德共同出席并主持对话。 <a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 李慧思 摄\"> \n <input id=\"ce98df6bb7fa491f855814c070fffb2d\" name=\"ce98df6bb7fa491f855814c070fffb2d\" type=\"hidden\" value=\"http://www.chinanews.com/part/561/2017/1216/5103.html\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  12月16日，第九次中英经济财金对话在北京举行，中国国务院副总理马凯与英国财政大臣哈蒙德共同出席并主持对话。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 李慧思 摄 \n</div>\n<p>　　马凯表示，今年是中英建立大使级外交关系45周年，深化中英务实合作迎来新的机遇。本次对话是在十九大胜利召开后两国举行的首次经济财金对话，肩负着落实两国领导人达成的重要共识、在新历史起点上扩大中英经济财金合作的重要使命。双方要抓住战略机遇，持续深化能源合作、推动基础设施合作迈出坚实步伐，深化贸易投资合作、拓展产业和创新合作、推动“中国制造2025”和英国产业战略对接，加强“一带一路”框架下务实合作、探索第三方市场合作，稳步推进金融服务合作、共同推进人民币国际化，加强国际事务协调、促进世界经济持续复苏。中方愿与英方共同努力，为中英关系“黄金时代”不断注入新内涵，推动中英战略伙伴关系迈上新台阶。</p>\n<p>　　哈蒙德说，中共十九大为中国未来发展制定了宏伟计划，英方支持“一带一路”倡议，期待与中方进一步抓住机遇，深化各领域务实合作，推进实现英中关系“黄金时代”。</p>\n<p>　　本次对话双方就宏观经济形势、贸易投资、产业战略、金融改革与发展、“一带一路”合作等进行了深入讨论，共达成72项互利共赢成果。</p>\n<p>　　对话后，马凯与哈蒙德共同会见了记者。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/862a6271-00d7-4e60-b722-307aeaefc3b9.jpg',NULL,1845,'2017-12-16 20:17:00','2017-12-17 01:15:28','66',0),(2890,'三沙永兴机场通航近一年 成进出西沙便捷通道','张楷欣','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>海口12月16日电 (记者 洪坚鹏)记者16日从海航机场集团有限公司获悉，三沙永兴机场从去年12月底通航迄今，已累计安全飞行民航班机逾680架次，成为进出西沙群岛的便捷通道。当日，由该机场集团运营管理的海口美兰国际机场、三亚凤凰国际机场、琼海博鳌国际机场以及三沙永兴机场旅客吞吐量突破4000万人次。</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：散沙永兴机场民航公务包机。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 宋国强 摄\" src=\"upload/image/bd3a5764-b902-4e04-81a0-9d9ec878aa5a.jpg\" title=\"资料图：散沙永兴机场民航公务包机。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 宋国强 摄\"> \n <input id=\"e3c9a94d7ba047a594054f146c25e619\" name=\"e3c9a94d7ba047a594054f146c25e619\" type=\"hidden\" value=\"http://www.chinanews.com/gn/2016/12-22/8101973.shtml\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：散沙永兴机场民航公务包机。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 宋国强 摄 \n</div>\n<p>　　海航机场集团董事长兼总裁廖虹宇在媒体沟通会上介绍，近几年海南省各级政府从资金投入、资源配置等多方面支持省内各机场建设，先后出台多项奖励配套政策，有效促进了机场客流量的增长。海口美兰机场实现2000万人次旅客吞吐量，三亚凤凰机场吞吐量接近1900万人次。</p>\n<p>　　廖虹宇称，海航机场集团有限公司围绕国家“一带一路”建设，大量开通国际国内航线，搭建海南与国内外经济文化交流的桥梁。</p>\n<p>　　海南省旅游发展委员会相关官员表示，作为岛屿型省份，海南正从国际航线开发、加强邮轮合作、加强人文交流三方面推动建设“环南海旅游经济合作圈”。未来海南将大力提升交通基础设施水平尤其是民航基础设施水平建设，加快完成海口、三亚两机场扩建。</p>\n<p>　　目前海口美兰国际机场二期扩建工程正在加紧施工，预计2019年进行联动调试、竣工验收、校飞、试飞。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/bd3a5764-b902-4e04-81a0-9d9ec878aa5a.jpg',NULL,1147,'2017-12-16 22:35:00','2017-12-17 01:15:28','65',0),(2891,'穗台百名大学校长齐聚羊城 分享高校教师队伍建设经验','吉翔','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>广州12月16日电 (记者 郭军)由广州市台办指导，广州大学、台湾教育大学系统主办，广州大学台湾研究院承办的“第十三届穗台大学校长论坛”16日在广州大学召开。来自穗台两地近70所高校100多名高校校长及管理人员参加论坛。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　本次论坛以“穗台高校教师发展”为主题，分别设2场大会主旨发言和4场议题发言，深入探讨高校教师在发展过程中面临的问题与挑战，分享两岸高校教师队伍建设经验，加强两岸高校教师之间的交流与合作。</p>\n<p>　　广州市市委常委、统战部部长卢一先，广州市台办及广州大学等相关领导出席了论坛开幕式。广州市台办主任高耀宗在致辞中表示，高等院校是汇聚人才、培养人才、造就人才的摇篮，广州充满对人才的渴望和期待，希望通过此次论坛进一步加深穗台两地高校间的人才培育合作与交流，广州也将为台湾优秀的教育人才提供更加广阔的发展机遇。</p>\n<p>　　广州大学副校长张其学介绍说，自2005年以来，广州市已成功举办了12届穗台校长论坛研讨会，先后共邀请广州和台湾地区近450所高校的负责人相聚广州，围绕基础教育、高等教育、职业教育、特殊教育等多个不同主题和领域进行深入探讨，并促成了广州50多所院校与台湾相关院校建立了校际交流与合作关系，各种学术研讨、交流活动持续开展，有效搭建两岸教育系统工作交流平台，对促进穗台两地教育事业的发展起到了积极促进作用。</p>\n<p>　　台湾中华医事科技大学校长曾信超认为，台湾与广州地缘相近，语言相通，台湾教师大多数具有欧美留学经历，教学及科研能力较强，能为广州学校提升师资质量、增加师资的多元性。</p>\n<p>　　台北海洋科技大学校长唐彦博表示，两岸教育交流是时势所趋，未来双方应更务实加强各项交流，进而建立“共享资源、共存共荣”的合作策略，逐步营造互信的基础，亦有助于化解政治纠葛，共创美好未来。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,763,'2017-12-16 19:27:00','2017-12-17 01:15:28','70',0),(2892,'港交所将引入同股不同权 要求预期最低市值须达100亿元','李夏君','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>香港12月15日电 香港交易所(港交所)15日公布，就此前的框架咨询文件发表咨询总结，决定落实计划，按步拓宽香港上市制度，便利新兴产业及创新型公司来港上市。</p>\n<p>　　港交所表示，早前对香港上市制度进行了全面检讨，发现当中存在若干不足之处，尤其在吸引新兴产业及创新型公司方面，削弱了香港相对于全球其他主要上市地的整体竞争力。为此，港交所发表框架咨询文件咨询市场意见，是否需要拓宽上市条件以更配合创新型公司的需求，以及探索最合适可行的做法。</p>\n<p>　　港交所接获360份来自广泛界别，代表着香港不同市场人士对框架咨询文件的回应意见。这些回应意见包括甚具建设性的意见，并反映市场已有明确共识，认为港交所需要采取措施，拓宽香港资本市场上市渠道、提高香港作为环球金融中心的竞争力。</p>\n<p>　　市场咨询的主要调查结果显示，91%回应人士支持香港市场多元化发展，尤其是吸引更多新兴产业及创新型公司发行人来港上市的措施。市场大力支持容许尚未有盈利的公司在香港上市，但不太赞成让该等发行人享有较为“宽松”的首次上市条件。绝大部分回应意见都视不同投票权架构为关乎竞争力的议题，故大多支持容许不同投票权架构公司在香港上市，前提是须对该等公司施加规定，为股东提供适当的保障。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　大部分回应人士均支持设立创新板以拓宽香港上市渠道，但多数人更倾向一个较简单的上市架构，即接纳创新型公司发行人在主板上市。</p>\n<p>　　基于框架咨询文件所收集到的回应，及其后与证券及期货事务监察委员会(证监会)就监管方面的讨论，港交所决定落实计划拓宽现行的上市制度，并于《主板规则》新增两个章节，容许尚未盈利及未有收入的生物科技发行人，及不同投票权架构的新兴及创新产业发行人，在作出额外披露及制定保障措施后在主板上市。</p>\n<p>　　不同投票权架构公司的预期最低市值须达100亿元(港元，下同)，若市值低于400亿元，须通过于上市前的完整财政年度录得10亿元收入的较高收入测试。未有收入公司若根据《主板规则》新增的生物科技公司适用章节申请上市，预期最低市值须达15亿元。</p>\n<p>　　香港交易所集团行政总裁李小加表示，这是香港上市机制改革重要的一步，经过几个月的公开咨询，香港市场已经就完善上市规则、拥抱新经济达成了积极的共识，探明了市场改革的大方向。明年第一季度，将推进上市规则的细则咨询，制订出全面完善的细则规定，设定好相应的投资者保护措施。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,881,'2017-12-15 21:33:00','2017-12-17 01:15:28','69',0),(2893,'中国杭州开通至澳大利亚墨尔本直飞航线','张楷欣','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>杭州12月16日电 (记者 方��)16日，中国杭州首条直飞澳大利亚墨尔本的洲际航线迎来首航，该航线每周执行2个往返班次。杭州至墨尔本航线开通后，杭州机场国际地区通航点达到49个，覆盖全球20多个国家和地区。</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：厦门航空客机。王东明 摄\" src=\"upload/image/c2fe1665-0265-40ef-97ef-3e4aba909016.jpg\" title=\"资料图：厦门航空客机。王东明 摄\"> \n <input id=\"883761c07e504c6693eab5552116ce20\" name=\"883761c07e504c6693eab5552116ce20\" type=\"hidden\" value=\"http://www.chinanews.com/tp/hd2011/2015/12-28/595731.shtml\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：厦门航空客机。王东明 摄 \n</div>\n<p>　　据悉，墨尔本拥有人数众多的浙江籍华侨华人及留学生，近年来，从浙江前往澳大利亚的游客也逐年递增。此前，杭州及周边地区的旅客前往墨尔本，都需要从上海出发，或者从广州、香港中转。</p>\n<p>　　“杭州至墨尔本航线开通后，将至少节省3-5小时中转时间。”厦门航空客运营销委员会副总经理胡楠表示，该航线的开通将大大提高浙江及周边主要省市与大洋洲的连接速度，进一步拓宽中国华东地区的国际出行通道。</p>\n<p>　　记者了解到，杭州至墨尔本航线由厦门航空运营，厦门航空在该航线上将投入目前波音的旗舰机型――787梦想客机。该航线每周执行2个往返班次：每周三、周六20：15从杭州萧山国际机场起飞，次日9：45抵达墨尔本，航班号为MF813，飞行时间10.5小时；每周四、周日11：35从墨尔本机场起飞，19：05抵达杭州，航班号为MF814。(以上均为时间均为当地时间)</p>\n<p>　　杭州至墨尔本航线开通后，杭州机场国际地区通航点达到49个，覆盖全球20多个国家和地区，形成了辐射东亚、东南亚、南亚及中东地区，连接欧洲的国际(地区)客运航线网络，国际通达性明显提升。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/c2fe1665-0265-40ef-97ef-3e4aba909016.jpg',NULL,546,'2017-12-16 22:31:00','2017-12-17 01:15:28','65',0),(2894,'比亚迪全新一代唐“定妆”实车明年发布','陈海峰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月15日电 日前，比亚迪正式发布新能源旗舰SUV“全新一代唐”定妆照，实车有望在明年正式发布。</p>\n<p>　　基于“Dragon Face”设计理念，以及前奥迪设计总监沃尔夫冈・艾格的精湛雕琢，全新一代唐在造型上展示出了很强的豪华感和科技感，极具辨识度。</p>\n<p>　　从整体上看，全新一代唐将尊贵、自信、强大的“龙”形象演绎得淋漓尽致。前脸“大嘴”抢眼醒目，亮色横条幅进气格栅雄壮威严，带来了层次分明的立体感。两侧进气口增强了横向宽度，赋予车头冲击力。</p>\n<p>　　“龙眼”大灯炯炯有神，LED灯组科技感十足。中央悬挂篆体“唐”汉字LOGO，两侧有力延伸的超长“龙须”大大提升了视觉上的档次感。</p>\n<p>　　从侧面看，车头“龙眼”大灯、“龙嘴”进气格栅、“龙须”亮色格栅以及“腮式”进气口共同组成了向前方跃跃欲试的“龙头”，极具运动张力。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/0cf6dfb2-eb36-4003-b3a6-1a9cb15c2d6a.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　车身方面，全新一代唐通透、修长、精致。“龙须”格栅贯穿到灯眉并通过腰线延伸到车后端，整体感强且坚挺有力。双腰线设计，通过线条倒角变化营造出虚实结合的效果，底部裙线提升整体姿态。悬浮式车顶、向后延伸翘起的尾翼等设计，匹配大轮毂带来的“黄金比例”视觉效果，更显动感大气。</p>\n<p>　　车尾首次采用“Build Your Dreams”标识，彰显品牌初心。相比霸气外露的前脸，新车背部可谓“玄机暗藏”――尾翼和高位制动灯的结合，高挑的后保险杠棱线使得车身姿态蓄势待发，让尾部整体设计简约却不简单。“一体成形”横贯式LED尾灯，十分精致，带来很高辨识度。而在尾灯的两端尽头则采用对称式的蜿蜒设计，大气豪华――这是设计师基于“Dragon Face”设计理念做出的“龙游四海”造型演绎。</p>\n<p>　　此前率先以“Dragon Face”设计语言面世的宋MAX，上市后即月销过万，累计订单量突破35000台。可以预见，全新一代唐这款重磅新车将携手宋MAX在2018年为比亚迪在市场销量提升和品牌形象走强两大维度上提供巨大助益。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/0cf6dfb2-eb36-4003-b3a6-1a9cb15c2d6a.jpg',NULL,1584,'2017-12-15 15:42:00','2017-12-17 01:15:28','72',0),(2895,'北京冬奥会徽章等特许商品开售 首套纪念邮票月底发行','张楷欣','新华网','<p>　　<strong>北京冬奥会徽章等特许商品开售 首套纪念邮票月底发行</strong></p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：12月15日，北京2022年冬奥会会徽和冬残奥会会徽发布。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 韩海丹 摄\" src=\"upload/image/88f93672-df5f-46ae-b1fa-0c8b53904f36.jpg\" title=\"资料图：12月15日，北京2022年冬奥会会徽和冬残奥会会徽发布。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 韩海丹 摄\"> \n <input id=\"5e162af63b204fffa46d59feaed01b12\" name=\"5e162af63b204fffa46d59feaed01b12\" type=\"hidden\" value=\"http://www.chinanews.com/tp/hd2011/2017/12-16/788873.shtml\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：12月15日，北京2022年冬奥会会徽和冬残奥会会徽发布。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 韩海丹 摄 \n</div>\n<p>　　新华社北京12月16日电(记者姬烨、汪涌)在2022年北京冬奥会会徽和冬残奥会会徽于15日正式发布之后，北京冬奥组委在16日启动了2022年北京冬奥会和冬残奥会(以下简称北京冬奥会)特许经营试运行计划(以下简称试运行计划)，推出了徽章、钥匙扣等非贵金属产品，贵金属纪念章及相关制品，服装服饰及配饰、文具、陶瓷产品以及邮票和邮品等6大类特许商品，会徽纪念邮票也将于本月31日首发。</p>\n<p>　　北京冬奥会特许经营计划是北京冬奥会市场开发计划的重要组成部分，它是北京冬奥组委通过许可特许经营企业生产和销售带有北京冬奥会会徽、吉祥物、运动图标、口号、火炬形象等标志及相关知识产权的产品并向特许经营企业收取特许权费的一种市场开发行为。特许经营企业通过生产和销售特许产品获得利润，按一定比例向北京冬奥组委缴纳特许权费，以此对北京冬奥会的举办予以支持。与各级赞助企业不同，特许经营企业不享有与北京冬奥会相关联的市场营销权益。</p>\n<p>　　试运行计划将从北京冬奥会会徽发布后开始启动，至2018年6月30日截止，试运行期大约半年。在试运行结束后，北京冬奥组委将在明年7月份正式启动特许经营计划，推出更多品类的北京冬奥会特许商品在更大的市场范围内进行销售。</p>\n<p>　　北京冬奥组委采取线上线下相结合的方式销售北京冬奥会特许商品。北京冬奥组委在阿里巴巴旗下的天猫平台开设“北京2022特许商品官方网店”，网址为http://beijing2022.tmall.com。上线预售产品包括限量发行2022枚的徽章、制作精美的纯银纪念章，也有围巾、领带和笔记本，还有会徽发行的纪念封等。</p>\n<p>　　为方便消费者到店内购买特许商品，北京冬奥组委还将于明年1月开设首批10余家特许商品零售店。在线下销售启动前，“北京2022特许商品官方网店”将是消费者预定、购买北京冬奥会特许商品的唯一渠道。</p>\n<p>　　此外，北京冬奥组委将在今年12月31日联合中国邮政集团公司发行2022年冬奥会会徽和冬残奥会会徽纪念邮票，这是北京冬奥会的首套纪念邮票。届时，北京冬奥组委与中国邮政集团将联合举行首发仪式，随同邮票发行的还有纪念封、邮折、邮册等系列邮品。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/88f93672-df5f-46ae-b1fa-0c8b53904f36.jpg',NULL,1369,'2017-12-16 21:43:00','2017-12-17 01:15:28','66',0),(2896,'公安部：五年来共破获侵权假冒犯罪案件13.57万起','张楷欣','新华网','<p>　　<strong>五年来共破获侵权假冒犯罪案件13.57万起</strong></p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：广东省公安机关举行“打假行动”宣传活动。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 颜新阳 摄\" src=\"upload/image/1d4127aa-e6cb-4552-b428-634f48e53915.jpg\" title=\"资料图：广东省公安机关举行“打假行动”宣传活动。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 颜新阳 摄\"> \n <input id=\"a55e08e13fcf413c8d0edcd072786ccb\" name=\"a55e08e13fcf413c8d0edcd072786ccb\" type=\"hidden\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：广东省公安机关举行“打假行动”宣传活动。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>发 颜新阳 摄 \n</div>\n<p>　　新华社北京12月16日电(记者刘奕湛)记者16日从公安部获悉，全国公安机关始终把打击和防范侵犯知识产权犯罪作为一项重要任务，过去5年间，先后发起“打假行动”“网上打假行动”“云端2017”等专项行动，狠抓专案打击和战役攻坚，共破获侵权假冒犯罪案件13.57万起，涉案总价值逾930亿元，抓获犯罪嫌疑人14.9万名，保持了打击侵权假冒犯罪的强大声势。</p>\n<p>　　针对侵犯知识产权犯罪链条化、产业化、跨区域的趋势特点，公安机关树立“大数据”思维，强化数据意识和信息应用能力，对制假售假全环节、全链条实施精确打击，先后打掉2200余个跨区域、跨国境、产业化特大犯罪网络，以实际行动和战果回应人民期待，为企业技术创新、品牌创新提供有力执法保障。</p>\n<p>　　公安部有关负责人表示，公安机关将坚持“长期作战、强力遏制”指导思想，着力提升打击效能，坚决遏制突出犯罪，保持高压严打力度声势，依法保护产权、维护市场秩序，坚定守护新时代人民美好生活。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/1d4127aa-e6cb-4552-b428-634f48e53915.jpg',NULL,81,'2017-12-16 21:51:00','2017-12-17 01:15:28','64',0),(2897,'南非总统宣布将对近90%家庭的学生实行高等教育免费','张楷欣','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>约翰内斯堡12月16日电 (记者 宋方灿)继推行免费医疗和推出免费住房后，南非总统祖马16日宣布，将对占家庭总数90%的贫困家庭和工人家庭的学生实行高等教育免费制度。</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：南非总统祖马。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 盛佳鹏 摄\" src=\"upload/image/ebdc82a8-25b8-4773-9fb3-d1c1d9c7cf05.jpg\" title=\"资料图：南非总统祖马。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 盛佳鹏 摄\"> \n <input id=\"25fc0b19d2aa408590b83244fbc284bf\" name=\"25fc0b19d2aa408590b83244fbc284bf\" type=\"hidden\" value=\"http://www.chinanews.com/tp/hd2011/2017/09-05/768837.shtml\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：南非总统祖马。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 盛佳鹏 摄 \n</div>\n<p>　　祖马在一份声明中称，从2018年开始，新入学的大学一年级的贫困家庭或工人家庭的学生就可以享受到这项全额免费福利。祖马还承诺，在未来5年内，这项免费高等教育福利将惠及所有符合条件的学生。</p>\n<p>　　实施免费的学校包括全国各公立大学和一些职业技术培训学校。对于这些家庭的学生，祖马给出的定义是“家庭年总收入不高于35万兰特(约合27000美元)”。目前，南非的人均国内生产总值约为6000美元。根据这个标准，南非大部分的家庭都能满足免费标准。“这个政策将使南非90%的家庭享受到免费教育。”祖马说。</p>\n<p>　　祖马表示，“那些在新的定义下被归类为贫困或工人家庭的学生，将获得政府资助和支持，而非仅仅是贷款。”他还指出，这些全额免费内容包括导师费、学习材料费、伙食费、住宿费和交通费等。</p>\n<p>　　为实现这个目标，南非决定提高对大学的补贴比例，在未来5年内从原先占国内生产总值的0.68%提高到1%。南非政府还承诺，在2018年不会增加年总收入60万兰特以下家庭学生的学费。</p>\n<p>　　因为此前政府试图提高学费，南非近年来连续发生高校学生游行示威甚至演化为暴力冲突的事件，全国多所主要高校的贫困学生甚至喊出了“学费必须下降”的口号。不过，政府突然宣布对绝大多数学生高等教育完全免费，仍有些出人意料。</p>\n<p>　　对此，祖马解释说：“学校教育和培训是国家发展计划实施过程中最紧迫的挑战之一。青年需要通过学习获得机会，以确保他们有效参与国家的经济建设。深层次的收入不平等现象在我国依然存在，只有当高等教育可以培训青年，我们的国家才可以快速打破贫穷、失业和不平等这些恶性循环。”</p>\n<p>　　之前，南非政府除了推出免费医疗等制度，还面向经济条件一般的家庭推出了免费住房制度，在过去的23年中已经免费为超过400万个家庭解决了住房问题。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/ebdc82a8-25b8-4773-9fb3-d1c1d9c7cf05.jpg',NULL,566,'2017-12-16 23:28:00','2017-12-17 01:15:28','71',0),(2898,'韩国总统文在寅访重庆 参观大韩民国临时政府旧址','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>重庆12月16日电 (记者 刘相琳)韩国总统文在寅16日访问重庆，参观了位于重庆渝中区的大韩民国临时政府旧址。</p>\n<div style=\"text-align:center\"> \n <img alt=\"文在寅夫妇与独立运动人士后代在大韩民国临时政府旧址合影留念。周毅 摄\" src=\"upload/image/7909d01e-1469-4a77-887c-ad1c422d35f2.jpg\" title=\"文在寅夫妇与独立运动人士后代在大韩民国临时政府旧址合影留念。周毅 摄\"> \n <input id=\"12f00d32f68e4e678ade3baae91f5be8\" name=\"12f00d32f68e4e678ade3baae91f5be8\" type=\"hidden\" value=\"http://www.chinanews.com/tp/hd2011/2017/12-16/788968.shtml\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  文在寅夫妇与独立运动人士后代在大韩民国临时政府旧址合影留念。周毅 摄 \n</div>\n<p>　　1919年，大韩民国临时政府在上海成立。中国全面抗战爆发后，大韩民国临时政府于1940年迁至重庆。历史资料显示，从1940年迁往重庆到1945年返回韩国，在重庆的六年是大韩民国临时政府在中国开展民族独立运动最重要、最活跃的六年。</p>\n<p>　　旧址位于重庆渝中区莲花池正街38号，是一处青瓦灰砖的建筑群。文在寅当天一身深色正装走进旧址，夫妇二人共同向被誉为“韩国国父”的独立运动家金九的铜像敬献花篮，并鞠躬默哀。</p>\n<p>　　在旧址内，文在寅认真听讲解员介绍关于抗战时期大韩民国临时政府在重庆开展独立运动的情况。一幅大韩民国临时政府在中国境内迁徙的地图引起文在寅的注意，他主动向讲解员询问，这些地方是否建有纪念馆。得知上海、长沙等地也建有纪念馆后，文在寅点头示意。</p>\n<p>　　1945年，日本战败投降，大韩民国临时政府要员们离开重庆。临行前，一行人员在大韩民国临时政府内合影留念。文在寅当天也与大韩民国临时政府独立运动人士后代在相同的位置合影留念。</p>\n<p>　　参观结束后，文在寅在旧址展馆留言册上，用韩文题词：“大韩民国临时政府是我们的根，是我们的精神所在。”</p>\n<p>　　文在寅出席当天举行的中韩产业合作重庆论坛致辞时表示，重庆对韩国国民具有特别的历史意义，上午他参观了蕴含着韩国独立运动家灵魂和气息的大韩民国临时政府旧址，想到先烈们在重庆，和中国人民一起听到祖国光复的好消息，并相互拥抱，他不禁热血沸腾。</p>\n<p>　　访渝期间，文在寅还与中共中央政治局委员、重庆市委书记陈敏尔举行了会谈。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/7909d01e-1469-4a77-887c-ad1c422d35f2.jpg',NULL,1691,'2017-12-16 19:41:00','2017-12-17 01:15:28','64',0),(2899,'外籍华人在闽生活活动及永久居留享便利政策','李夏君','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>福州12月15日电 (闫旭)15日，福建省公安厅发布了深化支持福建自贸区创新发展有关出入境政策措施，其中两条专门服务于外籍华人。</p>\n<p>　　福建省公安厅副厅长薛祺安表示，这些更加宽松的出入境政策，更具温情的人性化措施，更具吸引力的投资创业软环境，能够进一步增强海外华人的自豪感和归属感，激发他们来华来闽创新创业的热情。</p>\n<p>　　福建新出台的外籍华人在闽生活活动及永久居留便利政策，具体为：</p>\n<p>　　一是便利外籍华人在闽生活活动。对来福州、厦门、平潭探望亲属、洽谈商务、开展科教文卫交流活动及处理私人事务的外籍华人，可以签发5年以内多次入出境有效签证；对在福州、厦门、平潭工作、学习、探亲以及从事私人事务需长期居留的，可以按规定签发有效期5年以内的居留许可。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　根据现有签证规范，来华洽谈商务、科教文卫交流活动的，只能换发入境有效期不超过1年的签证，处理私人事务的甚至只能换发入境有效期短于3个月的签证。</p>\n<p>　　福建省公安厅出入境管理局局长陈毅坤说，这项政策实施后，来福州市、厦门市、平潭综合实验区探望亲属、洽谈商务、开展科教文卫交流活动及处理私人事务的外籍华人，都可以换发有效期5年以内多次入出境有效签证和居留证件。</p>\n<p>　　二是便利外籍华人申请永久居留。外籍华人具有博士研究生以上学历在自贸区工作，或在自贸区连续工作满4年、每年在中国境内实际居住累计不少于6个月，可直接申请在华永久居留。</p>\n<p>　　根据《外国人在中国永久居留审批管理办法》，在华工作的外国人必须在中国担任副总经理、副厂长等职务以上或者具有副教授、副研究员等副高级职称以上以及享受同等待遇，已连续任职满四年、四年内在中国居留累计不少于三年且纳税记录良好的，才可以申请永久居留。</p>\n<p>　　陈毅坤指出，这项政策实施后，使得在福建自贸区工作的具有博士学位的外籍华人可直接申请在华永久居留。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1205,'2017-12-15 21:33:00','2017-12-17 01:15:28','73',0),(2900,'司法部：人民调解年化解矛盾纠纷达900多万件','王诗尧','新华网','<p>　　<strong>司法部：人民调解年化解矛盾纠纷达900多万件</strong></p>\n<p>　　新华社北京12月16日电(记者王茜)记者日前从司法部获悉，我国人民调解每年化解矛盾纠纷达900多万件。</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：北京市人民调解工作先进集体和个人接受表彰。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 苏丹 摄\" src=\"upload/image/ee65be68-db5e-4453-9b0b-d967f9ce34fb.jpg\" title=\"资料图：北京市人民调解工作先进集体和个人接受表彰。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 苏丹 摄\"> \n <input id=\"0a08e236c4344904bbb9960580187aa8\" name=\"0a08e236c4344904bbb9960580187aa8\" type=\"hidden\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：北京市人民调解工作先进集体和个人接受表彰。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>发 苏丹 摄 \n</div>\n<p>　　人民调解是在继承和发扬我国民间调解优良传统的基础上发展起来的一项中国特色社会主义法律制度，具有方法灵活、程序便捷、不伤感情、不收费用等特点，在我国矛盾纠纷多元化解机制中发挥着基础性作用。</p>\n<p>　　目前，全国共有人民调解员近400万名、人民调解组织80万个，调解成功率97%以上。</p>\n<p>　　司法部基层工作指导司司长罗厚如表示，预防和化解矛盾纠纷，是人民调解工作的主要任务，也是人民调解工作的价值所在。为了更好地做好新时代矛盾化解工作，司法部将坚持预防为主，建立健全矛盾纠纷排查机制，并大力推广现代信息技术在矛盾纠纷化解中的运用。同时要依法调解，主动加强与基层法院、公安等部门联系沟通。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/ee65be68-db5e-4453-9b0b-d967f9ce34fb.jpg',NULL,764,'2017-12-16 19:21:00','2017-12-17 01:15:28','64',0),(2901,'北京小客车指标有效期延至1年 4种情形不予办理更新指标','罗攀','中国新闻网','<p>　　<strong>北京小客车指标有效期延至1年 四种情形不予办理更新指标 </strong></p>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>北京12月15日电 (记者 杜燕)新修订的《北京市小客车数量调控暂行规定》今天正式公布，修订主要涉及两个方面：将原来6个月的指标有效期延长至1年；增加了不予办理更新指标的情形。</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：高速路上，车辆排起长队。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 宦玮 摄\" src=\"upload/image/737ee483-cc85-4b56-af9c-9c845c022653.jpg\" title=\"资料图：高速路上，车辆排起长队。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 宦玮 摄\"> \n <input id=\"916acf969eef4f25a79a08e65aef2fc6\" name=\"916acf969eef4f25a79a08e65aef2fc6\" type=\"hidden\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：高速路上，车辆排起长队。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>发 宦玮 摄 \n</div>\n<p>　　<strong>小客车指标有效期改为1年</strong></p>\n<p>　　根据人大代表、政协委员和广大市民的建议，本次修订延长了指标有效期，一方面给予市民更充裕的时间合理安排购车计划，也有助于进一步放缓机动车的增速。</p>\n<p>　　据介绍，有效期延长不仅针对新增的配置指标，对于车辆转出或报废后申请更新指标的时间期限，以及获得更新指标以后购车上牌的时间期限，也都相应延长。即市民摇号获得普通指标或者轮候获得新能源指标以后，有一年的时间进行选择；更新车辆的市民，从淘汰前一辆车到买新车有两年的时间进行选择。</p>\n<p>　　新的暂行规定于2018年1月15日施行。在正式施行前已经持有的、尚在有效期之内的指标将自动延期。</p>\n<p>　　小客车指标调控管理信息系统将于2018年1月15日0时进行数据更新，所有还没使用的有效指标都会在原有效期基础上延长6个月。</p>\n<p>　　指标所有人原则上应当持新的指标确认通知书办理相关手续(登录小客车指标调控管理信息系统https://www.bjhjyd.gov.cn 进入用户中心即可打印)。因各种原因未能打印的，持原有指标确认通知书在办事窗口也能予以确认。北京市小客车指标调控管理办公室提醒指标所有人，一定要在新的有效期内办理车辆购置登记手续。</p>\n<p>　　此外，新的暂行规定正式施行前6个月已经办理了旧车注销登记还未申请更新指标的，申请时限也会自动延期。</p>\n<p>　　<strong>四种情形不予办理更新指标</strong></p>\n<p>　　为了更好地落实商务部、国家发改委、公安部、环保部2013年颁布施行的《机动车强制报废标准规定》，进一步加强对北京市存量机动车的规范使用管理。对于已经达到国家规定的强制报废标准、并已被公安交通管理部门公告牌证作废的车辆，北京市小客车指标调控管理办公室将不再受理更新指标的申请。</p>\n<p>　　《机动车强制报废标准规定》明确指出逾期不办理注销登记、被公安机关交通管理部门公告机动车牌证作废的情形有：</p>\n<p>　　(1)达到使用年限(其中，小、微型非营运载客汽车无使用年限限制。小微型、中型、大型出租载客汽车使用年限分别为8年、10年、12年，租赁载客汽车15年)；</p>\n<p>　　(2)经修理和调整仍不符合机动车安全技术国家标准对在用车有关要求的；</p>\n<p>　　(3)经修理和调整或者采用控制技术后，向大气排放污染物或者噪声仍不符合国家标准对在用车有关要求的；</p>\n<p>　　(4)在检验有效期届满后连续3个机动车检验周期内未取得机动车检验合格标志的。</p>\n<p>　　对于第4种情形，《中华人民共和国道路交通安全法实施条例》第十六条规定了各类机动车的检验周期，其中针对小客车的有：营运载客汽车5年以内每年检验1次；超过5年的，每6个月检验1次；小型、微型非营运载客汽车6年以内每2年检验1次；超过6年的，每年检验1次；超过15年的，每6个月检验1次。</p>\n<p>　　根据《中华人民共和国道路交通安全法实施条例》第九条规定：已注册登记的机动车达到国家规定的强制报废标准的，公安机关交通管理部门应当在报废期满的2个月前通知机动车所有人办理注销登记。机动车所有人应当在报废期满前将机动车交售给机动车回收企业，由机动车回收企业将报废的机动车登记证书、号牌、行驶证交公安机关交通管理部门注销。机动车所有人逾期不办理注销登记的，公安机关交通管理部门应当公告该机动车登记证书、号牌、行驶证作废。</p>\n<p>　　交通管理部门建议，机动车所有人自行计算机动车检验周期，如名下车辆因超过三个检验周期，已达到国家规定的强制报废标准的，请在2018年1月15日前办理交售机动车报废回收企业并办理注销登记，以免影响生成更新指标。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/737ee483-cc85-4b56-af9c-9c845c022653.jpg',NULL,231,'2017-12-15 16:13:00','2017-12-17 01:15:29','72',0),(2902,'北京2022年冬奥会启动特许经营试运行计划','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>北京12月16日电 (记者 尹力)北京冬奥组委16日启动北京2022年冬奥会和冬残奥会(下简称北京冬奥会)特许经营试运行计划。该计划通过推出部分类别的特许商品，测试北京冬奥会特许商品市场定位、价格水平及消费者需求特点。</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：12月15日，北京2022年冬奥会会徽“冬梦”发布。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 韩海丹 摄\" src=\"upload/image/32f5b84b-8fed-44ef-b6be-c79d88ebe661.jpg\" title=\"资料图：12月15日，北京2022年冬奥会会徽“冬梦”发布。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 韩海丹 摄\"> \n <input id=\"ed27838004f345dab48d1dcc2bf7691e\" name=\"ed27838004f345dab48d1dcc2bf7691e\" type=\"hidden\" value=\"http://www.chinanews.com/tp/hd2011/2017/12-16/788873.shtml\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：12月15日，北京2022年冬奥会会徽“冬梦”发布。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 韩海丹 摄 \n</div>\n<p>　　北京冬奥会两大会徽日前已正式亮相。据北京冬奥组委介绍，为配合会徽发布，扩大会徽推广，满足消费者和奥林匹克收藏爱好者对北京冬奥会纪念品的消费需求，冬奥组委启动了特许经营试运行计划，旨在为北京冬奥会特许经营计划正式运行打好基础。</p>\n<p>　　据北京冬奥组委介绍，本次试运行将有针对性地开展市场测试，即日启动至2018年6月30日截止。约半年的试运行期结束后，明年7月将正式启动特许经营计划，推出更多品类的北京冬奥会特许商品，并在更大的市场范围内销售。</p>\n<p>　　从产品类别来看，试运行计划将推出6大类特许商品，包括徽章、钥匙扣等非贵金属产品，贵金属纪念章及相关制品，服装服饰及配饰，文具，陶瓷产品以及邮票和邮品。北京冬奥组委通过定向征集方式，选择曾参与过2008年北京奥运会、2010年上海世博会和2014年南京青奥会特许商品生产的企业作为特许生产商。</p>\n<p>　　在销售方式上，北京冬奥组委采取线上线下相结合的方式销售。其中，16日14时已启动北京冬奥会特许商品预售。当晚20时开始，参加预售的50款左右特许商品将上线。</p>\n<p>　　线下销售方面，北京冬奥组委采取设立专卖店或专柜的方式销售，首批将批准开设10余个特许商品零售店，分布在北京、石家庄、张家口、南京、上海等城市。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/32f5b84b-8fed-44ef-b6be-c79d88ebe661.jpg',NULL,1065,'2017-12-16 20:56:00','2017-12-17 01:15:29','74',0),(2903,'书法教育界：要让“书法进课堂”见实效','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>北京12月16日电 (记者 马海燕)“开展中小学书法教育，并不是让每一个学生成为书法家，而是让每一个孩子都能写一手好字，以此了解并传承优秀传统文化，为终身学习奠定基础。”中国教育学会常务副会长兼秘书长杨念鲁16日表示。</p>\n<p>　　由中国教育学会组织的全国中小学书法教育教学研讨会16日在北京召开。来自全国政协、教育部、中国文联、中国书法家协会、中国教育学会书法教育专业委员会的相关领导，以及全国各地的书法教研员及相关教师近400余人参加了会议。如何推进“书法进课堂”见实效成为与会者讨论的话题。</p>\n<div style=\"text-align:center\"> \n <img alt=\"教育部基础教育司副巡视员王岱发言。 主办方供图\" src=\"upload/image/0458a76a-96db-4698-80a8-bf82c4f7a127.jpg\" title=\"教育部基础教育司副巡视员王岱发言。 主办方供图\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  教育部基础教育司副巡视员王岱发言。 主办方供图 \n</div>\n<p>　　杨念鲁表示，书法教育不仅是美的教育，它还具有育德、启智、健体、审美等综合效应，这不仅是一种技能的教习，也是一种文化修炼、精神陶冶。“书法进课堂”是新时代对中小学教育提出的新要求。</p>\n<p>　　教育部基础教育司副巡视员王岱指出，目前一些地方还存在着书法课时保障不到位、书法教师不足、教学评价方式有待改进等问题。要坚持不懈地开展书法教育工作，推进“书法进课堂”工作；同时要加强教学管理，提高书法教育质量；加强社会合作，共同推进书法教育工作。</p>\n<p>　　中国书法家协会主席苏士澍认为，目前我国书法教育需要正面解决区域发展不平衡、师资力量不足的问题，需对书法教育研究给予更多的关注和投入，创设良好的教育环境，建立长期发展目标。</p>\n<p>　　杨念鲁提出，中国教育学会将把“书法进课堂”作为书法教育专业委员会的中心工作大力推进，并依托这个平台对中小学书法教育提供标准研制、教学研讨、师资培训、政策咨询以及成果展示和交流的全方位、专业的支持和服务。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/0458a76a-96db-4698-80a8-bf82c4f7a127.jpg',NULL,1649,'2017-12-16 20:15:00','2017-12-17 01:15:29','75',0),(2904,'老挝官员向中国车企抛“橄榄枝” 盼建汽车基地','魏巍','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>柳州12月15日电(周潇男)老挝人革党中组部党委副书记、副部长万赛・庞沙文15日在此间表示，老挝与中国汽车行业合作潜力巨大，希望中国的车企到本国投资兴业。</p>\n<p>　　当天上午，老挝高级干部考察团一行30人来到广西汽车集团考察，万赛・庞沙文为团长。</p>\n<p>　　广西柳州是中国距离东盟最近的新兴汽车城。广西汽车集团是一家生产汽配件、发动机和整车的汽车生产企业，2016年营收突破200亿元人民币。</p>\n<p>　　当日，考察团参观了该集团的冲焊车间前围板自动化生产线、内饰车间座舱生产线、外饰车间涂装生产线。考察团成员仔细聆听该企业的相关的介绍，不断咨询该集团的汽配产品信息，并和相关负责人互换联系方式，欲寻求进一步合作的机会。</p>\n<p>　　万赛・庞沙文称，通过考察学习，发现广西汽车集团发展十分迅猛，具有一定优势，且是自主品牌。</p>\n<div style=\"text-align:center\"> \n <img alt=\"12月15日，老挝高级干部考察团一行30人考察参观广西汽车集团考察后提出，希望该集团能在老挝资兴建汽车基地或分厂。　蒙圣华 摄\" src=\"upload/image/0c0ab6e9-3fa5-499a-90a3-3f119a004855.jpg\" title=\"12月15日，老挝高级干部考察团一行30人考察参观广西汽车集团考察后提出，希望该集团能在老挝资兴建汽车基地或分厂。　蒙圣华 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  12月15日，老挝高级干部考察团一行30人考察参观广西汽车集团考察后提出，希望该集团能在老挝资兴建汽车基地或分厂。　蒙圣华 摄 \n</div>\n<p>　　“中国的卡车、工程车、摩托车在老挝很受欢迎。近年来，老挝车辆购买量持续激增，广西汽车集团应利用地缘优势，积极对接与老挝相关产业的合作。”万赛・庞沙文说。</p>\n<p>　　万赛・庞沙文的提议，得到了积极回应。“我公司已在老挝设立了经销商，希望能有更进一步的合作。”广西汽车集团总裁助理韦明凤表示。</p>\n<p>　　考察团成员随后详细的咨询了相关政策和是否存在汽车垄断等问题，在得到解答后，万赛・庞沙文再次抛出“橄榄枝”，饶有兴趣地表示，老挝目前还没有自己的汽车工业体系，欢迎广西汽车集团去投资兴建汽车基地或分厂。</p>\n<p>　　同行的老挝工贸部进出口司司长苏里荣・皮拉冯表示，中国汽车早在10多年前就进入老挝市场。从2014年开始，老挝开始进口柳州企业生产的汽车零部件及整车。</p>\n<p>　　万赛・庞沙文表示，希望中方能派人到老挝进行洽谈，早一步促成相关合作。“我愿意为柳州的车企积极宣传，让柳州车企更快走进老挝，走向更多的国家。”(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/0c0ab6e9-3fa5-499a-90a3-3f119a004855.jpg',NULL,921,'2017-12-15 17:25:00','2017-12-17 01:15:29','72',0),(2905,'侧记：美国旧金山告别市长李孟贤','孔庆玲','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>旧金山12月15日电 题：侧记：美国旧金山告别市长李孟贤</p>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 刘丹</p>\n<p>　　15日清早6时30分左右，美国旧金山市政府大厦前波尔克街，两辆闪烁着红灯的消防车相向伸出云梯，四名消防员在清冽的寒风中奋力悬挂一面巨大的美国国旗。街对面的市政府大厦旗杆上，降下一半的美国国旗飘扬着。</p>\n<div style=\"text-align:center\"> \n <img alt=\"当地时间12月15日，美国旧金山举行李孟贤市长遗体告别仪式，纪念12日凌晨因心脏病去世的李孟贤。他是旧金山历史上首位华裔、亚裔市长，享年65岁。 <a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 刘丹 摄\" src=\"upload/image/0e22085b-0035-4fac-a6ee-de493e378ec9.jpg\" title=\"当地时间12月15日，美国旧金山举行李孟贤市长遗体告别仪式，纪念12日凌晨因心脏病去世的李孟贤。他是旧金山历史上首位华裔、亚裔市长，享年65岁。 <a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 刘丹 摄\"> \n <input id=\"7564d984e7db4005bde126bf9bba2508\" name=\"7564d984e7db4005bde126bf9bba2508\" type=\"hidden\" value=\"http://www.chinanews.com/tp/hd2011/2017/12-16/788915.shtml\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  当地时间12月15日，美国旧金山举行李孟贤市长遗体告别仪式，纪念12日凌晨因心脏病去世的李孟贤。他是旧金山历史上首位华裔、亚裔市长，享年65岁。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 刘丹 摄 \n</div>\n<p>　　消防车后面的广场，一颗缀满白色灯饰的巨型圣诞树竖立，提醒人们美国最隆重的传统节日圣诞节就要来临。广场上空，两架直升飞机盘旋，发出轰鸣声。</p>\n<p>　　警察、消防员、法警、市府官员列队在台阶上等候。在他们一旁，花束、蜡烛、卡片、照片、留言字条铺满层层台阶。</p>\n<p>　　“李孟贤市长安息(Mayor Ed Lee Rest In Peace)”、“我们爱你”的中英文字样随处可见。</p>\n<div style=\"text-align:center\"> \n <img alt=\"当地时间12月15日，美国旧金山举行李孟贤市长遗体告别仪式，纪念12日凌晨因心脏病去世的李孟贤。他是旧金山历史上首位华裔、亚裔市长，享年65岁。图为民众排队进入市政府。 <a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 刘丹 摄\" src=\"upload/image/ba51d86f-ca71-401a-939d-85b8ca3fbf0d.jpg\" title=\"当地时间12月15日，美国旧金山举行李孟贤市长遗体告别仪式，纪念12日凌晨因心脏病去世的李孟贤。他是旧金山历史上首位华裔、亚裔市长，享年65岁。图为民众排队进入市政府。 <a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 刘丹 摄\"> \n <input id=\"2aa132e8ff174c79aa2c70d86029a464\" name=\"2aa132e8ff174c79aa2c70d86029a464\" type=\"hidden\" value=\"http://www.chinanews.com/tp/hd2011/2017/12-16/788915.shtml\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  当地时间12月15日，美国旧金山举行李孟贤市长遗体告别仪式，纪念12日凌晨因心脏病去世的李孟贤。他是旧金山历史上首位华裔、亚裔市长，享年65岁。图为民众排队进入市政府。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 刘丹 摄 \n</div>\n<p>　　近7时，数辆警用摩托车开道，从麦克阿里斯特街转弯进入波尔克街，一辆黑色灵车缓缓驶来停下，消警举手敬礼，6名黑衣男子从灵车后部将美国国旗包裹的棺木抬起，慢慢步上台阶，进入市政府大厦。</p>\n<p>　　就在抵达前，灵车由警队护送，清早6时离开帝利市殡仪馆，绕行李孟贤住所，以及他每天上班的路线。</p>\n<p>　　12日凌晨1时11分因心脏病猝逝的李孟贤在离开了这座大厦3天后，再次回到他工作了38年的市政府。65岁的他长眠在棺木里，与他的员工、市民作别。</p>\n<p>　　大厦外，民众排队安静等候。抱着婴儿的年轻母亲，带着小狗的长者，背着双肩包的学生，不同肤色，不同阶层，长长的队伍延伸了一个街区。有人匆匆赶来，在台阶前放一束花，抹一把泪。</p>\n<p>　　市政府圆形大厅内，旧金山交响乐团的大提琴手演奏着法国作曲家萨扎尔・弗兰克的《天赐神粮》(Panis Angelicus)，庄严神圣，带着淡淡的忧伤。</p>\n<p>　　李孟贤的灵柩置放在大厅的入口处，黑色双重围栏中，一名法警和一名警察守护。对面的台阶，是他2011年宣誓就任市长的地方，也是他曾经无数次主持和出席各类活动的所在。拾级而上，是他用过6年的市长办公室。</p>\n<div style=\"text-align:center\"> \n <img alt=\"当地时间12月15日，美国旧金山举行李孟贤市长遗体告别仪式，纪念12日凌晨因心脏病去世的李孟贤。他是旧金山历史上首位华裔、亚裔市长，享年65岁。 <a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 刘丹 摄\" src=\"upload/image/e9aeebd1-fec3-470b-a92b-d8106f06a383.jpg\" title=\"当地时间12月15日，美国旧金山举行李孟贤市长遗体告别仪式，纪念12日凌晨因心脏病去世的李孟贤。他是旧金山历史上首位华裔、亚裔市长，享年65岁。 <a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 刘丹 摄\"> \n <input id=\"fe537d96b8114256b15ac77939c62995\" name=\"fe537d96b8114256b15ac77939c62995\" type=\"hidden\" value=\"http://www.chinanews.com/tp/hd2011/2017/12-16/788915.shtml\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  当地时间12月15日，美国旧金山举行李孟贤市长遗体告别仪式，纪念12日凌晨因心脏病去世的李孟贤。他是旧金山历史上首位华裔、亚裔市长，享年65岁。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 刘丹 摄 \n</div>\n<p>　　大厅两侧陈列的花圈，用中英文写着缅怀和不舍。素衣的人群缓缓走到台阶前，向灵柩鞠躬、默哀，致以最后的敬意，啜泣和呜咽声与琴声交织。</p>\n<p>　　圆形大厅一旁的南大厅内，人们分成6组列队，6本留言册，中英文一页页填满，字迹不时被留言人的眼泪浸湿。</p>\n<p>　　晚上7时，预定的告别时间已到，大厦外等候的队伍仍在不断拉长，匆匆赶来的人们希望最后见一面李孟贤，他们的愿望得以实现。</p>\n<p>　　8时，李孟贤灵柩离开市政府大厦。</p>\n<p>　　台阶上，李孟贤的大幅照片被鲜花环绕，有人在照片一侧放了一个圣诞帽，露出招牌笑容的他，乐呵呵看着告别的人们，仿佛未曾离开。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/e9aeebd1-fec3-470b-a92b-d8106f06a383.jpg',NULL,417,'2017-12-16 15:35:00','2017-12-17 01:15:29','73',0),(2906,'广西举办中高级人才交流大会 部分岗位年薪百万以上','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>南宁12月16日电(记者 黄艳梅)2017年广西中高级人才交流大会16日在南宁国际会展中心举办，共有215家用人单位携6231个岗位到会招才纳士，部分岗位年薪达百万元以上。</p>\n<p>　　本次人才交流大会由广西人才市场主办，这是本年度广西面向中高端人才举办的规模最大、规格最高的人才交流会。</p>\n<p>　　本次大会名企云集，行业分布广泛，参会单位有上市公司、大型国企、高职高校、科研院所、知名民企等类型，涵盖了计算机、教育科研、房地产、医疗卫生、金融投资、能源等多个领域。</p>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>记者当天在会上了解到，本次大会岗位均为年薪5万元以上，其中6-10万年薪岗位有2557个，10-15万年薪岗位1497个，15-20万年薪岗位987个，20万以上年薪有206个。数个岗位突破百万年薪，如广西一家金融机构提供高级管理人员岗位年薪达100―150万元左右。</p>\n<div style=\"text-align:center\"> \n <img alt=\"2017年广西中高级人才交流大会现场。　黄艳梅 摄\" src=\"upload/image/bd1afc48-6ebc-4dc8-a99b-b3f2e8134f62.JPG\" title=\"2017年广西中高级人才交流大会现场。　黄艳梅 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  2017年广西中高级人才交流大会现场。　黄艳梅 摄 \n</div>\n<p>　　据主办方统计，当天参会人才有一万多人，主要以中高级经营管理、技术、营销类等社会精英以及高校应往届硕士、博士研究生为主。广西多地推出人才优惠政策，组织人社部门组团争夺人才。大会还首次为2016-2018年毕业的全日制历届、应届硕博人才提供交通补助，鼓励区内外硕博人才前来参会。</p>\n<p>　　广西是中国面向东盟开放合作的前沿和窗口，在中国对外开放格局中地位突出。广西渴望人才，也重视人才。近年来，广西通过实施院士后备培养工程、八桂学者工程、高校“百人计划”等重大人才项目，灵活引进了数百名“国字号”领军人才以及一大批金融、扶贫、园区管理等领域的急需紧缺高层次人才和优秀青苗人才。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/bd1afc48-6ebc-4dc8-a99b-b3f2e8134f62.JPG',NULL,1569,'2017-12-16 20:56:00','2017-12-17 01:15:29','65',0),(2907,'大学英语四六级今开考 历经30年四六级路在何方？','吉翔','央广网','<p>　　央广网北京12月16日消息(记者车丽 河南台记者赵珊珊)据中国之声《新闻晚高峰》报道，2017年12月全国大学英语四、六级考试及高等学校英语应用能力课程考试今天(16日)开考，全国约有900多万考生参加考试。诞生于1987年，四六级今年走入了而立之年。三十岁的四六级未来将何去何从？</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：2017年6月17日，全国英语四六级等级考试开考。朱一鸣 摄 图片来源：视觉中国\" src=\"upload/image/86ac1a85-e6b2-4be4-a4d3-ae670c0e30d8.jpg\" title=\"资料图：2017年6月17日，全国英语四六级等级考试开考。朱一鸣 摄 图片来源：视觉中国\"> \n <input id=\"0e9648a163bb47589252453b163286ca\" name=\"0e9648a163bb47589252453b163286ca\" type=\"hidden\" value=\"http://www.chinanews.com/tp/hd2011/2017/06-17/748442.shtml\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：2017年6月17日，全国英语四六级等级考试开考。朱一鸣 摄 图片来源：视觉中国 \n</div>\n<p>　　“四级靠运气，六级靠实力”，这是今天刚刚走出英语六级考场的河南安阳考生闫明的体会。</p>\n<p>　　近年来，人们对于四六级考试的争议一直不断。一方面，不少学生认为四六级考试越来越难；另一方面，不少用人单位和国内外高校认为，四六级考试并不能完全检测出学生真正的英语水平。作为全国性的英语考试，也是客观评价英语学习能力之一的一种手段，身边大学生们如何看待这场考试？</p>\n<p>　　记者：你参与四六级考试为了什么？</p>\n<p>　　学生：为了拿到毕业证。</p>\n<p>　　记者：换言之，四六级考试是和学位是挂钩的。如果和学位不挂钩，你是否会参加考试？</p>\n<p>　　学生：不会。对一些保研的同学而言，有的学校需要六级成绩；还有一些同学出去找工作的时候，一些用人单位也比较看重四六级的成绩。</p>\n<p>　　至于大学四六级考试到底有没有“用”？记者走访了多家用人单位人力资源部门，一半以上都要求四级是低配。</p>\n<p>　　不过，在12月的国家公务员考试中，部分岗位也不再对是否通过四六级有硬性要求，部分高校也明确表示不再把四六级成绩与学位证挂钩。</p>\n<p>　　在2017年12月召开的第三届语言测试与评价国际研讨会会上，教育部部长助理郑富芝透露，教育部已顺利完成中国英语能力等级量化研制，这是中国第一个覆盖全学段的英语能力测评标准。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/86ac1a85-e6b2-4be4-a4d3-ae670c0e30d8.jpg',NULL,1791,'2017-12-16 19:36:00','2017-12-17 01:15:29','64',0),(2908,'登山界大咖云集沈阳 珠峰探索公园首揭神秘面纱','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>沈阳12月16日电 (记者 沈殿成)夏伯渝、陈业伟、张伟、扎西・拉克巴・夏尔巴、孙义全，这是一个个在登山界耀眼的名字。12月16日下午，他们出现在沈阳参加一个共同的活动―沈阳“珠峰探索公园”首次揭开神秘面纱。</p>\n<p>　　沈阳“珠峰探索公园”是全球首个珠峰探索主题室内公园，其面向3岁以上全龄客群，是大众切身感受珠峰风貌的互动式体验综合体。</p>\n<p>　　辽宁籍艺术家、登山家孙义全曾于2013年5月成功登顶珠峰，成为世界上首位登顶珠穆朗玛峰的艺术家，也是中国从南坡登顶珠峰最年轻的登山家。“珠峰探索公园”项目是合作方携手孙义全历时10年筹备而成。整个公园分为珠峰实物装备展区、珠峰文献展区、珠峰摄影展区、珠峰影院、珠峰极寒体验馆、珠峰VR体验馆、孙义全作品展区、喜马拉雅山脉等比缩放展示区、珠峰环保展区及珠峰周边产品展区，共计十大展区。</p>\n<div style=\"text-align:center\"> \n <img alt=\"现场举行签约仪式。　沈殿成 摄\" src=\"upload/image/cf4449fd-9085-4535-8a8c-0f9bc3172e81.jpg\" title=\"现场举行签约仪式。　沈殿成 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  现场举行签约仪式。　沈殿成 摄 \n</div>\n<p>　　在珠峰探索公园中，除了实物，游客还可听到夏伯渝、陈业伟、张伟、扎西・拉克巴・夏尔巴等人以影像方式进行的现场讲解。其中摄影作品部分由中国金犀牛最佳摄影师奖获得者、全球14座海拔8000米以上绝对高峰拍摄者陈业伟讲解；实物展由国际职业八千米登山领队、十四座探险俱乐部创始人张伟讲解；文献展由登山界的刀锋战士，1975年开始攀登珠峰的著名登山家夏伯渝讲解；珠峰的地形地貌，喜马拉雅山脉的全景，珠峰攀登路线等由尼泊尔七峰探险公司的董事总经理、已经成功登顶珠穆朗玛峰8次的扎西・拉克巴・夏尔巴讲解。</p>\n<p>　　推介会上，孙义全说，这个公园脱胎于我的一个理念，它是我之于珠峰、之于艺术、之于人生的一次呈现。希望通过珠峰探索公园，让更多的人走近珠峰，带给大家更多的思考和感悟。珠峰不仅仅只属于攀登者，她还属于每一个可以与之分享生死体验、艺术观念以及对人生充满热爱的人们。</p>\n<p>　　对于艺术创作，孙义全说，于我而言，登山本身即是艺术的创作，登山过程的一切体验与感悟都是这件艺术品中不可或缺的部分。珠峰探索公园本身即是一个创想和作品，也是对人应该回归自然的呼唤。</p>\n<p>　　推介会现场，全球第二大家庭娱乐景点运营公司英国默林娱乐集团旗下品牌―沈阳乐高探索中心和沈阳海洋探索中心签署了与珠峰探索公园项目运营方以及孙义全的三方战略合作协议。</p>\n<p>　　推介会上宣布：2018年3月25日，“珠峰探索公园”正式启幕。</p>\n<p>　　推介会之后，夏伯渝、陈业伟、张伟、扎西・拉克巴・夏尔巴、孙义全等共同出席主题为“攀登向上荣耀沈阳”的2017中国沈阳国际登山论坛，分享登山过程中超越生死的故事，并就珠峰与城市精神、登山与人生等话题畅所欲言。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/cf4449fd-9085-4535-8a8c-0f9bc3172e81.jpg',NULL,717,'2017-12-16 20:40:00','2017-12-17 01:15:29','74',0),(2909,'刘涛节目当“园长”疲累 照顾六个小朋友洗澡','刘欢','红网','<p align=\"center\"><img src=\"upload/image/4c68192e-9d21-472e-ac3d-ef5a479c8605.jpg\"></p>\n<p class=\"pictext\" align=\"center\">刘涛</p>\n<p>　　<strong>《萌宠小大人》刘涛曾舜��“园长CP”首发“遇险”</strong></p>\n<p>　　“全能”刘涛搭档“小王子”曾舜��组成“园长CP”空降萌宠学园。首次面对“两大难搞物种”，两人显然有些不适应，刘涛忙成“陀螺”累到崩溃，为照顾好孩子还直接当众diss节目组；曾舜��寒冬中落水，还被孩子们集体喊“漏油”。开播便蓄势“搞事情”的《萌宠小大人》将会开启怎样的新世界呢？</p>\n<p>　　《萌宠小大人》是由爱奇艺、晓映传媒、华谊兄弟时尚联合出品的国内首档成长实验类真人秀，秉持着“直击儿童社会能力”节目的初衷，自明天起，每周六20:00，爱奇艺带您感受来自萌宠学园的欢乐。</p>\n<p>　　<strong>刘涛罕见发飙当众diss节目组只为孩子？ 曾舜��下水救狗受伤入院</strong></p>\n<p>　　萌宠学园集结了世界上“两大难搞物种”：萌娃和萌宠，还一次凑齐了六对！这样刘涛和曾舜��在采访中都表示“有难度”、“要完”。</p>\n<p>　　果不其然，刚刚上任园长的刘涛就在孩子们吃饭的时候“爆发”了，“你们想要的太多了”，“我特别想diss这件事情”！素来有“陀螺涛”之称的刘涛面对任何事情都能从容应对，在萌宠学园中，无论是化解“疯狂脑型男”泰勒的认真battle，还是化身“名侦探柯南”确认狗屎的归属，即便是同时照顾六个孩子洗澡她都能坚持下来，到底节目组对孩子做了怎样的安排让她如此动怒，当场“发飙”diss节目组？副园长曾舜��也同样“出师不利”。他自告奋勇前去解救被困河中的汪星人，在经历船桨丢失、狗狗落水等一系列事件之后，他果断选择下水“徒手救狗”。汪星人营救成功，他的脚却被河底异物扎伤，不得不去医院处理。如此英勇的举动会对孩子们有怎样的影响？</p>\n<p align=\"center\"><img src=\"upload/image/de0b1ff3-e56f-4106-9302-d3191f8e548d.jpg\"></p>\n<p class=\"pictext\" align=\"center\">曾舜��</p>\n<p>　　<strong>“小大人”上阵捡狗屎 人生第一课困难重重</strong></p>\n<p>　　六位萌娃泰勒、贝贝、杨珉骁、小糖果、郑子祺、何芯妍首次离开父母，独自面对未知的生活，而第一步便是选择自己的汪星人朋友。六只汪星人小豆、汤包、奥斯卡、尤达、大胖、肉丸子分别被放进六个不透明的箱子，孩子们能否选中心爱的狗狗？他们选狗的兴奋很快被萌宠学园的第一个重大任务冲淡，他们要集体出门捡狗屎！孩子们对满地臭气熏天的狗屎望而却步，捡狗屎的过程也是充满艰辛，小糖果因难以忍耐狗屎的臭味而干呕，泰勒轻微洁癖却踩中狗屎，一系列事件层出不穷，刘涛和曾舜��又将怎样应付？</p>\n<p>　　《萌宠小大人》开篇便以别出心裁的方式开启锻炼孩子能力的第一课，身为园长的刘涛、曾舜��更是在这个过程中起到了言传身教的作用。在当下儿童安全愈发引起人们广泛关注的今天，《萌宠小大人》无疑是用精心的任务设计、用心的细节考虑和专业的行业理念亮出网综的新态度，扛起新时代的社会责任，树立起综艺节目趣味与意义并存的新标杆。《萌宠小大人》将于12月16日起，每周六20:00与您相约爱奇艺，一起萌动整个冬日！</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/de0b1ff3-e56f-4106-9302-d3191f8e548d.jpg',NULL,598,'2017-12-15 14:38:00','2017-12-17 01:15:29','68',0),(2910,'郑晓龙新剧将播出 杨子姗赞陈晓入戏快','魏巍','新华网','<p>　　<strong>郑晓龙《红蔷薇》将播 杨子姗赞陈晓入戏快</strong></p>\n<p>　　2017年转眼接近尾声，江苏卫视幸福剧场岁末压轴大戏《红蔷薇》12月16日起播出。该剧是由郑晓龙担纲艺术总监，金晔执导、杨子姗、陈晓领衔主演的年代谍战剧。讲述了不经世事的千金小姐夏雨竹，主动投身革命浪潮，与肖君浩并肩战斗，共同为民族复兴奉献青春，逐步成长为一名拥有坚定革命信仰的人。</p>\n<p align=\"center\"><img src=\"upload/image/310a9c50-3944-4c0f-827e-7452736694a0.jpg\"></p>\n<p>　　不同于以往谍战题材剧，《红蔷薇》以另类视角推陈出新，在爱国热血的主旋律色彩之下，摆脱了无所不能的英雄化脸谱，将血肉交融的人物成长过程丰富呈现。金牌团队幕后操刀，实力派演员鼎力加盟，使得该剧未播先热。12月14日，《红蔷薇》举办了开播发布会，众主创莅临发布会现场与观众热烈互动，歌手张靓颖也空降发布会现场为大家带来了同名主题曲。同时，主创团队也为观众带来了该剧拍摄时精彩纷呈的幕后故事。在发布会现场，郑晓龙导演也坦言：“《红蔷薇》除了展现那个时代之外，也是想去表达了一种精神，因为这部片子也是给年轻人看的”。</p>\n<p>　　女一号杨子姗搭档陈晓演绎不一样的谍战剧，现场对陈晓也是盛赞有加“陈晓是一个很有经验的演员，他的入戏速度很快，我们开始都不好意思和对方说话，结果晚上就拍吻戏，没想到一遍就能过”。剧中情感线索也颇耐人寻味，既有令人百感交集的至诚之爱，也不乏闺蜜同心携手到反目成仇的虐心友情。</p>\n<p>　　金牌师徒搭档打造别样谍战剧</p>\n<p>　　郑晓龙：精神美和心灵美才最重要</p>\n<p>　　众所周知，《红蔷薇》制作班底富有“超强含金量”，导演阵容郑晓龙、金晔师徒曾联手《甄�执�》、《芈月传》创收视佳话；编剧阵容周萌、王莹菲联合执笔《与狼共舞》也曾创播出空前盛况。金牌师徒搭档联手制作，默契值爆表，更为《红蔷薇》的精彩呈现奠定实力基础。在发布会现场，郑晓龙导演对团队的幕后付出表示了高度认可，金晔导演也向观众分享了创作初衷，他表示：“我做这个《红蔷薇》目的也是为了让大家不要忘记无名英雄，虽然前途未补，他们依然无怨无悔。”</p>\n<p>　　作为一部体现爱国热血的主旋律题材剧，《红蔷薇》所承载的精神内核自然不同。它讲述的是“女特工”的成长故事，如何能够规避一般谍战题材架构，在悬疑重重、危险交织的故事背景下去体现主人公精神内涵，就需要主创团队巧夺天工，在固有的套路下提炼人物精华。对于这部剧的“别样”设定，在发布会现场，郑晓龙导演表示要“出人物”，他说：“《红蔷薇》这部剧，跟以往的这些谍战片有所不同，主要讲的是人生，讲一个人有价值的一生。而这个人的工作恰恰是卧底，这个就特别有意思。这样就能在谍战的过程中塑造出几个有血有肉，有时代代表性的鲜明人物，所以我就对这个特别感兴趣”。</p>\n<p>　　而对于这种具备积极思想的正能量剧，郑晓龙导演也坦言要诠释美感的初心，“我们想讲的美不是那种关于穿衣服的，人多么帅气的，而是敢于做情报工作的精神上的美，是心灵的美，这才是最重要的，也是长久的，真正感动人的。谍战是很残酷很激烈的，不是偶像的那种美”。</p>\n<p>　　情感铺陈揭秘谍战特工热血背后</p>\n<p>　　编剧王莹菲：这是一部很浪漫有温度的戏</p>\n<p>　　《红蔷薇》以“人”为中心，除了信仰支撑以外，爱情、友情、亲情的多面穿插也为主人公们谍海成长的惊险历程增添色彩。现场的40分钟的片花展示了这部不一样的谍战剧爱情、友情、亲情的发展脉络。在爱情上，先有夏雨竹和任致远的精神启蒙，再有夏雨竹与肖君浩之间的信仰坚守，来展现时代洪流下的精神蜕变；在友情一面，也从闺蜜共患难的温暖友情，突出形势冲刷后反目成仇的人性变革；而在亲情之上，更直面乱世而生的骨肉亲情间的抉择和矛盾。多面情感色彩的铺垫，将历史框架中的传奇脸谱真实再塑，于血肉间诠释谍战特工的热血幕后，更为人所津津乐道。</p>\n<p>　　在时代背景下裹挟的人们，有冲突，有矛盾，有伪装，也有忠诚和热血，这也就是《红蔷薇》所要表达的“谍战人生”。对于该剧所想要表达的意义，编剧王莹菲表示：“我觉得这部戏是很浪漫，很有温度的一部戏。它不仅仅讲了谍战，更多的是讲人生理念，这些之前郑晓龙导演和我们讨论的时候也说过，我们讲的是谍战人生。我记得当时剧本里有一句台词：在这个波澜壮阔的历史里，时代改变了他们，但他们最终也改变了时代”。</p>\n<p>　　杨子姗：《红蔷薇》是自《致青春》后又一次成长</p>\n<p>　　与毛林林演绎谍战姐妹花</p>\n<p>　　除了题材的创新点，《红蔷薇》中的演绎阵容也可谓不按套路出牌，清一色青年演员压阵，剧中主演杨子姗、陈晓更是谍战剧“首秀”。不仅如此，杨子姗更是挑战超长年龄跨度演绎谍战女特工一生事迹，在谈及第一次接演该剧时的难点，杨子姗则表示会把压力变成动力。而对于人物塑造，她也有自己的心得： “因为我从来没演过谍战剧，在我知道夏雨竹其实有原型和真实事迹后，我希望自己尽量去还原当时的心情，以及她为国家做出的牺牲，希望能够去贴近她。在《致青春》中我每天都在否定自己，因为角色离自己太远了，经过那四个月表演的训练，接下来几年我发现很多角色都可以掌控，这次我又战胜了我自己，这个夏雨竹让我有了很大成长”。</p>\n<p>　　在发布会现场，杨子姗、毛林林、田雷、谭凯也为观众提前曝光了在剧中的角色关系，杨子姗更在现场打趣到四人关系是田雷“追”出来的。虽身为剧中的第一大反派人物代表，田雷却不忘逗趣相爱相杀不易，因为“反派也要面子”。</p>\n<p>　　此外，杨子姗、毛林林戏里的闺蜜CP档充满爱恨情仇，二人虽一个是富家千金，一个是出身低微的丫头，却能够互相照顾，姐妹情深，但终因身份背景不同和时势造化产生了信仰分歧渐行渐远，最后彼此对立拔刀相向成了“塑料姐妹花”。不过在现场，两人却都默契直言“想关心对方”、“会心疼”，并互诉对对方角色的欣赏之处。毛林林表示：“陈晓今天不在，我是带班的‘男一号’，因为剧中所有的翻墙、格斗危险戏都是我自己亲自来的，没有用替身”。</p>\n<p>　　杨子姗、陈晓默契有加“吻戏一遍过”</p>\n<p>　　张靓颖空降发布会献歌</p>\n<p>　　预告片中，陈晓饰演的肖君浩表面上风流倜傥、玩世不恭，在乱世中结识了杨子姗饰演的夏雨竹，并对她展开了猛烈的追求攻势，而实际上，肖君浩是一位智勇双全、富有家国情怀的地下党，一直隐瞒身份在敌方潜伏多年，两人在重重危机中共同携手度过难关，成了一对生死恋人。</p>\n<p>　　虽然陈晓并未到现场出席发布会，但杨子姗对他却是赞赏有加，“我觉得陈晓是一个很有经验的演员，他的入戏速度很快，我们开始都不好意思和对方说话。结果晚上就拍吻戏，没想到一遍就能过”。有男主演缺席的遗憾也有一个惊喜，《红蔷薇》主题曲演唱者张靓颖突然空降发布会，让观众大感意外，除了现场播放了唯美的主题曲MV以外，她还清唱了《红蔷薇》的高潮部分，让各位主演如痴如醉。张靓颖谈到这首谍战剧同名主题曲的创作时说“接到这个任务的时候我就想如果做一个不一样的谍战剧主题曲，在创作时候也想把一个革命者人生的跌宕起伏融入到作品当中”。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/310a9c50-3944-4c0f-827e-7452736694a0.jpg',NULL,1669,'2017-12-15 15:38:00','2017-12-17 01:15:29','68',0),(2911,'上海车牌“年度最后一拍”：逾22万人参拍 中标率5.3%','吉翔','中国新闻网','<div style=\"text-align:center\"> \n <img src=\"upload/image/12b32bb4-a785-4321-b33a-068e5a499779.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>上海12月16日电 (记者 马化宇)上海12月车牌拍卖结果16日最终出炉，当天共有228148人参与竞拍，中标率为5.3%。</p>\n<p>　　本月官方共投放沪牌额度12147辆，比上月增加1145辆，在历史投放数量上处于高位，被市民称为“本年度最后一拍的福利”。</p>\n<p>　　为此，不少人希望借此机会“碰碰运气”。从中标率来看，较此前有所提升。</p>\n<p>　　然而面对超过22万人的“拍牌大军”，官方投放的沪牌依旧显得杯水车薪，不少“黄牛”也借此推出相应的“代拍服务”。</p>\n<p>　　在一家“代拍”网店中，顾客可以选择多达八种“套餐”：“三年以上钻石拍手代拍费12000元(人民币，下同)，不中赔偿300元”；“专家拍手代拍费19800元，不中赔偿600元”；更有甚者还推出“内部代拍名额”38800元的天价。</p>\n<p>　　有业内人士称，这些号称有“内部资源”的代拍者实际是通过增加代拍人手或是外挂软件提高中标概率，并不存在“100%命中”的说法，请市民切勿轻信。</p>\n<p>　　根据本月拍卖数据显示，本次车牌拍卖的平均成交价为92848元、最低成交价为92800元，若加上“黄牛”的代拍费用，沪牌“身价”将轻松突破10万元。</p>\n<p>　　当前，上海车牌拍卖实行季度“警示价”制度，即每个季度由官方根据上季度拍卖情况设置警示价，竞买人首次出价阶段出价不得高于“警示价”。</p>\n<p>　　今年四季度，上海车牌拍卖警示价为91700元，沪牌的拍卖价格已经连续半年维持在9万元以上，频频刷新历史最高纪录。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/12b32bb4-a785-4321-b33a-068e5a499779.jpg',NULL,688,'2017-12-16 13:00:00','2017-12-17 01:15:29','72',0),(2912,'公安部部署开展“春雷行动”打击侵犯知识产权犯罪','张楷欣','新华网','<p>　　<strong>公安部部署开展“春雷行动”打击侵犯知识产权犯罪</strong></p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：广东省公安厅内展示缴获信息。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 陈骥�F 摄\" src=\"upload/image/95ce6b50-e790-4d7a-ac83-cb5df1078ff4.jpg\" title=\"资料图：广东省公安厅内展示缴获信息。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 陈骥�F 摄\"> \n <input id=\"3265731a26b748ce91e15a72ef1c5abc\" name=\"3265731a26b748ce91e15a72ef1c5abc\" type=\"hidden\" value=\"http://www.chinanews.com/tp/hd2011/2017/09-21/772970.shtml\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：广东省公安厅内展示缴获信息。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 陈骥�F 摄 \n</div>\n<p>　　新华社北京12月16日电(记者刘奕湛)记者16日从公安部获悉，近日，公安部在打击经济犯罪“云端2017”专项行动中，部署开展为期2个月的打击侵犯知识产权犯罪“春雷行动”，紧盯日化用品、妇幼用品、服饰箱包、汽车配件、打印耗材、机电建材等重点领域，严查“元旦”“春节”两节节日市场，特别是针对各地屡打不绝的地域性造假“顽疾”开展严厉打击和集中整治。</p>\n<p>　　据介绍，当前，侵犯知识产权犯罪形势仍然不容乐观，制假售假活动仍较活跃，许多犯罪链条和网络尚未斩断，行业性、区域性、领域性制假售假现象依然突出。“春雷行动”将打击矛头对准各类侵犯知识产权犯罪，强力围剿直接坑害群众利益、危害生产生活安全、妨碍企业创新发展、侵害知识产权的突出犯罪，坚决以强有力打击破案攻势，让侵权者付出沉重代价，不断提升人民群众的获得感、幸福感、安全感，为决胜全面建成小康社会创造良好的市场环境、社会环境和法治环境。</p>\n<p>　　公安部有关负责人表示，全国公安机关将以强力推进“春雷行动”为抓手，深挖犯罪源头窝点，摧毁犯罪产业链条，严惩制假售假不法分子，防止此类犯罪蔓延发展。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/95ce6b50-e790-4d7a-ac83-cb5df1078ff4.jpg',NULL,708,'2017-12-16 22:22:00','2017-12-17 01:15:29','64',0),(2913,'证监会今年以来作出行政处罚决定206件','魏巍','中国证券报','<p>　　<strong>证监会今年以来作出行政处罚决定206件，罚没款金额73.24亿元</strong></p>\n<p>　　<strong>重拳整治乱象 严监管重塑市场生态</strong></p>\n<p>　　今年以来，证监会共开出200多张“罚单”，罚没款合计超过70亿元，金额远超去年全年。专家指出，证监会持续对各类违法违规行为保持高压态势，行政处罚重拳频出，对违法违规行为形成了强有力震慑，为资本市场健康稳定发展保驾护航。预计未来将进一步加大稽查执法工作的及时性和有效性，进一步拓展案件覆盖面，进一步创新稽查执法思路，优化案件查办组织模式，不断提升稽查执法效率，从而切实维护市场秩序，促进资本市场健康发展，增强投资者信心，提升监管公信力。</p>\n<p>　　依法全面从严监管</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　统计数据显示，证监会今年以来共作出行政处罚决定206件，罚没款金额73.24亿元，是去年全年的1.7倍；市场禁入36人，预计全年市场禁入人数将再创历史新高。</p>\n<p>　　“2016年以来，证监会大力坚持依法、全面、从严的监管理念，今年更是对各类违法违规行为加强处罚追责，通过严查操纵股市、深入整治市场乱象，积极稳妥处置重点风险，提升市场违规成本等方式来达到从严治市的目的。”新时代证券首席经济学家潘向东表示，全面深入推进稽查执法是“强监管”的落实与体现，有助于净化市场生态环境，确保资本市场健康发展， 使其更好地服务实体经济，提升服务效率和水平，并保护好投资者的合法权益。</p>\n<p>　　“证券监管成效显著，稽查执法居功至伟。”南开大学金融发展研究院负责人田利辉认为，证监会稽查执法工作是维护资本市场公平和公正的重要基石之一。“天下之事，不难于立法，而难于法之必行。”没有有效的稽查执法，就没有真正的基于法治精神的资本市场。令在必信，法在必行。只有坚持有效的稽查执法，金融监管才能落到实处。</p>\n<p>　　中国人民大学法学院教授刘俊海表示，今年以来证监会案件查处力度加大，这是践行依法、全面、从严监管理念的具体体现，这样才能解决资本市场存在的不规范、不公平、不平衡等矛盾。同时，通过加强监管，消除投资者的焦虑感、挫折感、失落感，提升投资者幸福感、获得感、安全感，建设投资者友好型资本市场。</p>\n<p>　　整治市场乱象 维护市场秩序</p>\n<p>　　业内人士表示，证监会敢于亮剑，对慧球科技“1001项议案”信息披露违法系列案、鲜言操纵“多伦股份”案、九好集团与鞍重股份“忽悠式重组”案、山东墨龙信息披露违法及实际控制人内幕交易案、唐汉博跨境操纵“小商品城”案等社会关注度高、市场影响大的案件依法高效作出行政处罚，迅速回应市场关切，有效地震慑违规、违法行为，整治市场乱象，净化市场生态，维护市场秩序，增强投资者信心，提升监管公信力。</p>\n<p>　　田利辉认为，顶格处罚、办大案要案、行刑联合进一步反映了证监会稽查部门严格执法的精神。</p>\n<p>　　潘向东认为，从今年以来证监会通报行政处罚案件的数量以及罚没金额来看，力度较往年有所加大，显示了监管与稽查执法的高压态势，这是践行依法、全面、从严监管的具体表现。未来监管部门将继续推进资本市场监管的法制化、常态化和公开化，提高市场主体违法违规及失信成本，维护市场“三公”原则。</p>\n<p>　　潘向东强调，从今年证监会稽查执法工作的特点来看，监管部门对各类违法、违规行为持零容忍态度，通过全链条的从严监管，进一步整治市场乱象，良好的A股市场生态正在逐步形成。</p>\n<p>　　刘俊海表示，查处的大案、要案以及典型案例增多，其特点是标的金额较大，涉案主体多为“资本大鳄”及演艺界明星，这向投资者传递了一个信号，即不论涉案主体的资本规模多寡，只要其出现违法违规及失信行为，资本市场的“法治牙齿”就会发力。</p>\n<p>　　监管能力不断提升</p>\n<p>　　证监会新闻发言人常德鹏日前在介绍证监会部署专项执法行动第四批案件情况时强调，下一步，证监会将进一步把主动防范化解系统性金融风险放在更加重要的位置，依法全面从严打击影响市场稳定运行、侵害投资者合法权益的各类违法违规行为，坚决维护国家金融安全，促进多层次资本市场健康发展。</p>\n<p>　　潘向东强调，未来应进一步增强稽查执法工作的及时性和有效性，加大对案件曝光的力度，进一步创新稽查执法思路，优化案件查办组织模式，不断提升稽查执法效率，从而切实维护市场秩序，促进资本市场健康发展，增强投资者信心，提升监管公信力。</p>\n<p>　　“奉法者强则国强，奉法者弱则国弱。”田利辉建议，证监会要继续严格执法、及时通报、加大宣传、震慑市场的不良参与者，同时通过技术手段逐步开展以互联网和大数据为基础的智能监管，逐步形成“不敢违、不能违和不想违”的、健康规范的市场环境。</p>\n<p>　　刘俊海认为，下一步，证监会的案件通报可以更精准、更聚焦，通过大数据、云计算等技术建立跨市场、跨地域、跨部门的，24小时全天候、360度全方位、信息实时更新，快捷高效的资本市场违法行为查处合作机制。</p>\n<p>　　此前，证监会在研究部署稽查执法工作时表示，近两年来，稽查执法工作取得了查办案件数量大幅增加、案件类型全面拓展、办案效率显著提升、办案质量稳步提高的明显成绩，但随着市场广度与深度的不断拓展，交易模式、交易技术的日益复杂，违法违规案件的发案数量将会越来越多，案件查办的难度将越来越高。</p>\n<p>　　证监会强调，有效应对这些新挑战，要坚决贯彻落实依法全面从严监管工作方针，要进一步拓展案件覆盖面，以专项执法行动为重点，以常规案件办理为基础，加大科技支撑力度，深化体制改革，强化统一指挥，注重执法协同，狠抓规范管理，坚决履行法律赋予的神圣职责，为更好地维护资本市场公开公平公正的市场秩序、更好地保护中小投资者的合法权益、更好地促进资本市场的健康稳定发展，提供坚强有力的执法保障。记者 徐昭</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,738,'2017-12-15 14:55:00','2017-12-17 01:15:29','67',0),(2914,'浪漫催泪爱情电影成都开机','田博川','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>成都12月16日电 (何浠 林冬梅)16日，院线电影《亲爱的，我要和别人结婚了》在成都举行开机发布会，导演覃杰携主创保剑锋、戚砚笛、徐�t�蔚妊菰绷料唷�</p>\n<p>　　据悉，《亲爱的，我要和别人结婚了》是一部根据真实故事改编的以汶川地震为背景的爱情电影。讲述了一个感人至深的爱情故事。</p>\n<div style=\"text-align:center\"> \n <img alt=\"主演保剑锋、戚砚笛、徐�t�巍⒉艿t诺亮相发布会现场。　钟欣 摄\" src=\"upload/image/2bf68001-3698-4933-af6b-3612bd24a4e9.jpg\" title=\"主演保剑锋、戚砚笛、徐�t�巍⒉艿t诺亮相发布会现场。　钟欣 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  主演保剑锋、戚砚笛、徐�t�巍⒉艿t诺亮相发布会现场。　钟欣 摄 \n</div>\n<p>　　2017年8月，一封致汶川地震丧生女友的“亲爱的，我要去跟别人结婚了”的信件传遍网络，打动了千万人。开机仪式上，保剑锋现场真诚朗读这封信，低声吟诉，化身为主人公“董先生”。“今日的告别，权当一种仪式。因为我深知，此生虽不再入蜀地，你却已经在我心里获得了永生。”一字一句，感动了在场的不少观众。随后，由歌手米粒演唱的《满天星》更是将现场感人的气氛推向高潮。</p>\n<p>　　在主创互动环节，保剑锋坦言没有人会拒绝这样的片子，“我们想通过这个片子给大家更多的爱，更多的希望。”同时他也谈到近期在成都，虽然一连几天都是阴天，但四川人民依然很开朗，有很红火的气氛。“没有因为每年的这些灾难而让自己意志消停，永远的是那么坚强、乐观。”</p>\n<p>　　据介绍，电影《亲爱的，我要和别人结婚了》预计将于2018年5月11日全国上映。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/2bf68001-3698-4933-af6b-3612bd24a4e9.jpg',NULL,324,'2017-12-16 14:56:00','2017-12-17 01:15:29','68',0),(2915,'侨商侨企入驻云南万侨创新创业产业园 冀望托起“回国创业梦”','李夏君','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>昆明12月15日电(缪超 李晓琳)15日，多家侨商侨企与云南省万侨创新创业产业园在昆明举行签约仪式，正式入驻万侨创新创业产业园。侨商侨企冀望产业园能够托起“回国创业梦”。</p>\n<p>　　万侨创新创业产业园是云南省侨办为承接海外华裔科技人才与归国留学生赴昆明创新创业而打造的服务平台。园区针对侨的特点，提供专门的行业资讯、产业培育、创业指导、后勤保障等多方位服务，为海外侨胞和归国留学生打造优质的创新创业环境。</p>\n<p>　　当天，澳大利亚云南总商会、中澳生物资源可持续研究与发展工程中心、云南北飞科技有限公司与万侨创新创业产业园签约，正式入驻该产业园。</p>\n<div style=\"text-align:center\"> \n <img alt=\"侨商侨企与云南省万侨创新创业产业园签约。　缪超 摄\" src=\"upload/image/c4c2b309-8660-4685-b284-e9cfdfbf136c.jpg\" title=\"侨商侨企与云南省万侨创新创业产业园签约。　缪超 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  侨商侨企与云南省万侨创新创业产业园签约。　缪超 摄 \n</div>\n<p>　　澳大利亚云南总商会副会长白云辉向记者介绍，她在澳大利亚从事地产与外贸行业，2016年已在云南红河州达成意向性投资，目前正计划在昆明投资兴业。“我虽祖籍云南，回来云南后对当地市场并不了解，希望万侨创新创业产业园能够帮助我们在云南投资兴业。”</p>\n<p>　　“今年我刚才从美国硅谷回来创业。”云南北飞科技有限公司总裁刘焱告诉记者，身为美国华侨的她目前正尝试将人工智能与大数据在教育领域做垂直运用。</p>\n<p>　　刘焱说，万侨创新创业产业园有着优秀的创新创业氛围，入驻后会在技术和政策上得到支持。“希望入驻这里能够在我创业期间得到切实扶持。”</p>\n<p>　　目前，万侨创新创业产业园落地在昆明国家级经济技术开发区银河科技园内。银河科技园总经理宋治乔表示，万侨创新创业产业园刚刚启动，今后将完善产业园组织架构、运营模式、管理理念，带领广大侨胞精英们在云南成功创业。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/c4c2b309-8660-4685-b284-e9cfdfbf136c.jpg',NULL,1340,'2017-12-15 21:35:00','2017-12-17 01:15:29','73',0),(2916,'北京小客车指标2018年调整为10万个 新能源指标6万','孙静波','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月15日电 北京市小客车指标调控管理办公室15日在其官网发布《关于2018年小客车指标总量和配置比例的通告》，通告称，2018年小客车指标年度配额为10万个，其中普通指标额度4万个，新能源指标额度6万个。</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：北京第二轮购车摇号。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 陆欣 摄\" src=\"upload/image/05b0e153-1e56-4011-97ab-7ded87673d52.jpg\" title=\"资料图：北京第二轮购车摇号。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 陆欣 摄\"> \n <input id=\"edb5286f369643b09669800008ccf53f\" name=\"edb5286f369643b09669800008ccf53f\" type=\"hidden\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：北京第二轮购车摇号。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>发 陆欣 摄 \n</div>\n<p>　　根据《北京市小客车数量调控暂行规定》(市政府令第227号)、《北京市人民政府关于修改〈北京市小客车数量调控暂行规定〉的决定》(市政府令第276号)及其《实施细则》的有关规定，经市交通行政主管部门会同市发展改革、公安交通、环境保护、科学技术等相关行政主管部门研究并报请市人民政府批准，现就2018年北京市小客车指标额度及配置比例等有关事项通告如下：</p>\n<p>　　一、2018年小客车指标年度配额为10万个，其中普通指标额度4万个，新能源指标额度6万个。</p>\n<p>　　二、2018年小客车指标配置比例如下：</p>\n<p>　　1.普通小客车指标。个人指标额度占年度指标配额的95%，共计38000个；单位指标额度占年度指标配额的4%，共计1600个；营运小客车指标额度占年度指标配额的1%，共计400个。</p>\n<p>　　2.新能源小客车指标。个人指标额度占年度指标配额的90%，共计54000个；单位指标额度占年度指标配额的5%，共计3000个；营运小客车指标额度占年度指标配额的5%，共计3000个。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/05b0e153-1e56-4011-97ab-7ded87673d52.jpg',NULL,538,'2017-12-15 15:10:00','2017-12-17 01:15:29','72',0),(2917,'分期乐商城上线礼品频道 “十大分期礼品”有哪些？','魏巍','中国网','<p><strong>　　乐信分期乐商城上线礼品频道 “十大分期礼品”都有谁？</strong></p>\n<p>　　“分期买礼物”正成为年轻人的时尚消费方式。圣诞新年将至，乐信旗下分期乐商城上线了礼品频道，帮助有“选择困难症”的朋友通过分期买到最合适的礼物。</p>\n<p align=\"center\"><img src=\"upload/image/198a4bf8-d75b-476a-8314-cefc2cd89430.png\"></p>\n<p>　　“很多年轻白领都有送礼的需求，无论是工作上还是生活上。开设礼品频道就是为解决这一刚需，帮助他们节约挑选礼物的时间，也让更多人感受到分期消费的魅力。”分期乐商城负责人表示，今年圣诞节期间，分期乐商城还专门将推出多款圣诞限量版礼品。</p>\n<p>　　分期乐商城礼品频道分4个专区，包括“送伴侣”、“送父母”、“送朋友”和“送同事”。每个专区里都有数十至上百种精选商品并且滚动更新，每款礼品都标注了热度，便于顾客选购。</p>\n<p>　　据介绍，目前分期乐商城礼品销量仍然维持稳定增长，根据热度排序，用户最爱分期购买的商品包括：陈李济红枣阿胶糕、雅萌滚轮瘦身美容仪、CHANEL邂逅香水礼品组合、BURBERRY中性驼色轻盈混纺围巾等等，基本都是有品牌知名度的高品质商品。</p>\n<p align=\"center\"><img src=\"upload/image/ab8221e7-2a95-4572-9acd-31a330c134c1.png\"></p>\n<p>　　以上述BURBERRY羊绒围巾为例，售价2599元，月供最低只需要137.42元。数据显示，这款商品的关注度排在“送伴侣”礼品前10名，购买者以22-25岁的未婚白领为主。</p>\n<p>　　事实上，早在今年七夕时，就有很多用户通过分期买礼物。数据显示，七夕节前一周，分期乐平台日百商品订单量比平日大增六成左右，珠宝首饰类销量更是平时的三倍。</p>\n<p>　　“很多年轻人都把分期作为一种新潮的消费方式，礼品分期只是其一。”分期乐商城负责人表示，分期工具帮助年轻人更加从容、自信地追求品质生活，受到越来越多用户的欢迎。如今，信用消费已经成为各大电商的标配。</p>\n<p>　　刚刚过去的双11大促期间，以分期乐商城为代表的分期电商表现十分活跃。京东白条支付交易额同比增长450%；苏宁“任性付”的免息券疯抢数量，也同比增长超过600%；11日凌晨第一小时里，分期乐商城下单量和下单金额分别比去年同期增长3倍和6倍。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/ab8221e7-2a95-4572-9acd-31a330c134c1.png',NULL,1248,'2017-12-15 15:07:00','2017-12-17 01:15:29','67',0),(2918,'深圳民间团体来台关怀抗战老兵','张楷欣','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>台北12月16日电 (记者 刘舒凌 杨程晨)深圳龙越慈善基金会访台团队16日下午来到台北市中山堂举办了一场“给台湾老兵送一份家乡的礼包”活动。</p>\n<div style=\"text-align:center\"> \n <img alt=\"12月16日，深圳龙越慈善基金会、台湾全球华人纪念抗日协会在台北中山堂举办“给台湾老兵送一份家乡的礼包”活动。 杨程晨 摄\" src=\"upload/image/b6e232e8-c856-4985-bb37-0be4bb0d69d1.jpg\" style=\"border:px solid #000000\" title=\"12月16日，深圳龙越慈善基金会、台湾全球华人纪念抗日协会在台北中山堂举办“给台湾老兵送一份家乡的礼包”活动。 杨程晨 摄\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  12月16日，深圳龙越慈善基金会、台湾全球华人纪念抗日协会在台北中山堂举办“给台湾老兵送一份家乡的礼包”活动。 杨程晨 摄 \n</div>\n<p>　　1987年，台湾当局在众多大陆籍老兵发出“我要回家”呐喊的压力下，开放大陆探亲。30年后，深圳龙越慈善基金会专程来台关怀曾参与抗战的老兵们，致赠他们各自家乡的风味礼包，表达敬意，也慰藉其思乡之情。</p>\n<p>　　2011年成立以来，深圳龙越慈善基金会一直致力于帮助抗战老兵。据了解，这场由该基金会发起的活动，15日起将先后前往桃园、台北、台南、高雄等地慰问老兵。</p>\n<p>　　这些由大陆各地志愿者准备的礼包，除了家乡的特产，还有一封故乡志愿者的来信。</p>\n<p>　　16日，龙越慈善基金会副秘书长罗杨为就为现场来自苏北的老兵念了江苏志愿者所写的信：“这是您离开家乡的第68个冬天，中山陵的梧桐黄了，明故宫的银杏叶也铺满了六百年前的老城墙，68年的一个转身，这里的春天就再也没有等到过您的脚步。”各地写信的年轻人长期为抗战老兵提供志愿服务，其朴实动情的话让许多人红了眼眶。</p>\n<p>　　罗杨为表示，团队将竭尽所能为台湾老兵服务。前一天他们在数小时内，就帮助一位来自吉林、从未回乡的89岁老兵找到亲人；网络时代的便利，让归根故里的愿望更便于实现。</p>\n<p>　　当天到场的数十位老兵家乡分布在上海、山东、四川、安徽、苏州、河南、湖南、福建等大陆各省市，曾在各大战场参与大小战役。其中，今年99岁的陶士君受邀致辞时回顾他前后8年参与大小战斗的心得，强调这是一场千万人血肉换来的胜利，理应得到隆重的纪念。</p>\n<p>　　全球华人纪念抗日协会是主要由抗战老兵组成的台湾民间团体，协助举办此次关怀活动。台湾老兵后代、协会秘书长钟蕾妮说，她曾跟随龙越的志愿者团队到大陆偏乡访问抗战老兵，佩服他们所作出的努力，也特别希望台湾年轻人能够认识抗战历史、珍惜老兵的贡献。</p>\n<p>　　台湾当局前领导人马英九当天透过电话连线向到场老兵表达了慰问。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/b6e232e8-c856-4985-bb37-0be4bb0d69d1.jpg',NULL,977,'2017-12-16 23:25:00','2017-12-17 01:15:29','70',0),(2919,'中国城市职工养老储备指数升至60.8 充足度明显增强','王诗尧','中国新闻网','<div style=\"text-align:center\"> \n <img alt=\"2017年中国大中城市职工养老储备指数12月16日在北京发布。 程春雨 摄\" src=\"upload/image/ea3c8f5b-55b2-438c-93db-1df10ac84ba4.jpg\" style=\"border:px solid #000000\" title=\"2017年中国大中城市职工养老储备指数12月16日在北京发布。 程春雨 摄\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  2017年中国大中城市职工养老储备指数12月16日在北京发布。 程春雨 摄 \n</div>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>北京12月16日电 (记者 程春雨)今日，在北京发布的一份报告显示，2017年中国大中城市职工养老储备指数(PRICE)为60.8，相比2015年提高1.1，基本符合中国目前经济社会发展现状，未来进一步提升空间仍然巨大。</p>\n<p>　　2017年中国大中城市职工养老储备指数充足度和稳定度指数值为61.2和63.2，相对2015年的59.2和62.6明显增强；职工基本养老保险充足度指数值为90.6，比2015年的数值高出9.3，源于国家对民生事业的高度重视和巨大投入。</p>\n<p>　　经济转型也正在重塑各行业职工养老储备水平。分行业类型看，知识性和技术性较强的服务型行业储备指数提升较快，相对排名跃升较大，而一些支持过去中国高速增长的行业，如房地产行业、建筑业和制造业指数增长偏慢，导致相对排名下降明显。</p>\n<p>　　报告称，指数的提升符合中国社会发展现状，但随着人口老龄化的加速发展和人们生活水平的日益提高，进一步提升中国职工养老储备水平成时代的迫切要求和必然趋势。</p>\n<p>　　大中城市职工养老储备指数研究项目会由中国保险行业协会联合中国劳动和社会保障科学研究院、中国社会科院世界社保研究中心、中国经济信息社主办，平安养老险承办。自2013年首次发布以来，已持续开展三届。指数选取了我国36个大中城市开展调查而进行量化评价。</p>\n<div style=\"text-align:center\"> \n <img alt=\"2017年中国大中城市职工养老储备指数发布会现场。程春雨 摄\" src=\"upload/image/bb8fa6b8-f746-461f-bfef-592c4be3dff2.jpg\" style=\"border:px solid #000000\" title=\"2017年中国大中城市职工养老储备指数发布会现场。程春雨 摄\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  2017年中国大中城市职工养老储备指数发布会现场。程春雨 摄 \n</div>\n<p>　　2017年，该指数项目还对我国职工养老保障领域中税延养老保险、企业年金、个人商业养老保险、住房反向抵押保险等社会民生热点问题展开调研。结果显示，超过53.3%的职工会选择\"保底收益+分红，分红不确定，风险适中\"的产品；其次是\"投资收益固定型，无风险\"的产品，占比42.4%，说明收益保底、风险适中的商业养老保险产品将受到大众青睐。</p>\n<p>　　调查发现，高学历、高财富的中年人购买个人商业养老保险的比例较高，期望养老金替代率和预期社保替代率处于中等水平的人也是购买商业养老保险的主要人群。</p>\n<p>　　针对调研中发现的养老储备发展中行业之间、不同年龄群体之间、养老保障第二支柱发展不充分、不平衡等问题。报告建议，通过制度完善推动养老事业多元化、多样化发展，强化个人自我保障意识，让职工尽早参加基本养老保险，主动购买个人商业保险计划等，继续稳步提升企业职工养老储备水平；还建议在设计并推出延税型或补贴型商业养老保险时，兼顾考虑不同职工在购买能力和保障力度的差异问题。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/bb8fa6b8-f746-461f-bfef-592c4be3dff2.jpg',NULL,855,'2017-12-16 18:34:00','2017-12-17 01:15:29','66',0),(2920,'百度金融两周年 朱光致敬离开的老员工：永远是朋友','陈海峰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月15日电 12月14日，百度金融(FSG)迎来了成立两周年的日子，并在百度大厦举行“信仰照耀征程”两周年庆典，新老员工齐聚一堂，共同分享生日的喜悦。百度金融成立两年来，每一个FSGer都在不断进取、验证假设、获取真知。用科技，让普惠金融真正落地开花，让世界发生更多美好的改变。</p>\n<div style=\"text-align:center\"> \n <img alt=\"百度高级副总裁朱光致辞\" src=\"upload/image/9b55ec39-8f0e-403c-aa26-a1c9280f3688.jpg\" style=\"border:px solid #000000\" title=\"百度高级副总裁朱光致辞\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  百度高级副总裁朱光致辞 \n</div>\n<p>　　作为百度金融掌舵人，百度高级副总裁朱光出席庆典，感谢了FSG全体员工对百度金融所做出的重要贡献。百度人力资源副总裁刘辉在致辞中对FSGers两年的努力给予充分肯定：“两年的时间，充分见证了百度金融的软实力、文化和核心价值。在朱光的带领下，百度金融体现了百度越变越美和追求极致的迭代文化。百度金融必然会越来越好，Moving Foward！”</p>\n<p>　　现场，朱光还特别感谢离开FSG的老员工们对百度金融所付出的努力和汗水。</p>\n<p>　　庆典当日，曾任FSG副总经理的王劲送给朱光的一个智能音箱和一封信：“朱光：热烈祝贺FSG成立两周年！作为FSG的掌门人，一路辛苦了。祝FSG同学们共同奋斗，再创辉煌。祝你身体健康，事业蒸蒸日上。――王劲”</p>\n<p>　　收到王劲礼物和祝福的朱光十分感动，回忆起FSG成立之初的日子，并深情感言：“2015年12月14日，王劲和我，以及百度Fellow孙云丰，共同开创了百度金融的事业。今年年初，因为个人创业的原因，王劲离开百度金融，目前他自己的事业也发展得非常好。其实不止王劲，过去两年，有非常多的人为FSG工作，和我们一起努力。有一些人因为个人原因离开了FSG，有些还在百度大家庭，有些在新事业舞台上挥洒和表现。我们应该把最热烈的掌声送给哪怕只为FSG做过一天贡献的人，因为没有他们，就不会有FSG今天的成就。我们永远都是朋友，让我们感谢他们！”</p>\n<div style=\"text-align:center\"> \n <img alt=\"曾任职百度金融的三位老员工表达了对百度金融的祝福。\" src=\"upload/image/792ca213-6c77-41bd-9b18-29f551fb66c3.jpg\" style=\"border:px solid #000000\" title=\"曾任职百度金融的三位老员工表达了对百度金融的祝福。\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  曾任职百度金融的三位老员工表达了对百度金融的祝福。 \n</div>\n<p>　　百信银行副行长王晓炜表示，“作为百度的老员工，我非常有幸地见证了FSG的成长。很荣幸我们百信银行成为FSG金融科技能力的一块试验田，希望百信银行能够把FSG的金融科技能力输出，把工作做得更好，做得更大。”</p>\n<p>　　为了感谢王晓炜对FSG的贡献，现场朱光给王晓炜送了一支签字笔，希望百信银行以后能书写更多辉煌。未来，百度金融将持续为百信银行输出科技力量，共同践行普惠梦想。</p>\n<p>　　“我是2016年4月加入FSG，这段日子回想起来比较辛苦，但是也很甜蜜，因为出来的成绩很好，用户也很认可，在FSG不仅学到了很多知识， 还认识了一群非常有趣的人。他们是各行各业的精英，又有趣又聪明。在百度金融最开心的就是，朱光总为了慰劳大家，经常请同事们吃各种好吃的。” 百度知识体系总监王京津说道。现场她也收到了朱光所送的文房四宝。文房四宝寓意着知识的力量，知识能带给人改变。就如同百度金融的教育信贷业务，帮助很多人学到知识、改变命运，让金融更有温度。</p>\n<p>　　现场播放了老员工给百度金融的祝福视频。百度副总裁沈抖在视频里面回忆了百度金融从零走到今天所历经的那些岁月，怀念起与朱光一起并肩战斗的日子。百度搜索发展部周红忠也在祝福视频中表示，曾经在FSG经历的那些日子很开心，也很激动看到FSG现在取得这么好的成绩。</p>\n<p>　　庆典上，FSG员工回忆过去、展望未来，气氛温暖热烈。朱光表示：“帮助无助的人改变自己的命运，这就是FSG的信仰。很多年以后，FSG同事们在一起，也会为我们共同的信仰感到无比的骄傲，因为中国需要我们，世界需要我们，金融界也非常非常的需要我们！相信FSG会在大家的支持下越走越远，普惠金融的梦想一定会变得越来越近。”</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/792ca213-6c77-41bd-9b18-29f551fb66c3.jpg',NULL,576,'2017-12-15 17:16:00','2017-12-17 01:15:29','67',0),(2921,'百度出席北京汽车产业创新大集 做汽车智能化“摆渡人”','陈海峰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月15日电 智能化与互联化成为汽车产业重构的重要方向，中国汽车品牌如何在技术变革浪潮和产业风口中实现创新升级成为行业热议话题。12月15日，首届北京汽车产业创新大集在北汽研发基地召开，本次会议由北汽集团与中国电动汽车百人会EV100主办，围绕汽车产业创新话题展开。百度Apollo开放平台作为北汽集团的战略合作伙伴出席活动并发表了演讲。</p>\n<div style=\"text-align:center\"> \n <img alt=\"顾维灏讲解百度Apollo开放平台核心理念\" src=\"upload/image/2f385748-d2ea-45c7-ba7e-af0c9fa6583a.jpg\" style=\"border:px solid #000000\" title=\"顾维灏讲解百度Apollo开放平台核心理念\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  顾维灏讲解百度Apollo开放平台核心理念 \n</div>\n<p>　　百度与北汽是“人工智能＋汽车”跨界融合的“模范CP”。10月13日，百度与北汽集团正式签署战略合作协议，宣布在自动驾驶、车联网、云服务等领域达成集团层面战略合作，一同探索中国汽车产业智能化升级之路。在此次活动上，百度以核心嘉宾亮相，展示了Apollo开放平台的核心理念和自动驾驶产品Apollo Pilot。百度智能汽车事业部总经理顾维灏在演讲中表示， Apollo开放平台作为全球首个开放的自动驾驶平台，围绕“开放能力、共享资源、加速创新、持续共赢”的核心理念，正构建起强大的 “win-win”汽车生态系统，推动自动驾驶商业化进程。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图片引用10月13日百度与北汽集团战略合作发布会\" src=\"upload/image/49912f05-e633-410e-90c8-fd0b35f83fea.jpg\" style=\"border:px solid #000000\" title=\"图片引用10月13日百度与北汽集团战略合作发布会\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  图片引用10月13日百度与北汽集团战略合作发布会 \n</div>\n<p>　　Apollo Pilot就是百度为推进自动驾驶商业化而推出的量产产品。11月16日，百度Apollo开放平台发布了自动驾驶产品Apollo Pilot，标志着 Apollo平台从技术创新走向产品创新。据顾维灏介绍，Apollo Pilot是目前最适应中国市场的自动驾驶产品。它拥有最大的中国交通场景库，仅在高速及环路多车道自动驾驶功能下，就包含了近百种细分场景。在海量场景库的支持下，Apollo Pilot能够熟练应对中国各种交通情况，满足驾驶员在城市道路、高速、泊车等各方面的需求，全面超越市面上现有的安全和驾驶辅助产品。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/ea5979ff-0292-46aa-a797-b7b067d8cfc2.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　此外，Apollo Pilot还是车主忠诚尽职的“安全守护者”。在安全性方面，Apollo Pilot搭建了全局多维安全框架，能够优化处理重点安全威胁。此外，它还以真实交通事故数据作为安全检验标准，与中国交通事故深入研究工作组(CIDAS)合作，基于中国真实交通事故的数据，对产品选型、功能设计、驾驶策略、性能指标等多方面进行了慎重考量，完全满足中国及欧洲的汽车安全标准。同时，Apollo Pilot还是一款聪明的自动驾驶产品，具备不断学习、持续优化、自动升级的能力，未来可通过OTA不断升级迭代。</p>\n<p>　　北汽集团董事长徐和谊在活动开场致辞中表示，新时代中国汽车产业格局面临重塑，亟待建立产业生态圈。围绕新能源及智能网联汽车发展展开战略合作，把汽车产业创新资源与产业资本的汇聚交流提升到新的战略高度迫在眉睫。他的观点与百度不谋而合。Apollo开放平台意欲通过人工智能对汽车产业的垂直整合，形成AI为核心聚力的汽车智能生态共同体，从技术到思维上，颠覆汽车行业的运作模式，在为OEM赋能的同时，重新定义其角色和意义。目前，Apollo开放平台已获得了超70家OEM、Tier1、地图厂商、芯片厂商、自动驾驶系统厂商等企业的加入，是目前全球涵盖产业最为丰富、最为全面的自动驾驶生态。</p>\n<p>　　李彦宏就曾表示，Apollo开放平台将通过“AI＋开放”呈现盎然生机。作为中国原创自动驾驶平台，百度Apollo代表了中国自动驾驶技术的方向和未来，百度Apollo生态的形成，将推进我国汽车产业驶入快车道，在技术、理念等多维度上，使中国自动驾驶成为世界的标杆。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/ea5979ff-0292-46aa-a797-b7b067d8cfc2.jpg',NULL,481,'2017-12-15 15:28:00','2017-12-17 01:15:29','72',0),(2922,'国足东亚杯练兵效果如何？冲2022世界杯要“相信过程”','张楷欣','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>北京12月17日电(记者 王牧青) 场场进球、一场未胜，中国男足以平韩国、平朝鲜、负日本的战绩完成了2017年东亚杯任务，最终排名第三，中规中矩。以“练兵为主“出发，里皮率领的这支年轻国足不乏亮点，强调希望的同时，也应更冷静地意识到，面向新一轮世界杯周期，这些希望还远远不够。</p>\n<p>　　此去日本，里皮的大名单带上了6名U23球员，首战韩国队，6人全部首发出场，“银狐”考察新人的目的已再明确不过。相比今年3月在12强赛1-0力克韩国队，除了于大宝，里皮更换了10名首发。当然，韩国队也更换了9人。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为韦世豪庆祝进球。 图片来源：视觉中国\" src=\"upload/image/4e8e4c21-702d-4d38-9f0d-23b78d553463.jpg\" style=\"border:px solid #000000\" title=\"图为韦世豪庆祝进球。 图片来源：视觉中国\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  图为韦世豪庆祝进球。 图片来源：视觉中国 \n</div>\n<p>　　其实，“东亚杯=练兵杯”早已是日本、韩国两队多年奉行的原则。近几届赛事，日韩出征东亚杯的大名单都排除了在欧洲联赛效力的球员，这项赛事更像是在检验日韩本土国脚的测验场。</p>\n<p>　　以刚刚结束的2017年东亚杯为例，已晋级明年世界杯决赛圈的日韩两队，希望进一步挖掘和测试明年有实力征战世界杯的国脚――效力欧洲联赛的国脚已不需要检验。</p>\n<p>　　但无缘明年世界杯的中国男足情况不同，这次选择边缘国脚+U23球员，是因为年龄结构偏大的国足急需更新换代，为冲击2022年世界杯提前做准备。刚刚结束的12强赛，中国队还需要郑智、冯潇霆、蒿俊闵这批30-40岁的老将扛起大梁，但要想冲击5年后的2022世界杯，中国队的中生一代需要接过大旗。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为韦世豪(右)与韩国球员崔�创�(左)跳起争球。 \" src=\"upload/image/506ad4b2-f652-4272-9ef3-b4c6598f8cbf.jpg\" style=\"border:px solid #000000\" title=\"图为韦世豪(右)与韩国球员崔�创�(左)跳起争球。 \"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  图为韦世豪(右)与韩国球员崔�创�(左)跳起争球。 \n</div>\n<p>　　但中生一代的不济已是事实，中国足协力排众议在联赛施行U23新政，也是希望更年轻的一代球员尽快成熟起来，通过2019年亚洲杯的历练，在里皮的调教下避免重蹈12强赛出局的覆辙。</p>\n<p>　　三场比赛后，“老大哥”赵旭日总结：“结果不理想，但过程更重要。年轻球员通过这种比赛累计经验，得到了锻炼。”</p>\n<p>　　东亚杯上，国足的年轻球员亮点不少，韦世豪打进2粒进球，杨立瑜送出1次助攻，刘奕鸣打满3场270分钟，虽不属于U23球员，24岁的赵宇豪、贺惯的状态也是越来越好。他们虽偶尔仍暴露出稚嫩和青涩，却已超过了赛前大部分人的预期。</p>\n<p>　　但是，22岁的韦世豪、刘奕鸣，甚至20岁的杨立瑜，早就算不上“超新星”了。比比国家队的老大哥们，冯潇霆18岁就在中国顶级联赛进球，20岁踢了世青赛；郑智22岁时已被拜仁、柏林赫塔等队看中；黄博文16岁就踢上了中超，17岁生日前2个月完成了首粒中超进球。</p>\n<div style=\"text-align:center\"> \n <img alt=\"中国队球员邓涵文(右)与韩国球员金珍洙(左)展开激烈拼抢。\" src=\"upload/image/b7e9431d-d195-4f5e-b5aa-56e1a061adac.jpg\" style=\"border:px solid #000000\" title=\"中国队球员邓涵文(右)与韩国球员金珍洙(左)展开激烈拼抢。\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  中国队球员邓涵文(右)与韩国球员金珍洙(左)展开激烈拼抢。 \n</div>\n<p>　　所以说，U23球员在东亚杯的闪光表现，只是他们这代球员“迟到的惊喜”，也是足协U23政策催化出的结果。况且，面对派出“二队”的日韩对手，国足并没有任何优势。</p>\n<p>　　有人说，国足派出了“准三队”，不也和“日韩二队”有得一打？但这句话经不起推敲，因为4-5年后，这批球员将是国足的中流砥柱，但日韩的中流砥柱现在在哪：22岁的浅野拓磨、25岁的宇佐美贵史和武藤嘉纪；21岁的黄喜灿、25岁的孙兴愍。这些球员已在欧洲顶级联赛站稳脚跟，不会来踢东亚杯了。</p>\n<p>　　再举个例子，97-98年龄段的日本球员，至少有5人正效力于欧洲五大联赛，效力德乙等第二级联赛的更多。而中国的年轻球员呢？还需要靠足协的强制政策，才能得到联赛出场的机会，有的队里，U23球员总在上半场20-30分钟被换下，场面极其尴尬。</p>\n<p>　　说到底，中国年轻球员在东亚杯闪光，当然是国足未来的希望。但若想击败亚洲诸强，像日韩足球那样连续多届稳进世界杯，还需要更多的年轻球员脱颖而出，不仅踢上中超、进入国家队，更要在更广的舞台上获得历练的机会。</p>\n<p>　　如果说，韦世豪们的国家队首秀，是中国足球冲击2022年世界杯过程的开始，那么套用一句体育圈最近流行的话：“要相信过程。”言外之意，东亚杯的成绩并不重要，锻炼出新人才重要。涌现一两个新人还不够，持续涌现新人才合格。想圆世界杯梦，中国足球需要更多“相信过程”。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/b7e9431d-d195-4f5e-b5aa-56e1a061adac.jpg',NULL,107,'2017-12-17 01:15:00','2017-12-17 01:15:29','74',0),(2923,'2018年公务员省考陆续启动 北京上海今日笔试','张楷欣','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>客户端北京12月17日电(记者 阚枫)今日，北京、上海两地将举行2018年度公务员招录笔试，两地计划招录的总人数超8000人。</p>\n<p>　　2018年度国考前不久结束笔试。在省考层面，目前，北京、上海、浙江三省市均已经启动了2018年度公务员招录工作，浙江的笔试将在明年1月6日和7日举行。</p>\n<div style=\"text-align:center\"> \n <img alt=\"12月10日，山西太原一公务员考点，考生准备进入考场。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 武俊杰 摄\" src=\"upload/image/7e686be8-6157-44e5-ad06-63de89051f94.jpg\" style=\"border:px solid #000000\" title=\"12月10日，山西太原一公务员考点，考生准备进入考场。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 武俊杰 摄\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  12月10日，山西太原一公务员考点，考生准备进入考场。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 武俊杰 摄 \n</div>\n<p>　　<strong>北京：2018“京考”拟招录4272人</strong></p>\n<p>　　相对于被称为“国考”的中央机关及其直属机构公务员招录，以各省份为单位组织的公务员招考也被成为“省考”，这其中，北京市的公务员招录又被很多考生称为“京考”。</p>\n<p>　　根据北京市此前发布的《北京市各级机关2018年度考试录用公务员公告》，2018年“京考”公共科目的笔试时间为12月17日。</p>\n<p>　　据《北京日报》援引相关机构的统计数据，2018年北京市各级机关共招录公务员职位2095个，计划招录4272人，相比2017年职位数减少41个，招录人数减少750人。</p>\n<p>　　按照招录政策，本次“京考”，市级机关除部分特殊职位和专业性较强的职位外，主要招录具有2年以上基层工作经历人员。艰苦边远地区的街道、乡镇等基层职位适当降低进入门槛。同时，新录用到这些职位的人员应当签订最低服务5年的协议。按照北京市有关规定，新录用乡镇公务员最低服务年限为5年，服务期内一般不得调出乡镇。</p>\n<p>　　根据《北京日报》12月2日的报道，在本次“京考”报名截止前一日，本次“京考”的报名人数就超过4.4万人，最热岗位的竞争接近“千里挑一”。</p>\n<div style=\"text-align:center\"> \n <img alt=\"12月10日，山西太原一公务员考点，考生准备进入考场。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 武俊杰 摄\" src=\"upload/image/c6f8bb94-dd3c-45f8-8e9e-0ef87e86186c.jpg\" style=\"border:px solid #000000\" title=\"12月10日，山西太原一公务员考点，考生准备进入考场。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 武俊杰 摄\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  12月10日，山西太原一公务员考点，考生准备进入考场。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 武俊杰 摄 \n</div>\n<p>　　<strong>上海：计划招录3830人，约5.2万人报名</strong></p>\n<p>　　上海市2018年度公务员招录在今年11月3日至9日进行了报名，笔试时间也为12月17日。</p>\n<p>　　就招录人数来看，上海市的2018年公务员招录中，全市各级机关和参照公务员法管理的机关(单位)共推出招考职位1775个，计划录用公务员和参照公务员法管理的机关(单位)工作人员3830名。</p>\n<p>　　据上海市公务员局网站的消息，本次招录共有约5.2万人报名参加，与2017年的4.2万人，2016年的3.7万人相比，报考人数继续回升。报名考生中，具有本科学历的约占四分之三，硕士研究生及以上学历的约占四分之一，女性考生接近6成。</p>\n<p>　　从考试测查方式来看，2018年上海市公务员招录职位继续分为综合管理、政法、经济管理、财务管理、信息管理、城市建设管理和农村建设管理等七大类方向，其中除综合管理类职位外，其他类别职位方向在公共科目笔试的基础上，还需加试相应专业考试科目。</p>\n<p>　　根据招录政策，上海市2018年度的公务员招录也继续推出部分基层职位定向招录大学生村官、“三支一扶”等服务基层项目人员。此外，据介绍，上海也在完善公务员招录残疾人常态化机制，根据残疾人特点，在市、区党政机关继续推出部分公务员职位，招录具有正常履职身体条件的残疾人。</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 韦亮 摄\" src=\"upload/image/3395d318-304e-4461-930b-27edacb3e74e.jpg\" style=\"border:px solid #000000\" title=\"资料图。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 韦亮 摄\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  资料图。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>发 韦亮 摄 \n</div>\n<p>　　<strong>2018年公务员“省考”招录陆续启动</strong></p>\n<p>　　今年11月，伴随2018年“国考”招录的启动，部分省份2018年度公务员“省考”招录也陆续开启。</p>\n<p>　　除了北京和上海，2018年浙江省各级机关单位考试录用公务员工作此前也已进行了报名。浙江省2018年计划考录公务员(含参照公务员法管理的工作人员)7328名，比2017年增加了862名，笔试时间定为2018年1月6日和7日。</p>\n<p>　　公务员“省考”时间分布有何特点？华图教育公考辅导专家李曼卿此前对媒体分析，按照近几年的惯例，与北京、上海等省份早早启动公务员招录不同，大部分省份将“省考”集中在每年的3月至5月份进行笔试。例如，今年4月22日，全国有24个省份同日举行2017年度“省考”笔试，这也被考生称为公务员招录“联考”。</p>\n<p>　　值得一提的是，也有省份将公务员“省考”安排在下半年举行。例如，今天，河南省2017年统一考试录用公务员的笔试也将举行，全省共计划招录公务员(含参照公务员法管理单位工作人员)5418人。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/3395d318-304e-4461-930b-27edacb3e74e.jpg',NULL,1295,'2017-12-17 00:04:00','2017-12-17 01:15:30','64',0),(2924,'走进达喀尔冠军新世界 五十铃劲蓝动力耀然而来','吉翔','中国新闻网','<div style=\"text-align:center\"> \n <img src=\"upload/image/204b8116-843e-4035-a073-de122d425bba.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月15日电 12月14日江西五十铃达喀尔中国赛庆功颁奖晚会在北京举行。晚会现场，中国汽车摩托车运动联合会秘书长何建东、中国车王卢宁军，更有越野圈内知名赛车手们欢聚一堂。</p>\n<p>　　2017年这一年以来，江西五十铃始终活跃在各大越野赛场中，并凭借着坚实可靠的实力在环塔拉力赛、428青藏高原拉力赛、大越野等赛事中斩获诸多荣誉。更在赛事环境更为严苛的达喀尔中国赛中，江西五十铃以“准素车”的身份参赛，一举斩获厂商杯和T2量产组的双料冠军，同时包揽量产组个人前两名。</p>\n<p>　　据悉，江西五十铃今年参加的所有赛事，都是由民间车手“出战”，为更多民间越野爱好者提供了广阔的舞台，持续推广赛事平民化，致力于让中外车手都能在中国体验到世界级越野拉力赛事的魅力。</p>\n<p>　　在此次冠军荣耀之夜上，江西五十铃与越野爱好者们共同回顾了在2017达喀尔中国赛上的一路挑战艰难的征程。同时，车手们永不言败、奋勇直前的越野精神，也将一直传承下去，为更多消费者带来越野激情。</p>\n<p>　　此外本次发布会现场，江西五十铃为回馈广大热爱越野的消费者，发布了以发动机为首的核心技术――BluePower劲蓝动力。劲蓝动力凝聚五十铃百年匠心精神，重塑“劲”、“静”、“净”、“精”柴油新势力，点燃越野激情，为追求极致、向往自由的消费者，释放悦野梦想，迸发更多激情与喜悦。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/509e405d-9434-4eff-8888-945c85c4f831.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　劲蓝动力不仅搭载了五十铃全球领先的柴油发动机技术，具有低转速高扭矩的特征，还特别调整了发动机的内部结构，采用NVH工程超级静音减震系统。采用了集百年造车匠心的Ddi超压共轨技术，VGSTurbo可变截面涡轮增压及高耐久性石墨涂层活塞，使燃烧更加充分减少燃油消耗。坚持水性漆车身涂装工艺，满足了国家国V排放标准，使得江西五十铃汽车车型更具市场竞争力。</p>\n<p>　　江西五十铃将劲蓝动力运用到全新2018款mu-X上，全面升级而来的mu-X也迎来它的中文命名――牧游侠。牧游侠，这个中文名字饱含了探索、无畏、勇气的力量，最大化地发挥劲蓝动力的优势，让每一位驾乘者都能牧游天。之间，探索更丰富的越野生活。</p>\n<p>　　坚实可靠的出色性能以及锋芒毕露的霸气外观，让全新牧游侠引领驾乘者轻松开启舒心的驾驶生活，在保持原有卓越动力性和越野性能的3.0T</p>\n<p>　　冠军基础上，新增1.9T动力单元，带给消费者更丰富的用车体验。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/509e405d-9434-4eff-8888-945c85c4f831.jpg',NULL,300,'2017-12-15 14:08:00','2017-12-17 01:15:30','72',0),(2925,'深圳坂田永胜堂队中国(深圳)首届“麒麟王”争霸赛称王','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>深圳12月16日电(郑小红 廖建州)展麒麟风采，传非遗文化。16日下午，2017中国(深圳)首届“麒麟王”争霸赛在深圳科学高中体育馆举行。来自全国各地以及马拉西亚的共24支队伍各展武艺，最终，深圳坂田永胜堂麒麟队获得了首届“麒麟王”桂冠；马来西亚沙巴州斗湖客家工会麒麟团、马来西亚沙巴州拿笃客家工会青年团麒麟队获得优秀表演奖金奖。</p>\n<div style=\"text-align:center\"> \n <img alt=\"深圳坂田永胜堂麒麟队在比赛中 钟欣 摄\" src=\"upload/image/a154a3fa-fae0-4144-adc2-10b27d2c1a8b.jpg\" title=\"深圳坂田永胜堂麒麟队在比赛中 钟欣 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  深圳坂田永胜堂麒麟队在比赛中 钟欣 摄 \n</div>\n<p>　　当天上午一早，遵照舞麒麟的传统习俗，参赛的麒麟队伍前往深圳龙岗区坂田永胜堂麒麟馆进行了会师仪式，对东道主表示敬重的同时祈求祝福。比赛开始前，24支麒麟队还在体育场上演了一场“麒麟互拜”仪式，所有参赛队伍一齐上场，相互切磋、交流，共同为传承和保护麒麟舞这一优秀民间艺术瑰宝，实现非遗传承发展与经济社会效益的双赢献计献艺，场面甚是壮观。</p>\n<p>　　现场还有一支特殊的队伍，他们是来自坂田爱兴兰・蓝星书画中心的小画家们，他们现场创作，描绘各路麒麟风采。据悉，这些小画家当中有四位同学的作品刚刚获得FAC国际少儿绘画大赛百名优胜奖，入选“欢乐春节・艺术中国汇”纽约展出活动。</p>\n<p>　　本次比赛期间，深圳坂田街道还准备了腰鼓、黄梅戏、秧歌、纸马舞、小麒麟表演等5个具有特色的非遗展演项目，为麒麟队及观众带来了一场中国优秀传统文化和非遗文化盛宴，展示了龙岗区挖掘非物质文化遗产，将淹没在民间，甚至濒临失传的非物质文化遗产“活化”起来。</p>\n<div style=\"text-align:center\"> \n <img alt=\"小画家们现场创作，描绘各路麒麟风采 钟欣 摄\" src=\"upload/image/ea5578b7-28a0-4493-b44a-927cc0f496d1.jpg\" title=\"小画家们现场创作，描绘各路麒麟风采 钟欣 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  小画家们现场创作，描绘各路麒麟风采 钟欣 摄 \n</div>\n<p>　　比赛现场，一只只麒麟在现场锣鼓的伴奏下，时而双眼转动，时而四处张望，时而匍匐在地，时而腾空而起；作揖、摆尾、舔身、踢腿，神态可掬，将麒麟的喜、怒、哀、乐、惊、疑、醒、睡等动静神态都表现得惟妙惟肖。</p>\n<p>　　本次比赛由中国非物质文化遗产保护协会、广东省非物质文化遗产促进会、深圳市非物质文化遗产保护办公室主办，深圳市民间文艺家协会、深圳市非物质文化遗产保护中心、深圳市非物质文化遗产保护协会、龙岗区文学艺术界联合会、龙岗区坂田街道办事处承办。中国非物质文化遗产保护协会会长马文辉，广东省非物质文化遗产促进会副会长张梅，深圳龙岗区人民政府副区长尚博等到场观战。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/ea5578b7-28a0-4493-b44a-927cc0f496d1.jpg',NULL,687,'2017-12-16 20:59:00','2017-12-17 01:15:30','75',0),(2926,'著名诗人、人民文学出版社原总编辑屠岸逝世 享年94岁','张楷欣','中国新闻网','<div style=\"text-align:center\"> \n <img alt=\"图片来源：人民文学出版社官方微博截图。\" src=\"upload/image/d0d280f8-3e51-406e-a776-24c415de5230.jpg\" style=\"border:px solid #000000\" title=\"图片来源：人民文学出版社官方微博截图。\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  图片来源：人民文学出版社官方微博截图。 \n</div>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月16日电 据人民文学出版社官方微博消息：2017年12月16日下午5点，著名诗人、翻译家、出版家，人民文学出版社原总编辑屠岸先生，在京逝世，享年94岁。</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：著名诗人、翻译家屠岸。上官云 摄\" src=\"upload/image/cd34b371-7d22-4ad7-9bbe-b7ba26f0b016.jpg\" style=\"border:px solid #000000\" title=\"资料图：著名诗人、翻译家屠岸。上官云 摄\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  资料图：著名诗人、翻译家屠岸。 \n</div>\n<p>　　1923年，屠岸出生于江苏省常州市。他本名蒋璧厚，自幼家学深厚，母亲是当时的一位才女，写诗、作曲、绘画、弹琴均有所长，在屠岸还在读小学三四年级的时候，母亲就开始教他读唐诗，读《古文观止》，这对后来屠岸成为一名出色的诗人、作家，起到了重要作用。</p>\n<div style=\"text-align:center\"> \n <img alt=\"《屠岸诗文集》(8卷本)书影。人民文学出版社供图。\" src=\"upload/image/a1f50bbf-3d28-4747-9e82-2d47e5e2c4ff.jpg\" style=\"border:px solid #000000\" title=\"《屠岸诗文集》(8卷本)书影。人民文学出版社供图。\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  资料图：《屠岸诗文集》(8卷本)书影。人民文学出版社供图。 \n</div>\n<p>　　屠岸写诗，开始于少年时代。上初一时，他拿起笔写出了第一首诗《北风》；而后，就读于大学英文系的表兄推荐的《牛津英国诗选》和《英诗金库》则激发了屠岸对英文诗的浓厚兴趣。慢慢地，他在写诗的同时也开始译诗。2001年《济慈诗选》译本获第二届鲁迅文学奖翻译奖。2010年，屠岸获得中国翻译文化终身成就奖。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/a1f50bbf-3d28-4747-9e82-2d47e5e2c4ff.jpg',NULL,1493,'2017-12-16 22:28:00','2017-12-17 01:15:30','75',0),(2927,'逛厦门游金门：两岸自驾车友旅游季在厦门启动','孔庆玲','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>厦门12月16日电 (陈悦 许诗婷)“台湾岛腹地小，自驾空间有限，来到大陆自驾，不但可以领略大好河山，也可以增进两岸民众情感”，两岸自驾车友旅游季暨2017厦门自驾旅游节16日在厦门启动，台湾自驾旅游协会会长张美玉道出台湾自驾车友的心声。</p>\n<div style=\"text-align:center\"> \n <img alt=\"张美玉(右二)、翁自保(左二)受赠《厦金自驾旅游指南》 陈悦 摄\" src=\"upload/image/9a36799a-4c24-442c-a6f0-dc5f52574450.jpg\" title=\"张美玉(右二)、翁自保(左二)受赠《厦金自驾旅游指南》 陈悦 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  张美玉(右二)、翁自保(左二)受赠《厦金自驾旅游指南》 陈悦 摄 \n</div>\n<p>　　厦门市自驾旅游协会会长朱国安介绍，本次活动为期两个月，以“逛厦门游金门”为主题，重点推介厦门的“金砖”特色产品和游厦门逛金门的精品旅游线路。</p>\n<p>　　厦门市自驾旅游协会为此印制《厦金自驾旅游指南》，向两岸自驾车友推荐美丽厦门的“金砖”特色线路和来厦自驾旅游的酒店、餐厅、特色小吃和购物，更详细介绍赴金门旅游的办证须知、咨询服务、特色民宿、美食推荐等内容。</p>\n<p>　　自驾节还组织“房车大篷车队”进行四天三夜的清新福建踩线之旅，向广大自驾车友推介“游厦门逛金门”的特色旅游产品。</p>\n<div style=\"text-align:center\"> \n <img alt=\"房车车队整装待发。　吴泽恩 摄\" src=\"upload/image/831091fd-0aac-49ea-8898-d56b2d17190e.jpg\" title=\"房车车队整装待发。　吴泽恩 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  房车车队整装待发。　吴泽恩 摄 \n</div>\n<p>　　金门自驾试乘试驾活动是本次自驾节的“打头炮”，在开幕的同时，由闽浙粤赣四省六市的60位车友组成的活动体验团齐聚五通码头，乘坐厦金渡轮前往金门进行各类车友交流活动、欣赏金门美丽景色和采购金门的特色伴手礼。</p>\n<p>　　金门县政府参议翁自保说，厦门是度假休闲城市，金门则以自然生态慢活度假见长，两地旅游各有特色。</p>\n<p>　　他告诉记者，大陆游客已成为金门旅游主要增长点，两地旅游部门此前已经整合优势旅游资源，共同营销“金厦旅游线路”，希望通过活动，两地携手打造“海峡旅游品牌”，为金门创造无限商机。</p>\n<p>　　翁自保亦表示，未来金门将积极争取大陆车友金门自驾游，共同培育厦金自驾游线路。</p>\n<p>　　近年来，厦门积极推动两岸自驾游交流合作，此前已顺利实现台湾车友自驾车入闽旅游。</p>\n<p>　　厦门市旅游发展委员会副主任张国良在当天启动仪式上表示，厦门将进一步出台扶持政策，拓展两岸旅游合作新领域，推动厦门进一步成为两岸自驾游旅游目的地和集散中心。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/831091fd-0aac-49ea-8898-d56b2d17190e.jpg',NULL,591,'2017-12-16 13:48:00','2017-12-17 01:15:30','70',0),(2928,'香港工展会揭幕 林郑月娥购物显亲民','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>香港12月16日电(记者 李焯龙)香港历史悠久的大型展览会――工展会16日正式揭幕，特区行政长官林郑月娥当天凌晨从北京返港，一大早便来到现场参观及购物，且收获颇丰。主办方预计，今年的总销售额会有约10%至15%增长，有信心能突破10亿元(港币，下同)。</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图为林郑月娥。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 谭达明 摄\" src=\"upload/image/1620ac28-9371-41af-839b-a2cb96c6767d.jpg\" title=\"资料图为林郑月娥。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 谭达明 摄\"> \n <input id=\"8b697fd625514b9ba8bcaa600356f05f\" name=\"8b697fd625514b9ba8bcaa600356f05f\" type=\"hidden\" value=\"http://www.chinanews.com/part/561/2017/1122/4451.html\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图为林郑月娥。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 谭达明 摄 \n</div>\n<p>　　当天上午，香港岛铜锣湾维多利亚公园热闹非凡，香港中华厂商联合会(厂商会)继续在此举办年度盛事工展会，林郑月娥、外交部驻港特派员公署特派员谢锋、中央政府驻港联络办副主任谭铁牛等应邀出席开幕典礼。</p>\n<p>　　厂商会会长李秀恒致辞时表示，工展会在港家喻户晓，代表着香港工业辉煌和引以自豪的一面，不但为各大品牌提供平台展现其最新产品，还让港人和旅客了解更多有关香港工业的最新发展。</p>\n<p>　　他提到，工展会今年引入电子消费新模式，首次与两大电子支付商――“支付宝”(Alipay)和“微信支付”(Wechat Pay)合作，以方便内地旅客在场内购物消费。</p>\n<p>　　李秀恒表示，去年工展会的总销售额逾9亿元，预计今年会有约10%至15%增长。进场人次方面，去年约200万人次进场，也期望今年有10%至20%的增长。</p>\n<p>　　李秀恒还说，工展会近年多次举办关怀弱势的活动，让他印象深刻。最后，他特别感谢林郑月娥当天凌晨从北京返港，即便休息不足，亦来到现场支持工展会。</p>\n<p>　　典礼结束后，林郑月娥留在场内参观。首先，她来到“AR及VR互动学习体验区”，了解虚拟现实技术发展，然后转到“香港回归20周年庆典摄影比赛”得奖作品展参观。</p>\n<p>　　及后，林郑月娥开始“购物之旅”，到多个展销摊位与店员倾谈并试食。期间，不时有场内人士高喊“林太你好”，林郑月娥朝他们挥手微笑，又与店员合影留念。最后，她购买了虾子面、腊肠、腊肉等食物，共花5550多元。</p>\n<p>　　离开工展会前，林郑月娥会见传媒时表示，工展会与包括她在内的香港人一起成长，很高兴能赶回来出席工展会开幕典礼。她会将大部分购买的面条赠予天主教会的修女，腊味则留在礼宾府内自用和宴客，部分亦会送赠别人。</p>\n<p>　　她还说，香港经济今年整体向好，预祝工展会参展商生意兴隆，令社会气氛在圣诞、新年期间更欢乐。</p>\n<p>　　据介绍，本届工展会展期至明年1月8日，场内有超过400个参展商，共设逾880个摊位，参展商推出逾百项“抗通胀购物优惠”作招牌。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/1620ac28-9371-41af-839b-a2cb96c6767d.jpg',NULL,980,'2017-12-16 18:47:00','2017-12-17 01:15:30','69',0),(2929,'林郑月娥：香港要把握机遇更好融入国家发展大局','田博川','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>北京12月15日电 (记者 陈小愿)来北京述职的香港特别行政区行政长官林郑月娥15日表示，国家的发展过程中，香港有很多领域可以参与。香港要把握机遇，更好地融入国家发展大局。</p>\n<div style=\"text-align:center\"> \n <img alt=\"12月15日，香港特别行政区行政长官林郑月娥在香港驻京办会见记者。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 刘关关 摄\" src=\"upload/image/acf09f51-9d6c-43e2-a6c7-814d3b0f37fc.jpg\" style=\"border:px solid #000000\" title=\"12月15日，香港特别行政区行政长官林郑月娥在香港驻京办会见记者。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 刘关关 摄\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  12月15日，香港特别行政区行政长官林郑月娥在香港驻京办会见记者。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 刘关关 摄 \n</div>\n<p>　　当天，国家主席习近平、国务院总理李克强分别会见了林郑月娥。林郑月娥晚间在香港特区政府驻北京办事处会见记者时作上述表示。</p>\n<p>　　林郑月娥说，中共十九大报告指出，要支持香港融入国家发展大局。香港要了解十九大报告精神，以及国家的发展大局，以便更好地参与其中。</p>\n<p>　　她还表示，国家发展给香港带来很大的机遇，例如在提升人民生活水平等方面，对香港具有优势的服务业有很大的需求。香港要把握机遇，把专业服务做得更好。希望香港的专业服务团体、专业人士能抓住这个机遇，进入粤港澳大湾区或“一带一路”沿线地区，发展他们的专业。</p>\n<p>　　林郑月娥在记者会上还介绍了此次述职行程。</p>\n<p>　　据她介绍，除向国家领导人述职外，13日抵京以来，她还前往公安部、国家发展和改革委员会、国务院港澳事务办公室等部委和机构拜访。期间，香港特区政府与有关部委签署了《国家发展和改革委员会与香港特别行政区政府关于支持香港全面参与和助力“一带一路”建设的安排》和《内地与香港特别行政区关于就采取刑事强制措施或刑事检控等情况相互通报机制的安排》。</p>\n<p>　　林郑月娥透露，会面时，有关部委负责人表示，未来将陆续推出便利港人在内地生活、求学和工作的相关措施。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/acf09f51-9d6c-43e2-a6c7-814d3b0f37fc.jpg',NULL,923,'2017-12-16 00:31:00','2017-12-17 01:15:30','69',0),(2930,'青年唐卡画师在上海金泽古镇“集体毕业”','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>上海12月16日电 (王笈)壤巴拉觉囊非遗文化(上海)交流活动16日开幕。来自四川省阿坝藏族羌族自治州壤塘县的数十位青年唐卡画师完成了在“壤巴拉觉囊唐卡传习所”的刻苦修学，于上海金泽工艺社举办毕业作品展，“集体毕业”。</p>\n<div style=\"text-align:center\"> \n <img alt=\"12月16日，数十位青年唐卡画师完成了在“壤巴拉觉囊唐卡传习所”的刻苦修学，“集体毕业”。　汤彦俊 摄\" src=\"upload/image/138af7bd-0fe2-4fc8-a726-d47ede8c2eb0.jpg\" title=\"12月16日，数十位青年唐卡画师完成了在“壤巴拉觉囊唐卡传习所”的刻苦修学，“集体毕业”。　汤彦俊 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  12月16日，数十位青年唐卡画师完成了在“壤巴拉觉囊唐卡传习所”的刻苦修学，“集体毕业”。　汤彦俊 摄 \n</div>\n<p>　　四川省阿坝州壤塘县的“壤巴拉非物质文化遗产传习所”以觉囊梵音古乐、唐卡艺术、时轮藏香、藏医药、民族服饰等一系列国家级、省级非物质文化遗产的保护、研究和传习为核心，近年来在非遗研究、文化交流、传承保护等方面都取得了显著成就。目前，壤塘已有国家级非遗传承2项、国家级文保单位3个、国家级传统村落4座、省州级文化资源上百个，先后被评为中国民间艺术之乡、中国藏族民间文化保护传承基地。</p>\n<p>　　位于上海市青浦区的金泽古镇则是千古鱼米之乡，河流纵横交错，曾有四十二桥，桥桥有庙，享有“金泽古桥甲天下”的美誉。</p>\n<div style=\"text-align:center\"> \n <img alt=\"金泽工艺社“壤巴拉非遗传习(上海)基地”。　汤彦俊 摄\" src=\"upload/image/b35ebc05-5829-4367-aa0a-972238afcd7e.jpg\" title=\"金泽工艺社“壤巴拉非遗传习(上海)基地”。　汤彦俊 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  金泽工艺社“壤巴拉非遗传习(上海)基地”。　汤彦俊 摄 \n</div>\n<p>　　2016年，阿坝州壤塘县政府带领非遗文化走出高原，与上海市青浦区共同在金泽工艺社建立了“壤巴拉非遗传习(上海)基地”，促进非遗的传承与文化的交流。经过一年多的努力，觉囊唐卡、梵乐、雕刻、藏医药等9项非遗传习项目陆续办展，来自清华、中央美院、中国音乐学院、上海博物馆等20多所高校和文化机构的学者、艺术家参与科研和授课。古老传承与现代文化碰撞、高原村落与沿海都市相接，呈现出多元文化融合的独特魅力。</p>\n<p>　　“传习所解决了几个问题：让老百姓通过传习所成为比较‘高级’的劳动者，有所收入；让唐卡这一类差不多要失传了的祖国瑰宝发扬光大。”阿坝州委副书记、州长杨克宁告诉记者，“我们把传习所搬到上海青浦，应该说是让我们的孩子们走出来了，有了更宽广的视野，看到更多、听到更多，更有利于传习的发展。”</p>\n<div style=\"text-align:center\"> \n <img alt=\"金泽工艺社“壤巴拉非遗传习(上海)基地”。　汤彦俊 摄\" src=\"upload/image/b2f29daf-2472-46da-84cd-96071d9d9794.jpg\" title=\"金泽工艺社“壤巴拉非遗传习(上海)基地”。　汤彦俊 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  金泽工艺社“壤巴拉非遗传习(上海)基地”。　汤彦俊 摄 \n</div>\n<p>　　当天的壤巴拉觉囊非遗文化(上海)交流活动上，60多位学者和艺术家还以“壤塘――新时代传统文化创造性转化、创新性发展实践”为题，从不同领域和角度对“壤塘模式”进行了跨学科的学术研究。</p>\n<p>　　壤巴拉觉囊非遗文化(上海)交流活动由四川省阿坝州委、阿坝州人民政府，上海金泽工艺社等共同主办。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/b2f29daf-2472-46da-84cd-96071d9d9794.jpg',NULL,1171,'2017-12-16 20:14:00','2017-12-17 01:15:30','75',0),(2931,'105名马来西亚华裔少年探访中国雕版印刷博物馆','孔庆玲','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>南京12月16日电 (记者 崔佳明)16日，身着统一营服的105名马来西亚华裔少年在扬州走进中国雕版印刷博物馆，亲身体验古老的雕版印刷技艺，感受中国印刷文化。</p>\n<p>　　雕版印刷在印刷史上有着“活化石”之称，是中国传统文化脉络的精湛技艺。扬州是中国雕版印刷术的发源地，是中国国内唯一保存全套古老雕版印刷工艺的城市。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为马来华裔少年体验雕版印刷。　王爱萍 摄\" src=\"upload/image/6ba14e97-f7bc-4c59-af8a-29e2a2e50674.jpg\" title=\"图为马来华裔少年体验雕版印刷。　王爱萍 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为马来华裔少年体验雕版印刷。　王爱萍 摄 \n</div>\n<p>　　在参观过程中，雕版印刷大师现场展示雕版印刷技艺，演示抄写文字、雕版、印刷到成品的全过程。马来西亚华裔少年们认真倾听，仔细了解雕版印刷的历史，专注观察雕版印刷的过程，同时，马来西亚华裔少年们现场还在雕版印刷大师指导下，动手印刷了雕版印刷作品，并将印刷成品带回去，他们告诉记者，今天下午就要举行闭营仪式了，第一次这么近距离地接触雕版印刷，是他们此行留下的“扬州记忆”之一。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为雕版大师现场展示雕版技艺。　王爱萍 摄\" src=\"upload/image/e556dcb2-8510-410a-85be-f520ad26ef25.jpg\" title=\"图为雕版大师现场展示雕版技艺。　王爱萍 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为雕版大师现场展示雕版技艺。　王爱萍 摄 \n</div>\n<p>　　2017中国文化行--“一带一路”完美江苏冬令营8日在扬州开营，此次江苏冬令营由中国华文教育基金会主办，江苏省人民政府侨务办公室、扬州市人民政府侨务办公室承办，完美(中国)有限公司资助马来西亚105名华裔少年赴扬州参加冬令营，开展为期12天的中华传统文化学习与交流活动，旨在增强海外华裔少年对祖籍国的认同感，传承弘扬民族文化。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/e556dcb2-8510-410a-85be-f520ad26ef25.jpg',NULL,44,'2017-12-16 13:57:00','2017-12-17 01:15:30','73',0),(2932,'福利来了！12月20日起买火车票可积分换车票','张楷欣','北京晨报','<p>　　<strong>铁路购票积分可兑换车票</strong></p>\n<p>　　<strong>12月20日起实施 乘客须主动申请成为常旅客会员</strong></p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：西成高铁。刘忠俊 摄\" src=\"upload/image/fb7020fb-8300-446b-a750-2642665a3fa8.jpg\" title=\"资料图：西成高铁。刘忠俊 摄\"> \n <input id=\"2ae3c63782314757923670564d09571f\" name=\"2ae3c63782314757923670564d09571f\" type=\"hidden\" value=\"http://www.chinanews.com/tp/hd2011/2017/12-06/786985.shtml\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：西成高铁。刘忠俊 摄 \n</div>\n<p>　　北京晨报讯(记者 曹晶瑞)昨日，记者从中国铁路总公司获悉，12月20日起，成为铁路常旅客会员的旅客可以用积分换火车票了。</p>\n<p>　　中国铁路总公司有关部门负责人介绍，今年12月20日起，年满12周岁的自然人，通过12306网站(含手机APP)、车站专门窗口等铁路部门提供的渠道，在主动申请并完成身份认证后，可以成为“铁路畅行”常旅客会员。铁路常旅客会员购买车票将获得相应乘车积分，积分按照其所购车票票价进行累计(具体按照5倍于车票票面价格累计积分，即旅客购买100元车票获得积分500分)。积分首次达到10000分以上，即获得积分兑换资格，可用于兑换铁路部门指定车次的列车车票。</p>\n<p>　　据介绍，使用所兑换车票的乘车人可以是会员本人，也可以是其设定的受让人。目前，申请铁路常旅客会员的有效证件包括居民身份证、港澳居民来往内地通行证、台湾居民来往大陆通行证、护照等。</p>\n<p>　　该负责人表示，关于铁路常旅客会员服务详细情况，旅客可以登录铁路客户服务中心12306网站、拨打12306客服电话或到各大车站窗口查阅、咨询。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/fb7020fb-8300-446b-a750-2642665a3fa8.jpg',NULL,281,'2017-12-17 01:09:00','2017-12-17 01:15:30','65',0),(2933,'为国宝奔走！华侨华人助力海外流失文物踏上“归途”','孔庆玲','中国新闻网','<p>　<strong>　（侨务新时代）华侨华人助力海外流失文物踏上“归途”</strong></p>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>北京12月16日电 (记者 冉文娟)可喜可贺，又一“国宝”回归中国。知名侨商、世茂集团董事局主席许荣茂日前向北京故宫博物院捐赠《丝路山水图》。这幅明代长卷于上世纪30年代流失海外，由许荣茂出资2000万美元购得后无偿捐赠。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为《丝路山水地图》局部。 <a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 杜洋 摄\" src=\"upload/image/5160a763-b30e-4d92-9b48-9e7b766e9e6c.jpg\" title=\"图为《丝路山水地图》局部。 <a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 杜洋 摄\"> \n <input id=\"60f00b5466ad4ab3a5780e1b945e94c8\" name=\"60f00b5466ad4ab3a5780e1b945e94c8\" type=\"hidden\" value=\"http://www.chinanews.com/tp/hd2011/2017/11-30/785954.shtml\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  图为《丝路山水地图》局部。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 杜洋 摄 \n</div>\n<p>　　“当我知道故宫为筹措资金所苦时，就觉得有责任有义务让这幅画回归故里，为文物保护尽绵薄之力，便出资从私人藏家手中购入并捐给故宫。”许荣茂在捐赠仪式上如是说。</p>\n<p>　　联合国教科文组织前些年发布的报告显示，据不完全统计，在全世界47个国家、200多家博物馆的藏品中，有164万余件中国文物，这其中不包括私人收藏。另据中国文物学会统计，从1840年鸦片战争以来，因战争、不正当贸易等原因，超过1000万件中国文物流失海外，其中国家一、二级文物达100余万件。</p>\n<p>　　这些流失文物不仅是璀璨文明的象征，一定程度上也是中国历经“创伤”的见证。长期以来，以华侨华人为主体的“海外军团”通过多种方式为国宝奔走，促文物回归。</p>\n<p>　　比利时华人吴培20多年来一直从事外销瓷收藏。2014年，他耗资百万将珍藏的1000件瓷器运回国内，在江苏如皋创办“明清外销瓷展览馆”。在吴培看来，从事收藏不仅是兴趣使然，更是出于自己“文物回流”的心愿。</p>\n<p>　　“瓷器放在家中只能自己鉴赏，运回国内则能让更多人领略艺术之美”，吴培向记者透露，目前已将所藏瓷器的大部分运回国内，一方面通过展览让更多人了解外销瓷，另一方面，通过向高校捐赠，助力学术研究。</p>\n<p>　　据媒体报道，近年来不少海外华人收藏家出于安全和情感等因素考虑，纷纷将自己毕生所藏捐赠给中国国内博物馆，这成为文物“回流”的重要渠道。</p>\n<p>　　除个人捐赠之外，也有许多华人利用自身便利条件，在流失文物的追讨中积极斡旋，助力文物回归。</p>\n<p>　　2015年，福建三明市阳春村被盗的章公祖师肉身佛像现身匈牙利某个展览中，随即引爆舆论，当地村民踏上文物跨国追索之路。匈牙利华人李震是相关追讨事宜的海外联络人。</p>\n<p>　　为确认佛像就是阳春村被盗文物，李震等人多次到博物馆现场观察辨认，并就此事向荷兰驻匈牙利大使馆递交了请求荷兰政府帮助佛像回归的请愿书。此外，他还就佛像事宜两次前往荷兰，并在当地进行相关宣传活动。</p>\n<p>　　“失踪近20年的佛像被发现，我极为理解当地村民的心情，并希望提供力所能及的帮助”，李震告诉记者，目前追讨事宜已经进入法律程序，自己将和村民密切联系，关注事件进展，“盼望章公祖师脱离遥远的禁锢，回到自己的千年故地，重归于世代敬拜他的民众之中”。</p>\n<p>　　在助力文物回归过程中，海外侨胞不仅“单兵作战”，还采取“集体战术”。由华人发起成立的收藏家协会、文物保护促进会等广募资金，多渠道助力“宝物”重返华夏大地。</p>\n<p>　　2004年至今，由旅美华人招思虹组建的“金山之路”读者团队已先后让5000余件文物重新回归中国。“我们团队的宗旨是‘尽一份力，发一份光’，当好守责的‘匹夫’”，招思虹如是说。</p>\n<p>　　华侨华人倾尽心力，中国官方亦高度重视文物保护工作，鼓励和促进流散文物回流。</p>\n<p>　　2015年6月30日，中国首个海外回流文物口岸交易市场在北京国家对外文化贸易基地正式成立。该市场基于免证、保税等政策优势，面向国际文物经营机构、携带中国境外文物的归国人员，搭建起海外回流文物展览展示及交易平台。而针对文物回流降税简政的呼声，多个政府部门亦积极研讨，探索方案。</p>\n<p>　　“中国文化遗产事业得到了广大海外华侨华人无私的赞助和支持。”故宫博物院院长单霁翔曾在受访时对华侨华人表示敬意。相信在越来越多华侨华人的助力下，将会有更多文物踏上归途，重新散发光芒。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/5160a763-b30e-4d92-9b48-9e7b766e9e6c.jpg',NULL,1519,'2017-12-16 09:44:00','2017-12-17 01:15:30','73',0),(2934,'国际名校赛艇对抗赛暨中国赛艇公开赛在广州举行','张楷欣','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>广州12月16日电 (记者 唐贵江)2017中国广州国际名校赛艇对抗赛暨中国赛艇公开赛12月16日在广东省船艇训练中心举行。国际名校包括英国牛津大学、剑桥大学，美国耶鲁大学、哈佛大学、哥伦比亚大学，澳大利亚墨尔本大学、悉尼大学，新西兰奥塔哥大学；此次参赛的国内队伍全部来自国内“双一流”名校，包括清华大学、北京大学、浙江大学、西安交通大学、西安电子科技大学、中山大学、华南理工大学以及香港中文大学。</p>\n<div style=\"text-align:center\"> \n <img alt=\"海内外名校队伍争相参赛 唐贵江 摄\" src=\"upload/image/16ddb106-1a1e-4f53-8460-49fc667ad23b.jpg\" title=\"海内外名校队伍争相参赛 唐贵江 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  海内外名校队伍争相参赛 唐贵江 摄 \n</div>\n<p>　　本次比赛由中国赛艇协会和广东省体育局联合主办，广东省青少年训练竞赛中心、广东省船艇协会、体育之窗文化股份有限公司承办，广东省教育厅是支持单位，中国赛艇俱乐部联盟、广东省船艇训练中心、广东省青少年体育联合会、广东省赛艇协会、深圳市深潜教育培训有限公司协办，广州圣丰体育文化发展有限责任公司负责运营推广。</p>\n<p>　　比赛分为五个板块，分别为名校对抗赛、国家队精英对抗赛、企业大师对抗赛、名将对抗赛以及测功仪大赛。名校对抗赛参赛学校由国际、国内顶尖名校组成。国际名校包括英国牛津大学、剑桥大学，美国耶鲁大学、哈佛大学、哥伦比亚大学，澳大利亚墨尔本大学、悉尼大学，新西兰奥塔哥大学；此次参赛的国内队伍全部来自国内“双一流”名校，包括清华大学、北京大学、浙江大学、西安交通大学、西安电子科技大学、中山大学、华南理工大学以及香港中文大学。企业大师组由亚洲赛艇联合会主席王石、中国赛艇俱乐部联盟主席徐少春领衔参赛。名将组由历届在奥运会、亚运会上获得冠亚军和为中国赛艇运动做出突出贡献的教练员和运动员组成。</p>\n<div style=\"text-align:center\"> \n <img alt=\"运动员参赛中 唐贵江 摄\" src=\"upload/image/4ba96053-c610-43d2-bb2f-05444c9b90db.jpg\" title=\"运动员参赛中 唐贵江 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  运动员参赛中 唐贵江 摄 \n</div>\n<p>　　此次参赛学校及企业家俱乐部阵容为赛艇运动在中国开展以来最为强大的一次。经过激烈争夺，在名校组中，哥伦比亚大学和奥塔哥大学分获1000米男子八人单桨有舵手和女子四人双桨冠军；名将组中，奥运冠军金紫薇领衔的“08奥运冠军”队获得500米四人双桨冠军，1000米男、女子八人单桨有舵手冠军分别被湖北队和“88奥运”队夺得；精英组、大师组等组别各个小项的冠军也各有归属。</p>\n<p>　　亚洲赛艇联合会主席王石表示，“当我被选为亚洲赛艇联合会主席的时候，我曾经承诺在我任期之内要筹建300个赛艇俱乐部，3年过去了，中国的赛艇俱乐部已经从以前的20个到现在超过200个。显然，我们举办的赛事对我们民间推广赛艇运动是非常有成效的。”</p>\n<div style=\"text-align:center\"> \n <img alt=\"此次参赛学校及企业家俱乐部阵容为赛艇运动在中国开展以来最为强大的一次 唐贵江 摄\" src=\"upload/image/72c90710-d572-4eb9-839f-d0b8cd3ae306.jpg\" title=\"此次参赛学校及企业家俱乐部阵容为赛艇运动在中国开展以来最为强大的一次 唐贵江 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  此次参赛学校及企业家俱乐部阵容为赛艇运动在中国开展以来最为强大的一次 唐贵江 摄 \n</div>\n<p>　　中国赛艇协会副主席高敬萍表示，实际上赛艇不仅仅是一项运动，更重要的是符合现代社会发展的需求，因为赛艇除了体现了阳光、体魄之外，更重要的是体现了团队协作精神，所以在学生中开展赛艇运动对他们未来走向社会是很有好处的，我们希望通过此次活动可以促进国内外名校的交流，促进体教发展。</p>\n<p>　　曾获奥运银牌的张秀云说：“自从我下队以后到现在，还是第一次这样比赛，我觉得挺有意义的，因为老队员下队以后也不经常在一起，通过这样的活动大家可以在一起交流，我觉得非常好，可以给我们大家一次相聚的机会，我觉得很快乐。”</p>\n<p>　　颇为值得一提的是，17日还将举办中国体育教育高峰研讨会，届时将邀请国内外从事体育、教育的政府官员、专家学者、资深媒体、产业精英等莅临出席。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/72c90710-d572-4eb9-839f-d0b8cd3ae306.jpg',NULL,1904,'2017-12-16 23:20:00','2017-12-17 01:15:30','74',0),(2935,'昆仑决遵义见证中欧重炮对轰 郭文杰PK拉脱维亚名将','卢岩','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>北京12月17日电 记者从赛事组委会获悉，鸿坤昆仑决自由搏击大奖赛68将于17日在贵州遵义红花岗体育运动中心全面开战，本次比赛中，“美丽死神”奇斯辛柯再度来华献技，“KO女王”汪柯菡也将打响回归之战，而张伟丽领衔的“木兰传奇”金腰带争霸战和阿斯哈提、叶翔参加的100公斤以上级世界冠军赛同样是不容错过的搏击盛宴。此外还将进行多场超级战，其中一场90公斤级重量级对决十分引人关注，中国选手郭文杰将与奇斯辛柯拳馆的知名悍将阿尔图・戈尔洛夫展开重炮PK。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/82c36b22-3b51-4562-a711-bb1a7c04bb4b.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　26岁的郭文杰来自武汉壮格搏击俱乐部，与曾杀入诸神之战世界八强的“闪电”田鑫师出同门。身高188公分的郭文杰曾是体制内一位顶尖散打高手，2014年登顶全国散打锦标赛85公斤级冠军。在外战方面，郭文杰同样战绩显赫，相继夺取中德对抗赛85公斤级冠军、中俄对抗赛85公斤级冠军、中国功夫VS八国联赛85公斤级冠军等一系列优胜。</p>\n<div style=\"text-align:center\"> \n <img alt=\"组委会供图\" src=\"upload/image/c46fee3f-2724-4772-8193-418b59e2bcb3.jpg\" style=\"border:px solid #000000\" title=\"组委会供图\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  组委会供图 \n</div>\n<p>　　郭文杰目前保持着19战16胜3负9KO的战绩，胜率及KO率在同级别高手中位居顶端。11月5日的昆仑决66武汉站，郭文杰首次亮相昆仑决赛场便一鸣惊人，他与伊朗全国自由搏击冠军特拉伊・卡里米展开交锋。比赛中伊朗人不断通过强力低扫试图破坏郭文杰的重心和出拳节奏，但很快被郭文杰一波接一波的重拳攻势所阻击。第一回合战至1分45秒，郭文杰在近身拳法交换中一记完美的左摆拳将卡里米当场击昏，制造了当晚最值得回味的经典镜头。</p>\n<div style=\"text-align:center\"> \n <img alt=\"组委会供图\" src=\"upload/image/774952e1-295d-48ea-803b-c01c14b24674.jpg\" style=\"border:px solid #000000\" title=\"组委会供图\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  组委会供图 \n</div>\n<p>　　本次昆仑决68郭文杰的对手来头颇大，拉脱维亚名将阿尔图・戈尔洛夫是“美丽死神”奇斯辛柯拳馆的明星级拳手，这位25岁悍将过去在职业擂台上有着17战14胜3负5KO的不俗表现，把持着拉脱维亚K-1冠军和IFA世界冠军等头衔，在Enfusion、Dominet Event等欧洲顶级大赛也时有出彩表现。戈尔洛夫的战斗风格既有东欧选手与生俱来的彪悍勇猛，同时也善于用头脑打拳，将力量与技术实现了完美融合，更令对手难以征服。戈尔洛夫在昆仑决曾有过3次出场经历，取得了2胜1负的表现，打败了薄福凡和邓宁宁两位中国实力派好手。</p>\n<div style=\"text-align:center\"> \n <img alt=\"组委会供图\" src=\"upload/image/b69c73a3-c5ec-4de5-81b9-6a6321caf841.jpg\" style=\"border:px solid #000000\" title=\"组委会供图\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  组委会供图 \n</div>\n<p>　　12月17日，对付中国选手颇有心得的戈尔洛夫遭遇气势如虹的郭文杰，两位重炮手的碰撞势必将迸发出震耳轰鸣和夺目火光。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/b69c73a3-c5ec-4de5-81b9-6a6321caf841.jpg',NULL,1865,'2017-12-17 01:00:00','2017-12-17 01:15:30','74',0),(2936,'江西成立首家新疆籍乡亲汉语学习交流中心(图)','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>南昌12月16日电 (记者 苏路程)12月16日，江西首家新疆籍乡亲汉语学习交流中心在南昌成立，免费招收在赣新疆籍同胞，帮助其提高汉语水平。</p>\n<p>　　据介绍，上述交流中心设置了语文和日常双语课程，首批培训班将招收100人，每周末开课。</p>\n<p>　　上述交流中心负责人之一的新疆籍商人巴克尔在南昌生活了近10年。初到南昌时，因不懂汉语，巴克尔遇到诸多创业困扰。经过自学，巴克尔克服了语言障碍，在南昌开设公司，并带领新疆乡亲在此创业。</p>\n<p>　　“生意越做越大，来南昌创业的乡亲越来越多，但他们不会说汉语”，巴克尔称，开设汉语学习中心是为解决在外乡亲的语言沟通不畅问题，帮助他们提高普通话水平，“破除语言障碍，才能扎根江西，融入大家庭”。</p>\n<div style=\"text-align:center\"> \n <img alt=\"12月16日，江西首家新疆籍乡亲汉语学习交流中心在南昌成立。　苏路程 摄\" src=\"upload/image/fa2d82f4-95b2-49ce-ae01-a000951926c2.jpg\" title=\"12月16日，江西首家新疆籍乡亲汉语学习交流中心在南昌成立。　苏路程 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  12月16日，江西首家新疆籍乡亲汉语学习交流中心在南昌成立。　苏路程 摄 \n</div>\n<p>　　南昌工学院建筑工程学院辅导员艾孜买提・艾山是交流中心的授课老师。他称，很喜欢这个工作，“每个中国人都要学好国语，国语没学好会遇到很多障碍，我一直希望能帮助更多乡亲学好汉语。”</p>\n<p>　　根据新疆维吾尔自治区驻江西省新疆籍务工经商人员服务管理工作组统计数据显示，截至目前，有近万人新疆籍同胞在江西学习、务工、经商。</p>\n<p>　　上述工作组相关工作人员表示，“语言相通才能心灵通，心灵通才能达到交流”，新疆籍乡亲汉语学习交流中心可以提高新疆籍同胞的汉语水平，更加密切江西同胞与新疆同胞的交流，加强彼此的交流、交往和思想、文化交融，有利于促进民族团结。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/fa2d82f4-95b2-49ce-ae01-a000951926c2.jpg',NULL,1491,'2017-12-16 20:47:00','2017-12-17 01:15:30','65',0),(2937,'西双版纳植物园食堂现野生巨蟒','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>西双版纳12月16日电(常国轩 岩香)记者16日从云南省西双版纳傣族自治州森林公安局获悉，近日，西双版纳植物园东区食堂门口竟然出现了一条巨蟒，所幸被好心的植物园工作人员发现，并带到密林放生。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为工作人员将巨蟒带至密林深处放生。 西双版纳植物园供图\" src=\"upload/image/23129fb9-bf07-4ae3-8c3a-8823530c9279.jpg\" title=\"图为工作人员将巨蟒带至密林深处放生。 西双版纳植物园供图\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为工作人员将巨蟒带至密林深处放生。 西双版纳植物园供图 \n</div>\n<p>　　13日晚间，植物园食堂的一位工作人员在门口不远处发现一条巨蟒，植物园保安闻讯后立马赶到现场处置，以免有人或过往车辆误伤巨蟒。受惊后的巨蟒，爬到了路边的一颗榕树上一动不动。为避免巨蟒受到伤害，植物园工作人员决定并将其转移到远离道路的密林之中。找到适合林地后，工作人员小心地将巨蟒从树上擒获，用毛巾包裹住其头部，带到密林中后一放手，巨蟒便向草丛深处中“嗖”地窜去。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为工作人员捕捉巨蟒。 西双版纳植物园供图\" src=\"upload/image/2b30fd4b-3189-492a-a7e1-0aa58d60d2e6.jpg\" title=\"图为工作人员捕捉巨蟒。 西双版纳植物园供图\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为工作人员捕捉巨蟒。 西双版纳植物园供图 \n</div>\n<p>　　西双版纳植物园东区不仅保存有大量珍稀濒危植物，且为诸多珍稀动物提供了安全的生存场所。在这片林子周围，原鸡、水巨蜥、缅甸蟒、金花蛇、豹猫、毛耳飞鼠等国内难得一见的动物都曾多次被发现。</p>\n<p>　　森林公安民警提醒：蟒蛇属于国家一级珍贵、濒危野生重点保护动物，无论是抓、藏、买卖、运输、捕杀都属违法行为，将受到法律严惩。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/2b30fd4b-3189-492a-a7e1-0aa58d60d2e6.jpg',NULL,490,'2017-12-16 20:12:00','2017-12-17 01:15:30','65',0),(2938,'印尼雅加达举办“牡丹皇后”陈道容画展（图）','谢萍','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>雅加达12月16日电 (记者 林永传)在印尼书画界有“牡丹皇后”之称的陈道容，15日在雅加达广肇会馆展出了她近几年来创作的40多幅牡丹和10多幅孔雀画作。</p>\n<p>　　由东盟―中国书画家联合总会、印尼书法家协会、海外华人书法家协会和怡馨苑书法学社联合举办的本次展览，吸引了印尼书画界众多名人和书画爱好者前来观看。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为陈道容(左)致辞。　林永传 摄\" src=\"upload/image/bb5e130a-f2f6-453c-ae3b-11c741b58cc5.jpg\" title=\"图为陈道容(左)致辞。　林永传 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为陈道容(左)致辞。　林永传 摄 \n</div>\n<p>　　中国驻印尼大使馆文化处秘书王智显、印尼书画院董事长胡素丹、海外华人书法家协会联合主席兼主席团召集人阮渊椿、印尼书法家协会主席李秀贤到场致辞并为画展开幕剪彩。</p>\n<p>　　祖籍中国海南省琼州市、1944年出生于印尼的陈道容自幼喜爱书画，然而至60岁时才拜师学画，尤喜牡丹。每年4月逢中国河南洛阳牡丹花开时节，她便与夫君杨庆懋一道到洛阳长住，从牡丹花蕾看到盛开，潜心研习牡丹画，成为享誉印尼书画界的“牡丹皇后”。不久前，陈道容被中国洛阳市书画院聘为名誉院长。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为观众参观画展。　林永传 摄\" src=\"upload/image/24a0ef30-cd77-42c7-9525-a853130a3503.jpg\" title=\"图为观众参观画展。　林永传 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为观众参观画展。　林永传 摄 \n</div>\n<p>　　海外华人书法家协会辅导主席、印尼书画院董事长胡素丹称陈道容笔下的牡丹“雍容华贵，不落俗套，国色天香，飘然纸上”，并评论陈道容所画的孔雀“透出善良、高贵、典雅、吉祥、富贵等寓意和魅力，显得活泼生动”。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为展厅一角。　林永传 摄\" src=\"upload/image/f3448197-fe8a-48d8-a80f-e16d9fc87c9b.jpg\" title=\"图为展厅一角。　林永传 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为展厅一角。　林永传 摄 \n</div>\n<p>　　近几年来，印尼的书画活动日趋活跃，已经以“一带一路“为主题举办了数次书画展览；书画的国际交流亦日渐频繁并屡屡获奖，展现了百花齐放、欣欣向荣的景象。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/f3448197-fe8a-48d8-a80f-e16d9fc87c9b.jpg',NULL,260,'2017-12-16 10:05:00','2017-12-17 01:15:30','73',0),(2939,'首届中国汽车100小时耐力赛在广东肇庆开赛','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>肇庆12月16日电 (记者 郭军)首届中国汽车100小时耐力赛(以下简称：GT100)16日在位于广东肇庆的广东国际赛车场正式开赛。据悉，这是全球比赛时间最长、比赛难度最大，可与勒芒24小时耐力赛媲美的汽车耐力赛。共有20支车队的80位车手将在100小时内一决高低。</p>\n<div style=\"text-align:center\"> \n <img alt=\"参赛选手 主办方供图\" src=\"upload/image/0b4d4a07-216c-482d-b227-3809591cd9ee.jpg\" title=\"参赛选手 主办方供图\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  参赛选手 主办方供图 \n</div>\n<div style=\"text-align:center\"> \n <img alt=\"参赛选手 主办方供图\" src=\"upload/image/a4d257ac-3d53-494c-b433-c681c2ec8bb7.jpg\" title=\"参赛选手 主办方供图\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  参赛选手 主办方供图 \n</div>\n<p>　　据了解，中国汽车100小时耐力赛是全球首创、具有完全自主知识产权的全球民用量产汽车的极限挑战赛，由中国汽车摩托车运动联合会支持，选秀网、云南卫视、中国工业报社联合主办。</p>\n<div style=\"text-align:center\"> \n <img alt=\"首届中国汽车100小时耐力赛在广东肇庆开赛 主办方供图\" src=\"upload/image/b5caebc4-7c04-4e00-b925-503e2c3a38b8.jpg\" title=\"首届中国汽车100小时耐力赛在广东肇庆开赛 主办方供图\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  首届中国汽车100小时耐力赛在广东肇庆开赛 主办方供图 \n</div>\n<p>　　组委会执行主任兼秘书长安自能介绍说，此次赛事每部赛车由四名车手采用换人不换车的方式连续不断地行驶100小时，比谁连续行驶距离最长；所有参赛车辆必须为公安部、工信部目录公告内准许市场销售和上路的7座以下量产乘用车。比赛期间可以加油、充电、换胎、修车。获得第一名的车队将申请认证“100小时连续行驶里程最多的车辆”大世界基尼斯纪录。</p>\n<div style=\"text-align:center\"> \n <img alt=\"首届中国汽车100小时耐力赛在广东肇庆开赛 主办方供图\" src=\"upload/image/99c5725a-5ac2-42a5-9625-8944b66c6c81.jpg\" title=\"首届中国汽车100小时耐力赛在广东肇庆开赛 主办方供图\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  首届中国汽车100小时耐力赛在广东肇庆开赛 主办方供图 \n</div>\n<p>　　同时，大赛所用的车除做安全改装外不做任何性能改装，是原汁原味的民用车。参赛的20个汽车品牌由全球网友票选得出，其中包括11个中国自主品牌和9个外资合资品牌。参赛的80位车手由全球参加报名的3000多位车手中海选产生。</p>\n<p>　　赛事期间还举行丰富多彩的汽车嘉年华活动，包括俏佳轩中国超级汽车模特大赛、广东美食文化节、汽车音乐会、老爷车展等，让人流连忘返。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/99c5725a-5ac2-42a5-9625-8944b66c6c81.jpg',NULL,189,'2017-12-16 20:39:00','2017-12-17 01:15:31','74',0),(2940,'韦世豪再进球国足1-1平朝鲜 2平1负结束东亚杯征程','田博川','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>北京12月16日电 今天下午，2017男足东亚杯最后一轮在日本东京体育场开打，这也是中国男足2017年度的最后一场比赛。最终，国足在上半场先入一球的情况下，第80分钟被对手直接任意球破门扳平，全场1-1战平朝鲜队。以2平1负的战绩结束本届东亚杯。</p>\n<div style=\"text-align:center\"> \n <img alt=\"国足在上半场先入一球的情况下，最终1-1战平朝鲜队。图片来源：CFP视觉中国\" src=\"upload/image/8d2fe531-848c-476a-b35f-b598452e2485.jpg\" style=\"border:px solid #000000\" title=\"国足在上半场先入一球的情况下，最终1-1战平朝鲜队。图片来源：CFP视觉中国\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  国足在上半场先入一球的情况下，最终1-1战平朝鲜队。图片来源：CFP视觉中国 \n</div>\n<p>　　本届东亚杯前两轮，中国男足首战2-2逼平韩国队，次战以1-2不敌日本队。朝鲜队则以两个0-1先后输给了日本队和韩国队，未尝胜绩的两队都已失去争夺冠军的希望。</p>\n<p>　　开场第5分钟，于大宝小角度打门被门将李明国没收。李明国随后手抛球出现失误，赵旭日断球远射再被李明国勉强扑出，于大宝跟进补射破门但越位在先，进球无效。第20分钟，朝鲜队主罚角球到后点，安柄俊头球攻门被王大雷没收。</p>\n<div style=\"text-align:center\"> \n <img alt=\"韦世豪为中国队率先破门。图片来源：CFP视觉中国\" src=\"upload/image/8155ea9c-dca0-4d92-8bd9-7480ed38fe6d.jpg\" style=\"border:px solid #000000\" title=\"韦世豪为中国队率先破门。图片来源：CFP视觉中国\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  韦世豪为中国队率先破门。图片来源：CFP视觉中国 \n</div>\n<p>　　上半场前半段，中国队场面一度非常被动。第24分钟，里皮决定做出换人调整，吴曦换下了傅欢，把阵型从三后卫改成四后卫。变阵后，国足立马取得立竿见影的效果，第28分钟，朝鲜队角球被解围，吴曦禁区外得球后第一时间直塞，张文钊带球趟过后卫杀入禁区，冷静横敲门前，韦世豪推射空门得手，中国队1-0领先。</p>\n<p>　　第33分钟，韦世豪中场摆脱多人包夹后分球右路，邓涵文远射被没收。随后，郑日冠禁区外射门被封挡，形成单刀的安柄俊头球攻门偏出，中国队上半场1-0领先。</p>\n<p>　　下半场开场仅2分钟，韦世豪禁区前左路连续过人被放倒，他亲自主罚任意球打在人墙上，朝鲜门将李明国接高空球失误，但国足球员未能及时跟进。1分钟后，李永哲禁区外远射，皮球擦到贺惯的腿，稍稍偏出。</p>\n<p>　　第55分钟，朝鲜队边路连续两次传中，郑日冠和朴明成连续射门被挡。第58分钟，安柄俊肋部前插得球射门，出击的王大雷化解险情。第61分钟，范晓冬替下了张文钊。第65分钟，朝鲜队右路斜传禁区后点，郑日冠头球攻门打高。</p>\n<p>　　第75分钟，李永哲远射被王大雷侧身压在身下。2分钟后，吴曦右路横带后传给郑铮，后者远射稍稍偏出。里皮做出本场比赛最后一次换人，年轻前锋杨立瑜换下了于大宝。</p>\n<p>　　换人结束后，郑日冠主罚禁区前任意球破门，1-1。第85分钟，李恩哲远射被王大雷奋力扑出，随后朝鲜队开出角球，无人看管的郑日冠甩头攻门偏出。</p>\n<p>　　中国队(3-4-3)：23-王大雷；2-刘奕鸣、3-贺惯、16-郑铮；21-邓涵文、7-赵旭日(C)、8-赵宇豪、14-傅欢(24’ 15-吴曦)；11-张文钊、22-于大宝、20-韦世豪；</p>\n<p>　　替补：1-颜骏凌、12-石笑天、4-范晓冬、6-高准翼、9-肖智、10-尹鸿博、13-何超、15-吴曦、17-李学鹏、18-杨立瑜、19-郑龙。</p>\n<p>　　朝鲜队(4-5-1)：1-李明国(C)；2-沈贤振、3-张国哲、6-大姜国哲、18-李永哲；4-朴明成、5-李恩哲、14-姜国哲(小)、16-李荣直、11-郑日冠；10-安柄俊</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/8155ea9c-dca0-4d92-8bd9-7480ed38fe6d.jpg',NULL,1621,'2017-12-16 17:41:00','2017-12-17 01:15:31','74',0),(2941,'2017武汉速度赛马公开赛年度总决赛落幕','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>武汉12月16日电 (刘鸿清 徐金波)16日，在经过全年19个赛马日激烈角逐，2017武汉速度赛马公开赛年度总决赛在东方马城举行，“年度马王”、“年度冠军骑师”、“年度最佳练马师”、“年度最佳马主”等多个年度奖项正式揭晓。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为，激烈的比赛现场 刘鸿清 摄\" src=\"upload/image/6f8fb03d-0a39-47c7-8a87-1900e4327ec0.JPG\" title=\"图为，激烈的比赛现场 刘鸿清 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为，激烈的比赛现场 刘鸿清 摄 \n</div>\n<p>　　武汉速度赛马公开赛是中国内地唯一以马主为参赛主体的常态化赛马赛事，2017年度赛事共吸引国内数十家赛马俱乐部的500多匹良驹常年参赛。</p>\n<p>　　当天，30名世界绝色佳丽，40名中国优秀骑手，70匹顶级纯种宝马，冒着寒风在武汉东方马城给5000多名马迷上演本年度武汉最后的户外激情盛典。</p>\n<p>　　下午1点，入围第38届世界丽人大赛湖北赛区决赛的佳丽们为现场观众带来风情肚皮舞表演、纯情诗歌朗诵《雨巷》、柔情黄梅戏经典《十八相送》等才艺表演。与此同时，参加本次收官之战的一众骑师与全体丽人粉墨登场，刹那间点燃了场内的观众的激情。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为，骑师冲刺现场 刘鸿清 摄\" src=\"upload/image/317051df-0753-4c6d-af10-9309afc1a372.JPG\" title=\"图为，骑师冲刺现场 刘鸿清 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为，骑师冲刺现场 刘鸿清 摄 \n</div>\n<p>　　随着主礼嘉宾共同推动启动赛马嘉年华推杆，“2017武汉速度赛马公开赛年度总决赛”正式打响。在首场“中国马冠军杯”1200米比赛中，骑师刘学策骑1号马“乌孙赤兔”率先冲过终点，获得冠军，“乌孙赤兔” 也由此成为今年首设的“年度冠军中国马”。该项赛事就是为鼓励国产马繁育和培训而专设，希望更多的国产马参加速度赛马赛事，从而提高国产马竞技水平的品种改良，推动国产马事业发展。</p>\n<p>　　第二场4岁及以上马1200米纯血马精英赛，骑师赵殿化策骑7号马“吉祥如意”夺得头马；第三场“武汉育马者杯”2岁马组1000米纯血马比赛，是一场满闸赛事(14匹马)，骑师双英策骑6号马“凌波仙子”获得该场第一名；第四场“雅士・神奇百万杯”2岁马组1000米纯血马比赛，本场资金20万元，骑师双英策骑8号马“精彩时刻”再下一城；第五场4岁及以上马组1800米纯血马比赛，也是一场满闸赛事，比赛异常激烈，骑师董小军策骑3号马“黄金王”艰难冲出重围，将本场金牌收入囊中；最后一场3岁马组1700米纯血马比赛，骑师初一发挥出色，他策骑2号马“冠仪狮王”以1’52”6242的成绩冲过凯旋门，获得最后一枚金牌。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为，颁奖仪式现场 刘鸿清 摄\" src=\"upload/image/8d9a9811-876a-4628-affb-061b29075a1a.JPG\" title=\"图为，颁奖仪式现场 刘鸿清 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为，颁奖仪式现场 刘鸿清 摄 \n</div>\n<p>　　依据赛事规则，本年度获得头马最多的马匹、骑师、练马师、马主将分别获得“年度马王”、“年度冠军骑师”、“年度最佳练马师”、“年度最佳马主”桂冠。当日6场比赛结束后年度奖项也正式揭晓，“玉龙神湖”以全年3个头马的成绩，成为新晋马王；双英以9个头马的成绩的获得“年度冠军骑师”，“年度最佳练马师”则被张明亮获得，全年共有16个头马。玉龙马业则获得“年度最佳马主”称号，全年共获得10个头马，成为最大赢家。河北东兴赛马俱乐部董事长王卫东先生则获得2017武汉速度赛马公开赛“年度特别贡献奖”。</p>\n<p>　　赛马比赛的间隙，“第38届世界丽人大赛中国・湖北赛区总决赛”也举行了隆重的颁奖典礼，影视组、优雅组、风韵组、民选组、网络组前三甲，将代表湖北参加12月30日在上海举行的世界丽人中国区总决赛。</p>\n<p>　　“这既是中国赛马的年度压轴大戏，又是一场集赛马、文化、时尚、娱乐为一体的嘉年华”。东方神马集团总裁胡越高表示，东方马城此前已与包括国际旅游小姐、世界旅游小姐等选美机构以及主题游乐机构合作，进一步拓展马城时尚、娱乐元素，创建国内独树一帜的赛马文化旅游品牌。2018年，东方马城将按照武汉“全域旅游”理念，以“四马奔腾”为契机，建设赛马博物馆，开发互动游戏平台，打造中国首屈一指的马文化商业综合体，引进马秀等娱乐元素，进一步彰显武汉独特的赛马文化品牌。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/8d9a9811-876a-4628-affb-061b29075a1a.JPG',NULL,389,'2017-12-16 20:45:00','2017-12-17 01:15:31','74',0),(2942,'《天籁之战2》费玉清重铸经典 星素位置互换迎终极初赛','叶攀','中国新闻网','<div style=\"text-align:center\"> \n <img alt=\"张杰、杨坤、莫文蔚认真听歌\" src=\"upload/image/90354e0b-853b-4ce6-8a08-7e9b0a2eddfd.jpg\" style=\"border:px solid #000000\" title=\"张杰、杨坤、莫文蔚认真听歌\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  张杰、杨坤、莫文蔚认真听歌 \n</div>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月16日电 今晚20:30, 由东方卫视自主原创的大型星素励志音乐节目《天籁之战》第二季将播出第九期，经过两个月的激烈角逐，终于来到了最后一场初赛。四位素人挑战者在本期一上场就获得选择哪位“天籁唱将”来应战的权利，这一战对他们至关重要，直接决定哪位挑战者能抓住最后的机会杀进总决赛。</p>\n<p>　　这一场也是星素同台的最后一次对决，“天籁唱将”莫文蔚、费玉清、杨坤、华晨宇、张杰将同时上台，等待本轮仅剩的四位素人挑战者来挑战。除了素人挑战者拥有更多主动权之外，歌单上又增添了新的“神曲”，终极初赛，一触即发。</p>\n<p>　　<strong>费玉清幸运遇到“最合适”歌曲 重铸经典引人入胜</strong></p>\n<p>　　从第一期开始，《天籁之战》的歌单就“神曲”频出，总是会给“天籁唱将”们出一些难题。在本期节目中，“乐坛常青树”费玉清却幸运地被选中了一首经典怀旧之作，并且这首耳熟能详的电视剧主题曲，在多年前他本人就曾演绎过。而本次在天籁的舞台，小哥费玉清的演唱可以说比当年更胜一筹，让观众沉醉在歌曲的意境中无法自拔。</p>\n<p>　　小哥费玉清的演唱向来有着深厚的文化底蕴，将传统演唱技法与现代歌唱艺术熔于一炉，展示出个人独特的艺术魅力，他的歌声总是散发着古朴典雅的艺术气息，越是仔细品味越是回味无穷。对于这首歌曲，费玉清并没有做太多的改编，而是极大限度地保留了歌曲的原汁原味。一曲唱罢，让张杰瞬间化身迷弟：“小哥给我的感觉就是他的声音就是时间，我们经历的点点滴滴伴随着他的声音，就像电影片段一样在舞台上面回放。”而杨坤更是激动的说：“我是经历过那个年代的，这就是一种情怀，今天就是两个字，完美！”</p>\n<div style=\"text-align:center\"> \n <img alt=\"“天籁唱将”现场互动\" src=\"upload/image/99b83874-8c52-4a9c-ad8f-5b916b4e94f7.jpg\" style=\"border:px solid #000000\" title=\"“天籁唱将”现场互动\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  “天籁唱将”现场互动 \n</div>\n<p>　　<strong>《天籁之战》星素对战赛制大反转 打响终极初赛</strong></p>\n<p>　　之前的节目中在选人环节，“天籁唱将”都是在塔顶逐一亮相，来选择与自己对战的素人挑战者。而本期节目则上演大反转，星素位置调换，“天籁唱将”成为被选的一方，莫文蔚、费玉清、杨坤、华晨宇、张杰将首次同时登台，并且尝试被素人挑战者反选的滋味。由于赛制的转变，本场仅剩的四位素人挑战者张新、肖思齐、邓玲玲、周可人，拥有了更多主动权，到底最终他们会选择与哪位“天籁唱将”同台比拼，分出伯仲？</p>\n<p>　　《天籁之战》第二季到目前为止，已经有两位素人挑战者成功晋级总决赛，一位是两度成功挑战张杰，用实力唱功征服观众和“天籁唱将”的李光；另一位是以独特风格战胜凤凰传奇和费玉清，给观众带来新鲜花样的“摇滚济公”刘相松。接下来，还有耿斯汉、叶秉桓、李夏等素人挑战者，在半决赛的舞台上等待着再次给观众带来天籁之声。</p>\n<p>　　本季《天籁之战》的最后一场初赛即将打响，冲击总决赛的张新能否在总决赛与其他素人挑战者会合吗？一切答案尽在今晚20:30东方卫视《天籁之战》。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/99b83874-8c52-4a9c-ad8f-5b916b4e94f7.jpg',NULL,1445,'2017-12-16 10:58:00','2017-12-17 01:15:31','68',0),(2943,'武警新兵拉练：心中有信仰脚下有力量','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>杭州12月16日电 (见习记者 王刚 通讯员 陈杰 颜石斌 余宁磊)“不怕苦，不怕累，新时代里不掉队！”听到连长徐嘉成的质问，新兵林恒挺直了身板，与一营四连的全体战友回应道。日前，武警浙江总队开展新兵野营拉练，新兵们冒着严寒负重前行，在强化了军人意识的同时，更磨砺了其军人技能。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为：新兵们在山区行进。 武警浙江总队供图\" src=\"upload/image/cb8f8af9-c739-43fd-9206-0d50cc852224.JPG\" title=\"图为：新兵们在山区行进。 武警浙江总队供图\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为：新兵们在山区行进。 武警浙江总队供图 \n</div>\n<p>　　新兵野营拉练的第二天，各部队新兵们背负15公斤重的行囊，转战城区、乡村、山林等地。</p>\n<p>　　“确实很冷，每天要走十多个小时，快坚持不住的时候就想想路上干部和班长给我们说的红军长征故事，就会咬咬牙跟紧了。”1999年出生的女兵谢美云在休息的时候如是说。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为：武警金华支队新兵为战友表演街舞。 武警浙江总队供图\" src=\"upload/image/b565f121-f642-4c86-9eb0-d641b670bc8b.JPG\" title=\"图为：武警金华支队新兵为战友表演街舞。 武警浙江总队供图\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为：武警金华支队新兵为战友表演街舞。 武警浙江总队供图 \n</div>\n<p>　　心中有信仰，脚下有力量。据武警浙江总队新训团政委管新建介绍，该次野营拉练在从严磨练提升新兵军事技能的同时，更侧重于强化任务中的思想政治教育，通过重温入党誓词、革命歌曲熏陶、参观红色纪念馆等活动让拉练之路“红起来”，砥砺新兵参军报国的初心、革命军人的担当意识和强军使命感。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为：新兵们士气高昂。 武警浙江总队供图\" src=\"upload/image/a471a8ac-7a24-4a80-9be4-412bff279084.JPG\" title=\"图为：新兵们士气高昂。 武警浙江总队供图\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为：新兵们士气高昂。 武警浙江总队供图 \n</div>\n<div style=\"text-align:center\"> \n <img alt=\"图为：新兵进行武术表演为战友鼓劲。 武警浙江总队供图\" src=\"upload/image/86df35ae-3144-4f1e-a449-0d1c8e8a15a1.JPG\" title=\"图为：新兵进行武术表演为战友鼓劲。 武警浙江总队供图\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为：新兵进行武术表演为战友鼓劲。 武警浙江总队供图 \n</div>\n<div style=\"text-align:center\"> \n <img alt=\"图为：新兵们说唱十九大精神被点赞。 武警浙江总队供图\" src=\"upload/image/e7ce53d5-45be-4d68-a96b-1b07eb0f907a.JPG\" title=\"图为：新兵们说唱十九大精神被点赞。 武警浙江总队供图\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为：新兵们说唱十九大精神被点赞。 武警浙江总队供图 \n</div>\n<p>　　征途漫漫，面对100多公里的风雨之路，新兵们在接受思想教育的同时，还完成急行军、战地隐蔽、帐篷搭建等实战性科目，为今后的军旅之路夯实基础。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/e7ce53d5-45be-4d68-a96b-1b07eb0f907a.JPG',NULL,679,'2017-12-16 19:20:00','2017-12-17 01:15:31','64',0),(2944,'左小蕾：建设资本市场强国，先做好这四件事','王诗尧','国是直通车','<p>　　中国证监会主席刘士余提出的“建设资本市场强国”概念，该怎么理解？</p>\n<p>　　在12月4日，刘士余主席这样表述――“建设资本市场强国是实现中华民族伟大复兴中国梦的重要任务”。</p>\n<p>　　中国银河证券公司首席经济学家左小蕾在接受<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>国是直通车记者采访时表示，“要让资本市场真正地为强国服务，就要让资本为实体经济服务。”</p>\n<p>　　<strong>资本市场要服务实体经济</strong></p>\n<p>　　左小蕾认为“要想让资本市场为强国服务，就要通过资本市场为实体经济服务，才是有意义的。”</p>\n<p>　　“中国经济需要创新发展，就需要资本的支持，中国资本市场的一个重要原则就是不要让资本空转，减少那些脱实向虚，用钱赚钱的没有意义的经济活动。”她进一步解释道，“金融中的虚拟资本，例如影子银行，资本市场中的虚假重组兼并等这些都是虚拟的金融活动都不利于强国，他们并没有真正地为实体经济服务。”</p>\n<div style=\"text-align:center\"> \n <img alt=\"<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 陈晓根 摄 \" src=\"upload/image/a7744f11-b37c-43c5-98c1-e9994c5204ed.jpg\" style=\"border:px solid #000000\" title=\"<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 陈晓根 摄 \"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\"> \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>发 陈晓根 摄 \n</div>\n<p>　　从资本市场发展的历史上来看，资本市场的发展是建立在实体经济发展的需求上的，也因此才产生了股票、债券，基金等与资本相关的投资活动和资本市场。</p>\n<p>　　按照这样的思路，她进一步强调，实体经济是载体，资本是血液，如果资本脱离了实体经济的载体，做体外循环是没有意义，不仅作为载体的实体经济会垮掉，同时也会为国家整体的经济制造许多危机。</p>\n<p>　　在2017年中央经济工作会议中也专门将服务实体经济列为金融工作三项任务之首，并表示服务实体经济、满足经济社会发展需要，是金融的本分。</p>\n<p>　　对此，左小蕾强调，“我们常说的金融市场是二级市场，二级市场是交易市场，在交易活动中，上市公司将股权给投资人，投资人把钱给上市公司，是交易双方股权资金的互换，对于上市公司并没有直接的作用，如果要让资本市场真正地为强国服务，就必须通过资本市场，让资本真正为实体经济服务。”</p>\n<p>　　<strong>先要做好这四件事</strong></p>\n<p>　　支持上市公司做大做强是资本市场的基本原则，要想落实资本市场强国建设，左小蕾给出四点建议。</p>\n<div style=\"text-align:center\"> \n <img alt=\"<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 顾华夏 摄\" src=\"upload/image/342417e3-06cf-4b51-be21-632c20f70678.jpg\" style=\"border:px solid #000000\" title=\"<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 顾华夏 摄\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\"> \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>发 顾华夏 摄 \n</div>\n<p>　　第一点，要让优质公司上市，进行资本市场的资源优化配置。发展健康的公司在国家经济中可以一边贡献经济增长，一边又可以给投资人回报，这样的公司在资本市场才有资格获得资本支持。</p>\n<p>　　为防止问题企业带病申报、蒙混过关，IPO申请的监管方已经发力，据统计，自新一届发审委上任两个月内，共审核了61家公司的首发申请。其中，34家获得通过，22家被否，5家暂缓表决，通过率为55.7%，而2016年全年过会率为91.14%。</p>\n<p>　　第二点，要完善退市制度，让发展不好的公司退出资本市场，避免占用市场资源。</p>\n<p>　　资本市场应该真正发挥资源配置的主导作用，及时淘汰发展不好企业，让资本市场真正有效地支持强国和经济发展。</p>\n<p>　　中国的资本市场长期存在无法退市这个弊端，例如存在个别地方政府为了不让当地某些企业退市，甚至给予企业大规模的补贴，为企业增加许多非经营性收益来维持上市公司的地位。这样的做法不仅浪费财政资金，也会对资本市场带来不好的影响。</p>\n<div style=\"text-align:center\"> \n <img alt=\"<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 陈宏青 摄\" src=\"upload/image/1420db68-7ea2-42a7-991a-5dfafefccdee.jpg\" style=\"border:px solid #000000\" title=\"<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 陈宏青 摄\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\"> \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>发 陈宏青 摄 \n</div>\n<p>　　第三点，投资人的交易行为要更加理性。</p>\n<p>　　投资人不仅要关注价格的走势，还要观察到价格变化背后的本质原因。如果投资人能够辨别出某家上市公司股价的增长是源于虚假的交易行为，就可以拒绝购买该企业的股票，这样就会对公司产生压力，促使企业提高业绩，保持市值增长。</p>\n<p>　　第四点，要对上市公司资金用途进行监管，促使上市公司做好主营业务。</p>\n<p>　　建设资本市场强国，就需要在资本市场中进行一些制度性的完善，促使上市企业按照原先的计划来安排资金否则就要有相应的惩罚机制，杜绝某些不法上市公司用上市筹集的资金去炒自己的股票、做市值管理，进行无成本的圈钱行为。(杨佳欣)</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/257292a8-b7f9-439d-851c-f8f5b5ada1cb.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>&nbsp;</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/257292a8-b7f9-439d-851c-f8f5b5ada1cb.jpg',NULL,796,'2017-12-16 20:32:00','2017-12-17 01:15:31','66',0),(2945,'陈靖姑文化“海丝”行启幕','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>福州12月16日电 (记者 刘可耕)2017陈靖姑文化“海丝”行系列活动16日在福州启幕。“海上丝绸之路”沿线国家和地区的部分陈靖姑宫庙信众相聚于此，通过举行祈福仪式、交流会等活动，体验陈靖姑信俗文化源远流长的精神文化内涵。</p>\n<div style=\"text-align:center\"> \n <img alt=\"开幕式上的陈靖姑信俗表演《十二婆姐送平安》。　记者刘可耕 摄\" src=\"upload/image/22b21196-2546-4832-802e-f91dcabf0775.jpg\" title=\"开幕式上的陈靖姑信俗表演《十二婆姐送平安》。　记者刘可耕 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  开幕式上的陈靖姑信俗表演《十二婆姐送平安》。　记者刘可耕 摄 \n</div>\n<p>　　福州陈靖姑故居管委会主任郑炜认为，陈靖姑信俗文化播及世界各地，此次陈靖姑文化“海丝”行活动正是为了更好地联络“海丝”沿线人民的感情，为新时代“一带一路”建设提供助力。</p>\n<p>　　活动以沙画表演拉开序幕，将“妇幼保护神”陈靖姑富有传奇色彩的一生娓娓道来，让海内外来宾以艺术的视角领略了陈靖姑文化的历史与传承，感受到了陈靖姑文化与“海上丝绸之路”不可阻断的亲情联系。</p>\n<div style=\"text-align:center\"> \n <img alt=\"台湾新竹胜安宫管委会总干事陈成勋率领的该宫庙信众一行参加开幕式。　记者刘可耕 摄\" src=\"upload/image/fd45b059-7f04-427f-8f7c-1561bb867a92.jpg\" title=\"台湾新竹胜安宫管委会总干事陈成勋率领的该宫庙信众一行参加开幕式。　记者刘可耕 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  台湾新竹胜安宫管委会总干事陈成勋率领的该宫庙信众一行参加开幕式。　记者刘可耕 摄 \n</div>\n<div style=\"text-align:center\"> \n <img alt=\"“娘奶的故事――为顺天圣母的歌与咏”主题吟诵表演开幕式上演。　记者刘可耕 摄\" src=\"upload/image/274ea0d1-26b2-4342-9d82-c8326dfdfd3f.jpg\" title=\"“娘奶的故事――为顺天圣母的歌与咏”主题吟诵表演开幕式上演。　记者刘可耕 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  “娘奶的故事――为顺天圣母的歌与咏”主题吟诵表演开幕式上演。　记者刘可耕 摄 \n</div>\n<p>　　福州仓山区委常委、宣传部部长陈�d致辞称，千百年来，包括陈靖姑文化在内的传统文化随着先民的步伐走向了世界，形成了我们今天看到的陈靖姑文化在“海丝”沿线生根发芽的盛况。</p>\n<p>　　“感谢福州先民把‘顺天圣母’带到台湾，今天我带着学生回到福州谒祖、感恩。”台湾传统文化促进协会副理事长王亿凤说。</p>\n<div style=\"text-align:center\"> \n <img alt=\"台湾传统文化促进协会副理事长王亿凤与海内外来宾分享她心目中的顺天圣母陈靖姑。　记者刘可耕 摄\" src=\"upload/image/4e2f82c8-970f-4ab4-8cc1-d6fb584782fb.jpg\" title=\"台湾传统文化促进协会副理事长王亿凤与海内外来宾分享她心目中的顺天圣母陈靖姑。　记者刘可耕 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  台湾传统文化促进协会副理事长王亿凤与海内外来宾分享她心目中的顺天圣母陈靖姑。　记者刘可耕 摄 \n</div>\n<div style=\"text-align:center\"> \n <img alt=\"开幕式上，陈靖姑文化发展推广中心授牌成立。　记者刘可耕 摄\" src=\"upload/image/393847b8-dcbb-4b9e-824e-562bb4e95682.jpg\" title=\"开幕式上，陈靖姑文化发展推广中心授牌成立。　记者刘可耕 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  开幕式上，陈靖姑文化发展推广中心授牌成立。　记者刘可耕 摄 \n</div>\n<div style=\"text-align:center\"> \n <img alt=\"台湾辅仁大学宗教研究所陈郁明硕士以学术视角解读陈靖姑文化与“海上丝绸之路”的紧密联系。　记者刘可耕 摄\" src=\"upload/image/8cae4210-481a-4213-b057-a6865122ea03.jpg\" title=\"台湾辅仁大学宗教研究所陈郁明硕士以学术视角解读陈靖姑文化与“海上丝绸之路”的紧密联系。　记者刘可耕 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  台湾辅仁大学宗教研究所陈郁明硕士以学术视角解读陈靖姑文化与“海上丝绸之路”的紧密联系。　记者刘可耕 摄 \n</div>\n<p>　　泰国道教会副理事长陈永智指出，泰国作为“海丝”沿线国家，自古就是华人华侨在东南亚的重要聚居地，华人华侨带去了中华文化，其中就包含陈靖姑文化在内的中国传统信俗文化。</p>\n<p>　　“在泰国曼谷有一座三奶宫，其中供奉的就是起源于福州的陈靖姑信仰，这份信仰已成为泰国华人华侨日常生活的一部分。”陈永智说。</p>\n<div style=\"text-align:center\"> \n <img alt=\"泰国道教会副理事长陈永智介绍他心目中的平安女神陈靖姑。　记者刘可耕 摄\" src=\"upload/image/a95562cc-ed6a-4c68-a357-c2532b5f721e.jpg\" title=\"泰国道教会副理事长陈永智介绍他心目中的平安女神陈靖姑。　记者刘可耕 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  泰国道教会副理事长陈永智介绍他心目中的平安女神陈靖姑。　记者刘可耕 摄 \n</div>\n<p>　　当天，陈靖姑文化与海上丝绸之路文化论坛同时举行，闽台文史专家、“海丝”沿线国家和地区来宾共同为陈靖姑信俗文化的发展探寻新路径。此次系列活动还包括陈靖姑文化与海上丝绸之路专题图片展、陈靖姑信俗文化科仪交流会等。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/a95562cc-ed6a-4c68-a357-c2532b5f721e.jpg',NULL,37,'2017-12-16 20:14:00','2017-12-17 01:15:31','75',0),(2946,'香港大学任命美籍华裔学者张翔为下任校长','谢萍','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>香港12月15日电 (记者 曾平)香港大学15日举行记者会公布，香港大学校务委员会通过决议，任命张翔为香港大学第十六任校长，接替即将于2018年1月离职的现任校长马斐森。</p>\n<p>　　张翔在校务委员会会议结束后的记者会上表示，对自己获任命为港大校长感到非常荣幸。他说，香港是个特别的地方，这里中西交汇，并且拥有巨大的潜力，香港大学是世界顶尖学府，相信港大未来可以更加卓越。</p>\n<p>　　他指，自己虽然没有在香港的工作经历，不过以往在学术工作方面与香港有联系。</p>\n<p>　　据悉，张翔当天在多个咨询会中分别与港大学生、教职员和校友会面，并出席了教委会和校委会的会议，阐述他对港大的愿景。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　张翔出生于中国南京，在南京完成本科学业后，赴美国取得研究生和博士学位，其后一直在美国工作。他是美国加州大学伯克利分校葛守仁基金讲座教授，他在隐形材料领域的研究曾被美国时代杂志评为“2008年十大科学发现”。</p>\n<p>　　香港大学校务委员会主席李国章表示，张翔是国际著名学者，具备卓越的学术成就和领导才能。校委会在全球范围内为港大遴选校长，张翔是遴选标准下的最佳人选。</p>\n<p>　　李国章指，张翔将全职投入港大校长的工作，港大与他的合约为期5年。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1043,'2017-12-15 21:51:00','2017-12-17 01:15:41','69',0),(2947,'香港青年社团负责人：凝聚青年力量 弘扬国家观念','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>北京12月15日电 (记者 张晓曦)多位香港青年社团负责人、青年工作者等15日在北京表示，要凝聚青年力量，让香港青年更好地认识国家、宪法与基本法等，积极融入国家发展大局。</p>\n<p>　　当日，“一国两制”实践中的青年思潮与青年工作主题沙龙在北京联合大学举行，邀请多位香港青年社团负责人、青年工作者代表及内地学者等参与。</p>\n<p>　　新时代香港青年协会主席、“敢言计划”召集人陈晓锋建议，要在香港培养更多青年舆论人才，发出正能量的声音，营造良好的社会氛围。他提到推动宪法和基本法走进中学校园、促进粤港澳大湾区青年交流、深化相关理论研究和专题研究工作等。</p>\n<p>　　陈晓锋认为，青年工作是一项长期工作，一个城市应重视青年发展，使城市成为青年的“心灵家园”。当下，香港青年在发展中遇到向上流动难等问题，相关社团也应帮助香港青年更好地了解、认识国家，融入国家发展大局。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　全国青联委员、香港特区青年事务委员会委员、青贤智汇创会主席谢晓虹结合自身经历谈到，青年应做到见贤思齐、明辨是非。作为一名香港“90后”，谢晓虹于2016年5月创办香港青贤智汇，希望能团结各界青年，认识国家，弘扬中华文化。</p>\n<p>　　谢晓虹指出，很多香港青年对国家、宪法、基本法等仍缺乏了解与正确认识。做好青年工作，要从教育入手，并运用新媒体等方式，讲述更多“一国两制”的真实故事，增加香港青年对国家的认同感。</p>\n<p>　　新时代香港青年协会副主席、媒体人刘瑶从香港青年与内地青年互动、社交媒体对热点新闻事件的传播等角度发表观点。</p>\n<p>　　北京航空航天大学法学院副教授、“一国两制”法律研究中心执行主任田飞龙表示，青年是香港的未来，香港青年应承担起未来“一国两制”在香港的发展，并学会解决“一国两制”在发展过程中出现的问题。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1674,'2017-12-16 01:16:00','2017-12-17 01:15:46','69',0),(2948,'沪苏浙皖共建世界级旅游目的地 联合开发境外市场','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>黄山12月16日电 (记者 张强)沪苏浙皖三省一市旅游部门16日在安徽黄山共同签署了《推进长三角区域旅游一体化发展2018年行动计划》。计划提出，加大对入境旅游市场的推广力度，沪苏浙皖共同组织赴“一带一路”沿线和欧美等主要国际客源地开展旅游宣传推广，开展旅游促销活动。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　同时，在上海国际旅交会等重要活动期间，联合组织长三角区域旅游合作主体推广活动，联合邀请境外旅行商、媒体开展长三角区域旅游合作线路考察和采风活动。</p>\n<p>　　当日，2017长三角旅游合作第七次联席会议在安徽黄山召开。此次会议以深化务实合作、扩大有效供给、加快建设具有世界竞争力和影响力的旅游目的地为主题，旨在进一步深化长三角旅游交流合作，加快推进长三角旅游一体化进程。</p>\n<p>　　近年来，沪苏浙皖不断完善长三角区域旅游合作机制，联合开发境外旅游市场，共建世界级旅游目的地。2016年，沪苏浙皖实现旅游总收入达2.72万亿元人民币，占中国旅游总收入的五分之一。</p>\n<p>　　上海市旅游局局长徐未晚介绍，自2011年以来，沪苏浙皖三省一市共同发布实施《苏浙沪皖一体化合作框架协议》、《苏州共识》、《杭州方案》等一系列区域旅游合作文件，在长三角区域旅游的产业共推、形象培育、市场共建、监管联动、规范设置等方面，取得了较为明显的成效。</p>\n<p>　　安徽省旅游发展委员会主任万以学说，安徽将把此次会议签署的《推进长三角区域旅游一体化发展2018年行动计划》落到实处，为共同打造世界级旅游目的地作出应有的贡献。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,200,'2017-12-16 18:47:00','2017-12-17 01:15:47','66',0),(2949,'两岸联手力作电影《纸飞机》斩获世界级电影节大奖','吉翔','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>杭州12月16日电 (见习记者 杨韵仪)美国时间12月13日晚，好莱坞国际电影节金影奖颁奖典礼在美国好莱坞举行，由杭台两地共同打造的儿童原创电影《纸飞机》斩获金影奖最佳外语片、最佳新晋女演员以及最佳摄影等多项大奖。</p>\n<p>　　美国好莱坞国际电影节是以好莱坞为心脏，为国际电影人才建立的多元文化交流平台，以促进电影行业发展、激发创造力、鼓励反思和探险为原则目标，为“杰出新人艺术家”和“有影响力的独立电影”提供广阔的展示平台。</p>\n<div style=\"text-align:center\"> \n <img alt=\"电影主创人员在电影节现场。 《纸飞机》剧组供图\" src=\"upload/image/6c061628-9cfd-4d87-b162-8830e2ea038c.jpg\" title=\"电影主创人员在电影节现场。 《纸飞机》剧组供图\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  电影主创人员在电影节现场。 《纸飞机》剧组供图 \n</div>\n<p>　　《纸飞机》聚焦时下备受关注的亲子教育话题，展示在孩子成长过程中，来自两岸的父母和孩子面临的共同困惑。影片中不同的故事最终指向同一个愿景――保护孩子美好健康的内心世界，理解孩子的梦想。</p>\n<p>　　《纸飞机》也是继《你好呀，自己》、《似水流年》等斩获国际大奖的原创影片之后，第三部杭台两地联手的力作。据悉，《纸飞机》将会在美国加州洛杉矶市的莫妮卡综合剧院进行展映，放映日期定于美国时间12月18日，12:00-18:00。</p>\n<p>　　美国时间12月14日，在组委会安排的新闻发布会上，CCTV加州办事处的记者们第一时间采访了该片制片人、杭州台湾同胞投资企业协会副会长林佳燕。林佳燕介绍，该片一大亮点就是剧中的小演员们，他们比起戏中的“爸爸”“妈妈”们，演技丝毫不逊色，连续多日的拍摄没有叫苦叫累，并且全情投入自己的角色，最终赢得了观众们的情感共鸣和认可。</p>\n<p>　　这也是林佳燕又一部成功策划发起并担当制片的两岸原创电影，她希望借由这部影片，与观众一起探讨有关儿童心理及亲子关系等正向的教育理念与模式。</p>\n<div style=\"text-align:center\"> \n <img alt=\"该片制片人与导演在电影节现场。 《纸飞机》剧组供图\" src=\"upload/image/5ce4668b-f710-446c-b1b0-e70e343748f3.jpg\" title=\"该片制片人与导演在电影节现场。 《纸飞机》剧组供图\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  该片制片人与导演在电影节现场。 《纸飞机》剧组供图 \n</div>\n<p>　　该片导演翟中梁透露，自今年7月份开始，共有来自45个国家和地区近300多部影片报名参赛2017美国好莱坞国际电影节，激烈角逐电影节最高荣誉“金影奖”，而《纸飞机》因其以全新视角讲述儿童成长与梦想的故事得到评委的一致好评，最终收获大奖。</p>\n<p>　　据悉，该电影由国务院台湾事务办公室新闻局、浙江省人民政府台湾事务办公室、杭州市人民政府台湾事务办公室、杭州文化广播电视集团支持与指导，杭州电视台少儿频道、杭州古一文化创意有限公司共同拍摄。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/5ce4668b-f710-446c-b1b0-e70e343748f3.jpg',NULL,168,'2017-12-16 09:56:00','2017-12-17 01:15:48','68',0),(2950,'西安美术学院作品展亮相中国美术馆','张楷欣','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>北京12月16日电 (记者 应妮)“时代精神――西安美术学院作品展”16日在中国美术馆开幕。</p>\n<p>　　西安美术学院是西北地区的一所高水平美术院校，在新中国美术发展进程中具有重要地位。自建院以来，充分发挥地缘优势，突出西部特色，立足红色的延安精神、厚重的历史承传、丰富的民间艺术和具有鲜明特色的长安画派与黄土画派“四大传统”，创作了诸多在中国当代美术史上具有深远影响的优秀作品。</p>\n<div style=\"text-align:center\"> \n <img alt=\"雕塑《丝绸之路》 钟欣 摄\" src=\"upload/image/0bc6d33a-d8ac-416f-88af-f6b619fd5d12.jpg\" title=\"雕塑《丝绸之路》 钟欣 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  雕塑《丝绸之路》 钟欣 摄 \n</div>\n<p>　　刘文西的《祖孙四代》、张之光的《春雷》、杨晓阳的《苦水社火》、郭线庐的《信息，开发人类智力的契机》、姜怡翔的《深谷生生》、谌北新的《小白船》、郭北平的《不尽黄河》、王胜利的《高原盛会》……一批从西安美术学院走出来的名家的一幅幅精品力作悉数亮相，让观者大饱眼福。</p>\n<p>　　此次展览以“时代精神”为主题，以长安气度、丝路精神、中国力量、国际视野为依托，分典藏、中国画、油画、版画、雕塑五大板块，共汇集173位艺术家的200件佳作，展现了关注当下、扎根生活，用对时代精神的凝练构建适应强国战略的文艺理念，其中既有老一代艺术家在国家重大历史题材创作和重要科研学术项目中完成的代表性美术作品，也有在历届全国美展以及重大美术活动和竞赛中获奖的精品，还有近年来中青年艺术家在各自专业领域里有突出建树的力作。</p>\n<div style=\"text-align:center\"> \n <img alt=\"经典作品汇集展览现场 钟欣 摄\" src=\"upload/image/6c27c440-6502-40b0-9951-996f09fbbcc4.jpg\" title=\"经典作品汇集展览现场 钟欣 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  经典作品汇集展览现场 钟欣 摄 \n</div>\n<p>　　西安美术学院院长郭线庐说，“这次展览是西安美术学院时隔25年向全国人民的‘汇报’。展出的作品可以用三个词来形容：认真、朴实、深入生活。”</p>\n<p>　　据悉，展览将持续至12月24日。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/6c27c440-6502-40b0-9951-996f09fbbcc4.jpg',NULL,1775,'2017-12-16 21:40:00','2017-12-17 01:15:48','75',0),(2951,'广东茂名首次半程马拉松开跑 盲人跑团跑完21公里','吉翔','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>茂名12月16日电 (记者 梁盛)16日，广东茂名首次马拉松在茂名市露天矿生态公园开跑。在3000多名跑友中，由7名本地盲人组成的“心shine”盲人跑团成了现场一道靓丽“风景”。他们在领跑员的带领下，用一条头巾，卷成一个8字形，最终都在规定时间内完成了21公里的半程马拉松项目。</p>\n<p>　　据了解，茂名首次马拉松共分半程马拉松和迷你马拉松两大项目，其中半程共有近600名跑友参加，迷你马拉松则吸引了2400多名爱好者报名。茂名市委书记李红军还到现场为跑友加油鼓劲。</p>\n<div style=\"text-align:center\"> \n <img alt=\"盲人跑友 梁盛 摄\" src=\"upload/image/ac5b80e4-1f63-4d02-bdc5-17c0bdaae335.jpg\" title=\"盲人跑友 梁盛 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  盲人跑友 梁盛 摄 \n</div>\n<p>　　“本身喜欢跑步，想用自己的脚去丈量这个世界，跑步让自己变得更加活跃、开朗和自信，以前都是在外地比赛，这次难得在自家门口，所以要抓住机会，也算是用自己的行动支持茂名申办省运会。”盲跑团团长王俊向记者坦露了参赛初衷。</p>\n<div style=\"text-align:center\"> \n <img alt=\"盲人跑团 梁盛 摄\" src=\"upload/image/d14347cb-52d5-4df2-9882-f7c5e45f6665.jpg\" title=\"盲人跑团 梁盛 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  盲人跑团 梁盛 摄 \n</div>\n<p>　　据悉，茂名市露天矿生态公园前身是茂名油页岩露天矿，曾为新中国摘掉“贫油国”帽子做出积极贡献，停产后周边生态严重恶化并且矛盾多发。后来茂名市委、市政府通过“引水、种树、建馆、修路”系统性生态修复工程，让曾经的城市“疤痕”变身为碧波荡漾的生态公园，成为市民休闲娱乐的好去处。此次马拉松以“生态”为主题，整个赛道线路环绕了生态公园“好心湖”，跑友可以一边跑步，一边欣赏风光优美，景色秀丽的沿岸风景。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/d14347cb-52d5-4df2-9882-f7c5e45f6665.jpg',NULL,1838,'2017-12-16 17:10:00','2017-12-17 01:15:48','74',0),(2952,'17岁学生画作《牛气冲天》获首届深圳手绘城市名片大赛金奖','王诗尧','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>深圳12月16日电(记者 郑小红)“第一届手绘城市名片・深圳青少年创意设计大赛”颁奖典礼暨巡展开幕式16日下午举行，深圳龙岗区东升学校17岁学生刘霜芸创作的《牛气冲天》斩获金奖。</p>\n<p>　　此次2017第十三届创意十二月“人人享受好设计”之“第一届手绘城市名片・深圳青少年创意设计大赛”，由深圳市文体旅游局主办、深圳市夏杨文化传媒有限公司承办、深圳市宣传文化事业发展专项基金赞助。</p>\n<div style=\"text-align:center\"> \n <img alt=\"孩子们眼中的深圳 钟欣 摄\" src=\"upload/image/b52c2bc0-9c8c-410c-a416-cbabc115c94b.jpg\" title=\"孩子们眼中的深圳 钟欣 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  孩子们眼中的深圳 钟欣 摄 \n</div>\n<p>　　大赛历时三个多月，共设18个奖项，分别为金奖1名、银奖2名、铜奖3名、最佳色彩应用奖1名、最佳创意奖1名、优秀奖10名。参赛作品作了前期筛选、初评筛选出优秀作品200余幅，最后由专业评委评定50幅入围作品，从50幅入围作品中分别评选出各大奖项。金奖被来自龙岗区东升学校学生刘霜芸创作的《牛气冲天》斩获，银奖分别被来自童画美术教育连锁机构小朋友庄伊婷的《未来的深圳》和来自龙岗区布吉阳光小学小朋友何睿锐的《云端之城》收入囊中，铜奖被顾轩睿的《天空之城》、傅天宇的《时代・变迁》、孙畅的《深圳2027》拿下。</p>\n<p>　　该大赛自2017年9月初立项以来，共发放参赛画纸1118幅，至12月5日截稿，有效回收728幅作品，回收率达66%。</p>\n<div style=\"text-align:center\"> \n <img alt=\"“第一届手绘城市名片・深圳青少年创意设计大赛”颁奖典礼 钟欣 摄\" src=\"upload/image/01268f9e-c8b3-4fd5-b74b-78d20de64a15.jpg\" title=\"“第一届手绘城市名片・深圳青少年创意设计大赛”颁奖典礼 钟欣 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  “第一届手绘城市名片・深圳青少年创意设计大赛”颁奖典礼 钟欣 摄 \n</div>\n<p>　　本次大赛参赛地域较为广泛，有少量来自惠州、东莞的作品，占比约为10%，其余作品均来自深圳青少年学生创作，参赛成员几乎覆盖了深圳的各个区。参赛选手中年龄最小的只有4岁，最大的17岁。本次大赛作品形式皆为手绘，所有作品均为原创。从内容和创意上，有以深圳特区建立时间为主线，对不同时期的标志性建筑和历史性重大事件组合的元素，也有以《未来的深圳》为题材的优秀创意作品。部分作品在构思和创作上，对未来深圳城市的交通、城市风貌、市民生活，从孩子的内心，做了大胆的遐思、探索、创意、设计。作品呈现紧扣创意十二月“新时代、新创意”设计主题。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/01268f9e-c8b3-4fd5-b74b-78d20de64a15.jpg',NULL,702,'2017-12-16 20:13:00','2017-12-17 01:15:48','75',0),(2953,'首届广东省马术联赛收官 粤港团体对抗赛广东队夺冠','张楷欣','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>广州12月16日电 (记者 郭军)15日至16日，广东省马术协会主办的首届广东省马术联赛收官之战――2017广东省马术俱乐部联赛香港赛马会杯广州总决赛，在广东省黄村体育训练中心国际马术场举行。</p>\n<div style=\"text-align:center\"> \n <img alt=\"80CM团体赛颁奖 郭军 摄\" src=\"upload/image/8277f9a5-de46-47d4-b8e5-7d21ae117b72.JPG\" title=\"80CM团体赛颁奖 郭军 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  80CM团体赛颁奖 郭军 摄 \n</div>\n<p>　　来自广州、深圳、惠州、东莞、佛山、珠海、中山等市的10余个代表队以及广东代表队、香港代表队共上百人马组合，经过两天的激烈角逐，于16日晚尘埃落定，惠州�嗫テ锸趸岢晌�最大赢家，压轴赛事“粤港团体对抗赛”则由广东代表队摘得桂冠。</p>\n<div style=\"text-align:center\"> \n <img alt=\"80CM团体赛 郭军 摄\" src=\"upload/image/ffca2f91-e3d7-4d6b-aff5-ecdf55f6e5ff.jpg\" title=\"80CM团体赛 郭军 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  80CM团体赛 郭军 摄 \n</div>\n<p>　　此次比赛分盛装舞步、场地障碍两大项，赛事吸引了众多家庭和马术爱好者从广东各地赶来观赛，有的专程从香港赶来。</p>\n<p>　　广东省马术协会会长许建平表示，2017广东省马术联赛是广东首次举办全省性的马术比赛。自10月14日在广州启幕以来，先后在惠州、东莞成功举行，受到了广大马术俱乐部和马术爱好者的广泛欢迎，此次由黄村体育训练中心承办的赛事是联赛的总决赛，由香港赛马会总冠名。</p>\n<div style=\"text-align:center\"> \n <img alt=\"120CM大师赛 郭军 摄\" src=\"upload/image/62343173-50ce-4580-a7c4-dbfc4a3fd693.jpg\" title=\"120CM大师赛 郭军 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  120CM大师赛 郭军 摄 \n</div>\n<p>　　作为此次总决赛的压轴赛事，粤港团体对抗赛由来自香港和广东的骑手代表组队对抗，备受关注。参加粤港团体对抗赛的香港代表队由邓北胜、李善侬、邓美旋、叶心宜共一男三女组成，赛场经验丰富，其中三人均有20余年以上骑乘经验，年龄最大的邓北胜已年过六旬；广东代表队由巴・巴音得勒革、赵华亮、陈敏聪、谭国史共四名男骑手组成，除了年龄最大的赵华亮有十余年骑乘经验，其他均是20出头的沙场小将。</p>\n<p>　　16日，最终凭借相对稳定的发挥，广东队骑手成功斩获冠军奖牌。</p>\n<p>　　颁奖典礼后，香港代表队领队表示，四位骑手对于能够被邀请到广东参赛感到非常高兴，期待明年能够再次与广东省马术联赛相约。</p>\n<p>　　据了解，香港赛马会一直大力支持内地的体育及马术运动发展。自1984年起，香港赛马会已赠送千多匹退役赛驹给各省、市代表队。香港赛马会市场及客户事务执行总监张之杰表示，乐见到来自香港、广东的优秀骑手在赛场切磋交流，以及越来越多的青少年骑手通过广东省马术联赛增进对马术运动的兴趣和技能。</p>\n<div style=\"text-align:center\"> \n <img alt=\"粤港团体对抗赛 郭军 摄\" src=\"upload/image/b95d8988-58d3-44ce-a965-50fbe25d1640.jpg\" title=\"粤港团体对抗赛 郭军 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  粤港团体对抗赛 郭军 摄 \n</div>\n<p>　　广东省马术协会常务副会长兼秘书长罗东斌表示，2018年第二届广东省马术联赛将安排在更多的城市和俱乐部举行。同时，会积极与香港方面进行协商，在条件允许的情况下，粤港马术赛场切磋，会前置到各个分站的赛事中，也有可能未来作为一项单独的赛事来打造。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/b95d8988-58d3-44ce-a965-50fbe25d1640.jpg',NULL,523,'2017-12-16 23:22:00','2017-12-17 01:15:49','74',0),(2954,'中国男乒近七年间首失头把交椅，是好事还是坏事？','张楷欣','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>北京12月17日电(张一凡) 德国名将奥恰洛夫在日前的国际乒联年终总决赛男单首轮中，鏖战七局险胜日本选手丹羽孝希，也提前宣告锁定明年1月国际乒联排名男单第一。自2011年4月王皓登上世界第一宝座后，在将近7年的时间内，国乒还从未将世界第一的宝座让与他国选手。此番奥恰洛夫登顶，对于国乒来说到底是福是祸？</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：奥恰洛夫提前锁定新一期男单世界第一。\" src=\"upload/image/601b14c9-5a9b-4ada-8610-e69ff9ae1ae9.jpg\" style=\"border:px solid #000000\" title=\"资料图：奥恰洛夫提前锁定新一期男单世界第一。\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  资料图：奥恰洛夫提前锁定新一期男单世界第一。 \n</div>\n<p>　　在垄断男乒世界第一长达80个月后，国乒终于交出了这一宝座。上一次出现外国运动员排名世界第一的情况，还是在2011年3月份，当时奥恰洛夫的同胞波尔曾登顶世界第一。此番再次丢掉世界第一的宝座，究其缘由不难发现，国际乒联新的排名规则和国乒队员今年的伤病情况或许是主要原因。</p>\n<p>　　国际乒联新的积分规则，是取选手一年内成绩最好的8站赛事来计算积分，较低级别赛事的分数有所增加。此外，奥运会和世锦赛的积分分别可以保持4年和2年，但是会逐年递减。简而言之，新的国际排名规则鼓励运动员多打比赛，哪怕是级别不高的比赛，只要取得好成绩，所获的积分也绝不会少。而近年取得好成绩的球员，需要不断参加新的赛事，否则就会失去自己的积分优势。</p>\n<p>　　国乒队员今年参加的比赛可以用“少而精”来概括。由于今年是奥运会后的调整年，国乒队员大都选择在今年选择调养自己的伤病。当前排名世界第一的马龙甚至因为参加的赛事不够，而失去了参加年终总决赛的资格。“藏獒”张继科今年也是打打停停，甚至在上一期的世界排名内积分被隐藏。而国乒众将在参加的比赛少，过往的冠军积分又因为新规不断衰减的情况下，丢掉世界第一宝座也就不难理解。</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：马龙丢掉世界第一宝座。 <a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 武俊杰 摄\" src=\"upload/image/368ad4e8-18bb-4ada-b205-babc769b5e3f.jpg\" style=\"border:px solid #000000\" title=\"资料图：马龙丢掉世界第一宝座。 <a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 武俊杰 摄\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  资料图：马龙丢掉世界第一宝座。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 武俊杰 摄 \n</div>\n<p>　　即将登顶的奥恰洛夫，在今年的表现相当亮眼。这位德国名将本赛季斩获世界杯男乒单打冠军，欧洲16强赛冠军，中国公开赛、德国公开赛两站钻石赛的冠军以及保加利亚、印度两站一般公开赛的冠军，可谓是风光无限。年终总决赛之前，奥恰洛夫仅需要闯过第一轮，就可锁定世界第一。而这一现象，对于国乒来说，或许并非是一件坏事。</p>\n<p>　　有强劲的对手，比赛才会更加精彩，乒乓球运动才会长盛不衰。出色的外籍球员频频出现，不但会刺激国乒队员们更加努力提高自己的水平以及状态，也会让世界大赛的冠军竞争更加多元化。这有利于世界范围内运动员技术水平的提升，也会对乒乓球这项运动的长期发展提供良性循环。因此，乒坛需要更多“奥恰洛夫”的出现。</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：国乒需要重新积累自己的优势。 <a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 李卿 摄\" src=\"upload/image/924c5cae-2bfe-457d-9199-96ca3b3e1f4f.jpg\" style=\"border:px solid #000000\" title=\"资料图：国乒需要重新积累自己的优势。 <a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 李卿 摄\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  资料图：国乒需要重新积累自己的优势。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 李卿 摄 \n</div>\n<p>　　在新版世界排名体系中，不仅马龙的世界第一排名被中断，樊振东、许昕等国家队名将的排名也都降低。此前中国队一向占据世界前四的位置，从而能够保证在世锦赛等大赛中每个四分之一半区都有中国队员。如果排名不够理想，那么在国际大赛的抽签中，国乒有可能会因为种子席位的减少，而提前出现“内战”的情况。</p>\n<p>　　这无疑给国乒众将敲响了警钟。从追求大赛成绩这一角度来说，过多的“内耗”将会极大的缩减国乒的统治力，未来他们或许将面临不小的挑战。而正在举行的年终总决赛，就已经暴露出这一隐患，中国队二号、三号主力樊振东、许昕因为世界排名相对不高，只能出现提前相遇的情况，早早开始内战。</p>\n<p>　　因此在未来的赛季中，国乒需要开始重新积累自己的积分优势。提高参赛数量、科学地计算积分、合理地安排选手等方面都是势在必行的。以国乒队员们过硬的实力来看，想要重新夺回世界第一的宝座，或许并非一件难事。但如果志在保持在乒坛长期的霸主地位，则需要在更多细节上追求精益求精，以此来面对更多强敌的冲击。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/924c5cae-2bfe-457d-9199-96ca3b3e1f4f.jpg',NULL,1286,'2017-12-17 01:16:00','2017-12-17 01:16:48','74',0),(2955,'英国伯明翰发生6车相撞事故 致6人死亡1人重伤','于晓','海外网','<p><strong>　　英国伯明翰6车相撞 致6人死亡1人重伤</strong></p>\n<p>　　海外网12月17日电<strong></strong>据英国广播公司报道，英国伯明翰17日凌晨1点10分左右发生一起严重车祸，6辆车相撞致6人死亡，1人重伤。</p>\n<p>　　救护人员称，第一辆车中两人受轻伤，第二辆车中司机与女乘客当场死亡，男乘客送达医院后抢救无效死亡，第三辆车中三人当场死亡，一人重伤。其余车中人员受轻伤。</p>\n<p>　　目前，事故发生道路依然处于关闭中。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>',NULL,NULL,1497,'2017-12-17 18:38:00','2017-12-17 19:18:21','71',0),(2956,'每日优鲜荣膺2017“年度高成长”公司','陈海峰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月15日电 12月13日，36氪在“基业长新――WISE2017新商业峰会”上发布了2017“新商业100年度榜”之2017年度高成长公司榜单，这是一份挤掉估值水分、并根据各个公司在2017年度真实增长情况来核算其成长性的权威榜单。作为国内生鲜电商领军企业，每日优鲜获组委会评委一致认可，经专家组评选荣获2017“年度高成长”公司。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　据了解，该榜单入围公司全部聚焦于B轮后企业，36氪按照融资次数、市场份额增长、收入增长、利润增长、用户量或客户量增长、日活增长等数据分别进行排名统计，并引入36氪鲸准、36氪研究院对一级市场各项数据进行佐证，经过层层筛选，每日优鲜荣登榜单。主办方在给每日优鲜的获奖理由中写到：在北京这样的一线城市，每十户就有一户在使用他们提供的服务，而在保持主营业务竞争力之余，每日优鲜也在今年积极探索新风口形态。</p>\n<p>　　公开资料显示，短短三年时间，每日优鲜发展速度非常迅猛，2016年，每日优鲜年对年营收增长达527%，2017年3月，每日优鲜完成C+轮2.3亿美元融资，2017年8月宣布在全国一线城市实现整体盈利， 而且80%的复购率和663.9%的用户规模增速等代表生鲜电商行业竞争力的关键数据均保持行业领先水平。</p>\n<p>　　除此之外，今年6月每日优鲜新推出的无人货架便利购业务也备受行业和媒体关注。记者从每日优鲜了解到，不到半年时间，依托于每日优鲜全国1000多个全温区前置仓以及全品类精选商品的供应链基础设施，每日优鲜便利购已在北京、天津、上海、深圳、杭州等10个城市完成8000个高品质(百人以上)点位拓展，并计划在不久的将来快速铺设10万点位。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1045,'2017-12-15 15:56:00','2017-12-17 19:18:21','82',0),(2957,'上节目不按常理出牌 袁立事件捅破多少“秘密”？','魏巍','扬子晚报','<p><strong>　　上《演员的诞生》不按常理出牌</strong></p>\n<p><strong>　　袁立事件，捅破了多少“秘密”？</strong></p>\n<p>　　周六晚，热议不断的浙江卫视《演员的诞生》迎来了挑战赛的最后一战。节目中，两位经验丰富的演员冯雷、于毅进行终极演技对决，向经典剧目《追龙》致敬。值得一提的是，在接下来的新赛事开启之前，节目组还安排了一个特殊的环节，即导师与二十余位演员来了一场庄严又神圣的宣赛仪式：“我们将永远铭记，不忘初心，薪火相传，我是演员。”――这个场面似乎是在传达大家对于表演的敬畏和信念，但回望节目从开播到现在的各种“事故”，尤其是因为袁立这样一个不按常理出牌的演员的存在，让该节目正处于一个尴尬的现状。</p>\n<p>　　扬子晚报记者 张漪</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　袁立捅破了什么马蜂窝？</p>\n<p>　　《演员的诞生》自开演以来，几乎每个上台的人都引发了网络热门话题。从开始的郑爽、欧阳娜娜在舞台上演技差强人意，惹得表演导师们各种表情嫌弃和惊愕，再到老戏骨陈龙、郑昊等人被淘汰，再到黄圣依把悲剧演成喜剧……而袁立这期却揭开了节目组台前幕后的众多“秘密”。</p>\n<p>　　事情的起因是袁立在节目中被淘汰，败给了导师席上刘烨、章子怡的同窗班长张彤。俩人表演了宋丹丹的经典小品片段《超生游击队》，表演本身是见仁见智，但从播出的节目来看，袁立在表演后的说话态度表现，似乎都有失风度，被很多网友形容“像个神经病”。而恰是这类评判惹怒了本来觉得受到委屈的袁立，她立即“揭竿而起”，将幕后的一些“真相”用微信对话截图的方式进行了充分曝光。该事件持续数天，脉络大约是：袁立指节目小编导在邀请她上节目时，即承诺她可以晋级，但节目录制时却因某评委临时弃票而遭淘汰。小编导随即出面承认此事自己处理不妥，并宣布辞职谢罪；而《演员的诞生》节目组及相当多的网友也不是吃软饭的，他们反击袁立想红炒作，拿了80万出场费还黑心等等，这彻底将袁立激怒了，她开始“复盘”整个录制情况，指节目组在剪辑时将彩排、录制的顺序打乱，故意在她遭淘汰时剪进了一些根本不是当下的画面，使得她看起来像个“神经病”。</p>\n<p>　　原来真人秀节目结局早就写好了？</p>\n<p>　　袁立的爆料像是来了一个大起底，让很多并不熟悉电视节目运作的观众和普通网友们大吃一惊，他们惊呼：原来，这种真人秀节目，也是按戏的“剧本”来演的啊？淘汰谁、不淘汰谁，跟当天表演者的状态毫无关系，是早就写好的“结局”啊？！</p>\n<p>　　这一点在袁立和小编导的微信对话截图中很明显地得到了印证，几位导师该投谁的票，早在录制前，节目组便似乎与他们建立了默契。然而，从小编导说的微信来看，本次“事故”是因为宋丹丹虽按“规定”投了袁立的票，但在说话解释自己投给谁时，却临时起意说自己投给了张彤，这使得本来应该晋级的袁立，遭到了淘汰。此事，多少让人看出这几个“秘密”，第一，“赛果”在一些电视节目中，是被“预定”好的。第二，明星们不论是嘉宾还是导师，都因为平时备受宠爱，而不受控制。这个事件中，宋丹丹没有得到有效“控制”，袁立更没有被“搞定”。因此，引发了一场戳穿“皇帝新衣”的事件。</p>\n<p>　　原来有的演员有酬劳有的没有？</p>\n<p>　　袁立的这场抗争中，80万元的酬劳也成为焦点之一。然而，袁立却很快得到了更多大V和公知的支持，原因是什么？是因为她拿到这笔酬劳后，直接将这笔钱捐给了她平时一直在做的关注尘肺病农民的公益基金。甚至还有多位非常有影响力的大号，发表文章，力挺袁立，他们的观点是，袁立为真实而战，是值得尊重的，而更让人们尊重袁立的是，她的身后还站着她为之关注的600万尘肺病人。</p>\n<p>　　早前一期节目受到抨击的演员郑昊也坐不住了，他也通过微博发布了一些“真相”。郑昊那一期表演时曾被指演技不如新人，此次微博他指出了原因，首先，节目组给他的剧本就是如此，其次，节目组没有时间给他彩排。另外，郑昊也一句话带过，本次节目他不仅一分钱酬劳没有，还自掏腰包前去录节目。此事，让看客们纷纷为他打抱不平，网友们说，原来这个节目有的人有酬劳，有的人没有啊？</p>\n<p>　　原来表演者没来导师们也给好评？</p>\n<p>　　袁立事件发酵出的“真相”还不止郑昊这一桩，还有现场观众曝出欧阳娜娜那天被录制打分时，根本没有到现场。是节目组的剪辑师再次“妙手回春”将刘烨与欧阳娜娜“隔空”放在一个画面里，拼成了一组“戏”。事情最开始是因为郑昊指责当天没有给出彩排的时间，但为什么没有彩排？原因可能是因为与他演对手戏的欧阳娜娜没有彩排的时间。网上观众的爆料显示，连当天章子怡、刘烨们点评表演的整个场面，欧阳娜娜也没有在。那么，这是如何完成了一台仿佛大家都在场的戏的呢？原来依旧归功于“剪刀手”剪辑师。不过，有细心的大V仍旧从一两个画面中，发现了欧阳娜娜不在现场的真相。此事件更让《演员的诞生》节目话题沸腾。很多年轻观众表示，“原来电视节目都是‘骗’人的”。</p>\n<p>　　听听业内电视专家是如何看待这些的</p>\n<p>　　《演员的诞生》带来的“真假争议”、“酬劳高低”、“档期调控”等等争议，其实并非孤立存在。昨天扬子晚报记者采访了多位电视专家和运作人，他们表示，目前每一档真人秀节目事实上都不同程度地存在着同类问题。一位电视人告诉记者，中国真人秀节目大多模仿的是韩国节目，同时我们也引进了韩国真人秀节目中存在“编剧”这一职位的理念。不过，这个“编剧”与编电视故事的编剧有着本质区别，他们一般来说，是根据节目明星嘉宾真实的性格，来给节目设定“话题线”。此外，明星嘉宾在真人秀节目中的“酬劳”更应该受到相关部门的关注。据记者了解，袁立每期节目80万酬劳在明星中只能算是中下等，而中上等的则要在100-300万一期，甚至更多。重要的人物一整季(13期)节目下来，几千万酬劳不在话下。所以，你可以看到，几乎所有大牌明星全都“下场”做起了综艺。有电视人表示，袁立事件引发的观众“惊讶点”其实在业内看来司空见惯，从某种角度说，这些事件都是“常态”，在收视率成为考核一切标准的前提下，编导们将真人秀内容做的“悚动”一些，可以理解。还是那句老话，收视率是万恶之源。</p>\n<p>　　我们一般都做不了袁立</p>\n<p>　　这10年来，我们这个变化很快的社会持续热门的词其实并不多，但“潜规则”这个名词却十年不减热度。袁立这个事件，事实揭开的就是电视真人秀以及综艺节目的“潜规则”。“潜规则”虽普遍存在，但起码暂时还放不到台面上来说。很多人在此事件的初始阶段，认为袁立是“输不起”或者是“受不了委屈”，就出来“闹”。从人性角度看，这种原因也不是不存在，但是当整个事件变成了袁立为“真相而战”后，很多网友包括有思考能力的人都站在了袁立这一边，至少她“真实”“勇敢”。我们这个社会，掩人耳目、欺负老实人的假象和潜规则太多了，大多数人为了不惹事、不折腾而委曲求全、被迫低头，我们一般都做不了“袁立”，因此当这样的人出现，哪怕稍占一点理，都会引发强烈舆论。 张漪</p>\n<p>　　有点明白为什么烂剧横行了</p>\n<p>　　综艺节目肯定是要吸引眼球的，这样才有流量，可是这个节目玩过火了，从第一集就开始混战开撕，到最近越演越烈，袁立成了最后一根稻草，轩然大波使得网络社会最角落的人都看到这样的信息在刷屏。很多人说这个节目简直是“戏精的诞生”，怎么说呢，其中谁坑了谁，谁占理，谁无理取闹，对吃瓜群众来说，都是闹剧，这出台下的好戏是挺好看的，甚至很有“演技”。其实最近有个观点说得特别好，演员呢，有些人本身自带某种特质，就适合某种戏路，或者长年累月的积累后，形成了某种特质，没有哪个演员是万金油，“有什么好比的呢，我们又不是打拳的”，说白了，这不是竞技比赛。不过话说回来，热闹是好看，但这出闹剧的背后也让普通观众心寒，毕竟这就是现在烂剧横行的原因。只希望这节目也能起到另一个作用，对演员们有所促进和启发吧，把能力放在演技的学习和锤炼上吧。 孔小平</p>\n<p>　　如何证明自己不是疯子</p>\n<p>　　几年前我曾电话采访一位大腕儿主持人，该主持人非常礼貌地拒绝了，表示只接受电视镜头采访，否则无法控制他的话会被写成什么样子。这份小心和低调我是非常理解的，但现在看来镜头也是靠不住的。在你的声音和你的样子这些“铁证”面前，证明自己不是疯子这件事到底有多难？袁立经历了一出现实版的“飞越疯人院”。说实话，光学成像是物理的技术的，但剪切编辑是主观的伦理的。想想怪可怕的，只要发挥一点点加工，那章子怡和刘烨，一会儿拍桌子瞪眼睛扔鞋子，一会儿又哈哈大笑抱头和好，剪成疯子也不是做不到吧？还有欧阳娜娜黄圣依，想剪成多疯都没问题吧？</p>\n<p>　　至于节目的真假，说实话，我不清楚我不判断我也不在乎。毕竟我们谁能准确证明自己不是疯的呢。 张艳</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,936,'2017-12-17 14:34:00','2017-12-17 19:18:21','68',0),(2958,'高校极客挑战节目《极客出发》第二季开启','罗攀','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月17日电 大型全球高校极客挑战节目《极客出发》第二季12月17日(本周日)晚19:30央视财经频道震撼来袭，武汉大学与汉堡大学在“人脸识别”项目中一比高下，究竟是汉堡大学的“机器”识别快，还是武汉大学的“人眼”看得准？这场充满火药味的高智商较量，让极客观察员撒贝宁连连惊叹。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　《极客出发》作为国内科技类节目的先行者，第一季节目播出期间，《极客出发》就以独特的题材选择和世界前沿的科技元素让观众大饱眼福，收视口碑双丰收，百度搜索指数直线上升，微博电视指数每期上榜，在年轻互联网受众中影响力巨大。</p>\n<p>　　《极客出发》第二季经过整整十个月的策划和打磨，聚焦人与人工智能的结合，突破一个个生物极限，实现人类进化的2.0版本。每期节目一个黑科技，两所高校，三项挑战。无人车、人脸识别、隐身衣、脑控等来自中国、美国、德国、荷兰、以色列等创新高地的12个科技前沿的优秀项目，超过40%的项目首次在国内外电视综艺节目中亮相，哈佛、麻省理工、清华大学复旦大学、耶鲁大学、斯坦福大学、新加坡国立大学等等……来自全球24所顶级高校的青年挑战者，展现顶尖学子风采，通过他们的实践操作和探索分析观察，让观众切实感受到前沿科技项目与人相结合所带来的超能力。</p>\n<p>　　人脑推进科技发展，科技发展应用于人类实际生活，人机结合，才能发挥出人脑与科技的最大魅力。极客出发，改变世界。本周日晚19:30，关注央视财经频道大型全球高校挑战节目《极客出发》第二季，一起感受“人脸识别”技术的发展。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1915,'2017-12-17 01:21:00','2017-12-17 19:18:21','68',0),(2959,'澳门第十三届全国人大代表选举会议举行第二次全体会议 王晨出席并讲话','于晓','新华网','<p>　　新华社澳门12月17日电(记者 郭鑫、王晨曦)澳门特别行政区第十三届全国人大代表选举会议17日在澳门举行第二次全体会议，投票选举澳门第十三届全国人大代表。全国人大常委会副委员长兼秘书长王晨出席会议并讲话。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　选举会议主席团常务主席崔世安主持会议。出席会议的466位选举会议成员以无记名投票方式，选出12名澳门第十三届全国人大代表，并根据选举办法的规定，确定了3名得票数不少于选票三分之一的未当选为代表的候选人在代表因故出缺时的递补顺序。</p>\n<p>　　选举结束后，王晨讲话指出，澳门第十三届全国人大代表选举工作严格依法办事，充分发扬民主，是一次公开、公平、公正的选举。各位选举会议成员以国家主人翁的姿态，珍视参与国家事务的民主权利，心系国家根本利益和澳门同胞福祉，积极参与选举会议工作，圆满完成各项任务。他代表全国人大常委会和张德江委员长向新当选的全国人大代表表示热烈祝贺，并向选举会议成员表示衷心感谢。他说，相信新一届澳门全国人大代表，一定会秉承爱国爱澳的光荣传统，拥护并模范地遵守宪法和澳门特别行政区基本法，拥护“一国两制”方针政策，效忠中华人民共和国和澳门特别行政区，认真履行代表职责，为祖国繁荣昌盛和澳门长期繁荣稳定作出积极贡献。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,606,'2017-12-17 17:30:00','2017-12-17 19:18:21','69',0),(2960,'多名台籍专家获聘福建平潭科技研究院','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>平潭12月17日电 (记者 龙敏)福建平潭科技研究院17日在平潭揭牌成立，数十名海内外高层次人才获聘成为研究人员，其中包括台湾逢甲大学教授陈金发等多名台籍专家。</p>\n<p>　　平潭科技研究院为平潭综合实验区直属事业单位，由福建农林大学和平潭综合实验区合作共建，是平潭与国内外科研院校开展合作的综合平台和共享基地。</p>\n<p>　　平潭综合实验区管委会主任林文耀表示，平潭正加快建设国际旅游岛，将借助平潭科技研究院的平台继续开展对台合作与国际合作；进一步推动各种学术机构、学术会议、科技成果落地平潭，促进平潭成为海峡两岸乃至国际人才交流、学术探讨、科研合作的汇聚中心。</p>\n<p>　　林文耀认为，平潭科技研究院将有力提高平潭的创新能力，加大科技研发对平潭发展的支撑，推动科技成果的转化，促进平潭经济发展的科技含量。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　福建农林大学校长兰思仁说，平潭科技研究院将引进海内外以及台湾优质的教育和科技人才资源，大力发展研究生教育，成为海洋生物科技、海岛现代农业等区域经济发展和对接两岸现代农业与生物技术产业急需高层次人才的培养基地。</p>\n<p>　　兰思仁指出，平潭科技研究院将根据实际需要与合作单位共建分院，集中建设多个分院共享基础和公用设施，规划通过5年建设，成为集产学研一体的科研与人才培养基地。</p>\n<p>　　当天，中国工程院院士尹伟伦获聘成为平潭科技研究院学术委员会主任，中国科学院院士谢联辉、中国千人计划专家林辰涛获聘为副主任，陈金发、厦门大学教授林德荣、长江学者明瑞光等获聘为委员，台湾树之王绿化植物开发公司董事长罗能业等数十名专家获聘为专家团队成员。</p>\n<p>　　陈金发受访时表示，台湾与平潭地理位置相近气候条件相似，在海岛生态建设方面存在巨大的合作机遇。两岸研究人士在平潭开展共同研究攻关，将推动平潭生态发展规划与管理更加有序，助推国际旅游岛建设提速。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1894,'2017-12-17 19:03:00','2017-12-17 19:18:21','70',0),(2961,'民革十二届六中全会在北京召开','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>北京12月17日电 (记者 路梅)中国国民党革命委员会第十二届中央委员会第六次全体会议17日在北京召开。</p>\n<p>　　本次会议的主要内容是学习贯彻中共十九大精神，听取和审议民革第十二届中央常务委员会工作报告和中央监督委员会工作报告，审定提交第十三次全国代表大会的有关文件及事项。民革中央主席万鄂湘出席会议并代表民革十二届中央常务委员会作工作报告。</p>\n<p>　　万鄂湘回顾了2017年民革的工作。他指出，民革中央积极参与政治协商，精心组织专题调研，努力拓展参政议政领域，稳步提升信息工作水平，参政议政工作成效显著。2017年，聚焦国家经济社会发展全局性、战略性和前瞻性问题，结合民革参政议政优势和特色，围绕21个课题开展调研考察36次，形成调研成果21项，向中共中央、国务院报送书面建议15篇，得到了中央的高度重视，相关部委对有关建议进行了认真研究落实。</p>\n<p>　　万鄂湘说，民革中央扎实开展脱贫攻坚民主监督，深入拓展精准扶贫工作领域，着力强化社会服务品牌，社会服务工作持续推进。多次开展集中调研，积极探索破解深度贫困的对策，针对产业脱贫、精神脱贫等提出意见建议。民革全党坚决贯彻执行中共中央对台大政方针和决策部署，旗帜鲜明反对“台独”，关注台湾青年群体，做好台情研究工作，促进两岸交流交往，为推动两岸关系和平发展作出积极贡献。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　万鄂湘强调，即将召开的民革十三大是民革政治生活中的一件大事，也是中国共产党领导的多党合作事业中的一件大事。民革全党要更加紧密地团结在以习近平同志为核心的中共中央周围，认真学习宣传贯彻中共十九大精神，在习近平新时代中国特色社会主义思想的指引下，继承和发扬孙中山爱国、革命、不断进步精神，全面履行好参政党的各项职能，为决胜全面建成小康社会、夺取新时代中国特色社会主义伟大胜利、实现中华民族伟大复兴的中国梦、实现人民对美好生活的向往而努力奋斗。</p>\n<p>　　据悉，为期2天的民革十二届六中全会还将审议第十二届中央委员会向第十三次全国代表大会作的工作报告，审议《中国国民党革命委员会章程(修正案)》(草案)和相关说明，审议通过民革第十三次全国代表大会主席团、秘书长建议名单，民革第十三次全国代表大会代表资格审查委员会名单和民革第十二届中央委员会第六次全体会议决议等。</p>\n<p>　　民革中央常务副主席齐续春主持开幕会。民革中央原主席何鲁丽、周铁农，民革中央副主席修福金、刘凡、傅惠民、田惠光、郑建邦、邓力平、刘家强、李惠东，民革中央原副主席李赣骝、朱培康、谢克昌、何丕洁，民革中央顾问和民革中央委员等出席会议。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1399,'2017-12-18 10:44:00','2017-12-17 19:18:21','64',0),(2962,'华威乳业公司因伪造材料等问题被立案调查','魏巍','新华网','<p><strong>　　华威乳业公司因伪造材料等问题被立案调查</strong></p>\n<p>　　新华社北京12月17日电(记者 陈聪)国家食品药品监督管理总局日前发布对黑龙江农垦英博华威乳业有限公司进行食品安全生产规范体系检查的相关信息，指出该公司因伪造材料、提供虚假证言、使用同一配方生产不同品牌婴幼儿配方乳粉等问题被立案调查。</p>\n<p>　　食药监总局指出，该企业有未召回不合格产品的情况。在今年3月的监督抽检中，食药监总局发现该企业1批次彩贝乐婴儿配方乳粉(1段，生产日期2016年12月6日)标签营养成分表中铜、镁单位标识错误，其中铜的单位“μg/kg”错误标示为“mg/kg”，镁的单位“mg/kg”错误标示为“μg/kg”。此次检查发现，该批次不合格产品实际未召回；包材库中库存的营养标签标识错误的彩贝乐婴儿配方奶粉铁听(840个，规格880g)未做不合格品标识，仍然存在混用的风险。</p>\n<p>　　此外，在检查中发现，彩贝乐婴儿配方乳粉(规格880g，生产日期20160316、20161206)、彩贝乐较大婴儿配方乳粉(规格880g，生产日期20160313、20161220)、彩贝乐幼儿配方乳粉(规格880g，生产日期20160315、20161215、20161225、20170103)8个批次产品标签营养成分表中铜、镁单位标识存在同样错误，但未进行追溯及采取相应的处置措施。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　食药监总局指出，该企业存在毁灭生产相关的原始记录并伪造材料，藏匿留样，提供虚假证言情况。企业有2种100g听装产品属于同一企业以同一配方生产不同品种婴幼儿配方乳粉；有7种100g听装产品属于食品生产许可证副本载明的同一食品类别内的事项发生变化未按规定报告。</p>\n<p>　　黑龙江省食品药品监督管理局已责令该企业停产整改，并对其伪造材料、提供虚假证言、使用同一配方生产不同品牌婴幼儿配方乳粉等行为进行立案调查。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1206,'2017-12-17 16:28:00','2017-12-17 19:18:21','66',0),(2963,'评论：对“奇葩险”监管不能当吃瓜群众','魏巍','法制日报','<p><strong>　　对“奇葩险”监管不能当吃瓜群众</strong></p>\n<p>　　不少人把买低价“奇葩险种”当成一种互联网游戏，而不是一种真实意义上的投资行为。而且，不少互联网“奇葩险种”满足了当下客户碎片化的保险需求</p>\n<p>　　□　张立美</p>\n<p>　　不久前，某明星公布了恋爱消息，有商家开始销售该明星“恋爱险”，每单保费11.11元，承诺如一年后他仍保持恋爱关系，商家便支付双倍金额，不少粉丝竟然投保。如今，“奇葩险种”层出不穷：贴条险、熊孩子惹祸险、扶老人被讹险、忘穿秋裤险、美厨娘关爱险、肠胃险、痘痘险……险种之多之奇，只有想不到，没有做不到。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　近年来，随着移动互联网时代的到来，保险业界脑洞大开，各种“奇葩险种”以产品创新之名层出不穷，覆盖到人们生活的方方面面。毫不夸张地说，凡是生活里可能发生的风险，都有可能被设计成一款保险产品。</p>\n<p>　　纵观这些互联网“奇葩险种”，基本上是以营销噱头博取大众眼球，脱离了保险产品“给予保障”的基本宗旨，不具有保险的本质功能，与保险精神不符，更像是一种对赌游戏。有的“奇葩险种”还有悖社会公序良俗，变相鼓励违法犯罪。一些销售“奇葩险种”的商家或平台，压根儿就不具有经营保险业务资质，属于非法经营保险业务行为。不少“奇葩险种”还设置了消费陷阱，存在很大的消费风险。互联网“奇葩险种”一键投保很容易，但一键理赔很难，其拒赔率相当高。</p>\n<p>　　尽管如此，这些“奇葩险种”火爆互联网，销量相当可观。其热销的直接原因在于不少消费者对保险业缺乏专业知识和能力，容易被迷惑，更重要的是，互联网上的这些“奇葩险种”价格便宜，最低的保费只需要1块钱，很多人能买得起。这使得不少人把买低价“奇葩险种”当成一种互联网游戏，而不是一种真实意义上的投资行为。而且，不少互联网“奇葩险种”满足了当下客户碎片化的保险需求。</p>\n<p>　　发展互联网保险是保险行业的一个趋势，但是互联网保险产品的创新，必须坚守保险功能的底线，必须坚守“保险姓保”的行业价值根基，不能放任乃至放纵打着产品创新的旗号进行恶意营销。因此，要还互联网保险一片“净土”，保护消费者合法权益，为互联网保险业健康良性发展创造良好环境和预留出空间，就必须对互联网“奇葩险种”说不。</p>\n<p>　　一方面，要加强国民金融知识水平的普及和教育，提高消费者对保险的认知能力。另一方面，监管不能当互联网“奇葩险种”的吃瓜群众，必须给互联网保险戴上一顶“紧箍”，并对互联网“奇葩险种”常念紧箍咒。应当进一步完善保险法等法律法规，有必要将《财产保险公司保险产品开发指引》的相关规定上升为法律法规，既为互联网保险产品创新保驾护航，也为监管互联网“奇葩险种”提供法律依据和执法标准。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1388,'2017-12-17 09:01:00','2017-12-17 19:18:21','67',0),(2964,'广东纪委通报4起违反中央八项规定精神典型问题','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>广州12月17日电 (索有为 粤纪宣)广东省各级纪检监察机关今年以来把落实中央八项规定精神、纠正“四风”摆在更加突出位置，及时发现和严肃查处了一批违规违纪问题。元旦、春节将至，为加强警示教育，营造风清气正的节日氛围，中共广东省纪委17日通报其中4起典型问题。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　广州岭南国际企业集团有限公司副总经理李峰违规公款宴请等问题。2014年至2016年期间，李峰违反中共中央办公厅、国务院办公厅印发的《党政机关国内公务接待管理规定》中“不得提供高档酒水”的规定，先后在其组织的3次公务接待中使用高档白酒，合计5瓶共1.71万元。此外，李峰在兼任广州市东方宾馆股份有限公司党委书记期间，对该公司违规用公款购买月饼券送礼、违规公款接待等问题负主要领导责任。2017年9月，广州市纪委给予李峰党内严重警告处分。违纪款被收缴。</p>\n<p>　　广东省价格成本调查队违规公款旅游问题。2014年11-12月期间，广东省价格成本调查队分别由队长戴勇明、副队长杨晓程带队，组织前往山西、陕西、四川等地，开展为期7天的学习考察，两个考察组分别仅用半天时间与当地有关部门座谈交流，其余时间均安排参观当地旅游景点，共计花费7.62万元。2017年2月，省直机关纪工委给予戴勇明党内警告处分；2016年12月，省发展改革委给予杨晓程行政警告处分。违纪款被收缴。</p>\n<p>　　汕头市档案局两名党组成员违规收受礼金等问题。2014年至2016年，该局党组成员、副局长林伟雄、陈建功在每年春节前违规收受本单位业务外包公司所送礼金，分别合计为9万元、6万元。此外，两人还违规领取该公司发放的各类补贴分别为2.4万元、1.8万元。2017年3月，汕头市纪委、监察局对林伟雄、陈建功分别给予撤销党内职务、降级处分。违纪款被收缴。</p>\n<p>　　梅州市丰顺县国土资源局违规发放补助、私车公养问题。2013年至2015年期间，该局以发放“服装费”名义，先后3次违规用公款向干部职工发放补助共计76.23万元。此外，由时任党组书记、局长刘勤奋提议，经局党组班子成员同意，违规用公款42.63万元购买49张加油卡，供局党组成员和财会人员共15人的私家车加油使用。2017年11月，梅州市纪委对该局党组书记、局长刘勤奋和局党组成员张环琪、刘天生分别给予党内严重警告处分，另对其他6名党组成员分别给予党内警告处分。违纪款被收缴。</p>\n<p>　　广东省纪委称，纠正“四风”问题没有间歇期，改进作风没有休止符。各级纪检监察机关要进一步提高政治站位和政治自觉，强化监督执纪问责，坚持无禁区、全覆盖、零容忍，紧盯元旦、春节等时间节点，严肃查处违反中央八项规定精神问题，保持正风肃纪高压态势，坚决防止“四风”问题反弹回潮。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1534,'2017-12-17 17:38:00','2017-12-17 19:18:21','64',0),(2965,'百余位专家学者云集长沙 探讨世界高速铁路发展','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>长沙12月17日电 (王建湘 王轩)2017高速铁路国际研讨会17日在湖南长沙召开，吸引了海内外40家单位、21位院士、220余位专家学者与会，共同探讨世界高速铁路的发展。</p>\n<p>　　此次高速铁路国际研讨会以高铁规划设计与建造技术与模式、高铁运营管理与服务新技术、高铁安全保障技术、高铁可持续技术、高铁能源材料新技术、高铁载运装备新技术、铁路智能运输系统技术、综合交通体系中的高铁为主题。</p>\n<p>　　专家们以凝聚、交流、互鉴、共享、共进为目的，深入研讨高铁可持续发展规划、建设、制造、运营、管理和服务中面临的技术问题，问诊世界高速铁路的发展现状，把脉未来高速铁路发展的趋势与技术创新，积极促进国际交流平台建设，促进世界高速铁路的创新引领和长足发展。</p>\n<p>　　近五年来，中国高速铁路建设为世界范围内的轨道交通技术发展、产业格局调整贡献了“中国特色”和“中国标准”，特别是2017年9月21日，具有完全自主知识产权的中国标准动车组“复兴号”，在京沪高速铁路实现按时速350公里运行的商业运营，标志着中国铁路成套技术装备特别是高速动车组已经走在世界先进水平行列。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　院士与专家的36份报告，从土木工程、交通工程、车辆工程、粉末冶金等多个学科交叉的视角充分探讨了中国与世界高速铁路的发展趋势，各种新锐观点的碰撞，提出了中国智慧高铁的发展理念和思路，为创新引领高速铁路发展，进一步加快高速铁路建造技术和服务管理技术创新，形成新的经济增长极提出了对策良方。</p>\n<p>　　中国工程院院士孙永福从中国铁路发展的“走出去”战略机遇与挑战出发，提出了在坚持“五大发展理念”的视角和“一带一路”建设下中国铁路发展坚持“走出去”战略的指导思想、基本原则和战略目标。</p>\n<p>　　美国圣母大学AhanKareem院士从复杂多变天气环境下的结构和车辆影响出发，建立了结构和车辆非静态负荷效应的分析建模工具，引领高速铁路建造技术创新。</p>\n<p>　　据悉，中国高速铁路的快速发展带来了世界范围轨道交通的复兴与技术产业格局的重大调整，形成了一系列“中国特色”和“中国标准”，同时也面临高速铁路建造技术持续创新、保持可持续竞争力的严峻挑战。此次高速铁路国际研讨会，深度问诊高速铁路发展状况，并坚持在响应国家“一带一路”倡议下，连贯东西、沟通内外，达成各项共识，提出建设智慧高速铁路的创新理念和思路，打通世界高速铁路发展的“任督二脉”。</p>\n<p>　　此次研讨会由中南大学、詹天佑科学技术发展基金会、中国铁道科学研究院、中国中铁股份有限公司、中国中车股份有限公司、中国铁建股份有限公司、美国纽约州立大学布法罗分校、英国南安普顿大学、美国伊利诺伊大学香槟分校、轨道交通安全协同创新中心联合主办。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1460,'2017-12-17 18:08:00','2017-12-17 19:18:21','65',0),(2966,'中国经济学者反驳三种西方错误观点','罗攀','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>北京12月17日电 (记者 赵建华)明年中国将迎来改革开放40周年。中国社科院学部委员、副院长蔡�P17日在北京表示，中国书写了过去40年的成功故事，但在评价中国故事时却容易受到来自西方的错误观点影响。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　当天，中国社科院经济研究所在北京举行纪念改革开放40周年暨《经济学动态》复刊40周年大型研讨会，来自北京大学、清华大学、中国人民大学、南开大学、中国社科院、中国财政科学研究院等多家中国高校、研究机构的经济理论研究者与会。</p>\n<p>　　在此次研讨会上致辞时，蔡�P表示，讨论中国经济问题时，有三种来自西方的观点影响着不少经济学家。</p>\n<p>　　一是哈耶克的观点――“人类行为的意外后果”，认为中国改革开放的成就是无心插柳柳成荫，罗纳德・科斯、张五常等经济学者受其影响。过去40年，包括农民、工人、企业家在内的千千万万个中国当事人有意识地参与了改革开放，如果把他们说成是瞎撞，有失公允。</p>\n<p>　　二是经济学家钱纳里的观点，认为一个国家如果看清了体制弊端，并清除这些弊端，即使不存在发展的必要条件，也能实现快速发展。按照这一观点，如果“不具备发展条件”，中国过去30多年如何保证年均近两位数的高速增长？</p>\n<p>　　三是保罗・克鲁格曼和艾尔文・扬不断唱衰中国，认为中国的经济增长既不是奇迹，也不可持续。他们没有看准中国改革开放带来的、长达30多年的高速增长，总是在讲中国的发展不可持续，那么到底(高速增长)多少年才叫可持续呢？</p>\n<p>　　当天的研讨会上，中国社科院学部委员、经济研究所所长高培勇表示，需要在过去40年经济社会发展成就的基础上，总结中国发展的基本轨迹、基本经验和基本规律。今后中国的经济发展必然有别于以往的发展过程，须更多地从现实问题中找经验，提炼经济发展奇迹背后的中国经验和中国规律。</p>\n<p>　　《经济学动态》是由中国著名经济学家孙冶方倡办、中国社会科学院经济研究所主办的学术刊物，1960年创刊，1964年底停刊，1977年10月试复刊，1978年1月正式复刊。该刊物既反映中国国内经济理论动态，也探讨和跟踪其他国家经济学流派的最新观点，同时关注中国社会经济发展过程中的重大现实经济问题。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,502,'2017-12-17 18:15:00','2017-12-17 19:18:21','66',0),(2967,'2018世界杯滑雪登山赛举行 13国选手参赛','周驰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>张家口12月17日电 16日，“麒先体育杯”2018世界杯滑雪登山比赛在河北省张家口市崇礼县万龙滑雪场结束。这是滑雪登山世界杯首次走出欧洲，走进亚洲，落户中国。</p>\n<p>　　来自俄罗斯、伊朗、韩国、日本、意大利、瑞士、法国、西班牙、安道尔、德国、奥地利、比利时、中国等13个国家的近200名选手参加本次比赛。</p>\n<p>　　本次比赛分为成年男子、女子组；青年男子、女子组；公开赛男子、女子组。比赛项目为垂直竞速赛和短距离赛。</p>\n<p>　　比赛分两天进行。12月15日首先进行的是垂直竞速赛，路线全长3.6公里，累计爬升490米。最终，来自德国的安东以16分58秒获得男子专业组第一名，来自法国的卡塞尔以21分02秒获得女子专业组第一名。中国选手的最好成绩是金煜博的男子第31名和张娜的女子第16名，这也是亚洲选手的最好成绩。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　12月16日进行的是短距离赛，路线全长300米，累计爬升80米，最终，来自法国的Roux Laetitia获得女子短距离赛冠军，来自西班牙的GALICIA COTRION CLAUDIA和瑞士的FATTON MARIANNE获得女子短距离赛的亚军和季军。男子短距离赛第一名由意大利的CANCLINI NICOLO ERNESTO获得，第二名和第三名由西班牙的CARDONA COLL ORIOL和瑞士的ARNOLD IWAM获得。</p>\n<p>　　中国登山协会是国际滑雪登山联盟(ISMF)的正式成员国单位，今年是中国登山协会在我国推广滑雪登山运动的第11个年头，期间举办了多次全国赛事活动、亚洲赛事活动、青海岗什卡的国际交流活动和赛事、国际裁判员培训班等。滑雪登山项目经过中国登山协会十余年的引导和推动，取得了一定的效果，国际滑雪登山联盟将走出欧洲的第一个世界杯选择在中国就是最好的说明。</p>\n<p>　　本次比赛由国际滑雪登山联盟主办，亚洲滑雪登山联盟、中国登山协会、河北省体育局、崇礼区人民政府和万龙滑雪场承办。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,825,'2017-12-17 10:22:00','2017-12-17 19:18:21','74',0),(2968,'天津球员张修维醉驾与多车相撞获刑','魏巍','北京青年报','<p><strong>　　天津球员张修维醉驾与多车相撞获刑</strong></p>\n<p>　　今年8月，中超足球俱乐部天津权健球员张修维，凌晨醉酒驾车与6辆机动车发生碰撞，后被公安交管部门控制。经道路交通事故认定，张修维承担事故全部责任。日前，天津市和平区人民法院对该案作出判决，法院以张修维犯危险驾驶罪，判处其拘役三个月，缓刑三个月，并处罚金人民币8000元。</p>\n<p>　　张修维是中超足球俱乐部天津权健的球员，今年8月16日，天津交警发布情况通报称，张修维8月15日凌晨醉酒驾车，与停放在路边的6辆机动车发生碰撞，事故造成7辆机动车受损，无人员伤亡。事故发生后，交警立即依法对张修维进行了血液酒精含量检验。经检验，其血液酒精含量为189.3mg/100ml。</p>\n<p>　　判决书显示，和平区法院经依法适用刑事速裁程序审理查明，2017年8月15日4时许，张修维饮酒后驾驶白色小轿车，行驶到天津市和平区昆明路与岳阳道交口附近时，与停放在路边的6辆汽车发生碰撞。群众报警后，交警赶至现场将留在现场等待的张修维带至公安机关。经道路交通事故认定，张修维承担事故的全部责任，6辆车的车主不承担事故责任。经司法鉴定，张修维血液中检出乙醇成分，含量为189.3mg/100ml，属醉酒状态。张修维的机动车驾驶证现已被依法吊销。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　据了解，在案件开庭审理过程中，张修维表示没有异议。和平区法院认为，检方指控张修维犯危险驾驶罪的事实清楚，证据充分，指控罪名成立。张修维醉酒驾驶机动车，造成交通事故且负事故全部责任，依法应从重处罚；明知他人报案而在现场等待，抓捕时无抗拒行为，如实供述犯罪事实，具有自首情节，可从轻处罚；积极赔偿受损车辆的经济损失并获得谅解，自愿认罪认罚，系初犯，可酌情从轻处罚。公诉机关所提判处其拘役三个月，缓刑三个月，并处罚金的量刑建议符合法律规定，法院予以采纳。</p>\n<p>　　根据张修维犯罪的事实、犯罪的性质、情节和对于社会的危害程度，和平区法院以张修维犯危险驾驶罪，判处拘役三个月，缓刑三个月，并处罚金人民币8000元。文/本报记者 李铁柱</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,801,'2017-12-17 11:29:00','2017-12-17 19:18:21','74',0),(2969,'最高检：对电信网络诈骗犯罪一律依法快捕快诉','李明阳','新华网','<p><strong>　　最高检：对电信网络诈骗犯罪一律依法快捕快诉</strong></p>\n<p>　　新华社北京12月17日电(记者 陈菲)最高人民检察院侦查监督厅有关负责人日前指出，对电信网络诈骗犯罪坚持做到“三个一律”：一律依法快捕快诉；一律组成专班集中办理；对重点整治地区，一律加大源头治理和综合治理的力度。</p>\n<p>　　近年来，检察机关推动打击和预防网络犯罪力度持续增强。数据显示，2016年以来，全国检察机关适用涉嫌非法侵入计算机信息系统罪等7个涉及计算机犯罪罪名，向人民法院提起公诉727件1568人，其中2017年1月至9月提起公诉334件710人，同比分别上升82.5%和80.7%；对网络电信侵财犯罪案件提起公诉15671件41169人，其中2017年1月至9月提起公诉8257件22268人，同比分别上升88.6%和118.6 %。</p>\n<p>　　据悉，下一步检察机关将从五个方面做出部署：</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　一是进一步增强对电信网络诈骗犯罪危害性的认识，坚持依法严厉打击不动摇，保持高压态势，防止反弹。</p>\n<p>　　二是坚持以证据为核心。加强介入侦查引导取证工作，确保侦查取证及时到位，确保办案质量和效率。</p>\n<p>　　三是加强协作配合，促进源头治理。加强与公安、法院的协作配合，坚持依法从重从快，实现“快侦查、快提捕、快起诉、快审理”，形成打击防范电信诈骗的司法合力。同时，加强与银行、电信、互联网企业及行业监管部门的沟通联系，从治理上游犯罪入手，阻断公民信息泄露渠道，切断电信网络诈骗犯罪的源头。</p>\n<p>　　四是注重运用大数据收集研判犯罪态势，通过数据采集、整理、分析、发掘、应用，着力解决司法办案中证据收集、审查认定等难题。</p>\n<p>　　五是完善跨国跨境司法协作。特别是在跨国跨境协助调查取证、缉捕遣返犯罪嫌疑人、涉案赃款赃物移交、证据转换、司法文书送达、通信与网络证据的转换及证据采信问题等方面细化协作内容，力争形成完善的司法协作模式。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,104,'2017-12-17 15:29:00','2017-12-17 19:18:21','65',0),(2970,'民革十二届六中全会在京召开','魏巍','新华网','<p><strong>　　民革十二届六中全会在京召开</strong></p>\n<p>　　新华社北京12月17日电(记者姜潇)中国国民党革命委员会第十二届中央委员会第六次全体会议12月17日在北京召开。会议学习贯彻中共十九大精神，听取和审议民革第十二届中央常务委员会工作报告和中央监督委员会工作报告，审定提交第十三次全国代表大会的有关文件及事项。</p>\n<p>　　民革中央主席万鄂湘作工作报告，民革中央常务副主席齐续春主持开幕会。</p>\n<p>　　万鄂湘在回顾2017年工作时指出，一年来，民革全党深入学习贯彻中共十八大和十八届历次中央全会精神，认真学习宣传贯彻中共十九大精神，在习近平新时代中国特色社会主义思想指导下，大力加强自身建设，全面提高履职能力，各项工作呈现良好局面。深入开展“不忘合作初心，继续携手前进”主题教育活动，大力推动“民革党员之家”建设，思想政治工作不断强化；圆满完成30个民革省级组织换届工作，持续强化党内监督，组织建设全面加强；积极参与政治协商，精心组织专题调研，参政议政工作成效显著；扎实开展脱贫攻坚民主监督，着力强化社会服务品牌，社会服务工作持续推进；关注台湾青年群体，促进两岸交流交往，为推动两岸关系和平发展作出积极贡献。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　万鄂湘强调，即将召开的民革十三大是民革政治生活中的一件大事，也是中国共产党领导的多党合作事业中的一件大事。民革全党要更加紧密地团结在以习近平同志为核心的中共中央周围，认真学习宣传贯彻中共十九大精神，在习近平新时代中国特色社会主义思想的指引下，继承和发扬孙中山爱国、革命、不断进步精神，全面履行好参政党的各项职能，为决胜全面建成小康社会、夺取新时代中国特色社会主义伟大胜利、实现中华民族伟大复兴的中国梦、实现人民对美好生活的向往而努力奋斗。</p>\n<p>　　会议还将审议第十二届中央委员会向第十三次全国代表大会作的工作报告，审议《中国国民党革命委员会章程(修正案)》(草案)等其他事项。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,204,'2017-12-17 13:11:00','2017-12-17 19:18:21','64',0),(2971,'洪都拉斯军用直升机失事6人遇难 含现总统姐姐','吴倩','新华网','<p>　　<strong>洪都拉斯军用直升机失事6人遇难</strong></p>\n<p>　　新华社马那瓜12月16日电(记者 许雷 刘璇)特古西加尔巴消息：洪都拉斯军方16日证实，一架军用直升机当天在该国中部坠毁，机上6人全部遇难。</p>\n<p>　　洪军方发布公告说，当天上午，一架贝尔905型军用直升机自洪首都特古西加尔巴国际机场起飞前往中部城市科马亚瓜，机上载有洪都拉斯前通讯部长伊尔达・埃尔南德斯及其安保团队。直升机起飞后不久与地面控制塔失去联系。</p>\n<p>　　直升机失联后，军方派出两架军机展开搜索，在洪中部的一个小镇附近发现了失事直升机残骸和6名遇难者遗体。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　另据当地媒体报道，直升机上安装有类似黑匣子的装置，但出事时未能及时启动并记录失事过程，事故原因有待调查。</p>\n<p>　　伊尔达・埃尔南德斯现年51岁，曾在2014年至2017年出任洪都拉斯通讯部长。她是洪总统胡安・埃尔南德斯的姐姐。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1490,'2017-12-17 15:21:00','2017-12-17 19:18:21','71',0),(2972,'澳门第十三届全国人大代表选举会议主席团举行第三次会议','于晓','新华网','<p>　　澳门第十三届全国人大代表选举会议主席团举行第三次会议</p>\n<p>　　新华社澳门12月17日电(记者 郭鑫、王晨曦)澳门特别行政区第十三届全国人大代表选举会议主席团第三次会议17日在澳门举行。全国人大常委会副委员长兼秘书长王晨出席会议，主席团常务主席崔世安主持会议。</p>\n<p>　　会议决定发布关于澳门特别行政区第十三届全国人大代表选举结果的公告。根据《中华人民共和国澳门特别行政区选举第十三届全国人民代表大会代表的办法》的规定，由主席团将选举结果的报告报送全国人大常委会代表资格审查委员会进行代表资格审查。</p>\n<p>　　会后，主席团发布第三号公告。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　<strong>新闻链接：</strong></p>\n<p>　　中华人民共和国澳门特别行政区第十三届全国人民代表大会代表选举会议主席团公告</p>\n<p>　　中华人民共和国澳门特别行政区第十三届全国人民代表大会代表选举会议于2017年12月17日举行第二次全体会议，选举澳门特别行政区第十三届全国人民代表大会代表。按照《中华人民共和国澳门特别行政区选举第十三届全国人民代表大会代表的办法》的规定，会议选出了12名中华人民共和国澳门特别行政区第十三届全国人民代表大会代表，确定了3名未当选为代表的候选人在代表因故出缺时的递补顺序。现将选举结果予以公布。&gt;&gt;</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,741,'2017-12-17 17:27:00','2017-12-17 19:18:21','69',0),(2973,'日本政府将决定2018年度预算案 总额再创新高','吴倩','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月17日电 据日媒报道，日本政府正在编制的2018年度预算案的一般会计总额将达到97.7万亿日元(约合人民币5.7万亿元)左右。这一数字比2017年度的97.4547万亿日元增加了约2500亿日元，连续6年刷新最高纪录。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　报道称，在日本经济形势良好的带动下，预计税收将增加约1.4万亿日元至59.1万亿日元，新发国债额减少约7000亿日元，为33.7万亿日元左右。</p>\n<p>　　据悉，预算案经过18日的日本阁僚协调，将在22日的内阁会议上决定。原始预算中新发国债额连续8年减少，在2009年度的33.3万亿日元后，将可以控制在9年来最低水平。但新发国债占全部财政收入3成以上的格局自1999年度起未有变化，重审财政支出�热萑匀皇强翁狻�</p>\n<p>　　财政支出中，包括33万亿日元左右的社会保障费和5.2万亿日元左右的防卫费等一般财政支出总计约58.9万亿日元，增加约5000多亿日元。</p>\n<p>　　为补充地方财源不足而分配给日本地方政府的补贴(地方交付税)减少至约15.5万亿日元，用于偿还借款的国债费也减少约2000亿日元至约23.3万亿日元。</p>\n<p>　　根据计算，显示通过税收和税外收入能在多大程度上覆盖除去国债费的财政支出的基础财政收支将为约10.4万亿日元赤字。这比2017年度的10.8万亿日元赤字有所改善，也是2年来赤字幅度首次缩小。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1823,'2017-12-17 15:45:00','2017-12-17 19:18:21','71',0),(2974,'国际足联“女孩足球节”北京站启动 扩大女足运动基础','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>北京12月17日电 (记者 王曦)2017年国际足联―中国足协“Live Your Goal(追梦)”女孩足球节北京站活动17日在北京市第十九中学拉开帷幕，本次活动是由国际足联和中国足协联合策划的女足推广活动。</p>\n<p>　　“Live Your Goals”女孩足球节是由国际足联发起，并联合中国足协及全国青少年校园足球工作领导小组办公室共同主办，目前已发展成为一个国际足联高度认可的女子足球活动。足球节旨在鼓励女孩积极参与形式多样的趣味足球活动，让更多女孩儿体验足球生活、热爱足球运动、享受足球快乐，吸引更多女孩儿投身足球运动，不断扩大女足运动基础。</p>\n<div style=\"text-align:center\"> \n <img alt=\"2017年国际足联―中国足协“Live Your Goal(追梦)”女孩足球节北京站活动17日在北京市第十九中学拉开帷幕，本次活动是由国际足联和中国足协联合策划的女足推广活动。　王曦 摄\" src=\"upload/image/f63cd0a3-f3c6-482e-ae71-315366e85ae4.JPG\" title=\"2017年国际足联―中国足协“Live Your Goal(追梦)”女孩足球节北京站活动17日在北京市第十九中学拉开帷幕，本次活动是由国际足联和中国足协联合策划的女足推广活动。　王曦 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  2017年国际足联―中国足协“Live Your Goal(追梦)”女孩足球节北京站活动17日在北京市第十九中学拉开帷幕，本次活动是由国际足联和中国足协联合策划的女足推广活动。　王曦 摄 \n</div>\n<p>　　根据活动安排，每年“Live Your Goal”活动在中国女超、女甲联赛所在的16个赛区开展32期活动，作为女超联赛重要的城市之一，北京顺理成章地成为该活动的重要一站。中国足协代表张晗表示，此次国际足联―中国足协活动的中文主题是“出彩人生，追逐足球梦想”，旨在吸引和鼓励更多女孩儿参与到足球运动中来，着力推广女子足球运动，夯实中国女足的人力资源基础，有利于营造女子足球运动的发展及环境，尽快提高足球技能，在将来成为优秀的女子足球运动员。</p>\n<p>　　当日，共有200余名女足小球员和教练员参与了本次活动。作为承办方，北京精英足球俱乐部对本次活动给予了支持。该俱乐部自2014年成立以来，组织参与了国内外多项重大赛事活动。据了解，作为北京女足运动开展的重要地区，海淀区目前已成为“全国青少年校园足球试点区”，连续举办了三届“聚享杯”足球比赛，今年该赛事还首次增加了女子项目比赛。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/f63cd0a3-f3c6-482e-ae71-315366e85ae4.JPG',NULL,1489,'2017-12-17 13:10:00','2017-12-17 19:18:21','74',0),(2975,'国侨办“教育扶贫”甘肃积石山：从“上起学”到“上好学”','于晓','中国新闻网','<div style=\"text-align:center\"> \n <img alt=\" 12月11日，国侨办励志奖助学金暨冬季校服发放仪式在甘肃积石山县吊坪村蔚林小学举行。　南如卓玛 摄\" src=\"upload/image/3144c3a7-550a-43ed-8339-159276789207.jpg\" style=\"border:px solid #000000\" title=\" 12月11日，国侨办励志奖助学金暨冬季校服发放仪式在甘肃积石山县吊坪村蔚林小学举行。　南如卓玛 摄\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  12月11日，国侨办励志奖助学金暨冬季校服发放仪式在甘肃积石山县吊坪村蔚林小学举行。南如卓玛 摄 \n</div>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>兰州12月17日电 (记者 南如卓玛)甘肃省积石山县教育局日前向<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者披露，国侨办累计为该县教育系统捐资1676.23万元(人民币，下同)，其中改扩建学校32所，学生救助等方面投入696.88万元。</p>\n<p>　　积石山是国家级贫困县，国侨办自1998年开始定点帮扶。19年来，得益于海外侨胞和国内外侨团、侨校支持，国侨办致力于解决该县教育教学基础设施差、师资力量薄弱、贫困家庭上学负担重等问题。</p>\n<p>　　今年，国侨办筹集21万元设立了吊坪村励志奖助学金，对该村义务教育阶段、高中教育阶段贫困学生和品学兼优学生以及应届录取大学生，给予助学金和奖学金，并对在吊坪村执教的优秀老师给予奖励。近日，国侨办向吊坪村11名大学生和12名中小学生发放了奖学金，并向24名家庭困难学生等发放了助学金。</p>\n<p>　　国侨办挂职积石山县副县长李永清17日接受记者采访时表示，国侨办在积石山的教育扶贫涉及捐建学校、捐赠物资、资助贫困学生、支教、师资培训、夏令营活动等，但过去捐赠多是一次性资助或“一帮一”结对形式，可以说在解决“上起学”的问题。</p>\n<p>　　李永清说，总结这些年的经验，近两年国侨办探索如何能在“扶智”上发挥更大作用，提升当地教育水平，解决“上好学”的问题。目前，已制定了教育基金方案，成立管理机构，规定了资助奖励对象和发放标准，助困奖优重师资，并重在制度化、长久化，最终为了提高当地的教育水平。</p>\n<p>　　吊坪村五年级奖学金获得者马静说，当地官方虽免了学杂费，但生活费对于贫困家庭依然是一笔大数字，感谢国侨办的奖励，将以优异成绩作为回报。</p>\n<p>　　村民李广元代已考上大学的儿子领到了3000元助学金。他攥着写着钱数的牌子，黝黑的脸上露出笑容。</p>\n<p>　　蔚林小学校长苟进辉说，在国侨办帮扶下，该校有了新教学楼，受捐800册图书、6个书架、20台电脑等。同时，华侨大学每年的大学生支教，开阔了山里学生的眼界，为他们渴望努力学习走出大山埋下了“理想的种子”。</p>\n<p>　　积石山县教育局副局长陶永鹏表示，国侨办的帮扶、华人华侨的捐建，彻底改善了积石山部分学校的办学条件。同时，一大批贫困学生得到了救助。捐助项目的实施极大地调动了当地适龄儿童入学的积极性，提高了入学率。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/3144c3a7-550a-43ed-8339-159276789207.jpg',NULL,1134,'2017-12-17 18:44:00','2017-12-17 19:18:21','64',1),(2976,'腾讯与北京市金融工作局开发金融安全大数据监管平台','罗攀','中国新闻网','<p>　　<strong>腾讯与北京市金融工作局达成战略合作 国内首个金融安全大数据监管平台亮相</strong></p>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月17日电 12月16日下午，腾讯公司与北京市金融工作局战略合作签约仪式在京举行，双方将联合开发基于北京地区的金融安全大数据监管平台，致力于通过资源共享，对各种金融风险进行识别和监测预警，共同保护金融消费者合法权益，助力地方金融监管，防控金融风险。北京市金融工作局局长助理庞津维，中关村银行董事长郭洪，腾讯副总裁马斌，腾讯支付基础平台与金融应用线副总裁朱立强、腾讯安全总经理李旭阳等领导出席了本次签约仪式。</p>\n<p>　　北京市金融工作局作为负责促进北京市金融发展、金融服务和金融市场建设工作的市政府直属机构，在促进首都金融发展、金融服务、金融市场建设和维护首都金融安全稳定方面发挥着重要职能作用。北京市委书记蔡奇在全市金融工作会上重点强调要发展与首都地位相匹配的现代金融业。谋划首都金融发展，必须与加强“四个中心”功能建设、提高“四个服务”水平紧密结合。既要深刻认识首都金融所处的重要地位，更要以首善标准加强金融发展环境建设，切实加强金融监管和风险防控，坚决守住不发生系统性金融风险的底线。</p>\n<p>　　对于腾讯金融安全大数据监管平台首度亮相，北京市金融工作局党组书记、局长霍学文对其在金融监管领域的创新予以充分肯定，他表示，此次合作将大数据安全风控与金融监管工作结合起来，利用信息科技实现监管升级，维护社会公共治理秩序和良性发展。同时，腾讯金融安全大数据监管平台也将助力北京地区各金融企业防范互联网黑产风险，为金融创新保驾护航。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　腾讯金融安全大数据监管平台作为国内有影响力的金融监管平台，由腾讯金融科技业务依托腾讯安全反诈骗实验室的“灵鲲金融安全系统”搭建，主要用于防范普惠金融走向普“恶”金融，专门打击以金融创新之名行诈骗之实的黑产行为。该平台由多个系统组成，主要从以下三个方面推动金融科技的规范和创新发展。</p>\n<p>　　在金融创新方面，腾讯金融安全大数据监管平台对促进金融和科技同步发展，实现普惠金融具有重要意义。十九大报告曾提出，健全金融监管体系，守住不发生系统性金融风险的底线。近期发布的《中国金融科技发展报告(2017)》也再次强调“加强金融监管，重塑金融科技”以迎接中国未来成为全球最大金融科技应用市场的必要性。金融监管创新成为金融创新工作的重中之重。金融安全大数据监管平台结合腾讯的大数据、腾讯安全联合实验室反诈骗实验室AI技术优势，解决克服了监管科技历来存在的“数据(全网的底层数据)”、“算法( 模型能力远超一般小公司)”、“计算力(服务器计算能力)”不足等痛点，成为金融创新典范。</p>\n<p>　　在打击金融黑产方面，腾讯金融安全大数据监管平台能有效阻止或拦截网络黑产向线上业务运营的转移。金融安全大数据监管平台依托微信、QQ等强大的社交平台，及腾讯安全与网络黑产势力对抗十多年积累沉淀的大数据平台，采用基于金融犯罪样本挖掘金融风险并进行数据化、可视化的方式方法，以及建立从监测、分析、模型拟定、欺诈定型的全流程管理，并搭建了较好的从数据源管理到风险展示的系统架构，让善于藏匿的网络黑产无处遁形，充分保护金融消费者权益。</p>\n<p>　　在金融监管方面，腾讯金融安全大数据监管平台较大提升非法集资等涉众型金融犯罪“打早打小”的事前预警处置能力，实现对金融风险的识别和监测预警。此次战略合作的签订，腾讯与北京市金融工作局将合作推进金融监管科技相关技术研发与推广，通过引入多维度金融相关数据，研究并系统跟踪、研判不同业态金融风险，共同开发基于监测、分析、模型拟定、欺诈定型、监管科技等全流程管理模型，共同加大金融监管科技在风险防范和处置方面的应用与落地</p>\n<p>　　秉承“一切以用户价值为依归”的经营理念，以“连接一切”为战略方向，腾讯在金融科技赋能之路砥砺前行。腾讯公司董事会主席兼首席执行官马化腾曾表示，腾讯进行的是去中心化的赋能。腾讯金融安全大数据监管平台的推出，是腾讯在互联网+业务中通过自身科技力量赋能于金融生态圈里的合作伙伴，并充分保障被赋能者安全地掌握自身命运，实现真正的去中心化赋能，双方一起共建开放、共赢的互联网金融生态圈，积极促进普惠金融健康有序发展。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1145,'2017-12-17 10:18:00','2017-12-17 19:18:21','67',0),(2977,'《青春旅社》王源李小璐活力满满跳魔性解压舞','罗攀','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月17日电 东方卫视全新原创星素结合励志节目《青春旅社》于每周日21:40清新上线，本周即将播出第十期节目“歌舞青春”。新一期节目里，A、B栋再聚举办青春舞会，与客人们一起歌舞青春；“孩子王”王源带领小朋友尽情表演《青春修炼手册》经典舞蹈，即兴“飙戏”放飞自我；李小璐、戴军领衔A栋集体欢跳印度舞，搞笑表情和动作乐翻众人；luu brothers前来帮忙，众人因其长相相似傻傻分不清楚；黄雅莉更是秒变luu brothers“迷妹”，干活都倍增动力……</p>\n<p>　　与此同时，B栋竟遭遇停水危机，王源、赵英俊等人将要如何解决？王源、李小璐意外生病，他们能否坚持完成工作？经历了上一期节目的外出学习，本期节目中A、B旅社会有哪些升级服务呢？本周日21:40，东方卫视《青春旅社》为您揭晓！</p>\n<p>　　<strong>“孩子王”王源教学舞蹈乐翻天 逗趣“飙戏”放飞自我</strong></p>\n<p>　　本期节目中，由于A栋迎接了几位热爱跳广场舞的客人，所以A、B栋决定举办一场青春舞会。为此，B栋店长王源开始教几位小朋友跳舞，为晚上的舞蹈节目做准备。他耐心讲解《青春修炼手册》的舞蹈动作，带领小朋友们一起“左手右手一个慢动作，跳一下、比桃心、耍帅……”同时，王源、程晓�h、郭劲岐也向几位小朋友学习了一段舞蹈，然而，她们的舞蹈动作总是变换不一，让王源等人只能即兴发挥，“反差萌”表现尽显青春活力。</p>\n<p>　　除了与大家一起学习舞蹈，“孩子王”王源和程晓�h还带来了一系列“甜蜜轰炸”，气泡果饮、冰淇淋、棉花糖……用美食“俘获”了小朋友们的心。或许因为受小朋友的影响，王源也尽情释放自我。吃晚饭时，王源突然“飙戏”，表演了被美食吸引的陶醉状态，让不明真相的程晓�h信以为真。他还学新疆口音说话：“新疆吐鲁番的烤羊肉串……”逗趣模仿让众人哄然大笑！</p>\n<div style=\"text-align:center\"> \n <img alt=\"王源郭劲岐等人和小朋友一起跳舞\" src=\"upload/image/1ec41bae-a623-428f-8b28-db92065466e5.jpg\" style=\"border:px solid #000000\" title=\"王源郭劲岐等人和小朋友一起跳舞\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  王源郭劲岐等人和小朋友一起跳舞 \n</div>\n<p>　　<strong>众人齐聚欢跳魔性解压舞 李小璐戴军领衔“印度舞天团”放飞自我</strong></p>\n<p>　　晚饭后，A、B两栋齐聚一起开始歌舞青春。李小璐、戴军领衔A栋“印度舞天团”开始了印度舞表演。李小璐带病上阵热情不减，精准动作引来频频称赞。戴军即兴发挥，深得印度舞精髓，从眼神到动作都十分吸人眼球，让观众席中的王源高呼“为军哥打call，军哥太棒了，没有军哥的舞蹈不成舞……”紧接着，戴军还表现了独舞，搞笑表情和动作更是让众人笑声满满。</p>\n<p>　　整段舞蹈中，还有一位舞蹈新秀李静的表演也十分精彩。不擅长舞蹈的她受戴军影响也开始疯狂起来。然而，兴奋的李静竟然不小心摔倒，坐在地上，戴军则立刻“救援”，与李静一起坐地起舞，随机应变的能力十分厉害。由此，A栋集体变换舞种，一段freestyle尽显他们的青春活力。加之几位客人齐跳《大王叫我来巡山》的舞蹈，一场青春舞会点燃了每个人的激情。</p>\n<p>　　<strong>luu brothers前来帮忙众人脸盲分不清 黄雅莉秒变“迷妹”干活有动力</strong></p>\n<p>　　本期节目还迎来了三位新朋友luu brothers，三兄弟因长相相似，惹得李静、黄雅莉等人傻傻分不清楚，李静还总是将luu brothers的中文名“福满堂”记成“福禄寿”，引发许多笑点。戴军也分不清谁是老大谁是老三，不经意间总是叫错人。看来与三胞胎生活在一起也是一件十分有趣的事！接下来，luu brothers承担起A栋的接待客人工作，在李静的“洗脑式”指导下，三兄弟见到客人便开始争抢拿行李箱，将客人们直接带到A栋，不知道A栋有了luu brothers的助力会不会客人不断呢？</p>\n<p>　　而黄雅莉见到luu brothers更是秒变“迷妹”：“他们长得像让我忽略了颜值，其实长得蛮帅的！”随之，她便和戴军一起为三兄弟准备早餐，做饭过程中，黄雅莉略显兴奋，戴军一语中的：“为他们做东西很开心是吗？”黄雅莉自己也坦承：“干活特别有动力。”不知道luu brothers三兄弟的加入还会发生哪些趣事呢？期待他们更多精彩表现。</p>\n<p>　　除了以上精彩内容之外，本期节目里B栋竟然遭遇了停水危机，王源、赵英俊等人将如何解决问题？戴军资深粉丝为其而来，众人坐谈往事欢乐多；郭劲岐做可乐鸡翅获称赞，每个人都在努力成长……本期节目亮点多多，尽在本周日21:40东方卫视《青春旅社》，年轻的朋友来相会！</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/1ec41bae-a623-428f-8b28-db92065466e5.jpg',NULL,990,'2017-12-17 09:55:00','2017-12-17 19:18:21','68',0),(2978,'开赛首日收获一金一银 中国冬奥赛场经受世界杯考验','魏巍','北京青年报','<p><strong>　　开赛首日收获一金一银</strong></p>\n<p><strong>　　中国冬奥赛场经受世界杯考验</strong></p>\n<p>　　本报崇礼专电(记者 褚鹏)中信国安・国际雪联自由式滑雪空中技巧世界杯昨天在河北省张家口崇礼区云顶滑雪场开赛。中国队的贾宗洋不惧严寒和大风天气，夺得男子个人冠军。女子选手徐梦桃获得个人亚军。</p>\n<p align=\"center\"><img src=\"upload/image/79f5afb6-cb04-4c67-86a9-252923d798de.jpg\"></p>\n<p class=\"pictext\" align=\"center\">贾宗洋</p>\n<p>　　昨天的比赛，也是平昌冬奥会的资格赛，在2022年冬奥会比赛场地云顶滑雪场举行。来自加拿大、美国、俄罗斯、日本、瑞士等12个国家和地区的70余名国际顶尖男女运动员参赛，中国队派出12名选手参赛。</p>\n<p>　　中国男队当日表现不错，资格赛中齐广璞和贾宗洋分别以第一名和第四名的成绩晋级。决赛中贾宗洋凭借4.425的难度系数、127.88分的高分，强势夺冠。齐广璞的动作难度系数达到4.525，无奈落地失误，以95.93分位列第四。女子方面，徐梦桃在资格赛中获得91.65分的全场最好成绩，成为中国队晋级决赛的独苗，可惜决赛最后一跳落地稍有不稳，屈居女子单人决赛亚军。</p>\n<p>　　贾宗洋时隔三年后再次站上领奖台。此前他受伤、手术，成绩起伏不定。赛后，贾宗洋表示：“对结果非常满意。受伤后去年赛季信心受损，出现了很多失误。这让夺冠的意义非同一般，对我的信心也会有很大提升。”</p>\n<p>　　虽然最后一跳留下遗憾，徐梦桃依然在赛后与到场的朋友熟人拥抱庆祝。徐梦桃同样是从严重的伤病中恢复过来。2016年初她在比赛中摔伤，被担架抬出赛场。此后她的双腿韧带、半月板都做了手术。赛后徐梦桃说：“那段日子是最难的时刻，但我在教练的鼓励下，从两周台慢慢到三周台。团队一次次用数据来说明我的进步。这些都帮助我建立了重新回到领奖台的信念。这是世界杯第一站，这个结果我很满意。”</p>\n<p>　　本次世界杯首次来到云顶这座未来的冬奥会赛场。云顶滑雪场首席运营官本诺・纳戈尔表示，作为2021年世锦赛及2022年冬奥会举办地，本站世界杯赛事为云顶的运营带来了积极的效益。作为冬奥会自由式项目及单板项目的举办地，云顶滑雪场成为自由式群体及年轻群体的聚集地。为冬奥会服务也保证了云顶滑雪场具备一流的设施和服务。这也是云顶与其他雪场实质性的差异。</p>\n<p>　　对于本次赛事软硬件，徐梦桃和贾宗洋作为参加过奥运会的名将，表示这里的设施确实是国际标准。获得男子个人铜牌的欧文说：“我去过世界上很多国家比赛，崇礼赛区的设施非常棒，确实是国际级标准，非常期待北京冬奥会的来临。”</p>\n<p>　　本次比赛由国际滑雪联合会主办，国家体育总局冬季运动管理中心、中国滑雪协会、河北省体育局、张家口市人民政府协办。17日，赛事将迎来男子和女子团体赛的争夺。</p>\n<p>　　摄影/本报记者 崔俊</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/79f5afb6-cb04-4c67-86a9-252923d798de.jpg',NULL,1828,'2017-12-17 11:37:00','2017-12-17 19:18:21','74',0),(2979,'《国家宝藏》刘涛圆梦演绎“女战神”守护镇馆之宝','罗攀','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月17日电 由央视综艺频道(CCTV-3)出品的大型文博探索节目《国家宝藏》12月17日晚19：30即将播出第三期节目。本周，来自河南省博物院的三件传世国宝――妇好�{尊、云纹铜禁、贾湖骨笛将在刘涛、雷佳音、蔡国庆父子三组“国宝守护人”的演绎下，展现其充满传奇性的前世故事。</p>\n<p>　　作为华夏文明起源地之一，河南见证了华夏文明的诞生与繁盛。本期节目中展示的三件国宝“年龄”加起来超过万年，其中妇好�{尊、云纹铜禁都和酒有关，贾湖骨笛则将中华文明的音乐历史拉长到了8000多年前。究竟这些传奇国宝的背后都有着怎么样的故事？三位国宝守护人又会如何演绎这些传奇？</p>\n<p><strong>　　刘涛圆梦演绎“女战神”妇好守护镇馆之宝</strong></p>\n<p>　　作为我国历史上创建较早的博物馆之一，河南博物院自1927年创建至今已经整整90年，期间征集、出土馆藏近17万件。由于地理原因，河南省博物院中的馆藏历史也十分久远，上下五千年的历史在此都一览无遗。</p>\n<p>　　本期节目中，作为河南省博物院镇馆之宝的妇好�{尊就是出土自3000多年前商王武丁的妻子妇好的墓中。作为王的妻子，妇好不仅是母仪天下的王后，还是当时地位崇高的大祭司和统领大军的将军，堪称“三位一体”的“女武神”。而妇好�{尊，正是武丁为了奖赏妇好打败异族部落而特意打造的祭祀酒器。由于猫头鹰造型的青铜器非常稀有，并且里面还刻有“妇好”的铭文，因此无论从艺术价值还是历史价值上来讲，妇好�{尊都具有无可比拟的重要地位。</p>\n<p>　　为了演绎这位传奇“女战神”的经历，刘涛在现场连换三套服装，为观众展现了三千年的战争和祭祀场景。对于这位中国历史上有据可查的第一位女性军事统帅，刘涛也仰慕已久，直言：“曾经有人找我演妇好。我也很向往这位集三者为一体的传奇女性，但可惜因为种种原因，那个剧没拍成。这次在《国家宝藏》的舞台上，我终于等到了妇好，也算圆梦了。”</p>\n<p>　　除了刘涛的圆梦演绎，当年亲手将妇好�{尊挖掘出土的考古学家郑振香老师也现身讲述了与国宝的今生故事。据悉，当时郑振香还差点与妇好�{尊“擦肩而过”，这段经历又是怎么回事？</p>\n<div style=\"text-align:center\"> \n <img alt=\"雷佳音演绎“云纹铜禁”前生故事\" src=\"upload/image/1ba2c78f-2298-4e03-a680-a182372a22a7.jpg\" style=\"border:px solid #000000\" title=\"雷佳音演绎“云纹铜禁”前生故事\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  雷佳音演绎“云纹铜禁”前生故事 \n</div>\n<p><strong>　　雷佳音变身楚国“令尹”详解国宝意义</strong></p>\n<p>　　妇好�{尊作为祭祀酒器，由号称“酒中豪杰”的刘涛守护可谓相得益彰。而节目中另一个同样与酒有关的国宝云纹铜禁，守护者也和酒有着千丝万缕的关系。</p>\n<p>　　作为2500多年前楚国黄金时代的青铜文物，云纹铜禁自出土之日起便有着三宗“最”的称号：体积最大，年龄最大，工艺最精美。尤其是其身上由c、t、s等样式的铜梗拼成的云纹，繁复华美至极。被列为首批禁止出国展览的文物之一。</p>\n<p>　　节目中，雷佳音化身楚国“令尹”，与误入墓穴的汉代学子围绕着云纹铜禁展开了一场有关楚国800年沉浮的讨论，情到深处甚至痛哭流涕，不能自己。国宝与国宝守护人的搭配会碰撞出怎样的火花？云纹铜禁的背后又有着怎样的历史意义？</p>\n<p><strong>　　蔡国庆父子守护8000岁骨笛传承中华音乐</strong></p>\n<p>　　本期节目中，除了出现节目的第一位女守护人，还出现了第一对父子档守护人――蔡国庆、蔡轩正父子。作为著名的歌唱表演艺术家，蔡国庆将要守护的国宝自然也与音乐有关。</p>\n<p>　　作为三件国宝中“年龄”最大的国宝，贾湖骨笛出土于河南舞阳贾湖遗址，以鹤类禽鸟中空的尺骨制成。在贾湖骨笛现世之前，大多数人认为先秦之前只有5声调式，但这支七孔笛不仅改写了这个历史，还将中华的音乐历史延长了几千年。据悉，贾湖骨笛先后共被发掘出土30多支，是世界上最早的可吹奏乐器，比古埃及早2000年。其中，这支七孔骨笛可以说代表了当时制作的最高水平。</p>\n<p>　　虽然目前蔡国庆最著名的身份是歌手，但毕业于中戏的他以前学的是表演专业。这次带着儿子一同出演8000多年前的“原始人”，蔡国庆也直言是一个挑战。究竟在8000多年前，围绕这支骨笛会有怎样的故事？</p>\n<p>　　此外，蔡国庆还将邀请中国艺术研究院的专家萧兴华带大家一同聆听8000年前的骨笛声。究竟流传了8000年的骨笛会奏响怎样的乐曲？更多精彩，敬请期待12月17日晚19：30央视综艺频道(CCTV-3)《国家宝藏》。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/1ba2c78f-2298-4e03-a680-a182372a22a7.jpg',NULL,1152,'2017-12-17 09:55:00','2017-12-17 19:18:21','68',0),(2980,'奥斯卡外语片九强出炉 《战狼2》无缘入围','魏巍','北京青年报','<p><strong>　　奥斯卡外语片九强出炉 《战狼2》无缘入围</strong></p>\n<p>　　本报讯(记者 肖扬)第90届奥斯卡最佳外语片九强名单近日出炉，代表中国内地报名的《战狼2》如之前所预计的，未能入围，香港选送的《一念无明》、中国台湾选送的《日常对话》也出局。华语电影再与奥斯卡外语片无缘。</p>\n<p>　　今年有破纪录的92部影片送选报名参加明年的奥斯卡最佳外语片评选，创下奥斯卡史上参评最佳外语片的数量新纪录。入围初选提名的9部电影分别是智利影片《普通女人》、德国影片《凭空而来》、匈牙利影片《肉与灵》、以色列影片《狐步舞》、黎巴嫩影片《羞辱》、俄罗斯影片《无爱可诉》、塞内加尔影片《菲丽希缇》、南非影片《伤口》和瑞典影片《自由广场》。其中，《普通女人》《凭空而来》《无爱可诉》和《自由广场》在日前都获得了第75届美国电影电视金球奖最佳外语片奖提名。</p>\n<p>　　代表柬埔寨参选、安吉丽娜・朱莉执导的《他们先杀了我父亲：一个柬埔寨女儿的回忆录》、奥地利电影大师迈克尔・哈内克的新作《快乐结局》以及法国的《每分钟120击》则遗憾出局。《每分钟120击》是第70届戛纳电影节评委会大奖得主，当时因为它没能摘得金棕榈，只是拿下了仅次于金棕榈的第二名评委会大奖，评委会主席佩德罗・阿莫多瓦还泪洒现场。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　第90届奥斯卡金像奖提名名单将于当地时间明年1月23日公布，颁奖典礼将于明年3月4日在美国洛杉矶举行。</p>\n<p>　　奥斯卡最佳外语片奖为奥斯卡奖中颁发给年度最佳非英语电影的奖项，提名电影应在每年10月至第二年9月间于所在地区连续公映7日以上。最终奖项仅颁给电影导演，但也被认为是电影出品国家的荣誉。</p>\n<p>　　与奥斯卡其他奖项由美国电影艺术与科学学院全体会员投票选出不同，最佳外语片则另成立专门的评选委员会选出入围名单，再由参与过外语片放映活动的会员选出得奖影片。</p>\n<p>　　九片入围奥斯卡最佳外语片</p>\n<p>　　智利影片《普通女人》</p>\n<p>　　德国影片《凭空而来》</p>\n<p>　　匈牙利影片《肉与灵》</p>\n<p>　　以色列影片《狐步舞》</p>\n<p>　　黎巴嫩影片《羞辱》</p>\n<p>　　俄罗斯影片《无爱可诉》</p>\n<p>　　塞内加尔影片《菲丽希缇》</p>\n<p>　　南非影片《伤口》</p>\n<p>　　瑞典影片《自由广场》</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1461,'2017-12-17 11:43:00','2017-12-17 19:18:21','68',0),(2981,'南宁通报3起形式主义、官僚主义典型问题','魏巍','新华网','<p><strong>　　南宁通报3起形式主义、官僚主义典型问题</strong></p>\n<p>　　新华社南宁12月17日电(记者 向志强)为进一步严明纪律，教育警示广大党员干部，南宁市纪委日前通报3起形式主义、官僚主义典型问题。</p>\n<p>　　隆安县古潭乡政府副乡长卢美红等3人未能正确履职，古潭乡民政办在受理群众低保申请过程中，没有按规定时限开展审核、上报等工作，导致2015年11月至2016年10月该乡符合低保条件的群众没能及时享受补助。</p>\n<p>　　与此同时，在开展高龄、低保等补助发放工作中，古潭乡民政办未及时掌握享受补助对象死亡情况。2014年8月至2017年6月，有33名补助对象死亡后未及时上报有关部门核减，仍继续发放各项补助共5.4万多元。分管民政办的副乡长黄桃玲、卢美红和乡政府民政助理林云先分别被给予党内警告处分、行政警告处分、党内严重警告处分。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　在另一起失职问题中，兴宁区城乡居民最低生活保障管理办公室科员龚晓在参加三塘镇政府组织的2014年度村级惠民项目竣工验收工作中，没有正确履行工作职责，验收流于形式，致使项目多支付工程款4.6万多元，造成国家财政资金流失。龚晓受到党内警告处分。</p>\n<p>　　还有一起属于不作为、履职不力问题。2016年2月7日，青秀区建政街道二塘社区一生活区发生火灾，共有8户居民受灾，社区对受灾居民的安抚工作及后续困难救助未能及时掌握、及时协调解决，服务群众意识不强，造成了不良影响，社区支部书记蓝日星受到党内警告处分。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,379,'2017-12-17 16:19:00','2017-12-17 19:18:21','65',0),(2982,'印尼专家：西爪哇6.9级地震为印澳版块与欧亚板块碰撞引起','罗攀','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>雅加达12月17日电 印尼气象、气候和地球物理局地震和海啸中心负责人穆罕默德・里亚迪(Mochammad Riyadi)称，当地时间15日23时47分(北京时间16日零时47分)发生在印尼西爪哇的6.9级地震，是由于印度―澳大利亚板块与欧亚板块碰撞造成的俯冲带活动所引起的。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　据印尼官方安塔拉通讯社17日报道，穆罕默德・里亚迪在一份声明中介绍，由于印度―澳大利亚板块在爪哇岛南部地区与欧亚板块碰撞形成的俯冲带活动，引发了本次地震。</p>\n<p>　　据印尼气象、气候和地球物理局修正的数据，本次里氏6.9级地震震中位于西爪哇Kawalu西南42公里处，南纬7.75度，东经108.11度，震源深度107公里。</p>\n<p>　　地震发生后，当地曾迅速对西爪哇南部和中爪哇地区启动了海啸预警。</p>\n<p>　　“从靠近震中的潮位站记录观察，当时的海平面并没有上升记录。为此，印尼气象、气候和地球物理局2时26分撤销了海啸预警”。穆罕默德・里亚迪提醒民众“保持冷静，遵守当地减灾机构指令，关注气象、气候和地球物理局发布的准确信息，不要听信和散播谣言”。</p>\n<p>　　另据当地媒体17日援引印尼国家减灾署发言人苏托波(Sutopo)披露的最新数据，地震已造成一名62岁男子、一名80岁老妇和一名34岁女子死亡，900多栋房屋受损、其中近50栋被摧毁。当局已为失去住所的灾民搭建好帐篷并分发救灾物资。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1623,'2017-12-17 18:27:00','2017-12-17 19:18:21','71',0),(2983,'中国总领馆：4名在尼日利亚被绑中国公民获救','于晓','新华网','<p>　　<strong>中国总领馆：4名在尼日利亚被绑中国公民获救 　</strong></p>\n<p>　　新华社阿布贾12月17日电(记者张保平)中国驻拉各斯总领馆向新华社记者证实，在尼日利亚西南部地区被绑架的4名中国公民已于16日由尼军方解救。</p>\n<p>　　中国驻拉各斯总领馆说，14日凌晨5时，4名中国渔民在拉各斯以东的莱基港附近捕鱼时被海盗绑架。海盗的船只随后驶往翁多州方向，被正在巡逻的尼日利亚海军舰艇发现。16日，尼军方和绑匪在翁多州水域发生交火，4名中国公民被解救。</p>\n<p>　　中国驻拉各斯总领事巢小良对新华社记者说，有3名中国公民在绑架事件中受伤，正在当地医院接受治疗，领馆将派人探望。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　中国驻拉各斯总领馆此前曾发布安全提醒，临近圣诞节和岁末年初，尼日利亚绑架、抢劫和偷盗等恶性案件高发，侨胞和中资企业员工应提高安全防范意识，减少不必要的外出，避免夜间出行或单独出行。若因工作需要确需出行的，应配足武装护卫，选择合理的出行路线和出行时间。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,547,'2017-12-17 18:23:00','2017-12-17 19:18:21','71',0),(2984,'东亚杯国足2平1负 里皮：韦世豪有潜力提高国足进攻','魏巍','新京报','<p><strong>　　东亚杯国足2平1负排名第三，创历史最差战绩</strong></p>\n<p><strong>　　里皮 韦世豪有潜力提高国足进攻</strong></p>\n<p>　　国足东亚杯战绩</p>\n<p>　　第一场</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　国足2比2韩国队(12月9日)</p>\n<p>　　第二场</p>\n<p>　　国足1比2日本队(12月12日)</p>\n<p>　　第三场</p>\n<p>　　国足1比1朝鲜队(12月16日)</p>\n<p>　　昨晚，国足结束了今年的东亚杯之旅。3场比赛2平1负，1场未胜创造了球队征战东亚杯历史上的最差战绩。不过，考虑到主帅里皮的目的是锻炼新人，成绩就显得不是那么重要了。里皮说他对球队的表现很满意。对于两场比赛打进3球的韦世豪，里皮赞赏有加，他认为韦世豪具备提高国足锋线攻击力的潜力。</p>\n<p>　　1</p>\n<p>　　进球靠反击 防守不稳定</p>\n<p>　　考虑到下月的U23亚洲杯以及12强赛后国足面临的更新换代问题，本届东亚杯，国足以年轻队员担纲。首场对阵韩国队，里皮一口气派上了6名今年联赛中的U23球员。第二场对阵日本队，里皮仍然坚持了这一方针，并进行了大规模轮换。并且，后防线调整为5后卫，多名首场比赛没有首发的队员都获得了机会。</p>\n<p>　　最后一场对阵朝鲜队，国足阵型变化较小。对日本队一战有较好表现的张文钊获得首发，门将继续由王大雷担任，开场阵型仍是5后卫。</p>\n<p>　　三场比赛中，国足的控球率不占上风。韩国队和朝鲜队的球风都比较硬朗，日本队的脚下技术明显占优，三支球队的整体性都强于国足。</p>\n<p>　　进攻方面，国足的进球大多通过反击获得。韦世豪在首尾两场比赛的两个进球都是利用对方失误，边中结合的结果。里皮帐下的这支国足更加强调对手失误后迅速将球传递到前场的能力，不过球队中场经验不够丰富，向前方输送的能力有待加强。</p>\n<p>　　另一方面，国足的防守压力较大。比如昨日比赛，国足让朝鲜队打入了本次比赛中的唯一进球。</p>\n<p>　　2</p>\n<p>　　新人获锻炼 表现可圈点</p>\n<p>　　今日国足将士启程回国后，队员们将在机场就地解散，有的队员要准备俱乐部的冬训，有的则将短暂休息后投入到下月的U23亚洲杯中。对于大部分球员来说，2017的征程算是结束了。</p>\n<p>　　“在东亚杯之前，我们确定了这次比赛的目标就是锻炼新人，给五六名新人参加国际比赛的经验，他们也将作为主力参加明年的U23亚洲杯，然后补充到国家队中。通过这次比赛，这些年轻球员得到了锻炼，我们也达到了目的。”谈及这次东亚杯，里皮在赛后新闻发布会上如是说。</p>\n<p>　　从锻炼新人的角度看，这次国足完成了任务。里皮表示，这些球员通过这次东亚杯积累了国际比赛经验，“之前他们很少有国际赛场的出场机会，这次锻炼对他们来说是非常宝贵的经历。”</p>\n<p>　　里皮说：“有人表现好一点，有人出场时间少一点，整体来说我对年轻球员的表现还是比较满意。他们回到U23国家队后就是主力与核心，希望他们打好U23亚洲杯，未来能够有更多的机会进入国家队。”</p>\n<p>　　里皮也对俱乐部提出了要求。“我觉得不仅是国家队，俱乐部也应当多培养年轻球员，至少应当有五六名年轻球员保持在一线队阵容中。”他说。</p>\n<p>　　3</p>\n<p>　　韦世豪两球 潜力还很大</p>\n<p>　　本届杯赛，担当进攻大任的韦世豪打入两个进球。</p>\n<p>　　“这名球员(韦世豪)在俱乐部不是主力，那是因为外援占据了上港的主力。我从他的身上看到了潜力，我需要提高国家队的进攻力，我觉得韦世豪能够给国家队进攻带来改变。”里皮说。</p>\n<p>　　韦世豪感谢了里皮的赞扬和信任，他表示此行最大的收获就是国际比赛经验。韦世豪坦言，没想到三场比赛都能获得出场机会，“作为一名国家队新人，我没想到三场都上了，希望以后能够成为国家队常客。”韦世豪说。</p>\n<p>　　另外一个受关注的球员是后防线的刘奕鸣，三场比赛刘奕鸣全部首发，且打满了270分钟的比赛。</p>\n<p>　　比赛结束后，刘奕鸣也谈到了收获，他说：“代表国家队参加东亚杯收获了很多宝贵经验，也知道了自己以后的发展方向。教练对我们提出了很多要求，让我们更自信。”</p>\n<p>　　刘奕鸣也看到了自身的不足，“今后在组织进攻方面还需要加强。”作为主力中后卫谈及防守和失球时，刘奕鸣说：“我们都不希望丢球，真的非常遗憾，最后一场没能取得胜利。”</p>\n<p>　　4</p>\n<p>　　里皮许诺言 明年动真格</p>\n<p>　　本届东亚杯，国足取得了历史最差战绩，而三个对手派出的也并非最强实力。这不能不说是一个尴尬。</p>\n<p>　　与此同时，在连续不胜后，国足在亚洲的排名也在下滑。明年4月，2019年亚洲杯即将抽签，排名也会一定程度上影响到抽签的结果。</p>\n<p>　　从长远来看，为2019年亚洲杯和2022年卡塔尔世预赛培养更多可用之才本无可厚非。不过有业内人士认为，里皮即使不招入12强赛时的主力，也可以考虑让一些更有实力的球员入选。比如张呈栋、李磊、池忠国、陈志钊、刘彬彬、吴兴涵，这些曾经入选过国足或者国足二队的球员，尤其是一些93后球员，他们也是未来国足的中坚力量。</p>\n<p>　　里皮曾表示，在亚洲范围之内，国足应该有其应有的地位，也不应惧怕任何人。从12强赛到东亚杯这整个过程来看，国足还没有什么本质的变化。</p>\n<p>　　考虑到联赛刚刚结束、球员伤病等问题，这次可供国足选择的范围不大。里皮表示，在明年3月份中国杯的时候，他将征召最强的阵容参赛。但愿届时能让广大球迷看到一个不一样的国足。</p>\n<p>　　采写/新京报记者 房亮</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1079,'2017-12-17 11:18:00','2017-12-17 19:18:22','74',0),(2985,'吕凤子故居修缮后变“豪宅” 原貌几乎荡然无存','魏巍','扬子晚报','<p align=\"center\"><img src=\"upload/image/640a23d4-a9fb-4f11-8136-a05af8fff057.jpg\"></p>\n<p>　　原先的故居(图片为吕存提供)。</p>\n<p>　　修缮后的故居。</p>\n<p>　　新修的走廊。</p>\n<p>　　新建的井亭下，除了井栏，地下根本没井。</p>\n<p><strong>　　丹阳吕凤子故居修缮后变“豪宅” 让吕家后人都认不出来了</strong></p>\n<p>　　吕凤子乃中国近现代著名画家、书法家和艺术教育家，职业教育的重要发轫者。其胞弟吕��，中国近代佛学大师；堂弟吕叔湘，我国近代汉语研究的拓荒者和奠基人，语言学界一代宗师，《现代汉语词典》的主编。丹阳吕氏三杰在丹阳可谓家喻户晓。</p>\n<p>　　为配合房地产开发，与周边环境相协调，丹阳市对三吕故居进行了修缮。11日上午，封闭修缮主体工程结束之后，吕凤子的孙子吕存及其家人走进一看，眼前的景象让他们傻了眼。故居明明是民国时期建造的乱砖泥土墙，如今却变成了明清风格的豪宅，而且故居的原貌几乎荡然无存，再也看不到凤先生当年生活的痕迹。扬子晚报全媒体记者 吉启雷 张凌发 文 张凌发 摄</p>\n<p>　　故居前世</p>\n<p>　　捐校产毫无保留，女儿想留钢琴都不行</p>\n<p>　　扬子晚报记者在采访时得知，吕凤子先生共有5子2女，吕存是其长子吕去疾所生。吕存曾在吕凤子故居生活了将近60年。他拿出一份介绍爷爷的资料给记者看：吕凤子被誉为“江南才子”，废科举后考入南京两江优级师范学堂(1907年)，师从著名学者、近代奇人、书画家李瑞清先生。毕业后留两江附中任教。1910年于上海创办近代最早的美术专科学校“神州美术社”。1912年，吕凤子倾其家产，在家乡丹阳创办私立正则女子职业学校。</p>\n<p>　　资料显示，1938年，日军铁蹄之下，江南失守，丹阳沦陷，吕凤子历尽磨难迁正则学校入蜀，后在重庆璧山创立正则艺专。其间每逢办学资金出现困难，他便创作大量书画以资校用，而无一分一厘的个人盘算。抗战胜利后，吕凤子将整修一新的校舍，无偿交付当地，然后迁蜀校回故乡丹阳，在废墟上重建正则学校；解放后，他又第二次把校产毫无保留地捐献给故乡，就连女儿想保留一架钢琴的愿望都不予满足。</p>\n<p>　　吕凤子两袖清风，就连稿酬和工资都拿来贴补校用，且多次免去困难学生的学杂住宿费。许多当年的老学生，到了白发苍苍的暮年，只要提到曾给予他们无私关爱的吕校长，都忍不住热泪横流。</p>\n<p>　　徐悲鸿、张大千、陈之佛、傅抱石、钱松岩等对他的品德和才艺均有着极高的评价。我国现代美术界卓有声望和成就的美术家刘海粟、徐悲鸿、吴冠中、李可染曾随其受学，受到他的美术教育和艺术熏染。</p>\n<p>　　故居原貌</p>\n<p>　　民国时期简易砖木结构房屋</p>\n<p>　　2014年，丹阳市对城区西桥西片区进行改造，开发商要在这里开发房地产，为了配合拆迁，吕家人同意政府对镇江市文保单位“三吕故居”进行修缮。</p>\n<p>　　扬子晚报记者在修缮方案中看到，修缮的整体布局是在保持文保单位原有建筑、院落基本格局的前提下，一方面对后院文保单位建筑本体进行修缮，对后院内部分绿化及道路铺装进行梳理更新，恢复传统的建筑及院落布局。另一方面拆除前院内建筑，按现有街巷肌理及建筑布局关系更新建筑，与后院形成有序的衔接及过渡，并在西侧更新小型传统中式院落空间，整体功能得以完善。</p>\n<p>　　原有的文保单位本体(南屋、北屋、西房)为民国时期简易砖木结构房屋，距今已近70年，破损比较严重，墙体歪闪，构件损坏较多。因此对文保单位本体进行落架大修，其余单位建筑按照与文保单位相协调的风貌样式进行更新恢复。</p>\n<p>　　对于院落环境，方案也作了说明。前院后院按原有道路肌理及建筑布局进行更新梳理，原有古树名木、古井等重要历史要素予以保留。新增西院采用传统中式风格进行营造。</p>\n<p>　　修缮之后</p>\n<p>　　泥土墙平房摇身变“明清”豪宅</p>\n<p>　　明代古井和名贵树木都不在了</p>\n<p>　　然而，就在近日修缮主体工程竣工后，吕家人前往故居参观，却突然发现，除了吕叔湘故居房屋结构没发生改变外，吕��、吕凤子故居已经完全不是原来的模样，他们简直不敢相信自己的眼睛，原先非常普通的居家小院，如今却摇身成了庭院深深的豪宅，而且也不是原先民国时期的风格，而是明清风格的建筑。青砖灰瓦，朱门花窗、亭台石砖。</p>\n<p>　　吕存告诉记者，负责施工的南京大美集团的人向他们介绍了故居的布置情况，他们竟然把吕凤子住的房与整个后院说成是吕��先生的，并在吕凤子住的房内做了展板，宣传吕��，还要为吕��做一个坐禅的地方。</p>\n<p>　　吕��先生的儿子是清华大学终身教授、核研究所所长。他说他父亲没有说过在丹阳造过房子之事。吕��是著名的美术史论家，后受大哥吕凤子的影响转研究佛学，终成著名的佛学家。他研究、整理、翻译佛学著作，懂十几国语言，英、德、日、藏、梵等，还有古老的巴利文。</p>\n<p>　　吕存说，原吕凤子故居占地1040平方米，六进房，抗战期间被毁。1946年凤先生从四川回来，重建故居。分为前后二个部分，前院一进大门一间十几平方米的小屋，给他二弟住。其它均为园地，一边是竹林，一边是桃树。中间用曲折的石板路隔开。院中间是吕凤子次子吕去病1949年委托大哥吕去疾所造四十平方米二间房。后院是丁香，紫薇、玉兰、金银花，紫滕、松柏、腊梅与二棵大桂花 ，路边有迎春花、绣球花和芭蕉树、棕树。院子的最后面是吕凤子的住处。中间是正房，西边是厨房。还有会客的西厢房，现在这一切都没有了。</p>\n<p>　　吕存越看越气愤，再细看，故居不仅多出了很多间房，而且结构也发生了改变，原先有的，现在没有了，原先没有的照壁、门楼、院落却凭空冒出来了。</p>\n<p>　　吕存告诉记者，设计修缮方案时，没有任何人征求过他们的意见。</p>\n<p>　　吕存表示，1946年，吕凤子先生从四川捐掉学校后回到老家丹阳，由于原先的房屋被日本鬼子烧毁，凤先生便重新建成了几间乱砖泥土墙的房屋，窗户都是他自己设计的摇头窗，门前有个小院子，和其他兄弟的房屋相连，从外面进来，感觉有点西洋风格。</p>\n<p>　　吕存还说，院内一口600年的明代古井，一直都有清澈甘甜的井水，这口井不仅养育了吕家人，而且也滋润着附近的居民。如今，地下已经建成大型车库，古井已经不复存在，留下的井栏已经被嫁接到丹阳美术馆东边去了，更可笑的是，新建的井亭下，除了井栏，地下根本没井。</p>\n<p>　　网友点评</p>\n<p>　　保留故居原貌，才是真正的尊重</p>\n<p>　　此事网上传开后，众多网友开始热议。“七彩荷塘”说：“至今大家仍能从照片上看到故居的简朴典雅。保留故居的原貌，保留凤先生一生为国为民克己奉公，粪土功名利禄、富贵荣华的精神品质，才是真正的尊重凤先生，纪念凤先生。不要把奢华的明代建筑放到故居里面去，歪曲凤先生的形象，给他老人家脸上抹黑。”</p>\n<p>　　相关部门回应</p>\n<p>　　设计可能有偏差 可以进行适当修改</p>\n<p>　　对此，扬子晚报记者也采访了丹阳市文化部门负责此项工程的文化管理科科长朱群，他说，吕凤子故居的房屋已经非常破烂了，墙上的乱砖有5种，整个墙体都是用泥巴糊的，家里的床和桌椅也非常破旧，很不上档次。他们拆迁的房屋总共19间，现在建设的这样漂亮不是很好吗？至于故居还是不是原样，还是在于人怎么去说，说多了不就成真的了吗？他说，改造之后，确实多出了不少进房子。可能专家设计方案时稍微偏差了一点，不过我们可以进行适当的修改。</p>\n<p>　　当记者询问，当时设计方案时，有没有咨询过吕家人，摸清楚凤先生究竟在哪睡觉，在哪吃饭？在哪儿画画？朱群说他们不清楚。</p>\n<p>　　对600年明代古井一事，朱群说，他们并不知道，也没有考证过，他说这个古井的井栏其实并不漂亮，他们还准备找个漂亮一点的井栏替代。</p>\n<p>　　如今凤先生故居的门前小院，已经看不到凤先生亲手栽下的桂花、丁香等名贵树木，而是新栽的一些风景树，在记者的追问下，朱群告知这些树木已经移到城建花木园了。</p>\n<p>　　记者在现场看到，修缮后的故居已经被打通，里面做成了展廊，不再是普通小屋了，没有房间，更没有卧室。就连原先的古井原址也变成了厕所。凤先生故居以及其他牌匾被露天堆放在院子里，有的已经碎裂。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/640a23d4-a9fb-4f11-8136-a05af8fff057.jpg',NULL,554,'2017-12-17 14:15:00','2017-12-17 19:18:22','75',0),(2986,'党的十八大以来我国经济社会发展成就述评','于晓','新华网','<p>　　新华社北京12月17日电 <strong>题：实现历史性变革 迈向高质量发展――党的十八大以来我国经济社会发展成就述评</strong></p>\n<p>　　新华社记者韩洁、齐中熙、陈炜伟、于佳欣</p>\n<p>　　这是全球瞩目的发展“奇迹”――</p>\n<p>　　世界经济持续低迷背景下，尽管面临下行压力，中国经济再上新台阶，过去五年年均经济增速超过7%，对世界经济增长平均贡献率超过30%，成为世界经济增长的主要动力源和稳定器。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　这是展现活力与韧性的“中国力量”――</p>\n<p>　　供给侧结构性改革有力推进，经济结构出现重大变革，生态环境状况明显好转，人民获得感、幸福感明显增强，中国经济已由高速增长阶段转向高质量发展阶段。</p>\n<p>　　党的十八大以来，以习近平同志为核心的党中央准确把握复杂局势，科学判断，正确决策，真抓实干，引领我国经济社会发展取得历史性成就、发生历史性变革，推动中国经济巨轮向着高质量发展目标劈波斩浪、扬帆远行。</p>\n<p>　　<strong>稳定器：中国经济实力再上新台阶</strong></p>\n<p>　　回首5年前，中国经济面临三期叠加、产能过剩等一系列矛盾交织，经济下行压力加大，外界对于中国经济“失速”“硬着陆”等悲观预测层出不穷。</p>\n<p>　　12月13日，亚洲开发银行发布最新报告，再次上调对今年中国经济增速的预期，将9月份预测的6.7%调高至6.8%。此前，国际货币基金组织已在年内四度上调中国经济增长预期。</p>\n<p>　　国际机构空前一致的良好预期，源自中国经济的优异表现。</p>\n<p>　　2016年第四季度以来，中国经济一改近年逐级下探态势，经济增速企稳回升。今年第三季度，GDP同比增长6.8%，增速连续9个季度保持在6.7%至6.9%的区间。</p>\n<p>　　党的十八大以来，以习近平同志为核心的党中央运筹帷幄，以坚强的政治勇气和战略定力，统领全国上下贯彻落实新发展理念，引领经济发展新常态，推进供给侧结构性改革，推动中国经济巨轮穿过迷雾、波浪前行。</p>\n<p>　　千磨万击还坚劲，任尔东西南北风。</p>\n<p>　　理念清晰、方法正确，就能克服困难、化危为机。</p>\n<p>　　五年来，中国经济平均增速超过7%，国内生产总值从54万亿元增长到80万亿元，稳居世界第二位，经济韧性增强。</p>\n<p>　　“2013年到2016年，中国实现7.2%的年均经济增长速度、2%的通胀率、5%左右的调查失业率，这种较高增速、较多就业、较低物价搭配的运行格局难能可贵，在世界范围内一枝独秀。”国家统计局局长宁吉�此怠�</p>\n<p>　　五年来，中国经济结构调整稳中有进，消费潜力有序释放。城镇化率年均提高1.2个百分点，8000多万农业转移人口成为城镇居民。区域发展协调性更强，城乡差距继续缩小。“一带一路”建设、京津冀协同发展、长江经济带发展成效显著。</p>\n<p>　　五年来，中国经济国际影响力更加显著。2016年，中国GDP折合11.2万亿美元，占世界经济总量的14.8%，比2012年提高3.4个百分点。中国对世界经济增长的平均贡献率超过30%，居世界第一位，成为世界经济增长的动力之源、稳定之锚。</p>\n<p>　　“中国越发展，为世界带来的机遇和作出的贡献就越大。”不久前美国《时代》杂志亚洲版的封面文章说，中国的发展进步就是世界的发展进步，中国赢了，就是世界赢了。</p>\n<p>　　<strong>动力源：历史性变革激发中国经济强大活力</strong></p>\n<p>　　当不少企业在去产能战役中艰难前行时，位于“世界钢铁第一大省”河北的河钢集团却将此作为提高竞争力的契机。</p>\n<p>　　“与其被动应战，不如主动出击。”河钢集团董事长于勇说。2017年上半年，河钢集团铁、钢产量分别降低5.02%、0.75%，销售收入和利润却同比增长6.83%和74.07%，增降之间尽显发展活力。</p>\n<p>　　以供给侧结构性改革为主线，深入推进“三去一降一补”，我国经济运行质量效益明显好转――</p>\n<p>　　超过1亿吨粗钢产能和4亿吨煤炭产能退出市场；商品房待售面积持续下降，房价快速上涨势头得以遏制；企业杠杆率稳中趋降；为企业累计减税降费2万亿元；补短板持续发力；实体经济发展势头良好……</p>\n<p>　　“供给侧结构性改革为中国经济开启了结构调整的大门，提高全要素生产率已成为改革共识。”国际货币基金组织第一副总裁利普顿这样评价。</p>\n<p>　　明者因时而变，知者随事而制。十九大报告明确指出，我国经济已由高速增长阶段转向高质量发展阶段。</p>\n<p>　　这一判断表明，一方面我国不再像过去那样盲目追求速度，另一方面历经几年艰苦的调整，我国经济结构正出现重大变革。</p>\n<p>　　五年来，经济增长的动力格局之变，为中国打开更广阔的发展空间：</p>\n<p>　　――消费贡献更加突出。今年前三季度，我国最终消费支出对经济增长贡献率达64.5%，比上年同期提高2.8个百分点，高出资本形成的贡献率31.7个百分点。告别投资依赖，消费对经济拉动作用更加凸显。</p>\n<p>　　――新旧动能持续转换。“互联网+”、大数据、人工智能……2013年至2016年，高技术产业增加值年均增长11.3%，新动能不断加强，新产业、新业态和新商业模式蓬勃发展。</p>\n<p>　　――全面对外开放深入推进。从以出口为导向到进口和出口更加平衡，“一带一路”建设拓宽中国发展空间的同时，也为世界带来更多机遇。</p>\n<p>　　……</p>\n<p>　　“过去五年中国经济发展的内生变化，归根到底靠的是坚守新发展理念，以供给侧结构性改革为主线，以改革的方式推动结构性调整，增长潜力不断释放。”中国社科院经济研究所所长高培勇说。</p>\n<p>　　惟改革者进，惟创新者强。</p>\n<p>　　党的十八大以来，以习近平同志为核心的党中央引领改革涉深水、闯险滩、啃硬骨头，改革呈现全面发力、多点突破、纵深推进的崭新局面，改革红利不断释放，经济结构转型升级，推动中国经济巨轮行稳致远。</p>\n<p>　　<strong>落脚点：人民获得感、幸福感不断增强</strong></p>\n<p>　　“丹山赤水神仙宅，布袜青鞋作胜游。”浙江余姚的四明山腹地的柿林村因曲径通幽素有“桃花源”的美誉，如今，昔日的穷乡僻壤如今已是闻名遐迩的旅游景区。</p>\n<p>　　“这几年村里搞旅游，特别是自从通了动力电后，村里民宿遍地开花，村民的人均纯收入去年超过了5万元。”余姚市柿林村党支部书记沈炳荣告诉记者。</p>\n<p>　　党的十八大以来，无论是国家顶层设计，还是百姓身边“小事”，无不诠释出人民至上的执政情怀。从人民群众关心的事情做起，从让人民群众满意的事情做起，一系列惠民举措密集出台，人民群众的获得感、幸福感不断增强。</p>\n<p>　　这是“获得感”不断增强的5年――</p>\n<p>　　――就业状况持续改善，城镇新增就业年均1300万人以上；</p>\n<p>　　――脱贫攻坚战取得决定性进展，6000多万贫困人口稳定脱贫，贫困发生率从10.2%下降到4%；</p>\n<p>　　――覆盖城乡居民的社会保障体系基本建立，人民健康和医疗卫生水平大幅提高……</p>\n<p>　　这是“幸福感”持续累积的5年――</p>\n<p>　　天更蓝、水更清、环境更优美。过去五年，中国生态文明建设和生态环境保护的决心之大、力度之大、成效之大前所未有，生态环境状况明显好转。截至今年底，我国历时两年时间实现了中央环保督察31个省(区、市)全覆盖，直接推动解决群众身边的突出环境问题。</p>\n<p>　　推行垃圾分类制度、实行畜禽养殖废弃物处理和资源化、提高养老院服务质量、规范住房租赁市场和抑制房地产泡沫、加强食品安全监管……件件民生“小事”拉近了中南海与百姓的距离。</p>\n<p>　　“以就医为例，这几年通过分级诊疗等举措，老百姓看病更加便捷。”北京儿童医院超声科名誉主任贾立群对此感受真切，百姓身边的点滴变化，印证了我们党顺应民生期盼的努力。</p>\n<p>　　利民之事，丝发必兴；厉民之事，毫末必去。人民关心的现实的利益问题，就要一件事情接着一件事情办，一年接着一年干。</p>\n<p>　　加快教育现代化，办好人民满意的教育；提高就业质量和人民收入水平；全面建成覆盖全民、城乡统筹、权责清晰、保障适度、可持续的多层次社会保障体系；坚决打赢脱贫攻坚战……</p>\n<p>　　党的十九大对未来经济社会发展作出全面部署，一幅全面建成小康社会、开启全面建设社会主义现代化国家新征程的美丽画卷正徐徐展开。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,437,'2017-12-17 17:15:00','2017-12-17 19:18:22','66',0),(2987,'一位海归农村青年的别样“羊倌路”','周驰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>湖北巴东12月17日电 题：一位海归农村青年的别样“羊倌路”</p>\n<p>　　作者 付瑞勇 段勇 董晓斌</p>\n<p>　　“我想尝试‘互联网+’养羊。”17日上午，从乌干达回国已3年多的农村青年李延寿，正在电脑前调试设备，并将摄像头安装在羊舍。过不了多久，他的客户将能通过远程监控，实时观看“私人订制”山羊的养殖情况。</p>\n<p>　　3年多前，李延寿放弃了一家中国鞋厂海外销售代表的舒适工作，回到老家，成为了一名“海归羊倌”。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　李延寿今年31岁，家住地处三峡库区的湖北省巴东县沿渡河镇樟树村。这里地广人稀、植被丰茂、溪水清冽，当地养羊的农户不少，但李延寿却是很“特别”的一个。</p>\n<p>　　他的“养羊梦”萌生于初中毕业后，因为家庭贫困，当时他已辍学在家。他想通过养羊让家庭脱贫，但由于缺少创业启动资金，养羊计划只能搁置。</p>\n<p>　　2011年，李延寿来到浙江温州一家鞋厂打工，工作勤奋。次年，他被鞋厂选中派到非洲东部国家乌干达首都坎帕拉做销售代表，每年纯收入为10万余元(人民币，下同)。</p>\n<p>　　“包吃包住，工作也很轻松，连衣服都有专人给你洗。”但李延寿却觉得这种生活“枯燥无味”，他一直都记挂着自己的“养羊梦”和家乡的发展，于是决定回国创业。</p>\n<p>　　2014年，他带着20余万元积蓄从乌干达回到了樟树村，在父母的协助下办起了养羊场。</p>\n<p>　　万事开头难，李延寿迎难而上。放羊需要场地，他求助樟树村村支书向宗明，经协调，村民们暂借他一片面积300亩的山林使用；创业资金不够，他求助结对帮扶人杨军年，申请到了5万元扶贫信贷资金。</p>\n<p>　　2016年，李延寿的养羊场出栏山羊20只，收入2万余元。李延寿说，与以前相比的收入反差，让他还是有点失落，但他相信会找到办法通过养羊增加收入。</p>\n<p>　　每到牧草种收时节，李延寿会特地请来贫困户当帮工，种草每天100元，割草每天80元，背草每天130元，希望能带动更多的乡亲增收脱贫。“小李给了我们在家门口打工挣钱的机会。”贫困户谢丛燕说。</p>\n<p>　　为养好山羊，李延寿自学了种植牧草、培育种羊、注射疫苗等养殖技术。他还自建青贮池，将收购的玉米秸秆、花生秧等进行发酵，解决了山羊过冬进食难题。</p>\n<p>　　独特的海外工作经历，带给了李延寿不同于普通养羊户的眼界。他不仅会用微信朋友圈来推介自己的山羊生意，而且还根据当下的“互联网+”趋势，开创了网络“私人定制”养羊。</p>\n<p>　　李延寿告诉记者，在“私人定制”模式下，客户先在线选好心仪的山羊，然后他和家人负责喂养，喂养、出栏的全过程都有视频监控，客户可以随时观看直播。</p>\n<p>　　“今年可以出栏30多只，明年可以出栏70多只，一天总是比一天好。”李延寿说，从海外上班到坚守深山养羊，他相信自己的“羊倌路”正在绿水青山中越走越宽。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,452,'2017-12-17 14:45:00','2017-12-17 19:18:22','65',0),(2988,'澳门第十三届全国人民代表大会代表选举会议主席团公告','于晓','新华网','<p>　　新华社澳门12月17日电</p>\n<p style=\"text-align: center;\"><strong>　　中华人民共和国澳门特别行政区第十三届全国人民代表大会代表选举会议主席团公告</strong></p>\n<p style=\"text-align: center;\"><strong>　　(第三号)</strong></p>\n<p>　　中华人民共和国澳门特别行政区第十三届全国人民代表大会代表选举会议于2017年12月17日举行第二次全体会议，选举澳门特别行政区第十三届全国人民代表大会代表。按照《中华人民共和国澳门特别行政区选举第十三届全国人民代表大会代表的办法》的规定，会议选出了12名中华人民共和国澳门特别行政区第十三届全国人民代表大会代表，确定了3名未当选为代表的候选人在代表因故出缺时的递补顺序。现将选举结果予以公布：</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　一、当选为中华人民共和国澳门特别行政区第十三届全国人民代表大会代表的名单(12名，按繁体字姓名笔划排列)</p>\n<p>　　吴小丽(女) 何雪卿(女) 林笑云(女) 施家伦 高开贤 容永恩(女) 陆波 崔世平 贺一诚 黎世祺 刘艺良 萧志伟</p>\n<p>　　二、未当选为中华人民共和国澳门特别行政区第十三届全国人民代表大会代表的候选人递补名单(3名，按得票多少排列)</p>\n<p>　　何敬麟 冯家辉 黄仁民</p>\n<p>　　根据《中华人民共和国澳门特别行政区选举第十三届全国人民代表大会代表的办法》的规定，主席团已将以上选举结果报送全国人民代表大会常务委员会代表资格审查委员会，进行代表资格审查。全国人民代表大会常务委员会将根据代表资格审查委员会提出的报告，确认代表资格，公布代表名单。</p>\n<p style=\"text-align: right;\">中华人民共和国澳门特别行政区第十三届全国人民代表大会代表选举会议主席团</p>\n<p style=\"text-align: right;\">2017年12月17日</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1671,'2017-12-17 17:37:00','2017-12-17 19:18:22','69',0),(2989,'朋友酒驾被查男子开车去帮忙 忘了自己酒驾还没驾照','于晓','扬子晚报','<p><strong>　　朋友酒驾被查他开车去“救驾” 忘了自己也是酒驾还没驾照</strong></p>\n<p>　　扬子晚报讯(通讯员 谢寅 穆益峰 记者 于英杰)如皋男子李某接到朋友电话得知他因酒后驾驶摩托车被抓，护友心切立即驾车前来“救驾”，谁知竟忘记自己也是喝了酒且没驾驶证。日前，这对难兄难弟被如皋警方查获。</p>\n<p>　　12月10日晚上8点多，正在查处酒驾的如皋江安交巡警中队民警发现，在路口西侧约30米处有一辆黑色越野车忽然停下。民警发现后立即将执法记录仪对准车辆取证，只见一名黑衣男子缓缓下车，不慌不忙向民警走来。民警随即将他拦住。“朋友喝酒被你们查了，我走过来看看不行吗？”见民警要求对其检查，黑衣男子百般辩解，虽然承认自己喝了酒，但却对是否驾车矢口否认。可当民警将之前取证的画面给他观看之后，这名姓李的黑衣男子顿时哑口无言。</p>\n<p>　　原来，李某当天晚上接到朋友黄某的电话，说自己酒后驾驶摩托车被交警中队检查，想让他过来帮忙“说个情”。李某知道情况后立即开车赶赴现场，快到路口时，突然意识到自己也喝了不少酒，心慌了起来，此时想逃跑已来不及。经民警现场呼气检测，李某体内酒精含量达53mg/100ml，而好友黄某达76mg/100ml，二人均属于酒后驾驶非营运机动车。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　让人大跌眼镜的是，李某被查时尚未取得机动车驾驶证，科目二考试三次均未通过，之后他就放弃了，但还是敢开车上路。</p>\n<p>　　日前，李某因未取得驾照驾驶非营运机动车、饮酒驾驶，被如皋市公安局予以罚款2000元的行政处罚。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,35,'2017-12-17 14:22:00','2017-12-17 19:18:22','65',0),(2990,'好消息！WIFI全覆盖今后将推广到全部高铁','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>北京12月17日电(记者 李金磊)中国铁路总公司副总经理、党组成员黄民17日在第十九届北大光华新年论坛上说，目前京沪高铁复兴号动车组实现了WIFI全覆盖，这个今后将推广到全部高铁。</p>\n<div style=\"text-align:center\"> \n <img alt=\"第十九届北大光华新年论坛。<a target=\'_blank\' href=\'http://www.chinanews.com/\' >中新网</a>记者 李金磊 摄\" src=\"upload/image/a43feb81-04f0-4e8e-88f3-a2a52cac5437.jpg\" style=\"border:px solid #000000\" title=\"第十九届北大光华新年论坛。<a target=\'_blank\' href=\'http://www.chinanews.com/\' >中新网</a>记者 李金磊 摄\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  第十九届北大光华新年论坛。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>记者 李金磊 摄 \n</div>\n<p>　　黄民表示，目前中国铁路乘车体验发生了很大变化，已经实现了网上订票、微信支付、自助进站、互联网订餐，并将逐步推行刷脸进站。</p>\n<p>　　记者注意到，一些火车站配备了人脸识别系统，乘客可刷脸进站，如广州南站、兰州火车站、北京西站。以北京西站为例，其北广场进站通道启用了“刷脸进站”，旅客只需按提示将二代身份证和蓝色磁卡车票放置好，保持面部正对摄像头稍作停留进行识别，票证人相符，即可通过。</p>\n<p>　　黄民还透露，2017年正推进公司制改革，目前总公司公司制改革方案已报财政部，财政部正在组织研究，准备提报国务院；18个铁路局已完成公司制改革；非运输企业改革将在年内完成。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/a43feb81-04f0-4e8e-88f3-a2a52cac5437.jpg',NULL,1401,'2017-12-17 17:42:00','2017-12-17 19:18:22','66',0),(2991,'以色列常驻联合国代表：耶路撒冷将保留首都地位','吴倩','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月17日电 据外媒报道，以色列常驻联合国代表达农近日谴责安理会分发的决议草案称，保留耶路撒冷作为该国首都的地位，因为安理会决议草案认定改变耶路撒冷当前的地位“无效”。</p>\n<p>　　据以色列常驻联合国代表在声明中称：“投票和讨论都不会改变耶路撒冷一直以来都是，今后也将会使以色列首都的事实与我们的盟友一起，我们将继续争取历史真相。”</p>\n<p>　　联合国外交人士16日表示，由巴列斯坦起草的决议草案已由埃及代表团提交给安理会。</p>\n<p>　　草案称，“任何涉及改变耶路撒冷圣城的性质、地位和人口的决定和行动都没有法律效率，是无效的，应根据安理会的决议予以废除。”</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　草案要求所有国家“不承认与有关决议不符的任何行动或者是措施”。决议草案还“根据478号决议呼吁所有国家不要在耶路撒冷圣城设立大使馆”。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,881,'2017-12-17 16:59:00','2017-12-17 19:18:22','71',0),(2992,'卫生计生委：我国大病保险覆盖城乡居民超10亿人','于晓','新华网','<p>　　<strong>卫生计生委：我国大病保险覆盖城乡居民超10亿人 　</strong></p>\n<p>　　新华社北京12月17日电(记者 王宾)记者日前从国家卫生计生委获悉，我国大力推动医保支付方式改革，截至今年大病保险覆盖城乡居民超10亿人。</p>\n<p>　　国家卫计委副主任王贺胜介绍说，目前各省大病保险政策规定的支付比例达到了50%以上，受益人员的实际报销比例提高10―15个百分点。我国推进建立贫困人口医疗兜底保障机制，部分地区农村贫困人口医疗费用实际报销比例提高到80%―90%。</p>\n<p>　　为解除低收入群体寻医问药的“后顾之忧”，下一步我国将加快将低收入家庭的老年人、未成年人、重度残疾人、重病患者等低收入救助对象，以及因病致贫家庭重病患者纳入救助范围。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　作为“十三五”时期深化医药卫生体制改革重点任务之一，我国提出要健全重特大疾病保障机制，在全面实施城乡居民大病保险基础上，采取降低起付线、提高报销比例、合理确定合规医疗费用范围等措施，提高大病保险对困难群众支付的精准性。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,712,'2017-12-17 18:04:00','2017-12-17 19:18:22','66',0),(2993,'律师界热议律师会见监狱在押罪犯新规','魏巍','新华网','<p>　　保障执业权利，彰显司法进步――律师界热议律师会见监狱在押罪犯新规</p>\n<p>　　新华社北京12月17日电(记者 白阳)一段时间以来，“会见难”是律师执业经常遇到的老大难问题。日前，司法部出台《律师会见监狱在押罪犯规定》，施行13年的《律师会见监狱暂行规定》同时废止。</p>\n<p>　　多位律师接受采访时表示，新规明确监狱依法保障律师会见在押罪犯的权利，体现了法治理念的进步，将对维护社会公平正义产生积极影响。</p>\n<p>　　新规中，保障辩护律师会见被立案侦查、起诉、审判的在押罪犯时不被监听，不派警察在场等内容，受到了律师界的广泛欢迎。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　“很多监狱没有专门的律师会见场所，我们经常是要么和家属合在一起会见，要么在一个办公室里进行，有时和当事人谈了两句就被狱警打断了。”全国政协委员、四川鼎立律师事务所首席律师施杰深有感触。今年全国两会期间，他专门就律师会见监狱服刑人员中的问题提交了一份提案，其中关于设立律师会见室、修改律师会见人数规定等建议，在此次新规中均有体现，他对此十分高兴。</p>\n<p>　　“新规体现了对在押人员从‘重管理’到 ‘重教育改造’的宗旨变化，有效保障了在押人员的基本诉讼权利，这是法治理念的重要进步。”施杰说。</p>\n<p>　　上海汇业律师事务所律师肖婧注意到，与此前的暂行规定相比，新规为保障律师正常会见出台了多条便利措施，如扩大了律师会见在押罪犯的范围，明确罪犯的监护人、近亲属可以代为委托律师等。她表示，新规解决了此前规定中一些不适应当前律师会见在押罪犯工作的实际需要，是律师执业权利的重要保障。</p>\n<p>　　北京中闻律师事务所主任吴革认为，我国刑事诉讼案件律师辩护率较低的一个重要原因，是律师的辩护权作用没有充分发挥出来，当事人对辩护的作用没有认同感。保障落实律师会见权，将有效促进庭审实质化，对正在进行的以审判为中心的刑事诉讼制度改革产生积极影响。</p>\n<p>　　“现在有些司法行政机关观念还比较陈旧，觉得律师介入会对案件侦查造成干扰，司法部的新规在这方面释放出了积极信号。律师是群众感知法律成效的直接窗口，希望有关部门进一步加强顶层设计，真正将律师纳入法律共同体，共同促进公平正义的实现。”吴革说。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,899,'2017-12-17 16:20:00','2017-12-17 19:18:22','65',0),(2994,'全国银行卡在用发卡量突破65亿张','魏巍','新华网','<p><strong>　　全国银行卡在用发卡量突破65亿张</strong></p>\n<p>　　新华社北京12月17日电(记者 刘铮)中国人民银行近日发布的报告显示，我国银行卡发卡量平稳增长。截至三季度末，全国银行卡在用发卡数量达到65.18亿张，同比增长8.35%，环比增长2.69%。</p>\n<p>　　央行发布的三季度支付体系运行总体情况报告显示，截至三季度末，全国银行卡在用发卡数量中借记卡为59.66亿张，同比增长8.10%，信用卡和借贷合一卡为5.52亿张，同比增长11.18%。全国人均持有银行卡4.71张，其中人均持有信用卡0.36张。</p>\n<p>　　三季度末，我国银行卡渗透率达48.81%，环比上升0.11个百分点。银行卡渗透率是指剔除房地产、大宗批发等交易类型，银行卡消费金额占社会消费品零售总额的比例。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>',NULL,NULL,523,'2017-12-17 16:23:00','2017-12-17 19:18:22','67',0),(2995,'中国外贸信托联合BCG发布《中国信托行业报告》','陈海峰','中国网','<p><strong>　　中国外贸信托联合BCG发布《中国信托行业报告》</strong></p>\n<p>　　2017年12月15日，中国对外经济贸易信托有限公司(中国外贸信托)与波士顿咨询公司(BCG)在北京联合主办“远见成就融耀”――《中国信托行业报告――回归本源、服务实体、动能转换、转型发展》(以下简称“报告”)发布会。发布会围绕“信托如何更好地服务实体”与“信托如何满足人民日益增长的美好生活需要”进行了深入探讨，为我国信托业发展打开了新的行业视窗。</p>\n<p align=\"center\"><img src=\"upload/image/578f744b-a6e4-4393-b237-1332bedd546c.jpg\"></p>\n<p>　　《报告》指出，近十年来，中国信托业取得长足发展，行业规模持续增长，信托在国计民生和金融市场中的影响力日益增强，但是也面临着更为复杂的环境，行业共性的困惑和挑战俱有。站在新起点上，信托业需要明确回归本源的方向，探索服务实体的模式，迎接动能转换的挑战，把握转型发展的机遇。</p>\n<p>　　聚焦信托行业三大关键问题</p>\n<p>　　回首历程，中国信托业的发展史就是一部深刻的转型史，四十年来不断变革，砥砺前行。每一次行业转型的根本推动力，都来自于所处宏观经济和金融体系环境的变化。</p>\n<p>　　报告作者之一、中化集团总会计师、中化金融事业部总裁、中国外贸信托董事长杨林表示：“过去十年，信托资产来源日益多样、业务结构不断优化、业务模式持续丰富，行业在支持实体方面一直发挥着重要作用。十九大报告提出‘深化金融体制改革，增强金融服务实体经济能力’，信托未来要更加主动地服务国家发展战略，聚焦社会主要矛盾变化，全力服务实体经济与人民美好生活需求。”</p>\n<p>　　报告作者之一、BCG合伙人兼董事总经理、大中华区金融与保险行业负责人何大勇表示：“面对中国经济减速换挡、金融监管趋严、市场竞争加剧的新环境，信托业已经来到转型发展的关键路口，可持续发展的压力不容小觑，如何打造行业的核心竞争力，将是未来几年内信托行业发展的主题。”</p>\n<p>　　对于再次站在十字路口的信托业，报告通过分析如下三个关键问题，以探寻中国信托业的未来发展之路：</p>\n<p>　　(1)信托业的“本源”是什么？</p>\n<p>　　(2)信托业未来发展应把握的机遇是什么？</p>\n<p>　　(3)信托业的核心竞争力来源和差异化竞争模式是什么？</p>\n<p>　　中国信托业的“本源”</p>\n<p>　　报告指出，把握信托业的业务“本源”，需要分析中国当前及未来一段时间信托所处市场环境中的供需关系。报告作者之一、中国外贸信托总经理伊力扎提表示：“一个行业的本源，落实于需求和供给两大方面。信托所服务的需求，应源自于实体经济发展和人民美好生活的需要，而信托所履行的核心功能，应源于信托业差异化的竞争优势。上述两者之结合，为信托业之本源。这与十九大报告提出的金融服务实体的方向高度一致。”习近平总书记在十九大报告中指出，着力加快建设实体经济、科技创新、现代金融、人力资源协同发展的产业体系。在需求视角下，中国信托业的发展机遇集中体现在产业优化、消费升级、财富管理、新型城镇化与经济出海这五大经济发展主题上；在供给视角下，信托业下一阶段的发展重点为服务信托、私募融资、资产管理与财富管理四大业务模式。基于信托业的这四大重点业务，结合当前宏观经济转型升级、金融需求迫切、金融体系尚待健全的环境，信托业的发展有三大意义：服务实体经济、服务人民群众和推动金融改革。</p>\n<p>　　中国信托业未来发展机遇</p>\n<p>　　五大主题和四类信托业务的有机结合，将为信托行业带来蓬勃的发展机会。据报告预计，直接涉及的四大业务均将实现高速发展，从投融资的总规模看，融资服务、财富管理、资产管理业务市场在未来均保持年均10%以上的快速增长。报告指出，伴随着整体市场发展机遇的凸显以及行业自身竞争力的攀升，行业有望实现效率与质量的共同提升。据测算，从2016年到2021年，信托行业总收入有望达到1945亿元，其中非自营的业务收入约为1459亿元，相对2016年实现约14%的年化增长率；行业结构呈多元、均衡发展态势，规模结构方面，合作型服务信托业务规模大幅下降，能力型服务信托和投资信托(包括主动型资产管理规模和家族信托)规模占比将会从22%提升至35%；收入结构方面，私募融资一元独大格局有所改观，占比从55%降至48%，资产管理和财富管理的收入比例从26%提升至39%。</p>\n<p>　　中国信托业未来致胜之道</p>\n<p>　　为了在多元业务格局中建立优势、站稳脚跟并从激烈竞争中突围，信托业需把握核心竞争力来源，并以此为发展突破口。报告指出，未来行业致胜核心在于服务，行业动能也将逐步由私募投行驱动转为服务驱动。以此为契机，信托业应秉持“服务+业务”的发展思路，可考虑三大差异化业务模式：“服务+融资”、“服务+财富”、“服务+资管”。</p>\n<p>　　为更好捕捉行业转型机遇，信托公司在打造上述差异化业务模式的同时，需从四大方面完善支撑体系。具体包括：推动股权多元化，推行精细化考核方式，根据既定目标合理调整业绩预期；建立健全人力资源规章制度，多举措打造良性企业文化；强化风险意识，提升风险管理专业化水平；妥善规划，逐步夯实IT能力，关注新技术应用并提前布局。</p>\n<p>　　此外，报告针对财富管理、新技术运用与消费升级等备受关注的话题展开分析，为行业创新业务转型和升级提供思路借鉴。其中，财富管理方面，行业需分阶段发展业务，宜精准定位高净值及超高净值客户，从产品平台、服务团队、服务理念等多维度发力，以发展综合财富管理和家族办公室；新技术运用方面，行业可在渠道创新、产品服务创新与运营创新层面积极探索，把握金融科技浪潮下的创新发展机遇；消费金融方面，信托公司应先聚焦机构客户的金融服务需求，甄选业务合作伙伴，不断强化风控和IT能力，逐步拓展服务领域，择机打造消费金融综合服务平台。报告最后也指出中国信托业的成功转型需要监管与法律环境的进一步支持。</p>\n<p>　　报告作者之一、BCG合伙人兼董事经理陈本强表示：“顺应行业发展趋势，信托业需真正以‘客户为中心’，实现专业能力致胜，善用‘服务+’理念，打造信托业差异化核心竞争力。这在很大程度上要求信托公司全盘规划、悉心宣贯、优化架构、坚定转型，有序推动各项战略举措落地实施。”</p>\n<p>　　据了解，《报告》撰写历时6个月，共开展内部专家访谈63次，覆盖信托行业多家公司，涵盖前、中、后台共55项议题。伊力扎提介绍：“今年恰逢中国外贸信托成立30周年。30年来，公司一直坚持探索行业发展模式，坚信战略引领和研究推动作用。本份报告的完成，既是信托实践经验与BCG全球经验的结合，更是行业集体智慧的结晶。希望本报告在行业发展面临十字路口的当下，为行业发展贡献一份力量。”“远见成就融耀”，十九大为中国未来经济发展指明了方向，也为中国信托业未来发展明确了道路。未来行业将围绕“信托如何更好地服务实体”与“信托如何满足人民日益增长的美好生活需要”，努力践行“回归本源，服务实体，动能转换，转型发展”。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/578f744b-a6e4-4393-b237-1332bedd546c.jpg',NULL,1963,'2017-12-15 15:56:00','2017-12-17 19:18:22','82',0),(2996,'唐永宏：买特许商品就是为北京冬奥做贡献','魏巍','新京报','<p align=\"center\"><img src=\"upload/image/397169b0-d7be-427b-ae8a-de8880336f8a.jpg\"></p>\n<p>　　目前预售唯一渠道为阿里巴巴旗下天猫平台所开设的北京2022特许商品官方网店。</p>\n<p><strong>　　北京冬奥组委市场开发部特许经营和票务处处长唐永宏</strong></p>\n<p><strong>　　买特许商品就是为北京冬奥做贡献</strong></p>\n<p>　　特许商品是宣传推广会徽的重要载体，广受消费者和收藏爱好者期待。同时，特许经营计划也是奥运会的市场开发计划的一部分。与赞助计划、票务计划相比，特许经营计划有哪些不同？为此，新京报记者采访了北京冬奥组委市场开发部特许经营和票务处处长唐永宏。</p>\n<p>　　是宣传北京冬奥会非常好的载体</p>\n<p>　　新京报：你作为北京冬奥组委主管特许经营的负责人，可否介绍一下北京冬奥会的特许经营计划？</p>\n<p>　　唐永宏：奥运会的市场开发计划包括赞助、特许经营、票务三大计划，特许经营计划是其中重要的一部分。北京冬奥会特许经营计划通过许可特许经营企业生产和销售带有北京冬奥会会徽、吉祥物、运动图标、口号、火炬形象等标志及相关知识产权的产品，是向特许经营企业收取特许权费的一种市场开发行为。与各级赞助企业不同，特许经营企业不享有与北京冬奥会相关联的市场营销权益。</p>\n<p>　　与赞助计划不一样，特许经营计划除了为筹办冬奥会筹集资金外，更重要的是宣传和推广北京冬奥会品牌形象、中国传统文化和主办城市特色；同时为中小企业和社会公众参与和支持北京冬奥会搭建平台。</p>\n<p>　　新京报：如何通过特许经营计划来达到宣传推广冬奥会品牌形象的效果？</p>\n<p>　　唐永宏：会徽是冬奥会和冬残奥会的最显著标识，带有冬奥会会徽等相关知识产权的特许商品对于广大社会公众来讲更为直观，也更容易接受，是宣传北京冬奥会非常好的载体。设计精美、做工精良、富有品味的特许商品将成为传播北京冬奥会承办理念的文化使者。</p>\n<p>　　进入门槛低有利于鼓励国内企业参与</p>\n<p>　　新京报：给企业和公众参与支持冬奥搭建平台，如何理解？</p>\n<p>　　唐永宏：特许经营企业通过生产和销售特许产品获得利润，按一定比例向北京冬奥组委缴纳特许权费，以此为北京冬奥会举办做出贡献。对公众来说，每购买一件特许商品就有一部分购物款作为特许权费缴纳给北京冬奥组委，也体现了对北京冬奥会、中国奥运体育代表团的支持。</p>\n<p>　　对中小企业来说，北京冬奥组委对企业有质量认证和环保认证等资质要求，但因无需像赞助企业那样投入大量资金作为赞助，进入门槛较低，有利于鼓励国内企业参与和支持北京冬奥会的热情。</p>\n<p>　　首批预售商品类别在历届中销售额靠前</p>\n<p>　　新京报：首批50余款特许商品已开始预售，这些商品是怎么挑选的？</p>\n<p>　　唐永宏：首批预售的商品包括五类，是历届奥运会特许商品中很受消费者欢迎、销售额排在前列的产品类别，也具有生产周期相对较短、产品上市较快的特点，有利于满足消费者较快拥有带有会徽标志特许产品的愿望。</p>\n<p>　　上线预售产品有限量发行2022枚的纪念徽章，制作精美的纯银纪念章，也有真丝围巾、领带和国际流行色笔记本，还有会徽发行的纪念封，售价9.9元的环保笔的笔杆由矿泉水瓶为原料，充分体现了本届冬奥会绿色环保的承办理念。</p>\n<p>　　新京报：购买特许商品如何才能买到真货？</p>\n<p>　　唐永宏：在实体店开业前，预售唯一渠道为阿里巴巴旗下天猫平台所开设的北京2022特许商品官方网店。此外，没任何渠道发售北京冬奥会特许商品。同时，每件进入市场销售的特许商品均应配带一枚防伪标签。</p>\n<p>　　新京报记者 吴为</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/397169b0-d7be-427b-ae8a-de8880336f8a.jpg',NULL,814,'2017-12-17 10:57:00','2017-12-17 19:18:22','74',0),(2997,'目击中国大飞机C919第二架机“首飞”','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>上海12月17日电 题：目击中国大飞机C919第二架机“首飞”</p>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 张素</p>\n<p>　　连绵的雨停了，17日的上海迎来晴空。中国国产大飞机C919第二架客机一飞冲天，振奋人心。</p>\n<p>　　9时许，在中国商用飞机有限责任公司总装制造中心浦东基地，人们为C919第二架客机“首飞”举行了简短的出征仪式。激昂的旋律环绕，大飞机“亲友团”站在寒风里，不断欢呼致意。</p>\n<div style=\"text-align:center\"> \n <img alt=\"12月17日，第二架C919大型客机在上海浦东国际机场完成首次飞行。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 殷立勤 摄\" src=\"upload/image/9e8b7a46-1fc5-4c17-9f8a-d2178797c96d.jpg\" title=\"12月17日，第二架C919大型客机在上海浦东国际机场完成首次飞行。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 殷立勤 摄\"> \n <input id=\"9ffa03f53d7a4a53be6246cfe69fc217\" name=\"9ffa03f53d7a4a53be6246cfe69fc217\" type=\"hidden\" value=\"http://www.chinanews.com/tp/hd2011/2017/12-17/789048.shtml\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  12月17日，第二架C919大型客机在上海浦东国际机场完成首次飞行。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 殷立勤 摄 \n</div>\n<p>　　5名机组人员整装待发。机长吴鑫曾参加C919首架机“首飞”及C919首架机转场陕西阎良的任务。10时34分，他驾驶着C919第二架客机从浦东国际机场跑道起飞。</p>\n<p>　　将时钟拨回至2017年5月5日，中国国产大飞机C919首架机在这里成功“首飞”。11月10日，C919首架机从浦东出发“千里赶考”，奔赴阎良开启科研试飞、取证试飞、运行试飞、异地试飞和多地试飞。</p>\n<p>　　C919首架机准备“首飞”用了半年，C919第二架机把这个过程缩短到1个月以内。中国商飞公司民用飞机试飞中心102架机团队负责人毛为表示，这是因为飞机状态更成熟，准备体系更充分，试飞工作的效率也更高。</p>\n<p>　　对比两架飞机的“首飞”，这次飞得更高，飞得更快，飞得更久。数据显示，首架机“首飞”最高到达10000英尺，这次超过了15000英尺；首架机“首飞”最高速度为170节，这次突破了250节；首架机“首飞”了79分钟，这次飞行长达两个小时。</p>\n<p>　　研制人员给C919第二架客机的“首飞”安排了29个试验点，包括首架机“首飞”时不曾验证的飞机襟翼收放和起落架收放。“在考虑安全性的前提下，我们对飞机的发动机进行了密集的改装。”毛为说。</p>\n<div style=\"text-align:center\"> \n <img alt=\"12月17日，上午10时34分，国产大型客机C919-10102架机在上海浦东机场第四跑道首次飞上蓝天。 <a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 殷立勤 摄\" src=\"upload/image/9581ad66-1230-45cb-8996-3a2b357f75bb.jpg\" title=\"12月17日，上午10时34分，国产大型客机C919-10102架机在上海浦东机场第四跑道首次飞上蓝天。 <a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 殷立勤 摄\"> \n <input id=\"c207126399eb45fab06a819677a5c9e1\" name=\"c207126399eb45fab06a819677a5c9e1\" type=\"hidden\" value=\"http://www.chinanews.com/tp/hd2011/2017/12-17/789048.shtml\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  12月17日，上午10时34分，国产大型客机C919-10102架机在上海浦东机场第四跑道首次飞上蓝天。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 殷立勤 摄 \n</div>\n<p>　　12时30分许，大约距离中国商飞公司民用飞机试飞中心20千米处，C919第二架客机的实时影像出现在指挥中心的大屏幕上，引起现场欢呼。12时34分，人们通过屏幕观看到飞机平稳降落在跑道上，纷纷为其完美表现而鼓掌。</p>\n<p>　　“C919大型客机10102架机首飞任务圆满成功！”中国商用飞机有限责任公司董事长、党委书记贺东风宣布。</p>\n<p>　　在随后全体参研参试人员开展的航后讲评会上，C919大型客机总设计师吴光辉的发言言简意赅。他攥着话筒，连问“大伙高兴不高兴！”“102架机‘首飞’好不好！”得到众人齐声肯定的回答，他咧嘴大笑。</p>\n<p>　　当然对他们来说，今日的圆满“首飞”仅能带来片刻轻松。C919第二架客机在2017年内还将继续飞行，它要赶在明年1月转场到山东东营以前至少再完成4个架次的试飞。</p>\n<p>　　C919的第三架、第四架、第五架和第六架客机也都“在路上”。“103架机各部段的交付正在有序进行当中，争取在2018年推出来开展试飞工作。”毛为说，从第三架客机开始，“新成员”亮相频率将越来越快。</p>\n<p>　　“‘首飞’只是万里长征第一步，证明我们具备大飞机设计能力和制造能力。后面还有很多路要走，也许三四年，也许更长。”吴光辉坚定地说，“但是我相信，中国人一定能够在不远的将来乘坐我们自己的飞机旅行。”</p>\n<p>　　值得一提的是，1903年的今天，美国莱特兄弟设计制造的第一架飞机“飞行者一号”成功试飞。这跨越逾百年的“巧合”，正是人们不断创新、勇于探索的印证。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/9581ad66-1230-45cb-8996-3a2b357f75bb.jpg',NULL,1483,'2017-12-17 17:37:00','2017-12-17 19:18:22','64',0),(2998,'中国企业助力“一带一路”节能环保建设','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>柳州12月17日电 (周潇男)近年来，在“一带一路”建设实施背景下，中国环保企业迎来“走出去”好契机。</p>\n<p>　　广西柳工机械股份有限公司副总裁王太平表示，为了使产品对环境更加友好，柳工采用仿真技术有效降低了装载机、挖掘机噪音。在装载机、挖掘机噪声指标方面达到国际领先水平，装载机司机耳边噪音低于欧盟标准6分贝。</p>\n<p>　　“为了满足欧美的排放需求，我们对15条产品线进行更新换代工作，包括装载机、挖掘机、压路机、叉车等18款产品。”王太平表示，柳工严格执行总量控制及排污许可证制度，制订减排计划，控制产品生产过程中产生的“三废”物质，逐步削减污染排量。</p>\n<p>　　“我们在研发环节注重产品的节能减排属性，在市场受到了欢迎。目前，柳工的产品已沿‘一带一路’布局60多个国家。”王太平说。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　柳工仅是中国环保企业“走出去”的一个缩影。“一带一路”建设给环保企业带来机遇，包括推进生态产业园区建设、树立优质产能绿色品牌、推动环境标志产品进入政府采购等。</p>\n<p>　　2015年，环保企业光大国际以9亿元人民币收购波兰固废处理公司。光大国际执行董事兼副主席刘�B曾在媒体见面会上表示，将以“一带一路”为契机，全面启动国际化市场。</p>\n<p>　　据普华永道会计事务所的报告显示，2016年以来，中国环保企业或其参与设立的环保基金共发起17例海外并购案，总并购金额远超200亿元人民币。2015年全年，环保领域海外并购共有15起，累计交易额近55亿元人民币。对比2015年，2016年的中国环保海外并购虽然数量上增长不多，但交易额接近翻了两番。</p>\n<p>　　中国环境保护部环境保护对外合作中心副主任方莉认为，经过近20年的快速发展，中国环保产业在传统的工程建设、环保设备及产品制造等方面形成了明显优势，环境工程建设能力和环保设备及产品制造，不但能满足国内需求，也能为“走出去”提供支撑。</p>\n<p>　　通过消化吸收国外先进技术，一批环保企业已经具备了自行设计与制造关键设备的能力，技术水平达到国际先进水平。中国环保产业发展已初具规模，形成了相对完善的产业体系，产业供给能力和技术创新能力不断提升，服务领域不断拓展。</p>\n<p>　　中国环境保护部今年发布了《“一带一路”生态环境保护合作规划》。该规划提出，到2025年，要夯实生态环保合作基础，进一步完善生态环保合作平台建设，制定落实一系列生态环保合作支持政策，在铁路、电力等重点领域树立一批优质产能绿色品牌，建成一批环保产业合作示范基地。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,387,'2017-12-17 18:51:00','2017-12-17 19:18:22','66',0),(2999,'南丰梁锦松：粤港澳大湾区和杭州湾经济区应积极合作','周驰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>杭州12月17日电 (黄慧)“生物科技是下一轮创新科技发展的重点，这一块中国很有发展潜力，如果粤港澳大湾区和杭州湾经济区在生物科技方面加强合作将会有很大的机会。”16日，在由浙江钱塘江金融研修院主办的钱塘江论坛――新金融与新产业峰会上，香港特别行政区前财政司司长、南丰集团董事长梁锦松分享粤港澳大湾区的自身实践，以期给杭州湾经济区建设提供一些发展思路。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　在梁锦松看来，生物科技是下一轮创新科技发展的重点，如果粤港澳大湾区和杭州湾经济区在生物科技方面加强合作会有很大的机会。“浙江和广东有很好的基础，可结合粤港力量发展生物科技。香港有两所全球领先的医学院和优秀的科学家，而内地有广大的市场、临床研究会和优秀的科研中心。”梁锦松说。</p>\n<p>　　面临机遇之余，粤港澳大湾区发展生物科技还存着需要解决的痛点，梁锦松表示，有些要素流通没有那么充分，比如人才，比如两地税不一样。“因而人才需要能够更便捷的流通，税相关的痛点还需研究解决。”</p>\n<p>　　他提出，其他方面粤港澳大湾区和杭州湾经济区也可以加强合作。“杭州经济发展，特别是创新产业已有很高的起点，香港可以积极支持。”</p>\n<p>　　“例如在金融方面，香港的上市条例要改，让创新企业上市。科研方面，香港的大学与浙江更紧密合作。同时在‘创新、创意和创业’方面，双方可以加强联系互相促进，对国家作出贡献。”梁锦松表示。</p>\n<p>　　据梁锦松介绍，全球最有名的湾区有三个，分别是纽约湾区，三藩市湾区和东京湾区。在他看来，虽然从总量来讲，我们是处于后进的，但粤港澳大湾区还是可以的。“特别是金融，粤港澳大湾区有一个全球领先的国际金融中心，而纽约除了国际金融中心还有权力中心，三藩市主要有名的是IT、互联网等，其次还是生物科技的中心。”</p>\n<p>　　梁锦松表示，香港着重推进创意产业中心，谈的是创新、创意和创业，而这正离不开人才。“人才驱动现代发展，特别是创新科技、创意都需要人才。”但他指出人才除了需要发展机会外，还有着很特殊的要求。“他们希望有一个比较开放、自由、安全的环境，除法制和治安外，还要有教育和医疗环境。”</p>\n<p>　　他认为这些要素吸引着高端的人才，但同时人才和资金还有一个共性。“流通量大的市场就会吸引更多的人才，资金往资金堆里面扎堆，人才也是一样。”他解释说，人才喜欢跟不同的人才走在一块，特别是跟不同行业的人，如果有思想的碰撞，很可能就会出现创新的思维。</p>\n<p>　　因而他认为香港能为粤港澳大湾区提供环境的原因正在于此，杭州湾经济区也要有这么一个环境去吸引人才，这也是杭州湾经济区需要去思考的一点。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,479,'2017-12-17 10:30:00','2017-12-17 19:18:22','69',0),(3000,'西藏文化落地海外促藏文化传播','周驰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>拉萨12月17日电 题：西藏文化落地海外促藏文化传播</p>\n<p>　　作者 周文元 石凤英</p>\n<p>　　西藏的文化艺术、民俗风情、历史纵横，为海内外文化爱好者所向往，但高海拔以及路途遥远让不少人打了“退堂鼓”。如今，中国西藏文化以展览、学术研讨等形式的海外交流日趋频繁，多国民众在家门口就可赏到雪域高原的美。</p>\n<p>　　宋明是一家名为“雪堆白”的文化机构负责人，该机构致力于西藏传统手工艺的发掘、保护、传承和传播。不久前，他将唐卡作品《二十一度母唐卡礼赞》带去法国展览，“西藏现代唐卡的绘画艺术水准让法国人惊讶不已”。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　“这幅唐卡用了哪些颜料，画中的菩萨拿着什么法器……”宋明说，来看展览的法国人，有的家里收藏着祖父辈传下的唐卡，有的学习过唐卡绘画，他们提的问题，一听就是懂行的。</p>\n<p>　　“他们从画中看到了传统，但对当前西藏唐卡的现代化革新了解较少。”在策展期间，宋明与当地藏文化学者就西藏艺术史、唐卡绘画风格流变进行研讨。原定两个小时的座谈交流，被延长至四个小时。</p>\n<p>　　在推动文化事业和文化产业发展方面，中共十九大报告指出，加强中外人文交流，以我为主、兼收并蓄。</p>\n<p>　　在面向世界，增强西藏文化的海外传播方面，西藏自治区有“新作为”。</p>\n<p>　　“西藏文化产业之窗将在全球布局。”西藏自治区文化厅产业处处长罗布次仁透露，这是展示西藏文化的窗口，更是促进西藏文化与内地及国外资源互补互动的平台。</p>\n<p>　　罗布次仁表示，预计“十三五”末期，西藏文化产业之窗数量将达到20余个，“藏文化是西藏自治区与南亚国家乃至世界分享的一份厚礼。”</p>\n<p>　　“前不久参展的唐卡，在法国除了抢眼还抢手。”宋明说，一对老夫妇看到展览的海报宣传后，开车约三个小时，从戛纳到展览所在城市尼斯，进入展馆后，一站又是三个小时。展览期间，问价的参观者不少，但作品在开展前就都已被预购了。</p>\n<p>　　近些年，走过法国、意大利、德国、波兰、尼泊尔等国后，宋明意识到，没有到过西藏的外国人，大多通过文物、书籍、影视作品了解西藏，这终归还是“隔了一层”。“这次展览在尼斯落地，让当地人既意外又高兴，他们希望以后类似的展出能多一些。”</p>\n<p>　　近期，西藏自治区文化厅正在加快“西藏文化产业之窗・尼泊尔”的挂牌运营工作。随着西藏文化产业之窗布局全球，各国民众不出国门就可欣赏到原汁原味的西藏艺术与精美用品。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1322,'2017-12-17 14:52:00','2017-12-17 19:18:22','75',0),(3001,'呼应时代 回归价值――文化类节目广受热捧赢得口碑','张楷欣','光明日报','<p>　　<strong>呼应时代 回归价值</strong></p>\n<p>　　<strong>――文化类节目广受热捧赢得口碑</strong></p>\n<p>　　今年以来，一系列文化类节目的升温，与娱乐节目的“退烧”形成鲜明对比。面对消费迭代快速的综艺市场，文化类节目以“慢”的姿态逆生长起来――《中国诗词大会》领跑春节档，最高收视率破1；《见字如面》以台带网，单期网播量近千万；《朗读者》升温迅速，话题带动有力；《家风中华》把一个个生动感人的故事带到了我们的面前；《国家宝藏》讲述27件国宝的前世今生，让馆藏国宝活起来。</p>\n<p>　　“在物质生活丰富的当下，人们依然有着强烈的精神文化需求。”中国人民大学新闻学院博士何天平对记者说。文化类节目通过寓教于乐的方式，向观众展示中国的文化基因密码，让他们感受到历史的厚重，文化的深度。因此，文化类节目的火爆并不令人意外，是时代必然，也是价值回归。</p>\n<p>　　<strong>传承传统文化是使命也是责任</strong></p>\n<p>　　中国传统文化中既有许多优秀的核心思想和理念，比如讲仁爱、守诚信、崇正义等，也包含着丰富而复杂的上古神话、经典古籍、文学作品。中国上下五千年历史源远流长，传统文化如涓涓细流滋养着每一个中国人的内心。</p>\n<p>　　“传统文化中有很多美好的东西值得歌颂，我们要以节目引领广大观众，在全社会形成文化升温而不是娱乐过热，这是电视工作者的神圣使命和责任。”国家新闻出版广电总局宣传管理司司长高长力说。</p>\n<p>　　近年来，以《朗读者》《中国诗词大会》为代表的文化类节目正是凭着对民族传统文化的“接地气”传播，真正与观众形成了共鸣，填补了观众在这个阶段的节目观看需求。在传承传统文化方面，不少电视台都在做有益尝试。正在央视播出的《国家宝藏》，将“高冷”的文物鲜活地展现在观众面前，让人们通过电视节目感受到了每一件文物背后的历史故事，领悟到了每一件文物背后的传统文化。明年将在央视播出的文化类节目《谢谢了，我的家》，将以家风为展示主体，以家族人物为承载对象，旨在通过家风文化价值的发现，对民俗文化寻根，重塑家庭观念。</p>\n<p>　　近年来，传承并弘扬中华优秀传统文化的电视节目在形式和题材上百花齐放。“这些节目不仅用新颖的形式吸引了观众的目光，还要让他们持续观看、积极思考、热烈讨论，感染并鼓舞人心，指引前行的方向。”《朗读者》的制作人董卿说，“我们通过谈话把朗读者的个人生命体验建构起来。嘉宾在舞台上还原成诚恳的朗读者面貌，在我要读而不是我会读的主动表达中，完成‘以文学之名叩问生命’的使命。”</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：央视热播栏目《朗读者》的“朗读亭”现身南京。黄志毅 摄\" src=\"upload/image/b7ed5173-7ab4-4c13-9f4a-424f7c2dc2cd.jpg\" title=\"资料图：央视热播栏目《朗读者》的“朗读亭”现身南京。黄志毅 摄\"> \n <input id=\"56746c9ab707440e991985f86485f32d\" name=\"56746c9ab707440e991985f86485f32d\" type=\"hidden\" value=\"http://www.chinanews.com/tp/hd2011/2017/04-25/735725.shtml\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：央视热播栏目《朗读者》的“朗读亭”现身南京。黄志毅 摄 \n</div>\n<p>　　<strong>观众需要文化类节目浸润心灵</strong></p>\n<p>　　在过去很长一段时间里，我国文化类节目同质化严重的现象一直为业界所诟病。益智问答类节目《幸运52》《开心辞典》火爆荧屏后，一批形式大同小异的作品蜂拥而至；当《百家讲坛》红极一时，类似模式的讲座类节目又纷纷抢滩市场……相同类型题材节目的扎堆播出，不仅使观众产生审美疲劳，也让创作陷入思维窄巷。令人欣喜的是，这一问题近两年得到了明显改善。文化类节目在题材样式的拓展、文化内涵的挖掘、制作理念的创新等方面均实现突破。</p>\n<p>　　其实，观众需要文化节目浸润心灵。不少网友都表示，虽然博物馆没少去，不过每次去都是走马观花，并没看懂什么。珍藏在博物馆里的文物，看上去与自己平日的生活没有什么关联，但近期引发热议的节目《国家宝藏》，将国宝变得触手可及，真正让观众感受到了每一座博物馆中的民族文化基因，每一件历史文物中永不断线的生命轨迹。这档节目另辟蹊径，将文物背后的故事与历史，通过表演戏剧故事的形式，以一种更生动、更直接、更为观众所接受的方式传播出来。在首期节目播出后，故宫文物各色釉彩大瓶，就因其所体现的乾隆“农家乐审美”在网上火了一把。通过故宫志愿者张�`的生动解说，网友又了解到这件国宝瓷器背后的景德镇匠人工艺和大清盛世。“文物不再是冷冰冰的，隐藏在其背后的是我们祖先无与伦比的生活智慧，是流淌在我们血脉里的民族文化基因。”北京交通大学学生李政说。</p>\n<p>　　除《国家宝藏》外，《见字如面》也是热议度比较高的文化类节目。在《见字如面2》第五期节目“热血”篇中，赵立新以其极具感染力的舞台表现把林觉民《与妻书》的悲怆深情演绎得淋漓尽致。有网友评论：“赵立新的声音充满了情感，偶尔哽咽停顿直击人心。”此外，《见字如面2》对于信件进行现代化解读，通过通俗易懂却精准考究的现代话翻译，更能让年轻受众生动感知信件背后历史人物的精神高度，深刻体会那些值得传承的思想价值。</p>\n<p>　　<strong>在“有意义”与“有意思”中寻求平衡</strong></p>\n<p>　　今年7月，国家新闻出版广电总局《关于把电视上星综合频道办成讲导向、有文化的传播平台的通知》中提出，鼓励电视上星综合频道在黄金时段增加公益、文化、科技、经济类节目的播出数量和频次，倡导鼓励播出具有中华文化特色的自主原创节目。</p>\n<p>　　“从中华传统文化中汲取灵感和创意，努力做到‘有意思有意义’，这不只是对广电管理部门‘指挥棒’的被动响应、机械落实，更是各大卫视电视节目主动突破、持续创新的必然选择。”中国传媒大学文科科研处副研究员邓文卿对记者说。</p>\n<p>　　文化类节目如何做到既“有意思”又“有意义”，无疑是在内容方面推陈出新。谈及创新，故宫博物院院长单霁翔说：“不创新，没有一家博物馆的大门能够开得下去。”因此，央视打造的《国家宝藏》联合故宫和八大国家级重点博物院的行业突破，以近乎难以抉择的筛选条件――每家博物馆只能选出一件宝藏入主特展，用创新的节目形式抓住观众的心。《中国成语大会》总导演关正文说：“在制作《中国成语大会》的时候，我们强化了娱乐性的设计，释放了主持人活跃现场的能力，趣味编组了多样化的题目，鼓励选手与主持人互动，调动了嘉宾的状态，后期更是强调保留现场真实的欢乐气氛，并通过‘花字’强化了知识性和趣味性，把内在的文化担当转化为更轻松的外在形式，让大家在快乐中获取营养。”用轻松的外在形式承载深沉的内在担当，是这类节目取得成功的根本保障。</p>\n<p>　　中央财经大学文化经济研究院院长、北京大学文化产业研究院研究员魏鹏举认为：“未来文化类综艺发展会更加注重寓教于乐，教育价值是核心目标，娱乐性为重要手段。因为当今观众的注意力和精力十分有限，观众渴望轻松愉快的节目，并会被这样的节目吸引。”</p>\n<p>　　如今，多档节目合力再掀荧屏“文化热”。这些节目在表达方面更“综艺化”，从节目形式到邀请嘉宾都增加了综艺元素。但值得留意的是，节目的“综艺化”并不意味着娱乐化，节目的“有意思”和“有意义”并不矛盾。节目内容上还应该继续保持严谨的态度和深厚的文化底蕴，只不过通过年轻的表达方式，让节目有趣生动，从而用亲近的方式抓住观众的心。文化类综艺节目的发展，是对观众当下文化需求的洞悉，是时代的必然，也是价值的回归。</p>\n<p>　　(本报记者 牛梦笛 本报通讯员 容葶婷)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/b7ed5173-7ab4-4c13-9f4a-424f7c2dc2cd.jpg',NULL,1348,'2017-12-17 03:23:00','2017-12-17 19:18:22','75',0),(3002,'重庆大学牵手京东阅读推出全新电子图书服务模式','陈海峰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月15日电&nbsp;今天，重庆大学图书馆逸夫楼“重庆大学文库”召开的“重庆大学-京东阅读战略合作会议”上，重庆大学与国内知名的电子图书服务商京东阅读达成战略合作，这也是京东阅读首次正式宣布与高校图书馆合作，两者的强强合作将为高校读者带来全新的电子图书阅读体验。重庆大学副校长孟卫东教授、图书馆馆长杨新涯、京东图书文娱数字阅读部总经理罗卿及京东阅读战略合作伙伴――图书馆资源和系统服务商北京汇云博图科技有限公司总经理刘义勇等出席和见证了本次战略合作签约仪式。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　据业内人士介绍，高校图书馆经过十多年的大规模数字化建设，均拥有了海量的数字资源。电子图书是数字资源保障体系的重要组成部分，目前大型图书馆的电子图书通常超过100万种，但因为版权的原因，这些电子图书基本上都是3年前的回溯图书，很难满足者对新书、热门书的阅读需求。</p>\n<p>　　京东阅读是国内知名的电子图书服务商，一直致力于为用户提供最新的正版电子书，并提供数字阅读和移动阅读服务。京东数字阅读总经理罗卿介绍，京东阅读依托京东集团的平台优势，与全国各出版社均有合作，能快速解决电子图书版权问题，未来，京东阅读将建成全国最大的电子图书版权平台，为读者提供最新最热的图书阅读服务。今年京东阅读在个人版的基础上专门推出了京东阅读校园版，目前提供20多万种电子图书畅读，涵盖经济金融、社会科学、文学、管理、科技、历史、医学、教辅等二十多个大类，每周有新书更新，支持手机和电脑网页等多种方式阅读，并配套提供电子墨水屏阅读器，解决了高校图书馆对新书、热门书的迫切需求。而对于此次与重庆大学的强强联手，罗卿表示：“此举，标志着京东数字阅读正式进入图书馆行业，我们将通过重庆大学在图书馆业界的影响，以及京东在电子书行业的影响力，将进一步提高京东阅读在读者、媒体、潜在客户中的知名度。”</p>\n<p>　　会上，重庆大学图书馆馆长杨新涯和京东图书文娱数字阅读部总经理罗卿共同签署了战略合作协议。重庆大学副校长孟卫东对双方的合作表示高度认可：“此次合作，可以更好地丰富馆藏，为读者提供更好的条件。开展大数据分析，通过对读者的阅读习惯进行分析，提取阅读特征，可以为读者推荐好书，为读者提供更好的服务，帮助学生成长成才！希望以后进一步深化合作，为重庆大学40万校友提供阅读服务。”</p>\n<p>　　重庆大学图书馆馆长杨新涯介绍，本次与京东阅读的战略合作包括面向全校师生的电子图书阅读、读者在京东商城上订购图书由图书馆买单流程的打通、电子图书的长期保存服务等，有效解决了目前电子图书出版行业和图书馆行业的有效对接问题，借助京东的社会化平台，为读者提供更多、更新的电子图书，并逐步向校友群体拓展。此次合作将大大地弥补国内图书馆电子图书新书的服务短板，有利地促进重庆大学“书香重大”建设，标志着重庆大学图书馆的文献服务将迈上一个全新的台阶。</p>\n<p>　　重庆大学是教育部直属的全国重点大学，创办于1929年，是国家“211工程”和“985工程”重点建设的高水平研究型综合性大学，是国家“世界一流大学建设高校(A类)”，以机电、能源、材料、信息、生物、经管、建筑、土木、环保等学科优势而著称。</p>\n<p>　　重庆大学图书馆是中国西部地区最大的图书馆之一，建筑面积达6.6万平方米，5千多阅览座位，累计实体馆藏为453万余册，在建筑、管理、机械、电气、环境、冶金、信息等学科领域具有馆藏特色。2017年图书馆文献资源经费超过3000万，资源保障能力极强，是西部地区最大的数字文献中心之一。图书馆长期致力于数字图书馆的理论研究与实践，在自动化系统、图书馆管理系统和智慧图书馆等领域的研究与应用处于国内领先水平。</p>\n<p>　　据悉，京东阅读高校业务已全面与北京汇云博图科技有限公司合作，汇云博图是图书馆行业内知名的系统和资源服务商，为重庆大学、中南大学、湖南大学、华南理工大学、武汉大学、山东大学、哈尔滨工业大学等图书馆提供了图书馆大数据分析及智能信息发布服务，与长沙市图书馆、京东图书合作的“云馆藏”项目，将电商图书平台接入图书馆馆藏，首次用“互联网+”的理念实现了读者决策采购(PDA)，本次合作将汇云博图的行业资源优势与京东阅读的品牌及产品优势融合，将引领图书馆数字阅读的变革。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,880,'2017-12-15 15:57:00','2017-12-17 19:18:22','82',0),(3003,'巴基斯坦一教堂遭袭击致多人死伤 两嫌犯已死亡','周驰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月17日电 据“今日俄罗斯（RT）”电视台报道，当地时间本月17日下午，两名自杀式袭击者袭击了位于巴基斯坦西南部的一个教堂，造成至少五人死亡、十多人受伤。</p>\n<p>　　据报道，此次袭击发生在该国西南部的奎达市。该巴方内政部门官员称，犯案的其中一人已被击毙，另一人应已在爆炸中身亡。</p>\n<p>　　事发时，奎达的一座教堂突然发生爆炸，并伴有持续交火声。爆炸发生后，当地警方及军方人员已经到达现场，并拉起警戒线。</p>\n<p>&nbsp;</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>',NULL,NULL,513,'2017-12-17 16:26:00','2017-12-17 19:18:22','71',0),(3004,'吹沙造地筑梦南亚 探访中国“万顷沙”号耙吸吹沙船','魏巍','新华网','<p>　　新华社科伦坡12月17日电 <strong>记者手记：吹沙造地 筑梦南亚――探访中国“万顷沙”号耙吸吹沙船</strong></p>\n<p>　　新华社记者 朱瑞卿 唐璐</p>\n<p>　　斯里兰卡首都科伦坡海边的加尔费斯草地广场椰影婆娑，游客如织。旁边的港口城施工现场一派繁忙，三艘从中国远道而来的耙吸吹沙船正在夜以继日将科伦坡港口城变为现实。记者16日登上了其中的“万顷沙”号，一探这填海造地“神器”。</p>\n<p>　　科伦坡港口城是中斯两国在“一带一路”倡议下务实对接的重点项目。记者登船时，“万顷沙”号刚刚从科伦坡近海取沙回来，船的沙仓里装满沙子，船员们正在紧张忙碌做喷沙的最后准备工作。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　已有25年工作经验的“万顷沙”号船长林岳深告诉记者，“万顷沙”号每天都要去指定区域取沙，取完后驶回工地，将沙子吹到海里。整个完成一次取沙、吹沙需要4小时左右。船员实行三班倒，24小时连续作业。</p>\n<p>　　正在和林船长交谈着，一位船员跑来汇报，吹沙准备工作已经就绪。船长一声令下，从海底取来的黄沙伴着海水从出沙口喷涌而出。从船上看，黄沙的形状好似一道架在“万顷沙”号和港口城施工现场之间的彩虹。</p>\n<p>　　林岳深说，需求不同，吹沙方式也有所不同。最快的是开底直抛式，打开沙仓底部的舱门将沙子直接卸在海上，卸完一船沙只需10多分钟。如果想要将沙子吹得远些，一般选择接管吹沙，时间较长。当天选择的是虹喷式，这也是最常见的吹沙方法，整个过程需要60分钟左右。</p>\n<p>　　吹沙的时间还长，林岳深带着记者向“万顷沙”号船尾走去。在不远处的海域还有一艘船正在吹沙作业。林岳深说，这艘耙吸吹沙船是我国全自主生产的“新海凤”号。除了见到的这两艘，还有亚洲最大吹沙船“浚海1”号也参与这个项目的作业，这三艘吹沙船正在将港口城填海造地的梦想变成现实。</p>\n<p>　　来到船尾，这里悬挂着吸沙耙，好似《变形金刚》电影里的机器人手臂一般。船长说，到达近海指定的取沙地点后，悬挂在船翼两侧的吸沙耙将伸入海底，将海底的沙子通过管道吸入沙仓。“万顷沙”号作业一次最多可吸入6000立方米的沙子，每月计划作业量达90万立方米。</p>\n<p>　　说着说着，“万顷沙”号已完成了这一次的喷沙作业。记者即将下船，而“万顷沙”号也将再赴距离吹填区约20公里的指定取沙区。</p>\n<p>　　回到岸上，港口城项目开发投资单位中国交通建设股份有限公司(下称“中国交建”)广州航道局南亚分公司总经理刘志告诉记者，港口城项目2014年9月开工，虽然中间曾停工过一段时间，但目前整体填海造地工程进展顺利。</p>\n<p>　　刘志说，来自中国的耙吸吹沙船不仅在为科伦坡港口城的美好明天奠定基础，还证明了中国近年来填海造地技术的发展。中国交建如今作为全球最大的疏浚公司，有能力承担全国乃至全世界港口、航道的疏浚业务。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1557,'2017-12-17 13:54:00','2017-12-17 19:18:22','64',0),(3005,'通俄门调查：特朗普团队指检方非法取得电子邮件','吴倩','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月17日电 据“中央社”报道，一年前为了特朗普入主白宫成立的交接小组于本月16日称，调查2016年俄罗斯是否干预美国大选、也就是所谓“通俄门”事件的特别检察官，违法取得交接团队的数万封电子邮件。</p>\n<div style=\"text-align:center\"> \n <img alt=\"当地时间6月1日，美国总统特朗普在白宫宣布，美国将退出《巴黎协定》。他同时强调，美国也许会重返有关应对气候变化的协定，但前提条件是协定必须要对美国更加“公平”。 \n\n<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 刁海洋 摄\" src=\"upload/image/969e815d-3ce1-4890-8c63-b2c642fe96d3.jpg\" title=\"当地时间6月1日，美国总统特朗普在白宫宣布，美国将退出《巴黎协定》。他同时强调，美国也许会重返有关应对气候变化的协定，但前提条件是协定必须要对美国更加“公平”。 \n\n<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 刁海洋 摄\"> \n <input id=\"3f3ba8152cc4453f9147f87de046e476\" name=\"3f3ba8152cc4453f9147f87de046e476\" type=\"hidden\" value=\"http://www.chinanews.com/tp/hd2011/2017/06-02/744679.shtml\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：美国总统特朗普。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 刁海洋 摄 \n</div>\n<p>　　据报道，交接团队顾问朗霍弗(Kory Langhofer)致信国会委员会表示，特别检察官穆勒(Robert Mueller)团队，不当收取了联邦政府机关总务署(GSA)交付的电子邮件。</p>\n<p>　　根据路透社取得的信件副本，总务署员工违法把交接团队不公开的资料交给特别检察官办公室，包括特许不得揭露的讯息。信中指出，这些资料内有“数万封电邮”。</p>\n<p>　　据悉，在特朗普去年11月赢得美总统大选到1月就职期间，交接团队使用总务署(GSA)设施内的办公室工作。</p>\n<p>　　报道称，穆勒调查俄罗斯是否干预大选，以及特朗普和竞选团队成员是否勾结莫斯科之际，特朗普团队提出此项指控，让特朗普支持者与穆勒办公室日益加深的摩擦更加严重。</p>\n<p>　　白宫发言人桑德斯(Sarah Sanders)被问到对此事的看法时表示：“我们会继续和特别检察官充分合作，希望这程序能在近期完结。”而总务署和特别检察官办公室尚未响应记者要求对此置评。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/969e815d-3ce1-4890-8c63-b2c642fe96d3.jpg',NULL,180,'2017-12-17 15:06:00','2017-12-17 19:18:22','71',0),(3006,'引力波追踪者获颁“复旦-中植科学奖” 五位诺奖得主齐聚上海','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>上海12月17日电 (记者 陈静)17日，第二届“复旦-中植科学奖”颁奖典礼暨第三届“复旦科技创新论坛”在上海开幕。世界一流科学家齐聚一堂，论道全球科技前沿和创新趋势。</p>\n<p>　　2017“复旦-中植科学奖”获奖人、本年度诺贝尔物理学奖得主雷纳・韦斯(RainerWeiss)，基普・索恩(KipStephenThorne)和巴里・巴里什(BarryClarkBarish)共同出席颁奖典礼并发表联合报告《LIGO与引力波的发现》。</p>\n<p>　　当日，著名实验物理学家、1976年诺贝尔物理学奖得主、“复旦-中植科学奖”评奖委员会主席丁肇中宣读第二届“复旦-中植科学奖”获奖者名单。1985年诺贝尔物理学奖得主克劳斯・冯・克里青(Klausvon.Klitzing)，中国科学院院士、中国科技大学副校长潘建伟先后以《2018，重新定义“一千克”：计量学将迎来法国大革命以来最大变革》《打破量子工程学的壁垒》为题发表主旨报告，与现场听众一起分享了相关领域最前沿的科学研究和创新成果。</p>\n<p>　　据介绍，“复旦-中植科学奖”由复旦大学和中植企业集团于2015年合作设立，用以表彰在数学、物理和生物医学领域做出杰出贡献的全球科学家。第二届“复旦-中植科学奖”授予物理学领域的杰出科学家，已于今年7月公示评选结果，每位获奖者将获得证书与奖杯，并共享由中植企业集团捐赠的3,000,000人民币奖金。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　复旦大学党委书记焦扬，复旦大学校长、中国科学院院士许宁生，“复旦-中植科学奖”赞助方、中植企业集团董事长王伟，以及部分兄弟高校代表等出席论坛。克劳斯・冯・克里青、许宁生、王伟等向获奖者颁发荣誉证书和奖杯。</p>\n<p>　　16日晚，“复旦-中植科学奖”的三位获奖人专程到访复旦大学，与复旦大学的本科生、研究生以及复旦大学附属中学的高中生对话宇宙奥秘与科研探索。他们寄语青年学子，无论做什么研究，都要从中找到乐趣；在专注某一领域研究的同时，也要抬起头来，学习其他领域的知识，从中获得新的启示。</p>\n<p>　　作为著名科幻影片《星际穿越》的唯一科学顾问，获奖人基普・索恩还来到《星际穿越》观影会现场，为广大中国影迷揭秘影片背后的科学故事。</p>\n<p>　　“复旦科技创新论坛”由复旦大学主办，复旦大学高等学术研究院承办，中植企业集团赞助。论坛从2015年开始举办，旨在通过搭建全球视野下的交流与分享平台、聚焦全球科技前沿领域和创新趋势，为落实国家创新驱动发展战略，推动上海建设成为具有全球影响力的科技创新中心做出重要的贡献。</p>\n<p>　　本届论坛为期三天，由主论坛和三个分论坛组成，主题聚焦于大数据、量子物理和创新创业等多个领域。活跃在世界各地学界、商界和政界的400余位嘉宾参加了本届论坛。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1062,'2017-12-17 18:06:00','2017-12-17 19:18:23','65',0),(3007,'“中国鹤湖”今年已迎来近3.6万只越冬候鸟','罗攀','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>合肥12月17日电 (记者 赵强)经安徽省池州市升金湖国家级自然保护区科研人员监测，从西伯利亚等北方地区飞来的35921只越冬候鸟近日飞抵该区。</p>\n<div style=\"text-align:center\"> \n <img alt=\"1月9日，安徽池州升金湖保护区首次出现珍稀候鸟雪雁，刷新了保护区鸟类物种分布的新记录，雪雁外形与大雁相似、周身雪白。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 尹莉 摄\" src=\"upload/image/1fe3e6d6-670a-45c6-8a9b-99c0ec82998e.jpg\" title=\"1月9日，安徽池州升金湖保护区首次出现珍稀候鸟雪雁，刷新了保护区鸟类物种分布的新记录，雪雁外形与大雁相似、周身雪白。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 尹莉 摄\"> \n <input id=\"815b06fb56a248d48b580093adb4edd3\" name=\"815b06fb56a248d48b580093adb4edd3\" type=\"hidden\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：安徽池州升金湖保护区出现珍稀候鸟雪雁。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>发 尹莉 摄 \n</div>\n<p>　　记者17日从该保护区管理局了解到，根据最新监测调查，该区共记录到水鸟29种35921只。“由于很多候鸟尚未达到升金湖，水鸟数量整体处于上升期。”升金湖国家级自然保护区管理局科研救护中心工作人员丁浩说。</p>\n<p>　　升金湖又称“中国鹤湖”，是安徽省境内唯一以珍稀越冬水鸟为主要保护对象的国家级湿地自然保护区，已记录越冬水禽达70余种，总数超过20万只。2015年，升金湖入围《国际重要湿地名录》。</p>\n<p>　　丁浩告诉记者，升金湖的水鸟种类以鸭科为主，占到本次调查总数量的75.72%，其中以豆雁和斑嘴鸭数量最多。本次调查发现，越冬水鸟主要集中分布在升金湖的上湖和下湖地区，中湖地区因为水位较高，缺少食物，水鸟分布局限于部分地区。</p>\n<p>　　此次调查共发现水鸟6目，其中国家Ⅰ级保护动物有白头鹤和东方白鹳，国家Ⅱ级保护动物有小天鹅、白琵鹭和白额雁。最大的水鸟种群为豆雁，其数量达到23035，占总数的64.127%。</p>\n<p>　　科研人员称，今年升金湖的越冬候鸟数与往年同期基本相符。</p>\n<p>　　据了解，为确保候鸟能够安全迁飞和栖息，安徽省池州市东至县于去年10月出台《升金湖湿地生态效益补偿工作实施意见》，要求“对升金湖保护区范围内土地流转进行补偿，引导易受候鸟侵害的耕地区域的土地承包经营者主动参与湿地保护”。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/1fe3e6d6-670a-45c6-8a9b-99c0ec82998e.jpg',NULL,2026,'2017-12-17 18:17:00','2017-12-17 19:18:23','65',0),(3008,'第十三届“北京―东京论坛”发布《北京共识》','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>北京12月17日电 (记者 应妮)由中国外文局与日本言论NPO共同主办的第十三届“北京―东京论坛”17日在北京落下帷幕。中日主办方共同发布了《北京共识》。</p>\n<p>　　《北京共识》表示，双方一致认为，当前世界形势正经历复杂深刻的变化，机遇和挑战同步增长，国家间相互依存空前上升。新形势下为进一步扩大中日两国共同利益，双方改善关系、深化合作需求进一步增强。</p>\n<p>　　论坛欢迎两国政府改善两国关系的举措，再次确认民间对话的重要作用，并达成以下共识。</p>\n<p>　　一、中日关系正面临重要历史节点与发展契机。构建健康稳定、合作共赢、和谐相处的中日关系符合两国人民利益；强化全球治理，维护和发展亚洲和平与稳定是中日两国肩负的共同责任；切实按照中日四个政治文件和四点原则共识行事，是中日关系健康稳定发展的前提。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　二、近期中日领导人多次会晤，两国间多种沟通机制逐步恢复，这对于双方积累互信、解决分歧至关重要。下一步，双方应以高度的历史使命感、政治责任感，为进一步改善两国关系，加强战略性对话与交流，营造良好环境和气氛，以更多实际行动互不构成威胁，并在此基础上力争成为更加紧密的合作伙伴，通过增进两国战略互信，为两国领导人实现互访创造条件。媒体在此过程中应发挥积极的建设性作用。</p>\n<p>　　三、世界经济领域反全球化和保护主义的趋势日盛，中日两国为构建更加开放的经济秩序展开各种合作变得更加重要。我们认为，更加开放的国际经济秩序是世界经济发展的基础。</p>\n<p>　　两国应不断提升双多边合作水平，促进贸易、投资与资本流通，探讨对接“一带一路”框架下的合作，探索第三方市场合作等多种合作形式，采取务实行动，推进具体项目。此外，中日两国都面临着经济结构调整，在这一过程中，两国企业之间的合作十分重要。</p>\n<p>　　四、中日两国在维护地区和平稳定、维护国际核不扩散体制、合作应对非传统安全挑战等方面拥有重大共同利益和共同责任。我们一致认为要坚持朝鲜半岛无核化目标，不允许出现新的核威胁，坚持以和平手段消除威胁和解决争端。</p>\n<p>　　双方应加强危机管控，尽早建立并启动中日防务部门海空联络机制。</p>\n<p>　　五、论坛再次明确45年前中日邦交正常化在新时代、新形势下的重大积极意义。我们将基于本次对话取得的重要成果，积极行动，迎接明年中日和平友好条约缔结40周年。我们再次明确，承担民间对话的历史使命，坚持不懈努力，为推动中日关系稳定健康发展发挥建设性作用。</p>\n<p>　　为期两天的讨论中，来自中日两国政治、经济、外交、安全、媒体等各个领域的代表围绕“中日共建更加开放的世界经济秩序与维护亚洲和平”这一主题展开了务实交流和深入研讨，为中日关系的持续改善与健康发展提供了许多宝贵的真知灼见。北京―东京论坛由中日双方于2005年共同发起，已成为中日两国间高层次民间对话和交流的重要平台。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,2002,'2017-12-17 18:57:00','2017-12-17 19:18:23','64',0),(3009,'印尼熊德龙：“娘家”进入新时代 海外华人的腰杆更直了','吴倩','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>雅加达12月17日电 题：(众说十九大)印尼熊德龙：“娘家”进入新时代 海外华人的腰杆更直了</p>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 林永传</p>\n<p>　　“‘娘家’富起来、强起来，进入了一个新时代，海外华侨华人的头也抬得更高了，腰杆也挺得更直了”。印尼工商会馆中国委员会执行主席熊德龙在接受<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者专访时，道出了他的心声。</p>\n<div style=\"text-align:center\"> \n <img alt=\"8月22日，海外媒体代表印尼《国际日报》报业集团董事长熊德龙在开幕式上致辞。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 陈超 摄\" src=\"upload/image/5a048bbd-d08b-4322-afb8-fcc5f76e2bed.jpg\" title=\"8月22日，海外媒体代表印尼《国际日报》报业集团董事长熊德龙在开幕式上致辞。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 陈超 摄\"> \n <input id=\"c1a8df464db840379b3561b02291c9f4\" name=\"c1a8df464db840379b3561b02291c9f4\" type=\"hidden\" value=\"http://www.chinanews.com/tp/hd2011/2015/08-22/555394.shtml\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：8月22日，海外媒体代表印尼《国际日报》报业集团董事长熊德龙在开幕式上致辞。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>发 陈超 摄 \n</div>\n<p>　　习近平是一位亲民、果敢又有大国情怀的领导人</p>\n<p>　　“当习近平主席微笑着问‘熊先生，您现在是在美国，还是在印尼’时，我激动和钦佩的心情真是难以表达。20多年了，作为日理万机的大国领袖，还记得我们这些海外华人的名字，这是何等的亲民啊！”熊德龙回忆起2013年习近平在印尼国家博物馆出席活动时，亲切叫出几位华人名字、同他们热情交谈的细节。</p>\n<p>　　“要知道，我有幸见到习主席，已是20多年前的事了。那时还在福建省工作的他访问美国时到访美国《国际日报》，鼓励我们要讲好中国故事”。</p>\n<p>　　熊德龙介绍说，中共十八大以来，自己天天通过新闻报道密切关注和了解学习习近平的执政理念。</p>\n<p>　　“当深入农村、工厂和灾区，和人民在一起时，他是和蔼可亲的亲民领袖；在关乎党和国家前途命运、‘老虎苍蝇一起打’的雷霆反腐斗争中，他是具有坚定信念和信心的果敢将帅；当在国际舞台上为构建人类命运共同体而奔忙时，他又是拥有大国情怀的杰出领导人”。</p>\n<p>　　熊德龙说，习近平再次当选中共中央总书记，海外华侨华人无不欢欣鼓舞。</p>\n<p>　　“中国威协论”不攻自破</p>\n<p>　　“中共十九大报告明确提出，中国将坚持走和平发展道路，推动构建人类命运共同体。西方一小部分人总是乐于渲染的‘中国威胁论’已不攻自破”。熊德龙说，如今，国际社会普遍认为，同中国打交道、交朋友不但没有任何的威胁，中国还是“最可靠的朋友”。</p>\n<p>　　“中国的成功之路为各国发展树立了样板，东盟、中亚、非洲、美洲许许多多国家，已经用成果和实效证明了与中国交朋友的‘靠谱’”。</p>\n<p>　　熊德龙说，“一带一路”的倡议和实施，给沿线国家、给世界带来的好处是看得见的、实实在在的。</p>\n<p>　　熊德龙特别希望，作为亚洲大国之一的印尼，能够借助中国强劲的发展势头，借助“一带一路”建设的机遇，实施符合国情的经济振兴战略，努力实现腾飞之梦。</p>\n<p>　　“‘娘家’强起来，海外华人的腰杆也挺直了”</p>\n<p>　　“作为海外华人，我全过程关注了中共十九大。开幕式时，可以说是目不转睛地观看了习总书记作报告”。熊德龙说，当听到习近平讲到“加强海内外中华儿女大团结，齐心协力走向中华民族伟大复兴的光明前景”时，真是激动万分。</p>\n<p>　　熊德龙介绍说，1955年周恩来总理到万隆出席亚非会议期间，曾称海外华人“是从中国嫁出去的女儿，娘家人时刻在关心着你们，娘家人欢迎你们随时回来”。“如今，‘娘家’富起来、强起来，进入了一个新时代，正与世界五大洲的‘亲戚’手拉手、心连心，共圆中国梦”。</p>\n<p>　　熊德龙说，祖(籍)国的强大，让海外华侨华人“头也抬得更高了，腰杆也挺得更直了”。</p>\n<p>　　“因为我们能更加自信、更加理直气壮地为祖(籍)国和居住国的交流、合作和发展牵线搭桥，发挥‘民间外交’的作用了”。</p>\n<p>　　具有独特身世和传奇经历的熊德龙，数十年来一直以一颗特别的赤子之心，扮演着中美、中印尼友好的民间外交角色。73年前，并没有中国血统、刚出生几个月就成为孤儿的熊德龙，被一对旅居印尼的广东梅县华侨夫妇收养并养育成人。事业有成后，自称拥有“一颗百分之百中国心和一腔百分之百客家情”的熊德龙常年为中美、中印尼友好关系穿梭奔忙。1993年，他斥资收购美国华文报纸《国际日报》，开始“讲好中国故事、传递中国声音”的媒体事业。2001年回到印尼，在雅加达出版发行印尼版华文《国际日报》，目前该报已成为印尼第一大华文报。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/5a048bbd-d08b-4322-afb8-fcc5f76e2bed.jpg',NULL,1731,'2017-12-17 11:30:00','2017-12-17 19:18:23','73',0),(3010,'北京在疏解整治促提升中补建便民商业网点292个','于晓','新华网','<p>　　北京在疏解整治促提升中补建便民商业网点292个</p>\n<p>　　新华社北京12月17日电(记者 高健钧)记者从北京市商务委了解到，截至11月底，北京共建便民商业网点1210个，其中292个为疏解整治促提升补建网点。</p>\n<p>　　今年初，为在拆违建、封堵“开墙打洞”、疏解批发市场等疏解整治促提升工作中保障民生，北京以“撤一补一”为指导原则，计划建设提升1000个便民商业网点。</p>\n<p>　　截至目前，北京已建设提升蔬菜零售等7类基本便民商业网点1210个，其中新建873个，规范提升337个，超额完成年度建设任务。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　从业态看，北京建设提升蔬菜零售、超市和便利店、早餐点三类网点共占新增网点数量65%，分别为421个、201个、167个；从区域看，北京城六区新增网点541个，占新增网点总数量44.7%，其他10个区共建设提升网点669个，占55.3%。从新增网点所在区域位置看，292个是疏解整治促提升补建网点，同比占北京新增网点数量的24.1%。</p>\n<p>　　此外，北京蔬菜零售、便利店、早餐等基本便民商业服务网点在城市社区覆盖率达87.4%；基本便民商业网点连锁化率达34.9%，较上年度末提升5.5个百分点。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1695,'2017-12-17 17:54:00','2017-12-17 19:18:23','66',0),(3011,'香港奇幻电影《奇门遁甲》依托韩国技术登陆内地院线','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>太原12月17日电 (记者 胡健)由香港导演袁和平、徐克联手打造的电影《奇门遁甲》17日以韩国放映技术ScreenX版本亮相中国内地院线，试图在强手如林的“贺岁档”突出重围。</p>\n<p>　　《奇门遁甲》是年末的话题大片，宏大的制作和4.9分的网络评分，让该片的口碑在“贺岁档”遭遇冰火两重天。正因如此，依托韩国技术的ScreenX版本的加入，使电影在视听效果方面显得独树一帜。</p>\n<div style=\"text-align:center\"> \n <img alt=\"电影《奇门遁甲》ScreenX版本剧照。　片方提供 摄\" src=\"upload/image/11cb166d-70da-4379-86ac-3d0c184fd0d6.jpg\" title=\"电影《奇门遁甲》ScreenX版本剧照。　片方提供 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  电影《奇门遁甲》ScreenX版本剧照。　片方提供 \n</div>\n<p>　　记者17日在太原万达影城看到，在电影开头，巨大的陨石从太空撞击地球，观众在ScreenX的两侧银屏上体验到左右扩展的宇宙空间，甚至可以感受到陨石的速度，带来与众不同的沉浸感。</p>\n<p>　　《奇门遁甲》将1982年版的同名电影借用现代形式进行重拍，引起不少资深影迷的怀旧。“之前在网上看到过袁和平35年前的那部电影，当时的画面也很模糊。没想到如今还能看到‘八爷’(袁和平)的新作，而且还是ScreenX版本。”有观众如是说。</p>\n<p>　　电影《奇门遁甲》是由袁和平执导，徐克担任监制，讲述身怀“奇门遁甲”绝技的神秘组织“雾隐门”为拯救被外星人侵袭的世界挺身而出的冒险故事。</p>\n<p>　　2017年《长城》、《亚瑟王：斗兽争霸》、《加勒比海盗：死无对证》、《王牌特工：黄金圈》等好莱坞巨作接连以ScreenX版与观众见面，而国产片中有《大话西游3》、《西游伏妖篇》、《悟空传》等作品以ScreenX版上映。</p>\n<div style=\"text-align:center\"> \n <img alt=\"电影《奇门遁甲》ScreenX版本剧照。　片方提供 摄\" src=\"upload/image/2b9f1a2e-6c0d-4eca-bd07-a6f7201a5098.jpg\" title=\"电影《奇门遁甲》ScreenX版本剧照。　片方提供 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  电影《奇门遁甲》ScreenX版本剧照。　片方提供 \n</div>\n<p>　　2015年，ScreenX在美国拉斯维加斯亮相，初登国际舞台。此后逐步扩大了与各国院线的紧密合作。ScreenX是由韩国CJ集团研发的全世界第一个多面投影系统，通过270度多面投影将图像投影至影厅左右墙壁，让影迷获得超越电影框架的体验。</p>\n<p>　　目前，ScreenX在中国与万达、耀莱、CGV共同运营着21家影厅，此外，在美国、泰国、印度尼西亚、越南、土耳其等国家也积极扩张，截至2017年5月，全球共设有109个ScreenX影厅。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/2b9f1a2e-6c0d-4eca-bd07-a6f7201a5098.jpg',NULL,523,'2017-12-17 16:09:00','2017-12-17 19:18:23','68',0),(3012,'《挑战不可能》“复兴号”上首现“车票平衡奇观”','罗攀','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月17日电 12月17日晚八点，由央视综合频道和央视创造传媒联合推出的大型励志挑战节目《挑战不可能》第三季第五期将在央视一套准时播出。在运行最高时速350公里的“复兴号”动车组列车上，韩遂宁要用一张比羽毛更轻的车票作为关键配重，搭建两层的棕榈叶平衡系统，它能否平稳地通过南京长江大桥；汽车半只车身悬空，只用单边侧轮不足1米宽的单边桥，两位女赛车手的精彩对决，最终花落谁家；三位嘉宾与小学生联手涂鸦，医学博士严虎能否找出画作背后的真正创作者？</p>\n<p><strong>　　大国重器“复兴号”上搭建平衡系统配重是一张比羽毛还轻的火车票</strong></p>\n<p>　　来自郑州的测绘工程师韩遂宁挑战的项目是行驶在京沪铁路上的中国标准动车组列车“复兴号”上，搭建两层高的23根棕榈叶平衡系统。与以往不同的是，此次平衡的关键部件竟是一张比羽毛还轻的火车票。平衡系统通过全长1576米的南京长江大桥江面正桥，依然保持稳定，才算挑战成功。“复兴号”动车组列车的每一次转弯和变速，每一次会车甚至是轻微的晃动都让结果变得玄之又玄。李昌钰博士说：“哪怕一个小小的喷嚏、一次刹车，整个平衡系统都可能溃不成军。”</p>\n<p>　　号称“能平衡任意不规则物体”的韩遂宁，在上一季的《挑战不可能》中已有过精彩亮相，他曾让一辆摩托车直立保持平衡。而此次他将挑战的舞台拓展到了飞速行驶的“复兴号”动车组列车上。“复兴号”是我国自主研发的全世界行驶速度最快的动车组列车，运行最高时速可达350公里。本期节目中挑战空间再度延伸至“大国重器”，让观众在挑战中见证中国的发展，在中国的发展中找到挑战的机遇。</p>\n<div style=\"text-align:center\"> \n <img alt=\"董卿庾澄庆李昌钰博士观看挑战\" src=\"upload/image/117620e2-ddb1-4f65-8dc5-1ad07be4907a.jpg\" style=\"border:px solid #000000\" title=\"董卿庾澄庆李昌钰博士观看挑战\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  董卿庾澄庆李昌钰博士观看挑战 \n</div>\n<p><strong>　　女赛车手“下战书”PK单边悬空过桥苦练单边赛车特技欲求车神老爸认可</strong></p>\n<p>　　玩赛车是一项惊险又刺激的运动，女车手更是凤毛麟角。本期的挑战中，将有两位美女赛车手同台竞技，挑战悬空一半车身只用单边侧轮通过80cm宽的桥。来自广东的华婷婷玩转赛车特技，并向节目第一季的选手张伟发起挑战。</p>\n<p>　　汽车平稳行驶怎少的了四个车轮共同配合着力？侧翻起两个车轮行驶，本身就限制了驾驶员的视野，她们还要通过长100米、宽0.8米的单边桥，节目中也出现了赛车里罕见的PK谁“速度慢”的较量。正如骑自行车，在保持平衡的情况下，骑得越慢实际上越难。惯性与速度之间的博弈，考验的是两位车手的胆气和绝佳的机械操控能力。挑战现场意外连连，小撒背过身直呼“不敢看”，全程碎碎念“没问题”。华婷婷坦言，玩赛车是自己的梦想，18岁那年曾背着老爸偷开他的赛车，差点出现事故。华爸爸本人也是赛车手里的名将，曾与林志颖同队，并有自己的“华家班”赛车队，但对于女儿选择赛车事业却倍感纠结。今年29岁的她能否用实力证明自己的决心呢？</p>\n<p><strong>　　心理学专家通过3幅画找到6个人董卿：几句话就暴露了我的性格秘密？</strong></p>\n<p>　　从事绘画心理学研究的严虎博士擅长通过绘画内容分析人物性格。但是单凭几笔简单的绘画就找出对应的儿童作者，还要找出后续三位评委在画作上添加的元素，并再度指认涂鸦的作者，这听起来就有点不可思议了。严博士的观察时间只有30分钟，其后也只能通过简单有限的提问和观察，作为自己的判断线索。董卿表示十分好奇：“仅凭简单几句话，我的性格秘密就暴露了？我认为是找不出来了。”</p>\n<p>　　本次挑战有多重随机性加持，现场引来观众数次起立鼓掌。首先，随机从一百名二年级的孩子中选取二十名，其次三位嘉宾要通过“人物+地点+行为”的模式随机拟定绘画主题，由孩子们当场作画，最后嘉宾需要分别随机挑选出一幅作品，并在画作上添加自己的涂鸦。只有当挑战者准确找出三幅作品的小作者和挑选他们的嘉宾，以及嘉宾在画作上添加的内容，挑战才算完全成功。是什么经历让严博士走上“绘画心理治疗”这条路呢？严博士有一个四岁的女儿，他对于女儿的教育是否也用到类似的方法呢？</p>\n<p>　　“画像识人”将探索新的性格认知方法，《挑战不可能》这档节目在“识人”领域，或将再添一枚新星。 &nbsp;&nbsp;&nbsp;</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/117620e2-ddb1-4f65-8dc5-1ad07be4907a.jpg',NULL,1077,'2017-12-17 09:56:00','2017-12-17 19:18:23','68',0),(3013,'广西破获系列虚开发票案件 涉案金额超260亿元','魏巍','新华网','<p><strong>　　广西破获系列虚开发票案件 涉案金额超260亿元</strong></p>\n<p>　　新华社南宁12月17日电(记者 夏军)记者从广西河池市公安机关了解到，日前该市公安机关侦破系列虚开发票案件，打掉虚开发票团伙10个，抓获犯罪嫌疑人51人，涉案金额超过260亿元，涉及企业1.6万多家。</p>\n<p>　　今年6月，河池市宜州区国家税务局进行税务稽查发现，广西宾宾建材有限责任公司等11户纳税人有涉嫌虚开增值税普通发票行为。警方调查发现，今年4月至7月，当地共有22家公司涉嫌虚开发票1740份，价税合计金额1.544亿元。警方立即对该案立案侦查。</p>\n<p>　　警方侦查发现，这22家公司的法人并不是公司实际控制人，其实际控制人成立“空壳公司”套取增值税发票后，大额度、大批量对外虚开，在申报纳税期限到期前再“丢弃”原“空壳公司”，同步注册新公司，然后又疯狂对外虚开增值税发票，严重扰乱了正常市场经济秩序，造成国家税款巨大流失。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　自治区、市、县三级公安机关抽调300名精干警力成立专案组全力开展专案侦查，最终掌握了所有虚开发票犯罪团伙的犯罪证据，并统一行动对犯罪嫌疑人实施抓捕。</p>\n<p>　　警方审讯查明，今年以来，犯罪嫌疑人詹某、邱某、刘某等人，在广西河池、南宁、柳州、梧州、钦州、来宾、崇左等市，利用他人身份证或者委托中介机构注册登记“空壳公司”3126家，在没有真实货物交易的情况下，以收取0.5至1.5%“开票费”价格，为他人虚开增值税普通发票和增值税专用发票，从中谋取暴利。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,365,'2017-12-17 16:21:00','2017-12-17 19:18:23','65',0),(3014,'展示大爱无疆 歌剧《拉贝日记》再现人道主义情怀','张楷欣','北京青年报','<p>　　<strong>揭示残酷战争下的温暖情感 展示大爱无疆</strong></p>\n<p>　　<strong>歌剧《拉贝日记》再现人道主义情怀</strong></p>\n<div style=\"text-align:center\"> \n <img alt=\"歌剧《拉贝日记》剧照。图片来源：北京青年报\" src=\"upload/image/c4b6bd26-a55c-4a0b-8483-bbac3a1bf721.jpg\" style=\"border:px solid #000000\" title=\"歌剧《拉贝日记》剧照。图片来源：北京青年报\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  歌剧《拉贝日记》剧照。图片来源：北京青年报 \n</div>\n<p>　　本报讯(记者 伦兵)发生在1937年的南京大屠杀是日本侵略者惨无人道的暴行，而德国友人约翰・拉贝不仅亲历大屠杀的惨状，更与其他外国友人一起，以人道主义的名义建立安全区，以尽自己所能保护南京市民的生命，并记录下这一暴行的始末，成为后来“东京审判”的证据。</p>\n<p>　　12月13日是南京大屠杀惨案发生80周年，从国家公祭日这一天开始，连续三天，由唐建平作曲、周可编剧、伊莱嘉・莫辛斯基导演的原创歌剧《拉贝日记》在南京江苏大剧院首演。</p>\n<p>　　歌剧《拉贝日记》没有在舞台上着重刻画日本侵略者的血腥暴行，而是从当年敌机轰炸南京开始讲起，讲述德国友人拉贝和一批外国友人帮助中国人民躲避轰炸抵抗战争，救助大屠杀的幸存者，进而将自己在南京大屠杀期间的所见所闻记录下来并成为日后审判侵略者的证据。因此，该剧的英文名也叫“170天在南京”。</p>\n<p>　　作曲家唐建平介绍：“《拉贝日记》没有在舞台上展现血腥屠杀，而是通过一批国际友人的人道主义救助，展现大爱无疆。因此在音乐上，为拉贝创作了有巴赫风格的音乐，而魏特琳因为是在南京的美国人，在她的音乐里有美国国歌和中国民歌《茉莉花》的几个音符链接变化形成的音乐风格。这是描写在中国生活的一批外国友人，对演员也是一个考验，特别是拉贝的两段咏叹调，高音演唱比较难。”</p>\n<p>　　北京青年报记者在现场发现，在《拉贝日记》演出中，珍妮・魏特琳把妇女儿童保护在自己的教会学校，日本侵略者抢走妇女做慰安妇，魏特琳身心受到极度伤害，这时的咏叹调集中表现了面对残暴魏特琳独特的内心世界。而当拉贝要离开南京向市民告别时，咏叹调的唱词就是拉贝的演讲。音乐深情缓慢，一句句“感谢”，情感一步步升华，直到感谢南京人民使他看到正义的力量，现场观众禁不住热泪盈眶，报以热烈的掌声。那种跨越国界的爱温暖着劫后余生的市民，也温暖着观众。</p>\n<p>　　据悉，原创歌剧《拉贝日记》将于明年元月10日作为第三届中国歌剧节闭幕演出，再次登上江苏大剧院的舞台。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/c4b6bd26-a55c-4a0b-8483-bbac3a1bf721.jpg',NULL,378,'2017-12-17 03:12:00','2017-12-17 19:18:23','75',0),(3015,'2017国剧盛典在京录制 张若昀唐艺昕未同框','罗攀','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月17日电 12月16日，安徽卫视2017《国剧盛典》在北京录制。蒋雯丽、景甜、凯丽、雷佳音、李沁、陆毅、马伊�P、潘粤明、乔欣、秦海璐、唐艺昕、王珞丹、吴秀波、吴越、袁泉、张嘉译、张若昀等嘉宾亮相开幕红毯。</p>\n<p><strong>　　张若昀唐艺昕低调先后走红毯未同框</strong></p>\n<p>　　今年是安徽卫视《国剧盛典》举办的第十年，录制现场群星璀璨、热闹非凡。娱乐圈颇为看好的一对甜蜜情侣张若昀、唐艺昕也出席了本次活动。张若昀一身火红色长款西装十分惹眼，作为青年演员中的演技实力派，他交出《白夜追凶》《亲爱的她们》等优质剧作收获一票好评。当天唐艺昕则身穿黑色长裙，搭配烫金格纹外套，成熟干练魅力十足。遗憾的是，“高颜值CP”此次并未同框，而是非常低调先后踏上红毯。</p>\n<p><strong>　　胡一天签名意外引热议 粉丝调侃其字体太“清流”</strong></p>\n<p>　　2017下半年，胡一天饰演《致我们单纯的小美好》中傲娇医生江辰一角脱颖而出，成为炙手可热的实力新星。没想到在《国剧盛典》红毯上以黑色西装低调亮相的他，竟因一个签名引发热议。原来有现场观众PO出国剧盛典的签名背板，在一众花式字体当中，方方正正的”胡一天”三个大字格外引人注目。这下连亲生粉丝都忍不住调侃：老实人太清流！</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/58866dba-971e-4359-86f8-14ac0d1eea13.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p><strong>　　张嘉译秦海璐吴秀波 品质剧匠打造国剧经典</strong></p>\n<p>　　国剧的繁荣，得益于源源不断的新生力量，更离不开品质剧匠在电视土壤上的精耕细作。2017年当之无愧的史诗大剧《白鹿原》《军师联盟》，播出后斩获高收视、高口碑。本次国剧盛典特别邀请张嘉译、秦海璐、吴秀波等主创，他们将亲临现场交流创作经历。</p>\n<p>　　国剧十年，初心不变。安徽卫视《国剧盛典》秉持传扬国剧精品、致敬国剧匠人的精神，着力打造2017年国产电视剧的“致敬之夜”。据悉，安徽卫视2017《国剧盛典》将于2018年1月1日晚播出。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/58866dba-971e-4359-86f8-14ac0d1eea13.jpg',NULL,1162,'2017-12-17 09:57:00','2017-12-17 19:18:24','68',0),(3016,'中宣部等印发通知要求进一步加强社会主义核心价值观网上传播','魏巍','新华网','<p><strong>　　中宣部等印发通知要求进一步加强社会主义核心价值观网上传播</strong></p>\n<p>　　新华社北京12月17日电 中共中央宣传部、中央网信办、教育部、文化部、国家新闻出版广电总局、共青团中央近日联合印发通知，要求通过加强网上主旋律宣传、深化网上主题教育活动等进一步加强社会主义核心价值观网上传播。</p>\n<p>　　通知要求，要加强网上主旋律宣传，充分运用各类网络平台，广泛组织开展党的十九大精神的宣传，深入开展习近平新时代中国特色社会主义思想的宣传，开展中国特色社会主义和中国梦的宣传，开展统筹推进“五位一体”总体布局、协调推进“四个全面”战略布局的宣传。要坚持“正能量是总要求”，准确把握网络传播规律，把社会主义核心价值观的要求体现到网络宣传、网络文化、网络服务中，用正面声音和先进文化占领网络阵地，用正确的网络舆论引导思潮、凝聚共识。</p>\n<p>　　通知要求，要深化网上主题教育活动，以在全党开展“不忘初心、牢记使命”主题教育为契机，充分用好网络手段，通过网络直播、网络视频、网上交流等形式，为党员干部用党的创新理论武装头脑，搭建网上学习平台。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　通知还要求，要广泛开展网络公益活动，积极探索“互联网+公益”新模式，搭建网络公益平台，壮大网络公益队伍，形成线上线下踊跃参与公益事业新格局。要积极创作传播优秀文艺作品，推出一批网络文艺精品力作。要坚持用优秀文化充盈网络空间，不断增强广大网民的国家认同、民族认同、文化认同。要加强校园网络阵地建设，优化校园网络文化环境。要着力打造核心价值观网上传播品牌，发挥知名品牌引领网络民意的风向标作用。</p>\n<p>　　通知强调，要依法加强网络空间治理，加大对突出问题、问题网站专项整治力度，持续不断净化网上舆论生态。各级宣传、网信部门要发挥牵头抓总的作用，督促指导有关部门、单位和互联网企业将各项任务要求落到实处。教育、文化、新闻出版广电等部门要根据职责切实负起责任，依法加强监督管理。工会、共青团、妇联等群团组织和行业协会要充分调动各方力量积极参与，不断壮大社会主义核心价值观网上传播力量。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1855,'2017-12-17 15:38:00','2017-12-17 19:18:24','64',0),(3017,'保监会拟对险企采取差异化监管措施','魏巍','新华网','<p><strong>　　保监会拟禁止资产负债管理能力低的险企开展重大股票投资</strong></p>\n<p>　　新华社北京12月17日电(记者谭谟晓)记者从保监会获悉，保监会近日下发《保险资产负债管理监管办法(征求意见稿)》，拟对险企采取差异化监管措施，其中对资产负债管理能力评估结果为低的公司，将采取禁止其开展重大股票投资和上市公司收购等措施。</p>\n<p>　　征求意见稿显示，保险资产负债管理综合评级包括资产负债管理能力状况和资产负债匹配状况。保监会根据保险公司综合评分的高低将资产负债管理综合评级分为A、B、C、D四大类。对A类公司，保监会将适当给予资金运用范围、模式、比例以及保险产品等方面的政策支持。</p>\n<p>　　对评级为C类和D类的公司，保监会将采取限制性措施。其中，对C类公司，将采取风险提示、监管谈话、下发监管函、进行专项现场检查等措施；对D类公司，除可采取对C类公司的监管措施外，还将采取在一定期限内禁止申报新产品、限制资金运用形式等措施。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　征求意见稿明确，对于资产负债管理能力评估结果为低的公司，保监会不予备案其股权投资能力、不动产投资能力和衍生品运用能力，禁止其开展重大股票投资和上市公司收购。对已经取得上述相关能力备案的，视情况暂停其新增重大股票投资、上市公司收购、股权投资、不动产投资和运用衍生品。</p>\n<p>　　保监会有关部门负责人表示，保险公司应当建立健全资产负债管理体系，不断提升资产负债管理水平，及时监测资产负债匹配状况，防范资产负债错配风险。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,890,'2017-12-17 16:14:00','2017-12-17 19:18:25','67',0),(3018,'旅美学者郑洪将《南京不哭》20万元稿酬全部捐出关爱抗战老兵','李明阳','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>成都12月17日电 (记者 张浪)17日上午，旅美学者、物理学家、麻省理工学院终身教授郑洪将其著作的《南京不哭》(中文版)所得20万元稿酬捐予四川建川博物馆，并以此设立“郑丰基金”，用于扶持、关爱抗战老兵。该书的后续稿费，及此后建川博物馆或社会各界发起的相关捐款活动款项，亦会作为“郑丰基金”的筹资渠道。</p>\n<p>　　据悉，郑洪所著作的《南京不哭》以南京大屠杀为历史背景，讲述了毕业于麻省理工学院的美国学者约翰・温思策、中国学者任克文及南京女子陈梅等人在南京所经历的悲欢离合。此书是继张纯如之后，又一位国际知名学者以南京大屠杀作题痛诉国殇。</p>\n<div style=\"text-align:center\"> \n <img alt=\"旅美学者、物理学家、麻省理工学院终身教授郑洪(右)与建川博物馆馆长樊建川(中)签约捐赠稿酬。　张浪 摄\" src=\"upload/image/cd992eab-0c83-4e3b-849d-7b4b5b3d0038.JPG\" title=\"旅美学者、物理学家、麻省理工学院终身教授郑洪(右)与建川博物馆馆长樊建川(中)签约捐赠稿酬。　张浪 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  旅美学者、物理学家、麻省理工学院终身教授郑洪(右)与建川博物馆馆长樊建川(中)签约捐赠稿酬。　张浪 摄 \n</div>\n<p>　　郑洪表示，“当初写完《南京不哭》这本书的时候，对那些做过贡献的抗战老兵非常敬佩。在之前，我是一位科学家，崇拜的是爱因斯坦、杨振宁等大科学家，后来觉得这批抗战军人更值得敬重，因为他们牺牲的不是他的时间、智慧，而是他的性命。此次我将稿酬全部捐献出来，纪念那些逝去的军人，并力所能及帮助抗战老兵。但自己一个人无法完成这个任务，后来发现建川博物馆是一个非常适合的地方。”</p>\n<p>　　据了解，郑洪对写作此书夙愿已久。“我是卢沟桥事变前两个月出生的，我经历过八年抗战，所以我能够了解，那个时候的生活不是现在人能够想象的。我要用英文写个英文小说给西方人士看，中国的军队打得非常壮烈，非常英勇，我们不屈不挠，一直跟日本人打到底，最终我们胜利了，这是非常了不起的，是历史上非常壮烈的一章，所以说我要把它写出来。”郑洪说道。</p>\n<div style=\"text-align:center\"> \n <img alt=\"捐赠仪式现场。　张浪 摄\" src=\"upload/image/aa2b9169-5694-457b-bf95-d68368981fca.JPG\" title=\"捐赠仪式现场。　张浪 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  捐赠仪式现场。　张浪 摄 \n</div>\n<p>　　为写此书，郑洪旅居南京3个多月，走街串巷，四处寻访，去伪存真。期间，郑洪结识了侥幸于“南京大屠杀”的两位老人，并如实笔录了他们的战争遭遇。之后，不惜年迈，十易其稿，最终写就了这部具有深刻历史意义的小说。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/aa2b9169-5694-457b-bf95-d68368981fca.JPG',NULL,747,'2017-12-17 16:14:00','2017-12-17 19:18:25','73',0),(3019,'“C哩C哩”原唱Matteo入驻抖音 魔性舞是如何炼成的','陈海峰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月15日电 来自罗马尼亚的著名流行雷鬼音乐人Matteo即将来到中国，并正式在原创音乐短视频分享平台抖音开设个人账号，发布相关视频。Matteo将在中国召开乐迷见面会，并计划组织巡演。</p>\n<p>　　说起Matteo的名字，在中国除了小部分乐迷之外可能鲜有人知，但他有着另一个更广为中国用户所知的身份――今年风靡亚洲洗脑神曲《Panama》的原唱。在各大社交媒体、视频网站上都可以看到过“C哩C哩”舞的视频。这支火遍全国的魔性舞蹈的原声配乐就是《Panama》。</p>\n<p>　　据悉，Matteo在2013年创作并发布了这首快节奏情歌，并收录在同名专辑之中。今年 8月底，这首歌通过抖音的C哩C哩舞在中国爆火，也正是这一因素促成了Matteo此番来华。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/4745bee8-1645-4b8f-a94e-1861fa019181.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　C哩C哩舞发源于国内音乐短视频平台抖音。今年8月27日，抖音上的红人贝贝叔叔与另外两个抖音红人薛老湿和心然一起，用《Panama》作为背景乐拍摄了一支魔性舞蹈视频，并在抖音上发起了“C哩C哩舞”的挑战。这一魔性却又简单易学的舞蹈迅速在抖音爆红，并向全网蔓延。用户把抖音上C哩C哩舞的视频上传到了各大社交媒体和视频网站上。</p>\n<p>　　8月底9月初，抖音C哩C哩舞挑战上线之后，其百度指数直线飙升，最高峰值超过40000。以《Panama》为背景的各种舞蹈视频火之后，日本、韩国和泰国也有许多舞蹈团体加入其中。事实上不仅是《Panama》，一年来《爱的就是你》《全部都是你》等歌曲也纷纷通过抖音在网络流行。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/b5df51cf-d749-4f02-ae60-71294df19b18.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　目前，抖音上使用量TOP20的歌曲，大多属于这种情况。在成为抖音某个挑战赛的推荐BGM或者被抖音达人用户使用而引发跟拍热潮后，它们迅速蹿红。《Panama》目前在抖音的使用量已高达138.7万次，以它为BGM的视频播放量已经超过2000万。通过“爱的手势舞”走红的《爱的就是你》在站内已经有超过160万人使用。从几个月前开始，吴亦凡、鹿晗、莫文蔚、胡彦斌、郭采洁等就相继通过抖音宣传并首发新歌。</p>\n<p>　　业内人士分析，源源不断地有歌曲通过抖音走红背后是整个音乐宣发生态的变化――音乐短视频逐渐成为音乐推广中重要渠道之一。高参与度、碎片化的特点让音乐能够通过一支15秒的视频在很短的时间互动量激增。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/b5df51cf-d749-4f02-ae60-71294df19b18.jpg',NULL,662,'2017-12-15 15:44:00','2017-12-17 19:18:25','82',0),(3020,'长篇小说《一抹沧桑》在京研讨：彰显乡村叙事的活力','罗攀','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>北京12月17日电 (记者 高凯)作家陈玺乡土长篇《一抹沧桑》研讨会17日在京召开，包括吴义勤、雷达、白烨、邱华栋等在内的与会文学评论家、作家认为，这部充分还原生活本身的作品对于当代中国文学颇具意义，认为其“彰显了乡村叙事的活力，以大历史中普通人的生活呈现了时间洪流中人的永恒的情感。”</p>\n<p>　　《一抹沧桑》是作家陈玺的第二部长篇，今年六月初由作家出版社出版。该长篇80多万字，陈玺将自己的真实经历揉入小说，用散点透视的方式写了几个农村家庭，描述了几十年中国的农村变化。</p>\n<p>　　作品以七十年代至九十年代为背景，描写了陕西关中渭北塬上几户农家日作日息的生活形态，喜怒哀乐的情感姿态。“文革”后期的社会悸动，改革初期的生活躁动，都由日常的农家生活画面一一呈现出来。这个时期有些闭锁又有些温馨的田园式的农家生活，随着时代的更变，已一去不返。</p>\n<p>　　中国小说学会会长雷达认为，《一抹沧桑》真实性很强，具有其特别的意义，生活细节描写十分精彩，为一个时期的农村情状与农民形状，留下了一份真实而鲜活的历史印记。</p>\n<p>　　评论家白烨指出，陕西是优质文学的沃土，“《一抹沧桑》我认为最鲜明的特点就是接地气，有底气，这部作品依靠小人物、小生活、小细节，以大量的生活积累作基础，呈现出中国乡土特有的逻辑――‘生存’和‘孝’，非常踏实地还原了关中农村的自转方式。”</p>\n<p>　　作家邱华栋认为，绵密是陕西文学的大传统，“《一抹沧桑》有向《平凡的世界》《白鹿原》致敬的意味，这部长篇以生活中的小矛盾推进，这一特点与明清小说的写作方式有关联。这部作品的价值还在于给当代中国文学提供了有价值和意义的人物组像。成功地描述了在时代艰难的转身过程中，小人物认真而执着的生活。”</p>\n<div style=\"text-align:center\"> \n <img alt=\"长篇小说《一抹沧桑》在京研讨 高凯 摄\" src=\"upload/image/94236f02-bc81-483e-8398-e7c6fbadec54.jpg\" title=\"长篇小说《一抹沧桑》在京研讨 高凯 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  长篇小说《一抹沧桑》在京研讨 高凯 摄 \n</div>\n<p>　　中国现当代文学研究者黄德海认为，“《一抹沧桑》从某种程度上恢复了80年代的写作的活力，尤其是恢复了乡村叙事的活力，我认为这个层面上抵抗了累积抑郁和焦虑的虚无主义。”他认为，《一抹沧桑》的“沧桑”是时间感带来的，这种“沧桑”是有希望和踏实厚重的。</p>\n<p>　　以乡土小说《陌上》引发业内关注的青年作家付秀莹认为，《一抹沧桑》通过绵密的细节、众多的人物，耐心的呈现了人与人的关系、人与土地的关系、人与历史的关系以及人与现实的关系，“我认为它最终让读者看到了时间的洪流当中永恒的情感，悲伤与坚韧。”</p>\n<p>　　此外她认为，除了文学的意义，《一抹沧桑》对于乡村生活的细腻真实的记录也极具社会意义，“文学可以有比历史更为细腻生动的记录。”</p>\n<p>　　当天的研讨会中，与会界内人士还就《一抹沧桑》这部作品进行了生活真实与文学真实的探讨。</p>\n<p>　　雷达指出，“原汁原味和艺术概括的关系是一个值得探讨的问题，我认为小说家恰恰要有主观性，吃透素材后要有自己的温度，《一抹沧桑》如果说有不足，我认为是不够典型化，这部作品还可以进行进一步的提炼和集中。”</p>\n<p>　　白烨对此表示同意，“文学的真实还是应该更为概括，小说的方式应该是以一当十，高度的提炼概括，《一抹沧桑》是有提炼的空间的。”</p>\n<p>　　黄德海与付秀莹则认为，小说中不一定需要高度集中的故事线，“我认为这样把要表达的东西揉进细节，这样的耐心讲述，是《一抹沧桑》的一个鲜明特点”，黄德海说。</p>\n<p>　　付秀莹表示，“这部作品表现出作者带着欢愉的讲述的耐心，我感觉其中的群像是众多多而有序的，整个作品的这一特点很有吸引力。”(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/94236f02-bc81-483e-8398-e7c6fbadec54.jpg',NULL,749,'2017-12-17 19:12:00','2017-12-17 19:18:25','75',0),(3021,'王心凌蒋大为《不凡的改变》同台 玩转音乐改编','罗攀','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月17日电 12月17日22:00播出的江苏卫视《不凡的改变》，“甜心教主”王心凌和歌唱家蒋大为将同台玩转音乐改编。以甜美形象深入身心的王心凌在本期节目中变身“电音小公主”与敬唱歌手嗨玩摇滚；蒋大为的经典歌曲则迎来了各种新锐改编，大胆的编曲让老艺术家叫好不断。</p>\n<p>　　来到《不凡的改变》，王心凌开场用帅气动感的改编版《月光》，展示了小女生的成长蜕变。而接下来的三首改编，更是将王心凌歌曲的“甜美”标签彻底撕掉。耿斯汉撕心裂肺唱摇滚版《当你》，诉说从小开始对王心凌的爱慕；《花的嫁纱》原作者崔子格再作嫁衣，金属摇滚的曲风，彰显了原作者不凡的音乐创作能力；而以老男人为主的“浪荡绅士乐队”改编了布鲁斯风《睫毛弯弯》，当老男人唱起小女生情愫，效果是极为惊艳。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/331e64ce-0772-447e-a261-06d784a1d157.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　蒋大为老师宝刀不老，高昂嘹亮的歌声引得全场的起立致敬。《桃花盛开的地方》被苟乃鹏改编为嘻哈说唱，退伍军人的伴唱把全场的气氛推到高潮，加时团成员汪小敏想到自己的军人爸爸，感动到泪水满盈。PANDA组合的嘻哈版《敢问路在何方》唱出了年轻人的“北漂记”，《牡丹之歌》原作者乔羽之孙乔冠霖挑战爷爷的经典，加入昆曲元素的新锐改编，获得了加时团和蒋大为的认可。</p>\n<p>　　六首大胆新锐的改编，王心凌和蒋大为究竟会挑战哪首，而最终的效果能否让经典再惊艳？今晚22:00，来江苏卫视看《不凡的改变》，让改变发声！</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/331e64ce-0772-447e-a261-06d784a1d157.jpg',NULL,583,'2017-12-17 10:00:00','2017-12-17 19:18:26','68',0),(3022,'韩美两军举行联合演练 销毁大规模杀伤性武器','吴倩','中国新闻网','<p>　　韩联社首尔12月17日电 据韩国军方17日消息，韩军和驻韩美军第二师从12日至15日在位于京畿道议政府市的美军军营等国内多处军事设施进行代号为“Warrior Strike”的联合军演，演练销毁核武等大规模杀伤性武器(WMD)。</p>\n<div style=\"text-align:center\"> \n <img alt=\"韩美“Warrior Strike”联合军演现场照(韩联社/美陆军官方脸书截图)\" src=\"upload/image/a5d34c8f-501c-4ebf-8891-471b1c8bc3a4.jpg\" style=\"border:px solid #000000\" title=\"韩美“Warrior Strike”联合军演现场照(韩联社/美陆军官方脸书截图)\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  韩美“Warrior Strike”联合军演现场照(韩联社/美陆军官方脸书截图) \n</div>\n<p>　　报道称，这是韩美例行的联合军演，旨在提高销毁大规模杀伤性武器的能力。韩美两军动员数百名官兵和装甲车等武器进行此次演习。</p>\n<div style=\"text-align:center\"> \n <img alt=\"12月15日，韩国联合参谋本部议长郑景斗(前排居中)和韩美联合司令部司令文森特・布鲁克斯(前排左)访问隶属于驻韩美军第二师的防化部队。(韩联社/美陆军官方脸书截图)\" src=\"upload/image/de226a66-b4f9-443c-a9a2-5fdc9b60d017.jpg\" style=\"border:px solid #000000\" title=\"12月15日，韩国联合参谋本部议长郑景斗(前排居中)和韩美联合司令部司令文森特・布鲁克斯(前排左)访问隶属于驻韩美军第二师的防化部队。(韩联社/美陆军官方脸书截图)\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  12月15日，韩国联合参谋本部议长郑景斗(前排居中)和韩美联合司令部司令文森特・布鲁克斯(前排左)访问隶属于驻韩美军第二师的防化部队。(韩联社/美陆军官方脸书截图) \n</div>\n<p>　　据悉，军演期间，韩美假设在市区应战的情况进行迅速销毁大规模杀伤性武器的演习。</p>\n<p>　　演练最后一天的15日，韩国联合参谋本部议长郑景斗和韩美联合司令部司令文森特・布鲁克斯共同访问隶属于驻韩美军第二师的防化部队，听取军演情况并勉励官兵。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/de226a66-b4f9-443c-a9a2-5fdc9b60d017.jpg',NULL,1564,'2017-12-17 15:28:00','2017-12-17 19:18:26','71',0);
insert  into `news`(`id`,`title`,`editor`,`source`,`detail`,`imgsrc`,`videosrc`,`browsenum`,`time`,`modifiedtime`,`type`,`isdelete`) values (3023,'每日优鲜曾斌：“做好新零售需要以人为本”','陈海峰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月15日电 12月13日，由亿欧主办的“2017亿欧创新者年会暨第3届创新奖颁奖盛典”在中国大饭店开幕。活动现场，中央对外联络部原副部长于洪君，北大汇丰商学院教授、国民经济研究所所长樊纲，每日优鲜联合创始人兼总裁曾斌，麦当劳副总裁张帆，美团点评高级副总裁王慧文等100多位嘉宾出席此次大会。会上，曾斌做了《新零售以人为本》的主题演讲。他表示，“把人放在首位去思考，才能把新零售做好”。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/52eb0f65-d4ec-4b51-a204-2d2cdb0f573f.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　“如何让大家的工作变得更加温暖？怎样让大家的生活变得更加美好？”曾斌认为这是所有新零售从业人员需要深思的问题。而每日优鲜作为生鲜零售平台，希望让每个人随时随地享受食物的美好，他们给出的解决方案是实现全场景覆盖。</p>\n<p>　　用户购买生鲜食品的消费场景包括在家里、办公室、路上和公共场所。曾斌介绍说，每日优鲜已经用前置仓模式覆盖家庭场景，用无人货架便利购覆盖办公场景，后续还会提出更新、更好的方式去满足用户在路上和公共场所的生鲜购买需求，从而真正实现全场景的覆盖。</p>\n<p>　　对于如何实现全场景的覆盖，每日优鲜也有自己的“方法论”。曾斌介绍，“供应链的密度、物流的密度和数据的密度是我们实现全场景覆盖的基础，因为这关系到我们怎样更有效率的为用户提供更优质的商品和服务。”</p>\n<p>　　现场，曾斌也谈到，每日优鲜围绕供应链密度、物流密度和数据密度方面做了很深的积累，“我们在过去三年里面已经建立了一条中国到目前为止最大的、最先进的、最快速的、及时高效的生鲜冷链物流配送体系。”公开资料显示，目前每日优鲜已经在17个城市实现会员1小时极速达服务。</p>\n<p>　　最后，曾斌对于新零售未来的发展方向做了总结，“新零售需要以人为本，这意味着企业做商业模式设计的时候，并不是考虑今天企业有什么能力，做什么最方便、最有利，而是需要思考做什么事情是真正能够满足消费者的需求，真正对消费者划算的。”</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/52eb0f65-d4ec-4b51-a204-2d2cdb0f573f.jpg',NULL,104,'2017-12-15 16:17:00','2017-12-17 19:18:26','82',0),(3024,'第四届中国绿色发展与生态建设峰会在京召开','姜雨薇','新华网','<p>　　新华网北京12月15日电 由新华网主办、中国环境科学学会协办、绿色全域(北京)文化传媒有限公司承办的“第四届中国绿色发展与生态建设峰会暨《中国绿色发展典范案例汇编〔2017〕》发布仪式”今日在京开幕。峰会以“绿动中国，绿创未来”为主题，由“绿动中国共享峰会”、“《中国绿色发展典范案例汇编〔2017〕》发布”、“品牌核心资产的绿色会商”、“‘2017中国绿色发展优秀城市’与‘2017中国最具影响力绿色企业品牌’发布”等环节组成。峰会吸引了国家部门相关专家、学者，与来自全国各地方市县区的分管领导、部分国内知名企业代表、媒体人士近230人出席。</p>\n<div style=\"text-align:center\"> \n <img alt=\"12月15日，由新华网主办、中国环境科学学会协办、绿色全域(北京)文化传媒有限公司承办的“第四届中国绿色发展与生态建设峰会”在京开幕，峰会以“绿动中国，绿创未来”为主题。 图为新华网董事、副总裁申江婴致辞。 新华网 陈延特 摄\" src=\"upload/image/937bd532-7d6d-47bf-a4d7-37a78acac66b.jpg\" style=\"border:px solid #000000\" title=\"12月15日，由新华网主办、中国环境科学学会协办、绿色全域(北京)文化传媒有限公司承办的“第四届中国绿色发展与生态建设峰会”在京开幕，峰会以“绿动中国，绿创未来”为主题。 图为新华网董事、副总裁申江婴致辞。 新华网 陈延特 摄\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  12月15日，由新华网主办、中国环境科学学会协办、绿色全域(北京)文化传媒有限公司承办的“第四届中国绿色发展与生态建设峰会”在京开幕，峰会以“绿动中国，绿创未来”为主题。 图为新华网董事、副总裁申江婴致辞。 新华网 陈延特 摄 \n</div>\n<p>　　新华网董事、副总裁申江婴致欢迎辞时表示，绿色发展彰显历史担当，也蕴含治理智慧。新华网将一如既往地发挥权威性、公信力的优势，做好绿色发展的宣传和推广，让热心生态建设、践行绿色发展的城市、企业有更大的知名度和美誉度，有更好的发展环境。</p>\n<div style=\"text-align:center\"> \n <img alt=\"12月15日，由新华网主办、中国环境科学学会协办、绿色全域(北京)文化传媒有限公司承办的“第四届中国绿色发展与生态建设峰会”在京开幕，峰会以“绿动中国，绿创未来”为主题。图为全国政协人口资源环境委员会副主任江泽慧致辞。新华网 陈延特 摄\" src=\"upload/image/d727a76a-6feb-4f9b-a9d7-25326fa603cf.jpg\" style=\"border:px solid #000000\" title=\"12月15日，由新华网主办、中国环境科学学会协办、绿色全域(北京)文化传媒有限公司承办的“第四届中国绿色发展与生态建设峰会”在京开幕，峰会以“绿动中国，绿创未来”为主题。图为全国政协人口资源环境委员会副主任江泽慧致辞。新华网 陈延特 摄\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  12月15日，由新华网主办、中国环境科学学会协办、绿色全域(北京)文化传媒有限公司承办的“第四届中国绿色发展与生态建设峰会”在京开幕，峰会以“绿动中国，绿创未来”为主题。图为全国政协人口资源环境委员会副主任江泽慧致辞。新华网 陈延特 摄 \n</div>\n<p>　　“绿色是生命的象征，是大自然的底色，是现代文明进步的重要标志，建设生态文明关乎世界未来。”全国政协人口资源环境委员会副主任江泽慧表示，生态建设为美丽中国建设增绿添彩。地球生态是最复杂，也是最脆弱的生命支持系统。加强生态建设，保护生态环境，已经成为全球的共识。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/76433e04-c29e-4f9c-ae13-703893ede17b.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　“绿色发展要把生产、生活和生态紧密的联系起来，满足人们美好生活的需求。”十一届全国人大常委会财政经济委员会副主任委员、著名经济学家贺铿表示，绿色发展要有一个好的环境，让食品安全、空气清新、水干净走进人们的生活中，把生产、生活和生态紧密联系起来。绿色发展不仅要重视发展的过程，考虑环境、资源约束力的问题，还要贯彻到发展的结果，其中产品的安全性是一个重要的结果，应该给人们提供更放心、更可靠、更优质的产品。</p>\n<p>　　国务院参事、清华大学区域与城市发展中心主任施祖麟表示，生态危机、环境危机已成为全球挑战，没有哪个国家可以置身事外，独善其身。我国可按照绿色发展、循环发展和低碳发展的思路，完善生态环境管理制度，建立绿色发展经济体系，加快完成生态文明和美丽中国建设的主要任务。</p>\n<p>　　在“绿动中国共享峰会”环节，全国政协原常委、国家教育咨询委员会委员任玉岭，中国社科院研究生院城乡建设经济系主任陈淮，品牌战略专家、中央电视台品牌顾问李光斗做主题发言。</p>\n<p>　　在“绿创未来品牌核心资产绿色会商”环节中，国家统计局中国经济景气监测中心副主任潘建成、国务院发展研究中心资源与环境政策研究所副所长李佐军分别围绕“摈弃短期行为是推动绿色发展的关键”和“培育绿色发展新动能”做主题演讲；小黄车(北京)数据服务有限公司副总裁刘凯和台达集团-中达电通股份有限公司董事总经理游文人围绕“绿色责任与企业品牌”主题进行了精彩分享。</p>\n<p>　　作为峰会的一项重要组成部分，“2017绿色发展与生态建设优秀城市、2017中国最具影响力绿色企业”名单同期发布。其中四川省成都市、贵州省贵阳市、安徽省淮北市，中国石油化工集团公司、兖州煤业股份有限公司、河钢集团有限公司等84个市县区及部分国内知名企业入围。名单如下：</p>\n<p>　　四川省成都市&nbsp;</p>\n<p>　　贵州省贵阳市</p>\n<p>　　安徽省淮北市&nbsp;</p>\n<p>　　福建省宁德市</p>\n<p>　　广东省云浮市&nbsp;</p>\n<p>　　河南省许昌市</p>\n<p>　　湖南省株洲市&nbsp;</p>\n<p>　　吉林省通化市&nbsp;</p>\n<p>　　江苏省盐城市&nbsp;</p>\n<p>　　江西省赣州市</p>\n<p>　　四川省广元市</p>\n<p>　　黑龙江省七台河市&nbsp;</p>\n<p>　　云南省红河哈尼族彝族自治州&nbsp;&nbsp;</p>\n<p>　　云南省普洱市</p>\n<p>　　中国-新加坡天津生态城</p>\n<p>　　重庆市铜梁区&nbsp;</p>\n<p>　　重庆市渝中区&nbsp;</p>\n<p>　　重庆市垫江县</p>\n<p>　　安徽省界首市&nbsp;&nbsp;&nbsp;&nbsp;</p>\n<p>　　甘肃省玉门市&nbsp;</p>\n<p>　　海南省万宁市</p>\n<p>　　河南省禹州市&nbsp;</p>\n<p>　　江苏省东台市&nbsp;</p>\n<p>　　江苏省高邮市&nbsp;</p>\n<p>　　江苏省如皋市</p>\n<p>　　江苏省邳州市</p>\n<p>　　江西省德兴市</p>\n<p>　　安徽省泾县&nbsp;&nbsp;&nbsp;</p>\n<p>　　福建省南平市延平区</p>\n<p>　　广东省连山壮族瑶族自治县&nbsp;&nbsp;&nbsp;</p>\n<p>　　贵州省惠水县&nbsp;</p>\n<p>　　贵州省开阳县</p>\n<p>　　贵州松桃经济开发区&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\n<p>　　海南省琼中黎族苗族自治县</p>\n<p>　　河南省栾川县</p>\n<p>　　河南省南召县</p>\n<p>　　河南省西平县</p>\n<p>　　黑龙江省齐齐哈尔市铁锋区&nbsp;&nbsp;</p>\n<p>　　湖北省武汉市蔡甸区</p>\n<p>　　江苏省海安县</p>\n<p>　　江苏省沛县&nbsp;&nbsp;&nbsp;</p>\n<p>　　江西省信丰县&nbsp;</p>\n<p>　　山东省庆云县</p>\n<p>　　陕西省佛坪县</p>\n<p>　　陕西省洛南县</p>\n<p>　　四川省大英县&nbsp;</p>\n<p>　　四川省荣县</p>\n<p>　　浙江省缙云县</p>\n<p>　　浙江省天台县&nbsp;</p>\n<p>　　浙江省仙居县</p>\n<p>　　中国石油化工集团公司</p>\n<p>　　兖州煤业股份有限公司</p>\n<p>　　河钢集团有限公司</p>\n<p>　　云天化集团有限责任公司</p>\n<p>　　新兴铸管股份有限公司</p>\n<p>　　广西柳州钢铁集团有限公司</p>\n<p>　　四川省宜宾五粮液集团有限公司</p>\n<p>　　中国机械设备工程股份有限公司</p>\n<p>　　上海电力股份有限公司</p>\n<p>　　中煤平朔集团有限公司</p>\n<p>　　国网浙江省电力有限公司</p>\n<p>　　国家电投集团黄河上游水电开发有限责任公司</p>\n<p>　　湖北省交通投资集团有限公司</p>\n<p>　　云南建设基础设施投资股份有限公司</p>\n<p>　　广州万力集团有限公司</p>\n<p>　　内蒙古呼和浩特金谷农村商业银行股份有限公司</p>\n<p>　　绿色动力环保集团股份有限公司</p>\n<p>　　京能集团北京京西燃气热电有限公司</p>\n<p>　　北京环境卫生工程集团有限公司</p>\n<p>　　贵州开磷控股(集团)有限责任公司</p>\n<p>　　台达集团</p>\n<p>　　中国对外贸易中心(集团)</p>\n<p>　　华帝股份有限公司</p>\n<p>　　武汉光谷蓝焰新能源股份有限公司</p>\n<p>　　贵州宏立城集团</p>\n<p>　　安徽国祯环保节能科技股份有限公司</p>\n<p>　　河北欣意电缆有限公司</p>\n<p>　　理昂生态能源股份有限公司</p>\n<p>　　飞翼股份有限公司</p>\n<p>　　小黄车(北京)数据服务有限公司</p>\n<p>　　力合科技(湖南)股份有限公司</p>\n<p>　　四川川环科技股份有限公司</p>\n<p>　　北京中咨海外咨询有限公司</p>\n<p>　　山东美佳集团有限公司</p>\n<p>　　景德镇市国信节能科技股份有限公司</p>\n<p>　　东阿阿华医疗科技有限公司</p>\n<p>　　(以上名单排名不分先后)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/76433e04-c29e-4f9c-ae13-703893ede17b.jpg',NULL,1877,'2017-12-15 15:42:00','2017-12-17 19:18:26','82',0),(3025,'2017中国雇主品牌盛典落幕 猎豹移动斩获三项大奖','陈海峰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月15日电&nbsp;12月14日，2017年中国雇主品牌盛典隆重开幕，现场揭晓了中国雇主品牌三大榜单的21个奖项。值得注意的是，猎豹移动从4000多家企业中脱颖而出，摘得“中国大学生最佳雇主Top50”、“中国大学生最佳雇主智能雇主Top10”、”中国大学生最佳雇主互联网行业Top15”三项大奖。同时上榜的还有阿里巴巴、百度、美团点评等企业 。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/8e7fe6b3-0202-43f7-89e3-b109d98b506f.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p><strong>　　完善的人才培养机制，为毕业生提供施展才华的平台</strong></p>\n<p>　　据了解，中国雇主品牌评选是中国最权威、最大规模、最全面的雇主品牌活动。此次雇主品牌评选历时6个月调研，覆盖3亿职场人，最终榜单由百位行业专家共同参与，基于调研数据分析和科学机制，对来自等众多行业领域的4000多家企业评选而成。</p>\n<p>　　此次，猎豹移动一举将三项大奖收入囊中，主要源于其凭借前瞻性的全球化视野，开启了中国互联网公司出海新模式，并在工具、移动娱乐产品领先全球之后，全力出击人工智能，以场景化思维推动人工智能技术落地，得到行业的广泛认同。除此之外，猎豹移动优秀的企业文化、完善的人才吸引及培养体系，也赢得广大大学生和专业评委的高度认可。</p>\n<p>　　在人才吸纳上，猎豹移动可谓卓有成效，旗下人工智能公司猎户星空在成立之初就聚集了一批顶尖技术人才，拥有上百个在行业内有五年以上工作经验的工程师，博士占比近5成，成立一年时间公司已发展至超200人规模。同时，猎豹移动也非常注重人才培养，曾推出“全球青年养成计划“、”门徒计划”等活动，面向全球招揽有梦想的精英青年，为他们搭建才华的舞台，通过一系统的培训和前线实战，帮助他们成为具有全球视野的高端互联网人才。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/995b3c4a-7272-418d-be58-f990566c71b7.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p><strong>　　工具+人工智能的产业布局，让猎豹聚焦培养AI人才</strong></p>\n<p>　　目前，猎豹移动不仅将人工智能和原有的业务相结合，还通过投资猎户星空，加强了AI技术储备，形成了完善的产业布局。猎豹移动旗下人工智能公司猎户星空先后攻克语音交互和图像识别两大技术难题。猎户语音OS先后和喜马拉雅、小米、腾讯、美的、当当等知名品牌展开合作，远场语音数据量行业第一。人脸识别技术在微软百万名人识别等国际赛事中夺魁，已在门禁打卡以及人脸支付等实际场景中应用。</p>\n<p>　　据透露，未来猎豹移动更聚焦于 AI，并计划在明年3月发布一系列智能硬件和AI产品。猎豹移动CEO傅盛表示，人工智能是中国最大的一次机会，底层的AI技术完成构建之后，很多行业将有10倍增长的机会。对于人工智能行业高速发展所面临的人才稀缺，他认为，人工智能是一个体系化的技术，要找到场景，涉及到各个层面去落地，还要跟业务结合，中国有自己的闭环模式，核心人才中国应该自己来培养。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/995b3c4a-7272-418d-be58-f990566c71b7.jpg',NULL,1101,'2017-12-15 16:18:00','2017-12-17 19:18:26','82',0),(3026,'福州复兴船政文化掀文创“台风”','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>福州12月17日电 (林春茵 郑丽香)首届创新创业大赛邀请两岸文创势力研发“船政”伴手礼，首届海峡两岸船政文化学术沙龙研讨会聚焦船政文化在“一带一路”视野下的传承和发展……连日来，一系列“船政文化”主题活动在福建船政发祥地――福州市马尾区举办，掀起两岸文创合作热风。</p>\n<p>　　福州船政文化研究专家17日向<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>记者指出，福州复兴船政文化为海峡两岸文创青年带来合作机遇。</p>\n<p>　　福建船政兴起于十九世纪下半叶，清朝名将左宗棠、沈葆桢在闽江下游出海口展开建船厂、造兵舰、制飞机、办学堂、引人才、派学童出洋留学等一系列“富国强兵”活动。当下，作为“海丝”发祥地，福州正在打造“海丝”战略枢纽城市，“船政”文化成为资源富矿。</p>\n<div style=\"text-align:center\"> \n <img alt=\"阳光学院沈锐��团队以语音立体书《掌心古港》夺得马尾区创新创业大赛特等奖。(林春茵摄)\" src=\"upload/image/291325ff-0709-4993-8321-2e02ea3ad864.JPG\" title=\"阳光学院沈锐��团队以语音立体书《掌心古港》夺得马尾区创新创业大赛特等奖。(林春茵摄)\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  阳光学院沈锐��团队以语音立体书《掌心古港》夺得马尾区创新创业大赛特等奖。(林春茵摄) \n</div>\n<p>　　福州阳光学院商学系系主任、船政文化研究中心主任王秉安称，福州正大力引进台湾生活文创设计力量，两岸共同深耕厚植船政文化，提炼出鲜明的在地元素，快速发展“船政”文创产业。</p>\n<p>　　这从阳光学院近年持续引进台湾师资可见端倪。台湾世新大学教授、台北故宫博物院代理副院长金士先已在该校开设通识课程《博物馆与文创》；今年2月，台湾逢甲大学公共政策研究所副教授马彦彬就任阳光学院创新创业教育中心主任。</p>\n<div style=\"text-align:center\"> \n <img alt=\"阳光学院许萍萍团队“一角”晴雨伞展现马尾船政遗址。(林春茵摄)\" src=\"upload/image/803de5bb-d466-46f7-a8b3-3398f20fa3c2.JPG\" title=\"阳光学院许萍萍团队“一角”晴雨伞展现马尾船政遗址。(林春茵摄)\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  阳光学院许萍萍团队“一角”晴雨伞展现马尾船政遗址。(林春茵摄) \n</div>\n<p>　　马彦彬受访时表示，倡导学生深入人群，“去寻找生活的难点和痛点，真题真做”，以利于船政文化活态传承、创造价值；目前中心正着力引进台湾年青设计师或团队聚焦“船政”，“两岸文创设计理念有所不同，有差异才有合作价值”。</p>\n<p>　　福州马尾举办的首届创新创业大赛中，多个文创作品显示出“台风来袭”，包括船政题材手绘语音立体书、手绘罗星塔晴雨伞、船锚马灯造型的文房四宝等，造型和材质都颇为注重生活美学和量产可能。</p>\n<div style=\"text-align:center\"> \n <img alt=\"福建省商学院大学生李星洋展示《千重海浪渔人醉》文房四宝。(林春茵摄)\" src=\"upload/image/8bc88c06-c46f-4020-b7a3-fbac762bffa2.JPG\" title=\"福建省商学院大学生李星洋展示《千重海浪渔人醉》文房四宝。(林春茵摄)\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  福建省商学院大学生李星洋展示《千重海浪渔人醉》文房四宝。(林春茵摄) \n</div>\n<p>　　中华经济研究院台湾东南亚国家协会研究中心主任徐遵慈则在海峡两岸船政文化学术沙龙研讨会上指出，两岸文创产业发展差距缩小，随着“一带一路”建设推进，台湾青年可从中寻找发展机遇。</p>\n<p>　　“船政为‘海丝’提供文化温情。”中国甲午战争博物院客座研究员陈悦认为，福建船政出现多个中外合作、交流的范例，船政代表着近代中国试图认识、理解世界，寻找与世界平等相处的努力，是值得讲述的“极具国际化的中国故事。”(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/8bc88c06-c46f-4020-b7a3-fbac762bffa2.JPG',NULL,1603,'2017-12-17 14:31:00','2017-12-17 19:18:26','70',0),(3027,'著名诗人屠岸去世 曾感叹“人类不灭诗歌不亡”','周驰','中国新闻网','<div style=\"text-align:center\"> \n <img alt=\"图片来源：人民文学出版社官方微博截图。\" src=\"upload/image/2794e144-5097-457a-b111-7613304b117d.jpg\" style=\"border:px solid #000000\" title=\"图片来源：人民文学出版社官方微博截图。\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  图片来源：人民文学出版社官方微博截图。 \n</div>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>北京12月17日电 人民文学出版社官方微博16日晚发消息称，当天下午5点，著名诗人、翻译家、出版家，人民文学出版社原总编辑屠岸先生，在北京逝世，享年94岁。</p>\n<p>　　屠岸原名蒋壁厚，1923年生于江苏省常州市。他学鲁迅，用母亲的姓作为笔名的姓，名为叔牟。</p>\n<p>　　屠岸历任上海市军事管制委员会文艺处干部，华东文化部副科长，《戏剧报》编辑部主任，人民文学出版社总编辑，编审；中国作家协会第四届理事，第五、六、七届名誉委员；中国诗歌学会副会长。曾获得第二届鲁迅文学奖翻译奖、中国翻译文化终身成就奖、中国版权产业风云人物等。</p>\n<p>　　曾有媒体刊文这样评价屠岸――他就像诗神缪斯派来人间的天使，他每天都在努力地把心中无尽的爱与美，慷慨无私地抛洒给我们。</p>\n<p>　　著名文学评论家、北京大学教授谢冕，亦曾撰文称赞屠岸。“屠岸先生待人的诚恳、认真、周密、细致是大家都知道的，他对晚辈尤其平易，总是爱护有加。雍容儒雅是先生的‘形’，谦和中正则是先生的‘神’，在我的心目中，他是一位让人打内心敬畏的智慧长者”。</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：93岁著名诗人、翻译家屠岸。\" src=\"upload/image/cfce027b-c81c-412b-acf7-66ec1e43be2b.jpg\" style=\"border:px solid #000000\" title=\"资料图：93岁著名诗人、翻译家屠岸。\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  资料图：著名诗人、翻译家屠岸。 \n</div>\n<p>　　综合媒体消息，屠岸自幼家学深厚，其母是一位才女，写诗、作曲、绘画、弹琴均有所长，在屠岸还在读小学三年级的时候，母亲就开始教他读《古文观止》《唐诗三百首》《唐诗评注读本》等。</p>\n<p>　　2016年，屠岸接受《解放周末》专访，回忆这段往事，他说：“当时，母亲总是先解释诗文的内容，再自己朗诵几遍，然后叫我跟着她吟诵。我像唱山歌一样跟着吟诵，对内容不求甚解，只是觉得能从吟诵中得到乐趣。母亲教我的是‘常州吟诵’，2008年这种吟诵调被列入国家级非物质文化遗产，我是这个‘非遗’的三位代表性传人之一。”</p>\n<p>　　在母亲的耳濡目染下，屠岸成为了诗歌的朝圣者。1941年，18岁的屠岸第一次发表诗作《美丽的故园》，随后又陆续翻译了惠特曼诗选集《鼓声》、《莎士比亚十四行诗集》、《诗歌工作者在苏联》、《大臣夫人》等。</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：《屠岸诗文集》(8卷本)书影。人民文学出版社供图。\" src=\"upload/image/acd71ff4-6374-4e79-bc47-05076abc22ad.jpg\" style=\"border:px solid #000000\" title=\"资料图：《屠岸诗文集》(8卷本)书影。人民文学出版社供图。\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  资料图：《屠岸诗文集》(8卷本)书影。人民文学出版社供图。 \n</div>\n<p>　　此外，他还著有诗集《萱阴阁诗抄》《屠岸十四行诗》《哑歌人的自白》《深秋有如初春》《夜灯红处课儿诗》，以及散文诗集《诗爱者的自白》，文化随笔《倾听人类灵魂的声音》，文学评论集《诗论•文论•剧论》，散文集《霜降文存》，口述自传《生正逢时》等作品。2016年，93岁高寿的屠岸又出版了8卷本《屠岸诗文集》，不仅收录了经典作品，还整理收入了大量集外散篇作品。</p>\n<p>　　相较于其他文章体裁，屠岸一直偏爱写诗，古体诗、现代诗统统包揽。<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>(微信公众号：cns2012)记者早前专访他时，屠岸透露自己床头依然习惯性地放着纸笔，只要有灵感，就会随手记下来，“说不定，第二天兴致来了，就能写出一首好的作品”。</p>\n<p>　　屠岸曾说过，诗歌对自己来说，是“安身立命之本”。他一直保持一个习惯，吟诵着诗歌入睡。在他看来，无论是中国的李白、杜甫、白居易，还是西方的莎士比亚、华兹华斯、济慈，都是对自己生命的慰藉与激励。</p>\n<p>　　“写诗是情感的抒发。”屠岸这样界定写诗的妙处，“如果能将内心情感通过一首好作品表达出来，那么对作家来说也是一种极大的愉悦”。</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：著名诗人、翻译家屠岸。\" src=\"upload/image/c1d7861a-317a-4ee6-b1e4-0119033a1072.jpg\" style=\"border:px solid #000000\" title=\"资料图：著名诗人、翻译家屠岸。\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  资料图：著名诗人、翻译家屠岸。 \n</div>\n<p>　　屠岸有诗人的精神，晚年的他精神矍铄，心态开朗，对自己的年龄并不在意。据《北京晚报》消息，去年他在接受媒体采访时曾说：“因为生老病死是自然的规律我总要见上帝，什么时候上帝要我去我就去，但在我还没去之前，我还要非常愉快地工作，能够做多少就做多少。”</p>\n<p>　　作为国内首位完整翻译《莎士比亚十四行诗》的翻译家，屠岸多次提到自己被莎翁的艺术所征服，“十四行诗音韵优美，形式整齐，每一行都是抑扬格五音步，整首诗的押韵也十分讲究”。</p>\n<p>　　他也把这一理念移用到诗歌翻译上，“拥抱原诗是精神上的共振、融合，要把原作者的东西化为自己的，体会诗人的创作情绪。有时翻译不成功，非常困惑，千方百计地找到表述方式，特别是用于押韵的字词，最后就像追求爱人一样，终于追到了，是一种精神狂欢”。</p>\n<p>　　“诗是人类的精神家园，只要人类不灭，诗歌就不亡。”这是屠岸先生生前说过的话，虽然他离开了我们，但愿他如莎翁的诗那样，“将在不朽的诗中与时间同长”。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/c1d7861a-317a-4ee6-b1e4-0119033a1072.jpg',NULL,1476,'2017-12-17 01:21:00','2017-12-17 19:18:27','75',0),(3028,'泰国中国企业总商会罗勇商会举行迎新团拜晚会','吴倩','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>曼谷12月17日电 (赵婧楠)“一带一路，你我同行”暨泰国中国企业总商会罗勇商会2018年迎新团拜晚会，16日晚在泰国春武里府安美德城堡举行。</p>\n<p>　　中国驻泰王国大使吕健夫人潘鹏参赞、中国大使馆经商处张佩东参赞、泰国前副总理Pol.Gen.Chidchai Vanasatidya、安美德集团总裁邱威功等嘉宾以及泰中罗勇工业园区及周边近百家中资企业、近千名中泰员工共同参加。</p>\n<div style=\"text-align:center\"> \n <img alt=\"集体婚礼 赵婧楠 摄\" src=\"upload/image/1e9cb81a-6c5f-4687-8af6-fcbc132e3708.jpg\" title=\"集体婚礼 赵婧楠 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  集体婚礼 赵婧楠 摄 \n</div>\n<p>　　晚会上，与会人士相互交流，共话未来，并观赏文艺表演，其乐融融。</p>\n<p>　　当晚，罗勇工业园区内中资企业的12对中泰新人在晚会上举行集体婚礼，这在泰国当地中资企业还是第一次，成为本场晚会的一大亮点。</p>\n<div style=\"text-align:center\"> \n <img alt=\"中国驻泰王国大使吕健夫人潘鹏参赞讲话 赵婧楠 摄\" src=\"upload/image/e22ac316-bb8b-4521-9af7-38d124bd3fab.jpg\" title=\"中国驻泰王国大使吕健夫人潘鹏参赞讲话 赵婧楠 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  中国驻泰王国大使吕健夫人潘鹏参赞讲话 赵婧楠 摄 \n</div>\n<p>　　12对新人，寓意着泰中罗勇工业园成立12年。据悉，目前入园企业累计已达98家，带动中国对泰国投资累计超过25亿美金，现有泰国工人2万余名，中国工人逾2000名。</p>\n<p>　　本次晚会由泰国中国企业总商会罗勇商会主办，中国工商银行(泰国)股份有限公司等14家中泰企业共同协办。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/e22ac316-bb8b-4521-9af7-38d124bd3fab.jpg',NULL,1774,'2017-12-17 13:04:00','2017-12-17 19:18:27','73',0),(3029,'厉以宁：要摆脱路径依赖 不应再留恋高速度增长','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>北京12月17日电(记者 李金磊)北京大学光华管理学院名誉院长厉以宁17日在第十九届北大光华新年论坛上说，改革要摆脱路径依赖，中国不应再留恋高速度增长。</p>\n<div style=\"text-align:center\"> \n <img alt=\"第十九届北大光华新年论坛。<a target=\'_blank\' href=\'http://www.chinanews.com/\' >中新网</a>记者 李金磊 摄\" src=\"upload/image/d41ea673-5302-4950-9c2a-62c05b9dce8d.jpg\" style=\"border:px solid #000000\" title=\"第十九届北大光华新年论坛。<a target=\'_blank\' href=\'http://www.chinanews.com/\' >中新网</a>记者 李金磊 摄\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  第十九届北大光华新年论坛。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>记者 李金磊 摄 \n</div>\n<p>　　2018年，中国将迎来改革开放40周年。在谈到改革时，厉以宁表示，要摆脱路径依赖，在新思路指导下前进，走老路不一定没有风险。</p>\n<p>　　厉以宁解释称，所谓路径依赖，就是说走老路，这是最保险、最安全的，因为前人这么做，后人跟着他们的脚步走，这样不承担责任，同时也就回避了走新路可能遭遇的风险。在这种情况下，于是很多国家虽然感到现在的旧发展方式有问题，有矛盾，但还是坚持它的路径依赖。</p>\n<p>　　厉以宁说，由于存在路径依赖，不少人认为中国的国情还是慢慢地改，但改革是不能拖的，再走路径依赖的道路只能延迟经济的转变。</p>\n<p>　　“旧的模式是重数量、重速度，新的模式是重质量、重效果。”厉以宁说，不能长期停留在比较是旧生产模式好还是新生产模式好，因为经济在前进。传统发展模式是不会自动退出的，要挤掉它，才能找到新发展方式。</p>\n<p>　　厉以宁认为，在中国目前的情况下，不要再留恋高速度，中高速度就够了。去库存、补短板、降成本等方面的供给侧结构性改革，还需要进一步推进。</p>\n<p>　　厉以宁强调，新的发展要进行革命性改革，对于企业来说，改革要重在科技创新，提供新产品、新产能、新动能。真正的经济发展方式转变是在竞争中产生的。改革要有所为、有所不为，有所选择、有所抛弃。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/d41ea673-5302-4950-9c2a-62c05b9dce8d.jpg',NULL,1923,'2017-12-17 15:32:00','2017-12-17 19:18:27','66',0),(3030,'中国黑龙江冰雪季启幕：成海外华裔青少年“寻根”热点','周驰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>哈尔滨12月17日电 (记者 杨拓)16日，2017年海外华裔青少年“中国寻根之旅”冰雪营-黑龙江营和2017菲律宾华裔青少年“中国寻根之旅”冬令营-哈尔滨营同时开营。来自马来西亚、菲律宾、印度尼西亚和澳大利亚四国的近两百名华裔青少年将在黑龙江开启“冰雪狂欢”模式。</p>\n<p>　　进入12月后，中国北疆的冰雪旅游季拉开大幕。在中国冰雪文化热潮的背景下，最能代表中国冰雪文化特色的黑龙江成为海外华裔青少年们“寻根”的热点。</p>\n<div style=\"text-align:center\"> \n <img alt=\"2017年海外华裔青少年“中国寻根之旅”冰雪营-黑龙江营现场的授旗仪式。　杨拓 摄\" src=\"upload/image/abe1984d-bdc0-4e84-bcda-997f48e80081.jpg\" title=\"2017年海外华裔青少年“中国寻根之旅”冰雪营-黑龙江营现场的授旗仪式。　杨拓 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  2017年海外华裔青少年“中国寻根之旅”冰雪营-黑龙江营现场的授旗仪式。　杨拓 摄 \n</div>\n<p>　　黑龙江地处中国最北端，是中国最早开展冰雪产业的省份。世界最大的冰雪主题乐园冰雪大世界、太阳岛雪博会和雪乡等特色冰雪产业已经成为中国冰雪旅游的代表。随着中国成功申办2022年冬奥会，冰雪文化在中国民间掀起了热潮，而这一热潮的影响已经扩展到了海外的华人世界，并让黑龙江将成为了华裔青少年们的“首选”。</p>\n<p>　　“之前在家里只听爷爷奶奶们说起过这里(黑龙江)，说这里是中国最冷的地方。”来自马来西亚的叶子兴说：“马来西亚没有四季，所以能来黑龙江让我们非常兴奋，在没出发前就已经迫不及待地想要融入当地的冰雪文化了。”</p>\n<div style=\"text-align:center\"> \n <img alt=\"2017菲律宾华裔青少年“中国寻根之旅”冬令营-哈尔滨营启动现场，华裔少年柯俊杰接受当地媒体采访。　杨拓 摄\" src=\"upload/image/981d220a-25c8-4e5f-adcf-4a7e10d1d1cd.jpg\" title=\"2017菲律宾华裔青少年“中国寻根之旅”冬令营-哈尔滨营启动现场，华裔少年柯俊杰接受当地媒体采访。　杨拓 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  2017菲律宾华裔青少年“中国寻根之旅”冬令营-哈尔滨营启动现场，华裔少年柯俊杰接受当地媒体采访。　杨拓 摄 \n</div>\n<p>　　除了体验冰雪之外，来到黑龙江的海外华裔青少年也表示出了对冰雪运动的极大兴趣。“之前我在网上了解到，黑龙江是中国冬奥冠军的‘摇篮’，所以这次也想好好体验一下滑冰、滑雪等冬季运动。”来自菲律宾的柯俊杰说。</p>\n<p>　　“我省自1999年开始承办海外华裔青少年＇中国寻根之旅＇活动，至今已先后承办30期夏(冬)令营，参营的海外华裔青少年约2000人次，特别是‘冰雪营’深受广大海外华裔青少年的欢迎。”黑龙江省人民政府侨务办公室副主任王英春说道。可以预见，黑龙江的冰雪文化将会吸引更多海外华裔青少年前来感受中国冰雪的文化之“根”。(完)”</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/981d220a-25c8-4e5f-adcf-4a7e10d1d1cd.jpg',NULL,856,'2017-12-17 10:15:00','2017-12-17 19:18:28','73',0),(3031,'百余名中外选手呼伦贝尔-30℃挑战越野跑','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>呼伦贝尔12月17日电(记者张玮)2017中国・新巴尔虎右旗“冰雪之路”越野挑战赛17日在内蒙古自治区呼伦贝尔巴尔虎草原启幕，中外百余名选手-30℃“开跑”。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为肯尼亚选手一路领先 张玮摄\" src=\"upload/image/1c80c1b0-960a-44a1-9e90-33dc17ca9970.JPG\" title=\"图为肯尼亚选手一路领先 张玮摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为肯尼亚选手一路领先 张玮摄 \n</div>\n<p>　　比赛当日，当地气温低于-30℃，来自北京、天津、浙江、上海、广东及肯尼亚等地的百余名中外选手“全副武装征战”内蒙古大雪原。</p>\n<p>　　组委会消息称，本次挑战赛为22公里冰雪“半程马拉松”赛事，设有男子、女子专业组和业余组等比赛项目。北京奥运会自由式摔跤女子72公斤级冠军王娇、伦敦奥运会花样游泳团体亚军陈晓君，仰泳世界冠军高畅前来助赛。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为王娇、陈晓君、高畅参与其中 张玮摄\" src=\"upload/image/dd94e254-9cdd-42b7-b3e5-961b1f9e9e70.JPG\" title=\"图为王娇、陈晓君、高畅参与其中 张玮摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为王娇、陈晓君、高畅参与其中 张玮摄 \n</div>\n<p>　　来自肯尼亚的选手安德森获得男子专业组冠军。他说：“我是首次在这样极冷的气温下参赛，也是首次在冰雪中赛跑。这是一次挑战，天很冷，景很美，感受很特别。”</p>\n<p>　　“冬季长跑是一项考验人体能的运动，能在千里冰封、万里雪飘的地方举办这样的赛事，既赏心悦目，又挑战极限。”北京奥运会自由式摔跤72公斤级冠军王娇如是说。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为肯尼亚选手安德森夺冠 张玮摄\" src=\"upload/image/1b10cb8b-8160-447e-9b9e-85ac2d3d3486.JPG\" title=\"图为肯尼亚选手安德森夺冠 张玮摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为肯尼亚选手安德森夺冠 张玮摄 \n</div>\n<p>　　呼伦贝尔新巴尔虎右旗旗长布和巴雅尔表示，本次赛事的举办为中外运动员搭建了深入交流的平台，也将成为呼伦贝尔向世界展示冬季旅游的一种方式。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/1b10cb8b-8160-447e-9b9e-85ac2d3d3486.JPG',NULL,1639,'2017-12-17 18:13:00','2017-12-17 19:18:28','74',0),(3032,'“地标：测绘当代中国艺术”亮相北京嘉德艺术中心','于晓','中国新闻网','<div style=\"text-align:center\"> \n <img alt=\"汪建伟的《生生万物》 钟欣 摄\" src=\"upload/image/fdad5e7a-930f-4557-ad2b-a1db4d9e2833.jpg\" title=\"汪建伟的《生生万物》 钟欣 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  汪建伟的《生生万物》 钟欣 摄 \n</div>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>北京12月17日电 (记者 应妮)由国际资深策展人郑胜天、斐丹娜共同策划的“地标：测绘当代中国艺术”17日正式对公众开放，这是北京嘉德艺术中心当代艺术的首展，呈现了徐冰、黄永�f、汪建伟、蔡国强、刘建华、张健君、邱志杰、喻红、杨福东、徐震、刘�|、黄敏、吕山川、曹斐、孙逊等15位当代艺术家创作的具有纪念碑特征的大型装置、影象和绘画。</p>\n<div style=\"text-align:center\"> \n <img alt=\"徐冰的《鬼打墙》 钟欣 摄\" src=\"upload/image/392b3346-2e61-4953-afee-dae2fe307b29.jpg\" style=\"border:px solid #000000\" title=\"徐冰的《鬼打墙》 钟欣 摄\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  徐冰的《鬼打墙》 钟欣 摄 \n</div>\n<p>　　展览从不同的方面探索从具象到抽象、从过往到未来的“地标”，深入地剖析了这一名词的丰富内涵。作品包括徐冰的《鬼打墙》、汪建伟的《生生万物》、黄永�f的《嗡嘛呢叭咪�恕贰⒉坦�强的《蜘蛛网――为大英博物馆做的计划》、刘建华的《义乌调查》、张健君的《人类与他们的钟＃2》、邱志杰的《虚构地理学》、喻红《她――宝塔山姑娘》等等。</p>\n<p>　　关于此展，策展人郑胜天坦言：“展览并没有梳理美术史的野心，就是一个很单纯的展览。我只是想利用这么好的一个空间，把中国当代艺术30年来一些有意义的作品或者是大家印象比较深刻的作品重新放在一起，给观众一个再看到它们的机会。”他认为，中国当代艺术家们有时候费尽周折做出的装置艺术，或者展示的时间非常短，或者展出的机会非常少，因此大部分观众尤其是年轻一代比如“90后”“00后”，可能都没机会见到很多作品，只能在文献中读到相关作品的记载，比如徐冰的《鬼打墙》，黄永�f、张健君等人的作品。</p>\n<div style=\"text-align:center\"> \n <img alt=\"刘建华的《义乌调查》 钟欣 摄\" src=\"upload/image/5257ab26-b0a3-4540-b305-099a66496459.jpg\" title=\"刘建华的《义乌调查》 钟欣 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  刘建华的《义乌调查》 钟欣 摄 \n</div>\n<p>　　策展之初，郑胜天第一个想到的是徐冰的作品《鬼打墙》，“那是1990年，也是中国当代艺术第一次在国外比较大规模的展出，影响力比较大”。这件作品是艺术家尝试拓下长城的城墙，拓下来的过程就是不断把墨水“打”在附在城墙上的纸张，从而把墙面完整拓印下来。“这件作品感觉具有地标性质，不仅是最早的大规模作品，也是具有地标意义的地点。而嘉德艺术中心这座大楼希望能成为北京文化的新‘地标’，我和展览主办方想法不谋而合。”</p>\n<p>　　嘉德投资董事总裁兼CEO、嘉德艺术中心董事长寇勤介绍，嘉德艺术中心融合了北京旧式民居、中国山水画传统和当代设计的精神，大厦底部青砖样式的砖石“像素”，不仅从纹理、颜色和造型与建筑周边的胡同相融合，还辅之以山水画集大成之作《富春山居图》为原型，通过精炼提取的数千个圆孔像素嵌入墙体中，创造出抽象的山水轮廓。</p>\n<p>　　据悉，展览将持续至2018年1月31日。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/5257ab26-b0a3-4540-b305-099a66496459.jpg',NULL,715,'2017-12-17 15:21:00','2017-12-17 19:18:28','75',0),(3033,'南方首个乡村半程马拉松赛广东德庆开跑','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>肇庆12月17日电 (索有为 龙许)广东省肇庆市德庆县新农村示范片12月17日上午迎来了5000多名马拉松选手，他们在独具特色的岭南村落之间欢乐奔跑，享受运动的乐趣。近万名由本地乡亲和外来宾客构成的参与者，沐浴着冬日暖阳，共同见证了南方首个乡村半程马拉松――2017肇马・德庆乡村马拉松。</p>\n<p>　　本次比赛由肇庆市人民政府主办，为中国田径协会认证赛事。赛事设乡村半马跑(21.0975公里)和山水欢乐跑(5公里)两个项目。</p>\n<p>　　据了解，本次乡村半程马拉松系南方首个标准规范的乡村半程马拉松。比赛延续了“肇庆马拉松，欢跑山水中”活动口号，以“肇马+新农村建设”作为主题，旨在推动全民健身与健康德庆建设进程，加速“乡村+体育+旅游”深度融合，促进新时代城乡协调发展，为德庆打造生态休闲乡村游积累经验，将成为德庆美丽乡村建设又一张“新名片”。</p>\n<div style=\"text-align:center\"> \n <img alt=\"选手带娃上阵 龙许 摄\" src=\"upload/image/32e69160-c27a-442e-b2a9-8b1b46dea9b9.JPG\" title=\"选手带娃上阵 龙许 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  选手带娃上阵 龙许 摄 \n</div>\n<p>　　此次赛事把体育健身和新农村旅游推介紧密结合，将众多城里的体育爱好者吸引到乡村参与马拉松，让积极向上的体育精神延伸至美丽新农村，让农民也共享马拉松运动的乐趣。</p>\n<p>　　比赛起点设在官圩镇中学，途经省级新农村示范片区多个村庄，终点位于金林水乡景区。选手们在奔跑中领略了岭南村落、贡柑长廊、紫淮山园、葡萄庄园、紫薇花道、葱郁树林等乡村美景，热情的村民在赛道两旁表演雄鸡舞、蝴蝶舞、腰鼓舞、舞龙舞狮等民俗节目为他们加油鼓劲。</p>\n<p>　　赛事组委会在21公里的乡村半程马拉松赛道上设置了15个独具特色的赛事补给点，参赛选手和群众可以在补给点品尝到德庆贡柑、紫淮山等土特产品，还在赛事终点品尝到了巴戟糕、首乌糖等德庆特色小吃。</p>\n<p>　　赛事期间，德庆县将县内的著名旅游景点，如悦城龙母祖庙、德庆孔庙、三元塔、盘龙峡、金林水乡、玉龙寨等实行免门票开放，各大酒店、餐馆则推出以德庆竹篙粉为代表的优质本地美食，让游客品尝到真正的“德庆味道”。</p>\n<p>　　据悉，德庆县2014年入选第一批广东省级新农村示范片建设县。德庆县委书记陈元表示，德庆将以本次马拉松比赛为契机，扎实推进美丽乡村建设，力争2018年16个省定贫困村全面建成社会主义新农村示范村，引领全县推进新农村建设。同时，全域推进农村人居环境综合整治，深化农村综合改革，狠抓改革措施落地，激发农村经济发展活力，全力争当全省城乡协调发展示范县。</p>\n<div style=\"text-align:center\"> \n <img alt=\"2017肇马・德庆乡村马拉松开跑 许应斌 摄\" src=\"upload/image/d0458bdd-cce5-4041-aa17-ff5480183957.JPG\" title=\"2017肇马・德庆乡村马拉松开跑 许应斌 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  2017肇马・德庆乡村马拉松开跑 许应斌 摄 \n</div>\n<p>　　经过激烈角逐，最终吴金龙以1小时12分49秒荣获男子组半程马拉松冠军，宋璇以1小时26分44秒获得女子组半程马拉松第一名。</p>\n<p>　　本次赛事还设团体组项目，广州珠跑俱乐部跑团竞速队获得第一名。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/d0458bdd-cce5-4041-aa17-ff5480183957.JPG',NULL,980,'2017-12-17 15:19:00','2017-12-17 19:18:28','74',0),(3034,'西藏文化产业巧推“文化流量”提升','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>拉萨12月17日电 (赵朗 石凤英)日前，西藏新一轮的自治区级文化产业示范基地和自治区级文化产业示范园区申报工作启动。“这两个项目旨在提高西藏文化产业的总体实力和竞争力。”西藏自治区文化厅文化产业处处长罗布次仁17日接受<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>采访时如是说。</p>\n<p>　　截至目前，西藏已有41家国家级、自治区级文化产业示范基地和园区。罗布次仁透露，这部分基地和园区涵盖了西藏文化产业发展的重点领域，包括文化旅游业、民族手工艺品业、文化产品数字制作等，总年产值达6.65亿元(人民币，下同)。</p>\n<div style=\"text-align:center\"> \n <img alt=\"西藏培训非遗传承人，推动文化产业发展，图中佛像为非遗传承人作品。　孙翔 摄\" src=\"upload/image/c3b04f74-d336-4316-a315-e6daa5ec7d09.jpg\" title=\"西藏培训非遗传承人，推动文化产业发展，图中佛像为非遗传承人作品。　孙翔 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  西藏培训非遗传承人，推动文化产业发展，图中佛像为非遗传承人作品。　孙翔 摄 \n</div>\n<p>　　随着西藏文化产业顶层设计力度不断增大，其文化市场主体也不断壮大。“十三五”期间，西藏文化产业扶持资金总额不低于西藏财政支出的2%和对外援藏资金的20%。罗布次仁说，《西藏自治区“十三五”时期文化发展规划》也更注重引导更多民间资本投入西藏文化产业领域，采取贷款贴息、税收补贴、投资入股等多种方式转变扶持方向。</p>\n<div style=\"text-align:center\"> \n <img alt=\"第七届西藏唐卡艺术博览会吸引民众前来参观 何蓬磊 摄\" src=\"upload/image/32b6f75e-12cf-4913-a042-0e057a9097fb.jpg\" title=\"第七届西藏唐卡艺术博览会吸引民众前来参观 何蓬磊 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  第七届西藏唐卡艺术博览会吸引民众前来参观 何蓬磊 摄 \n</div>\n<p>　　据悉，目前，西藏建立了特色文化产业项目库，确定了中华民族特色文化产业示范园区、藏羌彝文化产业走廊建设、西藏特色文化产业基础设施建设三大类项目。初步统计，入库项目现已达到119个。另外，19个藏羌彝文化产业走廊重点项目全面启动，总投资达43.3亿元。</p>\n<p>　　随着经济的发展，西藏文化扶持力度的增加，西藏大众文化需求结构越来越多元，曾看似“高冷”的文化越来越受到认可。如油画、唐卡、手工艺等艺术展览已经成为西藏大多数民众的精神文化需求。</p>\n<div style=\"text-align:center\"> \n <img alt=\"第七届西藏唐卡艺术博览会展出的唐卡作品 何蓬磊 摄\" src=\"upload/image/06217080-f2eb-4c26-bba9-7933e9d7b759.jpg\" title=\"第七届西藏唐卡艺术博览会展出的唐卡作品 何蓬磊 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  第七届西藏唐卡艺术博览会展出的唐卡作品 何蓬磊 摄 \n</div>\n<p>　　又如，西藏舞台剧，虽发展历史不长，但近几年渐渐被观众认可。日前，公演的传统藏戏《朗萨雯波》，连演数天座无虚席。西藏自治区藏剧团团长班典旺久曾表示，它经过舞台化、数字化重新打造，满足了当代人对传统藏戏的审美。</p>\n<p>　　文化多元化体验不单单体现在舞台剧和艺术展览中，西藏题材动漫在文化产业扶持下也迎来了春天。全球第一部描写西藏的动画片《小卓玛》，曾创下当年中国国产动画片高收视率。正在上映的西藏第一部偶动画《阿古顿巴》，以西藏民间传说故事为主题，在当地也“吸粉”无数。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/06217080-f2eb-4c26-bba9-7933e9d7b759.jpg',NULL,180,'2017-12-17 16:34:00','2017-12-17 19:18:28','75',0),(3035,'第二架C919机长：相比首架C919 飞得更远更高更快','于晓','央视新闻客户端','<p>　　独家专访第二架C919机长：相比首架C919 飞得更远更高更快</p>\n<p>　　在这样一次飞行中，C919要完成怎样的任务呢？它未来会承担的角色会和首架C919飞机有何不同，关于这些问题，我们也是对执行此次飞行任务的机长吴鑫进行了独家专访。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/b5b01f72-248c-45ad-8393-3e504e1fb30f.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　吴鑫曾是今年5月5日C919首飞机组的副驾驶。此次驾驶第二架C919飞机进行首次飞行，吴鑫说，这次将比C919首飞安排更多的任务，因此飞行时间会更长。​</p>\n<p>　　中国商飞试飞中心第二架C919试飞机组机长 吴鑫：包括我们这次在空域里面，起落架会进行收放，会对襟副翼的形态进行检查，这几个构型的操纵稳定性会做一个全面的检查。</p>\n<p>　　记者：我记得101架机是在第二次飞行才做到收起落架。</p>\n<p>　　中国商飞试飞中心第二架C919试飞机组机长 吴鑫：对的。</p>\n<p>　　记者：时间上会比首飞要长？</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/07468cc1-7d51-4cf6-9038-656fa3688a6c.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　中国商飞试飞中心第二架C919试飞机组机长 吴鑫：对的，我们这次计划是在2个到2个半小时左右，比首飞长出1个小时。</p>\n<p>　　除了时间大幅增加外，第二架C919进行首次飞行，会到达更广阔的空域，更高的高度，达到更快的速度。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/ebd5ff11-f575-4611-af5d-40bb9ba334fe.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　中国商飞试飞中心第二架C919试飞机组机长 吴鑫：初步在10000英尺做一个检查后，我们会上升到15000英尺。增加空调，压调系统的检查。然后起落架的收放，襟副翼的收放都是在15000英尺做。因为襟副翼都收了，我们的速度会比101架机(首架C919)速度要大一些。我们这次预期速度到250节左右，也就是每小时450公里，首飞的时候我们速度限制在180节，也就是每小时350公里左右。</p>\n<p>　　吴鑫表示，这些提升也将意味着，第二架C919能够更快地完成飞行性能的初步验证，为尽快转场，开展下一阶段试飞打下基础。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/5f1d997c-0faf-47cd-a7ba-440dd6f9b5b1.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　中国商飞试飞中心第二架C919试飞机组机长 吴鑫：我们通过101试飞对飞机性能有了初步的了解，而且众所周知，我们在浦东机场试飞，对民航航班影响很大，我们也想试飞任务用较少的次数完成，尽量减少对民航的影响。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/5f1d997c-0faf-47cd-a7ba-440dd6f9b5b1.jpg',NULL,597,'2017-12-17 17:01:00','2017-12-17 19:18:28','64',0),(3036,'工作人员睡觉、玩手机 记者暗访政务大厅被塞红包','周驰','央视新闻客户端','<p>　　暗访 | 上班时间！政务大厅内工作人员睡觉、玩手机…还打羽毛球！</p>\n<p>　　一个城市的政务大厅，是当地政府设立的集中办理行政许可等行政审批项目以及一些服务项目的综合性机构，也是提高行政效能，为人民群众提供优质便捷高效服务的重要平台。然而，近日，记者却在吉林市政务大厅，拍到了与“便民服务”背道而驰的情形。</p>\n<p>　　<strong>​​工作时间开小差 上网睡觉玩手机</strong></p>\n<p>　　12月12日上午，记者在吉林市政务大厅内看到，前来办事的市民有很多，可政务大厅每个楼层均有不同程度空岗现象，另外还有工作人员上班时间上网、看手机、睡觉，对此记者进行了咨询，但工作人员并不作答。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/1789b9a4-32fb-4416-b6d8-12b158cff1fb.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　记者：现在是十一点半下班么？</p>\n<p>　　工作人员：对啊</p>\n<p>　　记者：那为啥现在就睡觉呢。上班时间可以玩微博么？</p>\n<p>　　工作时间，在政务大厅羽毛球场，工作人员竟赫然挥舞球杆，在场内锻炼。</p>\n<p>　　工作人员：俺们就是工业港的</p>\n<p>　　记者：工业港？</p>\n<p>　　工作人员：他们不允许随便打羽毛球，但是大厅工作人员在这打羽毛球 对啊</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/8f8a1d50-2a2d-4418-bbf1-6ac87ede8355.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　除了窗口岗位开小差外，政务大厅一层工作人员在上班时间看书。记者看到几位市民多次询问办事事宜，可这位工作人员头也不抬。</p>\n<p>　　不仅是吉林市政务大厅，在吉林市属龙潭区、丰满区、高新区、昌邑区政务大厅，记者也拍到了类似的情况。在高新区政务大厅内，窗口内有大量空岗现象，一台窗口仅仅两位工作人员在岗，在三楼税务部门窗口内几位工作人员全在低头看手机。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/e0d1bcca-39af-41df-92ca-5f0e52d696d0.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　记者：你在干嘛，同志？</p>\n<p>　　工作人员：没事</p>\n<p>　　记者：我能看看吗？</p>\n<p>　　工作人员：我们在看新闻</p>\n<p>　　随后记者又来到吉林市丰满区政务大厅，大厅内同样有工作人员在看书，看手机。面对记者的询问，这位工作人员是这样解释的。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/e10d00f0-eaf6-4b9c-8234-5e86d1154232.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　工作人员：你首先要弄明白他们的手机在干嘛。现在我们有学习支部的，他们正在看政治新闻呢，你不能说他们玩手机。</p>\n<p>　　记者：那好，我想问一下，在正常上班时间吉林市政府规定允许他们玩手机了吗？</p>\n<p>　　工作人员：他们不是玩手机，他们是在看新闻学习呢，这怎么是玩手机呢</p>\n<p>　　<strong>塞钱给记者 记者：不好意思不能收</strong></p>\n<p>　　在拍摄到这些现象的当天下午，吉林电视台记者返回吉林市政务大厅，将情况反映给有关领导，在这之后，意想不到的事情发生了。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/174b80f2-b0a2-48ec-bb9d-bb194eb8c690.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　吉林市政务大厅主任：</p>\n<p>　　虽然我们加强了大厅管理，但在大厅管理上还有漏洞，我们想针对这次的曝光情况检查出的问题，我们将认真核实</p>\n<p>　　当记者离开政务大厅，汽车刚刚发动，意想不到的一幕发生了，一位陌生男子打开了后侧车门向车内扔进了两个信封，随后快速离开。记者打开信封后发现是两大叠现金。记者赶忙下车追赶，并将两个信封送还。</p>\n<p>　　记者：不好意思，这钱我们不能收。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/174b80f2-b0a2-48ec-bb9d-bb194eb8c690.jpg',NULL,1351,'2017-12-17 14:22:00','2017-12-17 19:18:28','64',0),(3037,'云集微店百县千品进陕西 助力富平柿饼线上大卖','陈海峰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月15日电 12月14日，以“柿子甜了”为主题，富平柿饼牵手云集微店“喜”柿发布会在陕西富平县举行。云集微店副总裁张铁成、百县千品项目负责人吴雪及云集微店店主代表受邀前往富平柿饼基地，参观富平柿饼的传统加工工艺。当天，富平柿饼同步上线云集微店，这一“国宝级柿饼”挥着社交电商的翅膀，自此正式开启线上零售起飞之路。</p>\n<p>　　<strong>1小时26000斤柿饼被抢光</strong></p>\n<p>　　据了解，富平柿饼以当地生产的传统名优柿子品种“富平尖柿”为原料，经过清洗削皮、日晒压捏、捏晒整形、定型捂霜等多道工序精细制作而成。江湖人称其“吊饼”，“吊柿饼”是陕西富平县的传统。富平柿饼有四个特点，即甜、软、糯、无核。柿饼肉多霜厚，入口即化，甜美入心。</p>\n<p>　　12月14日上午9时，首批富平柿饼正式在云集微店上线。通过云集微店覆盖全国各地的270多万店主的分享，“富平柿饼”品牌信息飞速传播，触达至上千万终端消费者。上线仅1小时，首批10000组2.6斤装的富平柿饼已经被一抢而光，后续紧急补仓10000组再次上线。截至17时30分，20000组富平柿饼告罄。</p>\n<p>　　通过本次战略合作，云集微店帮助富平柿饼取得销售上的突破，全面打开线上零售市场，助力富平县真正作为“中国柿子之乡”享誉全国，打造了又一张地域优质农产品金名片。</p>\n<div style=\"text-align:center\"> \n <img alt=\"云集微店“百县千品”项目助力富平柿饼线上大卖\" src=\"upload/image/d40dd3e2-a81c-41f1-a7cf-3731996c7d44.jpg\" style=\"border:px solid #000000\" title=\"云集微店“百县千品”项目助力富平柿饼线上大卖\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  云集微店“百县千品”项目助力富平柿饼线上大卖 \n</div>\n<p>　　<strong>社交电商助力精准扶贫</strong></p>\n<p>　　富平柿饼的线上大卖，其实是云集微店“百县千品”项目的又一阶段性成果。</p>\n<p>　　近年来，随着互联网电商的兴起，中央精准扶贫工程提出了“电商扶贫”的号召。为此，云集微店聚焦农产品上行，进行了全方位的探索与实践。今年5月15日，云集微店在两周年庆典上宣布，联手浙大CARD中国农业品牌研究中心共同发起“百县千品”项目。该项目计划在三年时间内，培育孵化出100个地理标志农产品品牌，切实解决农产品上行问题。富平柿饼正是该项目的受益者之一。</p>\n<p>　　据悉，富平柿饼作为作为富平县的支柱产业，2014年被授予地标产品，近年来发展颇为迅速。目前，全县有13万亩柿子，年产鲜柿5万吨，加工柿饼1万吨，总产值6.5亿元，已有2100多户贫困户依托柿子产业实现了脱贫。尽管富平的柿子、柿饼价格一路攀升，但暴露出来的问题也亟待解决。比如，加工环节和后端营销的组织化、规模化、商品化程度差距明显，如何跳出低价竞争的泥淖，进行差异化竞争等。</p>\n<p>　　云集微店“百县千品”项目旨在打通销售渠道的同时，更注重打造品牌孵化平台。云集微店副总裁张铁成表示，“许多优质农产品往往由于信息和渠道不通畅而导致滞销或价格优势不明显，而云集微店正是帮助这些地标农产品电商化、品牌化，从而解决销售和溢价问题，实现农业增效增收。”</p>\n<p>　　<strong>脉冲式流量供给推动农产品上行</strong></p>\n<p>　　自今年5月启动以来，云集微店“百县千品”项目已创造了诸多销售奇迹：40秒抢光安徽界首25000斤土豆，1天售罄30000斤广西百色桂七芒，3小时卖完10万斤陕西洛川苹果，以及1.5小时卖掉25000斤临潼石榴等。</p>\n<p>　　这些亮眼的成绩背后，云集微店的社交平台优势起到了关键作用。虽说才成立两年多时间，但是在社交电商领域，云集微店俨然已成长为行业领军者。云集微店采用S2b2c模式，提供从货源、物流、客服、营销、IT 支持、培训等全方位一站式服务，为店主赋能，让他们通过社交媒体的及时交互，更好地发现消费者需求。</p>\n<p>　　这是社交零售的新趋势所在。以富平柿饼为例，相当于云集微店全球各地的270多万店主共同为其代言，通过协同化的社交网络，利用每一个店主的社交影响力来销售，节省了推广和流量获取成本。这种社交电商“脉冲式”的流量供给，正好符合了农产品上行量大、时短的特征。广度、深度、信任度结合在一起，最后便是商品信息传递和消费的速度。</p>\n<p>　　据了解，云集微店“百县千品”项目一直在全国范围内挖掘优质农产品，帮助更多县域打造地标性特色农产品牌。“百县千品”此次深入陕西，除了富平县外，还到了另一个国家级贫困县――榆林市吴堡县，同样聚焦当地最负盛名的地标农产品吴堡红枣等，并计划在近期上线云集微店。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/d40dd3e2-a81c-41f1-a7cf-3731996c7d44.jpg',NULL,1284,'2017-12-15 15:56:00','2017-12-17 19:18:28','82',0),(3038,'加强农产品推广 河北农业打造特色区域品牌','陈海峰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月15日电 一方水土，孕育一方特色。多年来，河北农业立足资源禀赋和区位优势，大力发展品牌农业、科技农业、绿色农业、质量农业，深入实施农产品加工倍增计划。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/0bd3f3e7-8c14-4683-b360-91e5be065d97.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　河北素有中国地理环境缩影的称号，其自然条件非常优越。地型地貌类型齐全，生物资源丰富多样，是世界公认的优质奶源区、葡萄种植黄金带和落叶果树最佳适生区。张家口、承德地区空气清新，植被覆盖率高，是北京的天然生态屏障和重要水源地。河北地区优越的自然条件，加快了河北农业发展的步伐。</p>\n<p>　　河北地区立足资源优势，农业基础极为深厚。全省主要农产品产量均居全国前列，粮食总产连续四年保持在670亿斤以上；蔬菜、水果、肉类、禽蛋、奶类、其中乳制品产量居全国首位，成为蒙牛、伊利、三元等企业的重要奶源地，日处理生鲜乳1.4万吨；水产品产量132万吨，渤海对虾、梭子蟹等海产品驰名中外。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/99ed09f1-e692-4904-8e51-9e8b5764f7d9.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　河北是北京的“菜篮子”，并构建全程监管机制。2016年北京市场上三分之一的蔬菜、一半的羊肉、鸡肉和80%的牛肉来自河北供应。全省农业标准化覆盖率达50%以上，建立了全程可追溯、互联共享的农产品质量安全信息平台，构建了从产地到餐桌的全程监管机制。</p>\n<p>　　当前，河北正在大力实施“区域、企业、产品”三位一体的农业品牌发展战略，“河北造”的品牌特色越来越鲜明。</p>\n<p>　　河北的产业优势打造了独有的区域品牌，并展现区域品牌差异化。河北各地依托资源优势和文化传承，始终将差异化、特色化作为品牌发展的核心，围绕蔬菜、乳品、林果、杂粮、食用菌、马铃薯等优势产业，走“人无我有、人有我优、人优我名”的路子，打造“河北特色、中国第一、世界知名”的区域品牌。</p>\n<p>　　好产品永远是市场的“敲门砖”，产品远销世界各地，河北农产品品牌逐渐呈多样化发展。经过多年的努力，河北培育的品牌农产品达到6万1千多个，涵盖了粮油、生鲜、果品、零食、饮品等领域，从“初字号”、“原字号”农产品到精深加工产品，全省通过无公害、绿色、有机认证的产品达到2578个，获得国际质量管理体系认证的有169个。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/a0c03b5b-80b8-46ba-8813-726589e8b364.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　河北坚持将企业作为品牌创建的主体，在科技创新、品牌创建、质量控制、市场营销等方面狠下功夫，使企业品牌不断壮大。经过多年的努力，河北培育了省级农业产业化龙头企业720家，国家级农业产业化龙头企业46家，河北培育的品牌农产品达到6万1千多个，涵盖了粮油、生鲜、果品、零食、饮品等领域。</p>\n<p>　　河北农产品将进一步打开北京消费市场，加强农产品推广渠道和宣传力度。据了解，河北农业将与北京二商集团、首农集团、北京菜篮子集团、中信国安集团和北京餐饮协会展开战略合作，让更多的北京市民品尝到河北品牌农产品的美味和特色。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/a0c03b5b-80b8-46ba-8813-726589e8b364.jpg',NULL,685,'2017-12-15 17:13:00','2017-12-17 19:18:28','82',0),(3039,'戏曲表演门外汉：用脸谱浮雕留住戏曲(图)','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>长治12月17日电 题：戏曲表演门外汉：用脸谱浮雕留住戏曲</p>\n<p>　　作者 李庭耀 杨静</p>\n<p>　　17日，山西省长治市黎城县一间工作室内，42岁的张学义手拿上党梆子脸谱浮雕，细细地勾勒出脸谱的轮廓。他说自己是表演戏曲的门外汉，却一心想把戏曲这门口传心授的艺术形式留在人们心中。</p>\n<p>　　上党梆子是山西省四大梆子之一，形成于明末清初，以演唱梆子为主，兼唱昆曲、皮黄、罗戏和卷戏。上党梆子各行当的基本表演程式称为“三把”，运用时演员挺胸昂头，腕柔臂圆。上党梆子以其稳健有力、简练明快的表演风格流行于山西省东南部长治、晋城等地。2006年，上党梆子入选中国首批国家级非物质文化遗产名录。</p>\n<div style=\"text-align:center\"> \n <img alt=\"17日，山西省长治市黎城县一间工作室内，42岁的张学义手拿上党梆子脸谱浮雕，细细地勾勒出脸谱的轮廓。他说自己是表演戏曲的门外汉，却一心想把戏曲这门口传心授的艺术形式留在人们心中。　张洪波 摄\" src=\"upload/image/a6189c19-aeef-440f-97c4-b36d8d407741.jpg\" title=\"17日，山西省长治市黎城县一间工作室内，42岁的张学义手拿上党梆子脸谱浮雕，细细地勾勒出脸谱的轮廓。他说自己是表演戏曲的门外汉，却一心想把戏曲这门口传心授的艺术形式留在人们心中。　张洪波 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  17日，山西省长治市黎城县一间工作室内，42岁的张学义手拿上党梆子脸谱浮雕，细细地勾勒出脸谱的轮廓。他说自己是表演戏曲的门外汉，却一心想把戏曲这门口传心授的艺术形式留在人们心中。　张洪波 摄 \n</div>\n<p>　　张学义是土生土长的长治人，他的外婆曾是上党戏曲的表演者。他回忆，自己很小的时候就跟着外婆进戏班玩耍。每逢庙会、赶集，上党梆子的戏台下总是挤满观看的人。从小的耳濡目染让张学义对地方戏曲文化产生了浓厚的感情。</p>\n<p>　　在当前新娱乐方式的冲击下，上党梆子的观众大量流失，很多剧团入不敷出，最终被迫解散。如何将戏曲通过别样的形式留存下来，成为张学义思考的问题。</p>\n<p>　　2010年，张学义萌生将戏曲脸谱做成彩绘浮雕的想法。他四处拜访老艺人，学习戏曲脸谱的绘制方法。在搜集整理上党戏曲脸谱相关资料的过程中，他发现很多上党戏曲脸谱都濒临失传或已经失传。</p>\n<p>　　“如果能用石膏模具上色、抛光这样的工艺，把这些脸谱做出来，使它们流传于民间，这对于传承上党戏曲艺术是一种贡献。”张学义说。</p>\n<div style=\"text-align:center\"> \n <img alt=\"张学义认为，戏曲只有留存在舞台上、留存在民众心中，才不会消亡。作为戏曲艺术的一部分，戏曲脸谱艺术在民众中的认知度越高，就越有利于戏曲艺术的传承和发展。　张洪波 摄\" src=\"upload/image/7fd1ae6a-03c6-4dfc-ba37-5c089321918f.jpg\" title=\"张学义认为，戏曲只有留存在舞台上、留存在民众心中，才不会消亡。作为戏曲艺术的一部分，戏曲脸谱艺术在民众中的认知度越高，就越有利于戏曲艺术的传承和发展。　张洪波 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  张学义认为，戏曲只有留存在舞台上、留存在民众心中，才不会消亡。作为戏曲艺术的一部分，戏曲脸谱艺术在民众中的认知度越高，就越有利于戏曲艺术的传承和发展。　张洪波 摄 \n</div>\n<p>　　张学义认为，戏曲只有留存在舞台上、留存在民众心中，才不会消亡。作为戏曲艺术的一部分，戏曲脸谱艺术在民众中的认知度越高，就越有利于戏曲艺术的传承和发展。</p>\n<p>　　让张学义没有想到的是，探索戏曲脸谱彩绘浮雕制作方法的难度如此之深。2010年下半年，张学义基本以这样一种状态度过：家里堆满试验戏曲脸谱制作工艺用的废料，投入数万元购买原料、反复试验却无法突破一些细节上的难题。最后，连家人都开始反对他的执拗。</p>\n<p>　　外出学习，反复尝试。张学义的坚持终于让他找到制作戏曲脸谱彩绘浮雕的方法。张学义说，他已收集200多种上党戏曲脸谱的图文资料。按照这些资料，制模、雕刻、打磨、抛光、彩绘上色、防腐的纯手工制作流程也已完善。张学义的戏曲脸谱浮雕被列入山西省第五批非物质文化遗产名录。</p>\n<p>　　2011年，张学义从村里找来十几位手艺人帮助加工戏曲脸谱彩绘浮雕。张学义的浮雕以上党戏曲人物脸谱为主，大多以古代英雄、忠臣良将为题材。比如黑脸的包拯、红脸的关公、豪爽勇猛的李逵等戏曲中人物脸谱。如今，张学义制作的戏曲脸谱彩绘浮雕受到很多人的欢迎，也已远销美国、意大利等国。</p>\n<p>　　张学义说，他准备把彩绘浮雕的脸谱内容拓展到其它戏曲种类。“现在有很多年轻人喜欢戏曲脸谱艺术，这说明了戏曲这种传统艺术的生命力所在。一定要用脸谱把戏曲留在人们心中。”(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/7fd1ae6a-03c6-4dfc-ba37-5c089321918f.jpg',NULL,719,'2017-12-17 13:14:00','2017-12-17 19:18:28','75',0),(3040,'不忘初心 共赢冠军――蒙牛强势发力2018','吉翔','中国新闻网','<div style=\"text-align:center\"> \n <img src=\"upload/image/e1f50818-e4bb-48db-a7e1-ee7d97e392dd.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>12月16日电 12月16日，蒙牛集团迎来了年度盛会――蒙牛集团2018客户年会。7000余名经销商代表、蒙牛员工齐聚厦门国际会展中心，共同探寻2018共赢冠军的路径。蒙牛集团CEO卢敏放在年会做了“不忘初心 共赢冠军”的主题报告，他重点强调道，蒙牛的初心是“内蒙牛，中国牛，世界牛”，在朝着初心奋进同时更要塑造“狼性文化”，以必赢、共赢的狠劲，向冠军之路进发。此外，蒙牛在年会现场宣布牵手俄罗斯世界杯，超高端新品也正式亮相，为打赢这场“共赢之战”不断加码。</p>\n<p>　　2017年，在股东的鼎力支持下，在“聚焦聚势、凝心凝力”的战略指引下，蒙牛继续推动组织架构调整，增持了现代牧业，发力常温、低温产品，并进行了广泛的战略合作。这一系列行动使得蒙牛走上了稳健增长的轨道，并一路驶入全球乳业十强。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/15b8c05f-8978-44bf-a116-303c073e1568.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　在今年，蒙牛明星品牌表现抢眼，去年销售额即已突破百亿的中国牛奶第一单品特仑苏获得了接近12%的增长。此外，纯甄、真果粒分别收获了30%、25%的高增长，冠益乳、优益C等产品也取得不俗业绩。</p>\n<p>　　值得庆贺的是，蒙牛低温事业部只用了11个月的时间就完成100.6亿元销售额，这也是中国乳业低温品类第一次实现这样的跨时代突破。蒙牛低温新产品销售占比突破性地占到总销售额的25%。明星产品的优异表现加上创新产品的集中爆发，帮助蒙牛低温品类实现了销售额两位数的增长，并牢牢占据着市场份额的首位。</p>\n<p>　　目前，蒙牛的朋友圈不仅包含上海迪士尼度假区、北京环球度假区、NBA、中国航天基金会、博鳌亚洲论坛等国内外顶尖资源，也包括阿里、京东这样的互联网巨头，在今年的“双11”期间，蒙牛又喜获佳绩，再次拿下乳制品全网销售冠军，夺得三连冠。</p>\n<p>　　中粮集团副总裁、蒙牛集团董事长马建平参会并对2018年寄予厚望。他代表股东中粮、达能、阿拉・福兹，对蒙牛的组织改革成效、经营业绩的大跨步提升表示肯定，也对蒙牛2018年的战略部署和双千亿战略目标充满信心。他引用“爱拼才会赢”这句歌词，勉励蒙牛人以爱拼的精神向冠军进发。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/c9a270a5-fdbc-4506-ab00-366230f1c64a.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　此次客户会上，特仑苏超高端荣耀首发的全球首款玻璃瓶常温酸奶、纯甄PET饮用型等新品集中亮相，以及“千镇万店”工程、网点突破200万等渠道新政，满满的“干货”让经销商客户直呼过瘾。而现场蒙牛宣布成为“俄罗斯世界杯全球官方赞助商”这一重磅消息更是让所有经销商为之振奋。不难看出，这场“共赢大战”即将开幕。</p>\n<p>　　新时代，新征程。正如总裁卢敏放所说，“2018年，是蒙牛双‘千亿’目标的关键一年。在机遇与挑战并存的形势下蒙牛必须‘赢’，我们要讲好蒙牛品牌故事，聚焦资源优势，以创新保持不凡。在狼性精神的推动下，持续深化改革，节本增效，加大创新研发力度；同时拓宽视野，做好全球布局，培育全球品牌，同所有合作伙伴一起共赢冠军。”</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/c9a270a5-fdbc-4506-ab00-366230f1c64a.jpg',NULL,505,'2017-12-16 15:13:00','2017-12-17 19:18:28','82',0),(3041,'天然气荒？别紧张，国家紧急出大招缓解用气紧张','周驰','央视财经','<p>　　<strong>天然气荒？！ 国家紧急出大招，你别慌！</strong></p>\n<p>　　最近“天然气供应紧张”问题广受关注，个别地区出现天然气气源告急现象。那么，今年天然气供不应求的原因是什么？如何缓解“气荒”？国家紧急部署了一个大招：16号，广东省天然气管网在鳌头首站开启天然气反输通道。这是国内首次由省级管网向国家主干管网反输天然气，也是首次“南气北输”。 这次的南气北输，是在国家发改委指导下，采用气源置换的方式进行。</p>\n<p>　　<strong>长沙启动天然气保供：本年度购气满1500方的居民用户限购</strong></p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/6a8ab454-9863-470b-8ec3-753cc09a73db.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　受气温逐步降低影响，长沙的天然气出现气源告急。15号，长沙启动天然气三级应急预案以保障居民的基本生活用气。 对具备双能源的工商业用户采取停供措施，要求其启动备用能源；对本年度天然气购气量已满1500立方的居民用户进行限购：每天限购天然气15立方。</p>\n<p>　　据了解，目前限购人群影响范围大概为6000户左右。根据长沙新奥燃气的预测，今冬明春长沙市气量缺口超6000万立方米，高峰日缺口150万立方米。</p>\n<p>　　<strong>“气荒”是因为民用“煤改气”吗？ 专家说真实原因是……</strong></p>\n<p>　　清华大学研究生院院长、原热能工程系主任，长江学者特聘教授 姚强16日就相关问题接受了记者采访。</p>\n<p>　　“今年天然气供不应求的紧张局面，是需求侧超预期规模的增长和供应侧始料未及的减少造成的。”姚强说。</p>\n<p>　　他介绍，今年1至10月，全国天然气消费量1865亿立方米，同比增长18.7%。其中，城市燃气增长10.1%，工业增长22.7%，发电增长27.5%，化工增长18.2%。</p>\n<p>　　出现天然气供应短缺时，基本的政策应该是“压非保民”，即压低非民用气，保障民用气。</p>\n<p>　　姚强指出，实际上，很多地方未能及时制定严格、详细、能落地的“压非保民”措施，未能将增加的用气量用于民用，而是给了工业。有的还出现了一些不正常情况，如由于民用气和非民用气价格双轨制，一些燃气公司打着民用气的名义申请到更多天然气，却将低价购得的民用气销售给出价更高的非民用使用，赚取差价，从中牟利，使本该保障民生的天然气供给更加紧缺。</p>\n<p>　　“综合分析，‘气短’现象应该是暂时的，国家各方面正在努力增加天然气供应，同时加强基础设施建设，相信民用天然气的供应可以得到保障。”他说。</p>\n<p>　　这会不会影响清洁取暖推进？他表示，应坚定不移地继续推进北方地区清洁取暖，在有条件的地区继续实施“煤改气”“煤改电”。</p>\n<p>　　他分析指出，“煤改气”是指将散烧煤改气，并不是所有的煤都要改用天然气。民用散煤的“煤改气”“煤改电”是民生工程，也是民心工程。</p>\n<p>　　其次，民用散煤确实加剧了我国北方地区冬季雾霾，发达国家治霾经验之一就是使用清洁能源。清洁取暖符合我国大气污染治理的实际。从今年环境质量看，“2＋26”城市11月PM2.5浓度同比下降37%，改善程度前所未有，“煤改气”“煤改电”的贡献功不可没。</p>\n<p>　　此外，民用散煤无法有效治理，替代是最佳途径，无法替代的也要采用清洁煤。</p>\n<p>　　他建议，对天然气供应和消费的特性要加强分析，加强统筹规划，提前安排好供气，有序推进。在“煤改气”“煤改电”设施没有完工的地方，原有的燃煤取暖设施也不能拆除，同时对于可能出现的天然气供应问题应做好预案，保障群众供暖。</p>\n<p>　　<strong>缓解北方用气紧张 中国首次“南气北输”</strong></p>\n<p>　　近期我国北方地区大面积天然气供应紧张，为缓解这一局面，16号，广东省天然气管网在鳌头首站开启天然气反输通道。这是国内首次由省级管网向国家主干管网反输天然气，也是首次“南气北输”。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/60f2b7d5-3097-409c-8e6c-b535c2b9336e.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　广东省天然气管网公司总经理 王革：我宣布广东省管网公司，反输西二线正式启动！</p>\n<p>　　16号上午9点，广东省管网在广州从化鳌头首站开启天然气反输通道，将来自中海油的南海海气和珠海LNG资源反输到中石油西气东输二线，首日300万立方米，未来可达到每天500万立方米。这次的南气北输，是在国家发改委指导下，采用气源置换的方式进行：一是中石油把它供应广东地区的天然气资源调往北方，另一方面是中海油用南海海气和LNG资源为中石油广东地区用户保供。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/56261696-00ef-4957-a17c-a05b062bc2ab.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　广东省天然气管网公司总经理 王革：把中石油目前供应广东的将近700多万方气调往北方，共同地来保证和助力，缓解北方的气荒。</p>\n<p>　　此次南气反输北方成功，证明广东省管网已具备了“南气北输”的基础条件。另据国资委消息，中海油“南气北运”第一车液化天然气，日前已将20吨液化天然气运抵华北地区。未来一段时间，参与“南气北运”的槽车将超过100辆。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/56261696-00ef-4957-a17c-a05b062bc2ab.jpg',NULL,1782,'2017-12-17 15:02:00','2017-12-17 19:18:29','66',0),(3042,'“探知未来”济南赛区复赛落幕 5支队伍晋级全国总决赛','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>济南12月17日电 (沙见龙)“探知未来”2017全国青年科普创新实验暨作品大赛济南赛区复赛12月17日在山东省科技馆举办。5支代表队从来自山东、河北的67支参赛队伍中脱颖而出，晋级全国总决赛。</p>\n<p>　　当天的复赛分为科普实验和创意作品两个单元，包含“风能利用”、“火星探索”、“未来教育”、“未来出行(中学组)”、“未来出行(大学组)”5个命题，每个命题总分居第一的队伍将晋级全国总决赛。</p>\n<div style=\"text-align:center\"> \n <img alt=\"选择“火星探索”命题的制作团队现场调试自己的作品。　沙见龙 摄\" src=\"upload/image/ba265880-9bcd-483a-90e1-e7271605f973.jpg\" title=\"选择“火星探索”命题的制作团队现场调试自己的作品。　沙见龙 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  选择“火星探索”命题的制作团队现场调试自己的作品。　沙见龙 摄 \n</div>\n<p>　　共有山东、河北各地市的122名中学生、62名大学生参加复赛。记者在现场看到，参赛队员现场领取制作材料后便开始着手制作，并在规定时间内完成作品。各队在制作过程中还要不断对作品进行测试、改进。</p>\n<p>　　来自山东郓城的初中生张越将和队友完成“风能利用”的命题，团队最终制作的参赛作品叫“逆风小车”，让小车将风力转换成动力逆风前行。张越告诉记者，初中开始接触物理知识后对科学制作产生了浓厚兴趣，能够在制作过程中发现很多不可思议的奥秘，学到更多知识。</p>\n<div style=\"text-align:center\"> \n <img alt=\"选择“风能利用”命题的制作团队正在调试自己的“逆风小车”。　沙见龙 摄\" src=\"upload/image/df57f64c-4b7a-438a-8289-abb0b692e999.jpg\" title=\"选择“风能利用”命题的制作团队正在调试自己的“逆风小车”。　沙见龙 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  选择“风能利用”命题的制作团队正在调试自己的“逆风小车”。　沙见龙 摄 \n</div>\n<p>　　“科技作品制作能够提高学生的动手能力，让学生将学到的知识应用到实践中”。在场外等候参赛队伍的济南第十四中学代表队指导老师张艳在接受<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>记者采访时表示，学生在制作中需要不断修改完善自己的作品，是一个不断探索和进步的过程，可以让学生在实践中更深刻地理解科学知识。</p>\n<p>　　据济南赛区组委会介绍，自大赛启动报名以来，济南赛区共有7000多支队伍报名参赛，其中1200多支队伍、3000多名学生提交参赛作品。通过科普实验单元排行榜、创意作品单元专家评审审议等方式，67支团队进入济南赛区复赛。复赛中优胜的5支队伍将代表济南赛区参加全国总决赛。</p>\n<p>　　据了解，全国青年科普创新实验暨作品大赛由中国科协科普部、共青团中央学校部共同主办，旨在鼓励“新一代”青少年学生积极参与科普实践，激发青少年梦想、关注未来。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/df57f64c-4b7a-438a-8289-abb0b692e999.jpg',NULL,476,'2017-12-17 16:19:00','2017-12-17 19:18:29','65',0),(3043,'热带风暴“厄多哈”继续给菲中部带来豪雨','吴倩','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>马尼拉12月17日电 (记者 关向东)厦门潜水游客马天南，17日9时终于登上接应的私家小船，与十多位来自法国的潜水客，离开了被热带风暴“厄多哈”(Urduja)围困三天的菲律宾中部宿务省马拉帕斯卡(Malapascua)岛。</p>\n<div style=\"text-align:center\"> \n <img alt=\"“厄多哈”16日肆虐菲中部的维萨亚(Visayas)及棉兰佬的卡拉加区(Caraga)，所带来的洪水及山崩，导致停电及交通中断，数万家庭被迫逃离家园。 图为受“厄多哈”影响，宿务达安班塔延街头被吹走屋顶的房屋。　马天南 摄\" src=\"upload/image/4ddcdfb9-d182-4caa-899e-994df0582d10.jpg\" title=\"“厄多哈”16日肆虐菲中部的维萨亚(Visayas)及棉兰佬的卡拉加区(Caraga)，所带来的洪水及山崩，导致停电及交通中断，数万家庭被迫逃离家园。 图为受“厄多哈”影响，宿务达安班塔延街头被吹走屋顶的房屋。　马天南 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  “厄多哈”16日肆虐菲中部的维萨亚(Visayas)及棉兰佬的卡拉加区(Caraga)，所带来的洪水及山崩，导致停电及交通中断，数万家庭被迫逃离家园。 图为受“厄多哈”影响，宿务达安班塔延街头被吹走屋顶的房屋。　马天南 摄 \n</div>\n<p>　　“这座约五千人居住的小岛，有一百多家潜店和度假村，客人来自世界各地”，马天南告诉<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者，受“厄多哈”影响岛上13日起即风雨交加，出岛交通已关闭数日，她居住的BB客栈16日起停电，17日起停水。</p>\n<div style=\"text-align:center\"> \n <img alt=\"受热带“厄多哈”影响，菲律宾中部宿务省马拉帕斯卡(Malapascua)岛上13日起即风雨交加，出岛交通已关闭数日，16日起停电，17日起停水。 17日晨，风雨间隙，几艘私家船载了几十位客人出岛回宿务省达安班塔延。　马天南 摄\" src=\"upload/image/01fa5e40-8f3f-4079-a1e9-1953a9675e4c.jpg\" title=\"受热带“厄多哈”影响，菲律宾中部宿务省马拉帕斯卡(Malapascua)岛上13日起即风雨交加，出岛交通已关闭数日，16日起停电，17日起停水。 17日晨，风雨间隙，几艘私家船载了几十位客人出岛回宿务省达安班塔延。　马天南 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  受热带“厄多哈”影响，菲律宾中部宿务省马拉帕斯卡(Malapascua)岛上13日起即风雨交加，出岛交通已关闭数日，16日起停电，17日起停水。 17日晨，风雨间隙，几艘私家船载了几十位客人出岛回宿务省达安班塔延。　马天南 摄 \n</div>\n<p>　　17日晨，风雨间隙，两艘私家船载了几十位客人出岛回宿务省达安班塔延。船员Lvan电话里告诉记者：自己家的房子靠近海边，昨夜屋顶被吹飞了。</p>\n<div style=\"text-align:center\"> \n <img alt=\"17日9时十多位来自法国的潜水客，乘坐私家船离开了被热带风暴“厄多哈”(Urduja)围困三天的菲律宾中部宿务省马拉帕斯卡(Malapascua)岛。 这座约五千人居住的小岛，有一百多家潜店和度假村，客人来自世界各地。　马天南 摄\" src=\"upload/image/320586da-82d4-4d6e-bba4-5d94a6507c9a.jpg\" title=\"17日9时十多位来自法国的潜水客，乘坐私家船离开了被热带风暴“厄多哈”(Urduja)围困三天的菲律宾中部宿务省马拉帕斯卡(Malapascua)岛。 这座约五千人居住的小岛，有一百多家潜店和度假村，客人来自世界各地。　马天南 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  17日9时十多位来自法国的潜水客，乘坐私家船离开了被热带风暴“厄多哈”(Urduja)围困三天的菲律宾中部宿务省马拉帕斯卡(Malapascua)岛。 这座约五千人居住的小岛，有一百多家潜店和度假村，客人来自世界各地。　马天南 摄 \n</div>\n<p>　　菲律宾国家气象局大气、地球物理和天文服务管理局(PAGASA)17日8时发布最新公告，热带风暴“厄多哈”继续以每小时13公里的速度向西移动，预计萨马岛北部地区将会有更多的暴雨，热带风暴中心附近最大风速为65公里/小时，阵风高达110公里/小时。</p>\n<div style=\"text-align:center\"> \n <img alt=\"受热带“厄多哈”影响，菲律宾中部宿务省马拉帕斯卡(Malapascua)岛上13日起即风雨交加，出岛交通已关闭数日，16日起停电，17日起停水。这座约五千人居住的小岛，有一百多家潜店和度假村，客人来自世界各地。图为 17日晨，风雨间隙，岛上居民在沙滩清埋风暴带来的垃圾。　马天南 摄\" src=\"upload/image/36b0fea8-210f-44e4-b139-0271c4f992ca.jpg\" title=\"受热带“厄多哈”影响，菲律宾中部宿务省马拉帕斯卡(Malapascua)岛上13日起即风雨交加，出岛交通已关闭数日，16日起停电，17日起停水。这座约五千人居住的小岛，有一百多家潜店和度假村，客人来自世界各地。图为 17日晨，风雨间隙，岛上居民在沙滩清埋风暴带来的垃圾。　马天南 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  受热带“厄多哈”影响，菲律宾中部宿务省马拉帕斯卡(Malapascua)岛上13日起即风雨交加，出岛交通已关闭数日，16日起停电，17日起停水。这座约五千人居住的小岛，有一百多家潜店和度假村，客人来自世界各地。图为 17日晨，风雨间隙，岛上居民在沙滩清埋风暴带来的垃圾。　马天南 摄 \n</div>\n<p>　　PAGASA公告：宿务北部、莱特岛、东萨马尔岛和萨马尔岛17日仍挂1号风球，海上旅行仍然是危险的。</p>\n<div style=\"text-align:center\"> \n <br> \n</div>\n<div style=\"text-align:center\"> \n <img alt=\"17日晨，“厄多哈”风雨间隙，菲律宾中部宿务省马拉帕斯卡(Malapascua)岛上居民在沙滩清埋风暴带来的垃圾。这座约五千人居住的小岛，有一百多家潜店和度假村，客人来自世界各地。　马天南 摄\" src=\"upload/image/c29e89df-38fb-4187-b7ec-2f1e553a4e0e.jpg\" title=\"17日晨，“厄多哈”风雨间隙，菲律宾中部宿务省马拉帕斯卡(Malapascua)岛上居民在沙滩清埋风暴带来的垃圾。这座约五千人居住的小岛，有一百多家潜店和度假村，客人来自世界各地。　马天南 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  17日晨，“厄多哈”风雨间隙，菲律宾中部宿务省马拉帕斯卡(Malapascua)岛上居民在沙滩清埋风暴带来的垃圾。这座约五千人居住的小岛，有一百多家潜店和度假村，客人来自世界各地。　马天南 摄 \n</div>\n<p>　　据《世界日报》报道“厄多哈”16日肆虐菲中部的维萨亚(Visayas)及棉兰佬的卡拉加区(Caraga)，所带来的洪水及山崩，导致停电及交通中断，数万家庭被迫逃离家园。</p>\n<div style=\"text-align:center\"> \n <img alt=\"受“厄多哈”影响，宿务达安班塔延停航，人们带着采购物品等候开航。　马天南 摄\" src=\"upload/image/7ec89813-d667-45e6-b874-a6d8f7297675.jpg\" title=\"受“厄多哈”影响，宿务达安班塔延停航，人们带着采购物品等候开航。　马天南 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  受“厄多哈”影响，宿务达安班塔延停航，人们带着采购物品等候开航。　马天南 摄 \n</div>\n<p>　　据菲律宾国家减灾管理委员会(NDRRMC)报告称：在“厄多哈”最强烈期间据报有6人失踪。据报莱特省(Leyte)有1名渔民及1男童死于洪涝，而比利兰省(Biliran)则有1人死于山崩。该委员会执行主任及民防办公室行政官黎加道・哈拉特说：实际死亡及失踪的人数还没有正式核实。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/7ec89813-d667-45e6-b874-a6d8f7297675.jpg',NULL,280,'2017-12-17 14:41:00','2017-12-17 19:18:29','71',0),(3044,'中国砀山冬季马术名家耐力赛在黄河故道开跑','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>宿州12月17日电 (记者 赵强)2017中国砀山冬季马术耐力赛17日在“梨都”安徽砀山黄河故道开跑，来自国内顶级耐力赛选手们克服冬季严寒跑完80公里的赛程。最终，来自北京天拓马业的隋博用时3小时16分28秒摘得冠军。</p>\n<div style=\"text-align:center\"> \n <img alt=\"选手亮相赛场 赵强 摄\" src=\"upload/image/0f0247fe-c26c-49c4-9174-c05a740cbf55.jpg\" title=\"选手亮相赛场 赵强 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  选手亮相赛场 赵强 摄 \n</div>\n<p>　　当日上午，随着号令下达，骑手们激情似火，威风、潇洒地驾驭着自己的爱马飞奔出去，顷刻间梨园旁尘土飞扬，气势雄壮。赛事期间，还有马术骑射表演、全国绕桶冠军精彩表演、马术障碍表演等。</p>\n<div style=\"text-align:center\"> \n <img alt=\"选手们赛前进行“马术绕桶赛” 赵强 摄\" src=\"upload/image/6b97bf68-01e3-406a-8c60-54fce616fe31.jpg\" title=\"选手们赛前进行“马术绕桶赛” 赵强 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  选手们赛前进行“马术绕桶赛” 赵强 摄 \n</div>\n<p>　　据介绍，本次参赛马匹是精心挑选的各种优质品种，主要有法系阿拉伯马、美系阿拉伯马、沙特阿拉伯马、阿拉伯半血马、盎格鲁马、布琼尼马等。参赛马匹须年满五岁。参加比赛的骑手与马匹必须至少参加过一次40公里的比赛才能参加本次耐力赛。</p>\n<div style=\"text-align:center\"> \n <img alt=\"耐力赛开跑 赵强 摄\" src=\"upload/image/01cad3ef-3b37-4829-bac0-d5ade749f462.jpg\" title=\"耐力赛开跑 赵强 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  耐力赛开跑 赵强 摄 \n</div>\n<p>　　本次耐力赛的技术代表、计时裁判、兽医委员会主席分别从法国请来。</p>\n<p>　　本次耐力赛技术代表和兽医长、国际马联耐力赛专家LeclercJeanLouis表示，这是他第三次来到中国。两年前，他第一次来中国时，感觉中国马术耐力赛还是属于传统型的，而现在无论是在技术方面，还是组织方面都有了很多进步，已接近国际耐力赛的水平了。</p>\n<div style=\"text-align:center\"> \n <img alt=\"选手回程时一幕 赵强 摄\" src=\"upload/image/19bd260b-3e80-4049-9afa-0a44423b075f.jpg\" title=\"选手回程时一幕 赵强 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  选手回程时一幕 赵强 摄 \n</div>\n<p>　　大赛期间，还举行了中国砀山马术高峰论坛，LeclercJeanLouis解读了马术耐力赛的历史、发展进程以及未来的发展方向，并讲解了赛事兽医的职责以及马匹福利的基本原则。“马术比赛是一项人和马共同完成的体育项目，保证马匹福利是重中之重。这对本次耐力赛骑手和马匹都提出了高标准的要求。”</p>\n<div style=\"text-align:center\"> \n <img alt=\"马术障碍表演 赵强 摄\" src=\"upload/image/152d8312-c350-40aa-9943-99135dc84a64.jpg\" title=\"马术障碍表演 赵强 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  马术障碍表演 赵强 摄 \n</div>\n<p>　　据原中国马协秘书长、亚马联发展计划委员会主席常伟介绍，中国马业的发展可以追溯到4000年前。近40年来，中国马业正经历了一个深刻变革的时期，正完成从传统马业向现代马业产业的历史性转型。</p>\n<div style=\"text-align:center\"> \n <img alt=\"耐力赛行进中 赵强 摄\" src=\"upload/image/7a752ac7-d48c-491f-963d-f52980d9f31a.jpg\" title=\"耐力赛行进中 赵强 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  耐力赛行进中 赵强 摄 \n</div>\n<p>　　常伟透露，目前全国有超过1400家马术俱乐部和马场，200多家马产业企业，各类协会近百家，全国每年进口运动马匹2000匹，马上运动爱好者人数达60万。</p>\n<p>　　据了解，砀山马术耐力赛赛道独具特色，被中国马业协会誉为“中国十大黄金赛道之首”，是国内最好的马术耐力赛赛道。为发展壮大马术产业，砀山县政府和安徽省体育局将在砀山黄河故道两岸建设“梨都马术运动小镇”，是安徽省首批15个体育特色小镇之一。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/7a752ac7-d48c-491f-963d-f52980d9f31a.jpg',NULL,1744,'2017-12-17 18:54:00','2017-12-17 19:18:29','74',0),(3045,'全国50余支队伍齐聚　福州“24小时接力跑”迎新年','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>福州12月17日电 (张羽)第九个城市跑点――福州站的“2017要跑・24小时城市接力赛”17日下午15时许落幕。在过去的24小时里，对于由全女将组成的“飞跑团”来说可谓挑战重重，从16日下午的低温阴雨，到17日的艳阳高照，成员Amy直呼“好有挑战”。</p>\n<p>　　这是“中国人寿・2017要跑・24小时城市接力赛”首次来到福州举办，由于比赛场地位于福州郊区的贵安花海拓展公园内，周边山色秀美，却因16日的低温阴雨给50多支竞赛队伍增加了比赛难度。</p>\n<div style=\"text-align:center\"> \n <img alt=\"24小时前，来自全国的50多支队伍，在细雨中起跑。　张羽 摄\" src=\"upload/image/d4490f2d-152e-48a5-a6af-44c0c089089f.JPG\" title=\"24小时前，来自全国的50多支队伍，在细雨中起跑。　张羽 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  24小时前，来自全国的50多支队伍，在细雨中起跑。　张羽 摄 \n</div>\n<p>　　规则规定，竞赛中所有参赛团队同时起跑，连续24小时后同时结束。团队中每名成员在赛道上每次至少要完成一圈跑量才可计入里程。团队成员间通过接力的形式，反复上下赛道完成竞赛。每个参赛团队同一时间只能且必须有一名成员在赛道上进行比赛，最终以每个团队所有跑者在24小时内接力跑步的累积跑量决定名次。</p>\n<p>　　Amy与其他6名队友来自全国各地，她告诉记者，在24小时里，7个人赛前分工已比较明确，包括睡觉、做饭、后勤以及最重要的跑步。“在跑步中收获快乐，在团队协作中收获友情，希望以这种特殊的方式迎接新年。”Amy说。</p>\n<p>　　与其他田径赛事不同，记者在比赛现场看到，因为赛程长达24小时，各个参赛跑团都带着帐篷在比赛区域附近“安营扎寨”，不少队伍还自备电饭锅等器具，主办方还特地为赛道外的助威团、后援团们准备了精彩的表演：乐队演出、午夜影院经典影片播放，宛如户外派对一般。</p>\n<p>　　除了跑步、协作等乐趣外，让众多跑友乐此不疲的还有公益。据了解，“要跑・24小时城市接力赛”是由中国田径协会、众辉国际体育管理有限公司主办的一项24小时内持续进行的团队接力赛事。经过3年的发展壮大，已经有千支跑团万名跑友参与其中，累计为姚基金、渐冻人基金捐款超过百万元。</p>\n<p>　　2017年，“要跑・24小时城市接力赛”获得中国田协官方授权，成为迄今为止国内规模最大、最具影响力的团队接力赛事，赛事版图扩大到全国十城，从五月至十二月，年度最后一站将在广州收官。众辉体育总经理李辉在接受采访表示，这一赛事的特色及魅力在于，它将跑步这一原本人们认定的“孤独运动”，变成了一项团队的运动，已经成为跑步运动中的新潮流。</p>\n<p>　　比赛鸣枪当天，国际超级马拉松冠军、单次公路跑最长最快纪录创造者许秀涛也来到福州起跑仪式现场。他表示，这是他今年的第九次挑战，“一个人的24小时极为艰辛，期待许秀涛为达成自己的公益目标，克服重重困难，奋力向前。”(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/d4490f2d-152e-48a5-a6af-44c0c089089f.JPG',NULL,1141,'2017-12-17 16:11:00','2017-12-17 19:18:41','74',0),(3046,'中国经济的历史跨越：创新开启中国经济新未来','魏巍','央视网','<p><strong>　　【中国经济的历史跨越】创新开启中国经济新未来</strong></p>\n<p>　　<strong>央视网消息：</strong>党的十九大报告指出，创新是引领发展的第一动力，是建设现代化经济体系的战略支撑。面向未来，我国正以全球视野谋划和推进创新，加强前沿科技布局，完善国家创新体系。创新，正在开启中国经济永续发展的新未来。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/f50f978c-e257-442d-a986-88799accc677.jpg\"></p>\n<p>　　记者朱继华说，我手里拿的这个像粉笔一样的东西，是我国刚刚研制成功的超级显微镜，如果用它来观察头发丝，扫出来的断面相当于是一个足球场那么大。不仅如此，它还是世界上首个用在超强磁场中的显微镜。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/4308cc0f-d8ac-4616-87ed-30145915ebeb.gif\"></p>\n<p>　　超级显微镜加上超强磁场，这样的强强联合，为我国在物质、材料、高分子化学、生命科学等众多领域，打开了全新的科学探索通道，让实现更多颠覆性的技术突破成为可能。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/978f60ed-1b6b-4f99-aa54-4f5e087c161c.jpg\"></p>\n<p>　　中科院强磁场中心研究员陆轻铀说，我们能在比别人强得多的磁场里面去进行原子分辨率的测量，这个对后面新物质的发现，新机制的发现，是具有非常大的帮助。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/87513c45-2d2f-4df9-92af-75b2f8f3830b.gif\"></p>\n<p>　　目前我国已经在核物理、未来网络、空间和天文等多个领域建成了50个重大科学装置，在大科学装置聚集的北京怀柔、上海张江和安徽合肥，国家建立三个综合性科学中心，瞄准世界科技前沿的基础研究，孵化战略新兴产业。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/8a59d445-9f45-47e0-854a-258ae4d933c5.jpg\"></p>\n<p>　　安徽省科技厅厅长宛晓春说，大科学装置的这些基础研究，有沿途下蛋的这样的功能，这个蛋，我们把它接好了，我们就能催生我们新的工业技术，催生我们百亿、千亿级的新兴产业出来。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/04c7c8f8-fa2e-497b-b556-bd0c9e5dee45.gif\"></p>\n<p>　　十九大报告把加快建设创新型国家作为构建现代经济体系中重要内容，并用“战略支撑”四个字，强化了创新的重要地位。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/9e414a62-dda0-420a-9ef8-f0f3d9782997.jpg\"></p>\n<p>　　中国宏观经济研究院常务副院长王昌林说，科技创新实际上就相当于我们经济体系的筋骨，为我们经济体系增添力量，增强动力。</p>\n<p>　　创新的引擎，正在被改革点燃。169项全面创新改革举措在全国八个试验区域全部启动。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/dc384bf4-4866-4131-b67e-cca4820ca4c7.gif\"></p>\n<p>　　目前，在数字科技的人工智能、智联网、金融科技等方面，中国已经走在了世界前列，北京中关村成为继美国硅谷之后，全球第二大吸引全球风险投资的区域，人工智能产业与硅谷起头并进。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/74dfd127-bb87-4326-99bd-d8468ef7d446.jpg\"></p>\n<p>　　中关村管委会主任翟立新说，我们预计到2020年，中关村人工智能领域产业规模将超过500亿元，带动相关产业规模将超过5000亿元。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/feb7b37e-18cf-4ef2-8f4a-1ec67db7f0c4.jpg\"></p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/51940271-dc0c-4ff2-9491-0367f87ed15f.gif\"></p>\n<p>　　在刚刚落幕的第四届世界互联网大会上，发布了14项世界最顶尖互联网科技成果，其中超过一半来自中国，世界知识产权组织等发布的2017年全球创新指数报告显示，中国国际排名从2016年的25位升至22位，成为唯一进入前25名集团的中等收入国家。创新对国家现代化经济体系的支撑作用正在逐步显现，也在为中国经济开启崭新的未来。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/51940271-dc0c-4ff2-9491-0367f87ed15f.gif',NULL,237,'2017-12-17 15:52:00','2017-12-17 19:19:19','66',0),(3047,'中国经济的历史跨越：创新开启中国经济新未来','魏巍','央视网','<p><strong>　　【中国经济的历史跨越】创新开启中国经济新未来</strong></p>\n<p>　　<strong>央视网消息：</strong>党的十九大报告指出，创新是引领发展的第一动力，是建设现代化经济体系的战略支撑。面向未来，我国正以全球视野谋划和推进创新，加强前沿科技布局，完善国家创新体系。创新，正在开启中国经济永续发展的新未来。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/8f3848d6-0634-4f2d-85fe-b22068b19c56.jpg\"></p>\n<p>　　记者朱继华说，我手里拿的这个像粉笔一样的东西，是我国刚刚研制成功的超级显微镜，如果用它来观察头发丝，扫出来的断面相当于是一个足球场那么大。不仅如此，它还是世界上首个用在超强磁场中的显微镜。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/ca5e07eb-0676-4207-bdad-3224d2079a4a.gif\"></p>\n<p>　　超级显微镜加上超强磁场，这样的强强联合，为我国在物质、材料、高分子化学、生命科学等众多领域，打开了全新的科学探索通道，让实现更多颠覆性的技术突破成为可能。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/61ed278a-1a79-4edf-9939-62ff4aa15408.jpg\"></p>\n<p>　　中科院强磁场中心研究员陆轻铀说，我们能在比别人强得多的磁场里面去进行原子分辨率的测量，这个对后面新物质的发现，新机制的发现，是具有非常大的帮助。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/10761fe6-2cf9-40f8-8ee9-23069cd7558d.gif\"></p>\n<p>　　目前我国已经在核物理、未来网络、空间和天文等多个领域建成了50个重大科学装置，在大科学装置聚集的北京怀柔、上海张江和安徽合肥，国家建立三个综合性科学中心，瞄准世界科技前沿的基础研究，孵化战略新兴产业。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/3612242c-9cf7-464e-b348-4f17e16537f4.jpg\"></p>\n<p>　　安徽省科技厅厅长宛晓春说，大科学装置的这些基础研究，有沿途下蛋的这样的功能，这个蛋，我们把它接好了，我们就能催生我们新的工业技术，催生我们百亿、千亿级的新兴产业出来。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/7b5a3b22-06f1-4e87-802b-ca56a8cd8269.gif\"></p>\n<p>　　十九大报告把加快建设创新型国家作为构建现代经济体系中重要内容，并用“战略支撑”四个字，强化了创新的重要地位。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/b73d4323-9ce4-4648-8a8a-e5b0942395a6.jpg\"></p>\n<p>　　中国宏观经济研究院常务副院长王昌林说，科技创新实际上就相当于我们经济体系的筋骨，为我们经济体系增添力量，增强动力。</p>\n<p>　　创新的引擎，正在被改革点燃。169项全面创新改革举措在全国八个试验区域全部启动。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/e67fc507-bb95-4e0a-ae3f-216231c7fda3.gif\"></p>\n<p>　　目前，在数字科技的人工智能、智联网、金融科技等方面，中国已经走在了世界前列，北京中关村成为继美国硅谷之后，全球第二大吸引全球风险投资的区域，人工智能产业与硅谷起头并进。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/4eb173fb-e746-4511-b5c1-67820268c5c3.jpg\"></p>\n<p>　　中关村管委会主任翟立新说，我们预计到2020年，中关村人工智能领域产业规模将超过500亿元，带动相关产业规模将超过5000亿元。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/1821ff2c-3283-413b-b5e0-56bf37e8eddc.jpg\"></p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/ed3477fd-4920-4a5a-b209-c44e938800ca.gif\"></p>\n<p>　　在刚刚落幕的第四届世界互联网大会上，发布了14项世界最顶尖互联网科技成果，其中超过一半来自中国，世界知识产权组织等发布的2017年全球创新指数报告显示，中国国际排名从2016年的25位升至22位，成为唯一进入前25名集团的中等收入国家。创新对国家现代化经济体系的支撑作用正在逐步显现，也在为中国经济开启崭新的未来。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/ed3477fd-4920-4a5a-b209-c44e938800ca.gif',NULL,1229,'2017-12-17 15:52:00','2017-12-17 19:19:51','66',0),(3048,'中国经济的历史跨越：创新开启中国经济新未来','魏巍','央视网','<p><strong>　　【中国经济的历史跨越】创新开启中国经济新未来</strong></p>\n<p>　　<strong>央视网消息：</strong>党的十九大报告指出，创新是引领发展的第一动力，是建设现代化经济体系的战略支撑。面向未来，我国正以全球视野谋划和推进创新，加强前沿科技布局，完善国家创新体系。创新，正在开启中国经济永续发展的新未来。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/7da4a0e9-2ffb-4037-8370-f89786f2ee79.jpg\"></p>\n<p>　　记者朱继华说，我手里拿的这个像粉笔一样的东西，是我国刚刚研制成功的超级显微镜，如果用它来观察头发丝，扫出来的断面相当于是一个足球场那么大。不仅如此，它还是世界上首个用在超强磁场中的显微镜。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/d7ecc6d0-0ef1-491c-bed5-a6bd3c5938d3.gif\"></p>\n<p>　　超级显微镜加上超强磁场，这样的强强联合，为我国在物质、材料、高分子化学、生命科学等众多领域，打开了全新的科学探索通道，让实现更多颠覆性的技术突破成为可能。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/e74f49d0-5782-463a-bd2a-98d92ac879c7.jpg\"></p>\n<p>　　中科院强磁场中心研究员陆轻铀说，我们能在比别人强得多的磁场里面去进行原子分辨率的测量，这个对后面新物质的发现，新机制的发现，是具有非常大的帮助。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/1dc26c5f-9806-4f61-8cdf-877e5746a547.gif\"></p>\n<p>　　目前我国已经在核物理、未来网络、空间和天文等多个领域建成了50个重大科学装置，在大科学装置聚集的北京怀柔、上海张江和安徽合肥，国家建立三个综合性科学中心，瞄准世界科技前沿的基础研究，孵化战略新兴产业。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/b90838b3-6e75-4618-9eca-a10e8b386790.jpg\"></p>\n<p>　　安徽省科技厅厅长宛晓春说，大科学装置的这些基础研究，有沿途下蛋的这样的功能，这个蛋，我们把它接好了，我们就能催生我们新的工业技术，催生我们百亿、千亿级的新兴产业出来。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/4ee513ca-2cfc-4ab8-9b61-2bd618194b84.gif\"></p>\n<p>　　十九大报告把加快建设创新型国家作为构建现代经济体系中重要内容，并用“战略支撑”四个字，强化了创新的重要地位。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/3baab113-79ac-40d7-a019-ae9bb1df2b73.jpg\"></p>\n<p>　　中国宏观经济研究院常务副院长王昌林说，科技创新实际上就相当于我们经济体系的筋骨，为我们经济体系增添力量，增强动力。</p>\n<p>　　创新的引擎，正在被改革点燃。169项全面创新改革举措在全国八个试验区域全部启动。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/18db0598-a1e9-4ec6-9001-05222c4d39a8.gif\"></p>\n<p>　　目前，在数字科技的人工智能、智联网、金融科技等方面，中国已经走在了世界前列，北京中关村成为继美国硅谷之后，全球第二大吸引全球风险投资的区域，人工智能产业与硅谷起头并进。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/574d7428-0a41-42b8-918f-c294ef49adbd.jpg\"></p>\n<p>　　中关村管委会主任翟立新说，我们预计到2020年，中关村人工智能领域产业规模将超过500亿元，带动相关产业规模将超过5000亿元。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/ae562e33-f522-42e9-b430-7856c0c42ad0.jpg\"></p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/1e3c6a05-feaa-4307-aa28-81e26708b99d.gif\"></p>\n<p>　　在刚刚落幕的第四届世界互联网大会上，发布了14项世界最顶尖互联网科技成果，其中超过一半来自中国，世界知识产权组织等发布的2017年全球创新指数报告显示，中国国际排名从2016年的25位升至22位，成为唯一进入前25名集团的中等收入国家。创新对国家现代化经济体系的支撑作用正在逐步显现，也在为中国经济开启崭新的未来。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/1e3c6a05-feaa-4307-aa28-81e26708b99d.gif',NULL,377,'2017-12-17 15:52:00','2017-12-17 19:20:01','66',0),(3049,'巴基斯坦奎达市一座教堂遭袭 已有至少8人死亡','于晓','新华网','<p>　　<strong>巴基斯坦奎达市一座教堂遭袭致8人死亡 </strong></p>\n<p>　　新华社伊斯兰堡12月17日电(记者刘天)巴基斯坦西南部俾路支省首府奎达一座教堂17日遭袭，现场发生爆炸和激烈交火，已有至少8人死亡。</p>\n<p>　　当地媒体援引奎达一家医院发言人的话说，在这起自杀式爆炸袭击中，还有44人受伤，其中9人伤势严重，死亡人数可能进一步上升。目前，奎达市所有医院已进入紧急状态，对受伤人员进行救治。</p>\n<p>　　俾路支省内政部长布格蒂告诉当地媒体，4名恐怖分子当日向奎达的一座教堂发动袭击。其中一人在引爆爆炸物前被教堂周围的安全部队击毙，另外一人则引爆了身上的爆炸物，还有两名参与袭击的恐怖分子在交火后逃离现场。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　目前，巴基斯坦安全部队已肃清被袭教堂周围地区，正在展开追踪和调查行动。暂无任何组织宣称制造了此次袭击。</p>\n<p>　　据当地媒体报道，袭击发生时，教堂内约有500人正在进行宗教活动。报道援引当地一名警官的话说，由于警方已在教堂周围部署了大量安保力量，所以恐怖分子未能按计划实施袭击。</p>\n<p>　　巴基斯坦总理阿巴西在一份声明中谴责了此次袭击，并要求相关部门全力救治伤员。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,906,'2017-12-17 19:20:00','2017-12-17 19:20:37','71',0),(3061,'fasdf','asfdf','asdf','<p>sdfasdfasdfasdfsdf</p>','0',NULL,0,'2018-01-06 13:52:07','2018-01-06 13:52:17','64',0),(3062,'花椒推出百万作战 每天30万奖金掀直播答题热','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>1月5日电 1月5日，花椒百万作战将正式上线(花椒ID：888888)，比赛每天进行3场，每场比赛的奖金均为10万元，成为奖金金额最高的直播答题。同时，花椒百万作战还与江苏卫视《一站到底》进行合作，著名节目主持人李好会担任百万作战的主持人，而百万作战的获胜者还将有机会参加《一站到底》。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　据了解，花椒百万作战开始时间分别为19:00、20:00、21:00，每场比赛持续时间约为20分钟左右。其参与门槛极低，用户只需在比赛开局前进入答题直播间，便能免费参与答题。</p>\n<p>　　答题的规则也很简单，每场设置12道选择题，难度都不高，但涵盖面极广，囊括了天文地理、人文历史、体育科技、娱乐影视等。在答题过程中，每道题的对话框会出现在屏幕中央，用户点击正确的选项即可进行答题，每道题限时10秒，超时或者答错即被淘汰，增加了游戏的紧张度和刺激感，最终全部正确的人平分10万元奖金。</p>\n<p>　　比赛还设置了“复活”机制，用户只要把游戏直播间分享到微信中，成功邀请新朋友下载并登录，就可以获得复活道具，每个用户可以在一局游戏中使用3次，除最后1题以外，在答错其他题目后都满血复活，重新回到游戏。让用户能够最大程度参与享受游戏，也以此达到为更多用户送福利的目的。</p>\n<p>　　同其他游戏相比，直播答题包含知识点，适合所有人参加，而每天30万的奖金则为参与的用户带来实际的利益，真正阐释了“知识就是金钱”，也将进一步掀起直播答题热。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1581,'2018-01-05 16:51:00','2018-01-06 14:47:13','82',0),(3063,'大数据AI科学家裴健加盟京东 领衔大数据与智慧供应链研发','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>1月5日电 京东集团(JD.COM)今日宣布，加拿大一级研究讲座教授(大数据科学领域)、加拿大西蒙弗雷泽大学计算科学学院教授、统计与精算学系和健康科学院兼职教授裴健入职京东，任京东集团副总裁，向京东集团董事局主席兼首席执行官刘强东汇报。裴健将负责大数据平台与产品研发部，整合京东集团整体的数据采集、处理和相关数据产品研发的工作，持续探索大数据领域的先进技术理念和生态实践，同时，裴健还将充分发挥其在数据科学及机器学习等人工智能领域的研究能力和行业应用经验，兼管京东在智慧供应链领域的创新和应用。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　在数据科学、大数据、数据挖掘和数据库系统等领域，裴健擅长为数据密集型应用设计开发创新性的数据业务产品和高效的数据分析技术。</p>\n<p>　　刘强东表示，京东下一个十二年发展的核心是技术，京东要用技术打造无界零售模式。京东定位于未来的零售基础设施服务，将向全社会提供“零售即服务(RaaS)”的解决方案，以云计算、大数据、人工智能等技术创新为更多合作伙伴互联网+转型助力。京东大力发展人工智能技术，就是要持续改善零售行业的成本、效率与用户体验。裴健教授在人工智能领域精深的研究能力及广阔的视野，将帮助京东技术能力再上新的高度。</p>\n<p>　　据了解，京东积累了丰富精准的大数据，很早就开始布局大数据挖掘和应用，建立了从大数据基础平台、挖掘工具、知识画像体系到智能商业应用的完整体系。大数据不仅应用在京东业务的每个环节，全面推动成本、效率和用户体验的优化，更开始对外输出大数据分析和挖掘能力，帮助更多企业高效成长。</p>\n<p>　　作为零售企业的核心竞争力，供应链一直是京东技术研发和创新的核心领域。京东打造了智慧供应链系统，在市场洞察、用户研究、选品定价、预测计划、库存管理、精准营销等多方面展开深入探索，提高零售供应链效益、降低成本。这个系统不仅帮助京东实现了海量商品的高效运营，也开始对外赋能，助力企业降本增效，带动了零售行业的发展。</p>\n<p>　　前不久，人工智能领域权威科学家周伯文入职京东，负责京东AI平台与研究部相关业务；人工智能领域资深科学家薄列峰加盟京东金融，出任京东金融AI实验室首席科学家；11月27日，京东集团与斯坦福人工智能实验室(SAIL)启动京东-斯坦福联合AI研究计划，双方联手围绕机器学习、深度学习、机器人、自然语言处理和计算机视觉等前沿技术方向开展研究等。近期京东在人工智能高端人才引入和对外合作方面动作频频，正是实践其技术驱动战略的鲜明体现。京东拥有完整、精准、价值链最长的数据，为人工智能的充分实践打造了理想的数据基础；同时，京东丰富的应用场景，为技术人才成长和技术创新实践提供了最佳土壤。正是这些因素让京东对技术人才形成了巨大的吸引力，也让京东成为中国人工智能领域核心的实践者和重要的推动者。</p>\n<p>　　业界人士认为，裴健在全球大数据人工智能领域取得了不菲的成就，同时积极参与推动行业交流与发展，在众多学术组织机构和活动中发挥重要作用，并与全球业界合作伙伴保持着广泛的产业联系。京东拥有人工智能领域的丰富数据基础和应用场景，裴健教授的加盟对京东提升大数据、人工智能、智慧供应链等领域的技术研发能力具有重要意义。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,77,'2018-01-05 16:39:00','2018-01-06 14:47:13','82',0),(3064,'中国短道速滑队平昌冬奥会大名单出炉：周洋、武大靖领衔','魏巍','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>北京1月5日电 (邢�)中国短道速滑队5日公布平昌冬奥会集训的18人大名单。两届冬奥会冠军得主周洋、世锦赛冠军武大靖将领衔8女10男展开最后一个月的集训备战。</p>\n<p>　　2017年举办的短道速滑世界杯四站比赛是平昌冬奥会的资格赛，中国队共获得4金2银2铜，拿到了全部8个项目共20个满额参赛席位。这是中国短道速滑队自2010年温哥华冬奥会以来，连续三届拿到满额参赛席位。</p>\n<p>　　据中国短道速滑队主教练李琰介绍，获得世界杯接力或单项前三名的选手直接入选国家队大名单，在上周进行的全国锦标赛上获得冠军的选手也将入选国家队，此外还将从全国锦标赛中获得第2到8名的运动员中抽选一部分进入冬奥会集训名单。</p>\n<p>　　武大靖在世界杯上海站和首尔站上共斩获3块金牌，周洋、范可新等人则取得女子3000米接力金牌，因此得以直接入选大名单；在全国锦标赛中分别获得男女全能冠军的陈德全、曲春雨也在名单之中，同样表现优异的小将于松楠、李璇等均榜上有名。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　大名单中，唯一获得过冬奥会冠军的周洋由于受伤病困扰，已有两年没参加国际比赛。在去年世界杯上她选择复出，但从成绩和状态上看似乎还未重返巅峰。主教练李琰曾对记者透露，周洋目前恢复不错，但伤病对系统训练还是会产生影响。“有她在队里，军心会更稳。”</p>\n<p>　　武大靖堪称中国短道速滑男子组领军人物。在世界杯四站分站赛结束后，武大靖在男子500米项目上卫冕年终总冠军，国际滑冰联合会曾称他为“冲刺之王”。在2014年索契冬奥会上，武大靖获得男子500米亚军和5000米接力铜牌。他也曾表示渴望在平昌冬奥会上取得突破。</p>\n<p>　　接下来中国短道速滑队将前往上海进行最后一个月的集中冲刺备战，最终参赛名单将在平昌冬奥会中国代表团成立前公布。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1653,'2018-01-05 20:34:00','2018-01-06 14:47:12','74',0),(3065,'应对环保压力 台湾要和燃油摩托车说“再见”','王忠会','人民日报海外版','<p><strong>　　台湾要和燃油摩托车说“再见”</strong></p>\n<p>　　新华社台北1月4日电 (记者喻菲、李凯)摩托车重镇台湾正启动推广电动摩托车计划，以应对不断加大的环保压力。台有关部门已推出了2035年全面禁售燃油摩托车的政策目标，并加大了建设充电站等配套措施的力度。</p>\n<p>　　摩托车(当地称“机车”)犹如台湾街头的一道风景线。交通高峰时，每当绿灯亮起，等候在路口的摩托大军伴随着“轰隆”之声浩浩荡荡地疾驰而去。台湾有2300多万人口，却拥有近1400万辆摩托车，是世界摩托车密度最高的地区。这些燃油摩托车在给骑行者带来交通便利的同时，也严重加剧了当地空气污染。有环保团体指出，摩托车“贡献”台湾PM2.5空污排放量超过两成。</p>\n<p>　　面对岛内对空气污染此起彼伏的抗议，台行政主管部门去年12月底推出了“空污防制行动方案”，其中就包括2035年新售摩托车全面电动化、2040年新售汽车全面电动化的目标。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　新年伊始，台经济事务主管部门又宣布，为了推动电动车产业，除2018年至2022年补助购买电动摩托车外，未来5年将建立3310座电动摩托车充换电站，其中1000座由公营的中油公司建置。全台各地目前建有1800座电动摩托车充电站。</p>\n<p>　　台湾从今年还开始发放电动摩托车专属号牌、设立专属停车位并提供停车优惠；同时通过法规规定停车空间预留充电管线，严格取缔污染大的二程摩托车，加快淘汰老旧摩托车。</p>\n<p>　　据最新统计，2017年台湾摩托车销售量突破100万辆，而电动摩托车的销量为4万多辆，台多年累计销售电动车约10万辆。</p>\n<p>　　台湾业者指出，台湾摩托车电动化才刚刚起步，未来发展是机遇。一项调查显示，近六成的台湾摩托车使用者表示愿意购置或换用电动摩托车。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1661,'2018-01-05 15:56:00','2018-01-06 14:47:12','70',0),(3066,'香港与内地联手打击跨境偷运走私集团 香港31人被捕','官志雄','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>1月5日电 据香港电台网站消息，香港特区警方及入境处联同内地部门，打击一个跨境偷运“人蛇”及走私集团，其中在香港有31人被捕，包括3名集团骨干成员。行动中检获一批重190公斤的疑似走私紫坛木，估计市值约60万港元。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　香港警方称，该集团运作约一个月，用渔船从深圳蛇口偷运“人蛇”到香港流浮山上岸，安排他们做非法劳工及住宿。</p>\n<p>　　香港警方经调查后，5日清晨锁定该集团一艘渔船，水警将其截停并上船搜查，检获一批紫坛木及拘捕4人，发现船内加装了暗格让人藏匿。</p>\n<p>　　香港警方也与入境处在香港多处搜查，整个行动总共拘捕31人，涉及罪行包括非法入境、偷运“人蛇”，及雇用非法劳工等。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1203,'2018-01-05 17:14:00','2018-01-06 14:47:12','69',0),(3067,'新能源车贬值太快陷尴尬 专家吁打通二手车流通渠道','姜贞宇','南方日报','<p>　<strong>　贬值太快，新能源车陷入尴尬</strong></p>\n<p><strong>　　专家建议尽快打通新能源二手车流通机制</strong></p>\n<p>　　一辆95万元的特斯拉Model S，三年下来残值仅剩原价格的三分之一，而等值的传统燃油车最低残值都超过了50%。太快的贬值速度，使得不少新能源准车主不得不权衡利弊。</p>\n<p>　　“打通二手车流通渠道已迫在眉睫。”全国乘用车市场信息联席会秘书长崔东树认为，虽然新能源二手车面临不少痛点，但“危”中有“机”，围绕检测、评估、回收等服务，一个潜在的新兴市场将吸引不少机构进入。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　行业现状：新能源汽车进入置换期</p>\n<p>　　在刚结束的2017年，新能源车迎来新一轮增长。去年前11个月，销量实现10连增，1-11月的销量突破60万辆大关，预计全年将突破70万辆。从本月起，对新能源汽车实施免征车辆购置税优惠，再加上广州等地推出新能源车专用号牌，将进一步加快新能源车的推广。</p>\n<p>　　与此同时，新能源二手车的流通问题也开始显现。新能源汽车市场是从2014年开始活跃，部分新能源汽车开始进入置换期。如果往前追溯，中国第一批新能源汽车还要提早在2012年。</p>\n<p>　　记者走访广州市场了解到，目前广州多家二手车交易市场，难觅新能源二手车踪影。“像特斯拉的用户，在买单时基本不会考虑到后续的二次销售。”广州一位从事二手车交易的负责人告诉记者，目前新能源二手车交易存在一些痛点，比如，二手车的回收比较集中于新能源车专卖店，其他交易渠道也因为没有可参照的价格标准，出现有价无市的情况。“即便有交易，考虑到市场流通还不成熟，买方也会有意把价格压低”。</p>\n<p>　　“残值率不高自然会影响消费者购置新能源车的积极性，因此，迫切需要不断完善新能源二手车流通渠道，才有助于提升新能源二手车的保值，增强用户的信心。”崔东树说。</p>\n<p>　　专家建议：完善标准推动第三方参与积极性</p>\n<p>　　根据此前工信部发布的数据，如果以5-8年报废期计算，2020年之前，新能源二手车就会迎来爆发式增长，同期增长的还有废旧电池以及新能源报废汽车。在崔东树看来，未来几年，围绕鉴定、评估、回收等新能源二手车服务也将迎来新的机遇，这将推动一个新兴市场的形成。</p>\n<p>　　“首先还应该建立完善的行业评估标准。”崔东树表示，同样型号的车，如果好车坏车都是同一个标准或者同样的价格，对行业发展不利。其次，崔东树认为，完善的电池管理系统也亟须出台，“电池含有钴等稀有元素，对车主而言是一笔不可忽视的财富，可以鼓励第三方机构积极参与二手车回收利用”。</p>\n<p>　　无论是出于社会责任，还是经济价值，目前，已经有车企涉足新能源二手车服务市场。早在2016年8月，北汽新能源联合第三方评估机构，推出了业内首个《北汽新能源二手车检测标准》，涵盖从里到外的专业检测项目108个，填补了新能源汽车检测评估的空白，对推动我国新能源车二手车的流通大有裨益。</p>\n<p>　　而在商用电动汽车领域，也初步探索出一套相对成熟的运营模式。吉利远程纯电动轻卡有关负责人告诉记者，新能源车主要销售给大型物流等企业，这些公司通过后续的租赁、维护、回收等机制，可以实现长远效益。</p>\n<p>　　崔东树也表示，在建立行业标准的同时，还可以考虑给予新能源二手车回收单位更多的优惠税收政策，推动更多车企以外的第三方机构积极参与。</p>\n<p>　　南方日报记者 郜小平</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,581,'2018-01-05 10:06:00','2018-01-06 14:47:12','72',0),(3068,'李嘉诚呼吁香港人勿炒楼 称比特币“绝对有风险”','王忠会','中国新闻网','<div style=\"text-align:center\"> \n <img alt=\"李嘉诚。图片来源：香港《大公报》。\" src=\"upload/image/6f62d5e1-cd62-4211-a143-15a51a4ba49c.jpg\" style=\"border:px solid #000000\" title=\"李嘉诚。图片来源：香港《大公报》。\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  李嘉诚。图片来源：香港《大公报》。 \n</div>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>1月5日电 据香港《大公报》消息，新年伊始，香港企业家、长和系主席李嘉诚表示，预期今年美国加息两至三次，香港地区也会跟随加息。他重申，港人不要炒楼，倘若能够负担得起，自住则无问题。</p>\n<p>　　李嘉诚在晚宴致辞中提醒，要对各种不确定因素保持警惕，低利率趋势改变、贸易对垒萌生、创新科技革命兴起，2018年的主节奏应为“发展不忘稳健”。他还提到“一带一路”及“大湾区”策略，有利于整体经济发展，认为香港“必定有机可寻”。</p>\n<p>　　香港楼市方面，李嘉诚重申：“自住的话，能够负担得起，无问题，如果‘炒楼’就不好”。他提到，“纳米楼”也是市场供求的问题，相信未来相关供应会有所增加。</p>\n<p>　　去年11月，长实以402亿港元出售旗下地标商厦“中环中心”75%业权，造价创香港写字楼物业历来最高单价。被问到会否再出售香港物业，李嘉诚回应指暂未会再出售物业，又强调正在兴建酒店，也有收购商场物业，相信今年固定收入可创新高。</p>\n<p>　　李嘉诚称，集团过去一年固定收入稳步向前，未来固定收入业务将成为“极为重要的部分”，有信心录得大幅增长。</p>\n<p>　　谈及近期热爆的比特币，李嘉诚并无正面回应有否投资，只表示“我就保守的，(比特币)绝对有风险”。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/6f62d5e1-cd62-4211-a143-15a51a4ba49c.jpg',NULL,1017,'2006-04-05 11:15:00','2018-01-06 14:47:13','69',0),(3069,'一汽-大众奥迪第30次蝉联中国豪华车市场年销量冠军','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>1月5日电 2017年，一汽-大众奥迪全年销量再创新高，共计交付汽车595,288辆，连续30次夺得中国豪华车市场年销量冠军。其中，国产车型全年销售545,000辆，进口车型全年销售50,288辆。同时，一汽-大众奥迪12月单月销售汽车68,948辆，创造了历史最佳的单月销量纪录。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　五大国产车型表现强劲，引领细分市场。其中，奥迪A6L全年销售142,214辆，历三十载芳华，始终占据整体豪华车市场最畅销车型王座。即将换代的奥迪Q5仍然展示出强大的市场号召力，全年共销售124,055辆，称雄豪华B级SUV细分市场。奥迪A4L自换代以来销量一路攀升，2017年全年销售114,483辆，同比劲增16%。两款A级车型――奥迪A3和奥迪Q3，合力贡献了全年总销量的近28%，充分表明一汽-大众奥迪的年轻化战略已深入人心。</p>\n<p>　　进口车方面，全新奥迪A5凭借创新的设计美学、领先的智能科技、强劲的动力性能备受市场认可，全年销量同比增长21%。奥迪Q7同样表现出色，全年销量同比增长20%，彰显了超越同级的全能实力。2017年全新引入的奥迪A6 Avant、奥迪TT RS Coupé、奥迪RS 3 Limousine等个性化车型，亦赢得了众多粉丝的青睐。</p>\n<p>　　2018年，一汽-大众奥迪将迎来产品和品牌的“双大年”。新一轮产品攻势，将为中国消费者带来多达16款重磅新车，包括集大量AI人工智能科技于一身的旗舰车型全新一代奥迪A8L、全新换代并加长的全新奥迪Q5L、全新引入的国产车型全新奥迪Q2L，以及奥迪RS 3 Sportback、奥迪RS 4 Avant、奥迪RS 5 Coupé等Audi Sport家族高性能车型，奥迪品牌旗舰级全尺寸SUV全新奥迪Q8也将在中国举行全球首发。2018年也是奥迪全新品牌战略在华开启元年，奥迪全球品牌峰会将首次在中国举办。以用户为中心打造的Audi Sport嘉年华、奥迪Q家族新境之旅、奥迪allroad三大体验平台，将为用户带来集群式、立体化、可持续的品牌体验。</p>\n<p>　　在一汽携手奥迪30周年的历史节点上，一汽-大众奥迪将在一汽-大众“2025战略”指引下，保持豪华车品牌力第一，不忘初心，砥砺前行，开启全新一轮增长周期，持续引领豪华车市场。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,564,'2018-01-05 16:55:00','2018-01-06 14:47:13','72',0),(3070,'“倒立男孩”传递平昌冬奥火炬：体育让我不惧困难','王硕','人民日报','<p><strong>　　“倒立男孩”传递平昌冬奥会火炬</strong></p>\n<p><strong>　　“体育让我不惧任何困难”</strong></p>\n<p>　　还有一个多月，平昌冬奥会就将拉开帷幕，目前火炬接力活动正在韩国境内如火如荼地进行。新年第一天，火炬传递到韩国东南部的城市浦项。其中一位特殊的火炬手引来了众多市民的驻足围观，为他加油呐喊的声音格外响亮，这位坐在轮椅上完成火炬传递的少年就是来自中国四川省宜宾市屏山镇的颜玉宏。</p>\n<p>　　出生于2002年的颜玉宏是一名初三学生，他在未满1岁时因为一次高烧患上了小儿麻痹症，导致下半身瘫痪，只能通过倒立行走。上小学后，他每天上学、放学都要经历3个小时的倒立行走，对知识的渴望让他坚持着这条漫长且艰辛的求学路，春夏秋冬，风雨无阻。幸运的是，“倒立男孩”的故事得到了各界的关注，经历多次手术和康复训练，颜玉宏终于能够站起来了。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　随着身体的逐渐康复，热爱运动的颜玉宏开始追逐埋藏在内心深处的“体育梦”。2012年时，四川省残联游泳队为颜玉宏做了水上测试，认为他很有希望成为一名出色的游泳运动员。</p>\n<p>　　在教练罗敏的带领下，颜玉宏开始了刻苦的训练。“他肯吃苦，又具有游泳天赋，一个多月就已经掌握了4种游泳姿势。”罗敏表示。颜玉宏说：“更重要的是，游泳锻炼了我的耐力和毅力，让我不惧任何困难。”在2014年的四川省残运会上，参加训练只有一年多的颜玉宏斩获了100米仰泳、200米混合泳和100米自由泳三个项目的奖牌。“通过游泳，我变得更加自信。”</p>\n<p>　　颜玉宏在逆境中自强不息的精神以及他对体育运动的热爱，让他获得了平昌冬奥会赞助商三星电子的推举，作为特邀火炬手参加了此次平昌冬奥会火炬传递。“能成为奥运火炬手，一直是我的梦想。我希望能够参与2022年北京冬奥会，在自己的祖国传递奥运圣火。” 颜玉宏说。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1393,'2018-01-05 09:11:00','2018-01-06 14:47:13','74',0),(3071,'“驾车闯关澳门”的内地司机疑精神异常被遣返','王忠会','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>澳门1月5日电(记者龙土有)记者5日从澳门治安警察局获悉，日前驾驶内地牌车辆闯关澳门的内地司机熊某，基于其言语没有逻辑，怀疑精神异常，已于4日被遣返内地，警方将会向内地警方求证其精神状况。涉案车辆则被澳门警方扣押。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　去年12月31日，一辆挂内地牌的白色奔驰轿车擅自闯过横琴口岸，后被澳门治安警察局追查截获，事件无造成任何人员受伤或财物损毁。经调查，车辆在行驶期间并没有上落任何人或货物，只有一名司机在车内。</p>\n<p>　　澳门特区政府保安司司长黄少泽对事件深感震惊，认为事件反映了澳门边境口岸存在严重的保安漏洞，两地边境执法部门之间的沟通也有不足，指示澳门海关和治安警察局立即开展跟进调查并检讨，迅速采取整顿措施，避免同类事件再次发生。</p>\n<p>　　黄少泽强调，边境安全关乎澳门特区的内部公共安全和对外形象，保安部队和部门应时刻保持警惕，不断加强内部监督和执法管理，持续提升对各类边境事件的预防和应变能力，为澳门居民和旅客提供切实、可靠的安全和出行保障，维护澳门社会的发展和稳定。</p>\n<p>　　事件发生后，澳门海关与治安警察局组成工作小组跟进检讨，制订多项措施，加强防控能力。一是与内地有关部门进行协调，进一步完善信息通报机制，并将在短期内进行两地联合演练，以加强对突发事故的应变及协作能力；二是优化莲花口岸车道设备，包括加长车道栏及设置围栏，添置钉刺带等，用于拦截企图冲关车辆。</p>\n<p>　　此外，两地警方除现已有的一条点对点直线联络电话外，还增加一条紧急联络热线，使两地警方能更快速更有效地进行联络。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,743,'2018-01-05 14:38:00','2018-01-06 14:47:13','69',0),(3072,'20句话回顾习近平在党的十九大精神研讨班开班式讲话','左盛丹','人民网','<p>　　<strong>20句话回顾习近平在党的十九大精神研讨班开班式讲话</strong></p>\n<p>　　学习路上按：1月5日上午，新进中央委员会的委员、候补委员和省部级主要领导干部学习贯彻习近平新时代中国特色社会主义思想和党的十九大精神研讨班在中央党校开班。习近平总书记发表重要讲话，学习路上为您精选摘编其中二十句精彩话语，供网友们学习。</p>\n<p>　　1.做到坚持和发展中国特色社会主义要一以贯之，推进党的建设新的伟大工程要一以贯之，增强忧患意识、防范风险挑战要一以贯之。</p>\n<p>　　2.历史和现实都告诉我们，一场社会革命要取得最终胜利，往往需要一个漫长的历史过程。只有回看走过的路、比较别人的路、远眺前行的路，弄清楚我们从哪儿来、往哪儿去，很多问题才能看得深、把得准。</p>\n<p>　　3.中国特色社会主义不是从天上掉下来的。</p>\n<p>　　4.这个新时代是中国特色社会主义新时代，而不是别的什么新时代。</p>\n<p>　　5.党要在新的历史方位上实现新时代党的历史使命，最根本的就是要高举中国特色社会主义伟大旗帜。</p>\n<p>　　6.不忘初心，牢记使命，就不要忘记我们是共产党人，我们是革命者，不要丧失了革命精神。</p>\n<p>　　7.昨天的成功并不代表着今后能够永远成功，过去的辉煌并不意味着未来可以永远辉煌。</p>\n<p>　　8.时代是出卷人，我们是答卷人，人民是阅卷人。</p>\n<p>　　9.要实现党和国家兴旺发达、长治久安，全党同志必须保持革命精神、革命斗志。</p>\n<p>　　10.决不能因为胜利而骄傲，决不能因为成就而懈怠，决不能因为困难而退缩。</p>\n<p>　　11.要把新时代坚持和发展中国特色社会主义这场伟大社会革命进行好，我们党必须勇于进行自我革命，把党建设得更加坚强有力。</p>\n<p>　　12.勇于自我革命，从严管党治党，是我们党最鲜明的品格，全面从严治党永远在路上。</p>\n<p>　　13.新时代党的建设新的伟大工程，既要培元固本，也要开拓创新，既要把住关键重点，也要形成整体态势，特别是要发挥彻底的自我革命精神。</p>\n<p>　　14.功成名就时做到居安思危、保持创业初期那种励精图治的精神状态不容易，执掌政权后做到节俭内敛、敬终如始不容易，承平时期严以治吏、防腐戒奢不容易，重大变革关头顺乎潮流、顺应民心不容易。</p>\n<p>　　15.我们党要始终成为时代先锋、民族脊梁，始终成为马克思主义执政党，自身必须始终过硬。</p>\n<p>　　16.中央委员会成员和省部级主要领导干部必须做到信念过硬，带头做共产主义远大理想和中国特色社会主义共同理想的坚定信仰者和忠实实践者。</p>\n<p>　　17.领导干部要把践行“三严三实”贯穿于全部工作生活中，养成一种习惯、化为一种境界。</p>\n<p>　　18.要加强道德修养，带头弘扬社会主义核心价值观，明辨是非善恶，追求健康情趣，不断向廉洁自律的高标准看齐，做到心有所戒、行有所止，守住底线、不碰高压线。</p>\n<p>　　19.前进道路不可能一帆风顺，越是取得成绩的时候，越是要有如履薄冰的谨慎，越是要有居安思危的忧患，绝不能犯战略性、颠覆性错误。</p>\n<p>　　20.我们要继续进行具有许多新的历史特点的伟大斗争，准备战胜一切艰难险阻，朝着我们党确立的伟大目标奋勇前进。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,595,'2018-01-06 13:38:00','2018-01-06 14:47:13','64',0),(3073,'周绪红告别重庆大学：“反差萌”校长数次成网红','高辰','重庆晨报','<p><strong>　　他曾因寄语学子“平凡的世界会有人懂你”、“像黄葛树一样活出自己的节奏、精彩和笃定”走红 也曾因发布与校园彩虹的自拍、贺岁卡通形象而登上“热搜”……</strong></p>\n<p><strong> </strong></p>\n<p><strong>　　“萌系”校长周绪红，告别重庆大学</strong></p>\n<p>　　1月3日，中共中央组织部在重庆大学宣布了中共中央、国务院的任免决定，张宗益任重庆大学校长(副部长级)，周绪红不再担任重庆大学校长职务。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　工学领域“大神级”老校长</p>\n<p>　　在足球场下建起车库</p>\n<p>　　第一次见到周绪红的人，一定不相信他已经62岁了：头发乌黑，精神矍铄，跟人说话时普通话中略带湖南口音，还带着掩盖不住的微笑。</p>\n<p>　　在重大学生的印象中，周绪红令人感到亲切。他同时也是一名超级“学霸”：湖南大学工学博士，长期致力于建筑结构工程领域的科学研究、人才培养和工程实践。主持完成国家“十五”科技攻关重大项目与“十一五”科技支撑重点项目等研究与开发项目50余项，是我国在该领域的学科带头人之一。获得省部级以上科技奖励14项，其中国家科技进步二等奖3项(2项排名第一，1项排名第二)，省部级科技进步一等奖7项(4项排名第一)及二等奖5项。</p>\n<p>　　此外，周绪红还在钢结构及新型结构体系的理论研究、工程应用与产业化发展方面取得了创新成果。</p>\n<p>　　自2013年6月担任重庆大学校长以来，笑容可掬的周绪红，结合他的“大神级”工学背景，在重庆大学完成了一项又一项看似不可能完成的改造。</p>\n<p>　　以前，重庆大学B区校园内可供停车的位置不多，由于是老校区道路较为狭窄，车辆如果随意停在道路两侧，很容易造成交通堵塞、人车混行。2015年初，周绪红刚到重大一年半，工学专业出身的他想到一个问题：为何不利用操场的地下空间建一个地下停车场？这样既不会占用地面土地资源，又能解决停车难。</p>\n<p>　　当年1月，B区足球场下建停车库方案一出，很多人对此产生疑虑。周绪红毅然决然地说：“我是搞土木的，这个事我一定能办成！”</p>\n<p>　　凭借多方调研和不怕困难的“工科男”精神，周绪红带领团队历时1年零8个月，于2016年9月建成了B区地下停车场――足球场下，可容纳1031辆车停放。B区操场比原来升高了1.2米，全面翻新，铺上了蓝色塑胶。</p>\n<p>　　打造出了“别人家的大学”</p>\n<p>　　寝室食堂都让人羡慕</p>\n<p>　　周绪红在任的四年半岁月里，重大校园环境一点点蜕变，成为被羡慕的“别人家的大学”。</p>\n<p>　　重大的寝室是上床下桌四人间，空调、WiFi、24小时热水；虎溪校区的食堂因为装潢温馨漂亮，多次登上微博热搜，甚至还有重大校友发微博表示“让离开了学校的我们只为一碗小面而心心念念”；主打复古怀旧风的虎溪校区图书馆也成为网红，不仅外观夺人眼球，更是有20万册正版电子书可以免费看。</p>\n<p>　　重庆大学的这些蜕变，很大程度上是因为周绪红的“圆心理论”：本科教学作为“圆心”，各级党政一把手和班子成员、管理人员、教师和学生都要自觉地围绕这个“圆心”转，所有职能部门的工作都要围绕它去展开，并确立了“以人才培养为根本的价值观，人才培养质量是生命线的质量观，学生多样化发展的人才观，学生主体、教师主导的教学观”。</p>\n<p>　　在他四年半任期中，重庆大学正式成为“世界一流大学建设高校(A类)”，在全球四大著名大学排行榜中均榜上有名；工程学科跻身ESI前1‰，材料科学、化学、计算机科学和临床医学4个学科进入ESI前1%……</p>\n<p>　　重大2017届毕业生余幸怡现在留学美国，她的大学生涯刚好与周绪红的任期重合。“学校好多次都上了热搜，以前的同学都说很羡慕我们学校。我很高兴自己见证了一个逐步成长、逐步走向世界的母校！”余幸怡非常自豪。</p>\n<p>　　“反差萌”让他数次成网红</p>\n<p>　　给学生的寄语成经典</p>\n<p>　　2013年6月，新官上任的周绪红，谦虚地请全校师生接受自己这名重庆大学的“新兵”――“我一定将自己的一切融入重大发展的滚滚洪流中，将自己的心血毫无保留地奉献给重庆大学的师生员工。”</p>\n<p>　　许多与周绪红有过接触的重大学子，谈起老校长都无比亲切：“我们爱他，是因为他除了兢兢业业之外，还有与众不同的魅力，尤其是那种‘反差萌’。”</p>\n<p>　　2017年春天，老校区的湖畔出现了一道小彩虹，路过的周校长看到后，便拿起手机与彩虹自拍了一张，当天被发布在重庆大学官方微博上，同学们纷纷被“萌翻”，在评论区贴上了“校长同款彩虹”。</p>\n<p>　　这张“彩虹自拍”事件更是登上了微博热搜，周绪红的“反差萌”形象在多地学生群体中火了起来。</p>\n<p>　　去年春节前夕，周绪红还通过官方微信发布了一幅自己的卡通形象：身着中式大红唐装，邀请重大人一起写春联、抢红包。消息一出，刷爆了重大人的朋友圈。</p>\n<p>　　“平凡的世界会有人懂你”“像黄葛树一样活出自己的节奏、精彩和笃定”……周绪红在多届重庆大学的毕业典礼上，寄语学子、鼓励他们积极面对人生和社会，这些话一度成为经典，在网络上被大量转发、点赞。</p>\n<p>　　对待学生，周绪红也一向平易近人。有一次，重大新闻中心的值班小记者去采访他，紧张得说不出话。周校长亲切地问：“同学你是哪个学院的？”小记者回答说是土木的。周绪红笑着告诉他：“我也是土木的，你别紧张。”</p>\n<p>　　面对学生，周绪红毫无私心。2017年大年初三，远在外地的他从朋友圈中得知新闻学院有同学身患重病即将手术的消息，立即委托新闻学院院长代为前往医院看望，并以个人名义向学生捐款2000元。</p>\n<p>　　“接力棒”交到重大人手中</p>\n<p>　　张宗益校长开启新征程</p>\n<p>　　周绪红曾说：“我的人生注定与西部和西部高等教育结下不解之缘。”现在，他把重大发展的“接力棒”交到同样奉献于西部教育发展的张宗益校长手中。</p>\n<p>　　曾在重大求学，又在重大工作数十年的张宗益，将带领重大人开始新的征程，他说：“绪红校长始终忠诚于党的教育事业，政治立场坚定、学术成就突出、管理经验丰富，是我学习的榜样。从绪红校长手中接过重庆大学第十七任校长的接力棒，我深知使命光荣，深感责任重大。”</p>\n<p>　　见习记者 杨辛�h</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,926,'2018-01-06 11:09:00','2018-01-06 14:47:13','64',0),(3074,'中共中央编译局中央文献翻译研究基地在津揭牌','陈海峰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>天津1月5日电 (记者 张道正)中央编译局副局长柴方国、天津外国语大学党委书记殷奇5日在天津共同为“中共中央编译局中央文献翻译研究基地”揭牌，原部校共建的中央文献翻译研究基地正式提升为中共中央编译局直属的部级研究基地。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　中共中央编译局天津外国语大学中央文献翻译研究基地正式成立于2011年，是中共中央编译局与天津外国语大学联合创办的国内第一个翻译研究基地，是高等院校与党政部门合作、更好地服务国家、社会的一种新形式，肩负着翻译研究、人才培养、学科建设等多重使命，主要特色是开展中央文献翻译研究及培养党和国家重要文献对外翻译的专业人才，是硕士、博士、博士后高端人才培养单位。</p>\n<p>　　天津外国语大学中央文献翻译研究基地与中央编译局经过多年的合作交流，培养了一批高质量的人才，产出了一批高水准的科研成果，共同举办了“第六届全国中译外学术研讨会”以及三届“中央文献翻译与研究论坛”，合作翻译了2016、2017年政府工作报告解读文件。一系列合作为中央文献翻译和传播过程中的重要问题搭建了深入研讨的对话平台，并已成为中央文献翻译和研究成果发布的重要平台。本次会议的召开将对基地的科研、教学、翻译产生深远影响。</p>\n<p>　　天津外国语大学校长陈法春表示，加强中共中央编译局中央文献翻译研究基地的建设，是天津外国语大学与中央编译局服务党和国家决策理论推广、中国文化走出去、国际话语体系建设等国家战略的重要举措，也是落实国家“双一流”战略的具体实践，基地将在全面打造人才、学科、科研三位一体的体系，服务创新型国家的战略需求，培养拔尖创新人才，产生和推出具有广泛影响力的重大标志性成果等方面做出不懈的努力。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1466,'2018-01-05 22:57:00','2018-01-06 14:47:13','75',0),(3075,'蔡英文陈水扁哪一点输给马英九？信因果信风水','王忠会','中国台湾网','<p>　<strong>　蔡英文陈水扁凭这一点就输给了马英九！学者：台湾的不幸</strong></p>\n<p>　　中国台湾网1月5日讯 据台湾《中时电子报》报道，台当局“行政院长”赖清德金华官邸修缮，遭台北市议员质疑是改风水。对此，“行政院”发言人徐国勇表示，“院长”是不信风水的人，相关指涉是无稽之谈。但资深媒体人唐湘龙则表示，依宗教信仰的逻辑，平常出言必称“功德”的赖清德，信风水也不奇怪；此外他还直言，若台湾真有不信风水的政治人物，大概就是马英九了吧。</p>\n<div style=\"text-align:center\"> \n <img alt=\"台当局前任领导人马英九(左)、台当局现任领导人蔡英文(右)。(图片来源：台湾《中时电子报》)\" src=\"upload/image/8f02c683-46b6-42b0-a0c4-ad3e9483c608.jpg\" style=\"border:px solid #000000\" title=\"台当局前任领导人马英九(左)、台当局现任领导人蔡英文(右)。(图片来源：台湾《中时电子报》)\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  台当局前任领导人马英九(左)、台当局现任领导人蔡英文(右)。(图片来源：台湾《中时电子报》) \n</div>\n<p>　　政治人物到底信不信风水？唐湘龙在《NOWnews今日新闻》中提到此话题时认为“官越大越信”、都是“宁可信其有”的态度，小的从办公室方位、摆设等开始，大的则大兴土木、邀法师作法等。但他也表示，若政治人物十个有九个信风水的话，那剩下一个不信的，大概就是马英九了。</p>\n<p>　　他从马家治丧说起，称对方是走极简风，不发讣闻、不设灵堂、不举行公祭，简单举行家祭不到三天后随即火化，骨灰安厝公墓。而反观蔡英文父亲则是高坟大墓，还要看风水求荫庇后人。唐还提到，在陈水扁时代，除了请法师测台湾运势外，连黄琪那种小骗子阿扁都信，相比下，谢长廷的“大师”宋七力根本不算什么。</p>\n<p>　　至于赖清德信不信风水，唐湘龙没有理会其发言人的“代打”，称依宗教信仰的逻辑，信功德一定信因果，信因果一定信风水，所以平常发言必提“功德”的赖清德，信风水也不奇怪。唐湘龙更直言，当局者信到这个地步，是全台湾不幸，台湾的政治风气根本是鼓励江湖术士行骗天下。(中国台湾网 杨旋)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/8f02c683-46b6-42b0-a0c4-ad3e9483c608.jpg',NULL,864,'2018-01-05 15:42:00','2018-01-06 14:47:13','70',0),(3076,'金融科技硕果累累 佰仟金融知识产权申请上百件','姜贞宇','东北新闻网','<p>　　一分耕耘，一分收获。佰仟金融在2017年持续发力科技领域，自主研发多个系统及软件，全面优化用户体验，目前已初现成果。基于在技术研发方面的优异表现，佰仟金融继去年6月成为深圳市软件行业协会理事单位后，在2017中国第八届金鼎奖评选中荣膺 “年度优秀科技金融公司”称号，近日还入选为深圳市专利协会常务理事会员，科技实力再获肯定。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　佰仟金融始终以用户需求为核心，加大金融科技创新投入，与用户相关的技术及系统发明，最终转化为升级服务水平的基石。据了解，佰仟金融在2017年仅专利、软件著作权申请近百件，目前已取得8件专利证书、34件计算机软件著作权登记证书，意味着科技服务能力得到权威认可。</p>\n<p>　　为打造最可信赖的微金融综合服务平台，佰仟金融的专利申请既涵盖客户端、APP页面的外观专利，也包括基于开发运维、客户端、风控、信审、催收等业务流程而结合互联网+创新的多项技术及系统发明。在2017年6月至9月期间，佰仟金融自主研发的智能信息推送系统、蜂巢提单系统、风控数据接入系统等多个金融业务流程系统，相继获得由国家版权局颁发的计算机软件著作权登记证书。</p>\n<p>　　“公司鼓励我们以实用性、创新性为导向，针对市场未来可能的技术趋势、客户提出的或潜在的需求，来进行自主研发和探索，改进原有的方案或者提出实现某个功能的新型路径。”佰仟金融相关负责人介绍，公司高度重视技术创新，为促进公司技术创新和形成自主知识产权，提高核心竞争力，已制定知识产权管理办法，并建立知识产权申请机制。</p>\n<p>　　在尊重和保护知识产权的基础上，佰仟金融也大力支持员工自主创新和研发新技术。为鼓励员工创新，佰仟金融不仅在内部上线专利申请流程，举办专利培训课程指导员工提交申请，同时为专利申请设置专项奖励。佰仟金融计划在2018年持续推动研发创新能力，聚焦大数据、区块链、人工智能、云计算等技术，并加大研发转化力度，以金融科技助力实体经济发展。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,604,'2018-01-05 16:08:00','2018-01-06 14:47:13','67',0),(3077,'智能金融元年五大关键词:AI驱动下的金融行业变革','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>1月5日电 2017年被称为“智能金融元年”。</p>\n<p>　　这一年，人工智能被提升到国家战略发展层面，从政策到应用，再到与金融行业的快速融合，AI技术逐渐成为金融业顶尖公司的“标配”设施；</p>\n<p>　　这一年，以人工智能、大数据等为核心要素，BAT等互联网巨头公司全面携手银行、保险、基金等金融机构，不断提升金融服务效率，拓展金融服务的广度和深度。</p>\n<p>　　新年伊始，回顾过去，五个关键词勾勒出2017智能金融元年的那些创新、变革与竞合，也成为未来金融业洗牌与提升的重要因素。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　<strong>关键词一：国家战略</strong></p>\n<p>　　<strong>核心事件：</strong></p>\n<p>　　2017年5月，央行成立金融科技委员会，加强对金融科技工作的研究规划和统筹协调，为金融科技发展提供战略规划与政策指引。</p>\n<p>　　2017年7月，国务院《关于印发新一代人工智能发展规划的通知》将人工智能升级为国家级战略。通过推动人工智能与各行业融合创新，在金融、制造、农业、物流等重点行业和领域开展人工智能应用试点示范，推动人工智能的规模化应用。</p>\n<p>　　2017年12月，工信部发布《促进新一代人工智能产业发展三年行动计划》，推动智能产品在金融等领域的应用。计划指出要以市场需求为牵引，积极培育人工智能创新产品和服务，推动智能产品在金融、工业、医疗、交通等领域的应用。</p>\n<p>　　点评：国家战略助力智能金融 “狂奔”</p>\n<p>　　金融是人工智能技术的最佳落地场景之一。在国家战略及有利政策的支持下，各大型金融主体、互联网金融企业纷纷响应国家号召，利用自身优势，将人工智能技术运用于金融产品与服务中，为“AI+Fintech”的智能金融发展营造了良好的环境。</p>\n<p>　　以蚂蚁金服、百度金融为例。利用自身大数据、人工智能技术，互联网巨头公司在金融科技的研发和应用上具有领先优势，在国家战略的号召下，对外联合的“赋能”模式又成为必然选择。可以说政策为人工智能在金融领域“施展抱负”提供了指引和巨大的空间。</p>\n<p>　　<strong>关键词二、概念诞生</strong></p>\n<p>　　<strong>核心事件：</strong></p>\n<p>　　2017年6月20日，在百度与中国农业银行签署战略合作会议上，百度董事长兼首席执行官李彦宏首次提出“智能金融”这一概念。</p>\n<p>　　李彦宏指出，智能金融即人工智能与金融的全面融合，以人工智能、大数据、区块链等高新科技为核心要素，全面赋能金融机构，提升金融机构的服务效率，拓展金融服务的广度和深度，使得全社会都能获得平等、高效、专业的金融服务，实现金融服务的智能化、个性化、定制化。</p>\n<p>　　而在智能金融应用领域，百度金融则率先提出了七大应用布局，包括智能获客、身份识别、大数据风控、智能投顾、智能客服、金融云、区块链。百度金融也成为首个完整描绘智能金融应用方向的机构。</p>\n<p>　　2017年7月，在《新一代人工智能发展规划》中，智能金融第一次被官方正式提出，至此，智能金融正式开启了一个新的时代。</p>\n<p>　　点评：全面发力，开启AI+Fintech新纪元</p>\n<p>　　人工智能并不是一个很新鲜的概念，在经历了60多年的不断探索，终于开始为各行业提供实实在在的价值。“智能金融”概念的提出，是AI在金融领域快速发展并落地应用的体现。</p>\n<p>　　从Fintech到 Tech-fin，再到AI Fintech,2017年人工智能在金融行业掀起了革命性的改变，也开始在身份识别、获客、智能投顾、智能客服等领域扎实落地。可以说，伴随智能金融概念的诞生，人工智能与金融结合迎来了新纪元。</p>\n<p>　　<strong>关键词三：技术破局</strong></p>\n<p>　　<strong>核心事件：</strong></p>\n<p>　　2017年6月，蚂蚁金服对外表示，将自有的人工智能、金融云计算、小额支付等多项技术都将逐步开放给合作伙伴。在财富管理领域，蚂蚁金服的金融科技已经对天弘、博时、国泰、建信、南方、兴全、民生加银等多家基金公司开放。</p>\n<p>　　2017年11月，百度金融推出大数据风控平台――“般若”。在算法上可利用深度学习、关联挖掘等解决数据稀疏问题。从3000+降维至400维，可将行为的风险区分度有效提升5%+；基于百度数亿级用户数据，通过图计算，可将信用标签传递，丰富信贷样本；在信用领域，央行征信数据加百度数据可以将客群的风险区分度提升13%；在反欺诈领域，百度已拥有百亿节点、五百亿边的关联网络，可以提升识别骗贷团伙的成功率；在智能服务上，百度人脸识别技术在商业上的运用过程中，对攻击的拒绝率可以做到95%。</p>\n<p>　　点评：技术驱动，夯实智能金融基础</p>\n<p>　　智能金融的基础便是“AI”技术及大数据。作为自带不同技术基因的大型互联网科技公司，BAT在智能金融的优势明显，也在为传统金融机构的合作中承担重要的赋能角色。</p>\n<p>　　其中，百度以搜索数据见长，阿里以电商数据为傲，腾讯则以社交数据为本，各有优势，各有建树。</p>\n<p>　　<strong>关键词四：跨界合作</strong></p>\n<p>　　<strong>核心事件：</strong></p>\n<p>　　2017年3月，阿里巴巴、蚂蚁金服与中国建设银行举行签约仪式，双方就推进建行信用卡的线上开卡业务，以及线上线下渠道业务、电子支付业务等展开合作。</p>\n<p>　　2017年6月，百度与中国农业银行达成战略合作，共建农行金融大脑以及客户画像、精准营销、客户信用评价、风险监控、智能投顾、智能客服等六个方向的具体应用。11月，仅4个多月的时间，双方共建了联合实验室，并完成农行金融大脑一期工程的软硬件搭建及部署及实验室验收。</p>\n<p>　　2017年6月，京东金融与工商银行签署全面合作协议；11月，双方宣布推出“工银小白”数字银行，成为国内银行业首个开到互联网平台上的银行。</p>\n<p>　　2017年6月，“中国银行――腾讯金融科技联合实验室”正式挂牌成立，宣布将在金融云等云计算领域开展更纵深的合作。9月，腾讯与中国银行再度携手，签署战略合作协议。</p>\n<p>　　点评：巨头联姻，争相布局金融科技</p>\n<p>　　智能金融在2017年倒逼了传统金融机构的变革。事实上，传统金融机构变革存在乏力现象，而互联网科技企业在拓展金融业务时也面临着缺乏线下金融渠道等难题，在这种情况下，跨界合作为双方“拥抱彼此”提供了最佳契机，以国有大行为代表的传统金融机构和互联网科技企业纷纷展开合作，通过优势互补，不断推进各自在金融科技领域的布局，实现合作共赢。</p>\n<p>　　<strong>关键词五：服务多层次实体</strong></p>\n<p>　　<strong>核心事件：</strong></p>\n<p>　　2017年6月，建行在北京正式发布“小微快贷”互联网融资平台。通过该行内部多个系统组件、外部人民银行征信系统等互通互联，利用互联网渠道，实现了小微企业贷款全流程网络化、自助化操作。</p>\n<p>　　2017年6月，蚂蚁金服宣布投入10亿元贷款支持农产品生鲜行业，以金融科技为生鲜上游种养殖农民提供覆盖生产、流通、销售的CFO综合服务，打通“从村头到餐桌”的全产业链模式。</p>\n<p>　　2017年11月，百度与农行合作推出AB贷这一产品，专注为农行现有客群提供更优质的服务。通过百度信用分等技术，在优级客群中找到响应率高的用户，解决优级客群响应率低的行业痛点。</p>\n<p>　　点评：“智能”服务，助力金融服务“脱虚向实”</p>\n<p>　　金融的基本功能之一是为实体经济造血输血，在智能金融的帮助下，金融业“脱虚向实”更加扎实，深入覆盖到众多小微企业、个体工商户以及众多薄征信、征信“白户”。</p>\n<p>　　在这一过程中，人工智能发挥了重要的作用，在获客、营销、风控上全面拓展了金融服务的广度与深度，并渗透到多层次实体中。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,991,'2018-01-05 16:50:00','2018-01-06 14:47:13','82',0),(3078,'狗年生肖邮票深圳首发 香港邮迷组团赴深抢购(图)','官志雄','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>1月5日电 据香港《文汇报》报道，5日上午，《戊戌年》狗年生肖邮票及戊戌年《百福盈门》封装银章在深圳首发，吸引了大批集邮爱好者参加，不少香港铁杆“邮迷”更是组团北上排队，为的是能抢购首日封等纪念邮品和加盖纪念邮戳。</p>\n<div style=\"text-align:center\"> \n <img alt=\"《戊戌年》狗年生肖邮票吸引了大批集邮爱好者购买。图片来源：香港文汇报。\" src=\"upload/image/8bc609d2-d7e2-4320-b1dd-23b59806730b.jpg\" style=\"border:px solid #000000\" title=\"《戊戌年》狗年生肖邮票吸引了大批集邮爱好者购买。图片来源：香港文汇报。\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  《戊戌年》狗年生肖邮票吸引了大批集邮爱好者购买。图片来源：香港文汇报。 \n</div>\n<p>　　据中国邮政深圳分公司介绍，该套生肖邮票延续了第四轮生肖票“合家欢”的设计理念，展示了中华田园犬的形象。当日与《戊戌年》生肖邮票一同发行的还有生肖首日封、纪念封、纪念邮折和纪念邮册等特色邮品。</p>\n<p>　　据中国邮政深圳分公司集邮与文化传媒部主任黄立新介绍，每年生肖邮票首发时，都有大批港澳地区集邮迷会到现场排队购票，纪念封、纪念戳等都大受追捧。</p>\n<p>　　黄立新说：“深港两地集邮迷之间的交流特别多，很多香港同胞一早就知道今日会有首发，一大早就过关来排队。生肖邮票和传统文化题材类邮票最受香港邮迷喜爱，同样香港发行的文化类邮品内地邮迷也会跨境去购买，例如去年香港发行的旗袍主题邮票，就有很多内地邮迷过去采购。”</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/8bc609d2-d7e2-4320-b1dd-23b59806730b.jpg',NULL,1626,'2018-01-05 15:56:00','2018-01-06 14:47:13','69',0),(3079,'厦门出口茶叶“身价”增 乌龙茶均价涨逾六成','史建磊','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>厦门1月6日电 (童小麟 康江卫)厦门是中国茶叶出口大省福建的重要茶叶出口口岸。去年，从厦门出口的茶叶在世界上卖得更“贵”了，尤其是乌龙茶出口均价比上年增长六成。</p>\n<p>　　厦门出入境检验检疫局6日披露，厦门检区去年共出口各类茶叶6096.7吨，货值达到4356万美元，在出口数量比上年下降9%的情况下，货值反而比上年提升逾三成，均价也比上年提升45.7%。</p>\n<p>　　在厦门出口茶叶中，乌龙茶居主要地位，尤以对日本、东南亚、美国及香港等地出口为主。</p>\n<p>　　来自厦门出入境检验检疫局的统计数字显示，去年，乌龙茶出口5482.5吨，占茶叶出口总量九成左右；货值3795.3万美元，比上年增长近七成；均价达到每千克6.92美元，比上年增长逾六成。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　业界人士指，厦门出口茶叶在数量减少的情况下，货值仍出现了较大幅度的增长，主要是出口茶叶产品档次和质量明显提高。特别是主销产品乌龙茶，产品花色结构优化，中高档次的茶叶占比大幅提高，致均价大幅上涨。</p>\n<p>　　厦门还不断拓展茶叶出口新市场。去年，缅甸成为厦门的新增茶叶出口国家，从厦门进口茶叶货值达379万美元，一举跃居厦门检区茶叶出口国家和地区货值前5位。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,518,'2018-01-06 12:47:00','2018-01-06 14:47:13','66',0),(3080,'让“创新”激荡新能源汽车应用的春潮','魏巍','东北新闻网','<p><strong>　　让“创新”激荡新能源汽车应用的春潮</strong></p>\n<p>　　发展新能源汽车，是我国从汽车大国迈向汽车强国的必经之路。近年来，在工信部、财政部、发改委、交输运输部、商务部等多部委和各省市党委、政府的大力支持下，中国已经成为全球最大的新能源汽车产销国，成为最具活力的新市场。但是另一方面，运营难、推广难等问题一直困扰产业发展，不仅打击了企业创新发展的积极性，还给初生的新能源汽车产业套上 “紧箍咒”。其实，新能源汽车产业在全球范围都属于全新课题，具有复杂性、艰巨性、长期性，不妨理性地看待“部分闲置”，给产业发展一点时间，让“创新”激荡新能源汽车应用的春潮。</p>\n<p>　　党的报告指出，创新是引领发展的第一动力。新能源汽车产业发展成绩来之不易，离不开创新之举，未来潜力无限，更需要创新驱动。作为我国战略性新兴产业之一，新能源汽车产业更需要发挥创新这一发展的不竭动力，从应用创新、模式创新、制度创新等多个层面，全面驱动我国新能源汽车产业跑得更远。</p>\n<p>　　创新，需要在制度创新上发力，在机制建设上突破。根据我国现行的交通管理法规以及各省市实践，车辆从事道路运输经营活动的，需要满足三证合一，即一车、一人、一证。以纯电动大巴为例，大巴车上路运营要求司机持有A1牌照。运营平台公司为了满足三证合一的规定，需要较长时间去招聘和车辆一一匹配的司机，这导致一定时期内的车辆闲置。这需要我们在完善鼓励产业发展的基础体系，针对产业发展的实际痛点、难点，制订、完善和创新相应的配套制度，在三证合一的问题上发挥敢闯敢试的精神，打破这一传统桎锢，盘活产业发展的各个环节。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　交通运输部2015年出台了《关于加快推进新能源汽车在交通运输行业推广应用的实施意见》，2017年又出台了《关于深化改革加快推进道路客运转型升级的指导意见》，提出了扩大道路客运企业经营自主权，简化和优化行政审批程序，大力发展旅游客运和包车客运，提升农村客运公共服务水平等一系列针对性的措施。这些改革创新措施的推进，对解决三证合一的新能源汽车推广应用的瓶颈问题提供了制度创新的基础，目前运营公司存在的部分闲置问题一定能迎刃而解。</p>\n<p>　　创新，需要以问题导向来破解深层次难题。与传统汽车相比，新能源汽车存在其特有的新困难、新问题，如购置成本高、充电难、续航里程短等，需要通过针对性的创新来尝试解决，如新能源汽车充电场站建设耗时在半年以上，按传统车辆运营企业先洽谈需求、后购车投放的模式已经不适用于新兴产业的发展。为及时满足市场需求，新能源汽车运营企业往往通过模式创新，一次性购置车辆才能降低替代(燃油车)成本，才能建设相关配套设施，以提前展开布局。这涉及到核心零部件、整车企业、运营公司、充电场站、电力配套、城市规划管理等产业链上下游和多部门的衔接沟通、标准对接，如果某一环节出现延迟或缺位，就可能导致部分车辆闲置现象。这是伴随新兴事物出现的新问题，这些新问题需要靠新思路，这才是想作为、会作为的关键。如果企业陷入消极心态被动等待，害怕质疑而裹足不前，中国新能源汽车产业有可能再失去黄金十年。</p>\n<p>　　创新，需要营造宽松的环境，“看空”中国新能源产业发展毫无依据。优化创新环境要着力聚焦“松绑”、“包容”等关键点。“松绑”，就是要推动企业在技术、标准以及监管等方面充分对接，最大限度减少事前准入限制，进一步减少对守法经营企业的行政干预，让企业放开手脚，大胆创新。“包容”，就是要营造更加宽松的环境，鼓励企业发挥所长，宽容企业在探索创新中的失误，给企业创新与试错的空间。一叶障目不见泰山，是分析产业经济的大忌；将车辆上牌登记过程中的“空跑”与骗补谋补划上等号，无疑忽视了监管部门的智慧和决心，将整个中国新能源汽车产业引入歧途。</p>\n<p>　　新能源汽车产业的发展，需要各个企业继续发挥敢闯敢拼的精神，敢做先锋，不当看客，保持锐意创新的勇气、敢为人先的锐气、蓬勃向上的朝气。中共中央、国务院《关于营造企业家健康成长环境弘扬优秀企业家精神更好发挥企业家作用的意见》也明确指出，应当营造尊重和激励企业家干事创业的社会氛围，营造鼓励创新、宽容失败的文化和社会氛围，对企业家合法经营中出现的失误失败给予更多理解、宽容、帮助。对在企业发展中大胆探索、锐意改革所出现的失误，只要不属于有令不行、有禁不止、不当谋利、主观故意、独断专行等情形者，要予以容错，为担当者担当、为负责者负责、为干事者撑腰。</p>\n<p>　　2018年是贯彻党的精神的开局之年，是改革开放40周年，是决胜全面建成小康社会、实施“十三五”规划承上启下的关键一年。心中有阳光，脚下有力量。站在新起点，开启新征程，需要我们不忘初心，敢于担当，不畏艰难险阻，用创新激荡起产业发展的春潮。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,26,'2018-01-05 16:16:00','2018-01-06 14:47:13','72',0),(3081,'林绍纲35年目睹文坛：曾表面风平浪静暗地里斗争不断','高辰','中国新闻周刊','<p><strong>　　林绍纲：35年目睹之文坛现状</strong></p>\n<p>　　“过去的文坛和现在不同，常常表面风平浪静，暗地里斗争不断。而我这一生，就为这些文坛大家服务了。”</p>\n<p>　　中国新闻周刊记者/宋春丹</p>\n<p>　　1956年6月的一天，28岁的林绍纲从中南大区文化机关调进中国作家协会，来到北京市东总布胡同22号报到。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　从此，除了“文革”中一个短暂时期，他从未离开作协，直到1991年退休。</p>\n<p>　　“过去的文坛和现在不同，常常表面风平浪静，暗地里斗争不断。而我这一生，就为这些文坛大家服务了。”</p>\n<p><strong>　　东总布胡同22号</strong></p>\n<p>　　每天早上8点，林绍纲骑着自行车准时到达东总布胡同22号的作协大院。中国作协的前身是1949年成立的“全国文协”，1953年改为现名。</p>\n<p>　　从红漆大门进去，是一座中西合璧的豪华三进院落。穿过前院迎面假山中间的月亮门，是一座四合院，七间北房，东西各四间厢房。再后面是一个种满花草树木的院子，主楼是一座绿色琉璃瓦顶的三层楼房。整个院落由木质雕花回廊连接。</p>\n<p>　　北洋军阀时期，这里是北宁铁路局局长的私宅，日本占领时期成了日本宪兵队司令部，抗战胜利后又成为国民党“励志社”的所在地。因为当年铁路局局长在这里自杀，这座大院被人称为北京“四大凶宅”之一。</p>\n<p>　　当时，东总布胡同22号既是作协机关所在地，也是一些作协领导和作家的宿舍，艾青、陈企霞、邵荃麟、张天翼、沙汀、严文井都住在这里。大院里，大家都以“同志”相称，不称呼职务。</p>\n<p>　　作协机关除担任行政职务者之外，还有专职写作的驻会作家20多人，周立波、张天翼、艾青、冰心、白朗、罗烽、艾芜、赵树理都在其中。</p>\n<p>　　驻会作家的待遇很高，文艺三级就相当于正局级干部待遇。张天翼、周立波、冰心等被定为文艺一级，政治行政待遇上靠行政八级(刘白羽等作协领导为行政八级)，工资收入更高，在300元以上(行政八级200元)。</p>\n<p>　　那时，稿费学习苏联，采取基本稿酬加印数稿酬的方式。著名作家一部长篇小说可以拿到五六万甚至七八万元的稿酬。当时北京一个小四合院房价就一万多，赵树理、丁玲、杨朔、萧殷等几位作家都买了房子。艾青的稿费比较高，一发稿费就请大家吃饭，一桌也不过百八十块。不过，普通作家一本书的稿酬很少上万的。</p>\n<p>　　驻会作家出差和深入生活的一切费用，都由作协报销。创作期间生活上遇到困难，可向作协的创作委员会申请创作基金。但这种情况较少，作协当时的财政也并不宽裕。</p>\n<p>　　颐和园的云松巢是中央特批给作协的创作休养地，只有几间房。丁玲曾在那住过一段时间。1951年夏一个星期天的下午，罗瑞卿陪毛泽东来云松巢看望了她。另一处休养地是北京西山八大处的证果寺，这是北京市无偿划拨给作协的。禅房被改成单间宿舍，供作家写作、休养。</p>\n<p>　　1955年之前，作协有一段比较宽松的时期。作家不坐班，每周只参加三天半的政治学习。周六晚饭后，文艺界的人爱到22号院来，交流文学艺术，讨论新闻时事，唱歌、下棋、聊天。</p>\n<p>　　早些年，创作委员会每周最少举办两次作品讨论会。后来运动多起来，也会间歇举办。作协主席茅盾很喜欢参加这个活动。老舍挂名作协副主席，但行政关系在北京市文联，只是偶尔来参加。有一段时间，老舍和曹禺都是作协书记处书记，关系看起来格外好。老舍常常一手拄着拐杖一手摇着折扇走进会议室，跟大家打招呼，有时还会做个鬼脸。他喜欢和曹禺开玩笑，一句一个“家宝”：“家宝，你还记得那年我们相约去看曹雪芹纪念展你迟到的事吗？”</p>\n<p>　　1957年初，作协机关搬到王府大街64号，东总布胡同22号院改为用来存放图书和招待宾客。</p>\n<p><strong>　　作协领导</strong></p>\n<p>　　林绍纲调到作协时，邵荃麟是作协党组书记，刘白羽、郭小川为副书记(后任命严文井为副书记)。郭小川兼任作协秘书长，副秘书长两位，黎辛分管党务人事，张僖分管行政。</p>\n<p>　　通州人刘白羽有一种北京遗老遗少的派头，夏天穿一身白色丝绸，戴时髦的遮阳礼帽，手里摇一把白色折扇。开会讲话时一口京腔，情绪饱满，极富煽动性。他的讲话一般内容简练，不像周扬能一口气脱稿讲四个钟头。</p>\n<p>　　严文井不大参与日常事务，听汇报或者批阅公文通常只谈原则，不给具体意见。他爱猫，最多时家里养过七只。他喜欢西洋音乐，常去琉璃厂淘唱片，家里有几百张奏鸣曲和交响乐的唱片。开会时，他常偷偷给人画肖像，好友张天翼、赵树理经常成为他的素材。</p>\n<p>　　他生性诙谐，个性外向不设防，宴请外宾时，谈完正事之后喜欢讲故事。长相俊美的单身汉杨朔总成为严文井开玩笑的对象，如果席间坐着日本作家，他就会请人给杨朔介绍日本女孩做妻子，总是弄得杨朔很尴尬。</p>\n<p>　　在一次批斗丁玲的会上，旁人的发言都很激烈，严文井却站起来说：“陈明配不上丁玲。”招致哄堂大笑，批判也进行不下去了。</p>\n<p>　　38岁就当上作协秘书长的郭小川年轻时髦，上班经常穿灰色西装，系红领带。他性格开朗天真，几乎和任何人都能一见如故。虽然不用坐班，但他基本上每天都会骑着自行车来作协。</p>\n<p>　　负责外事工作的杨朔是林绍纲的直接领导，林绍纲经常去杨朔家请示汇报。</p>\n<p>　　杨朔住在一所狭窄的平房里，这是他用稿费买的，格局不好，光线很暗。他终身未婚，和弟弟一家住在这里，自己的生活由一位保姆照顾。他常西装革履，虽然也时常开开玩笑，但与人相处总有一点隔阂感。</p>\n<p>　　林绍纲来时，杨朔会匆匆收起写字桌上的书刊和稿纸，寒暄几句，习惯性地说起近来睡眠不好，头疼，离不开安眠药。听汇报时，他有时走神，眼睛发直，边点头边嗯嗯。他的工作压力很大，失眠和神经衰弱影响到了他的创作。他的字一个一个圆圆的，该短的笔划拉得很长，该长的笔划又写得很短，很难辨认。每次看他的批阅，林绍纲都有点头疼。</p>\n<p><strong>　　作协的外事</strong></p>\n<p>　　作协设有创作委员会、外国文学委员会、普及工作部、古典文学部、文学基金全委会等机构，还编辑出版《文艺报》《人民文学》《新观察》《文艺学习》《文学遗产》《译文》《中国文学》(英文版)等刊物。外国文学委员会(简称“外委会”)的职责是与国外作家进行联络和交往，向国外推介作品，开展“民间外交”。</p>\n<p>　　外委会主任为萧三，副主任杨朔、陈白尘。林绍纲担任办公室副主任，手下有十多位精通各种小语种的职员。</p>\n<p>　　1958年后，亚非作家在国际上活跃起来。根据亚非作家常设事务局的要求，成立了中国作家联络委员会，茅盾担任主席，刘白羽担任副主席，杨朔任秘书长。</p>\n<p>　　每次外国作家来访，都由外委会负责安排，邀请与来访者身份地位和作品知名度相当的国内作家会见。</p>\n<p>　　茅盾、严文井、巴金、冰心、叶君健都是外事活动的常客。很多小说家口才一般，不善于应付外事场合，外委会通常不敢邀请。姚雪垠是个例外，他参与外事活动灵活得体，比较活跃。</p>\n<p>　　当时外国作家经常点名求见赵树理，但林绍纲邀请他时总是被拒绝。一次，他终于答应了，穿着布鞋、胡子拉碴地随团出访，住宾馆非硬板床不睡，只好让他锁上门睡地板。别人在台上讲话，他不爱听了就去洗手抽烟，边哼哼山西上党梆子。看晚会总是睡着，甚至打起呼噜。</p>\n<p>　　林绍纲近百次陪同作协副主席巴金参加活动。巴金说话声音不大，发言稿多是亲自起草。他从来不领工资，一直靠稿费生活，在作家中几乎独一无二。</p>\n<p>　　上个世纪五六十年代，凡是外国尤其是日本作家代表团来访，作协也会邀请国务院外事办负责人廖承志会见或参加宴请。</p>\n<p>　　去国外出访，名单由作协领导提出。</p>\n<p>　　“刘白羽当时在作协几乎说一不二。巴金和刘白羽一同出访，虽然巴金是代表团团长，但也会听刘白羽的。”林绍纲说。</p>\n<p>　　外委会也会对名单提出建议。上个世纪60年代初，中苏激烈论战，文艺界来往也带有浓厚的火药味，所以选派作家以政治性为第一考虑。外委会一致认为，诗人李季和陕西作家王汶石是最合适的人选。</p>\n<p>　　每次出访，林绍纲要组织人员为代表团成员订做西装和中山装、准备发言稿等。按照杨朔要求，代表团出访期间，他总是留在国内负责后勤。直到1979年，才有机会第一次随团出国。</p>\n<p>　　推介作品主要由外委会主办的《中国文学》杂志(英文版)来承担，外委会将国内的优秀作品翻译成英文，发行到亚、非、东欧国家。杂志由茅盾挂名主编，副主编叶君健具体负责。</p>\n<p>　　精通十几门外语的叶君健在中国对外文化交往中一直扮演着重要的角色。出国访问时，他通常是代表团中的“高参”。从上个世纪50年代起，直到90年代，他在北海后门边的家都是接待文化界外宾的点，时间久了，工作人员都习惯称这里为“外交小院”。除了“文革”期间受到过影响，这个小院近半个世纪都是热闹的。</p>\n<p>　　每年，作协还会主办“纪念世界文化名人”活动。纪念泰戈尔那次，因为作家中很少有人熟悉泰戈尔，林绍纲和同事专门去找了研究泰戈尔的专家，分析他的生平和作品，写出报告和发言稿。</p>\n<p><strong>　　丁陈事件</strong></p>\n<p>　　但作协绝非安静的书斋。</p>\n<p>　　“不堪回首。改革开放前，政治斗争几乎没断过，而作协往往首当其冲。一旦政治斗争来了，只能服从。”林绍纲说。</p>\n<p>　　林绍纲到作协时，正赶上丁玲和陈企霞分别提出书面申诉，要求对1955年的“丁陈反党小集团”结论给予复查。</p>\n<p>　　1956年6月28日，中宣部部长办公会议决定成立研究组。杨雨民、阮章竞、康濯、严文井、葛洛、黎辛、张僖组成“作协七人小组”，负责具体调查，后改为刘白羽、杨雨民、张海组成的三人小组。调查组下设办公室，由丁宁负责，抽调了林绍纲等五人做具体工作。</p>\n<p>　　办公室将16次批判会的发言稿打印出来，分发原发言者，逐个面谈。共约谈了133位作家，其中丁玲问题70人，陈企霞问题63人。谈话记录竖排打印在16开纸上，摞起来有近半米高。</p>\n<p>　　林绍纲负责约谈康濯、严文井、阮章竞、袁静等人。在他的印象中，被约谈者都对当时的批判感到沉重。</p>\n<p>　　无论是个别谈话，还是在修正后的书面发言稿中，绝大多数人都将“反党集团”“一股反党逆流”“宗派小集团”等字句抹去了，取代以缓和的字眼。最后得出结论，“反党小集团”不能成立。</p>\n<p>　　写书面结论的工作推来推去，最后落到了郭小川头上。他历时三月，数易其稿。结论写道：“(丁陈)某些时候和有些问题上形成了一种宗派性质的结合，向党闹独立性。他们这种宗派主义性质的错误是严重的，但还没有发展到反党小集团的程度，因此不应以反党小集团论。”结论在作协党组会上通过，经中宣部审阅同意，送丁玲、陈企霞征求意见。</p>\n<p>　　1957年6月6日，丁玲和陈企霞在作协党组扩大会议上被摘帽。</p>\n<p>　　周扬和刘白羽在讲话中表示，1955年的批判有偏差，斗争过火，对揭发材料没有核查清楚就向中央写报告，不慎重。时任作协党组书记的周扬表示承担责任。</p>\n<p>　　会上，丁玲质问：“为什么会发生这样的错误？”陈企霞则称这是“政治迫害”。会议出现僵局，只能暂时休会。</p>\n<p>　　没想到，休会期间，整风形势又发生了变化。</p>\n<p>　　1957年夏季的一天早上，一辆小轿车把林绍纲接到东总布胡同22号，接上刘白羽后，驶往天津。</p>\n<p>　　天津作协分会主席方纪安排了一个小会。会上，在不断追问下，一位天津女作家“交代了与陈企霞的不正当关系”以及陈企霞和丁玲的某些政治言论。</p>\n<p>　　由此，批判升级。一本《南唐二主词校订》被说成陈企霞和天津女作家秘密联络的暗号，又说丁玲准备在即将召开的文代会上“公然宣布退出作协”。丁玲、陈企霞、冯雪峰、艾青、李又然、罗烽、白朗7人被打成“丁陈反党集团”，定为右派。</p>\n<p>　　各部门提交了右派名单，由作协党组讨论。</p>\n<p>　　评论家黄秋耘写了很多当时看来完全够右派资格的文章，林绍纲至今记得他有一篇文章叫《刺在哪里》，文辞严厉。 但邵荃麟是黄秋耘在香港做地下工作时期的老领导，对他知根知底，党组会上将他力保了下来，只做留党察看。</p>\n<p>　　《人民日报》头版文章《北大在闹事》的两位作者朱行和黄沙也在名单上。郭小川说，朱行只有24岁，就不要划了。朱行因此被保了下来。</p>\n<p>　　最后，作协有30多人被划为右派，占比约10%，超额完成。“文艺界从三十年代起就存在宗派斗争，一直延续到反右时期。”林绍纲说。</p>\n<p>　　风口浪尖上，冯牧被调来作协担任文艺报副主编。他培养的很多作家都成了右派，但他幸运地躲了过去。林绍纲也很担心自己被戴上帽子，幸而过关。</p>\n<p>　　1959年，反右倾开始，作协党组秘密批判赵树理和郭小川。后来，林绍纲听郭小川说：“批判赵树理才好玩儿呢，你说一句，他说十句，翻着他那个笔记本说得有理有据，谁也说不过他，咋办，主持人只能宣布散会。”</p>\n<p><strong>　　苦中作乐</strong></p>\n<p>　　虽然作协总是风浪不断，但生活待遇一直很高，即便在三年困难时期也一样。</p>\n<p>　　1961年初春，突患肝炎的林绍纲被安排到八大处休养。作协党组书记邵荃麟夫妇、文学评论家侯金镜也在此休养。一日三餐，同桌进餐。伙食很好，早餐有牛奶和鸡蛋。作家们每天上午读书写作，下午自由活动，散步聊天。</p>\n<p>　　幽默又有些口吃的侯金镜提醒过林绍纲，但他还是在一天下午散步时被邵荃麟“逮着”了。得知林绍纲正在看巴尔扎克的《欧也妮葛朗台》和《高老头》，邵荃麟就站在小山坡上操着难懂的浙江口音跟他说了半天巴尔扎克。</p>\n<p>　　邵荃麟从不发脾气，但是也极少开怀大笑，从早到晚烟不离手，每天无休止地读书写作。他每天要吸三包大中华。困难时期，作协秘书长张僖通过外事渠道每个月给他搞来9条烟，其中一半是自行燃灭的。</p>\n<p>　　在八大处待了不到半个月，林绍纲就接到杨朔的电话，催他回来一起筹备“亚非作家东京紧急会议”，只能匆匆赶回去上班。</p>\n<p>　　1961年，中国派出了庞大的作家代表团出席这次会议。团长巴金，副团长冰心和刘白羽，沙汀、杨朔、李季、叶君健等为团员。</p>\n<p>　　这次会议后不久，杨朔因与作协主要领导难以合作，1962年初离开了作协，调到中国亚非团结委员会工作。</p>\n<p>　　此前不久，郭小川也调离了作协，去《人民日报》做高级记者。</p>\n<p>　　这期间，林绍纲的家搬进了王府大街黄土岗胡同13号作协宿舍大院。他的住处是三间东厢房，房间虽小，但花砖铺地，很有气派。《文艺报》副主编冯牧则搬进了西厢房，与他家遥遥相对。</p>\n<p>　　院里住着十多户人家，基本上都是作协的工作人员或者驻会作家。此前，郭小川、李季都在这里住过。13号院的东院是《诗刊》编辑部，主编臧克家和副主编阮章竞、徐迟、葛洛是这里的常客，吟诗论道的声音充斥着整个院子。</p>\n<p>　　冯牧最为好客，尤其喜欢聪明的少年。他家里一天到晚人流不断，周巍峙、贺敬之、张光年等常来做客。他很爱聊天，茶前饭后爱站在院里，无论老少都能和他聊到一起，而且一聊起来就没完，直到家人喊吃饭了才散。</p>\n<p>　　1964年3月，文联和作协开始整风。秋天，社会上已经开始批判“三家村”，作协主要是批邵荃麟和赵树理的“中间人物论”。</p>\n<p>　　批判告一段落后，邵荃麟调离了中国作协，到中国社会科学院外文研究所当所长。刘白羽接任作协党组书记，严文井担任党组副书记。两人经常在党组会上争得面红耳赤，互相拍桌子。</p>\n<p>　　两年后，“文革”开始，作协被砸烂，东总布22号院也被北京市的机关接管。冰心、臧克家、张天翼、张光年、陈白尘、李季等人被关进牛棚，经常被送到13号院劳动，运家具、搞卫生。</p>\n<p>　　邵荃麟被隔离，香烟盒被没收。烟瘾上来时，他就躺在床上，双目紧闭，眉头紧皱，右手食指和中指作夹烟状，“吸吮”一口，接着又作弹烟状。</p>\n<p>　　1966年，林绍纲有一次去台基厂的对外友协，碰见正在办公楼一层打扫厕所的杨朔。他双颊微红，面露尴尬地对林绍纲说：“他们说我里通外国，真是无中生有。”第二年，林绍纲又在一个外事场合碰到了杨朔，知道他被解放允许参加一些活动，彼此都很高兴。</p>\n<p>　　没想到，这是他们的最后一面。1968年夏天，在“清理阶级队伍”中，作协办公的中国文联大楼门前的墙上贴出大字报：“打倒反革命修正主义分子杨朔。”两天后，林绍纲听说杨朔自杀身亡。</p>\n<p><strong>　　老作家远去</strong></p>\n<p>　　1969年国庆节前，作协全体180余人下放湖北咸宁文化部干校。此前调离作协的陈白尘、郭小川等人也被带回接受批判劳改。作协被编为第五连，作协党组成员李季担任连长。</p>\n<p>　　干校宿舍经常变动，林绍纲和郭小川曾同住一个房间。郭小川年过半百，神经衰弱，有肝病，每晚临睡前都要吞一大把红的黄的药片，然后点一支烟，钻进被窝。林绍纲经常听到他用手拍打被子、弹烟灰，嘴里念叨：“糟了，又烧了个窟窿。”</p>\n<p>　　有两三次，林绍纲在夜里醒来，看见郭小川坐在床边的小板凳上，趴在床沿，头钻进被子一角，左手拿着手电筒，右手写东西。</p>\n<p>　　陈白尘白天湖中放鸭，晚上守夜值班，一听到狗叫响动，就喊一句：“有守夜人在此，休得无礼！”</p>\n<p>　　林绍纲跟着收音机自学后负责教唱“革命样板戏”唱段，教的学的都无奈地对付，久患肺气肿的冯牧却是行家，有时会指出他教唱的不足。</p>\n<p>　　清查“五一六分子”时，55岁的严文井夜里偷偷把一块熟的狗肉和馒头塞给挨整的阎纲，阎纲感动得直流眼泪。</p>\n<p>　　侯金镜被安排在蔬菜班。这在干校本算轻活，但1971年夏天干旱，需每天浇地。40多度的气温下，患有高血压的侯金镜连挑了20多天的水，身子又黑又瘦，不久后突发脑溢血去世，年仅51岁，是唯一一个在咸宁干校殉职的作家。</p>\n<p>　　1974年12月，咸宁干校撤销，人员被安置到图书馆、博物馆等单位。1976年10月，“四人帮”被粉碎。</p>\n<p>　　但很多人没等到那天。赵树理1970年被迫害致死，邵荃麟1971年病死狱中。郭小川听闻了“四人帮”被粉碎的消息，却没来得及欢庆胜利。</p>\n<p>　　1976年10月17日晚，欢欣鼓舞的郭小川临睡前习惯性地点燃了香烟，却在烟头未熄的时候睡着，烟蒂点燃了棉被。57岁的他葬身火海，熏黑的手表上，指针停在了8点20分。</p>\n<p>　　1978年，经中央批示，中国文联和中国作协正式恢复。林绍纲被负责筹备的秘书长张僖从北京图书馆要了回来，进入新成立的作协对外联络部。不久后，对外联络部设欧美和亚非分部，林绍纲分管亚非部。</p>\n<p>　　应日中文化交流协会的邀请，中国作家代表团1979年5月访问了日本，周扬为团长。林绍纲第一次得以随团出访。</p>\n<p>　　这是他首次和周扬近距离接触。每次吃中餐，周扬的夫人苏灵扬都要亲自给他布菜，嘴里念叨着，这个能吃，那个不能吃；肉丝炒韭菜不能吃，吃了会拉肚子。</p>\n<p>　　时任作协副主席、党组副书记冯牧也在代表团中。这也是他第一次出国访问。此前，他一直被视作右倾，且家庭社会关系复杂。这次出访，被认为是一种平反。</p>\n<p>　　“文革”中受到批判的刘白羽1978年获平反，回到军队担任了总政文化部部长。80年代初，作协的一些外事活动也常邀请他参加。他变得很谦虚，不见了过去居高临下的官气，平易近人了很多，林绍纲和他相处得很融洽。</p>\n<p>　　叶君健仍然活跃在外事领域，出访了一百多个国家和地区。首都机场的员工很多都认识他了，因为经常看到这位白发老人出入。</p>\n<p>　　1991年，林绍纲退休。那时，在世的“文革”前的老作家已经屈指可数了。</p>\n<p>　　现在，90岁的林绍纲很少出门了。过去几年，每次经过东总布胡同22号，他都很想进去看一看，但大门紧锁，一直未能如愿。</p>\n<p>　　(《中国新闻周刊》2018年第2期)</p>\n<p>　　声明：刊用《中国新闻周刊》稿件务经书面授权</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1164,'2018-01-06 10:18:00','2018-01-06 14:47:13','75',0),(3082,'男子强奸女孩不成残忍杀人 终审维持死缓判决','高辰','羊城晚报','<p><strong>　　强奸女孩不成残忍杀人</strong></p>\n<p><strong>　　余辉案终审维持死缓判决</strong></p>\n<p>　　羊城晚报讯 记者董柳，通讯员蔡海武、傅惟惟报道：1月5日，广东省高级人民法院对上诉人余辉故意杀人案作出终审裁定，维持一审法院对上诉人余辉的死缓判决。</p>\n<p>　　法院经审理查明，2012年5月31日，余辉打昏上学途中的李某某，欲实施强奸，后李某某恢复清醒并反抗，余辉采用暴力方式杀害了李某某。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　法院认为，余辉犯罪动机卑劣，杀害对象是未成年人，主观恶性深，犯罪手段残忍，后果特别严重。</p>\n<p>　　2015年10月28日，韶关市中级人民法院一审以故意杀人罪判处余辉死缓。一审宣判后，余辉不服，提出上诉。二审法院依法组成合议庭，对本案进行了公开开庭审理。</p>\n<p>　　二审法院认为，原审判决认定事实清楚，证据确实、充分，定罪准确，审判程序合法，量刑适当，遂作出上述裁定。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1433,'2018-01-06 13:50:00','2018-01-06 14:47:13','65',0),(3083,'银监会规范商业银行委托贷款管理 防止资金脱实向虚','罗攀','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>1月6日电 据中国银行业监督管理委员会网站消息，近日，《商业银行委托贷款管理办法》制定出台，银监会有关部门负责人表示，《办法》要求委托贷款资金用途应符合国家宏观调控和产业政策，有利于促进业务健康发展，防止资金脱实向虚，从而更好地发挥服务实体经济的作用。</p>\n<div style=\"text-align:center\"> \n <img alt=\"12月8日，山西太原，银行工作人员清点货币。当日，来自中国外汇交易中心的数据显示，人民币对美元汇率中间价报6.6218，较前一交易日下行23个基点，至此人民币中间价“十连跌”。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 张云 摄\" src=\"upload/image/865f3207-baa7-457f-8964-332703772857.jpg\" title=\"12月8日，山西太原，银行工作人员清点货币。当日，来自中国外汇交易中心的数据显示，人民币对美元汇率中间价报6.6218，较前一交易日下行23个基点，至此人民币中间价“十连跌”。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 张云 摄\"> \n <input id=\"5ed3d06c692b4d0885a708c5efa462bd\" name=\"5ed3d06c692b4d0885a708c5efa462bd\" type=\"hidden\" value=\"http://www.chinanews.com/part/561/2017/1208/4856.html\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：银行工作人员清点货币。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 张云 摄 \n</div>\n<p>　　该负责人指出，近年来，商业银行委托贷款业务发展较快，对服务实体经济发展发挥了积极作用，但由于缺乏统一的制度规范，也存在一定风险隐患。对此，银监会高度重视，按照回归本源、规范发展的总体思路，制定了《办法》。</p>\n<p>　　该负责人介绍称，《办法》共33条，主要包括五方面内容：一是明确委托贷款的业务定位和各方当事人职责。《办法》明确委托贷款业务是商业银行的委托代理业务，商业银行作为受托人，按照权责利匹配原则提供服务，委托人承担委托贷款的信用风险。二是规范委托贷款的资金来源。《办法》要求商业银行对委托资金来源合法性进行必要的审查，且明确了不得用于发放委托贷款的资金类型。三是规范委托贷款的资金用途。《办法》体现了金融服务实体经济的要求，明确委托资金用途应符合有关规定，并对资金用途进行了限定。四是要求商业银行加强委托贷款风险管理。《办法》要求商业银行将委托贷款业务与自营业务严格区分，加强风险隔离和业务管理。五是加强委托贷款业务的监管。《办法》明确，银监会或其派出机构将对违规办理委托贷款业务的商业银行依法采取相应监管措施或实施处罚。</p>\n<p>　　针对《办法》制定目的和作用，该负责人指出，一是弥补监管短板。目前没有专门制度对委托贷款业务进行全面、系统的规范，《办法》出台填补了委托贷款监管制度空白，为商业银行办理委托贷款业务提供了制度依据。二是加强风险管理。《办法》要求商业银行完善委托贷款业务内部管理制度和流程，严格风险控制措施，不得超越受托人职责开展业务，同时强化了相关监管要求。三是服务实体经济。《办法》要求委托贷款资金用途应符合国家宏观调控和产业政策，有利于促进业务健康发展，防止资金脱实向虚，从而更好地发挥服务实体经济的作用。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/865f3207-baa7-457f-8964-332703772857.jpg',NULL,460,'2018-01-06 13:21:00','2018-01-06 14:47:13','67',0),(3084,'5女偷10吨蒜头装4辆汽车倒卖 6年间均落网获刑','高辰','海南特区报','<p><strong>　　女子伙同他人偷走10吨蒜头</strong></p>\n<p><strong>　　用货车从海口运到儋州卖得4.8万元；在同伙相继落网后，怀有身孕的她向警方自首，被判缓刑</strong></p>\n<p>　　记者 吴兴 通讯员 毛雨佳 蔡莉</p>\n<p>　　本报讯 6年前，儋州女子王某与陈某等4名女性好友合伙盗窃海口某仓库内的10吨蒜头，这些蒜头装了4面包车，随后用大货车从海口运到儋州售卖，非法获利4.8万元。6年间，王某的同伙陆续落网被判刑。2017年6月26日，怀有身孕的王某到派出所投案自首。近日，海口市龙华区人民法院一审以盗窃罪判处王某有期徒刑二年五个月，缓刑三年，并处罚金人民币5000元。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　2011年2月中旬，王某和陈某女(已判刑)、黎某英(已判刑)及小美、陈某香(均另案处理)在海口市区捡破烂途经海口一仓库时，发现该仓库存放着大量蒜头，遂起盗窃之念，5人商议伺机行窃。</p>\n<p>　　同年2月16日凌晨2时许，5人伙同王某政(已判刑)、王某勤(已判刑)、王某业来到仓库处，王某业、王某勤在路口望风，5名女子打开一层仓库大门并将蒜头搬到王某政驾驶的面包车内，后王某政驾驶面包车将蒜头转运给由某(另案处理)驾驶的货车上。当天快亮时，几人来回4次共计从仓库运了4面包车蒜头到由某驾驶的货车上。随后，王某政、王某勤驾车载着5名女子逃离，由某驾驶货车将蒜头运到儋州市。</p>\n<p>　　几天后，5名女子和由某来到儋州市销售偷来的蒜头，共售得人民币4.8万元。5名女子和由某每人分得赃款人民币6000元，王某政、王某业、王某勤每人分得赃款人民币4000元。</p>\n<p>　　6年间，陈某女、黎某英、小美、陈某香、王某政、王某勤及由某相继被判处刑罚。2017年6月26日，怀有身孕的王某到派出所投案自首，2017年10月11日，王某将分得的赃款人民币6000元退还被害人。</p>\n<p>　　法院经审理认为，被告人王某无视国法，以非法占有为目的，结伙采取秘密手段窃取他人财物，销售后得款人民币48000元，盗窃数额巨大，其行为已构成盗窃罪，应予惩处。被告人王某犯罪后自动投案，如实供述自己的罪行，是自首，依法可减轻处罚；鉴于其主动退还赃款给被害人，可酌情从轻处罚。考虑到被告人王某犯罪情节较轻，具有悔罪表现，没有再犯罪危险，宣告缓刑对所居住社区没有不良影响，对其可宣告缓刑。龙华区法院根据被告人王某犯罪的事实、性质、情节及其行为对社会的危害程度，依法作出上述判决。</p>\n<p>　　一审宣判后，王某当庭表示服判，不上诉。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,83,'2018-01-06 13:40:00','2018-01-06 14:47:13','65',0),(3085,'中国物流与采购联合会: 2017年全球经济良好收官','史建磊','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>北京1月6日电 (记者 刘长忠)中国物流与采购联合会6日发布的全球制造业采购经理指数(CFLP-GPMI)显示，2017年12月份全球制造业PMI为56.3%，环比上升0.5个百分点，连续两个月环比上升，持续五个月在55%以上较高水平，创出年内新高。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　分析认为，2017年1至12月，全球制造业PMI均值达54.7%的较高水平。综合来看，2017年全球经济持续稳步复苏的力度有所增强，实现良好收官。</p>\n<p>　　据中国物流信息中心专家组分析，2017年12月(CFLP-GPMI)呈现：亚洲制造业增速稳步加快，指数连续小幅上升；欧洲制造业延续稳步复苏趋势，指数高位连升；美洲制造业增速加快，指数高位回升；大洋洲制造业增速放缓，指数回落；非洲制造业增速加快，指数升幅明显特点。</p>\n<p>　　2017年12月份，亚洲制造业PMI为52.6%，较上月上升0.1个百分点，创年内新高，连续两个月稳定在52%以上，高于2017年全年51.9%的平均水平，表明亚洲制造业增速呈现稳步加快的发展趋势。从亚洲主要经济体的增速来看，亚洲仍是全球经济复苏的主要动力，增速高于全球水平。</p>\n<p>　　从主要国家变化看，日本和印度制造业活动近两个月出现明显加快趋势，指数升幅扩大。沙特和阿联酋等石油国家保持良好发展趋势，指数保持在57%左右的高位。中国的制造业活动虽小幅回落，但稳定在51.6%。2018年，中国经济的持续稳定向好发展仍是推动亚洲经济稳步加快的主要动力。</p>\n<p>　　2017年12月份，欧洲制造业PMI为58.7%，较上月上升0.1个百分点，连续五个月环比上升，创出年内新高，指数高于2017年全年56.4%的平均水平，表明欧洲制造业有望延续稳步复苏趋势。</p>\n<p>　　在主要国家中，德国和法国等核心国家实现稳健复苏，制造业实现持续加快增长态势，PMI在较高水平上实现连升走势，12月均创年内新高。预期2018年欧洲经济有望保持稳步复苏趋势。</p>\n<p>　　2017年12月份，美洲制造业PMI为58.4%，较上月上升1.1个百分点，高于2017年全年56.5%的平均水平，显示美洲制造业增速在上个月有所放缓后，重回加快上升趋势。从制造业指数变化看，2018年美洲经济有望延续复苏趋势。</p>\n<p>　　在主要国家中，巴西和墨西哥等新兴经济体的制造业增速有所放缓，但指数水平仍保持在51%以上，表明这些国家经济保持温和增长。美国和加拿大等发达经济体的制造业保持活跃发展趋势。特别是美国制造业强劲复苏趋势有望延续。</p>\n<p>　　专家称，2018年全球经济有望延续稳步复苏势头，各区域经济体发展势头依然良好。基于全球经济活动的良好发展态势，国际货币基金组织、世界银行和经合组织三大机构均看好2018年全球经济增长。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1481,'2018-01-06 13:49:00','2018-01-06 14:47:13','66',0),(3086,'瞒报数据泄露信息 河南47家违规金融机构被罚716万元','高辰','新华网','<p><strong>　　瞒报数据泄露信息 河南47家违规金融机构被罚716万元</strong></p>\n<p>　　新华社郑州1月5日电(记者李鹏)近日，中国人民银行郑州中心支行向社会公布的行政处罚信息显示，自2017年11月6日至12月底不到两个月的时间内，因涉及瞒报虚报数据、过失泄露信息等违规行为，河南辖内47家金融机构受到行政处罚，罚款金额达716万元。</p>\n<p>　　被处罚的违规机构涵盖各类金融机构，既包括四大国有银行，民生、光大、广发、中信、浦发等5家全国股份制银行，也包括21家地方农商行和农信社、7家村镇银行以及中原银行、平顶山银行等2家城商行；同时还包括6家保险公司、1家资产管理公司和1家涉及金融服务的网络科技公司，共计47家。</p>\n<p>　　处罚信息还显示，这些金融机构的违规行为主要包括虚报瞒报漏报金融数据、过失泄露信息、未按规定履行客户身份识别义务、未按规定报送大额交易报告或者可疑交易报告、内控制度不完善、不按规定收兑、挑剔残缺污损人民币等。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　其中，虚报瞒报漏报金融数据的违规行为高达数十起；多家银行不仅不按规定收兑、挑剔残缺污损人民币，甚至对外支付不宜流通的货币；还有银行机构不仅过失泄露信息，而且未经授权便查询个人信用信息，甚至违法出售个人信息。</p>\n<p>　　针对上述违规行为，2017年11月6日至12月底，人行共向47家金融单位及其分支机构开出了77张、716万元的罚单。其中，工商银行河南省分行辖内分支机构以9张罚单位居罚单数量榜首；而河南范县农村商业银行及其相关负责人则被罚款最多，“双罚”金额共计66万元。</p>\n<p>　　同时，在除银行外的违规机构中，福建国通星驿网络科技有限公司河南分公司、中国信达资产管理股份有限公司河南省分公司、中国人寿保险股份有限公司河南省分公司三家机构的“双罚”金额也都超过了50万元。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,35,'2017-12-31 14:36:00','2018-01-06 14:47:13','67',0),(3087,'资管产品征税政策落地 行业积极“备战”应对忙','高辰','新华网','<p>　　新华社北京1月5日电 <strong>题：资管产品征税政策落地 行业积极“备战”应对忙</strong></p>\n<p>　　新华社记者 王都鹏、刘慧</p>\n<p>　　进入2018年，资管行业征收增值税政策正式落地实施。</p>\n<p>　　为规模已经超过百万亿元的资管行业设计一套统一的征税办法并非易事。此前，主管部门多次下发征税补充文件，力求政策平稳落地；行业内的各部门和机构也不断做出调整，以求契合征税具体要求。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　元旦前夕，财政部、国家税务总局下发“租入固定资产进项税额抵扣等增值税政策的通知”，进一步明确了部分资管产品在金融产品转让时的涉税问题。随后，为保证行业估值工作的顺利进行，基金业协会也下发证券投资基金增值税核算估值参考意见及释义。</p>\n<p>　　为积极应对征税，资管行业的不同纳税主体也进行了多项准备。国内某大型公募基金负责人说，从2017年三季度起，该公司就开始为产品征收增值税进行一系列前期准备，包括产品计税分析、内部宣讲培训、委托人沟通等。</p>\n<p>　　在临近税收政策正式实施的前两个月，该公募基金的准备工作聚焦在升级改造估值核算系统、梳理产品各项投资标的计税参数、测算计征增值税对产品投资回报的影响、发送书面投资人通知函件等具体操作性事项上。</p>\n<p>　　私募基金行业同样如此。由于征税将影响资管产品净值，部分私募基金在征税前夕已发布公告，积极和投资者进行沟通，告知对缴纳增值税的具体安排，资管产品净值将会发生哪些变化。</p>\n<p>　　可以看出，各部门和机构的积极准备，已推动资管行业征收增值税迈出“第一步”。但从另一个角度来看，资管征税覆盖范围较广，涉及多个金融子行业，不同类别的金融机构所面对的情况千差万别，所需调整不尽相同。</p>\n<p>　　有业内人士认为，私募证券投资基金受到的征税影响，可能会大于银行、信托、券商等其他资管产品。而从目前对征税的准备情况来看，由于征税环节尚存“模糊”地带，令私募证券基金的处境有些“尴尬”。</p>\n<p>　　记者了解到，公募基金买卖股票、债券的收入免征增值税，但私募基金同类型买卖是否也被纳入免征范围，征税文件并未明确。金融机构间的资金往来业务免收增值税，但私募基金是否算金融机构，也存争议。</p>\n<p>　　事实上，基金行业的积极应对，是整个资管行业应对增值税缴纳的缩影。资管产品投资过程中应缴税而未缴税的情况预计会大幅改观。但在新的征税办法下，资管机构存在较多需自行判断缴税与否的情况，需要后续进一步出台细则指引。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1106,'2018-01-05 15:27:00','2018-01-06 14:47:13','67',0),(3088,'新能源车购置焦虑在 创新依然不能停','左盛丹','羊城晚报','<p>　　<strong>新能源车购置焦虑在，创新依然不能停</strong></p>\n<p>　　2017年12月末，国家四部门发出的通知明确，自2018年1月1日至2020年12月31日，我国将对购置的新能源汽车免征车辆购置税。新政密集出台，无疑将深刻影响新能源汽车行业发展。但是企业能否通过技术创新，解决消费者顾虑，则是政策成败的关键。</p>\n<p>　　业内预计，2018年新能源汽车产销量有望超过100万辆，到2020年，新能源汽车年产销达到200万辆。新能源汽车大卖，“限牌”政策居功至伟。麦肯锡中国发布的《2017中国汽车消费者调查报告》显示，“限牌”城市买下了全国60%的新能源汽车。这意味着消费者为了求得一个车牌，被引导到买新能源的道路上。这种消费者行为很难说都是人们积极主动的选择结果，但在社会示范上却是释放正能量的。</p>\n<p>　　尽管公众的接受度近年来有所提升，但新能源车续航快速缩水、电动车充电难和小毛病频发等短板和痛点仍未得到有效解决。消费者对国内新能源汽车、尤其是纯电动汽车仍有明显顾虑。尤其是对新加入新能源行列、原本没有成熟技术储备的国产车型来说，让客户选择的过程，本身就是充满风险。至于购买进口新能源车型，产品虽然优秀，却几乎不能获得政策鼓励，这无形中又是在保护一批相对落后的生产厂家。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　比如目前国产纯电动汽车续航里程普遍低于400公里，购车成本和同级别燃油车相比并不会低，甚至还更高。至于充电桩缺乏、充电时间长的问题更是普遍。如果不是因为车牌驱动，可能很多几乎是山寨的车型根本就不会有人问津。至于电池安全性问题，同样让公众焦虑。比如燃油车有碰撞标准，但电动车往往没有经过类似的考验，极端情况下，车辆损毁程度如何，缺乏数据支持。</p>\n<p>　　即使是新能源中的混合动力车型，同样存在电池短板的问题。因此在二手市场上，这些车型同样不好卖。相比于汽油发动机可预期的寿命周期，二手买家无法估计电池的衰减速度。更重要的是，新能源车型要跟着厂家的体系进行维护，车辆的修复和再利用除了厂家以外，没有第三方能做到。这自然也限制了许多二手车的需求。将来二手卖不出去，今天的一手需求自然就会被抑制。</p>\n<p>　　这些年，国家已经出台了许多政策引导新能源汽车行业健康发展，类似购车补贴、免征购置税及降低首付比例等，确实和消费者直接相关。还有更多的产业政策，也将推动汽车厂商生产更多高品质产品，间接带动消费升级。和燃油车相同的是，最终产品力更强的品牌才可能存活下来。所以企业需要持续创新、不断迭代，在国际的前沿研发中找出更优秀的替代品。至于单纯依赖政策生存，只会逆向开发、被动模仿的厂家，虽然拥有一波兴盛的时光，难免还是会遭遇市场淘汰。只有真正能解决消费者焦虑的企业，才能和新能源时代一起走得更远。</p>\n<p>　　耀琪</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1433,'2018-01-04 16:32:00','2018-01-06 14:47:13','72',0),(3089,'香港新任律政司司长郑若骅：运用法律原则 维护香港法治','魏巍','新华网','<p><strong>　　香港特区政府新任律政司司长郑若骅：运用法律原则 维护香港法治</strong></p>\n<p>　　新华社香港1月5日电(记者 周雪婷)依照香港特别行政区基本法的有关规定，根据香港特别行政区行政长官林郑月娥的提名和建议，国务院决定：任命郑若骅为律政司司长，免去袁国强的律政司司长职务，自2018年1月6日起生效。郑若骅5日表示，律政司司长的主要使命是维护香港法治，她衷心感谢行政长官及中央政府信任，未来她仍会坚持运用法律原则，以专业知识和经验贡献香港社会。</p>\n<p>　　林郑月娥和郑若骅、袁国强5日上午会见传媒。林郑月娥表示，郑若骅多年来积极参与公共服务，熟悉政府事务，深受公务员敬重。她很有信心郑若骅能胜任律政司司长的职务。</p>\n<p>　　郑若骅对获委任为香港特区政府律政司司长感到荣幸。她表示，律政司司长是行政长官和政府的首席法律顾问，主要使命是维护香港法治。她指出，香港已回归祖国二十年。“一国两制”对香港是一个最适合和有利的安排。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　郑若骅说，过往多年她在香港不同范畴担任公职，能够在新岗位继续为香港服务，对她来说既是一份荣誉，也是一个挑战。郑若骅表示，她将会用一向的信心和信念，尽她所能，以专业知识和经验贡献香港社会。她也感谢袁国强多年来为香港尽心尽力贡献良多。</p>\n<p>　　郑若骅现年59岁。她在加入政府前是私人执业资深大律师，具特许工程师、特许仲裁员及专业调解员资格。郑若骅是亚洲国际法律研究院创始成员及前任主席、国际商事仲裁会前任副主席、国际商会国际仲裁院前任副主席及香港国际仲裁中心前任主席。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1426,'2018-01-05 20:47:00','2018-01-06 14:47:13','69',0),(3090,'福建漳州市政协倡设“漳州人过台湾纪念馆”','孔庆玲','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>福建漳州1月5日电 (记者 张金川)福建漳州市政协台港澳侨联络委员会5日提出，在新建成的漳州博物馆设立“漳州人过台湾纪念馆”。</p>\n<p>　　福建省漳州市政协十三届二次会议5日开幕，台盟漳州市委员会副主委许钢代表漳州市政协台港澳侨联络委员会作大会发言时提出上述建议。</p>\n<p>　　据漳州市政协台港澳侨联络委员会介绍，漳州历史上对台湾开发、建设、治理、发展贡献极大，涉台人物众多，涉台资源丰富，具体表现在五个方面：</p>\n<p>　　一是漳州人是移民台湾的先驱者和主力军。漳州人移民开发台湾萌于隋唐，始于宋元，盛于明清。台湾现有2300万人口中，祖籍漳州的近900万人，占近35.8%，跃居台胞籍别人口之首位。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　二是漳州人开发台湾成效卓著。明天启年间，龙海人颜思齐组织闽南漳泉籍三千多恳民入台垦殖，这是第一次由大陆民间组织的大规模移民垦殖；平和人林石开发台中、角美人林平侯开发台北盆地，形成“台中雾峰林家”和“台北板桥林家”，是台湾五大望族之两族；漳浦人吴沙、陈辉煌先后开发出宜兰北部和南部，分别被誉为“开兰始祖”和“开兰元勋”。</p>\n<p>　　三是漳州人治理台湾贡献突出。漳浦人蓝鼎元、蓝理、蓝廷珍等先后提出许多有效治理台湾的政策措施，史称“漳浦蓝氏三杰”。漳浦人黄性震提出《平台十策》，为统一大业做出重要贡献，被誉为“平台策士”。2005年开启两岸“破冰之旅”的连战，2008年以来开启8次“陈江会”的江炳坤等台湾知名人士，祖籍都在漳州。</p>\n<p>　　四是漳州人对台湾文化教育及宗教艺术影响深远。角美人陈永华把儒家学说传入台湾，被誉为“台湾文化教育奠基人”；诏安人谢颖苏率先到台湾传播国画技艺，被誉为“台湾美术开山祖师”。漳州涉台文物共有306处，占福建省的20%，在大陆地级市中排名第一。</p>\n<p>　　五是漳州人见证两岸同胞骨肉相连作用独特。漳州人移民台湾，多以姓氏聚族而居，或是以同县同乡聚居一处，建立“血缘聚落”和“同乡聚落”。在台湾，“小漳州”聚落遍布全岛，如台北的芝山岩，台南的漳州里，云林的平和里等。</p>\n<p>　　许钢认为，设立“漳州人过台湾纪念馆”有利于发挥漳州对台“五缘”优势，有利于增强漳台两地经济社会文化交流，有利于增强祖籍漳州的近千万台湾同胞特别是台湾新生代对漳州祖地的认同感和向心力。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,291,'2018-01-05 21:16:00','2018-01-06 14:47:13','70',0),(3091,'厦门拟全力打造对台交流合作战略支点','高辰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>厦门1月6日电 (记者 杨伏山)厦门市市长庄稼汉6日称，2018年将“坚定不移深化融合发展，全力打造对台交流合作战略支点”。</p>\n<p>　　厦门市十五届人大二次会议当天开幕，庄稼汉会上作政府工作报告。这是今年该市拟重点做好的七个方面工作之一。</p>\n<p>　　庄稼汉表示，今年是十九大精神开局之年、改革开放40周年、决胜全面建成小康社会、实施“十三五”规划承上启下的关键一年，该市将努力为全国全省大局做出新贡献。</p>\n<p>　　在谈到今年该市将重点做好七个方面工作之一的对台工作时，庄稼汉说，厦门将更好发挥对台工作综合优势，为促进两岸关系和平发展作出更大贡献。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　他表示，将提升经贸合作实效，加快建设“一区三中心”，鼓励台资银行、证券、保险等金融机构来厦设立总部和资金营运中心；争取率先在自贸区落实海峡两岸服贸协定若干对台开放措施，争取赴金门旅游便利和政策，打造厦金融合发展示范区。</p>\n<p>　　庄稼汉还表示，厦门将加强厦台两地人力资源合作，打造一批国家级海峡两岸青创基地、对台研学旅行基地，吸引更多台湾青年来厦就业创业；同时，积极研究推动在厦台胞享受市民待遇、台企享受陆企待遇，增进同胞福祉，促进心灵契合。</p>\n<p>　　庄稼汉在会上提及今年该市将重点做好的七个方面工作，还包括全力建设高素质创新创业之城、全力打造高颜值生态花园之城、全力扩大有效投资、全力增强发展活力动力以及全力提升市民获得感幸福感、全力维护社会和谐稳定等工作。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,257,'2018-01-06 11:29:00','2018-01-06 14:47:13','70',0),(3092,'日媒：朝鲜官员称朝方“可能参加”平昌冬奥','史建磊','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>1月6日电 据日本共同社消息，6日，国际奥委会朝鲜籍委员张雄(Chang Ung)表示，朝鲜“可能参加”将于2月份在韩国平昌举办的冬奥会。</p>\n<p>　　报道称，张雄是在由平壤飞抵北京后，作出以上表态的。日媒援引熟悉情况的消息来源称，他可能将前往瑞士，与国际奥委会商议朝鲜参与平昌冬奥会的可能性。</p>\n<p>　　此前，韩国统一部发言人白泰铉表示，朝鲜接受了9日在韩朝边界板门店“和平之家”举行会谈的提议，双方的议题将是包括平昌冬奥会相关事宜在内的韩朝关系改善问题。</p>\n<p>　　今年1月1日，朝鲜劳动党委员长金正恩通过朝鲜中央电视台发表2018年新年贺词，他表示，祝愿平昌冬奥会圆满成功，朝鲜有意采取包括派团参奥的措施，并愿与韩方就此尽快举行会谈。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　对此，韩国总统文在寅2日在青瓦台主持召开国务会议时予以积极评价。文在寅指出，这是朝鲜对韩方“让平昌冬奥成为改善韩朝关系的转机”提议的响应。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1291,'2018-01-06 12:57:00','2018-01-06 14:47:13','71',0),(3093,'围棋在变局之中寻妙手：协会实体化 运作市场化','王硕','人民日报','<p><strong>　　协会实体化 运作市场化 竞技多样化</strong></p>\n<p><strong>　　围棋 变局之中寻妙手</strong></p>\n<p>　　1月1日，世界围棋等级分排名更新了最新一期榜单，韩国棋手朴廷桓以3640分高居首位，中国棋手柯洁以9分之差紧随其后。一个月之前的围甲收官战，柯洁负于江维杰，朴廷桓击败范蕴若，双方“隔空争锋”后，延续了38个月之久的世界围棋“第一人”之位终于易主。此后两人交替领先，可以预见未来的榜首之争将呈现拉锯之势。</p>\n<p>　　中、韩两位领军棋手的排位较量，可谓2017年围棋世界风云骤变的鲜活注脚。这种变化，不局限于竞技格局的调整，抑或新陈代谢的加速，而是源自项目定位、发展路径、改革思路、市场布局等变化推出的一系列革新举措。面对机遇与挑战，领跑的中国围棋站在了再出发的新起点。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　棋坛“三国杀”更激烈</p>\n<p>　　2017中国十佳劳伦斯冠军奖颁奖盛典上，中国围棋队总教练俞斌代替正在新奥杯鏖战的柯洁，上台领取了最佳非奥运动员奖。这也是围棋项目首次获得此项殊荣。几天后，柯洁以3∶2战胜彭立尧，捧起了个人职业生涯第五座世界冠军奖杯。中国棋手之中，除了“八冠王”古力，无人有此傲人战绩。</p>\n<p>　　对于20岁的柯洁而言，早早攀登到顶峰后，却在过去一年体味到“过山车”般的滋味。“人机大战”后惊人的22连胜，一度让棋迷感慨柯洁已然“人间无敌”，但下半年他状态起伏不定，甚至出现连败，在唯一闯入的世界大战决赛中，也险些被对手翻盘。不过，在业内看来，少年王者要追赶前辈大棋士的脚步，这是必须经历的磨练。</p>\n<p>　　中国围棋的高光时刻，从来不在于一枝独秀，而在于百花齐放。2017年新增的3位世界冠军都是“90后”：LG杯，23岁的党毅飞从预选赛一路杀入决赛夺冠，捅破了多年的“窗户纸”；春兰杯，24岁的檀啸决赛战胜韩国名将朴永训，一洗国际大赛难迈过八强门槛的尴尬；三星杯，19岁的辜梓豪上演大逆转，成为目前最年轻的世界冠军。此外，连笑加冕天元、名人两大头衔，新星谢尔豪闯入新一届LG杯决赛……这无疑是中国围棋人才厚度的进一步彰显。</p>\n<p>　　尽管表面看来中国围棋的统治地位越发牢固，但依然不能掉以轻心。从韩国棋手提前会师梦百合杯决赛，到世界“第一人”之位易主，中、韩棋手对决远远没到轻易分出胜负的时候。世界等级分前10名中，中国棋手只比韩国棋手多一人。农心杯三国围棋擂台赛，若不是党毅飞力挽狂澜，以三连胜稳住阵脚，中国队或许早与日本队一样只剩“光杆司令”。而日本棋手11年后重回世界大赛决赛，也预示着今后棋坛“三国杀”局势将愈发激烈。</p>\n<p>　　人工智能全面抢滩</p>\n<p>　　“我曾说不再跟人工智能下棋，现在食言了。”去年年底，柯洁宣布将在今年4月的首届“吴清源杯”世界女子围棋赛期间举行的2018世界人工智能围棋大赛上，与“天壤”展开较量。值得一提的是，这是人机大战历史上首次使用“机械臂”来落子，科幻电影出现的一幕终于成真。</p>\n<p>　　在柯洁看来，下棋是不断挑战自我的过程，他很享受挑战更高对手的感觉。这种“享受”或许代表了棋界对人工智能的心态变化：从忐忑到坦然，从自卑到自强，从疏离到学习，人工智能与围棋从此进入亲密接触的新时代。</p>\n<p>　　去年5月的乌镇围棋峰会上，柯洁以0∶3负于“阿尔法围棋”，陈耀烨、周睿羊等5位世界冠军以“相谈棋”的方式征战团体赛告负，共同宣告人工智能围棋完全超越人类棋手。10月下旬，谷歌下属公司DeepMind在《自然》发表论文，称其研发的新版“阿尔法围棋”已完全不需要人类棋谱，通过自我对弈训练，它已成为目前最强的围棋程序。而在年底，“阿尔法围棋”教学工具上线，供全球爱好者免费学习。</p>\n<p>　　人工智能带给围棋的，除了压倒性的胜率，还有技术与理念的革新、学习之风的兴起，重新点燃了人类突破思维局限的一盏明灯。人机大战后，“阿尔法围棋”展现的新着法、新思路，被不断运用于人类对局中，各种人工智能围棋大赛办得风生水起。如果说，30年前的中日围棋擂台赛掀起了第一波“围棋热”，而今人工智能打破了地域、国界、竞技的界限，让古老的围棋迎来新的推广黄金期。</p>\n<p>　　围棋改革正式启动</p>\n<p>　　日前，中国围棋协会换届会议召开，选举林建超为新一任主席，罗超毅、聂卫平、孙光明、常昊、雷翔任副主席，罗超毅兼任秘书长。林建超表示，中国围棋改革将正式启动，以协会实体化、运作市场化、竞技多样化为核心内容，让中国围棋更上层楼。</p>\n<p>　　其实，改革早在去年就已经落子，初步展现了“大模样”。第十三届全运会，围棋作为19个群众项目之一，24年之后重回全运大家庭，不像从前一样只设立专业组，而是开始向棋迷开放。尽管最终跻身决赛的只有94名运动员，但整个海选过程辐射全国，分区预选赛进行了227场比赛，参赛总人数超过4.5万人。据业内人士估算，以这种参与态势，下届全运会参赛人数或将增长10倍。</p>\n<p>　　让民间深厚的围棋群众基础，变成赛事、产业、项目并辔而行的动力，为广大爱好者服务，既是推动全民健身国家战略落实的重要布局，也为围棋可持续发展注入新的生机。首届中国围棋大会就是“全民围棋”的一次有益尝试，面向普通棋友的啤酒围棋赛、混双亲子赛等创新性赛事，展现了围棋独特的魅力。这场围棋嘉年华吸引了约5500人参与，创造了新纪录。</p>\n<p>　　围棋传承千年、历久弥新，其中蕴含的文化、教育属性，如今在普及与推广中越来越被重视。林建超表示，下一步将推动围棋进校园、进教材，尽快颁布新的围棋等级标准，打通专业与业余。正所谓，变则通，通则久。身处变局之中，因改革而重新焕发活力，何尝不是中国围棋之幸？</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1798,'2018-01-05 09:09:00','2018-01-06 14:47:13','74',0),(3094,'网逃夫妻形迹可疑落网 高科技作弊每月赌赢10万元','高辰','海南特区报','<p><strong>　　用高科技手段作弊 每月赌博赢10万元</strong></p>\n<p><strong>　　夫妻俩涉嫌诈骗三亚落网</strong></p>\n<p>　　记者 沈丽焕</p>\n<p>　　本报讯 1月3日，海口铁路公安处三亚车站派出所执勤民警王东浩带领协警在进站口开展巡查工作时，发现一对夫妻在进站口徘徊，当夫妻俩与民警对视后立即掉头走向别的地方。王东浩觉得这对夫妻形迹可疑，随即上前查问。经过比对身份信息发现，两人均涉嫌诈骗而被警方列为网上在逃人员。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　民警了解到，这对夫妻(湖南省湘潭县人丈夫叫张某，妻子叫李某)涉及现代化科技手段和专业化分工的团伙赌博诈骗案。从2017年3月份起，不务正业的张某和妻子想通过非法手段捞钱，两人从网上搜索到一个作弊博彩的广告，根据广告上的联系电话联系到幕后推手付某。付某是这起案件中负责现代化科技手段和专业化分工的团伙负责人。付某聘请管理人员、键盘手、分析员等专业人员组成打牌作弊团伙。张某和妻子出资向付某购买赌博作弊仪器(监视器、对讲机等)后，付某组织键盘手、分析师利用监控、网络、耳机等工具与张某沟通，张某和妻子赌博赢钱后按金额的10%-30%给付某提成。如此下来，夫妻俩每月通过打牌和打麻将等赌博方式赢约10万元。</p>\n<p>　　目前，嫌疑人张某和李某已被移交湖北省咸宁市公安局处理。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,757,'2018-01-06 13:35:00','2018-01-06 14:47:13','65',0),(3095,'国乒新政将年轻人推向前台 薛飞跻身首批受益者','高辰','长江日报','<p>　　<strong>国乒新政将年轻人推向前台</strong></p>\n<p><strong>　　武汉小将薛飞跻身首批受益者</strong></p>\n<p>　　《中国乒乓球协会2018年参加国际赛事运动员选拔办法(试行)》5日上午公布，最引人瞩目的是代表中国队参加2018年世乒赛和世界杯团体赛的5名球员中，除3名国家队主力，另外两个名额分别给了乒超积分前三名的非国家队成员以及U21排名靠前的年轻选手，这在中国乒乓球队的历史上还是首次。</p>\n<p>　　世界大赛组队原则有大变</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　根据中国乒协2015年7月发布的《国家乒乓球队运动员选拔办法实施细则》，奥运会参赛选手选拔先根据两年内的世锦赛、世界杯、亚锦赛等国际大赛成绩确定主力运动员范围，再根据世界排名、参赛配对等综合因素从主力队员中择优选拔。其他重大国际赛事参赛运动员的选拔主要通过队内积分和直通比赛选拔进行。</p>\n<p>　　5日出台的“国乒新政”对上述选拔办法做出了重大改变，2018年的一类赛事有世锦赛、世界杯团体赛、世界杯单打，除世界杯单打由国际乒联指定参赛资格外，世锦赛和世界杯团体赛中国队都有5个单打名额，“新政”规定，这5人分别为2017年世锦赛单打前三名中的1人、2017年全运会单打前三名中的1人，世锦赛选拔赛或国家队选拔赛单打前三名中的1人，2017―2018赛季乒超联赛积分排名前三名中的非国家队员1人，中国乒协U21积分排名靠前1人。</p>\n<p>　　此外，“国乒新政”把2018年亚洲杯、国际乒联世界巡回赛，以及青少年类的多项国际赛事列为二至四类级别比赛，除国际乒联和亚乒联指定参赛选手的比赛外，其余赛事的组队参赛原则也都有相应的规定。</p>\n<p>　　让年轻人走向前台是最大亮点</p>\n<p>　　在武汉市乒乓球队总教练王农看来，国乒新政的最大亮点是要把年轻人推向前台。王农在国乒新政出台前去北京时，国乒男队代理教练组长吴敬平曾对他说过，国家队不能老是依靠张继科、马龙、许昕等老将打天下，现在要重点培养年轻队员，所以2018年的世锦赛、世界杯以及总数接近20站的国际乒联巡回赛，都要多给年轻选手参赛机会。</p>\n<p>　　“这次出台的新政策果然如此，无论世锦赛还是世界杯，国乒男、女队都有1个名额留给21岁以下的年轻人去竞争。看来国家队已经真正意识到，只有多让年轻选手去重大赛事中磨练，才有利于提高综合竞技水平，促使他们往上走，尽快达到一定的高度。这样的重大改革，对中国乒乓球的发展是有利的。”王农说。</p>\n<p>　　曾获得过2017年世界青年锦标赛男单、男双、混双、男团这4项冠军的武汉18岁小将薛飞，将成为“国乒新政”颁布后首批获得国际赛事锻炼的年轻选手之一，他将参加2018年第一站国际乒联巡回赛。15岁的武汉队员熊梦阳，以及13岁的湖北队员陶育畅等人，也已被指定将参加国际青少年比赛。</p>\n<p>　　参赛人选还得看实际情况确定</p>\n<p>　　“国乒新政”出台的目的，是“为深化改革，进一步调动各方面积极性，促进运动员的培养成长，全力做好东京奥运会备战工作，体现运动员在参加国际赛事选拔工作中公开、公正、透明的原则”。</p>\n<p>　　由于中国乒乓球队人才济济，所以参加奥运会、世锦赛和世界杯这世界三大赛的名额最宝贵。2018年有世锦赛和世界杯团体赛，中国队一般是派5名主力参加单打项目。尽管“新政”对选拔范围作了明确限定，但是每一项具体规定都有一定的讨论空间，比如各项前三名选手以及U21积分排名靠前的选手中，最终谁能获得参赛资格，还是得由教练组提出名单进行集体研究。</p>\n<p>　　有意思的是，“国乒新政”发布当天，女队正在进行非常惨烈的内部选拔赛，陈梦、武杨、木子、王曼昱、陈幸同、孙颖莎、冯亚兰等7人争夺剩余的两个名额，与已经提前确定的丁宁、刘诗雯、朱雨玲组成参赛阵容，准备参加2月22日至25日在伦敦举行的世界杯团体赛。如果按新规则施行的话，U21名额必将在陈幸同、孙颖莎、王曼昱3人中产生，落选的两人再与其他4人一起争夺另一个名额，至于最终的结果如何，很快就会揭晓了。（记者高崇成）</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1697,'2017-12-31 14:36:00','2018-01-06 14:47:13','74',0),(3096,'发改委：到2020年中国智能汽车新车占比将达50%','魏巍','新华网','<p><strong>　　《智能汽车创新发展战略》(征求意见稿)称2020年我国智能汽车新车占比达50%</strong></p>\n<p>　　新华社北京1月5日电(记者 安蓓)国家发展改革委5日公布《智能汽车创新发展战略》(征求意见稿)，提出到2020年我国智能汽车新车占比将达50%。</p>\n<p>　　根据征求意见稿，到2020年，中国标准智能汽车的技术创新、产业生态、路网设施、法规标准、产品监管和信息安全体系框架基本形成。中高级别智能汽车实现市场化应用，重点区域示范运行取得成效。智能道路交通系统建设取得积极进展，大城市、高速公路的车用无线通信网络(LTE-V2X)覆盖率达到90%，北斗高精度时空服务实现全覆盖。</p>\n<p>　　到2025年，中国标准智能汽车的技术创新、产业生态、路网设施、法规标准、产品监管和信息安全体系全面形成。新车基本实现智能化，高级别智能汽车实现规模化应用。“人-车-路-云”实现高度协同，新一代车用无线通信网络(5G-V2X)基本满足智能汽车发展需要。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　到2035年，中国标准智能汽车享誉全球，率先建成智能汽车强国，全民共享“安全、高效、绿色、文明”的智能汽车社会。</p>\n<p>　　征求意见稿指出，当前，新一轮科技革命和产业变革蓬勃兴起，智能汽车已成为汽车产业发展的战略方向。发展智能汽车不仅是解决汽车社会面临的交通安全、道路拥堵、能源消耗、环境污染等问题的重要手段，更是深化供给侧结构性改革、实施创新驱动发展战略、建成现代化强国的重要支撑，对不断满足人民日益增长的美好生活需要具有十分重要的意义。</p>\n<p>　　此次公开征求意见的时间为2018年1月5日至2018年1月20日。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1768,'2018-01-05 19:44:00','2018-01-06 14:47:13','72',0),(3097,'三亚落实中央环保督查组反馈意见 新机场等项目停工','罗攀','中国新闻网','<p>　　<strong>三亚多举措落实中央环保督查组反馈意见 新机场凤凰岛等项目停工 </strong></p>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>三亚1月6日电 (记者 尹海明)记者6日从海南省三亚市有关部门获悉，三亚市认真贯彻落实中央第四环境保护督察组和海南省委、省政府督察整改工作要求，制定25项整改措施清单。其中包括安排执法人员进驻三亚市新机场项目建设现场，确保全面停工；凤凰岛项目建设立即停工，严格凤凰岛土地用途管理，严禁建设任何形式的房地产项目等。</p>\n<p>　　在三亚25项整改措施清单中，部分主要整改清单工作拟安排如下：</p>\n<p>　　1、2018年6月前，修订完善《三亚市推行环境保护行政责任制实施办法》，建立健全市级环保督查工作机制。</p>\n<p>　　2、2019年12月前，推进落实解决2017年初梳理出的全市5大类53项环境保护突出问题。</p>\n<p>　　3、2018年1月完成对全市岸线侵蚀情况的全面调查。2018年5月完成亚龙湾岸滩整治前期研究工作，启动三亚湾岸滩实验修复工程，2019年底前完成亚龙湾岸滩整治工程。</p>\n<p>　　4、2018年底前基本消除城镇建成区黑臭水体，争创国家级生态建设文明示范市。</p>\n<p>　　5、2018年底前对己建、在建示范村的水源治理进行整改和提升，加快推进北部山区饮水安全工程建设。</p>\n<p>　　6、2020年5月前，在全市范围内新建4座污水处理厂，实现污水收集处理全覆盖。</p>\n<p>　　7、2018年2月底前，对半岛一号项目现状用地进行生态修复。</p>\n<p>　　8、2018年5月底前启动三亚湾岸滩实验修复工程，2019年底前完成亚龙湾岸滩整治工程。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为三亚凤凰岛。(资料图片) 尹海明 摄\" src=\"upload/image/f81505e3-47f9-4ced-b053-498e2d63e0f7.jpg\" title=\"图为三亚凤凰岛。(资料图片) 尹海明 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为三亚凤凰岛。(资料图片) 尹海明 摄 \n</div>\n<p>　　9、安排执法人员进驻三亚市新机场项目建设现场，确保全面停工。</p>\n<p>　　10、凤凰岛项目建设立即停工，严格凤凰岛土地用途管理，严禁建设任何形式的房地产项目。于2018年3月前完成对三亚湾生态损害岸段进行排查。2018年5月前组织论证对改善凤凰岛两岛间水体交换工程方案进行论证。2018年底完成凤凰岛附近海域开展珊瑚礁修复工程。2019年前完成对三亚湾西段生态修复整治。</p>\n<p>　　11、2018年12月前，对全市各湾区生态环境进行整治，严控占用海岸带行为，推进海岸带生态修复。2019年12月底前引导海岸带海水养殖项目退出，建立海防林长效监管机制。</p>\n<p>　　12、2019年12月前，全面排查全市范围内的海水养殖，划定禁养区，限养区和适养区，加强限养区和养殖区的规划布局。</p>\n<p>　　13、2018年5月前，明确珊瑚礁保护区边界坐标，划定保护区三区界限。</p>\n<p>　　14、2018年5月底前完成三亚洲际度假酒店海上餐厅及码头的拆除工作，并开始珊瑚礁生态恢复工作。完成亚龙湾瑞吉度假酒店配套游艇码头立案查处工作。</p>\n<p>　　15、2019年6月底前，全面排查，逐步退出芒果林等人工林，开展甘什岭自然保护区森林生态环境修复工作，实行封山育林。</p>\n<p>　　16、2020年基本实现市政污水管网全覆盖，污水收集率超过95%。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/f81505e3-47f9-4ced-b053-498e2d63e0f7.jpg',NULL,1925,'2018-01-06 14:18:00','2018-01-06 14:47:13','64',0),(3098,'女子乘坐网约车 司机半路甩客还吓唬给好评','罗攀','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>柳州1月6日电 (罗宗南)广西柳州市柳南巡警大队通报，1月4日，一名女子乘坐网约车时，遭司机丢弃在半道。临别前网约车司机还用拍视频方式吓唬要给好评，不然就将视频发布网上。</p>\n<p>　　当晚19时许，覃女士在该市柳工大道用手机下单约了一辆网约车。由于当时路面拥堵，司机黎某直到十几分钟后才赶到。在冷雨夜中挨了冻的覃女士上车后对黎某进行埋怨。黎某是个急性子，听到覃女士数落自己，与之争辩起来。二人你一言我一语，没多久争辩变成争执。</p>\n<p>　　在车子驶入柳邕路后，黎某不想再与覃女士争论，亦不愿再搭载覃女士，于是将车靠边停下，将其赶下了车。期间，黎某掏出手机，将摄像头对准覃女士晃了几十秒，声称已经拍下覃女士一段视频，如若覃女士向平台投诉或不予好评，便把所拍视频发布网上，说罢黎某便驾车扬长而去。由于担心黎某真的拍下自己视频，并把视频发布网上，对自己生活造成困扰，覃女士报警求助。</p>\n<p>　　警方接报后赶到现场向覃女士了解详细情况，并通过覃女士提供电话联系上黎某。黎某告诉民警，其并未拍下覃女士视频，只是当时在气头上，担心覃女士会向平台投诉，影响以后接单，才出言吓唬。由于黎某的不当言行给覃女士带来困扰，民警对其进行严肃批评教育，同时责其向覃女士道歉。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>',NULL,NULL,514,'2018-01-06 09:06:00','2018-01-06 14:47:13','72',0),(3099,'运行成本降低融资难题缓解 干实体经济更有奔头了','高辰','人民日报','<p><strong>　　运行成本降低 融资难题缓解</strong></p>\n<p><strong>　　干实体经济 更有奔头了(政策解读・掷地有声好政策⑨)</strong></p>\n<p>　　本报记者&nbsp;&nbsp;王&nbsp;&nbsp;政</p>\n<p>　　近日，第二十五次全国企业家年度跟踪调查显示，认为目前盈利“正常”或“好于正常”的企业家占53.4%，为近5年来最高值。“2017年前三季度，公司动力电池投资扩产项目总资金超过720亿元，全年出货量将达12GWh。”宁德时代新能源科技公司董事长曾毓群说。对实体经济如此有信心，离不开国家一系列为制造业减负、鼓励发展新能源汽车的好政策。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　2017年前10月，我国规上工业增加值同比增6.7%，实现利润同比增23.3%，比前9月加快0.5个百分点。10月末规上工业企业资产负债率为55.7%，比上年同期下降0.5个百分点。</p>\n<p>　　进一步简政放权减税降费</p>\n<p>　　2017年，围绕清除实体经济发展的隐性障碍，国家出台了一系列政策。</p>\n<p>　　――进一步简政放权、降低涉企经营服务性收费，《关于进一步削减工商登记前置审批事项的决定》《关于加快推进“多证合一”改革的指导意见》《关于调整工业产品生产许可证管理目录和试行简化审批程序的决定》先后发布。</p>\n<p>　　――破解融资难题，出台《关于提升银行业服务实体经济质效的指导意见》《关于进一步激发社会领域投资活力的意见》《关于发挥民间投资作用 推进实施制造强国战略的指导意见》。</p>\n<p>　　――降低运行成本，印发《关于进一步鼓励开展多式联运工作的通知》《关于进一步推进物流降本增效促进实体经济发展的意见》。</p>\n<p>　　――为鼓励创新，颁布《知识产权综合管理改革试点总体方案》《制造业人才发展规划指南》《关于县域创新驱动发展的若干意见》《关于深化科技奖励制度改革的方案》……</p>\n<p>　　“2017年全国两会提出，全年为企业减负1万亿元。上半年出台的4批政策减税降费7180亿元，7月1日施行的新一轮减税降费措施，每年可再减轻企业负担2830亿元，减负1万亿元的政策礼包已提前发放。”国务院减轻企业负担部际联席会议办公室有关负责人说。上个月举行的中央经济工作会议再次强调，2018年将继续清理涉企收费，深化电力、石油天然气、铁路等行业改革，降低用能、物流成本。</p>\n<p>　　新能源汽车等新兴产业蓬勃发展</p>\n<p>　　千余片智能光伏板，日最高发电量125千瓦，可满足超过400辆电车充电，每年可节省煤炭4万公斤，减排二氧化碳10万公斤……2017年12月，国内首个实现绿色零排放的光伏超级充电站，在上海松江区投入运营。截至2017年10月底，上海共建成充电设施近11万个，全市新能源汽车车桩比达到1∶0.83；前11月，推广各类新能源汽车3.9万辆。</p>\n<p>　　为了推动产业由政策拉动向市场拉动转变，2017年《关于调整新能源汽车推广应用财政补贴政策的通知》开始实施。在“后补贴时代”，如何继续激发企业投资的积极性？工信部出台《乘用车企业平均燃料消耗量与新能源汽车积分并行管理办法》，明确乘用车平均燃料消耗量积分、新能源汽车积分的评价、计算、结转、受让以及购买办法。</p>\n<p>　　“双积分”政策提振了国内外车企发展新能源汽车的积极性。2017年12月，广汽某款新车上市销售，搭载由1.5升发动机与电动机所组成的插电式混合动力系统，百公里综合工况油耗为1.8升，最大综合续驶里程超过600公里。</p>\n<p>　　“将中方研发的新能源车型导入合资企业生产、销售，并且挂中方母公司的商标，这在以前几乎是不可能的事。”资深汽车评论员张志勇说，“双积分”政策开启了合资企业双品牌时代。</p>\n<p>　　统计显示，2017年前11月，我国销售新能源汽车60.9万辆，同比增长51.4%。预计全年销量可达70万辆，连续三年世界第一。</p>\n<p>　　提前布局工业互联网与人工智能</p>\n<p>　　率先实现语音识别系统用户自行定义唤醒词，可识别20多种方言；首家搭载了第四阶段远程控制车联网系统，可远程启动发动机、空调，远程关锁，一键寻车，手机规划好的行车路线一键发送……日前，搭载科大讯飞语音识别系统3.0版的华晨某款新车上市。</p>\n<p>　　华晨汽车只是科大讯飞语音及人工智能技术近20个整车合作伙伴中的一个。截至目前，超200款车型、累计超1000万部车辆搭载了科大讯飞语音交互系统。</p>\n<p>　　2017年7月，国务院印发《新一代人工智能发展规划》，为我国人工智能产业发展指明了方向；12月，工信部印发《促进新一代人工智能产业发展三年行动计划(2018―2020年)》，明确发展智能网联汽车、智能服务机器人、智能语音交互系统等智能化产品，鼓励新人工智能技术在工业领域的探索应用。</p>\n<p>　　同样提前布局的还有工业互联网。2017年11月，为抢占制造业竞争制高点，形成实体经济与网络相互促进、同步提升的良好格局，《深化“互联网+先进制造业”发展工业互联网的指导意见》印发。</p>\n<p>　　企业生产效率提升25%，运营成本降低20%，产品不良率降低20%，每年因检修导致的停产时间降低30%……树根互联与大豪科技联合打造的“根云”智慧缝纫解决方案，不仅能对设备进行实时监控和智能配置，还可以向用户提供自行定制绣花图样，并向相应的工业绣花机发送操作指令。而类似的解决方案，树根互联已经提供给了42个行业。</p>\n<p>　　“实体经济发展的新动能来自于占比90%以上的中小企业。”树根互联技术有限公司CEO贺东东认为，工业互联网平台能为实体经济发展贡献更大力量。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1298,'2018-01-06 09:05:00','2018-01-06 14:47:13','66',0),(3100,'“侨胞之家”落户云南昆明华侨新村','孔庆玲','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>昆明1月5日电 (陈静 杜潇潇)5日，昆明市完成了对昆明华侨新村的改建工作，并为其授牌“侨胞之家”，拟将归国华侨集中居住的华侨新村建设成一个侨史展示、侨文化传承的爱国主义教育中心。</p>\n<p>　　华侨新村位于昆明市五华区人民西路30号，上世纪60年代，为了安置归国的华侨，当地政府划出这片区域作为安置点。到了上世纪80年代，华侨新村共建有三栋别墅，2栋公寓。1990年前后，共居住有住户21户。2017年，在昆明市侨联、昆明市五华区委、区政府的支持下，昆明市五华区龙翔街道对华侨新村临街独立房屋的墙面、路面进行了微改造并建设了侨文化宣传栏、文化墙。</p>\n<p>　　当日，刚刚揭牌的“侨胞之家”引来了不少市民驻足参观，展板上的历史故事展示了华侨新村厚重的侨乡文化。</p>\n<p>　　杨泽清一家从华侨新村刚落成至今都居住在这里，见证了华侨新村的发展与兴衰。他说，此次改造后，华侨新村的环境和治安问题得到了很大改善。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　昆明“侨胞之家\"落户华侨新村，将为昆明的归侨、侨眷搭建一个交流、互动的平台，使得“侨胞之家\"真正具有“家\"的味道。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,530,'2018-01-05 20:46:00','2018-01-06 14:47:13','73',0),(3101,'经济观察：中国水资源税改革扩至九省市区 运行平稳','史建磊','中国新闻网','<div style=\"text-align:center\"> \n <img alt=\"资料图：南水北调中线工程渠首，河南省淅川县丹江口水库。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 王中举 摄\" src=\"upload/image/6ac56e5b-01f6-485a-b794-5a9b7a3739de.jpg\" title=\"资料图：南水北调中线工程渠首，河南省淅川县丹江口水库。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 王中举 摄\"> \n <input id=\"af1f1b7f532748bcb83d43385e839e9b\" name=\"af1f1b7f532748bcb83d43385e839e9b\" type=\"hidden\" value=\"http://www.chinanews.com/part/561/2017/1212/4943.html\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：南水北调中线工程渠首，河南省淅川县丹江口水库。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 王中举 摄 \n</div>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>北京1月6日电 (记者 赵建华)新年伊始，北京、天津、山西、内蒙古、河南、山东、四川、陕西、宁夏9省(自治区、直辖市)陆续迎来水资源税征收期。这些地方原先缴纳水资源费的企业开始缴纳水资源税。</p>\n<p>　　在前年河北省率先实施水资源税改革试点的基础上，中国自去年12月1日起在上述9省(自治区、直辖市)扩大水资源税改革试点。</p>\n<p>　　1月1日上午，河南新乡县地税局开出首张水资源税税票，纳税人为河南心连心化肥有限公司，共缴纳水资源税19.99万元(人民币，下同)。这家化肥企业的办税人员宋光钊表示，新乡县是缺水地区，“我们又是用水大户，开征水资源税，对我们压力不小。不过开征水资源税是为了提升全社会的节水意识，保护水资源，每个企业都会积极配合”。</p>\n<p>　　在河南省会，郑州自来水投资控股有限公司相关负责人强调，开征水资源税，由水资源费改为水资源税，是收费制度向征税制度的平稳转换，对居民和一般工商业税额标准基本保持不变，不增加正常生产生活用水负担。</p>\n<p>　　在陕西，西安地方税务局曲江分局2日开出了税额1050万元的水资源税税票，陕西省水资源税改革试点顺利落地实施。</p>\n<p>　　西安市地税局相关负责人表示，水资源费改税，不增加城市居民和城镇公共供水企业的负担。在缺水日趋严重的情况下，将利用税收杠杆去撬动绿色节水的环保机制。通过设置差别税额，可望抑制地下水超采和不合理用水的需求，助推企业高效节能用水、调整优化用水结构，最终实现促进水资源节约和开发利用。</p>\n<p>　　同日，北京地税也迎来首位水资源税纳税人――延庆八达岭特区办事处。核定用水量后，纳税人缴纳了水资源税93537元。</p>\n<p>　　北京市财政局表示，2016年北京市水资源总量为35.06亿立方米，按照年末常住人口2172.9万人计算，人均水资源占有量为161立方米，仅为全国人均水资源占有量的7%。跟其他地方一样，水资源费改税后，实现税费平移，城镇公共供水企业负担不变，居民正常生活用水负担不变，工农业正常生产用水负担不变，不会增加正常生产生活负担。同时，加强对超计划取用水、高耗水特种行业用水、超采地下水等行为的税收调节。</p>\n<p>　　中国财政科学研究院院长刘尚希分析说，随着经济社会不断发展，水已成为中国严重短缺的产品和制约环境质量的主要因素。改革后，税收的杠杆作用会显现。如果企业的用水成本上升，就会节水、增加水资源循环利用，甚至改变原先的生产方式，进而促进经济转型。</p>\n<p>　　中国国家税务总局税收科研所研究员李平表示，河北省的经验表明，水资源税改革引导节约用水的调控作用日趋显现，达到了预期目标。试点范围扩大，有助于加快构建起科学完整的、促进生态环境保护的绿色税收制度。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/6ac56e5b-01f6-485a-b794-5a9b7a3739de.jpg',NULL,730,'2018-01-06 11:03:00','2018-01-06 14:47:13','66',0),(3102,'十问“湖长”：你是一个什么“长”？','高辰','新华网','<p>　　新华社北京1月5日电 <strong>题：十问“湖长”：你是一个什么“长”？</strong></p>\n<p>　　新华社“中国网事”记者浦超 杨思琪</p>\n<p>　　中共中央办公厅、国务院办公厅印发的《关于在湖泊实施湖长制的指导意见》4日正式公布，继“河长”上任后，“湖长”也“上线”了。</p>\n<p>　　那么，“湖长”是个什么长？与“河长”有什么关系？“湖长”主要做什么？为什么设“湖长”？“湖长”责任如何落到实处？……新华社记者为您逐一解读。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　【“湖长”是个什么“长”？】《关于在湖泊实施湖长制的指导意见》指出，为进一步加强湖泊管理保护工作，2018年年底前，全国湖泊将全面建立省、市、县、乡四级“湖长”制，建立健全以党政领导负责制为核心的责任体系，落实属地管理责任。湖泊最高层级的“湖长”是第一责任人，对湖泊的管理保护负总责。</p>\n<p>　　【“湖长”主要做什么？】最高层级“湖长”负责统筹协调湖泊与入湖河流的管理保护工作，确定湖泊管理保护目标任务，组织制定“一湖一策”方案，明确各级“湖长”职责，协调解决湖泊管理保护中的重大问题，依法组织整治围垦湖泊、侵占水域、超标排污、违法养殖、非法采砂等突出问题。其他各级“湖长”对湖泊在本辖区内的管理保护负直接责任，按职责分工组织实施湖泊管理保护工作。</p>\n<p>　　【跨区域湖泊谁任“湖长”？】各省(自治区、直辖市)行政区域内主要湖泊，跨省级行政区域且在本辖区地位和作用重要的湖泊，由省级负责同志担任“湖长”；跨市地级行政区域的湖泊，原则上由省级负责同志担任“湖长”；跨县级行政区域的湖泊，原则上由市地级负责同志担任“湖长”。</p>\n<p>　　【湖长制是河长制“再升级”吗？】2016年年底，中共中央办公厅、国务院办公厅印发《关于全面推行河长制的意见》，全国省市县乡四级30多万名河长上岗履职，河湖专项整治行动深入开展，很多河湖实现了从“没人管”到“有人管”、从“多头管”到“统一管”、从“管不住”到“管得好”的转变，生态系统逐步恢复，环境质量不断改善，受到人民群众好评。</p>\n<p>　　“湖长”一词正式发布，距离“河长”一年有余。此举意味着我国在建立河长制有力管理保护江河后，针对湖泊特点和问题推出湖长制，进一步强化管理保护工作。</p>\n<p>　　【我国有多少湖泊需要“湖长”？】湖泊是江河水系的重要组成部分，是蓄洪储水的重要空间，在防洪、供水、航运、生态等方面具有不可替代的作用。全国现有水面面积1平方公里以上的天然湖泊2865个，总面积7.8万平方公里，淡水资源量约占全国水资源量的8.5%。</p>\n<p>　　【湖泊为什么需要“湖长”？】与河流相比，湖泊水域较为封闭，水体流动相对缓慢，水体交换更新周期长，自我修复能力弱，生态平衡易受到自然和人类活动的影响，容易发生水质污染、水体富营养化，存在内源污染风险，遭受污染后治理修复难，对区域生态环境影响大，必须预防为先、保护为本，落实更加严格的管理保护措施。</p>\n<p>　　【“湖长”要治疗哪些湖泊“疾病”？】一些地方围垦湖泊、侵占水域、超标排污、违法养殖、非法采砂，造成湖泊面积萎缩、水域空间减少、水系连通不畅、水环境状况恶化、生物栖息地破坏，湖泊功能严重退化。虽然近年来各地积极采取退田还湖、退渔还湖等一系列措施，湖泊生态环境有所改善，但尚未实现根本好转。</p>\n<p>　　【“湖长”会面临什么样的复杂情况？】河湖关系复杂，湖泊管理保护需要与入湖河流通盘考虑、协调推进；湖泊水体连通，边界监测断面不易确定，准确界定沿湖行政区域管理保护责任较难；湖泊水域岸线及周边普遍存在种植养殖、旅游开发等活动，管理保护不当极易导致无序开发；不同湖泊差异明显，必须因地制宜、因湖施策，统筹管理保护。</p>\n<p>　　【“湖长”的主要任务是什么？】严格湖泊水域空间管控，严格控制开发利用行为；加强湖泊岸线管理保护，实行分区管理，强化岸线用途管制；加强湖泊水资源保护和水污染防治，落实最严格水资源管理制度和排污许可证制度，严格控制入湖污染物总量；加大湖泊水环境综合整治力度；开展湖泊生态治理与修复；健全湖泊执法监管机制。</p>\n<p>　　【“湖长”工作谁来监督？】意见要求，各级党委和政府要加强组织领导，层层建立责任制，强化部门联动；各地区各有关部门要建立“一湖一档”，加强分类指导，完善监测监控；要通过湖长公告、湖长公示牌、湖长APP、微信公众号、社会监督员等多种方式加强社会监督；各地要建立健全考核问责机制，县级及以上“湖长”负责组织对相应湖泊下一级“湖长”进行考核，考核结果作为地方党政领导干部综合考核评价的重要依据；实行湖泊生态环境损害责任终身追究制，对造成湖泊面积萎缩、水体恶化、生态功能退化等生态环境损害的，严格按有关规定追究相关单位和人员的责任。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,773,'2018-01-06 08:57:00','2018-01-06 14:47:13','64',0),(3103,'这东西3元卖了十年如今涨价 你的钱包准备好了吗','陈海峰','央视财经','<p>　　3元卖了十年！如今突然涨价，你的钱包准备好了吗？</p>\n<p>　　过去十年，啤酒几乎没有涨价。市场上，3元一瓶的普通绿瓶啤酒、按人头收费不限量畅饮、吃饭就送啤酒……啤酒企业“赔钱赚吆喝”的现象屡见不鲜。</p>\n<p>　　但这样的好日子似乎要结束了。继高端白酒集体涨价后，不少男人爱喝的啤酒也迎来一大波涨价潮。有媒体5日报道称，因为生产成本增加，2018年1月1日起，包括华润雪花、青岛啤酒在内的多家啤酒企业将对产品进行价格调整。</p>\n<p>　　近日，在网络上有媒体消息称，啤酒行业将集体涨价。而且详细例举了各家公司的所谓《调价通告》。受此影响，A股中的相关公司股价也迅速拉涨。对于这则消息，青岛啤酒就在今天发布公告，对此进行了澄清。</p>\n<p>　　<strong>网传啤酒价格大幅上涨 青岛啤酒发布澄清公告</strong></p>\n<p>　　华润雪花的《调价通告》称，由于人工、运输费用等经营成本增加，自1月1日起对部分产品价格进行上调，通告落款为“华润雪花浙江销售分公司”； 而青岛啤酒公司方面通告中称，由于原料价格上涨、人工成本、运输费用增加及环保税开征等原因造成生产成本增加，因此“从1月1日起，对部分产品价格及费用进行调整”。不过将对哪些产品的价格进行调整，上调多少，两家公司的通告函中并未作过多说明。</p>\n<p align=\"center\"><img src=\"upload/image/c9f54ba0-537e-420c-a508-4b8828d62890.jpg\"></p>\n<p>　　不过就在今天中午，针对之前10%-20%涨价幅度的传言，青岛啤酒进行了澄清：公司产品拟对部分区域的部分产品进行价格上调，并非全部产品，涨价部分产品的平均涨价幅度不超过5%。央视财经记者也搜索了目前电商平台上青岛啤酒12听500ml /箱装的价格为38元，按百分之五的涨幅预测，12听/箱价格上涨不到2元。</p>\n<p>　　随后记者也咨询了中国酒业协会的相关人士，他表示，长期以来，啤酒的价格被低估了，此次涨价是正常的市场行为，对啤酒行业来说，是一个价格的回归。</p>\n<p>　　<strong>啤酒行业已过最坏时点</strong></p>\n<p>　　实际上，去年12月，燕京啤酒已率先涨价。彼时，燕京啤酒对460ml本生啤酒（纸1*12）终端进货结算价格顺涨3元/箱，单瓶零售价格顺涨1元/瓶。</p>\n<p>　　对此，中泰证券食品饮料首席分析师范劲松认为，此轮提价先后顺序是重庆啤酒、燕京啤酒、华润啤酒、青岛啤酒。</p>\n<p>　　整个啤酒业的情况是，2014年至2016年，我国啤酒产业面临产能过剩、市场需求减少等问题，国内巨头也出现产量下滑“喝不动”的现象。</p>\n<p>　　连滑三年后，国内啤酒业去年重回增长。国家统计局数据显示，2017年1~7月，国内啤酒累计产量2781.7万千升，同比增长0.8%。</p>\n<p>　　不少业内人士认为，尽管2017年上半年行业出现复苏，但只能视其为经历3年下滑后的“触底反弹”。啤酒产业专家方刚表示，啤酒行业经历两年多下滑后，下滑空间已经不大，如今出现反弹非常正常。</p>\n<p>　　目前看来，国产啤酒似乎也迎来价量齐升的局面，行业反弹是否企稳？</p>\n<p>　　范劲松表示，2015年之后，啤酒企业在建工程大幅减少，资产折旧大幅下降，员工人数不再增加，周转速度加快，企业的经营现金流明显好转，结合行业销量小幅提升，竞争逐步有序。据此，其认为行业最坏的时刻已经过去了，行业拐点已现。</p>\n<p>　　范劲松在研报中表示，啤酒已经经历了2013-2016年的下滑，后期下滑空间有限，大概率进入存量博弈阶段，集中度提高将是不可阻挡的趋势，中国啤酒吨价很低，相当于美国的四分之一、德国的九分之一，日后价格方面还有很大的提升空间。他还认为，2018年龙头企业有望陆续提价，提价之后预计会发生两种现象，其一是龙头企业可抢占更多的市场份额；其二是企业的利润率有望提升。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/c9f54ba0-537e-420c-a508-4b8828d62890.jpg',NULL,646,'2018-01-06 07:53:00','2018-01-06 14:47:13','66',0),(3104,'舞剧《朱�q》登台纽约林肯艺术中心','高辰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>纽约1月5日电 (记者 廖攀)由上海歌舞团历时4年创作的舞剧《朱�q》5日在纽约林肯艺术中心的大卫・寇克剧院演出。</p>\n<p>　　寒冷的天气并没有阻挡民众前往观看的热情。在开演两周前，纽约林肯中心的三场演出票几近售罄，周日场更开放了高层加座。</p>\n<div style=\"text-align:center\"> \n <img alt=\"作为中国对外文化集团打造的“中华风韵”系列演出的组成部分，舞剧《朱�q》以环保为主题，通过凄美动人的故事情节和唯美浪漫的舞台画面，讲述朱�q与人类相惜相怜的命运，诉说人类在现代化、城市化进程中与自然休戚相关的同命关系。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 廖攀 摄\" src=\"upload/image/f871d7ec-f162-451b-9ab1-210316b628a7.jpg\" title=\"作为中国对外文化集团打造的“中华风韵”系列演出的组成部分，舞剧《朱�q》以环保为主题，通过凄美动人的故事情节和唯美浪漫的舞台画面，讲述朱�q与人类相惜相怜的命运，诉说人类在现代化、城市化进程中与自然休戚相关的同命关系。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 廖攀 摄\"> \n <input id=\"793e2f0f22294bd783cce6e1b94a84b5\" name=\"793e2f0f22294bd783cce6e1b94a84b5\" type=\"hidden\" value=\"http://www.chinanews.com/tp/hd2011/2018/01-06/792404.shtml\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：舞剧《朱�q》以环保为主题，通过凄美动人的故事情节和唯美浪漫的舞台画面，讲述朱�q与人类相惜相怜的命运，诉说人类在现代化、城市化进程中与自然休戚相关的同命关系。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 廖攀 摄 \n</div>\n<p>　　《朱�q》由中国人民对外友好协会和中共上海市委宣传部出品，以环保为主题，通过凄美动人的故事情节和唯美浪漫的舞台画面，讲述了朱�q这一珍禽与人类相惜相怜的命运，呼唤人们在曾经失去中叩问永久珍惜的情感共鸣。舞剧中，以主演朱洁静、王佳俊为首的舞者们用有温度的肢体语言展开言说，用不同质感的舞蹈表达朱�q所处环境的变化。</p>\n<p>　　关于“环保”题材的作品，之前大都以纪录片的形式呈现。《朱�q》则以舞蹈这一独特的艺术形式，用柔软、细腻的身体表达，诉说人类在现代化、城市化进程中与自然、环境、生灵休戚与共的同命关系。</p>\n<p>　　纽约演出后，《朱�q》将于1月11日至12日在波士顿博赫中心舒伯特剧院演出两场。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/f871d7ec-f162-451b-9ab1-210316b628a7.jpg',NULL,597,'2018-01-06 13:43:00','2018-01-06 14:47:13','75',0),(3105,'鲁能俱乐部迎成立20周年纪念日 球员备战新赛季','王硕','济南日报','<p>　　<strong>今迎成立20周年纪念日</strong></p>\n<p>　　<strong>鲁能俱乐部一线球员开始新赛季第一练</strong></p>\n<p>　　鲁能29名本土一线球员1月4日结束假期，在基地重新集结，今天将开始新赛季第一练。1月5日对鲁能俱乐部来说，是一个特殊的日子。20年前的今天，山东泰山足球俱乐部正式更名为山东鲁能泰山足球俱乐部。选择在俱乐部成立20周年纪念日开启球队备战2018赛季第一练，或许只是一种巧合，但对于渴望在2018赛季重塑辉煌的鲁能俱乐部及球员来说，无疑是一种莫大的激励。</p>\n<p>　　按照计划，鲁能29名本土球员将在5日、6日分批次去医院进行全方位身体检查及在俱乐部进行身体技能测试，7日奔赴广州集训。4名外援佩莱、吉尔、塔尔德利、西塞将直接在广州与球队会合。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　在29名球员中，近期与天津权健传出绯闻的戴琳如期归队，而正随U23国足集训备战的刘洋、李海龙、姚均晟以及即将回购的高准翼都将缺席本次集训。球队广州冬训为期20天，训练内容以体能和技战术训练为主。该阶段冬训结束后，球队将直接飞赴迪拜进行为期半个月的海外拉练。</p>\n<p>　　5日，俱乐部还将举行球迷墙揭幕仪式。届时，郝伟将作为教练员代表；蒿俊闵、王大雷、周海滨、张弛将作为现役球员代表；邢锐、李明、察可军、宋黎辉将作为老队员代表与部分球迷进行互动。</p>\n<p>　　新赛季随着中国足协对内外援引进的限制及U23球员出场的严控，对鲁能来说将是证明自己的机会，但“保三争一”的赛季目标也是不小的挑战。今年正值俱乐部成立20周年，支持者们期待着那支曾经打遍中超无敌手的鲁能王者归来。 (本报记者 王蓓贝)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1066,'2018-01-05 13:43:00','2018-01-06 14:47:13','74',0),(3106,'巴西房价10年来首次下跌 业者：还会持续下滑','吴合琴','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>1月5日电 南美侨报网编译称，据巴西经济研究基金会(Fipe)以及Zap房地产公布的FipeZap房地产指数显示，2017年巴西住宅价格名义下跌了0.53%，是该指数10年前开始计算以来的首次名义价格下跌。</p>\n<p>　　巴西《环球报》1月3日报道，Fipe的数据是根据Zap房地产网站中20个城市的房价计算而出的，依据为网上公布的价格，而并非实际合同签约价格。如果考虑到2017的通胀率(金融市场预计的2.78%)，实际价格则下跌了3.23%。但2017年的跌幅低于2016年，当年的价格实际下跌了5%以上。</p>\n<p>　　2017年，20个城市中13个城市的住宅价格名义下跌，下跌幅度最高的是里约热内卢(-4.45%)、尼泰罗伊(-3.43%)、福塔莱萨(-3.35%)和巴西利亚联邦特区(-2.27%)。价格上涨幅度最高的是贝洛奥里藏特(4.47%)和弗洛里亚诺波利斯(4.34%)，这两个城市的房价也实际上涨。</p>\n<p>　　在经济危机时期的2014年至2017年，巴西住宅价格实际下跌了17%。房地产市场专家表示，房价的下跌主要是由于2015年来劳动力市场的恶化致使消费者购买力下降以及信贷的收缩。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　Fipe的经济学家布鲁诺・奥利瓦(Bruno Oliva)表示，在房价快速上涨一段时间后，出现下降是正常的现象。“尽管上几个月出现了一些好的消息，但不管是经济还是房地产市场，都还比较低迷，2017年的需求仍然很低，消费者非常谨慎。”他说。</p>\n<p>　　巴西房地产信贷和储蓄机构协会(Abecip)的数据显示，2014年12月份，超过3.3万套房屋为贷款购买，2017年11月份则降至8000多套。</p>\n<p>　　保罗住房公会(Secovi-SP)的首席经济学家塞尔索・彼得鲁奇(Celso Petrucci)表示，从2017年7月份开始房地产市场才出现了复苏的迹象。“2017年年初，消费者的预期仍然很低，因此去年的开局和2015年和2016年相似。”他说。</p>\n<p>　　Fipe的奥利瓦预计，2018年巴西房价的下滑趋势会继续维持下去。“虽然经济的复苏有利于房地产市场升温，但这种复苏很难对2018年的房价产生很大的影响。”</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1612,'2018-01-05 16:57:00','2018-01-06 14:47:13','73',0),(3107,'大连权健女足外援当选非洲足球小姐','唐云云','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>大连1月5日电 当地时间1月4日，非洲足联在加纳首都阿克拉宣布，效力于大连权健女子足球俱乐部的尼日利亚外援埃・奥莎拉蝉联2017年度非洲足球小姐。这也是她自2014年和2016年后第三次获此殊荣。</p>\n<p>　　23岁的奥莎拉击败了南非的克・科噶特拉纳以及喀麦隆的加・阿・奥古尼。</p>\n<p>　　奥莎拉说：“2017年，我离开阿森纳女足，加入中国的大连权健女足，很多人曾质疑我的决定，但我告诉自己我可以，于是才有了今天。”</p>\n<p>　　效力于大连权健女足的奥莎拉来自尼日利亚，场上位置司职前锋，此前曾效力于利物浦、阿森纳女足。2017年2月，奥莎拉加盟大连权健女足俱乐部，随即助球队卫冕2017中国足协女子足球超级联赛冠军，并以12个进球成为联赛最佳射手。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　2014年，奥莎拉获得世青赛金球奖和金靴奖。2016年，奥莎拉率领尼日利亚队获得了非洲女足国家杯冠军，也因此当选当年非洲足球小姐。</p>\n<p>　　据悉，效力于英超利物浦队的埃及球星萨拉赫当选2017年度非洲足球先生。(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1128,'2018-01-05 15:44:00','2018-01-06 14:47:13','74',0),(3108,'经济不景气 台湾银发族再就业寻求事业第二春 　　','王硕','人民日报海外版','<p><strong>　　寻求事业第二春</strong></p>\n<p><strong>　　台湾银发族就业忙</strong></p>\n<p>　　本报记者 柴逸扉</p>\n<p>　　65岁以上老年人口占整体比例13.55%，台当局内部事务主管部门前不久公布的这一数据显示，台湾的人口老龄化日趋严重。不过，尽管被人说成“银发海啸”，但这些长者们并不甘心“到点退休”，反而一直在另谋职业焕发第二春。如今，银发族们就业忙成为了台湾劳动力市场上的一大特色。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　大环境迫使谋出路</p>\n<p>　　银发族为何愿意“退而不休”？有多种原因。台北市政府就业服务处长游淑真表示，近3年因台湾经济不景气，中小企业倒闭增加，造成中高龄失业人口攀升。2016年，台北市65岁以上求职者，就比2013年增长70%，显示越来越多中高龄者，寻求事业第二春。</p>\n<p>　　义守大学企管系讲座教授刘常勇说，大环境不佳，企业往往解聘资深员工或鼓励他们退休，加上台湾地区老龄人群的寿命延长，中高龄者不能依赖子女扶养，这些人离开职场须再就业，这是趋势也是社会问题。</p>\n<p>　　但从个案来说，有的人是因为经济压力选择再就业，也有人是因为退休后不想在家里跟家人“大眼瞪小眼”，他们找新工作往往不在乎薪水，只想让退休不无聊。有台媒报道指出，曾有一位老先生到招聘市场应聘，说他不要“一例一休”，要每天来上班，因为在家无聊，但公司跟他说没办法，这是法令规定。</p>\n<p>　　而对企业来讲，由于台湾地区少子化效应浮现，若企业都争抢年轻人，付出的代价比较高，所以不少企业已准备改用中高龄求职者。</p>\n<p>　　银发族群优势颇多</p>\n<p>　　虽然银发族群再就业存在一定的被迫因素，但这并不代表他们是一群竞争力低的群体。相反，银发族有许多优势是职场新鲜人们所不能比的。</p>\n<p>　　曾经参与银发族招聘的佳�D公司经理周仲年表示，自己公司的老龄员工有些具备在欧美工作的经验，具有业务专长。他认为，年轻人有年轻人的好，中高龄也有中高龄的长处，像经验丰富、能在短时间上手、发现问题时也会立即提出，这是年轻员工较欠缺的特点。</p>\n<p>　　有企业主认为，中高龄员工最大特色，就是稳定性够，虽然受限于体力，但不会突然就不来上班，答应你的事一定会做到。例如说今天上4小时，就是4小时，不会打混摸鱼。</p>\n<p>　　负责在台北市组织中高龄者就业媒合会的中联扶轮社长毕丽家认为，只要管理方式略做改变，让中高龄者感受到尊重，给予适合的职务，他们会非常愿意奉献专业，“这些稳定性高的员工，都是公司的宝”。</p>\n<p>　　多管齐下帮助就业</p>\n<p>　　为了妥善运用人力、再创劳动价值，台北市就业服务处于台北捷运东区地下街成立银发人才就业咨询站。除了提供银发族职涯咨询、履历及面试咨询以及就业媒合等服务，他们也希望如同电影“高年级实习生”般，将有管理才能、顾问能力的银发族组成人才顾问团，让有心力工作的银发高龄者得以再次在职场舞台上展现风采。</p>\n<p>　　台北市政府劳动局相关负责人表示，为了延续银发族的劳动价值，有关部门以“多一次机会”为口号，鼓励企业聘用银发专业人才。银发人才就业咨询站特地设立在交通位置方便的地方，让长辈可以轻松到达，服务项目包括一对一职涯咨询、求职求才、创业协助以及银发人才顾问团等服务，有就业需求的银发族也可以在网站上找到最新、有保障的岗位，也能精准地与求才企业做匹配。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,523,'2018-01-05 16:02:00','2018-01-06 14:47:13','70',0),(3109,'青海省多地发布今年首次寒潮黄色预警信号','罗攀','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>西宁1月6日电 (孙睿李静)记者6日从青海省气象局获悉，预计6日至7日，青海省除海西西部外，其余地区都将出现降雪降温天气。</p>\n<p>　　据青海省气象台首席预报员马学莲介绍，受冷空气影响，预计6日至7日，青海省除海西西部多云外，省内其余地区都将有小雪天气，过程期间北部地区降温明显，日平均气温将下降4至6摄氏度，其中东北部农业区将下降8至10摄氏度；唐古拉山区、海西、环青海湖地区有6到7级偏西风。</p>\n<p>　　当日上午9时30分青海省气象台发布西宁、海东大部、海南、海北寒潮黄色预警信号，这也是2018年首次发布寒潮黄色预警信号，预计未来24小时内上述地区日平均气温将要下降4摄氏度以上并可能持续。</p>\n<p>　　“据最新积雪遥感监测资料显示，1月4日玛多县除黄河乡南部外，花石峡镇、马查理镇、扎陵湖乡和黄河乡北部积雪面积占行政区域面积的76.03%，积雪深度以5到10厘米为主；称多县积雪主要分布在清水河镇和扎朵镇，积雪面积占行政区域面积的60.93%，积雪深度以1到2厘米为主；兴海县积雪面积比例占行政区域面积的45.18%、积雪深度以2到5厘米为主。而此次降雪天气过程将有可能加剧玛多、称多、兴海县的积雪面积和积雪深度，对当地畜牧业和民众出行带来不利影响。”马学莲说，为此建议青南积雪较深的地区，减少放牧时间，适当增补青饲料；过往车辆司机密切关注路况，注意出行安全。</p>\n<div style=\"text-align:center\"> \n <img alt=\"西宁市发布寒潮黄色预警信号，预计未来24小时内西宁地区日平均气温将下降4℃以上并可能持续。　孙睿 摄\" src=\"upload/image/bdb5a847-a864-45e3-96e1-27c1b6db2fe1.JPG\" title=\"西宁市发布寒潮黄色预警信号，预计未来24小时内西宁地区日平均气温将下降4℃以上并可能持续。　孙睿 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  西宁市发布寒潮黄色预警信号，预计未来24小时内西宁地区日平均气温将下降4℃以上并可能持续。　孙睿 摄 \n</div>\n<p>　　马学莲表示，虽然降雪给交通带来了不利影响，但及时净化了空气，增加了空气湿润度，但由于降雪量级较小，对全省森林草原火险等级改善不明显，各地仍需加强防火工作；同时受冷空气影响东部农业区气温低，相关单位要做好设施农业的防寒保暖工作。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/bdb5a847-a864-45e3-96e1-27c1b6db2fe1.JPG',NULL,171,'2018-01-06 13:30:00','2018-01-06 14:47:13','65',0),(3110,'人民日报：网剧必须表达正向自洽的价值观','高辰','人民日报','<p><strong>　　网剧亟待价值观蜕变(文艺评论・新挑战)</strong></p>\n<p>　　张 成</p>\n<p>　　2017年有两条新闻结合起来看耐人寻味。一是美国小伙读了半年中国网络小说后，成功戒掉毒品。另一条是网络自制剧《白夜追凶》被美国流媒体巨头网飞购买海外发行权，将会在100多个国家和地区播出。第一条无法考证真假，似有夸张之嫌，但中国网络小说走入欧美大众日常生活，并且在海外拥有数百万之众读者确是不争事实。如今，大量中国网络自制剧改编自网络小说，二者不仅受众群重合，而且格调、风格、价值观也息息相通。在可预期的未来，将会有越来越多网络小说改编成网络自制内容，并逐渐成为主流文化产品。网络小说改编作品能否超越自身畛域，以更为正向的价值观面对观众，将会成为中国网络文艺能否走得更远、走向更广阔世界的关键要素。</p>\n<p>　　网剧初生阶段，经历了亚文化与主流文化的冲突与摇摆。在2015年末，一部根据同名网络小说改编的现象级网剧《太子妃升职记》出世，打着强烈的网络小说印记。霸道总裁、穿越、宫斗所隐喻的办公室政治等人设、故事要素都直接脱胎于网络小说。其“雷人”服化道和情节导致不少观众对网剧产生“粗糙制作”固有印象。此后《无心法师》《法医秦明》等涉案、悬疑题材网剧也都收获较高人气，然而其中一些脱线情节和对涉案奇观的过度展示也曾受到争议。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　近一两年，迅猛发展的网剧一直在努力调整题材类型及适配的价值观。从最开始的微电影常常打情欲、暴力擦边球，发展到今天网络自制内容成功出海，除了制作愈发精良之外，价值观的蝉蜕与嬗变亦是关键。如《白夜追凶》的案情设置和人物设置都非常符合该类型规范，合理地表现中国特色，故事既接地气又有丰富的戏剧性，突出公安干警的奉献和敬业精神。可以说，越是投入巨大、制作精良的网络文艺作品，越应注重从网络亚文化中提取适宜主流观众的价值观，网剧人物形象也许不同于以往文艺作品中的同类形象，但必须表达正向自洽的价值观。因此，借助网络文艺丰富细分的类型和题材叙事话语以表达正能量的价值观，将是网络文艺的新方向。</p>\n<p>　　中国海量网络小说资源，数亿网民的庞大市场，为中国网剧发展提供了无限前景。坚持主流方向，摆正价值立场，相信中国网剧将以多样类型、丰富创意，为中国观众甚至世界观众带来源源不断的好作品。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1418,'2018-01-06 08:23:00','2018-01-06 14:47:13','68',0),(3111,'媒体评台当局欲“以武拒统”：别做大头梦了！','王忠会','人民日报海外版','<p><strong>　　“以武拒统”？别做大头梦了！(日月谈)</strong></p>\n<p>　　汪灵犀</p>\n<p>　　新年伊始，“两岸军事话题”就因着台当局领导人蔡英文的两场记者会而成为所谓的热点新闻。宣扬“大陆军事威胁论”，煽动两岸民众对立对抗，民进党动作频频、老调重谈，无非是想以拖待变、浑水摸鱼。但无论民进党如何故技重施，诚如国台办发言人安峰山所言，搞“以武拒统”是没有出路的！</p>\n<p>　　用武器堆不出安全感</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　去年12月26日，蔡英文发布上台以来的第一份所谓“国防报告书”，摆出要以多兵重器吓阻大陆的架势，有民进党“立委”扬言，台湾“一个阿兵哥就能摧毁大陆一辆坦克”。</p>\n<p>　　3天后，蔡英文又把2017年终记者会特意安排在桃园中科院龙潭园区，只为展示所谓的“防务自主”能力，让台湾和境外媒体一睹台湾“自制武器”。蔡英文在会上放言，“接下来每一年的‘国防预算’都会稳步增长”。</p>\n<p>　　民进党当局如此苦心渲染大陆军事威胁，虚张声势，大言炎炎，让人啼笑皆非。大陆一贯以来寻求两岸和平发展、和平统一，只要民进党不推行“台独”分裂，不触犯《反分裂国家法》，又何必做贼心虚？</p>\n<p>　　蔡英文说，台湾“自身‘国防’能力的提升才是‘国家安全’最终的保障”，这个笑话有点冷。今天两岸军事实力对比悬殊，是举世皆知的事实。台湾当局就算穷兵黩武，也不可能改变这一事实。台湾要靠提升军备“以武拒统”，不但是缘木求鱼，而且是南辕北辙。岛内民众看最清楚不过：用武器堆不出安全感，搞对抗、搞军备竞赛，不仅不能保障台湾安全，反而会给两岸关系带来风险，损害台海和平。陈水扁当年走过的老路犹历历在目，教训不可谓不深刻。</p>\n<p>　　回到“九二共识”才是正道</p>\n<p>　　要维护台海和平，台当局唯一的出路就是放下对抗思维，承认“九二共识”的核心意涵，让两岸关系回到和平发展的轨道上来。</p>\n<p>　　回想10年前，在“九二共识”的基础上，两岸实现直接通航、通邮、通商，两岸经济关系走向正常化，大批大陆游客赴台旅游观光、参访交流，和平发展的红利深入人心。现如今，和平发展是大势所趋，谁也不愿意回到那种动荡不安、人心惶惶的日子去。</p>\n<p>　　本来，两岸同胞是“打断骨头连着筋”的兄弟，两岸走和平发展的道路，优势互补，互相提升，才有利于两岸人民，有利于台湾人民，让中华民族扬眉吐气，在世界舞台上发光发热。可蔡英文上台后，不仅始终不承认“九二共识”，还在岛内搞“去中国化”、降低所谓“公投”门槛、为“台独”打开方便之门，甚至于侦办新党青年军打压“促统”力量。一年多来，在民进党狭隘的对抗思维下，两岸关系越来越“冷”。</p>\n<p>　　蔡英文还一边讲“台湾安全不能靠别人”，一边又不断将日本、美国等拉进东亚区域，“联美日抗中”之心昭然若揭。问题是，其他国家只会在意自身利益，谁会甘愿为民进党的错误路线埋单？</p>\n<p>　　解铃还须系铃人</p>\n<p>　　“两岸关系不会暴走、不会僵住、也不会走回在过去国民党时代不相往来的老路。” 这是蔡英文对岛内民众的新年承诺。她还说，台湾“持续保持善意，维持一个稳定、一致、可预测的两岸关系”。不幸的是，民进党自上台以来，所有的“善意”都只停留在口头，手底下干出来的事，桩桩件件都充斥着深深敌意。阻碍两岸交流、操作“宪改”议题、借“促转条例”搞政治追杀等大动作自不必说，小动作也不胜枚举：强撤“微调课纲”，拆卸抗日纪念碑文，废除“国父”遗像……民进党一直在妄图与大陆作历史文化上的切割，切断两岸血脉联系。</p>\n<p>　　在坚决反对形形式式的“台独”分裂、大力促进两岸交流与融合的同时，大陆方面的善意之门始终敞开。正如习近平总书记在十九大报告中所说，只要承认“九二共识”的历史事实，认同两岸同属一个中国，两岸双方就能开展对话，协商解决两岸同胞关心的问题，台湾任何政党和团体同大陆交往也不会存在障碍。</p>\n<p>　　2018年来了，岛内民众都希望民进党当局认真总结经验教训，放下对抗思维，让两岸关系回到和平发展的正轨上来；希望蔡英文能够远离“以武拒统”的老路，少点折腾，少点对立，为民众做点好事，也给民进党留点后路。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,650,'2018-01-05 15:53:00','2018-01-06 14:47:13','70',0),(3112,'《海上牧云记》结局留悬念 黄轩赋予牧云笙灵魂','唐云云','华声在线','<p align=\"center\"><img src=\"upload/image/08c41138-f538-43e6-9f99-d9fdb1d1404f.jpg\"></p>\n<p>　　<strong>《海上牧云记》结局留悬念 黄轩赋予牧云笙灵魂</strong></p>\n<p>　　由黄轩领衔主演的大型东方魔幻剧《九州-海上牧云记》在经历数十天的热播热议之后，迎来备受关注的大结局。然而看似尘埃落定的九州世界却并未平静，在命运中浮沉、抗争的牧云笙最终走了自己选择的道路，黄轩也在激烈的戏剧冲突中尽情释放魅力，赋予角色多彩的灵魂。</p>\n<p>　　<strong>牧云笙以爱助力开挂 黄轩诠释黑化笙魅力爆发</strong></p>\n<p>　　《海上牧云记》行至结尾，牧云笙终于迎来了彻底爆发的一刻。他心持“杀我父者，陷我友者，我不饶”的执念，又担心无法控制彻底觉醒的悠游魅而陷入矛盾。黄轩完美诠释了幻境之中的牧云笙，面对兄弟受困焦急万分，获得盼兮安慰时候面带依恋和平静，和母亲的对话又流露出孩童版的无助，让人物在短短几分钟内迅速丰满而立体。</p>\n<p>　　心中小爱升华为大爱，牧云笙最终从中获得操控心中妖物的力量，开挂打败墨先生。黄轩演绎黑化版牧云笙，从拼命的克制到最后的爆发，颇具层次和渲染力。精彩的冲突戏码也让黄轩的魅力得到尽情的展现，实力演技值得称赞。</p>\n<p>　　<strong>牧云笙弃位追爱而去 黄轩交出完美答卷</strong></p>\n<p>　　局势混乱九州动荡，牧云笙被迫登基，为控制心魔拼命挣扎，也无力改变朝堂现状。得知盼兮会在世间最美的地方复活，对权势毫无眷恋的他弃皇位而去。黄轩诠释这个阶段的牧云笙之时，面对乱世无奈说着“这世间哪里才是最美的地方”，平静中带着忧虑，看似放下却并未释然，看似结束却仅是开始，耐人寻味。</p>\n<p>　　回首《海上牧云记》数十集的历程，黄轩诠释的牧云笙，从不问世事的平淡，到卷入其中的矛盾，以及初尝情爱的羞涩、面对选择的纠结、担负责任的决绝、陷入困境的爆发、面对命运的无奈……每一个阶段的牧云笙都具备独特的人物魅力，让人不由自主设身处地去疼惜他，理解他。黄轩也再次用一个复杂矛盾的鲜活人物，证明了自己的实力演技。</p>\n<p>　　《海上牧云记》正在湖南卫视偶像独播剧场每天四集热播中，黄轩领衔主演的电影《芳华》《妖猫传》也在持续热映。三部作品皆交出令人称赞的答卷，成为大势演员的黄轩，会在接下来的作品中，赋予更多角色特有的魅力，敬请期待！</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/08c41138-f538-43e6-9f99-d9fdb1d1404f.jpg',NULL,488,'2018-01-05 16:23:00','2018-01-06 14:47:13','68',0),(3113,'中国拟完善商业银行大额风险暴露监管','陈海峰','中国新闻网','<p><strong>　　中国拟完善商业银行大额风险暴露监管 明确三类量化标准</strong></p>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>北京1月5日电 (记者 王恩博)为推动中国商业银行强化大额风险暴露管理，有效防控集中度风险，中国银监会5日公布《商业银行大额风险暴露管理办法》，并向社会公开征求意见。</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：山西太原，银行工作人员正在清点货币。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 张云 摄\" src=\"upload/image/e41a8acb-bed8-4337-a962-c1187811e4cd.jpg\" title=\"资料图：山西太原，银行工作人员正在清点货币。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 张云 摄\"> \n <input id=\"8b0f3c0dd1d4404a9fef63cc46841b3f\" name=\"8b0f3c0dd1d4404a9fef63cc46841b3f\" type=\"hidden\" value=\"http://www.chinanews.com/part/561/2017/0919/2978.html\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：山西太原，银行工作人员正在清点货币。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 张云 摄 \n</div>\n<p>　　大额风险暴露监管是审慎监管的重要组成部分。银监会有关部门负责人表示，近年来随着中国银行业快速发展，银行对客户的授信方式日趋多元化，客户集中度风险呈现出一些新特点。借鉴国际监管标准，结合国内银行业实践，制订统一、规范的大额风险暴露监管规则势在必行。</p>\n<p>　　此次《办法》明确了商业银行大额风险暴露监管标准，规定了风险暴露计算范围和方法，并从组织架构、管理制度、内部限额、信息系统等方面对商业银行强化大额风险管控提出具体要求，并明确了监管部门可以采取的监管措施。</p>\n<p>　　具体而言，对于非同业单一客户，《办法》重申了《商业银行法》贷款不超过资本10%的要求，同时规定包括贷款在内的所有信用风险暴露不得超过一级资本15%；对于非同业关联客户，《办法》规定其风险暴露不得超过一级资本的20%；对于同业客户，《办法》按照巴塞尔委员会监管要求，规定其风险暴露不得超过一级资本25%，但考虑到部分银行同业风险暴露已超过该标准，官方对此类风险暴露设置了三年过渡期。</p>\n<p>　　除规定大额风险暴露量化监管标准，银监会还针对商业银行大额风险暴露管理提出了多方面要求。例如，商业银行要建立和完善大额风险暴露管理组织架构，明确董事会、高级管理层、相关部门管理职责，构建相互衔接、有效制衡的运行机制，同时制定并定期修订大额风险暴露管理制度，及时报监管部门备案等。</p>\n<p>　　上述负责人强调，《办法》有助于推动商业银行提升集中度风险管理水平，降低客户授信集中度；引导银行回归本源、专注主业，弱化对同业业务的依赖，将更多资金投向实体经济。此外，相关举措还将改变授信过程中“搭便车”、“垒大户”等现象，提高中小企业信贷可获得性，改善信贷资源配置效率，降低系统性风险。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/e41a8acb-bed8-4337-a962-c1187811e4cd.jpg',NULL,1192,'2018-01-05 22:47:00','2018-01-06 14:47:13','67',0),(3114,'中国强化商业银行股权管理 严防大股东滥用权利','陈海峰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>北京1月5日电 (记者 王恩博)经过为期一个月的公开征求意见并进行修改后，中国银监会5日晚正式发布《商业银行股权管理暂行办法》，力图通过健全穿透监管框架严防商业银行大股东滥用权利等现象。</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：山西太原，民众在银行办理业务。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 张云 摄\" src=\"upload/image/9eacd3e8-d5da-4ce2-a32f-850148154bd8.jpg\" title=\"资料图：山西太原，民众在银行办理业务。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 张云 摄\"> \n <input id=\"1514fef39ac5430d8ad3bbaf7c192216\" name=\"1514fef39ac5430d8ad3bbaf7c192216\" type=\"hidden\" value=\"http://www.chinanews.com/part/561/2017/0801/1955.html\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：山西太原，民众在银行办理业务。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 张云 摄 \n</div>\n<p>　　当前，中国银行业金融机构快速发展，社会资本发起设立、参股或收购银行业金融机构的积极性不断提高。但一些乱象也随之发生，如违规使用非自有资金入股、代持股份、滥用股东权利损害银行利益等。</p>\n<p>　　银监会有关部门负责人对此表示，出台上述《办法》旨在规范商业银行股东特别是主要股东行为，加强股东资质的穿透审查，加大对违法违规行为的查处力度，保护商业银行存款人和其他客户合法权益，维护股东合法利益，保障商业银行安全稳健运行，促进商业银行持续健康发展。</p>\n<p>　　该负责人介绍说，《办法》在信息披露、入股数量、持股期限、资本补充以及公司治理等方面，对主要股东提出了明确要求，从而切实防范大股东违规干预商业银行经营管理现象。针对有商业银行与股东及相关人员进行关联交易，出现利益输送、掏空银行等现象，《办法》将主要股东及其控股股东、实际控制人、关联方、一致行动人、最终受益人一并纳入商业银行的关联方管理，防止股东通过同业投资、资管计划等渠道转移、侵占商业银行资金。</p>\n<p>　　与此同时，银监会还明确了金融产品入股商业银行的规则。根据《办法》，单一投资人、发行人或管理人及其实际控制人、关联方、一致行动人控制的金融产品持有同一商业银行股份合计不得超过该商业银行股份总额的5%。此外，商业银行主要股东亦不得以发行、管理或通过其他手段控制的金融产品持有同一商业银行股份。</p>\n<p>　　银监会透露，未来将把对商业银行经营管理有重大影响的主要股东作为监管重点，并设立专章规定监管部门在股权管理方面的监管职责和手段，重点加强穿透监管、对违规不改正的股东采取限制股东权利，责令商业银行控股股东转让股权等监管措施。同时官方还将印发通知，重点解决存量股东规范问题。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/9eacd3e8-d5da-4ce2-a32f-850148154bd8.jpg',NULL,929,'2018-04-01 16:33:00','2018-01-06 14:47:13','67',0),(3115,'北京地铁常态化限流车站96座 CBD车站早晚高峰常限流','高辰','北京青年报','<p><strong>　　北京地铁路网常态化限流车站达96座</strong></p>\n<p><strong>　　城市核心区重点车站早晚高峰多有限流 调整多条线路运力缓解郊区流量压力</strong></p>\n<div style=\"text-align:center\"> \n <img alt=\"12月30日，北京地铁燕房线开通试运行，这是中国第一条具有完全自主知识产权的轨道交通全自动运行系统，燕房线位于北京房山区，本次开通线路包括14.4公里高架线路、9座车站，设计时速为80公里。当日，北京轨道交通燕房线、S1线、西郊线开通试运行。目前，北京轨道交通运营线路共22条、总里程608公里、车站370座。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 张宇 摄\" src=\"upload/image/61737e65-607a-4692-9bef-7c817b0c6e65.jpg\" title=\"12月30日，北京地铁燕房线开通试运行，这是中国第一条具有完全自主知识产权的轨道交通全自动运行系统，燕房线位于北京房山区，本次开通线路包括14.4公里高架线路、9座车站，设计时速为80公里。当日，北京轨道交通燕房线、S1线、西郊线开通试运行。目前，北京轨道交通运营线路共22条、总里程608公里、车站370座。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 张宇 摄\"> \n <input id=\"fed79c27e09544b0b8eb307204913347\" name=\"fed79c27e09544b0b8eb307204913347\" type=\"hidden\" value=\"http://www.chinanews.com/tp/hd2011/2017/12-30/791073.shtml\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：2017年12月30日，北京地铁燕房线开通试运行，这是中国第一条具有完全自主知识产权的轨道交通全自动运行系统。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 张宇 摄 \n</div>\n<p>　　近日，北京地铁运营公司更新了目前地铁的限流情况。当前，北京地铁运营公司常态化限流车站达到73座，京港地铁常态化限流车站数量为23座。北京地铁路网常态化限流车站达96座。城市核心区重点车站早晚高峰均有限流，郊区线路或者末端地铁站大多在早高峰时段限流。</p>\n<p>　　目前常态线路限流车站96座</p>\n<p>　　北京市交通委数据显示，轨道交通路网日均客运量达1030.07万人次，同比增长3.6%；最高日客运量达1294.02万人次。</p>\n<p>　　近日，据北京地铁公司通报，北京地铁公司所辖16条线路常态化限流车站73座。地铁1号线、2号线、5号线、6号线、7号线、8号线、9号线、10号线、13号线等城市核心区线路均有限流车站。其中，5号线是限流车站“大户”，常态化限流车站达到15座。2号线尽管处于核心区，但由于换乘站多，常态化限流车站只有朝阳门一座。</p>\n<p>　　昨天，北京青年报记者从京港地铁获悉，京港地铁客流不断攀升，根据客流情况，部分车站在高峰时段会进行常态化限流，目前常态化限流车站为23座。</p>\n<p>　　目前，全路网限流车站达到96座。地铁工作人员会通过引导、广播等形式加强客流疏导，以保障站内乘客的安全。</p>\n<p>　　CBD周边车站早晚高峰常限流</p>\n<p>　　北京地铁的限流模式从一定程度上能反映出市民的“职住习惯”。</p>\n<p>　　10号线、5号线、八通线限流车站密集。5号线天通苑北、天通苑、天通苑南、立水桥北等，从北向南超过15座车站限流，乘客每天需要排队进站。6号线、八通线的东部车站也存在同样情况。</p>\n<p>　　地铁10号线的限流车站也很密集，达到10座。但其限流时间则集中在晚高峰，主要是CBD周边区域，如国贸、金台夕照等车站的限流模式为早高峰和晚高峰或者是单独的晚高峰限流。1号线四惠站的限流时间最晚，能延续到晚上8点。地铁14号线金台路、望京等商业区、写字楼密集的车站也在晚高峰限流。</p>\n<p>　　地铁大兴线限流车站则达到11座，天宫院以北车站在早高峰7点至9点常态化限流。</p>\n<p>　　调整多条郊区线路运力</p>\n<p>　　地铁昌平线、亦庄线、八通线等郊区线路基本都在早高峰限流。限流时间集中在6点半左右，比核心区线路早一个小时。对此，北京地铁公司调整了多条郊区线路的运力。</p>\n<p>　　1月2日开始，亦庄线早高峰8点前，列车运行间隔由5分25秒缩至4分30秒，运力提高20.4%；早高峰8点后，列车运行间隔由5分25秒缩至5分钟，运力提高8.3%。房山线早高峰7点40分前，列车间隔由4分40秒缩至4分30秒，运力提高3.7%；7点40分后，列车间隔由5分10秒缩至4分40秒，运力提高10.7%。</p>\n<p>　　文/本报记者 刘��</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/61737e65-607a-4692-9bef-7c817b0c6e65.jpg',NULL,1642,'2018-01-06 11:05:00','2018-01-06 14:47:13','66',0),(3116,'辽篮将迎战黑马江苏 郭艾伦要肩负盯对方外援重任','王硕','沈阳日报','<p><strong>　　盯住布鲁克斯！</strong></p>\n<p>　　上轮掀翻排名第一的浙江广厦队之后，辽宁男篮已经获得五连胜。今晚，他们在主场又将迎来一场硬仗，对手是本赛季最大的黑马江苏肯帝亚男篮。</p>\n<p>　　江苏队目前位列辽篮之后，暂列积分榜第三位。该队的小外援布鲁克斯高居得分榜第二位，拥有超强的个人能力，他将是辽篮的重点盯防对象。</p>\n<p>　　老牌劲旅江苏队本赛季强势复苏，该队的引援非常成功，原广东队的李原宇，原吉林队的柳伟，原上海队的吴冠希，原广州队的史鸿飞，这些在原球队的边缘人，本赛季到了江苏队之后，都打出了职业生涯的最佳表现。除了内援，江苏队的外援引进非常成功，尤其是小外援马尚・布鲁克斯。本赛季迄今，布鲁克斯场均轰下38.1分，在得分榜上仅排在新疆队“劳模”亚当斯之后，不过布鲁克斯场均出场时间却比亚当斯少了将近3分钟。在效率榜上，布鲁克斯也以37.14的效率值，仅次于深圳队大外援萨林杰。此外，布鲁克斯场均还能贡献8.3个篮板，6.1次助攻和2.3次抢断，数据相当全面。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　有意思的是，布鲁克斯本赛季截至目前只缺席了一场比赛，那就是江苏队主场与辽篮的比赛。两队首回合交锋，辽篮大胜江苏队32分，但那场比赛江苏队让布鲁克斯轮休，没有与辽篮硬拼。今晚的比赛，辽篮如何防守布鲁克斯将成为首要任务。</p>\n<p>　　布鲁克斯攻击手段非常多样，想要彻底盯死不太现实。辽篮可以参考之前与广厦队比赛中对福特森的防守策略，重点切断布鲁克斯和队友的联系，让他尽可能地选择单打。特别是上场防守福特森出色的郭艾伦，本场仍将要肩负起盯防对方核心外援的重任。</p>\n<p>　　从近期表现来看，辽篮取得了一波五连胜，而江苏队却遭遇两连败，尤其是上一轮主场被晋级季后赛几乎无望的吉林队战胜，对球队士气是一个打击。</p>\n<p>　　沈阳日报、沈报融媒记者 马骋 牛莉制图</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,178,'2018-01-05 10:25:00','2018-01-06 14:47:13','74',0),(3117,'北京尾号限行下周轮换 预计周五晚高峰压力大','陈海峰','新京报','<p><strong>　　尾号限行下周轮换周五晚高峰压力大</strong></p>\n<p align=\"center\"><img src=\"upload/image/196252dc-6f24-433c-9a64-f37ec082ba75.jpg\"></p>\n<p>　　新京报讯 (记者郭超)下周一开始，机动车尾号限行将进行轮换，预计周一、周五交通压力较为突出。</p>\n<p>　　气温继续走低 驾车需求增加</p>\n<p>　　昨日，北京市公安交管局发布本周末以及下周工作日交通提示，进入冬季以来，全市路面交通压力始终保持在较高水平。1月8日起，机动车尾号限行将进行轮换，自1月8日至4月8日，星期一至星期五限行机动车车牌尾号分别为：5和0、1和6、2和7、3和8、4和9。轮换后，预计周一早高峰、周五全天交通压力较大。另外，下周将进入“三九天”，驾车出行增多，周五晚间交通压力将更为突出。</p>\n<p>　　具体到路段，预计工作日早高峰期间，东三环南路、远通桥、肖家河桥、中关村北大街、菜户营南路等路段，以及部分学校周边交通压力较大。</p>\n<p>　　此外，近期京藏高速、京承高速进京方向交通压力也呈上升趋势，特别是轻微事故导致拥堵的情况增多，对高速公路和快速路的影响尤其明显。</p>\n<p>　　工作日晚高峰期间，预计东二环、北二环、北四环、将台东路、建外大街、通惠河北路等路段交通压力较大，尾号4和9限行的周五晚高峰，交通压力会更为突出。另外，晚高峰期间视线较差，交通事故时有发生，而事故是导致近期部分道路拥堵的重要原因。交管部门提示市民朋友，晚高峰驾车时应更加谨慎，避免发生事故影响回家的行程。</p>\n<p>　　自驾去雪场建议提前规划路线</p>\n<p>　　近期，周末驾车前往周边雪场的出行需求骤增，远郊滑雪场周边道路将出现短时车流集中的情况，昌平、延庆、顺义、丰台的热门滑雪场的游客较多。交管部门建议去往各滑雪场的市民朋友提前规划好出行地点和路线，关注实时路况，或选择夜场休闲娱乐。</p>\n<p>　　此外，大型商圈方面，蓝色港湾、朝阳北路大悦城、三里屯等商圈周边道路近期容易出现车多行驶缓慢的情况，特别是晚高峰期间，蓝色港湾周边交通压力将有所增加。去往朝阳北路大悦城的市民朋友，可关注实时路况，选择车流相对较小的路段行驶，或乘坐地铁等公共交通工具前往。</p>\n<p>　　值得注意的是，近期天气寒冷，发烧感冒情况较多，工作日期间，友谊医院、儿童医院、儿研所等热门医院周边道路交通压力持续较大。交管部门建议市民朋友出行时关注实时路况，适时绕行热门医院周边道路；前往就诊的市民朋友可选择周边停车场停放车辆，减少停车等候时间，在外出时应注意防寒保暖。</p>\n<p>　　大型活动出行提示：</p>\n<p>　　今日晚间，首钢篮球中心、奥体中心体育馆将分别举办体育赛事；明日和1月11日晚间，五棵松体育馆将举办体育赛事；本周末，朝阳公园将举办文艺演出。此外，国家体育场、顺义国际鲜花港将举办冰雪主题活动。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/196252dc-6f24-433c-9a64-f37ec082ba75.jpg',NULL,612,'2018-01-06 03:32:00','2018-01-06 14:47:13','72',0),(3118,'美国2018年花样滑冰锦标赛华裔男单选手陈巍领先','唐云云','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>圣荷西1月4日电 (记者 刘丹)4日，在美国加州圣荷西举行的2018年花样滑冰锦标赛上，18岁的华裔小将陈巍(Nathan Chen)在男子短节目比赛中无悬念夺得第一。</p>\n<div style=\"text-align:center\"> \n <img alt=\"当地时间1月4日晚，美国加州圣荷西，18岁华裔选手陈巍(Nathan Chen)在2018年美国花样滑冰锦标赛男子短节目比赛中以104.45分获得第一。他是世界上首位、也是唯一能在完成五种四周跳的选手，陈巍是2017-2018年度国际滑冰联盟花样滑冰大奖赛总决赛金牌得主，被视为平昌冬奥会美国队夺取男单金牌的希望。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 刘丹 摄\" src=\"upload/image/87a80d04-63da-4bc8-bca7-caecb2854c19.jpg\" title=\"当地时间1月4日晚，美国加州圣荷西，18岁华裔选手陈巍(Nathan Chen)在2018年美国花样滑冰锦标赛男子短节目比赛中以104.45分获得第一。他是世界上首位、也是唯一能在完成五种四周跳的选手，陈巍是2017-2018年度国际滑冰联盟花样滑冰大奖赛总决赛金牌得主，被视为平昌冬奥会美国队夺取男单金牌的希望。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 刘丹 摄\"> \n <input id=\"d2b0f7f6c6a645b28841f3fce866a147\" name=\"d2b0f7f6c6a645b28841f3fce866a147\" type=\"hidden\" value=\"http://www.chinanews.com/part/561/2018/0105/5403.html\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  当地时间1月4日晚，美国加州圣荷西，18岁华裔选手陈巍(Nathan Chen)在2018年美国花样滑冰锦标赛男子短节目比赛中以104.45分获得第一。他是世界上首位、也是唯一能在完成五种四周跳的选手，陈巍是2017-2018年度国际滑冰联盟花样滑冰大奖赛总决赛金牌得主，被视为平昌冬奥会美国队夺取男单金牌的希望。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 刘丹 摄 \n</div>\n<p>　　当天的比赛共有21名选手登场，陈巍排在第四组第一个亮相，在2分48秒的表演中，他以近乎完美的高难度四周跳征服裁判和观众，以总分104.45分获第一，比第二名亚当・瑞彭(Adam Rippon)的96.52分高出7.93分。</p>\n<p>　　犹他州盐湖城的陈巍是世界上首位、也是唯一能在比赛中完成五种四周跳的选手，他的父母分别来自中国广西和北京。</p>\n<p>　　从3岁开始滑冰的陈巍是2017-2018年度国际滑冰联盟花样滑冰大奖赛总决赛金牌得主，2017年度四大洲花样滑冰锦标赛与2016-2017年度美国花样滑冰锦标赛冠军，被视为平昌冬奥会美国队夺取男单金牌的希望。</p>\n<p>　　当天的男单短节目比赛，还有3名华裔选手出战，17岁的周知方(Vincent Zhou)以89.02分列第五，他也是2017年花样滑冰世界青年锦标赛男单冠军。22岁的吉米・马(Jimmy Ma)以75.28分列第11名，20岁的凯文・岑(Kevin Shum)失误较多，以52.04分排在最后。</p>\n<p>　　5日将进行女子单人滑的短节目比赛，17岁的华裔选手陈楷雯(Karen Chen)的表演令人期待，她在2017年夺得美国花样滑冰锦标赛女单冠军。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/87a80d04-63da-4bc8-bca7-caecb2854c19.jpg',NULL,1525,'2018-01-05 17:00:00','2018-01-06 14:47:13','74',0),(3119,'魏大勋隔空表白贾玲 王博文吐槽陈赫腿粗','唐云云','中国日报网','<p>　<strong>　魏大勋隔空表白贾玲 王博文要抱Angelababy大腿</strong></p>\n<p>　　熟悉魏大勋的粉丝可能都知道他是一个王者荣耀的忠实玩家，而在昨天，王博文却在一场活动上公开爆料魏大勋在游戏中“猪队友”，这是怎么一回事呢？原来，1月4日，腾讯视频《王者出击》走进中国人民大学举办了一场自带buff的校园见面会，魏大勋和王博文出席活动现场，二人“互怼”爆料丝毫不甘示弱。</p>\n<p align=\"center\"><img alt=\"魏大勋隔空表白贾玲 王博文要抱Angelababy大腿\" src=\"upload/image/25c35e0f-9784-4373-9107-a8f0aa3715d1.jpg\"></p>\n<p>　　在活动现场，《王者出击》将节目主题搬到线下，由街舞团带来王者荣耀开场舞，瞬间点燃了现场观众的热情。魏大勋、王博文携各路“英雄”前来助阵。见面会上，两人首先接受了主持人的现场版快问快答，各种犀利问题轮番上阵。魏大勋在扬言想要和贾玲组“爱情买卖CP”的同时又爆料她是“大胖忽悠”，而另一边被问到“抱陈赫大腿什么感觉”的王博文则耿直吐槽“陈赫腿粗”，引得现场阵阵爆笑。在互动环节中，除了给粉丝抽奖发福利外，两人还毫无保留的释放了自己的“综艺之魂”，魏大勋与粉丝现场上演双人舞，甜蜜互动惹得现场观众尖叫连连；而王博文则与粉丝展开了一场切水果大比拼，手脚并用的他还是没能取得比赛的胜利・・・・・・・两人与粉丝甜蜜互动，现场气氛十分活跃。</p>\n<p>　　不过，最让人期待的还是“王者出击校园打call战”的颁奖环节。这场由腾讯视频发起的校园创意投放大赛自开展以来就吸引了来自全国各地高校学生的积极参与，四川高校的巨幅英雄海报覆盖打造的“王者天梯”、浙江工业大学的“王者出击”校园寻宝活动、河南高校的“王者出击巅峰食堂”、江苏高校的 “3D峡谷出击”……高校学生纷纷组队脑暴，拿出看家本领，利用节目中的素材，将节目内容以趣味性十足的形式展示在充满活力的校园中，为《王者出击》打 call。</p>\n<p>　　见面会现场，王博文对此次“王者出击校园打call战”参赛团队的创意作品大加赞美，还忍不住调侃“这些都能放在楼梯上，脑洞也是大了”。最终，四川高校的“王者天梯”创意投放获得了评委们的一致青睐，成为此次“王者出击校园打call战”的冠军，王博文代表《王者出击》节目组为胜利团队进行了颁奖。</p>\n<p>　　这档让高校纷纷打call的节目《王者出击》是由腾讯视频自主研发的王者荣耀实景真人对抗赛，节目自播出以来就受到广泛关注。作为“游改综”的先行者，《王者出击》在还原游戏场景上的表现可圈可点，游戏里水晶、防御塔、红蓝buff等各种元素均被高度还原，集结Angelababy、陈赫等一众人气明星兼游戏忠粉组成四支战队，在“王者峡谷”进行厮杀，高度的场景还原营造出身临其境的共鸣感，带来了真正的沉浸式观看体验。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/25c35e0f-9784-4373-9107-a8f0aa3715d1.jpg',NULL,1907,'2018-01-05 15:23:00','2018-01-06 14:47:13','68',0),(3120,'中国迎无人便利店浪潮 或以超过日欧美速度发展','陈海峰','参考消息网','<p><strong>　　日媒：中国无人便利店浪潮已至 或以超过日欧美速度发展</strong></p>\n<p>　　参考消息网<strong>1月6日报道 </strong>日媒称，中国的零售店正在迎来无人化的浪潮。随着手机支付的普及，没有店员的便利店相继开业。家电连锁巨头苏宁云商和阿里巴巴集团也已涉足其中，显示出爆炸式增长的迹象。在期待因人工费上涨和电商崛起而陷入苦战的零售店卷土重来的同时，便利性和技术上的缺陷等普及面临的课题仍然存在。</p>\n<p align=\"center\"><img src=\"upload/image/bd2c034a-8059-499b-becc-a472fa6491a6.jpg\"></p>\n<p>　　资料图片：在“F5未来商店”通过触摸屏选择商品并结账后即可拿到商品(广州)。(图片来源：《日本经济新闻》网站)</p>\n<p>　　据《日本经济新闻》网站1月2日报道，广州市出现了无人便利店“F5未来商店”。在约30平方米的店内，实际商品一件也没有摆放。店内只有安装在墙上的触摸屏以及餐饮区。</p>\n<p>　　记者向住在附近的家庭主妇李女士(40岁)请教了购买方法。首先，在触摸屏上从饮料、点心和快餐等约100种商品之中选出想要的，然后选择结账，扫描屏幕生成的二维码完成支付。</p>\n<p>　　之后，在店后面的仓库里，由机械选出商品送至取物口。从结账到领取需要3分钟左右。“商品数量还很少，但不用在收银台前排队，也没有态度糟糕的店员，所以我经常来，”李女士笑着说。</p>\n<p>　　未来商店由佛山市欧谱电子商务有限公司运营。该公司表示，自2014年起进行了多次试验，2016年底正式启动运营。在广州和深圳共有5家店，争取尽快达到100家。</p>\n<p>　　报道称，在中国，进入2017年后，初创企业的无人便利店不断开店。中山市宾哥网络科技有限公司在上海等地开设了约200家名为“缤果盒子”的店铺。除了广东省之外，在北京和辽宁省等地，新开无人便利店的趋势也在扩大。</p>\n<p>　　中国的支付基础设施日趋完善，使得初创企业的商业拓展更加顺利。</p>\n<p>　　此外，中国的大型企业也在涉足无人商店。苏宁8月在江苏省南京市开设了无人服装店“Biu”。截至2017年11月，已在上海和重庆等地增加至5家。只要下载并注册苏宁金融APP，通过出口处的摄像头进行面部识别，即可自动完成结算。此外，阿里巴巴在浙江省杭州市运营的无人超市“淘咖啡”也是在出口处读取商品进行结账。</p>\n<p>　　报道称，中国无人零售店普及的背景是，实体零售店受到电商挤压，正面临顾客减少的困境。实体店因房租和人工费上涨，盈利日趋严峻。既节省空间又能降低人工费的无人店受到关注，其优点包括顾客在收银台前的排队压力减轻，同时运营商还不会收到假币。</p>\n<p>　　在美国，亚马逊正在进行“亚马逊尝试”(Amazon Go)无人零售店的实验。日本JR东日本也将利用人工智能(AI)启动无人商店的试验，零售店的无人化浪潮正在波及全世界。中国在技术等方面处于劣势，但在实用化方面领先一步。</p>\n<p>　　报道称，中国的零售店由于店员服务质量原本就不高等原因，消费者在购物时追求便利性与合理性的趋势突出。同时，对于网上支付的抵触感也很低，无人店今后或将以超过日本和欧美的速度发展。</p>\n<p>　　目前中国的便利店数量多达约10万家。但是，便利店主要集中于大城市周边，在地方城市仍是个人商店居多。因此，预计今后无人零售店的开店余地依然很大。据调查公司中商产业研究院预测，2017年无人零售店的市场规模为100亿元，但预计5年后(2022年)将扩大至9500亿元。</p>\n<p>　　报道称，不过，无人便利店的普及仍面临技术层面的课题。记者实际进入无人便利店购物，发现部分想买的商品买不到。广州市内的一家无人便利店，收银台前的入口门却敞开着。虽然设置了监控摄像头，但是防偷盗对策并不完善。</p>\n<p>　　其他调查公司的调查显示，七成的利用者对无人便利店感到满意，回答不满意的人中，83%的人表示“商品数量较少，找不到想买的商品”。34%的人回答“没有自己喜欢的品牌”，还有22%的人回答“商品破损、品质差”。</p>\n<p>　　报道称，中国企业的惯例是“先做起来有问题再改善”，而不是从一开始就追求完善的服务。今后，预计无人便利店将逐渐得到改善。其中，能否采取有效的防盗对策以及迅速应对顾客的不满将成为企业在黎明期市场掌握主导权的决定性因素。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/bd2c034a-8059-499b-becc-a472fa6491a6.jpg',NULL,488,'2018-01-06 03:42:00','2018-01-06 14:47:13','66',0),(3121,'多重因素助推涨势 人民币今年“稳”字当头','陈海峰','人民日报海外版','<p>　　<strong>人民币今年“稳”字当头(锐财经)</strong></p>\n<p>　　1月5日，人民币兑美元中间价报6.4915，再次突破6.5。近期，接连升值的人民币引发市场关注，未来的行情走势也成为热议话题。专家表示，这一阶段人民币兑美元的升值受多重因素影响，不过，结合2017年人民币的整体走势来看，其背后反映的是中国良好的经济基本面和乐观的市场预期。未来在经济结构转型和市场化推动下，人民币汇率的基本稳定值得期待。</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：山西太原，银行工作人员清点货币。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 张云 摄\" src=\"upload/image/4de2ed3f-dc05-44de-ae67-5ba4944f4bf4.jpg\" title=\"资料图：山西太原，银行工作人员清点货币。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 张云 摄\"> \n <input id=\"c2b2720f6aee41788b50fb7cf4d6af9a\" name=\"c2b2720f6aee41788b50fb7cf4d6af9a\" type=\"hidden\" value=\"http://www.chinanews.com/part/561/2017/1214/5054.html\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：山西太原，银行工作人员清点货币。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 张云 摄 \n</div>\n<p>　　多重因素助推涨势</p>\n<p>　　近期人民币兑美元中间价的高峰出现在1月3日。当日，中国官方报人民币兑美元汇率中间价较前一交易日上调159个基点，突破6.5报6.4920，创下2016年5月以来的最高。</p>\n<p>　　这一波涨情始于2017年12月中旬前后，几乎同一时间段，美元指数从94上方下滑至2018年1月3日的92以下。有分析指出，人民币“跨年”大涨的原因一是外部美元指数走低；另外，2017年12月以来居民和企业结汇增加、外汇市场供求持续改善，二者助推了人民币走强。</p>\n<p>　　不过，中国国内经济的作用不容低估。2017年前三季度，中国经济增速达到6.9%，中国制造业PMI全年处在扩张区间，供给侧结构性改革等也取得良好效果。</p>\n<p>　　对外经贸大学金融学院副院长范言慧向本报记者分析指出，人民币汇率升值最根本的原因还是在于中国经济的表现。2017年中国经济复苏态势明显，总体表现不错，汇率则是经济基本面的反映。另一方面，中国金融状况的总体稳定，也对近期人民币升值起到了支撑作用。</p>\n<p>　　中国人民银行参事盛松成也表达了同样的观点。他认为，除兑美元汇率上涨外，人民币兑大多数货币也都是在升值，主要影响因素还是中国国内经济。人民币汇率近期的升值主要是当前中国经济金融形势的反映，同时受到市场预期的影响。</p>\n<p>　　市场看好韧性增强</p>\n<p>　　实际上，不光年末走强，2017年全年，人民币兑美元稳中有升，打破了此前连续3年的贬值趋势。</p>\n<p>　　早在2017年初，许多机构和分析人士并不看好人民币汇率走势，预测人民币兑美元汇率破7的不少，然而实际情况却出乎意料。2017年，在美联储加息等外部压力下，人民币兑美元汇率基本维持在6.9-6.5的区间。</p>\n<p>　　范言慧认为，人民币在2017年的表现正是充分反映了经济基本面、市场供求以及市场预期的作用。而从长期来看，中国经济结构的顺利调整和持续改善也将对未来人民币汇率的弹性空间形成支撑，人民币的承压性会不断增强。</p>\n<p>　　中国人民大学财政金融学院副院长赵锡军则认为，中国经济和国际收支情况是影响人民币兑美元汇率的基本因素。“总体来看，2017年中国的国际收支和经济表现还是非常强劲的，目前的预期也不错，这是一个基本的支撑。”</p>\n<p>　　国家外汇管理局局长潘功胜也在此前表示，近一段时间人民币汇率比较稳定，而且能够看到汇率是市场化推动的，央行已经基本上退出了常态化干预，十九大以后人民币汇率会有更加稳定的基础。</p>\n<p>　　双向波动仍是常态</p>\n<p>　　回到近期表现，人民币升值会延续吗？2018年行情会怎样？</p>\n<p>　　“最近一两年来，我们始终强调保持人民币汇率的基本稳定。”盛松成说，汇率大幅升值不利于保持中国在世界贸易中的份额，中国可能错失当前全球经济复苏的红利。而汇率大幅贬值不仅加大资本外流压力，通过贬值促进出口的做法也得不偿失，此外还会有损大国形象，甚至引发贸易摩擦。</p>\n<p>　　在范言慧看来，中期和短期内，人民币不太有大涨大跌的可能性，更可能在现有水平下保持稳定态势。“2018年的升值空间不会太大，人民币兑美元汇率可能在6.0-6.9之间双向波动，这也是中国经济可以承受的范围。”</p>\n<p>　　赵锡军也强调，今年人民币的双向波动会日益明显。他认为，在2018年美联储预期会加息3次，缩表进程也会推进，此外美国减税效应的预期也会叠加，这些都会使得资金流动出现波动并对外汇市场产生影响。而中国在今年也会有如供给侧结构性改革、风险控制等新的变化。国内外因素的共同作用，都将使得人民币汇率在今年的波动情况更为复杂。</p>\n<p>　　不过专家同时指出，近些年人民币汇率框架的改革本身就有助于人民币汇率的双向调整。双向波动也是汇率弹性增强的表现，中国正在扩大对汇率波动的容忍度。未来一段时间，人民币的双向波动和基本稳定极有可能上演。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/4de2ed3f-dc05-44de-ae67-5ba4944f4bf4.jpg',NULL,389,'2018-01-06 06:44:00','2018-01-06 14:47:13','67',0),(3122,'银监会：商业银行委托贷款不得从事债券期货等投资','史建磊','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>1月6日电 据中国银行业监督管理委员会网站消息，银监会近日发布《商业银行委托贷款管理办法》（以下简称《办法》）。《办法》明确，委托贷款资金不得从事债券、期货、金融衍生品、资产管理产品等投资，不得作为注册资本金、注册验资，不得用于股本权益性投资或增资扩股等。</p>\n<div style=\"text-align:center\"> \n <img alt=\"12月14日，山西太原，银行工作人员清点货币。当日，来自中国外汇交易中心的数据显示，人民币对美元汇率中间价报6.6033，较前一交易日上涨218个基点。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 张云 摄\" src=\"upload/image/65f3f46e-3cf6-4368-870c-62d8fe219bc5.jpg\" title=\"12月14日，山西太原，银行工作人员清点货币。当日，来自中国外汇交易中心的数据显示，人民币对美元汇率中间价报6.6033，较前一交易日上涨218个基点。<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 张云 摄\"> \n <input id=\"c2b2720f6aee41788b50fb7cf4d6af9a\" name=\"c2b2720f6aee41788b50fb7cf4d6af9a\" type=\"hidden\" value=\"http://www.chinanews.com/part/561/2017/1214/5054.html\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：银行工作人员清点货币。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 张云 摄 \n</div>\n<p>　　《办法》明确委托贷款业务是商业银行的委托代理业务，商业银行作为受托人，按照权责利匹配原则提供服务，不得代委托人确定借款人，不得参与贷款决策，不得提供各种形式担保；委托人应自行确定委托贷款的借款人，对借款人资质、贷款项目等进行审查，并承担委托贷款的信用风险。</p>\n<p>　　《办法》对委托贷款资金来源提出合法合规性要求，商业银行不得接受受托管理的他人资金、银行的授信资金、具有特定用途的各类专项基金、其他债务性资金和无法证明来源的资金等发放委托贷款。</p>\n<p>　　《办法》体现了金融服务实体经济的总体要求，明确规定委托资金用途应符合法律法规、国家宏观调控和产业政策，资金不得用于生产、经营或投资国家禁止的领域和用途，不得从事债券、期货、金融衍生品、资产管理产品等投资，不得作为注册资本金、注册验资，不得用于股本权益性投资或增资扩股等。</p>\n<p>　　《办法》要求，商业银行将委托贷款业务与自营业务严格区分，加强风险隔离和业务管理。商业银行应建立、完善委托贷款管理信息系统，确保该项业务信息的完整、连续、准确和可追溯。</p>\n<p>　　《办法》明确，商业银行违规办理委托贷款业务的，由银监会或其派出机构责令限期改正。逾期未改正，或者其行为严重危及商业银行的稳健运行、损害客户合法权益的，银监会或其派出机构将对违规办理委托贷款业务的商业银行依法采取相应监管措施或实施处罚。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/65f3f46e-3cf6-4368-870c-62d8fe219bc5.jpg',NULL,1564,'2018-01-06 13:17:00','2018-01-06 14:47:13','67',0),(3123,'中国首创技术突破瓶颈 有望破解废水处理世界性难题','陈海峰','经济日报','<p><strong>　　处理难降解废水有望不再是世界性难题</strong></p>\n<p align=\"center\"><img src=\"upload/image/c450b292-4e81-4fa5-a324-f016ef055173.jpg\"></p>\n<p>　　本报记者 喻 剑摄</p>\n<p>　　日前，在“中广核技电子束处理工业废水技术科技成果发布会暨项目签约仪式”上，中广核核技术发展股份有限公司(简称中广核技)与清华大学联合宣布：“中国首创、世界领先”的电子束处理工业废水技术已拿到科技成果鉴定证书，正式完成由中国核能行业协会组织的科技成果鉴定。双方还将联合成立“电子束及环境技术应用联合研究中心”，共同推动中国电子束技术在环境保护领域的基础研究、科技成果转化及产业化推广等工作。这标志着中国电子束技术首次实现真正意义上的产业化应用</p>\n<p>　　“目前，电子束处理工业废水技术已臻于成熟，具备大规模产业化应用的条件，标志着我国工业废水深度处理技术实现了历史性重大突破，是中国乃至世界工业废水处理领域的一次技术飞跃。”中广核核技术发展股份有限公司董事长张剑锋介绍，他们已陆续开展电子束辐照技术研究、废水处理工艺革新、关键装备制造、技术规范编制等相关工作，并建立了国内首个电子束处理工业废水示范项目。</p>\n<p>　　清华大学教授、中国工程院院士钱易表示，电子束处理工业废水技术结合生物处理工艺具有独特优势，在环境污染治理领域有着广阔应用前景，特别是在难降解废水和工业废水深度处理方面，将推动中国工业废水处理领域向更高标准和更环保的方向发展。</p>\n<p>　　中国首创技术突破瓶颈</p>\n<p>　　有望破解废水处理“世界性难题”</p>\n<p>　　不久前，中国核能行业协会组织了9名院士和相关领域知名专家，对电子束处理工业废水技术进行了科技成果鉴定，并得出结论：该技术成果实现了电子束处理工业废水技术的自主创新，达到了国际领先水平，具有广阔的推广前景，将产生重要的环境、经济和社会效益。</p>\n<p>　　鉴定委员会认为，该技术中的水处理专用电子加速器、辐照反应器整体装置以及电子束处理工业废水工艺技术，属于国内首创，突破了当前难降解废水处理的技术瓶颈，一旦实现大规模产业化，可大幅度提高我国工业废水治理水平。该技术结合生物技术深度处理工业废水工艺，成本更低，净化程度更高，可实现废水的高标准排放或者中水回用。而且，针对难降解有机物质具有其他技术难以比拟的优势，有望解决难降解废水处理“世界性难题”。</p>\n<p align=\"center\"><img src=\"upload/image/790d02b3-6e0d-4c01-b914-1c8a8d3694e5.jpg\"></p>\n<p class=\"pictext\" align=\"center\">图为项目签约仪式现场。</p>\n<p>　　清华大学核能与新能源技术研究院副院长王建龙介绍，实验证明，该技术广泛适用于印染、造纸、化工、制药等各行业废水处理以及水质复杂的工业园区废水处理，并可用于医疗废弃物、抗生素菌渣等特殊危险废物的无害化处理。随着技术改进和研发综合解决方案，未来还可应用于污水中无机重金属离子的去除，以及固体污泥、工业废气、环境突发应急、医用污水等领域。</p>\n<p>　　“电子束处理工业废水技术具有安全、环保的特点。”王建龙表示，在安全方面，该技术虽属于核技术应用范畴，但电子束处理废水技术具有两个特点：一是不使用放射性核素，其核心装置电子加速器通过电源控制开启和关闭，断电后不产生任何辐射；二是电子加速器实现了安全联锁设计，确保在出现任何意外状况时，能够及时、快速断电，实施保护措施。此外，工业废水经过电子束辐照处理后，并没有放射性，对环境没有任何影响。</p>\n<p>　　在环保方面，电子束辐射装置在全球范围内的工业生产、科学研究中已广泛应用，既有可供借鉴的成熟经验，也有现行的防护与运行法规。而且，电子束处理工业废水技术在处理废水过程中，不会产生和排放有毒害副产物，不会对周边居民的环境与生活造成不良影响。</p>\n<p>　　产业化应用获突破</p>\n<p>　　国际首座电子束辐照处理印染废水项目建成</p>\n<p>　　目前，电子束辐照技术作为处理难降解废水的一种重要解决手段，被国际原子能机构(IAEA)列为21世纪和平利用原子能的主要研究方向，已成为国际新型环保技术的研究热点和重要发展趋势。2017年3月，中广核技在浙江省金华市的恒昌集团建成国内首座电子束辐照处理印染废水示范项目。</p>\n<p>　　“这是目前国际上唯一在运行的同类装置。在经济性方面，电子束处理工业废水技术具有很强市场竞争力。”张剑锋介绍，“以示范工程为例，企业的原处理工艺只能达到接入城市污水处理厂的纳管标准。如今，采用电子束辐照加混凝沉淀处理工艺，出水水质能够达到直接排放标准要求。如果实现直接排放，与原工艺相比较，每吨废水处理费用可节省约1.4元。按照每天处理1万吨废水计算，设备每年运行350天，一年可节约490万元左右。同时，印染废水经过电子束辐照深度处理后，能够实现部分回用，提高经济效益”。</p>\n<p>　　随着与恒昌集团《电子束处理废水项目销售协议》的签署，中国电子束技术在工业废水处理应用中迈出实质性步伐，首次实现真正意义上的产业化应用。恒昌集团环保事业部总经理王勇表示，基于示范项目半年多的稳定运行，处理效果显著：出水COD在60mg/L以内，色度10倍以下，不返色，无二次污染，出水水质稳定，达到直排标准。“我们将示范项目直接转为产业化，成为全国首家正式使用电子束处理工业废水技术的企业。”王勇说。</p>\n<p>　　“随着中国水环境治理的标准和要求升级，水环境治理需求也逐步释放。”张剑锋认为，企业污水处理设施改造升级和园区废水专业化集中处理，是排污企业适应我国日益提升的环保标准的两个主要途径，也是我国当前应对环境污染所采取的有利治理保护措施，更是未来企业和工业园区的发展方向。</p>\n<p>　　科技成果转化提速</p>\n<p>　　构建多样化商业模式</p>\n<p>　　对于中广核技与清华大学将联合成立“电子束及环境技术应用联合研究中心”，共同推动中国电子束技术在环境保护领域的基础研究和产业化推广等相关工作，张剑锋表示，清华大学拥有电子束辐照及其他环保技术的基础研究优势，中广核技拥有完整的以电子加速器为核心的核技术应用高端产业链，具有产业化优势。此次双方进一步加深合作，将建立高新环保技术创新平台和科技成果产业化基地，有望加快科技成果转化和应用，提升我国环境污染治理水平。</p>\n<p>　　“我们具有良好的合作历史，共同承担并完成了国家‘863’高技术课题和科技部国际合作专项等重大科研项目，在电子束处理工业废水技术的核心装备和工艺方面取得了重大科研成果。”王建龙介绍，2017年6月15日，双方还联合发起编制了电子束处理印染废水和造纸废水技术规范，这将为该技术成果转化和产业化提供科学标准依据，奠定良好推广基础。</p>\n<p>　　为此，一方面，中广核技通过加强与清华大学的合作完善优化技术能力，推动科技成果快速转化；另一方面，也开展探讨多种商业模式的可行性，谋求从单纯的技术提供商向综合服务商转变。例如，他们结合抗生素制药、皮革制造等行业特点，定制行业解决方案；寻求与工业污水处理领域的大型环保公司合作，以技术换市场；加强与排污企业沟通，对其现有设施进行改造并接受委托运营等。</p>\n<p>　　国内趋于严苛的环保标准，在为工业废水治理带来新要求的同时，也带来了庞大的市场需求。按照2014年我国工业废水排放量205.3亿吨计算：每天废水排放量超过5600万吨，每套电子束辐照装置处理能力为5000吨/天，按照5%的工业废水采用电子束辐照技术处理计算，这需要电子束辐照装置600套，对应市场规模近100亿元。</p>\n<p>　　“该技术在环境污染治理领域有着广阔应用前景，特别是在难降解废水和工业废水深度处理方面。我们将通过构建多样化的商业模式，促进污染企业进行清洁化改造，加速实现国家‘十三五’规划中‘实施工业污染源全面达标排放计划’，有效缓解中国工业废水污染问题。”张剑锋表示。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/790d02b3-6e0d-4c01-b914-1c8a8d3694e5.jpg',NULL,524,'2018-01-13 05:17:00','2018-01-06 14:47:13','66',0),(3124,'“前任”系列开启影剧联动模式 网剧曝光个性海报','魏巍','环球网','<p>　　<strong>《嗨！前任》开播 上演“虎系女友”驯夫记</strong></p>\n<p>　　由田羽生总监制、苏筱晨执导，姚远、曾梦雪、罗米、郭欣禹主演的爱情喜剧《嗨！前任》今晚20:00正式上线。开播在即，片方公布一组单人海报与剧照。孟云、艾达、麦可、余飞四位人物形象与风格独特的漫画背景融合，配色大胆、人物鲜明，为剧集增添时尚色彩。</p>\n<p align=\"center\"><img src=\"upload/image/eea60c9b-aba4-499e-b108-4dc5469d1dd3.jpg\"></p>\n<p>　　个性海报曝光 影剧联动火热开启</p>\n<p>　　最新曝光的单人海报中，饰演艾达的曾梦雪身着大红色外套，半熟女性气质扑面而来；减龄十足的丸子头与嘴角的两个梨涡又凸显出古灵精怪；饰演麦可的罗米皮衣搭配麻花辫，耍酷中透漏出可爱；饰演孟云的姚远与饰演余飞的郭欣禹神情间神采奕奕，开朗洒脱的性格自然流露。</p>\n<p>　　网剧《嗨！前任》由“前任系列”电影原班幕后班底打造。“前任系列”电影《前任3：再见前任》自12月29日上映以来，不仅不断刷新票房纪录，更是斩获2018年元旦档票房冠军，势头愈来愈猛。据悉，网剧《嗨！前任》的播出预示着“前任系列”影剧联动模式的正式开启。“前任系列”电影在影院火热公映，“前任系列”网剧在视频平台上线，“前任系列”热度大增，不禁让人对今晚播出的网剧更加期待！</p>\n<p>　　姚远曾梦雪变情侣 上演“虎系女友”驯夫记</p>\n<p>　　《嗨！前任》中，姚远、郭欣禹将接棒韩庚、郑恺，重新演绎“孟云”“余飞”这对好兄弟。孟云在剧中依然是有着众多前任的“都市浪子”形象。虽有固定女友，但依然同余飞一起，浪迹于众多美女之间。艾达也不甘示弱，化身“虎系女友”，与孟云的众多前任针锋相对，并上演花式逼婚！但当孟云最爱的前任麦可出现时，二人逐渐对这段感情产生不安与怀疑。在繁华都市下追逐真爱的他们，又将如何处理这段感情？　　　　　　　　　　　　</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/eea60c9b-aba4-499e-b108-4dc5469d1dd3.jpg',NULL,1575,'2018-01-05 15:28:00','2018-01-06 14:47:13','68',0),(3125,'晓风棋牌：微信小程序开启棋牌游戏行业下一波热潮','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>1月5日电 2017年最后两天，元旦前夕，腾讯为广大移动端用户和玩家们带来了“微信小游戏”。 如果你每天使用微信，一定会发现，《跳一跳》这款 H5游戏不知不觉中已经占领了微信朋友圈的头条位置，其火热程度完全不弱于前两年一款类似玩法的现象级游戏\"Flappy Bird\"。《跳一跳》虽然操作简单，难度却不低，朋友圈也因此掀起了一股比拼跳格子数目的热潮。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/1084a7ae-2d78-4fdc-875c-553047cd630e.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　《跳一跳》如腾讯预期取得巨大成功后，微信很快乘胜出击，在几天内，接连推出了多达15款微信小游戏供用户选择。这些小游戏都是通过微信小程序实现的，用户只需下载最新的微信6.6.1版，就可以顺利体验到。</p>\n<p>　　这里面有一个有趣的现象，15款小游戏中接近一半是棋牌游戏。既有《欢乐斗地主》、《天天德州》等全国性的棋牌游戏产品，也不乏《广东麻将》、《四川麻将》这类地方棋牌游戏的身影，种类不多，但涵盖面广，腾讯当然也看到了这两年地方棋牌游戏的火热，入局的意思很明显。</p>\n<p>　　类似《跳一跳》、《欢乐坦克大战》这类都属于轻度H5游戏，这类游戏的特点是上手简单、容易操作，同时又有一定的难度，很容易短期内让人沉浸其中，但是低门槛也造成这类游戏只能作为快餐文化的一部分，用户黏性低，长期价值不高，更缺乏合理的套现能力。</p>\n<p>　　为了保证稳定和流畅，微信对小程序游戏大小进行了严格的限制，这也间接限定了游戏类型。有哪些内容不复杂，但用户粘度有非常大的游戏类型呢，这是微信方需要考虑的问题。答案到这里显然呼之欲出，棋牌就是其中那为数不多的最优解之一。如果再加上棋牌游戏自身表现出来的强大套现能力，与微信小游戏简直“天作之合”。</p>\n<p>　　H5游戏《跳一跳》的火爆和微信小游戏的开放，使得棋牌游戏从业者们看到了行业新的爆发增长点。目前上线的这几款H5棋牌游戏既包含有传统的金币对战模式，又融入了这两年大热的房卡游戏模式，很难说腾讯不是瞄准了现今棋牌游戏爆发的巨大红利。</p>\n<p>　　作为棋牌游戏系统之一，H5棋牌游戏一直是晓风棋牌着力打造的研发方向之一。凭借着过硬的技术积累，晓风棋牌游戏系统帮助过大量客户在市场转型期快速抓住热点，获取首波红利。</p>\n<p>　　经过4年的迭代，晓风棋牌游戏系统迄今已发展到最新的SGS 3.0版本。SGS棋牌游戏系统由安卓APP、苹果APP、游戏玩法、游戏推广官网、软件后台管理模块5个部份构成，通过棋牌游戏APP与晓风SAAS玩法库相连接，帮助全球棋牌游戏公司实现本地化、多玩化、个性化的棋牌系统、玩法设计、开发、运维服务。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/1084a7ae-2d78-4fdc-875c-553047cd630e.jpg',NULL,509,'2018-01-05 15:02:00','2018-01-06 14:47:14','82',0),(3126,'中国率先进入无现金社会 手机当钱包令美国人惊讶','罗攀','参考消息网','<p>　　<strong>美媒：中国率先进入无现金社会 手机当钱包令美国人惊讶</strong></p>\n<p align=\"center\"><img src=\"upload/image/732e86d7-7fdd-4ce1-ae84-5ab1e0cc5959.jpg\"></p>\n<p>　　资料图片：2017年11月29日，在合肥瑶海区胜利智能农贸市场，市民在扫码支付。新华社发</p>\n<p>　　参考消息网<strong>1月6日报道 </strong>美媒称，据估计，2016年美国移动支付规模为1120亿美元，相比之下，中国为9万亿美元(约合人民币58万亿元)。移动支付成为很多中国人支付各种商品和服务的手段，而手机支付渐增的一些连带影响正浮出水面。</p>\n<p>　　美国《华尔街日报》网站1月5日报道，在北京，一位乞讨者面前的纸板上写着“推荐使用微信支付”，这是当下趋势的一个写照。</p>\n<p>　　报道称，通过微信之类手机服务进行的支付风行全中国。在赢得网购支付的最初阵地之后，移动支付进军实体店支付领域，并迅速成为很多中国人支付各种东西的手段。其中包括支付小额个人债务。</p>\n<p>　　报道称，移动支付在中国日常商业活动中发挥着越来越大的作用。正如科技公司推动零售、汽车和媒体行业的创新一样，从中国互联网公司的成功可以一窥科技公司推动金融创新的未来情景。</p>\n<p>　　据Forrester Research的一项估计，美国2016年移动支付规模为1120亿美元。而据中国公司艾瑞咨询，2016年中国移动支付规模达9万亿美元。</p>\n<p>　　报道称，对于中国的互联网公司来说，他们获得的回报不仅仅是从商家处赚取的交易费，该费用通常为交易额的0.6%。公司还能收集到消费者数据，通过这些数据可将他们的应用程序转化为营销平台，用于提供从共享单车到旅行等多项扩展服务。</p>\n<p>　　报道认为，手机支付渐增的一些连带影响正浮出水面。这些支付尚未被纳入中国央行清算系统，这使中国货币相关部门追踪资本流动轨迹以及监控洗钱和欺诈的难度增大。中国央行已下令建立一个新的支付清算平台，要求非银行金融机构在2018年夏季之前将网络支付业务接入该平台。</p>\n<p>　　消费者还通过智能手机接收有关贷款、投资和其他金融产品的更多推介。据中国央行数据，2017年前8个月，中国的短期消费信贷同比增长160%。一些分析人士认为，部分原因是借贷便利度的增加。</p>\n<p>　　与此同时，中国人也在减少传统现金(比如硬币和纸币)的使用。据中国央行的一份报告，2016年，中国消费者的传统现金消费额约为人民币66万亿元(约合10万亿美元)，这一数据在两年内下降了约10%。</p>\n<p>　　阿里巴巴开辟了移动支付的道路。通过该公司开设的网上商城，商家将商品出售给消费者。十几年前，阿里巴巴效仿美国公司、现在的PayPal Holdings (PYPL)，推出了名为支付宝的委托付款服务。该系统可在买家收到所购商品之前代管付款。这一服务迅速走红。之后，支付宝逐渐发展成网络和移动支付服务商，并在2013年超越PayPal成为全球最大移动支付平台。</p>\n<p>　　同年，腾讯将一个移动支付系统与其广受欢迎的微信即时通讯应用关联在一起。根据iResearch的数据，目前微信支付在中国移动支付市场占据的份额为40%左右，支付宝的份额为54%。</p>\n<p>　　报道称，这两款应用的使用方式类似。人们将其银行账户与应用关联，之后在购物时就可以通过扫描商家的二维码或者让商家扫描自己的二维码进行支付。人们还可以通过点击微信或支付宝上的一个图标进行转账。</p>\n<p>　　最近在台湾的一个夜市，来自上海的纺织品销售员Manni Cheng用现金购买了烤大虾，但她随后注意到摊主接受支付宝付款。于是她要回现金，用手机付了款。</p>\n<p>　　Cheng说，她喜欢支付宝，因为可以跟踪自己的支出情况，而且不需要随身携带大量现金。她在支付宝中的余额转入了该应用内的货币市场基金。</p>\n<p>　　Cheng称，支付宝是一个带有理财功能的支付工具。</p>\n<p>　　报道称，把手机直接当钱包使用总是能让到中国旅游的外国人感到惊讶。来自纽约的招生人员Diego Merino最近待在北京，有天他想买份饺子当午餐，当他问一位朋友要现金时，另一个人走了过来，用手机帮他付完款后就离开了。他表示，这个过程大约是三秒钟。</p>\n<p>　　在和美国旅游景点的酒店还有度假村达成合作之后，微信支付和支付宝在当地越来越受关注。近期有一批中国游客在拉斯维加斯凯撒皇宫酒店的Bacchanal Buffet吃自助餐，享用澳洲T骨羊排、冷冻帝王蟹腿和手工点心等各种美食，用餐结束后，他们拿出智能手机付了账单。</p>\n<p>　　凯撒皇宫酒店的高管Bruce Bommarito称，有一对美国夫妇停下来看他们用微信支付结账，还仔细询问了一番，这对夫妇非常惊讶，想知道美国人是不是也能用这种方式结账。美国人现在可以注册微信账号，但不能关联美国的银行卡。支付宝暂时未向美国人开通。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/732e86d7-7fdd-4ce1-ae84-5ab1e0cc5959.jpg',NULL,1727,'2018-01-06 03:44:00','2018-01-06 14:47:14','66',0),(3127,'西藏保护“雪山之王”员：科学保护 创新模式','李霈韵','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>拉萨1月6日电 (孙翔)“小雪豹太可爱了！”西藏那曲地区申扎县野生动物保护员塔杰乐呵呵地看着搜集到的雪豹视频，画面显示两只体型不大的雪豹正在嬉戏，一只雪豹在高处岩石上“后空翻”。</p>\n<div style=\"text-align:center\"> \n <img alt=\"12月18日，记者从西藏丁青县政府获悉，该县今年与山水自然保护中心合作，开展以雪豹为旗舰物种的生态系统研究和保护工作。山水自然保护中心项目部主任赵翔表示，此前西藏对雪豹的监测研究主要在西部的那曲羌塘和日喀则珠峰两地，对于东部的研究还是空白。从今年11月1日开始，当地16名牧民监测队员在100多平方公里的范围布设了20台红外相机，共捕捉到26次雪豹的活动影像。图为监测到的雪豹影像。(山水自然保护中心供图)\" src=\"upload/image/432d1f67-7953-45fc-8e2f-cbf7be676906.jpg\" title=\"12月18日，记者从西藏丁青县政府获悉，该县今年与山水自然保护中心合作，开展以雪豹为旗舰物种的生态系统研究和保护工作。山水自然保护中心项目部主任赵翔表示，此前西藏对雪豹的监测研究主要在西部的那曲羌塘和日喀则珠峰两地，对于东部的研究还是空白。从今年11月1日开始，当地16名牧民监测队员在100多平方公里的范围布设了20台红外相机，共捕捉到26次雪豹的活动影像。图为监测到的雪豹影像。(山水自然保护中心供图)\"> \n <input id=\"16247856c7d047059c9fcfff44035f04\" name=\"16247856c7d047059c9fcfff44035f04\" type=\"hidden\" value=\"http://www.chinanews.com/tp/hd2011/2017/12-18/789279.shtml\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：监测到的雪豹影像。(山水自然保护中心供图) \n</div>\n<p>　　雪豹皮毛灰白，伴有黑色点斑，被称为“雪山之王”，是中国国家一级保护动物，已被列入国际濒危野生动物红皮书。目前，中国雪豹数量约占世界总数的40%。广州市远望野生动物保护服务中心拉萨办公室负责人梁旭昶说，西藏拥有中国最大的雪豹栖息地。</p>\n<p>　　<img alt=\"雪豹被称为“雪山之王”，是中国国家一级保护动物，已被列入国际濒危野生动物红皮书。(广州市远望野生动物保护服务中心供图)\n<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 梁旭昶 摄\" src=\"upload/image/8815b6cc-2dbc-44cd-89a7-123a56789ddd.jpg\" style=\"font-size: 12px; text-align: center;\" title=\"雪豹被称为“雪山之王”，是中国国家一级保护动物，已被列入国际濒危野生动物红皮书。(广州市远望野生动物保护服务中心供图)\n<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 梁旭昶 摄\"></p>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  雪豹被称为“雪山之王”，是中国国家一级保护动物，已被列入国际濒危野生动物红皮书。(广州市远望野生动物保护服务中心供图) \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 梁旭昶 摄 \n</div>\n<p>　　“自从用了红外相机，我才真正见到雪豹。”塔杰说自己当了近10年野保员、每周巡护3次以上，却没见到过“活的雪豹”。他通过雪豹的粪便、刨坑、脚印辨识踪迹，观察死亡牲畜伤口判断其是否“命丧豹口”。</p>\n<p>　　塔杰说自己正在逐步掌握现代、科学的保护技能。经过一年多的学习，塔杰成为萨金队(萨金，藏语雪豹的音译)队长。萨金队由6名25至42岁的藏族青年组成，是一支基层牧民保护队伍，监测藏北高原上约1500平方公里范围的土地。</p>\n<p>　　梁旭昶参与了萨金队的培训及组建。他说，萨金队成员现在具有雪豹分布科学调查能力，能够确定雪豹出现的高频区域，进行红外相机的网格化布置及维护等。</p>\n<p>　　<img alt=\"图为红外相机拍摄到的雪豹。(广州市远望野生动物保护服务中心供图)\n<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 梁旭昶 摄\" src=\"upload/image/8af9e7ee-712b-4d7e-9f5f-dbefc19a4e96.jpg\" style=\"font-size: 12px; text-align: center;\" title=\"图为红外相机拍摄到的雪豹。(广州市远望野生动物保护服务中心供图)\n<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 梁旭昶 摄\"></p>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  图为红外相机拍摄到的雪豹。(广州市远望野生动物保护服务中心供图) \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 梁旭昶 摄 \n</div>\n<p>　　梁旭昶表示，广州市远望野生动物保护服务中心自2016年开始在申扎县进行雪豹野外观测。通过“空间捕捉重获”模型分析法，他估算在申扎县3000多平方公里的测算范围内，每百平方公里分布超过3只雪豹。</p>\n<p>　　“我们看到越来越多的普通农牧民加入到雪豹保护。”梁旭昶谈及，雪豹的活动区域并不限于保护区，其实大量区域与农牧民的生活、放牧区域重叠。申扎县野保员多杰说，西藏基层引入更为成熟的保护模式进行环境综合治理。如广州市远望野生动物保护服务中心众筹50万元人民币，通过社会购买服务的模式弥补农牧民因雪豹袭击而蒙受的损失，以更大力度保护雪豹及其他野生动物。</p>\n<p>　　为此，当地成立社区保护地治理委员会，负责资金使用、落实保护措施。委员会将引导农牧民在未来五年内拆除一半以上的草场围栏，进行综合环境治理；加强事前防范措施，减少人兽冲突。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/8af9e7ee-712b-4d7e-9f5f-dbefc19a4e96.jpg',NULL,609,'2018-01-06 10:07:00','2018-01-06 14:47:14','64',0),(3128,'不让梦想冰冻――软件谷暖冬关爱行动','魏巍','中国网','<p><strong>　　不让梦想冰冻――软件谷暖冬关爱行动</strong></p>\n<p>　　2018年1月4日至1月5日，2018年的初雪冰封南京，中国(南京)软件谷管委会联合南京氪空间、汇创在风雪的日子里为软件谷创业者送来一份暖冬关爱。</p>\n<p>　　本次活动主题为“Hello！World！”，主题源自《The Programme Language》中著名的演示程序。本活动主题旨在突出软件谷技术流、创业极客的形象，通过为软件谷内的创业者奉上冬日温暖、赠送新年礼物，用爱温暖软件谷的冬天，宣传软件谷良好的创新创业环境！</p>\n<p align=\"center\"><img src=\"upload/image/c3e1fd62-f757-4407-b3ad-f5ba0084a2af.png\"></p>\n<p>　　本次活动在江苏创业大街、创业车站等核心区域，精心打造了独树一帜的文化墙完美展示软件谷创业文化，搭配创意拍照框，供大家拍照，鼓励软件谷内的创业者利用微信、微博发照片，展现软件谷创业创新活力，在1月5日对参与者奖励创业加油早餐包，在严冬为软件谷内创业者提供实实在在的温暖。活动现场谷内优秀企业创意产品展示，带来互动体验，吸引了诸多人才热情参与。后期，软件谷还将从活动微信推文精选优质留言进行奖励。</p>\n<p>　　过去的一年，软件谷围绕创业痛点充分发挥政策杠杆效应，在认真贯彻落实国家、省、市支持双创各类政策的同时，制定出台《雨花台区关于进一步加快软件及信息服务业发展的政策意见》(雨委发〔2015〕33号)、《关于鼓励和扶持科技创业企业融资贷款的实施意见》(谷管委〔2015〕24号)、《资金(雨花)科技创业特别社区科技企业孵化器创业企业扶持办法》、《雨花台区知识产权战略专项资金管理办法》等政策措施，每年拿出2亿元以上资金支持创新创业。软件谷通过编印《软件谷企业服务手册》、搭建企业服务平台、日常协调服务等方式，积极帮助企业协调解决在双创过程中遇到的落户、财税、知识产权、上市、社保、员工子女教育等方面具体问题，营造良好的服务环境。</p>\n<p>　　在全新来临的2018年，中国(南京)软件谷鼓励各位创业者欣赏自己、关爱自己、肯定自己，新的一年继续加强“双创”建设，共同为大家创造最优越的创新创业环境，帮助更多创业者在“东方硅谷”这片热土上实现创业梦想。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/c3e1fd62-f757-4407-b3ad-f5ba0084a2af.png',NULL,1194,'2018-01-05 16:39:00','2018-01-06 14:47:14','82',0),(3129,'“答卷人”习近平的这些话语重心长','高辰','央视网','<p align=\"center\"><img alt=\" \" src=\"upload/image/ed97f53b-a71e-42e1-9df8-345922e12b74.jpg\"></p>\n<p>　<strong>　“答卷人”习近平的这些话语重心长</strong></p>\n<p>　　<strong>央视网消息</strong>：1月5日，习近平总书记在学习贯彻党的十九大精神研讨班开班式上发表重要讲话。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/7082c595-eebc-42ad-be71-12d44dd9d282.jpg\"></p>\n<p>　　政治局七位常委全部出席，开班式主要面向中央委员会成员和省部级主要领导干部。总书记讲话金句箴言，段段走心。</p>\n<p>　　<strong>谈新时代中国特色社会主义</strong></p>\n<p>　　必须一以贯之进行下去。</p>\n<p>　　只有回看走过的路、比较别人的路、远眺前行的路，弄清楚我们从哪儿来、往哪儿去，很多问题才能看得深、把得准。</p>\n<p>　　中国特色社会主义不是从天上掉下来的。</p>\n<p>　　是党和人民历经千辛万苦、付出各种代价取得的宝贵成果。</p>\n<p>　　这个新时代是中国特色社会主义新时代，而不是别的什么新时代。</p>\n<p>　　不断开创新时代中国特色社会主义事业新局面。</p>\n<p>　　党的十九大作出中国特色社会主义进入新时代这个重大政治论断，党要在新的历史方位上实现新时代党的历史使命，最根本的就是要高举中国特色社会主义伟大旗帜。</p>\n<p>　　<strong>谈共产党人初心使命</strong></p>\n<p>　　不忘初心，牢记使命，就不要忘记我们是共产党人，我们是革命者，不要丧失了革命精神。</p>\n<p>　　昨天的成功并不代表着今后能够永远成功，过去的辉煌并不意味着未来可以永远辉煌。</p>\n<p>　　时代是出卷人，我们是答卷人，人民是阅卷人。</p>\n<p>　　决不能因为胜利而骄傲，决不能因为成就而懈怠，决不能因为困难而退缩。</p>\n<p>　　“只有不忘初心、牢记使命、永远奋斗，才能让中国共产党永远年轻。”还记得2017年10月31日，十九大闭幕仅一周，总书记就带领政治局常委，专程前往上海、浙江嘉兴，瞻仰一大会址和南湖红船，回顾建党历史，重温入党誓词，宣示新一届党中央领导集体的坚定政治信念，用深刻话语和实际行动，为广大党员干部作出表率，指引大家从党的光辉历史中汲取奋进的力量。</p>\n<p>　　<strong>谈新时代党的建设新的伟大工程</strong></p>\n<p>　　在统揽伟大斗争、伟大工程、伟大事业、伟大梦想中，起决定性作用的是新时代党的建设新的伟大工程。</p>\n<p>　　影响党的先进性、弱化党的纯洁性的各种因素具有很强的危险性和破坏性。</p>\n<p>　　新时代党的建设新的伟大工程，既要培元固本，也要开拓创新。特别是要发挥彻底的自我革命精神。</p>\n<p>　　以史为鉴可以知兴替。</p>\n<p>　　(注：这句出自唐太宗李世民的名言，意思是说借历史上的成败得失作为教训、使人警惕，可以知道一个国家的兴盛和衰废。)</p>\n<p>　　功成名就时做到居安思危、保持创业初期那种励精图治的精神状态不容易；执掌政权后做到节俭内敛、敬终如始不容易；承平时期严以治吏、防腐戒奢不容易；重大变革关头顺乎潮流、顺应民心不容易。</p>\n<p>　　(注：“敬终如始”最早出自《左传》，意思是在即将结束某项事情之时，应该像才开始一样富有朝气、热血澎湃、雄心勃勃。“承平”出自《汉书》，意思是持久的太平。)</p>\n<p>　　十九大报告中总结我们坚持党的领导、加强党的建设的新鲜经验，明确提出了新时代党的建设总要求，全面推进党的建设新的伟大工程，不断提高全面从严治党水平。</p>\n<p>　　勇于自我革命，从严管党治党，是我们党最鲜明的品格，全面从严治党永远在路上。</p>\n<p>　　<strong>谈抓住“关键少数”领导干部</strong></p>\n<p>　　建设好我们这样的大党，领导好我们这样的大国，中央委员会成员和省部级主要领导干部至关重要。</p>\n<p>　　以时不我待、只争朝夕的精神投入工作。</p>\n<p>　　要把我们党建设好，必须抓住“关键少数”。</p>\n<p>　　必须做到信念过硬；必须做到政治过硬；必须做到责任过硬；必须做到能力过硬；必须做到作风过硬。</p>\n<p>　　要把践行“三严三实”贯穿于全部工作生活中，养成一种习惯、化为一种境界。</p>\n<p>　　道德修养：明辨是非善恶，追求健康情趣。</p>\n<p>　　廉洁自律：心有所戒、行有所止，守住底线、不碰高压线。</p>\n<p>　　洁身自好：作为第一关，从小事小节上加强约束、规范自己。</p>\n<p>　　反对特权：坚决反对特权思想、特权现象，习惯在受监督和约束的环境中工作生活。</p>\n<p>　　高级干部要在时代洪流中成为坚守共产党人精神追求的中流砥柱。必须提升政治站位、提高工作能力、拓展工作视野、提高理论联系实际。领导干部不仅要自身过得硬，还要管好家属和身边工作人员。各级干部特别是领导干部要结合贯彻落实党的十九大精神真正动起来、深下去，切实把存在的矛盾和问题搞清搞透，把各项工作做实做好。</p>\n<p>　　<strong>谈增强忧患意识防范风险挑战</strong></p>\n<p>　　备豫不虞，为国常道。</p>\n<p>　　(注：“备豫不虞”出自《左传》，早做预备工作以防不测之事，指做事应该及早准备。)</p>\n<p>　　越是取得成绩的时候，越是要有如履薄冰的谨慎，越是要有居安思危的忧患。</p>\n<p>　　绝不能犯战略性、颠覆性错误。</p>\n<p>　　面对波谲云诡的国际形势、复杂敏感的周边环境、艰巨繁重的改革发展稳定任务。</p>\n<p>　　既要有防范风险的先手，也要有应对和化解风险挑战的高招。</p>\n<p>　　既要打好防范和抵御风险的有准备之战，也要打好化险为夷、转危为机的战略主动战。</p>\n<p>　　(注：“波谲云诡”出自汉《甘泉赋》，好像水波和云彩那样，形态不可捉摸。后多形容事物变幻莫测。)</p>\n<p>　　当前，世界正处于百年不遇的大变局之中，突发性风险始终存在，各方对人类和平与发展的前景既有期待、也有忧虑。国际之“乱”和中国之“治”，凸显中国方案的可行、中国理念的可贵。</p>\n<p>　　我们必须看到任务艰巨、时间紧迫。从现在起到实现决胜全面建成小康社会的奋斗目标，只有1000多天。时间不等人！我们必须走在时间前面。因此，我们要以时不我待、只争朝夕的精神投入工作。</p>\n<p align=\"center\"><img alt=\" \" src=\"upload/image/bad1a7a2-f5ac-4a61-8e6f-4200d7f60bae.jpg\"></p>\n<p>　　新时代中国特色社会主义的航线已经明确，中华民族伟大复兴的巨轮正在乘风破浪前行。在这艘巨轮上，每一份力量都不可或缺。因此，我们要推动全党全国各族人民把思想统一到党的十九大精神上来，把力量凝聚到实现党的十九大确定的目标任务上来。我们要继续进行具有许多新的历史特点的伟大斗争，准备战胜一切艰难险阻，朝着我们党确立的伟大目标奋勇前进！</p>\n<div id=\"function_code_page\"></div>','upload/image/bad1a7a2-f5ac-4a61-8e6f-4200d7f60bae.jpg',NULL,851,'2018-01-06 13:01:00','2018-01-06 14:47:14','64',0),(3130,'千万别用雨刮器除雪！雪天汽车保养有技巧','吴合琴','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>1月5日电 美国《世界日报》日前刊文称，纽约已正式进入大雪纷飞季节，由于气温影响，如何正确保养汽车非常重要；专家建议，车主要注意一些小技巧，才能避免冰雪天气带来的行车危险。</p>\n<div style=\"text-align:center\"> \n <img alt=\"　暴风雪袭击，不少车辆深埋入积雪中。(美国《世界日报》记者黄伊奕/摄影)\" src=\"upload/image/6e1a3056-be42-46a0-a82f-a41bbbe6cd69.jpg\" style=\"border:px solid #000000\" title=\"　暴风雪袭击，不少车辆深埋入积雪中。(美国《世界日报》记者黄伊奕/摄影)\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  暴风雪袭击，不少车辆深埋入积雪中。(美国《世界日报》记者黄伊奕/摄影) \n</div>\n<p>　　冬日行车因为车厢内外的温度差异，常导致车玻璃总是被雾气笼罩，即使启动汽车的开除霜功能也无效，除非打开车窗，但寒天开车窗并不适合。事实上，很多时候除霜功能无法达到除霜效果，主要是没有开启车外循环功能，造成车厢内外无法进行空气流通。</p>\n<p>　　所以先开启汽车除霜功能，再打开外循环，才能有效去除玻璃上的雾气。此外车主还可使用除霜产品涂抹在玻璃上，也同样有防止水气在玻璃上凝结。</p>\n<p>　　如何将车辆从掩埋的积雪中拯救出来，也是大部分纽约车主遇到的难题，不少人会采用热水冲化积雪的方式。但是热水与过低温度中的车漆、玻璃相接触时，很有可能造成车漆与玻璃损坏。</p>\n<p>　　专家建议，正确方法应该是用常温水冲浇挡风与车窗玻璃，去除影响视线的雪后，可先行上路，随着车温升高，车身上的积雪会自然慢慢融化。</p>\n<p>　　此外，千万不要用雨刮器除雪，这样会损坏雨刮器。玻璃水也一定要选择有防冻功能的，否则冰的液体体积增大，恐将撑坏玻璃水壶。</p>\n<p>　　而在冬季，车轮与车辆底盘十分重要，冬日车主应该适当的将轮胎胎压增大，填补胎压冷缩后与正常胎压的差距。此外还应及时清理轮胎夹缝里的异物，防止因气温过低导致轮胎变脆造成的爆胎风险。同时也应该及时查看底盘，雨雪中的酸性，碱性物质有可能会腐蚀底盘，使底盘生锈。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/6e1a3056-be42-46a0-a82f-a41bbbe6cd69.jpg',NULL,997,'2018-01-05 16:09:00','2018-01-06 14:47:14','73',0),(3131,'绿地金融入股竞界 助推绿地与电竞行业协同发展','叶攀','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>1月5日电 今日，绿地控股集团(600606.SH)旗下全资战略性金融投资平台绿地金融投资控股集团有限公司(下称“绿地金融”)宣布，拟向中国领先的电子竞技场景运营商――上海鹏塔网络科技有限公司(以下简称“竞界电竞”)战略投资6,000万元人民币并获其50%股权，这是2018新一年绿地在多元化产业发展方面的一大突破，也是绿地积极响应国家和上海市加快文化创意产业发展的实际举措，标志着“巨人”绿地跨界涉入“年轻、朝气、潜力无限”的电竞行业，并期待通过与绿地旗下既有的传统业务发挥协同效应，实现“地产+金融+电竞+”的新商业模式。</p>\n<p>　　当日，绿地金融与竞界电竞在上海虹口举行“创想无止�”绿地・竞界项目启动签约仪式，绿地金融将向竞界投资6,000万元人民币，获得其50%股权成为第一大股东，并计划在2-3年内将其打造成全国最大的电竞运营平台。本次合作距离上海市政府印发《关于加快本市文化创意产业创新发展的若干意见》仅半个月，是《意见》颁发后上海首单落地的重要文创发展项目。</p>\n<p>　　纵观绿地与竞界电竞的合作渊源缘起于更早时候。去年下半年，绿地集团推出“魔奇”公寓及CLUB，提前布局电子竞技为营销核心的创新型商住项目，竞界电竞则是该项目电竞部分解决方案提供商，迅速帮助“魔奇”带来电竞板块的内容和热度，与绿地发挥了极高的产业战略协同效应。未来，绿地金融还将携手竞界电竞与绿地商业集团在上海青浦合作新建国内最大的电子竞技场馆，而此次成功入股竞界电竞将为绿地的创新地产发展提供有力的内容及运营支持，同时为谋求未来在绿地电竞小镇建设的宏伟蓝图做好准备。</p>\n<p>　　为此，绿地集团总裁张玉良表示：“绿地涉足电竞行业，不仅看重年轻的电竞行业无限的发展潜力，也是看重其对绿地传统成熟的地产业务带来的巨大协同效应，包括未来电子竞界场馆建设预期成为发展热点，绿地正好能发挥自身的地产优势，与竞界电竞的运营能力结合，携手发展电竞界的龙头企业。”</p>\n<p>　　绿地集团执行副总裁、绿地金融董事长、总裁耿靖同时表示：“此次投资竞界电竞对绿地在文化创新领域的拓展具有重要战略意义，而且中国的电子竞技发展历经艰辛，多年经营终于迎来爆发，也体现出一个绝佳的投资机遇。竞界电竞不仅紧抓市场空白机遇，而且定位精准，绿地不仅在财务投资方面对其保持充足信心，也期待双方可以在产业合作上迸发出新的火花，实现双方各自新的飞跃式发展。”</p>\n<p>　　电子竞技是利用电子设备作为运动器械而实现的人与人之间智力对抗的体育运动，目前已经进入2017全运会、2020奥运会、2022年亚运会，2017年的电竞赛事观赛人数已预期将超过“超级碗”美国NFL职业联赛。</p>\n<p>　　综合艾瑞咨询、NewZoo、易观智库等行业研究数据显示，2017年中国电子竞技市场规模为337.6亿元人民币，较2014年的226.3亿元人民币增长了49.2%，电子竞技用户规模超过2.2亿人，年复合增长率达到30%；而世界电子竞技市场规模在2017年达到6.96亿美元，业内预计2020年可以爆发增长至14.88亿美元。</p>\n<div style=\"text-align:center\"> \n <img alt=\"数据来源：艾瑞咨询、NewZoo\" src=\"upload/image/095c412b-bff7-4c5e-bb8e-ca1c8a0e1e98.jpg\" style=\"border:px solid #000000\" title=\"数据来源：艾瑞咨询、NewZoo\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  数据来源：艾瑞咨询、NewZoo \n</div>\n<p>　　竞界电竞创始人、CEO任立表示：“电子竞技人群从初期的核心人群到全民普及，全球游戏人群将超过十亿，电子竞技行业的爆发已经成为行业共识。而竞界电竞从策划到创立，迄今已经形成完整的产业发展规划，此次与绿地合作，我们有信心站在巨人的肩膀上将竞界电竞培育成全国一流及领先的电竞解决方案提供商。”</p>\n<p>　　据介绍，目前竞界电竞的旗舰店已运营10个月，并与全国15个地方政府签订了合作协议，且拥有中国最著名女子职业电竞选手、08奥运会火炬手小苍等行业知名人士为事业合伙人。与绿地融合后的竞界电竞将建设和运营标准化的电子竞技场馆，导入各类电子竞技赛事和活动，并通过配套的电子竞技教学与陪练、优质酒水简餐的提供、有趣丰富的桌游Cosplay 活动等增强会员的粘性，打造一个具有粉丝忠诚度的电子竞技社区，在获得稳定收入的同时，未来粉丝APP 和线上社区的打造更是带来无限的想象空间。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/095c412b-bff7-4c5e-bb8e-ca1c8a0e1e98.jpg',NULL,271,'2018-04-01 16:33:00','2018-01-06 14:47:14','82',0),(3132,'去年香港强积金平均回报率19.09% 今年料放缓至5%','官志雄','中国新闻网','<div style=\"text-align:center\"> \n <img alt=\"陈锐隆((右)称，港股在多项利好因素支持下，预计今年首季走势将持续向好。图片来源：香港《文汇报》。\" src=\"upload/image/51265879-856b-4138-a605-ee8e6aad3169.jpg\" style=\"border:px solid #000000\" title=\"陈锐隆((右)称，港股在多项利好因素支持下，预计今年首季走势将持续向好。图片来源：香港《文汇报》。\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  陈锐隆((右)称，港股在多项利好因素支持下，预计今年首季走势将持续向好。图片来源：香港《文汇报》。 \n</div>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>1月5日电 据香港《文汇报》消息，去年香港投资市场造好，除股民大丰收之外，打工仔的强积金也有进账，平均回报率达19.09%，人均账面赚约3万元(港币，下同)。强积金顾问公司骏隆认为，美股今年回调压力较大，预料今年环球市场表现将较为波动，港股最多升至35,000点，强积金回报料仅5%。</p>\n<p>　　骏隆指出，环球央行的量化宽松政策推动去年股市造好，当中以香港股市最为突出。除了因为中国经济增长平稳向好，深港通及沪港通的资金流动亦为股票市场带来支持，美国税改得以通过，均利好投资气氛。然而，自欧洲宣布缩减买债规模，欧盟与英国仍就脱欧条款周旋，加上美国联储局宣布启动缩减资产负债表的行动及市场预期加息，令市场预期流动性逐步减少，为环球债券及股票带来隐忧。</p>\n<p>　　骏隆常务董事陈锐隆表示，港股市场基调良好，在多项利好因素支持下，预计今年首季港股走势仍将持续向好，但由于港股在去年已累积一定升幅，今年难保持去年升势，市场也只预计恒指今年会升至32,000至35,000点，一方面利好美股消息已被市场消化，加上美股估值偏高，美国税改通过将加重美国财赤压力，相对回调空间较大，而美联储局加息亦可能影响债券价格，推断强积金今年回报率将放缓至5%。</p>\n<p>　　他表示，面对市场波动，投资者需留意市况调整，建议风险承受能力较高的投资者可首选香港及亚洲 (日本除外)股票基金；中风险投资者可首选混合资产基金股票成份于大中华及亚洲区市场；低风险投资者则可考虑买入人民币货币市场基金等低风险类别基金，以获取较佳回报。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/51265879-856b-4138-a605-ee8e6aad3169.jpg',NULL,1698,'2018-01-05 11:05:00','2018-01-06 14:47:15','69',0),(3133,'不角逐党团干部是为参选台北市长？蒋万安回应','王忠会','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>1月5日电 据台湾《联合报》报道，针对有媒体报道称“国民党民意代表蒋万安表示将不参选党团干部，被认为是预留参选台北市长空间”，蒋万安5日回应称，本来就没有计划参选干部，但是从这个联想到参选，有点过度联想了。</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：国民党籍民意代表蒋万安。图片来源：中时电子报。\" src=\"upload/image/3b991c25-0499-42c9-8e3a-a22a72222c96.jpg\" style=\"border:px solid #000000\" title=\"资料图：国民党籍民意代表蒋万安。图片来源：中时电子报。\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  资料图：国民党籍民意代表蒋万安。图片来源：中时电子报。 \n</div>\n<p>　　蒋万安说，本来就没有计划参选党团干部，他认为不管有没有担任干部，党团成员都会就重大议案努力，有没有担任干部不是最重要考虑，但因为这样想到跟选举有关，过度联想了，联想太过牵强。</p>\n<p>　　蒋万安说，当下最重要的是台立法机构临时会，要帮助国民党团对抗这么霸道的执政党，临时会议程居然没有列入最重要的“空污法”，首要通过的是史上修的最烂的“劳基法”再修法，“谁在打假球很明显”。</p>\n<p>　　台湾《中国时报》5日报道称，国民党立院党团下周一将开放登记参选下会期首席副书记长，由于许多蓝营民代2018年都将投入选战或开始辅选，有意参选者不可能参选干部。而党内新星蒋万安4日表达将不参选干部，被认为是预留参选台北市长空间。对此，蒋万安4日表示，会在年节期间，冷静评估参选台北市长的可能。</p>\n<p>　　报道评论称，对参选台北市长一事，蒋万安表示，要等年节期间拜访基层，倾听民意后，再做谨慎评估。相较过去总说“在立法机构发声比其他事更重要”、“扮演好在野民代角色”，此说算是“首度松口”，准备挑战现任台北市长柯文哲。</p>\n<p>　　目前，国民党内表态参选台北市长者包括丁守中及郑丽文，国民党中央对北市选举目前定调是采取初选全民调，但称最快3月才决定人选。</p>\n<p>　　<strong>台媒：蒋万安参选北市对蓝军利大于弊</strong></p>\n<p>　　《中国时报》对此事发表评论称，从没有意愿、到愿冷静评估，观察蒋万安是否参选北市长的发言脉络，几可肯定只要没意外，他是选定了。因为，这位蓝营新秀只要敢撩下去，无论对他个人或党的整体利益，都将是利大于弊的政治牌局。</p>\n<p>　　评论称，在评估蒋是否参选时，国民党理应跳脱传统论资排辈的逻辑。而且，只要他愿意，胜算绝对是蓝营最大。</p>\n<p>　　第一，台北市选民近20%属外省族群，身为蒋家后代的蒋万安，比起其他党内候选人，无疑更有能量催出这块基本盘。</p>\n<p>　　其次，曾在硅谷担任企业并购律师的蒋万安，担任民代后却扭转昔日资方角色，在处理“劳基法”等重大民生法案都从劳方发声，且问政表现几无负评，这让总是攻击国民党“权贵”的对手，难有着力点。</p>\n<p>　　第三，蒋万安外在形象，比起竞争对手，在“妇女票”的磁吸效力，料是有过之而无不及。而他39岁的年龄，当然更容易拉近与年轻选民的距离。</p>\n<p>　　评论称，素有“百年老店”招牌的国民党若放胆起用政治资历不足2年的新秀，挑战全台湾最具指标意义的台北市，难道不是以行动宣告党的改变？</p>\n<p>　　不可讳言，柯文哲表现虽毁誉掺半，但许多政策与主张确实深获不少人信赖。在柯没犯大错前提下，要拉他下马并不容易。正因如此，国民党更应派出最强候选人应战。至于谁是最合适人选？从主客观条件来看，蒋万安怕是没有选择战场的权利了。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/3b991c25-0499-42c9-8e3a-a22a72222c96.jpg',NULL,1945,'2018-01-05 15:44:00','2018-01-06 14:47:15','70',0),(3134,'港股连升第8日收报30736点 距离创新高又走前一步','官志雄','中国新闻网','<div style=\"text-align:center\"> \n <img alt=\"图片来源：香港《文汇报》。\" src=\"upload/image/2ea6eda3-847a-4c5a-bf4d-ff5a53551274.jpg\" style=\"border:px solid #000000\" title=\"图片来源：香港《文汇报》。\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  图片来源：香港《文汇报》。 \n</div>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>1月5日电 据香港《文汇报》消息，港股昨日连升第8日，再升175点，全日收报30736点，距离创历史新高又走前一步。</p>\n<p>　　香港大市成交1,325亿元，当中严重落后的石油股，成为了大市焦点。国际油价升逾2%，刺激中海油(0883)升3.8%创两年高位，去年成为表现三只最差蓝筹之一的中石油(0857)，也终于大升5.1%，中石化(0386)亦升4.1%。市场人士认为，中东局势不稳，美元又回软，预计石油股还会继续受捧。</p>\n<p>　　国指昨再升114点，报12,203点，继续跑赢大市。石油股是升市功臣，除“三桶油”外，油服股升势更强，宏华(0196)及华油能源(1251)获大摩上调评级，股价都爆升约15%。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/2ea6eda3-847a-4c5a-bf4d-ff5a53551274.jpg',NULL,87,'2018-01-05 10:31:00','2018-01-06 14:47:15','69',0),(3135,'即遇2018跨年夜圆满举办，燃爆廊坊之夜','于晓','消费日报网','<p>　　在你想象中，跨年倒计时应该是怎样的？</p>\n<p>　　世界各地都有自己的特色跨年趴：纽约在时代广场举办的水晶球降落仪式、伦敦“伦敦眼”举办的焰火新年庆典、巴黎埃菲尔铁塔举办的灯光焰火、悉尼港大桥举办的焰火表演、东京举办的涩谷传统倒计时、香港维多利亚湾举办的焰火和花船巡游等。</p>\n<p>　　2017年12月31日，河北廊坊举行“河北廊坊 即遇跨年夜”，在别样灯光焰火里迎接2018年。</p>\n<p align=\"center\"><img src=\"upload/image/190ed817-bf02-47ef-897d-7eb574528917.png\"></p>\n<p>　　本次河北省廊坊市跨年夜活动由加优科技有限公司(旗下兴趣社交APP 即遇)承办，作为河北省廊坊市首届大型户外全民跨年活动，集特色小镇美食街、激光烟火秀、游戏互动、丰富的节目演绎瞬间燃爆现场，市民齐聚广场对于2018年欢呼呐喊。</p>\n<p align=\"center\"><img src=\"upload/image/d2ad231c-119e-45c6-821c-0f592cd14681.jpeg\"></p>\n<p>　　本次跨活动获得廊坊市政府认可及大力支持，力将打造廊坊新IP，丰富廊坊市人民的生活，在环保的基础上，增设的激光烟火秀，璀璨星空，点亮廊坊市人民的夜生活，寓意精彩的一年已经开启。</p>\n<p>　　本次跨年夜活动位于廊坊市开发区京津冀大数据应用体验中心，是京津冀大数据综合试验区重点工程，由润泽科技发展有限公司投资建设，是京津周边地标性的建筑综合体。拥有亚洲第三大LED液晶显示屏幕，夜幕中在流行灯带的映衬下，玲珑剔透，有“银河舰队”之美誉。项目包含研发、双创、办公、展览、会议、酒店、公寓几大功能分区，将打造成廊坊乃至京津冀大数据产业的核心承载平台，成为大数据产业年轻人创新创业的乐园。</p>\n<p align=\"center\"><img src=\"upload/image/8919449d-ff6e-4084-a00f-503b8c7df519.jpeg\"></p>\n<p>　　2017年即遇成功举办了：城市夜跑、水球大战、浓情七夕等一系列活动。2018年也将会定期举办各类赛事活动，丰富人们的生活，走出家门，拥抱陌生人，拥抱生活。</p>\n<p>　　即遇宗旨：开启兴趣社交新时代，社团、活动、即时IM、微动态、视频秀，多种新潮元素，带你领略令人心跳的世界，让你枯燥的生命拥有不一样的心动。真正有价值的生活，不是一味追问它的意义，而是勇敢的扎进生活。</p>\n<p>　　闯关赢大奖</p>\n<p>　　这是吃货的天堂，美食、小吃应有尽有！是嗨翻天的乐园，玩偶、工艺品数不胜数！小镇闯关环节设定闯关成功可获得抽取2018元大奖。</p>\n<p align=\"center\"><img src=\"upload/image/5d208adc-c311-4367-9c1a-6143f0e4c013.jpeg\"></p>\n<p class=\"pictext\" align=\"center\">创意拍照区</p>\n<p>　　广场吉祥物，彩绘涂鸦，浪漫的大屏幕留言……彰显人们对新生活的期待和祝福！</p>\n<p align=\"center\"><img src=\"upload/image/d4428024-41ee-413c-b234-84d13bae539e.jpeg\"></p>\n<p class=\"pictext\" align=\"center\">梦幻灯光展</p>\n<p>　　王者荣耀主题公园创意灯光展――</p>\n<p>　　节日的彩妆，缤纷的彩灯，装扮节日的氛围！</p>\n<p align=\"center\"><img src=\"upload/image/6f322e4d-5188-4169-88bb-7d2b6f81659f.jpeg\"></p>\n<p class=\"pictext\" align=\"center\">精彩演出区</p>\n<p>　　从晚上09:00起，即遇跨年倒计时现场每隔30分钟会上演灯光音乐秀，互动全场的巡回演出。</p>\n<p align=\"center\"><img src=\"upload/image/423051d3-3ba8-4ff2-81ae-21aec6d0584b.jpeg\"></p>\n<p class=\"pictext\" align=\"center\">灯光音乐汇演</p>\n<p>　　午夜11:00，即遇跨年现场上演精彩灯光秀，为汇演掀起序幕。</p>\n<p>　　大型舞蹈演出，精彩的歌唱表演，持续到新年数秒活动，让我们在踏入子夜的一刻，燃烧这个夜空！</p>\n<p>　　地标建筑上出现“2018”的大型字样，为迎新演出掀起高潮！激动人心的音乐，与夺目的光影效果、数万人的热情欢呼，交织出一场声光十足的跨年盛会，为您送上2018的第一份祝福！</p>\n<p>　　倒计时数秒</p>\n<p align=\"center\"><img src=\"upload/image/d80b1daf-cc7e-4361-9dec-a880b53ca608.png\"></p>\n<p>　　跨年的钟声敲起，全民一同迎接2018的到来！倒计时，欢呼、相拥！一起接受陌生人的祝福，迎接2018的到来！</p>\n<p>　　2017年12月31日，陌生人、朋友、家人共聚即遇跨年夜现场，热闹的集会，美味的小吃；聚集在炫酷的大屏幕前，在这场别开生面的新年倒计时活动里拥抱幸福的新生活！</p>\n<p align=\"center\"><img src=\"upload/image/cf2f7aea-ceac-4c0f-ab98-942bb914a783.png\"></p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/cf2f7aea-ceac-4c0f-ab98-942bb914a783.png',NULL,1544,'2018-01-05 17:17:00','2018-01-06 14:47:15','82',0),(3136,'桂港携手南向通道建设拓展合作空间','罗攀','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>南宁1月6日电(记者 杨陈)“广西是面向东盟的国际大通道，而东盟是香港第二大货物贸易伙伴和第四大服务贸易伙伴。在推进‘一带一路’建设方面，桂港两地都以东盟地区为重点，应加强合作，优势互补，携手推进中新互联互通南向通道建设，进一步拓展桂港合作新空间。”香港特别行政区政府驻粤经济贸易办事处主任邓家禧5日表示。</p>\n<p>　　当天，香港特别行政区驻广西联络处在南宁举行了“善用香港营商优势・把握‘一带一路’商机”研讨会。邓家禧在会上做上述表述。</p>\n<p>　　中新互联互通南向通道北接渝新欧国际通道，南经贵州至广西北部湾港转海运到中国香港地区、新加坡及整个东南亚，是纵贯中国西部的南北货运大通道和完整的国际海陆贸易环线。</p>\n<p>　　邓家禧认为，近日香港已与东盟国家签署了《自由贸易协定》，中国国家发展和改革委员会还与香港特区签署了《支持香港全面参与和助力“一带一路”建设的安排》(以下简称“安排”)，双政策优势为香港参与南向通道建设提供了巨大空间。</p>\n<p>　　“根据《安排》，国家支持香港在海事保险、船舶融资、海事法律和争议解决、船舶管理等高增值海运服务方面发挥优势，鼓励内地海运企业充分利用香港的专业服务。香港还可以为相关国家和地区政府机关、投资机构及企业提供公共行政、城市管理、航运、城市轨道交通运营等专业培训；此外，香港和内地还可在信息、公路、铁路、港口、机场等基础设施领域进一步建立合作伙伴关系，积极与相关国家和地区开展工程承包与劳务合作。”邓家禧说。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为香港特别行政区政府驻粤经济贸易办事处主任邓家禧在“善用香港营商优势？把握‘一带一路’商机”研讨会上讲话。　杨陈 摄\" src=\"upload/image/eaf187ee-df9f-418e-a5b2-b73ad1e436f2.JPG\" title=\"图为香港特别行政区政府驻粤经济贸易办事处主任邓家禧在“善用香港营商优势？把握‘一带一路’商机”研讨会上讲话。　杨陈 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为香港特别行政区政府驻粤经济贸易办事处主任邓家禧在“善用香港营商优势？把握‘一带一路’商机”研讨会上讲话。　杨陈 摄 \n</div>\n<p>　　广西壮族自治区商务厅副厅长谭秀洪对此表示认同。她说，香港作为南向通道的主要物流节点之一，目前，广西北港物流有限公司与香港南洋国际物流集团有限公司、香港永丰船务有限公司合作开行的北部湾港到香港“天天班”航线已经开通，有效解决了北部湾港至东北亚、美洲等东行货源的中转问题；1月上旬，国泰航空还将开通香港直飞南宁航线，相信中新互联互通南向通道的建设，将使广西企业利用香港平台拓展业务变得更为便利。</p>\n<p>　　谭秀洪建议，下一步桂港两地要加强南向通道基础设施建设合作，促进两地港口和物流企业合作，推动与东盟、中亚等地的业务往来，深化桂港产业合作，使桂港合作领域向纵深方向发展。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/eaf187ee-df9f-418e-a5b2-b73ad1e436f2.JPG',NULL,1149,'2018-01-06 09:22:00','2018-01-06 14:47:16','66',0),(3137,'陈乔恩酒驾只喝一罐啤酒？ 警大教授：至少14罐起','王忠会','中国台湾网','<p>　　<strong>陈乔恩酒驾只喝一罐啤酒？ 警大教授：至少14罐起</strong></p>\n<p>　　中国台湾网1月5日讯 据台湾《中时电子报》报道，台湾艺人陈乔恩昨(4)日凌晨酒驾，酒测值高达0.67，但她向警方供称仅喝一罐啤酒，被质疑说谎。对此，警大教授蔡中志表示，以陈乔恩不到50公斤的体重来估算，酒测值要达到0.67，至少要喝14罐以上。</p>\n<div style=\"text-align:center\"> \n <img alt=\"　台湾女艺人陈乔恩4日凌晨因酒驾被移交至台北地检署。(图片来源：台湾《中时电子报》)\" src=\"upload/image/644bf0c1-6f2a-4466-afe9-e1e88f3b587c.jpg\" style=\"border:px solid #000000\" title=\"　台湾女艺人陈乔恩4日凌晨因酒驾被移交至台北地检署。(图片来源：台湾《中时电子报》)\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  台湾女艺人陈乔恩4日凌晨因酒驾被移交至台北地检署。(图片来源：台湾《中时电子报》) \n</div>\n<p>　　大安分局警方4日凌晨2时在忠孝东路、复兴路口临检时，发现一部自小客红灯右转，上前盘查后，发现车内女驾驶浑身酒气，盘查身份后，才发现她是艺人陈乔恩，酒测值高达0.67，随即将她带回侦讯，之后移送台北地检署，遭检察官谕令10万元新台币交保。</p>\n<p>　　陈乔恩向警方供称仅喝一罐啤酒，外界普遍认为“不可能”。警大交通系教授蔡中志表示，以啤酒的酒精浓度5%来换算，50公斤的人喝800cc就超过0.25毫克(公共危险标准)、60公斤的要960cc、70公斤要1120cc、80公斤的要1280cc。</p>\n<p>　　陈乔恩体重不到50公斤，酒测值要达到0.67毫克，至少要喝5040cc，以罐装350cc来换算，大概要14罐以上。</p>\n<p>　　网友对于“一罐啤酒”说法也不买账，有人表示，“啤酒只喝一罐，威士忌也喝了半瓶，只不过后面那句没说出来。”、“只喝一罐啤酒=啤酒只喝一罐”、“她真的只有喝一罐啤酒啊，其余都喝别的了”。</p>\n<p>　　而新北市议员李婉钰被问到“1罐啤酒的酒测值是否高达0.67”时更是大笑说：“我觉得1罐啤酒对我来说应该是0，所以我不知道正常人应该是多少。”(中国台湾网 尹赛楠)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/644bf0c1-6f2a-4466-afe9-e1e88f3b587c.jpg',NULL,723,'2018-01-05 15:45:00','2018-01-06 14:47:16','70',0),(3138,'江苏一男子早晨酒后驾车开进路边商店 致一死一伤','左盛丹','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>南京1月6日电(谷华 徐绍辉)6日上午8点，江苏盐城大丰一男子酒后驾车，撞上一辆电动三轮车后又撞进路边商店里，才得以停车。事故造成一死一伤。</p>\n<p>　　在大丰市区幸福东大街延伸段与(老)S226交界处的事故现场，记者看到，一辆银色的别克商务车车头插进路边的百货批发商店里面，商店门口的防盗窗和物品散落一地。在商店前面的路边，一辆电动三轮车翻倒在地。</p>\n<div style=\"text-align:center\"> \n <img alt=\"被撞翻的电动三轮车。　徐绍辉 摄\" src=\"upload/image/cb15f4c7-82b3-4e34-a1b7-667d2912a874.jpg\" title=\"被撞翻的电动三轮车。　徐绍辉 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  被撞翻的电动三轮车。　徐绍辉 摄 \n</div>\n<p>　　“当时我正在屋里，突然听到一声巨响，只见别克商务车撞上一辆电动三轮车，由于速度过快，车子又撞进路边的商店里才停下来。”据一位目击者介绍，当时该商店门口没有人。电动三轮车上是一对老夫妻，男的当场死亡，女的已送到医院抢救。</p>\n<p>　　事故发生后，当地交警迅速赶到现场将肇事司机控制。“从昨晚和朋友喝酒喝到早晨，接到老板用车的电话，开到这里就发生了交通事故。”肇事车的驾驶员说。</p>\n<p>　　据了解，经酒精检测仪现场检测，该驾驶员涉嫌醉酒后驾驶机动车，警方已对其抽取血液样本送检。目前，案件正在进一步侦办中。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/cb15f4c7-82b3-4e34-a1b7-667d2912a874.jpg',NULL,1923,'2018-01-06 14:21:00','2018-01-06 14:47:16','65',0),(3139,'佯装高富帅专钓大陆女性 台湾多名男子被起诉','王忠会','中国台湾网','<p><strong>　　谨防网络交友诈骗！台湾“爱情骗子”专钓大陆女性</strong></p>\n<p>　　中国台湾网1月5日讯 据台湾媒体报道，台湾地区近期破获多起爱情诈骗案，诈骗集团不仅设立机房、盗用高富帅照片、撰写爱情骗局脚本，还专门找高收入的大陆女性下手，此等“爱情骗子”让欲通过网络交友寻获爱情的女性被骗情、骗钱。</p>\n<div style=\"text-align:center\"> \n <img alt=\"爱情诈骗集团的教战手册，密密麻麻写满爱情骗局剧本。(图片来源：台湾“中国时报”)\" src=\"upload/image/ef138d19-2cb8-4718-9280-178c7170a8dd.jpg\" style=\"border:px solid #000000\" title=\"爱情诈骗集团的教战手册，密密麻麻写满爱情骗局剧本。(图片来源：台湾“中国时报”)\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  爱情诈骗集团的教战手册，密密麻麻写满爱情骗局剧本。(图片来源：台湾“中国时报”) \n</div>\n<p>　　台中吴姓男子等10人佯装高富帅专钓大陆女性</p>\n<p>　　哪有那么多“高富帅”？年龄在21至36岁的台中吴姓男子等10人，做起无本生意扮演爱情骗子，透过交友网站“珍爱网”、“世纪情缘”等找寻单身女子交往。他们佯称是日本夏普、台湾台塑集团干部等白领阶层，专钓大陆女性，至少有2名女性上当被骗约11万人民币。台中地检署日前侦结此案，10名男子依涉嫌组织犯罪条例、加重诈欺罪遭到起诉。</p>\n<p>　　起诉书指出，吴姓男子于前年4月在台中北区设立诈骗机房，由江姓、李姓男子等多人担任机房工作。有别于一般的假公安诈骗，他们是“骗感情诈骗”，先在网络上盗取高富帅男子照片，再到交友网站“珍爱网”“世纪情缘”等专找大陆女子下手。</p>\n<p>　　诈骗成员自称是日本夏普、台塑集团等干部，以结婚为前提与对方交往，等到交往一段时间见对方已经坠入情网后，再佯称因业绩未达标，暂时需要资金协助等理由，骗对方先汇款应急，一名卢姓女子因而被骗3万人民币。</p>\n<p>　　集团成员中的李男，自立门户到高雄市鼓山区美术南五街设立机房，以同样手法诈得赵姓大陆女子7万9200元人民币。</p>\n<p>　　嘉义警方破获诈骗集团装帅骗财</p>\n<p>　　嘉义市警方日前破获以陈男为首脑的爱情诈骗集团，他们编撰多种剧情的教战手册，专骗两岸暨香港成熟女子，半年就不法牟利上千万元新台币。当地警方在嘉义市、嘉义县太保市2处机房逮捕7名嫌疑犯，嘉义市机房更兼营网络赌博。该案于4日依诈欺、赌博罪移送法办。</p>\n<p>　　当地警方调查得知，该诈骗集团在网络交友网站冒用印度尼西亚、澳洲混血帅气男的照片，通过网络交友聊天，再佯装“以结婚为前提”，与锁定的对象进一步交往、嘘寒问暖，取得女子的信任及对爱情的憧憬，进而以“投资理财”“事业需要资金周转”“亲人重病急需钱”等借口，诱使女子汇钱纾困。</p>\n<p>　　经警方调查，嘉义市国贤一街的机房由该集团共犯林政贤负责，主要做网络赌博。该集团自去年8月至今约半年，不法获利上千万元新台币，10余名被害人分布在山东、上海、深圳、香港等地，多是高收入单身女子，还有女银行长。(中国台湾网 杨永青)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/ef138d19-2cb8-4718-9280-178c7170a8dd.jpg',NULL,1449,'2018-01-05 15:48:00','2018-01-06 14:47:16','70',0),(3140,'李宇春演喜剧角色还说四川话 夸梁朝伟拍戏认真','叶攀','中国新闻网','<div style=\"text-align:center\"> \n <img alt=\"李宇春、胡巴新年祈福\" src=\"upload/image/a6827606-578f-4f1f-aecf-640246165241.jpg\" style=\"border:px solid #000000\" title=\"李宇春、胡巴新年祈福\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  李宇春、胡巴新年祈福 \n</div>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>北京1月5日电 昨日，电影《捉妖记》在北京举行路演，李宇春现身助阵。对于此次和梁朝伟合作，她表示很兴奋，称赞对方十分认真，“跟他合作能学到很多东西，因为他可以帮助新的演员进入角色，很开心”。</p>\n<p>　　记者了解到，和第一部相比，《捉妖记2》是全新的故事，讲述天荫带着小岚踏上寻父之路，而重回永宁村的胡巴再度被妖王追杀。</p>\n<div style=\"text-align:center\"> \n <img alt=\"李宇春、胡巴与观众合影\" src=\"upload/image/ee18b9a5-eb30-4ec8-b4d2-128ecb666b12.jpg\" style=\"border:px solid #000000\" title=\"李宇春、胡巴与观众合影\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  李宇春、胡巴与观众合影 \n</div>\n<p>　　李宇春透露，自己出演的朱金真是钱庄老板，造型霸气但内心可爱，而且很容易上当受骗，因此常常被梁朝伟饰演的屠四谷有借无还。谈到电影里的两套造型，李宇春坦言十分喜欢，“我当时先试的黑色，非常美、非常华丽，试金色时觉得真的很富贵，很像宫廷大戏，当时很吃惊，但是我还是觉得非常惊喜，很有挑战”。</p>\n<p>　　“我一直想尝试喜剧角色。”李宇春表示，自己从拿到剧本到拍摄都非常开心，就连配音的时候还一度笑出眼泪。“这次是拍到我内心想拍的喜剧题材，我朋友来探班都没认出我，还是比较惊喜。”</p>\n<p>　　电影里，李宇春和梁朝伟有不少对手戏，虽然二人并非首次合作，但李宇春依然很兴奋，“之前跟他是客串，不过瘾，这次跟他合作学到很多东西，因为他非常认真，能带新演员进入角色当中”。</p>\n<div style=\"text-align:center\"> \n <img alt=\"李宇春\" src=\"upload/image/fcfdfb10-aea3-4a01-bb19-1a02e7f1f842.jpg\" style=\"border:px solid #000000\" title=\"李宇春\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  李宇春 \n</div>\n<p>　　李宇春也表示，虽然梁朝伟话不多，但平时也会一起聊天，“我们在试戏的时候，彼此也有默契”。</p>\n<p>　　至于首次在影视剧中使用家乡话，李宇春笑称看起来本应得心应手，但其实也并不顺利，“其实角色跟我的讲话方式不太像，只是语言我熟悉一些，所以当时跟表演老师设计语言时，我自己也写了很多东西，虽然拍摄中有一些不适应，有时候还会说着说着串到普通话上，但后来看一下还是蛮搞笑的”。</p>\n<p>　　据悉，《捉妖记2》将于2018年大年初一上映。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/fcfdfb10-aea3-4a01-bb19-1a02e7f1f842.jpg',NULL,1327,'2006-04-05 11:15:00','2018-01-06 14:47:17','68',0),(3141,'答好新时代的考卷','高辰','中国青年网','<div style=\"text-align:center\"> \n <img alt=\"　1月5日，新进中央委员会的委员、候补委员和省部级主要领导干部学习贯彻习近平新时代中国特色社会主义思想和党的十九大精神研讨班在中央党校开班。中共中央总书记、国家主席、中央军委主席习近平在开班式上发表重要讲话。新华社记者 鞠鹏 摄\" src=\"upload/image/29aa6789-ca4f-4d39-8131-ad53cf8e35d0.jpg\" style=\"border:px solid #000000\" title=\"　1月5日，新进中央委员会的委员、候补委员和省部级主要领导干部学习贯彻习近平新时代中国特色社会主义思想和党的十九大精神研讨班在中央党校开班。中共中央总书记、国家主席、中央军委主席习近平在开班式上发表重要讲话。新华社记者 鞠鹏 摄\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  1月5日，新进中央委员会的委员、候补委员和省部级主要领导干部学习贯彻习近平新时代中国特色社会主义思想和党的十九大精神研讨班在中央党校开班。中共中央总书记、国家主席、中央军委主席习近平在开班式上发表重要讲话。新华社记者 鞠鹏 摄 \n</div>\n<p><strong>　　答好新时代的考卷</strong></p>\n<p>　　“时代是出卷人，我们是答卷人，人民是阅卷人。”</p>\n<p>　　1月5日，习近平总书记在学习贯彻党的十九大精神研讨班开班式上发表重要讲话，深刻阐述坚持和发展中国特色社会主义要一以贯之、推进党的建设新的伟大工程要一以贯之、增强忧患意识、防范风险挑战要一以贯之等重大问题，为全党深入学习贯彻习近平新时代中国特色社会主义思想和党的十九大精神，更好地坚持和发展中国特色社会主义进一步指明了方向。</p>\n<p>　　我们党从诞生起就时刻保持着革命的精神，不因胜利而骄傲，不因成就而懈怠，不因困难而退缩，随时身处时代的考场之中，认真答题，接受人民评判。</p>\n<p>　　自1949年起，中国共产党始终牢记“两个务必”，不骄不躁、艰苦奋斗，经受了革命胜利后的考验，带领中国人民经过近30年的建设，建立了比较完整的工业体系，让祖国自立于世界民族之林，党向人民交了一份沉甸甸的答卷。30年后，我们走上了改革开放的时代考场，高举改革的大旗，热情拥抱世界，开启了中国大步赶上世界潮流的新局。</p>\n<p>　　如今，站在新时代的考场，时代一片灿烂。我们赓续奋斗，用5年的时间，取得了历史性成就，中国跃升为全球第二大经济体，综合国力与日俱增，人民生活极大改善，同时党风有了根本性转变。此刻，我们也处在党团结带领全国各族人民决胜全面建成小康社会，奋力夺取新时代中国特色社会主义伟大胜利的关键时期，时代的命题更深刻，答题的难度更大，人民的阅卷更严格，要交上一份让人民满意的答卷，需要下大功夫。</p>\n<p>　　<strong>答好新时代的考卷，必须提高政治站位。</strong>时代给出的复杂考卷，蕴含着瞬息变幻的政治风云。举什么旗、走什么路，是事关政治方向的问题。答题者必须提高政治站位，高屋建瓴，增强政治意识，坚定政治信仰，站稳政治立场，严明政治纪律，提升政治水准，以博大雄浑的政治底气，方能审好题，答好题。</p>\n<p>　　<strong>答好新时代的考卷，必须树立历史眼光。</strong>时代给出的浩繁考卷，蕴含着沧桑巨变的历史踪影。“历史是过去传到将来的回声”，能看到多远的过去，就能看到多远的未来。答题者必须以历史的眼光审视现实，不忘初心，不忘走过的路，以史为鉴，汲取治国理政的智慧，在历史和现实的结合点给出正确的答案。</p>\n<p>　　<strong>答好新时代的考卷，必须强化理论思维。</strong>时代给出的深奥考卷，蕴含着深邃神奇的理论韵味。习近平新时代中国特色社会主义思想，是我们解答新时代考卷的理论指南，这里有定理、有公式、有规律，循着理论的路线，我们答题的思路一定清晰明朗，紧扣人民。</p>\n<p>　　<strong>答好新时代的考卷，必须增强大局观念。</strong>时代给出的宏大考卷，蕴含着包罗万象的大局棋盘。答题者必须牢固树立高度自觉的大局意识，自觉从大局看问题，把工作放到大局中去思考、定位、摆布，做到正确认识大局、自觉服从大局、坚决维护大局，才能总揽全局，把握答卷的大纲。</p>\n<p>　　<strong>答好新时代的考卷，必须丰富知识素养。</strong>时代给出的精深考卷，蕴含着繁复的知识构成。外行不能领导内行，新时代的知识层出不穷、日新月异，大数据、云计算、人工智能等具有时代标志的知识扑面而来，答题者一定要成为现代新颖知识的学习者、现代新颖知识的运用者，以扎实的知识功底解答新时代的考卷。</p>\n<p>　　<strong>答好新时代的考卷，必须坚持问题导向。</strong>时代给出的悬疑考卷，蕴含着纷繁的问题集成。坚持问题导向，解决突出问题，是我们重要的思想方法和工作方法，也是党的十八大以来全面从严治党的鲜明特点和成功经验。问题就是实际、就是“有的放矢”的靶子，要始终坚持问题导向，把发现问题、剖析问题、解决问题作为出发点和落脚点，答题才能精准。</p>\n<p>　　总之，要从历史和现实相贯通、国际和国内相关联、理论和实际相结合的宽广视角，对一些重大理论和实践问题进行思考和把握，做到三个“一以贯之”，一以贯之坚持和发展中国特色社会主义，要有坚定自信的革命眼光；一以贯之推进党的建设新的伟大工程，要有持续不断的革命精神；一以贯之增强忧患意识、防范风险挑战，要有积极应对的革命手段。</p>\n<p>　　时代是出卷人，我们要融入时代；我们是答卷人，我们要有自信；人民是阅卷人，我们要敬畏人民。我们一定要呼应人民对美好生活的向往，不负人民期待，努力答好时代考卷。要深入学习领会贯彻落实习近平总书记“1・5”重要讲话精神，紧密团结在以习近平同志为核心的党中央周围，为在新的更高起点上开创新时代中国特色社会主义事业新局面而努力奋斗，向人民交上一份知民心、暖民心的优异答卷。(中国青年网特约评论员 蔡恩泽)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/29aa6789-ca4f-4d39-8131-ad53cf8e35d0.jpg',NULL,681,'2018-01-06 13:03:00','2018-01-06 14:47:17','64',0),(3142,'2017网络消费“她经济”增长迅速 “818”成年度消费节点','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>1月5日电 近日 ，央视《中国财经报道》对2017年网络消费数据进行了盘点报道。报道称“她经济”增长迅速，消费者总体“回归理性”，而“818”成为“双11”外的又一年度消费节点。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/4ec56719-4930-4907-b646-b59f2973026b.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　<strong>“她经济”是未来消费“风口”</strong></p>\n<p>　　据报道，群邑智库基于中国15-64岁女性人群的调查显示：从消费数据来看，中国女性消费者在过去一年平均网购总花费从2013年的3830元增加到2017年的13254元，近5年的增幅达到246%。女性用户崛起，随着线上购物融入日常生活，女性消费开始在家庭消费的总舵领域引领线上线下零售市场。目前，内地女性经济市场规模近2.5万亿元，至2019年有望达到4.5万亿，是未来消费行业的“风口”。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/9d4eb416-93a6-4029-babf-e382380b6bee.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　随着电商物流、服务条件的进一步改善，线上购物不断在新的产品品类和用户场景呈现爆发式增长，比如“宠物消费”、“奶粉消费”等。</p>\n<p>　　根据《2017年中国宠物行业白皮书》显示，90后为主的年轻群体成为宠物消费主力军，其中女性占比超过7成。宠物成为年轻人，尤其是女性的“真爱”，她们对宠物不惜花费，选购宠物用品时也更注重质量，价格敏感度较低。“她经济”拉动了“它经济”高速增长。</p>\n<p>　　<img src=\"upload/image/e9e8183c-65ca-456e-bf31-7c899efcabe6.jpg\" style=\"border:px solid #000000\"></p>\n<p>　　<strong>“818”成为年度第二消费节点</strong></p>\n<p>　　关于月度消费额占比方面，《中国财经报道》盘点传递出很多新的信息。</p>\n<p>　　首先是消费逐步归于理性。据报道，与往年的购物占比集中在11月不同，2017年消费者购物趋于理性，月度消费差距缩小。根据报道的数据显示：除了比较突出的11月占比14%、8月占比11%、12月占比10%外，其他月度消费趋平稳，如6月、4月、7月占比8%，1月、3月、5月、9月、10月占比7%。</p>\n<p>　　另外，8月份消费成绩亮眼，“818发烧节”后来居上。虽然月度差距在缩小，但是8月消费数据占比较高，其消费金额占年度总额的11%，仅次于11月的14%。苏宁易购“818发烧节”已经成为继双11后的又一购物大节。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/e9e8183c-65ca-456e-bf31-7c899efcabe6.jpg',NULL,763,'2018-01-05 15:03:00','2018-01-06 14:47:18','82',0),(3143,'同比增长6.8% 一汽-大众大众品牌2017年销售140.5万辆','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>1月5电 最新数据显示，2017年12月，一汽-大众大众品牌全系车型集体发力，销售新车150,071辆，同比增长7.5%， 助力大众品牌成功实现140.5万辆的年度销量目标。截止目前，2017年1-12月，一汽-大众大众品牌累计销量达1,405,088辆，同比增长6.8%，在覆盖市场持续下降，且没有SUV车型的情况下，再次逆势增长，跑赢了市场大盘。值得关注的是，大众品牌旗下的各款高品质车型全年销量均表现不俗，持续领跑各自细分市场。</p>\n<div style=\"text-align:center\"> \n <img alt=\"迈腾全年累计销量达213,336辆，连续11个月领跑B级车市场\" src=\"upload/image/b657573e-0aff-4a65-b8f9-d3f1d3f8dc2a.jpg\" style=\"border:px solid #000000\" title=\"迈腾全年累计销量达213,336辆，连续11个月领跑B级车市场\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  迈腾全年累计销量达213,336辆，连续11个月领跑B级车市场 \n</div>\n<p>　　其中，“B级车市场领导者”迈腾表现最为亮眼，全年累计销量达213,336辆，同比大幅增长28.4%，连续11个月领跑B级车市场，稳坐B级车市场销量冠军宝座。</p>\n<p>　　“出彩家轿”宝来表现同样抢眼，全年终端销量达248,184辆，同比增长12.5%，继续强势引领A级主流三厢轿车细分市场，携手更多热爱家庭的年轻用户创造精彩人生。而旅行车市场的爆款车型――蔚领，全年销售新车达60,926辆，成为旅行车市场实至名归的开拓者。蔚领的到来，带动中国旅行车市场呈几何式增长，并始终以强劲的势头带领中国旅行车在市场上开疆扩土。</p>\n<div style=\"text-align:center\"> \n <img alt=\"速腾全年销量达328,550辆，继续保持A 级市场的冠军地位\" src=\"upload/image/60cda192-6c6e-4613-9836-7dba918ad6da.jpg\" style=\"border:px solid #000000\" title=\"速腾全年销量达328,550辆，继续保持A 级市场的冠军地位\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  速腾全年销量达328,550辆，继续保持A 级市场的冠军地位 \n</div>\n<p>　　不仅如此，A+级细分市场的标杆车型速腾，全年销量高达328,550辆，继续保持A+级市场的冠军地位。而随着速腾2018年型在市场中“就位”，速腾定能在新一年里交出一份答卷。</p>\n<p>　　“车坛常青树”捷达，不仅传承了历代车型的经典品质，同样传承下来的还有在市场中不可撼动的地位，2017全年销售新车323,408辆，书写销量新传奇。此外，成绩同样亮眼的还有高尔夫家族(含高尔夫•嘉旅)，全年销量达207,352辆，连续47个月蝉联细分市场第一，其中，高尔夫•嘉旅2017年累计销量达48,663辆，同比增长60.5%，交出一份漂亮的成绩单。而最美大众车CC全年累计销售新车23,332辆，以其优雅迷人的独特气质继续在细分市场中占有一席之地。</p>\n<div style=\"text-align:center\"> \n <img alt=\"蔚领上市以来月均销量5000 ，当之无愧的成为中国旅行车市场领军者\" src=\"upload/image/b2a1bb26-6a4c-4f5a-aeff-72b8006047f8.jpg\" style=\"border:px solid #000000\" title=\"蔚领上市以来月均销量5000 ，当之无愧的成为中国旅行车市场领军者\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  蔚领上市以来月均销量5000 ，当之无愧的成为中国旅行车市场领军者 \n</div>\n<p>　　业内人士评价，在2017年车市微增长的大环境下，一汽-大众凭借强大的产品实力和品牌号召力，全年销量再创年度新高，成功完成年度销量目标，实现了华丽收官。而随着一汽-大众大众品牌新产品的陆续启动以及品牌营销方面的重拳出击，2018年的市场表现将更加值得期待。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/b2a1bb26-6a4c-4f5a-aeff-72b8006047f8.jpg',NULL,1360,'2018-01-05 16:53:00','2018-01-06 14:47:18','72',0),(3144,'青岛啤酒携手天猫欢聚日送2018开年大礼','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>1月5日电 2018新年伊始，一向主打“畅享欢聚时刻”的青岛啤酒携手天猫欢聚日，打造了一场“双重欢聚”购物盛宴，为消费者送上了2018开年大礼包。</p>\n<p>　　远销全球100多个国家和地区的青岛啤酒，在线下把“欢聚”传递到世界各个角落。而在线上电商平台，无论是新品推出还是粉丝福利，天猫青岛啤酒官方旗舰店都是青啤粉丝“尝鲜”和“欢聚”的主阵地。</p>\n<p>　　天猫欢聚日作为天猫聚划算重磅推出的优势IP活动平台，主要打造品牌粉丝互动狂欢日，是仅次于双十一的最in品牌粉丝狂欢节。这一次，天猫欢聚日与青岛啤酒共同发力，在2018年1月2日－1月4日，推出青岛啤酒x天猫欢聚日的啤酒粉丝狂欢节，当欢聚遇见欢聚，把快乐无限传递。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/ff9046a3-fb8c-4f16-9966-d0ec0a285879.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　<strong>网购爆款青岛啤酒TOP3出炉 经典、奥古特各占一席</strong></p>\n<p>　　为期三天的青岛啤酒天猫欢聚日，为消费者送上了惊喜福利：“买N送N” 、“前30分钟限时购”、“支付最高得海岛游”等方式，以极具性价比的价格回馈给消费者。青岛啤酒此次与天猫欢聚日的合作势头猛劲，销量直抵2017年双十一。</p>\n<p>　　数据显示：在天猫欢聚日期间，青岛啤酒经典品类、“极地欢聚罐”、“青岛啤酒奥古特”，成为了网购爆款TOP3。</p>\n<p>　　其中，青岛啤酒经典系列因其拥有的广泛消费受众而荣登销量榜首，是青啤粉丝常购的基础款；青啤奥古特则历来以“百年经典 一世传奇”的淳厚与复古而备受商务人士青睐，也可以说是资深青啤粉丝的进阶款。而青岛啤酒“极地欢聚罐”作为青岛啤酒2017-2018的跨年之作，因其创意十足的外观和其倡导消费者自由远足的生活态度，而备受年轻消费群体的青睐。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/4ab72291-fc39-42ff-85ab-8a206b8c0c82.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　<strong>青岛啤酒跨年之作 “极地欢聚罐”热卖</strong></p>\n<p>　　2017年，青岛啤酒将欢聚延伸至世界最南端――南极，用脚步丈量世界的边际。并在离南极最近的乌斯怀亚港开了一家“极地欢聚吧”，让登陆南极的勇士畅饮青啤，共享欢聚。在线上，用户还可以通过“极地欢聚吧” 互动H5，完成线上“存酒”，鼓励用户去享受欢聚，传递欢聚。</p>\n<p>　　青岛啤酒“极地欢聚罐”就是在此基础上产生的新品，推出三款创意十足的“极地欢聚罐”，融合了南极呆萌企鹅，可爱南方狼，憨厚海豹的卡通形象。这一系列的主题“极地欢聚罐”也成为了此次青岛啤酒 x 天猫欢聚日的亮点产品，用户可以在青岛啤酒的天猫旗舰店购买到这款限量“极地欢聚罐”，即使你身未能抵达南极，但是能和来自南极的呆萌“新朋友”们在一起，让你“嗨”爆新年欢聚气氛。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/4c72bfdf-e5f7-4f87-9e85-56d6baf329f3.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　<strong>青岛啤酒趣味电商大数据，谁最舍得花钱？</strong></p>\n<p>　　数据显示：在青岛啤酒天猫欢聚日期间，网购青岛啤酒的TOP5区域分别分布在广东、山东、江苏、浙江和河南，这五个区域的消费者在啤酒的支出更多。</p>\n<p>　　平均到单笔消费上，最舍得花钱网购啤酒的城市TOP3分别是济南、武汉和杭州。</p>\n<p>　　另有数据显示，在女性消费者中，西藏、青海、安徽和黑龙江的妹子最为能喝，而河北、江苏、山西、广西、江西的妹子最不能喝。</p>\n<p>　　在所有网购青岛啤酒的消费群体中，男女比例大概为6：4。年龄结构上，80、90后占据主流，占到总消费者比例的70%。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/4c72bfdf-e5f7-4f87-9e85-56d6baf329f3.jpg',NULL,1983,'2018-01-05 17:15:00','2018-01-06 14:47:19','82',0),(3145,'谢霆锋出任腾讯音乐娱乐2018开年音乐大使 新歌曝光','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>1月5日电 新年伊始，在开心和喜悦的节日氛围中，谢霆锋为歌迷们献上了一份令人激动的新年“礼物”。4日，腾讯音乐娱乐集团X英皇娱乐谢霆锋2018异想天开音乐计划发布会在京举行。发布会上谢霆锋发布了2018年全年个人音乐计划，包括新歌《异想天开》上线、美食与音乐的跨界“美食音乐节”，腾讯音乐娱乐集团副总裁Andy吴伟林授予谢霆锋“腾讯音乐娱乐集团2018开年音乐大使“称号，并表示腾讯音乐娱乐集团将为谢霆锋提供全产业链保证，与霆锋一起打开音乐市场更多想象空间。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/fc7afbc1-c577-4361-838b-620c9276c77d.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　<strong>谢霆锋出任“腾讯音乐娱乐2018开年音乐大使”</strong></p>\n<p>　　发布会现场，谢霆锋新歌MV《异想天开》首度曝光，并于当天独家上线腾讯音乐娱乐集团旗下QQ音乐、酷狗音乐、酷我音乐三大平台。新歌曲沿用了谢霆锋最爱的Pop Rock风，他表示“希望用自己的音乐，将过去一段日子，往外拼搏的精神和面对困难时的态度，一口气灌录到全新歌曲上，带出追寻梦想的坚持”。</p>\n<p>　　作为谢霆锋音乐道路上的重要伙伴，腾讯音乐娱乐集团也为霆锋准备了一份特殊的礼物，授予霆锋“腾讯音乐娱乐集团2018开年音乐大使”称号，至此，腾讯音乐娱乐集团“Music+”继李宇春、周笔畅、张艺兴后，又加盟一员实力大将。“Music+” 计划是腾讯“数字音乐生态圈”构建和激活数字音乐行业和整个音乐产业的战略计划。腾讯音乐娱乐集团将为加入“Music+”计划内艺人提供包括作品发行、宣传推广、数据管理、演出支持、作品收益、版权管理与权利保护等全方位定制化服务，全产业链支持艺人的未来音乐版图。</p>\n<p>　　腾讯音乐娱乐集团副总裁Andy吴伟林表示“2018年腾讯音乐娱乐集团将与霆锋展开更多深度合作，结合腾讯系平台资源全力助力霆锋的音乐计划，一起携手打开音乐市场更多想象空间。”据悉，本次发布会获得了腾讯音乐娱乐集团旗下QQ音乐，酷狗音乐，酷狗直播，全民K歌四大平台的全程直播支持，以打造更多维度的联合传播矩阵。</p>\n<p>　　活动现场，Andy吴伟林还亲自送上了腾讯音乐娱乐集团为霆锋专属定制的一把异想天开定制版火焰吉他，希望霆锋可以用音乐开启异想天开的世界，再创经典。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/69c8f330-f7c1-45f9-8dfd-770e091185f1.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　<strong>18载“锋”芒不断档 以音乐诠释不羁人生</strong></p>\n<p>　　在发布会入场通道上，腾讯音乐娱乐集团根据谢霆锋的成长历程精心设置了一个音乐序厅，让到场嘉宾未进现场提前感受到谢霆锋的音乐魅力。发布会一开场，一组谢霆锋音乐成长史的视频短片深深吸引了在场嘉宾的注意力。</p>\n<p>　　从《谢谢你的爱1999》的横空出世开始，谢霆锋的音乐一直备受关注。作为英皇娱乐的当家艺人谢霆锋，1999年发行其第一张国语专辑一举获得台湾唱片销量榜冠军，2000年举办个人“Viva Live”世界巡回演唱会，第一次演唱会便在香港红�|体育馆连续举办六场。2001年谢霆锋在吉林长春举行了他在中国内地的首场个人演唱会，开创了当时长春市演出行业的最大型个唱； 同年7月28日霆锋在上海八万人体育场举行个唱， 直到2003年霆锋演唱会的足迹已遍及大陆、港台、日本、美国、澳洲及东南亚各地。</p>\n<p>　　正因为向来凡事都拼尽百分之二百的努力，才有了现如今成绩斐然的谢霆锋。回忆其成长历程，每一步都精彩斐然，但其初心未变，也从未离开自己所熟悉及喜爱的音乐圈，希望以音乐的形式可以和歌迷朋友们有更多交流，此次新歌《异想天开》，就承载了谢霆锋希望跟年轻人分享发奋的理念和态度。</p>\n<p>　　新歌《异想天开》采取一曲二词的形式，同时发布了国语版和粤语版两种视点，国语版《异想天开》以一个追梦人的口吻劝勉大家遇困难时不要轻言放弃，一定要拼尽全力，无所惧畏，甚至任性地“为所欲为”。活动现场，谢霆锋的艺人好友也同步发来了VCR祝福，祝霆锋音乐事业更加精彩。</p>\n<p>　　<strong>谢霆锋携手腾讯音乐娱乐 音乐也可以“异想天开”</strong></p>\n<p>　　谢霆锋2018音乐计划不仅包含现场曝光的新歌《异想天开》，同时公布了与腾讯音乐娱乐集团合作展开的美食音乐节的计划，英皇(北京)文化发展有限公司行政总裁兼英皇娱乐(香港)有限公司营运总裁霍汶希表示：“在谢霆锋的生命中，音乐和美食都占据着很重要的部分，他一直在探索美食与音乐的跨界互动新理念――美食与音乐不仅需要观赏，更需要品味和经历”。随后英皇娱乐(香港)有限公司行政总裁许佩斯也上台为谢霆锋送上了礼物，祝福2018年谢霆锋音乐事业越来越精彩。</p>\n<p>　　2018年谢霆锋将携手腾讯音乐娱乐集团，举办自己的首次美食音乐节，将自己最喜欢的两件事情结合在一起带给大家不一样的体验，并强调每一次推出的音乐都会非常认真和重视，希望大家一起期待接下来2018异想天开音乐计划，在随后的启动仪式中，腾讯音乐娱乐集团、英皇娱乐与霆锋共同开启了谢霆锋2018异想天开音乐计划。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/69c8f330-f7c1-45f9-8dfd-770e091185f1.jpg',NULL,964,'2018-01-05 15:31:00','2018-01-06 14:47:19','68',0),(3146,'华南大部地区强降雨来袭 广东广西福建将现大到暴雨','罗攀','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>1月6日电 据中央气象台网站消息，本周末，雨雪和寒冷将成为全国多地的天气关键词。河南、湖北、安徽等地将再度出现明显降雪，局地暴雪，部分降雪区域与前两日有所重合；华南大部地区则成为强降雨的聚集地，广东、广西、福建将普遍出现大到暴雨天气。另外，7-9日，受冷空气影响，我国中东部大部地区还将伴有大风降温天气，小寒过后，寒意更浓。</p>\n<p>　　中央气象台今晨继续发布暴雪蓝色预警：预计，6日08时至7日08时，陕西关中平原东部、山西南部、河南中西部、安徽西北部、湖北北部等地有大雪，其中河南东南部、湖北东北部局地有暴雪(10～14毫米)，新增积雪深度4～8厘米，局地超过10厘米。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/56ee87cf-ada2-45e5-b6b9-7360f7df0268.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　周日(7日)，降雪区域会进一步东移，东北地区东部以及山东东部、安徽中部等地有明显降雪，其中，安徽中部局地有暴雪。</p>\n<p>　　气象专家提醒，前几日，河南、湖北、安徽等地已遭遇强降雪袭击，局地出现大暴雪或特大暴雪。本轮降雪与前次降雪区域有所重合，会导致部分受灾地区雪上加霜，给交通出行带来不利影响，建议公众应根据天气状况合理安排出行，外出时要格外注意交通安全。</p>\n<p>　　在上轮雨雪天气过程中围观全程的华南地区这次成为降雨的主角。预计未来三天，江南中东部、华南等地有中到大雨，其中江南南部、华南中北部等地的部分地区有暴雨，局地大暴雨(日雨量100～120毫米)，华南中南部局地伴有雷暴天气；最强降水时段出现在6日至7日白天。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/0056aba4-7f90-466a-88fe-96952550aa7d.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　预计，7日至9日，受冷空气影响，中东部地区将有大风降温天气，大部地区气温将下降4-8℃，局地降温可达10℃，并伴有4-6级风。8日至10日，我国东部海区将有7-8级大风。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/0056aba4-7f90-466a-88fe-96952550aa7d.jpg',NULL,1676,'2018-01-06 12:30:00','2018-01-06 14:47:19','64',0),(3147,'黑龙江省全国趣味冰雪创意成果三项赛开赛(图)','高辰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>伊春1月6日电 (记者 魏来)2018黑龙江省全国趣味冰雪创意成果三项赛(金山小镇站)在伊春九峰山养心谷景区开赛。本次比赛共吸引来自韩国及中国14个省市的150余名冰雪体育爱好者和游客参赛。</p>\n<p>　　本次赛事由黑龙江省体育局、伊春市人民政府主办，是对黑龙江省体育局积极推动的两届全国趣味冰雪活动创意大赛成果的一次展示。该赛事共设雪地穿越徒步赛、雪圈竞速赛、趣味冰雪场地赛三大比赛项目，其中趣味冰雪场地赛由冰上撑撬加速赛、双脚冰橇竞速赛、踏冰蜘蛛绕桩赛组成。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为参赛选手正在比赛中。　魏来 摄\" src=\"upload/image/8ea1866b-535e-425e-aa90-05d67ab80349.jpg\" title=\"图为参赛选手正在比赛中。　魏来 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为参赛选手正在比赛中。　魏来 摄 \n</div>\n<p>　　比赛当日，当地气温零下20摄氏度，但寒冷的天气依然挡不住冰雪体育爱好者和游客参赛的热情。在趣味冰雪场地，来自哈尔滨的游客对<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>记者说：“这是我第一次参加趣味冰雪项目比赛。趣味比赛不仅能让我们享受快乐，还能锻炼体魄、磨练意志。”</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为参赛选手正在比赛中。　魏来 摄\" src=\"upload/image/a54cc242-37ac-4787-8a83-c38507cd8ed0.jpg\" title=\"图为参赛选手正在比赛中。　魏来 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为参赛选手正在比赛中。　魏来 摄 \n</div>\n<p>　　据本次赛事主办方相关负责人介绍，本次冰上撑撬加速赛、踏冰蜘蛛绕桩赛，是由优秀冰雪趣味创意成果转化出的新玩法。该系列赛事的成功举办，不仅成功对接和转化了全国趣味冰雪活动创意大赛的优秀成果，还将通过充满竞技色彩的比拼项目、富有趣味的冰雪体验活动设置，带动全民参与冰雪运动热情。</p>\n<p>　　据了解，本次赛事是黑龙江省全国趣味冰雪活动创意成果系列赛的首站。黑龙江省体育局还将于1月中下旬，分别在哈尔滨市的“赏冰乐雪”园区和亚布力举行两站比赛。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/a54cc242-37ac-4787-8a83-c38507cd8ed0.jpg',NULL,992,'2018-01-06 14:26:00','2018-01-06 14:47:20','74',0),(3148,'东盟驻邕使节及留学生举办“走近东盟-2018新年音乐会”','高辰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>南宁1月6日电(记者 杨陈)“这是我第一次在中国过新年，虽然很想念家人，但是在这里能听到家乡的音乐，跟许多同胞和东盟各国的年轻人一起庆祝新年，也是很有意义的事。”来自广西外国语学院的泰国留学生高帕静5日在接受记者采访时表示。</p>\n<p>　　当天，由广西国际文化交流中心、南宁市人民对外友好协会和南宁市博物馆共同举办的“走近东盟―2018新年音乐会”在南宁举行。活动邀请到马来西亚、老挝、缅甸、泰国、越南、柬埔寨等东盟国家驻南宁总领事馆的外交使节及留学生代表共300余人出席。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为东盟留学生在“走近东盟―2018新年音乐会”展示各国歌舞。　杨陈 摄\" src=\"upload/image/141b05d1-726a-4c9c-90ac-de662d89c0ea.JPG\" title=\"图为东盟留学生在“走近东盟―2018新年音乐会”展示各国歌舞。　杨陈 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为东盟留学生在“走近东盟―2018新年音乐会”展示各国歌舞。　杨陈 摄 \n</div>\n<p>　　在音乐会上，来自广西歌舞剧院的8位演奏家首先用中国民族乐器依次演奏了《南海姑娘》、《占巴花之歌》、《海鸥》、《星星索》、《茉莉花》等东盟十国和中国的民族歌曲。当泰国民歌《相思河畔》的旋律一响起，来华留学已有三年的高帕静一下湿了眼眶。“往年新年都是放假回家，与家人一起过，今年只有我一个人过，有些孤单。不过还好认识了很多新朋友，对我都非常友善亲切。新的一年，希望一切顺利。”</p>\n<p>　　在歌曲中化解乡愁的还有马来西亚驻南宁总领事黄奕瑞。在他看来，没有什么艺术形式比音乐更能够碰触一个民族的灵魂。“音乐和舞蹈既是属于民族的，也是属于世界的。我们期待通过音乐会上的音乐和舞蹈，把我们国家的文化魅力展现在大家眼前，让东盟国家的艺术风情浸入中国民众的心扉，从而增进彼此间的沟通和理解，加强人文交流，促进文明互鉴和文化合作，共同分享开放包容所带来的丰硕成果。”</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为东盟留学生在“走近东盟―2018新年音乐会”展示各国歌舞。　梁翔宇 摄\" src=\"upload/image/ee6f194a-1b28-4e4d-ba04-ea780ec9e574.jpg\" title=\"图为东盟留学生在“走近东盟―2018新年音乐会”展示各国歌舞。　梁翔宇 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为东盟留学生在“走近东盟―2018新年音乐会”展示各国歌舞。　梁翔宇 摄 \n</div>\n<p>　　广西壮族自治区人大常委会副主任、广西国际文化交流中心理事长高雄对此表示认同。他说，广西与东盟山水相连，一湾连七国。希望通过举办此次音乐会，加强双方文化交流，促进中国与东盟各国之间的了解和友谊，促进民心相通，并借此机会向在广西的留学生和东盟使节致以新年祝福。</p>\n<p>　　广西是连接中国与东盟的前沿窗口，也是东盟国家学生到中国留学的热门省区，双方留学生交流人数达到双向过万人。在当天的音乐会上，来自老挝、缅甸、泰国、越南的留学生代表，亦身着各国的特色民族服饰上台表演。大家载歌载舞，在异国他乡共同祈愿2018平安顺利。临近尾声时，广西艺术学院越南籍留学生清�f和何氏梅的《浮萍流云》歌伴舞表演，还将中国民谣《送别》融入到越南民歌之中，更是引发在场观众共鸣，掀起晚会高潮。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为广西艺术学院越南籍留学生清�f和何氏梅展示《浮萍流云》歌伴舞表演。　韦恬 摄\" src=\"upload/image/bf14beac-2e3d-4d0a-96e6-cc7b0349002a.jpg\" title=\"图为广西艺术学院越南籍留学生清�f和何氏梅展示《浮萍流云》歌伴舞表演。　韦恬 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为广西艺术学院越南籍留学生清�f和何氏梅展示《浮萍流云》歌伴舞表演。　韦恬 摄 \n</div>\n<p>　　在谈到为何在博物馆举办此次音乐会时，南宁市博物馆张晓剑表示，南宁是广西的首府，中国-东盟博览会的永久举办地，自古不同的文化在这里交流融合，打上了兼容开放的文化烙印。我们希望将文物的静态文化与音乐的动态文化相结合，以此促进中国与东盟之间文化交流，推广各具特色的民族文化，增进不同国家与民族之间的理解与认识。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/bf14beac-2e3d-4d0a-96e6-cc7b0349002a.jpg',NULL,1118,'2018-01-06 09:22:00','2018-01-06 14:47:20','75',0),(3149,'掌阅2017阅读榜单：阅读时间增加 女性付费意愿高','陈海峰','中国新闻网','<div style=\"text-align:center\"> \n <img alt=\"图片来源：掌阅提供\" src=\"upload/image/6177f577-b223-4d4f-a200-c2a164f3aaa0.jpg\" style=\"border:px solid #000000\" title=\"图片来源：掌阅提供\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  图片来源：掌阅提供 \n</div>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>北京1月5日电(记者 上官云)5日，“掌阅2017阅读榜单”在北京公布。掌阅版权总监吴贲对该榜单进行解读，表示其呈现阅读时长增加、女性付费意愿更高等7大阅读趋势，此外在“2017年出版榜单”中，入围新书较少，与之相比，掌阅文学口碑新作频出。</p>\n<p>　　根据掌阅本次公布的阅读大数据来看，2017年68%为25岁及25岁以下用户，90后00后占比7成；男性用户占比51%，女生占比49%，但女性付费意愿更高，相较于2016年，用户平均阅读时长更长(从35到38分钟)，“重度”用户超过每天1小时。吴贲介绍，掌阅目前已达到月活1亿用户，月活跃用户比上年有显著增加。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图片来源：掌阅提供\" src=\"upload/image/b37a6dbb-8634-447e-a606-3cc1f75f09c6.jpg\" style=\"border:px solid #000000\" title=\"图片来源：掌阅提供\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  图片来源：掌阅提供 \n</div>\n<p>　　“时长的增长有很多因素，比如新书推出速度的提升，用户对数字阅读的更加关注，以及掌阅在2017年对出版书、网络文学、漫画等方面的加大投入。”掌阅文化总编辑谢思鹏解释道。</p>\n<p>　　该榜单还显示，在“最受欢迎的连载小说”一项中，《逍遥游》、《盛唐风华》等作品上榜，题材丰富多样，覆盖了玄幻、都市、历史、古代言情、现代言情、仙侠、悬疑探险、穿越等各个类型。</p>\n<p>　　“2017年，掌阅文学在内容方面的深挖已经初见成效，作品类型日益丰富，尤其是历史分类。掌阅签约作者月关的《逍遥游》和天使奥斯卡的《盛唐风华》分别凭借113万、96万书圈粉丝成为当年历史类的热门作品，东一方的《帝师》是历史军事小说，连载期间曾获百度搜索小说风云榜历史类第一位。” 谢思鹏说。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图片来源：掌阅提供\" src=\"upload/image/d3c10ed6-01ad-42b1-b73c-748885d428a1.jpg\" style=\"border:px solid #000000\" title=\"图片来源：掌阅提供\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  图片来源：掌阅提供 \n</div>\n<p>　　值得注意的是，榜单还评选出了“最受欢迎的出版书TOP20”，《法医秦明》、《三体》纷纷上榜，《围城》等纯文学作品也在其中。吴贲解释，2016年评选的是“TOP10”作品，当时《解忧杂货店》《三体》《法医秦明》便在榜，结合这次评出的榜单，可以看到，大部分出版书榜单中的书都是常年热门的。</p>\n<p>　　“所谓‘出版书’是指有出版许可(ISBN)的书，从这个榜单中我们也可以看到，其中有很多网文IP出版物，也有纯文学作品，实际上是因为我们的出版市场正在变得越来越多元，既有传统文学，又有网络文学，这种现象近年来很普遍。” 吴贲解释道。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/d3c10ed6-01ad-42b1-b73c-748885d428a1.jpg',NULL,1243,'2018-01-06 00:06:00','2018-01-06 14:47:20','75',0),(3150,'多名中国同胞飞机上遭窃 做到这几点防破财','史建磊','中国侨网','<p>　　中国侨网1月6日电 <strong>原题：坐飞机看好你的钱！航班上发生的这件事，已有多名同胞中招</strong></p>\n<p>　　元旦刚过，春节不远，假期高峰季到来，出国旅游、回国探亲的人明显多于平日。兴高采烈踏上旅途(归途)，心里别提多美了。趁着飞行期间睡一觉，精力充沛享受假期，然而一觉醒来即将落地，有人却吓出一身冷汗：钱包、旅行袋里的现金不翼而飞，而且金额还不少！</p>\n<p>　　钱丢了、好心情也毁了，这样的糟心事相信谁也不想遇到。但令人遗憾的是，最近一段时间，已有航班发生了此类案件，受害者中不乏我们的同胞。</p>\n<p>　<strong>　一架航班多人被盗 中使馆急发安全提醒</strong></p>\n<p>　　元旦前夕，由中国某市飞往捷克布拉格的飞机上发生了一起重大盗窃案，多名中国乘客及2名外国乘客钱款被盗，中国驻捷克大使馆为此特别发出安全提醒。</p>\n<div style=\"text-align:center\"> \n <img alt=\"中国驻捷克大使馆为此特别发出安全提醒\" src=\"upload/image/66b1438c-ab2b-40e3-96ef-aab7d1e748c2.jpg\" style=\"border:px solid #000000\" title=\"中国驻捷克大使馆为此特别发出安全提醒\"> \n</div>\n<div class=\"pictext\" style=\"margin: 5px auto; text-align: center; text-indent: 2em;\">\n  中国驻捷克大使馆为此特别发出安全提醒 \n</div>\n<p>　　据同机乘客刘先生介绍，航班原本一切正常，直到降落前半小时，有乘客发现自己的包里丢了钱。乘务员通过广播发动寻找，大家纷纷翻起自己的包，结果陆续发现，自己竟然也丢钱了！数额从200欧元、500欧元、1000欧元不等，最多的竟然丢了5000美元。</p>\n<p>　　至于大家丢钱的位置，有人是座位下的小包里，大多数人是座位上方的行李箱小包中。</p>\n<p>　　见此，乘务员立即联系了布拉格警方。出乎乘客意料的是，就在一片翻找中，有人竟然在脚下发现了攥成一团的钱，各种面额都有，甚至还有一团钱正在地面滚动。</p>\n<p>　　“扒手在甩赃！”</p>\n<p>　　乘客们不约而同意识到这一点，窃贼的确也是这么想的，只是手法实在不算“高明”，反而暴露了自己。他随即被乘客发现并控制起来，飞机降落后被警方带走。</p>\n<p>　　<strong>侨领两年搜集线索 锁定“飞机大盗”</strong></p>\n<p>　　相比于单独作案的“独行侠”，团伙作案者往往更具威胁。以非洲地区为例，最近几年时有华人遭团伙盗窃的报道出现。</p>\n<p>　　有一位来自东非的侨领对盗窃行为深恶痛绝，他连续两年关注航班动态，靠自己的努力锁定了一个嫌疑团伙。</p>\n<p>　　据这位侨领介绍，目标对象由30-50人组成，每次2-3人行动，卡塔尔是主要集结地。</p>\n<p>　　卡塔尔航空、埃塞航空、肯尼亚航空、阿联酋航空的航班是主要活跃区域。据侨领观察，他们频繁出入于西亚和非洲，每个国家停留1-4天。他们故意把行李放在远离自己座位的地方，借此频繁在飞机上走动，待乘客入睡后开始盗窃，如被发现则谎称自己翻错了包。</p>\n<p>　　他们分工明确、协同作案，不少同胞的血汗钱被偷走，实在可恶！”侨领说，跨国犯罪侦破难度较大，希望自己的观察能为破案起到作用。</p>\n<p>　<strong>　飞机并没想象中安全 提高警惕防破财</strong></p>\n<p>　　在很多人的意识里，在公共交通上遭遇扒手，几率由高到底大致为公交车＞地铁＞长途车＞火车＞飞机。有这种想法无可厚非，站在小偷的角度考虑，在飞机上行窃风险较大，被发现后很难逃脱，而且机票本身就不便宜，啥也偷不到岂不成了损失？</p>\n<p>　　然而，上文提到的案件无疑给大家敲响了警钟。无论乘坐何种交通工具，出门在外都应时刻留意财务安全。结合小侨自己的出行经验，远离航班上的盗窃黑手，大家尤其要注意以下几点：</p>\n<p>　　1、钱财不多带、不外露</p>\n<p>　　现金够用即可，很多国家的银行系统没那么落后，没必要将“金库”带在身上，搞得自己神经紧张。同时也不要在飞机上清点财物，存放财物时尽量避开他人。</p>\n<p>　　2、贵重物品 目之所及</p>\n<p>　　乘机时，别让贵重物品离开自己的视线。特别是在休息前，请务必确保钱包等放在贴身处。另一个建议是，证件、信用卡和现金最好分开放置，万一发生不测可尽量减少损失。</p>\n<p>　　3、盯紧头上的行李箱</p>\n<p>　　漫漫旅途让身心疲惫，注意力难以保持，再加上机舱内昏暗的灯光，这些都使得头上的行李箱绝非万无一失。如果有人开启了你放东西的行李架，最好观察一下他的动作，直到行李架关闭再转移视线。出门在外，防人之心不可无。</p>\n<p>　　4、可疑人员 有迹可循</p>\n<p>　　东方航空曾发布乘机安全提示，其中一条指出，飞机起飞后，不法分子往往会坐到后排无人的座位进行观察，当大多数旅客睡熟后，便佯装翻找行李打开行李架，将别人的包袋拿到厕所或无人处实施盗窃。</p>\n<p>　　因此，如果有人不安于原位，经常离座走动、频繁进出洗手间，并且喜欢坐在身边无人的座位上，你就应该小心留意了。</p>\n<p>　　5、下飞机前检查行李物品</p>\n<p>　　下飞机前发现钱物丢失，还有追回可能，否则恐怕希望渺茫。</p>\n<p>　　发现异状，要立即与乘务员联系，但方式不应过于引人注意，否则可能打草惊蛇，给扒手甩赃、销赃留下可乘之机。(来源：中国侨网官方微信，ID：qiaowangzhongguo，编辑：付强）</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/b6db2e66-77c9-4d74-b844-b6ae90743c8d.jpg\" style=\"border:px solid #000000\"> \n</div>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/b6db2e66-77c9-4d74-b844-b6ae90743c8d.jpg',NULL,527,'2018-01-06 09:45:00','2018-01-06 14:47:20','73',0),(3151,'200余幅俄罗斯艺术品黑龙江展出 绘中国北方独特风情','高辰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>哈尔滨1月6日电(杨晓蕾 记者 刘锡菊)6日，“现实主义的魅力----俄罗斯油画展”在黑龙江省开展，200余件俄罗斯艺术品为中国南北游客献上一场文化艺术盛宴。</p>\n<div style=\"text-align:center\"> \n <img alt=\"6日，“现实主义的魅力----俄罗斯油画展”在黑龙江省开展。　刘锡菊 摄\" src=\"upload/image/2badc635-73da-4612-838f-5336f6b1f6f5.JPG\" title=\"6日，“现实主义的魅力----俄罗斯油画展”在黑龙江省开展。　刘锡菊 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  6日，“现实主义的魅力----俄罗斯油画展”在黑龙江省开展。　刘锡菊 摄 \n</div>\n<p>　　此次展览由黑龙江文化厅主办、哈药美术馆承办，韩建民中俄油画收藏馆协办，主题是“现实主义”，共展出了俄罗斯人民画家捷普洛夫・瓦列里・瓦西里耶维奇等十位俄罗斯画家的心血之作。</p>\n<div style=\"text-align:center\"> \n <img alt=\"6日，“现实主义的魅力----俄罗斯油画展”在黑龙江省开展。　刘锡菊 摄\" src=\"upload/image/43f44324-2032-47ce-bff3-0a4e723df8af.JPG\" title=\"6日，“现实主义的魅力----俄罗斯油画展”在黑龙江省开展。　刘锡菊 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  6日，“现实主义的魅力----俄罗斯油画展”在黑龙江省开展。　刘锡菊 摄 \n</div>\n<p>　　据韩建民中俄油画收藏馆工作人员介绍，在世界艺术殿堂中，俄罗斯油画具有独特而崇高的历史地位。现实主义是艺术发展进程中独树一帜的艺术现象，现实主义艺术家赞美自然，歌颂劳动，深刻而全面地展现了现实生活的广阔画面，尤其描绘普通劳动者的生活和经历，大自然也作为独立的题材受到现实主义画家青睐。</p>\n<div style=\"text-align:center\"> \n <img alt=\"6日，“现实主义的魅力----俄罗斯油画展”在黑龙江省开展。　刘锡菊 摄\" src=\"upload/image/ce657877-32cd-4a44-9e91-b10775c47200.JPG\" title=\"6日，“现实主义的魅力----俄罗斯油画展”在黑龙江省开展。　刘锡菊 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  6日，“现实主义的魅力----俄罗斯油画展”在黑龙江省开展。　刘锡菊 摄 \n</div>\n<p>　　在哈药美术馆展厅内，一幅幅充满哈尔滨风情的油画挂在墙壁上，生动再现了中国北方黑土地的风土人情和自然风貌，也描述了普通民众的日常生活。其中一幅《哈尔滨的风景》更是吸引了众多游客们的目光，该画描绘了松花江上，碧水映着白云，江畔的建筑物显得格外清丽，画面中鲜花、江水、建筑等景物构图层次丰富，真实再现辽阔壮美松花江畔。</p>\n<div style=\"text-align:center\"> \n <img alt=\"6日，“现实主义的魅力----俄罗斯油画展”在黑龙江省开展。　刘锡菊 摄\" src=\"upload/image/316de3e6-9f63-4406-bcea-85a147f304ce.JPG\" title=\"6日，“现实主义的魅力----俄罗斯油画展”在黑龙江省开展。　刘锡菊 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  6日，“现实主义的魅力----俄罗斯油画展”在黑龙江省开展。　刘锡菊 摄 \n</div>\n<p>　　“这是我第五次来到中国，哈尔滨这座中西合璧的城市，有很多俄罗斯文化的印迹，让我备感亲切。今后，我要常来哈尔滨，创作出既有俄罗斯感染力，又有中国风情的作品。” 俄罗斯人民画家捷普洛夫・瓦列里・瓦西里耶维奇说。</p>\n<p>　　北京游客李青成说：“通过画展，我感受到了原汁原味的中国欧陆风情。” (完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/316de3e6-9f63-4406-bcea-85a147f304ce.JPG',NULL,490,'2018-01-06 14:25:00','2018-01-06 14:47:20','75',0),(3152,'上海首个弄堂博物馆“怀旧风”留存“老上海”记忆','高辰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>上海1月6日电 (记者 许婧 康玉湛)距离上海传统商业中心南京西路仅百余米的上海市静安区西王小区弄口，开出了上海首个弄堂博物馆――西王花园弄堂博物馆。</p>\n<p>　　开馆仅3天，就引得无数市民到此追忆自己过往的“芳华”。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为观众在观展。　康玉湛 摄\" src=\"upload/image/f7a845a2-f59a-49b8-8219-529c832d31cd.jpg\" title=\"图为观众在观展。　康玉湛 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为观众在观展。　康玉湛 摄 \n</div>\n<p>　　这座颇具特色的博物馆，摆放的是从居民家中征集来的各种不同年代的老上海物件及百姓生活用品，主要通过展板、照片、实物以及短片等形式，展示海派弄堂文化和关于“老上海”生活的集体记忆。</p>\n<p>　　蝴蝶牌缝纫机、老大房八仙盒、上海益民食品厂什锦饼干铁皮盒、卡带录音机、BP机、各式粮票和乘车月票……极具年代感的展品勾起上海市民潘先生浓浓的“怀旧感”，“看着这些既有年代感又有怀旧感的展品，很亲切，对这些老物件我印象比较深，包括这种石库门建筑”。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为展出的上世纪90年代的“大哥大”、BP机等。　康玉湛 摄\" src=\"upload/image/029f7180-6c7a-4296-a940-03f0d64462ee.jpg\" title=\"图为展出的上世纪90年代的“大哥大”、BP机等。　康玉湛 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为展出的上世纪90年代的“大哥大”、BP机等。　康玉湛 摄 \n</div>\n<p>　　始建于1911年的西王小区，是一处具有百年历史的英式安妮女王风格建筑群，被列为上海市优秀历史保护建筑，美国记者史沫特莱也曾是西王小区的一名住户。如今，这里12幢花园里弄仍保存完好，弄堂博物馆清水红砖的外墙风格维系着小区的历史风貌。</p>\n<p>　　“这几天的观众说的最多就是‘勾起了我的童年回忆’”，博物馆工作人员黄小姐介绍说，展品都是周边居民捐赠的，并且会定期更换，正在展出的是最初收集到的一些展品，每个月还会根据不同的主题更新一些不同的内容，</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为展出的老大房八仙盒。　康玉湛 摄\" src=\"upload/image/bf0bcd5d-cca0-4bb0-9d66-a0386742ccd2.jpg\" title=\"图为展出的老大房八仙盒。　康玉湛 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为展出的老大房八仙盒。　康玉湛 摄 \n</div>\n<p>　　慕名而来的上海市民王先生有浓厚的上海情节，对上海的建筑和上海风情情有独钟。他对记者说，博物馆能看到上海人以前的生活情况，更让他想起自己小时候的故事，很有意思。</p>\n<p>　　南京西路的另一端是繁华的南京东路，坐落在此的上海市百一店同为优秀历史建筑，年逾八十的老店经过半年整修“新开”。在寸土寸金的南京东路商圈，上海市百一店辟出近800平方米设立“弄堂”主题区打造“100弄”，潜心挖掘老上海元素，唤起沪人儿时记忆。</p>\n<div style=\"text-align:center\"> \n <img alt=\"上海开出首个弄堂博物馆，展示海派弄堂文化和关于“老上海”生活的集体记忆。　康玉湛 摄\" src=\"upload/image/62b44652-c6dd-41cc-92ec-87dee6b80170.jpg\" title=\"上海开出首个弄堂博物馆，展示海派弄堂文化和关于“老上海”生活的集体记忆。　康玉湛 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  上海开出首个弄堂博物馆，展示海派弄堂文化和关于“老上海”生活的集体记忆。　康玉湛 摄 \n</div>\n<p>　　文化里弄开业的第一个展名为“上海复古生活展”，展示了收藏家郭纯享提供的80多个“三五”牌台钟、120多个搪瓷杯，以及旗袍、假领头、自行车、冰箱、面包机等各式各样的老物件，诉说着上海城市发展和生活进步的故事，参观者络绎不绝。</p>\n<p>　　在新近公布的《上海市城市总体规划(2017-2035年)》中，上海明确将建设国际文化大都市、“更有魅力、更有活力、更有温度的人文之城”。学界认为，对城市历史文脉和城市文化特色的传承发扬，是弘扬海派文化、江南文化的关键着力点。</p>\n<p>　　在复旦大学中文系教授梁永安看来，社区是城市文化的基础，凝结着一个地区居民的共同记忆。“弄堂”(也称“里弄”)，是有别于街面房子的胡同的通称。上世纪中叶，上海里弄式住宅的数量曾居全国之首。但上世纪90年代后，上海进行了大规模的重建和开发，大片弄堂随着房屋的拆除消失了，取而代之的是幢幢高楼，人们的生活也随之发生变化。街道政府牵头开设的弄堂博物馆恰恰是一种从微观上留住城市记忆的好尝试。</p>\n<p>　　西王花园弄堂博物馆所在的上海市静安区，历史建筑众多，文化遗存丰厚。根据静安区的规划，西王花园弄堂博物馆只是街道建设“博物馆群”的试点之一。今后还将依托区内的阮玲玉故居、张爱玲故居等老建筑资源和文化名人效应，在条件成熟时把它们建设成文化展馆，供民众参观。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/62b44652-c6dd-41cc-92ec-87dee6b80170.jpg',NULL,1346,'2018-01-06 13:17:00','2018-01-06 14:47:20','75',0),(3153,'直播答题每日瓜分30万 李好现身花椒百万作战','罗攀','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>1月6日电 1月5日，花椒正式推出直播答题节目《百万作战》(花椒ID：888888)。据悉，比赛每天3场，每场12道题，单场奖金均为10万元。花椒还与江苏卫视《一站到底》进行合作，答题获胜者有机会参加《一站到底》，同时，其主持人李好还会担任《百万作战》的主持人，为众多网友送出第一桶金。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/61ab34d0-45fb-4f1b-9723-03e0a266ea19.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　据悉，《百万作战》从1月5日持续到2月4日，三场比赛的开始时间分别为19:00、20:00、21:00，用户只需在游戏开局前进入指定直播间即可免费答题。游戏中，在李好提出问题时，每道题的对话框会出现在屏幕中央，限定时间为10秒，全部回答正确的人平分奖金，此外，用户还有复活机会，答错问题也可以重新回到比赛中。</p>\n<p>　　作为“提问界”的大师，李好所带来的题涵盖面极广，囊括了天文地理、人文历史、体育科技、娱乐影视等。而为了让更多的人能够获得奖金，很多题目都设置的比较简单，用李好的话说就是“大多是送分题，网友只要参加就都有可能获奖”。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/2321df55-c16e-4b76-891d-0b0254043796.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　游戏宣传一经推出，马上掀起全民直播答题热。大批用户纷纷下载花椒APP坐等游戏开局，截至目前，花椒已经杀入免费排行榜前十，可见其形势之火爆。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/1ab0d55f-31bb-43e5-9830-883a3394a5b6.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　相比“吃鸡”和“农药”，《百万作战》属于益智类知识竞赛游戏，不仅参与门槛低而且还可扩展知识面。邀请亲朋好友一起参与游戏瓜分奖金也不失为一个好的娱乐项目。李好表示，非常开心成为《百万作战》的主持人，很期待与获胜者一起登台录制《一站到底》。</p>\n<p>　　每晚七点，李好在花椒《百万作战》等你来挑战。如果你够聪明，脑洞够大，快快参与到游戏中来让你的知识变现吧。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/1ab0d55f-31bb-43e5-9830-883a3394a5b6.jpg',NULL,44,'2018-01-06 11:14:00','2018-01-06 14:47:21','68',0),(3154,'这部小成本电影为何成票房黑马？ 片方否认注水','史建磊','中国新闻网','<div style=\"text-align:center\"> \n <img alt=\"《前任3：再见前任》海报\" src=\"upload/image/316fccc5-581b-41e1-86d0-a4cbba4e079c.jpg\" style=\"border:px solid #000000\" title=\"《前任3：再见前任》海报\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  《前任3：再见前任》海报 \n</div>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>客户端北京1月6日电(记者 张曦)近日，小成本电影《前任3：再见前任》(以下简称《前任3》)突出重围，成为元旦档票房冠军，遥遥领先于《妖猫传》《解忧杂货店》等热门影片。尽管有网友质疑有注水嫌疑，但出品方和发行方均予以否认，也有影评人认为，该片之所以成为黑马，很大的一个因素是戳中了观众的自身情感经验。</p>\n<p>　　<strong>――上映8天破8亿</strong></p>\n<p>　　<strong>《前任3》成元旦档票房黑马</strong></p>\n<p>　　《前任3》是“前任”系列的第三部电影作品，由田羽生执导，韩庚、郑恺、罗米、于文文等出演，延续前两部的喜剧风格。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图片来源：猫眼电影专业版截图\" src=\"upload/image/dcf71ed5-274f-4ceb-a16e-6c258edb4ce5.jpg\" style=\"border:px solid #000000\" title=\"图片来源：猫眼电影专业版截图\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  图片来源：猫眼电影专业版截图 \n</div>\n<p>　　该片自上映以来，连续三日登顶单日票房榜首位。据猫眼专业版数据，截至5日下午4:00《前任3》上映8天票房突破8亿，排片高达33%，仅次于上映首日的好莱坞影片《星球大战：最后的绝地武士》。</p>\n<p>　　相比之下，被寄予厚望的陈凯歌的《妖猫传》、王俊凯等主演的《解忧杂货店》都票房平平，尤其《妖猫传》，上映15天票房刚破5亿，排片仅为2%，明显后劲不足。</p>\n<p>　　值得一提的是，“前任”系列的前两部作品《前任攻略》和《前任攻略2》分别于2014年、2015年上映，拿下1.29亿元和2.51亿元的票房成绩，《前任3》目前的成绩已超越两部前作的票房总和。</p>\n<div style=\"text-align:center\"> \n <img alt=\"《前任3》海报\" src=\"upload/image/06e8b2e4-f8c9-4696-8517-c4d08f0ebfac.jpg\" style=\"border:px solid #000000\" title=\"《前任3》海报\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  《前任3》海报 \n</div>\n<p>　　<strong>――质疑：下大雪也有人去看？</strong></p>\n<p>　　<strong>片方反驳：预算那么低，怎么注水？</strong></p>\n<p>　　对于《前任3》的令人意外的票房，有网友在网上发出质疑，并公布截图，“下大雪也有人去看？”“半夜还有包场？”但也有网友回应，“十点场的人比七点场的人还多”“下班去买票都买不到”等。</p>\n<p>　　对此，记者联系采访了出品方之一北京无限自在文化传媒股份有限公司负责人，对方予以否认：“没什么可能性，我们预算那么低，怎么注水？”发行方负责人则表示，影片排片高、票房好是因为影片的质量好，“内容好才是第一位”。</p>\n<p>　　另外，记者从猫眼专业版查到，该片在二线城市最受欢迎，上座率为19.2%，女性观众占到了60.8%，足见打情感牌的影片还是更受女性观众喜欢。</p>\n<div style=\"text-align:center\"> \n <img alt=\"资料图：主演韩庚向现场热情粉丝“比心”。　钟欣 摄\" src=\"upload/image/fc3c68d3-43df-470b-bdbe-aa90b7560b20.jpg\" style=\"border:px solid #000000\" title=\"资料图：主演韩庚向现场热情粉丝“比心”。　钟欣 摄\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  资料图：主演韩庚向现场热情粉丝“比心”。　钟欣 摄 \n</div>\n<p>　　<strong>――运气好还是档期好？</strong></p>\n<p>　　<strong>戳中观众的自身情感经验</strong></p>\n<p>　　为何这部小成本影片能在元旦档期突出重围？</p>\n<p>　　中国电影家协会秘书长饶曙光在接受记者采访时直言，一方面节日档期观众都愿意看相对轻松的电影，“现在电影的社交属性越来越强烈，因此节日档期喜剧片胜出的概率比较大”；另一方面，他认为《前任3》非常接地气，“本来分手是很沉重的话题，但通过喜剧的方式缓解了大家的焦虑，这也是票房成功的元素之一”。</p>\n<p>　　也有媒体评论称，当看电影成为大众的日常娱乐之后，普通观众不仅需要刀光剑影的大场面大制作，还需要共情的体验。“给好评的观众普遍认为这部电影虽然平庸，可是戳中了自己的泪点，让人感慨。他们并非为剧情感动，而是这部电影激发了他们自身的情感经验。”</p>\n<p>　　从豆瓣上的影评来看，虽然不少影迷给的评分不高，但“前任”这个话题还是引发了讨论，饶曙光表示这就是《前任3》的最大优势，“让观众有带入感，产生情感认同”。</p>\n<div style=\"text-align:center\"> \n <img alt=\"《前任3》海报\" src=\"upload/image/f01302df-897c-41a9-97e2-520ede8e8617.jpg\" style=\"border:px solid #000000\" title=\"《前任3》海报\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  《前任3》海报 \n</div>\n<p>　　<strong>――淡化导演个人色彩</strong></p>\n<p>　　<strong>分工写剧本 寻找观众的共鸣点</strong></p>\n<p>　　和一些电影不同，《前任3》有一个编剧团队，每个人分工不同：有人负责段子，有人负责影片的结构，有人负责台词，最终再交给导演田羽生，由他汇总执笔。</p>\n<p>　　“劣势是统一起来很难，速度很慢，优势是有很多不同色彩在里面。我挺喜欢这种集体式的创作，因为一个人创作的话也许很快，也许很容易，但会太有个人色彩。” 田羽生告诉记者，自己不仅淡化导演的个人色彩，还加强了真实性，不少情节都来自于身边好友的真实经历，“对于都市爱情喜剧这个题材，最重要的就是寻找到观众的共鸣，太童话或者太仙儿的东西大家不会接受”。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/f01302df-897c-41a9-97e2-520ede8e8617.jpg',NULL,671,'2018-01-06 00:09:00','2018-01-06 14:47:21','68',0),(3155,'十九大后，习近平视察部队时有哪些殷殷嘱托？','高辰','央视新闻','<p><strong>　　十九大后，习近平视察部队时有哪些殷殷嘱托？ </strong></p>\n<p>　　1月3日，习近平以最高统帅身份在开训动员大会上向全军发布训令，号召全军各级要强化练兵备战鲜明导向。</p>\n<p>　　当日，习近平又视察中部战区陆军某师，到基层部队训练场检查训练工作。</p>\n<p>　　一系列举动的背后，都在强化一个信号：军事训练的战略位置、中心地位始终不能动摇，能打仗、打胜仗的核心职能丝毫不能偏移，练兵备战的主业主责一刻不能松懈。</p>\n<div style=\"text-align:center\"> \n <img alt=\"1月3日，动员大会主会场部队受领训令后展开训练。\" src=\"upload/image/4b894ccd-4ac5-4a64-8f3e-961316a03433.jpg\" style=\"border:px solid #000000\" title=\"1月3日，动员大会主会场部队受领训令后展开训练。\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  1月3日，动员大会主会场部队受领训令后展开训练。 \n</div>\n<p>　　十九大以来，习主席对军事训练一直高度重视，念兹在兹的就是练兵备战，对实战化军事训练作出一系列重要指示，要求一切工作都必须向能打仗、打胜仗聚焦。</p>\n<p>　<strong>　一切工作都必须坚持战斗力标准</strong></p>\n<p>　　2017年10月18日，习近平在中国共产党第十九次全国代表大会上作报告时强调：</p>\n<p>　　军队是要准备打仗的，一切工作都必须坚持战斗力标准，向能打仗、打胜仗聚焦。</p>\n<div style=\"text-align:center\"> \n <img alt=\"习近平在中国共产党第十九次全国代表大会上作报告\" src=\"upload/image/299ceb29-4fa0-4fb6-9692-9b7ee16bc1dc.jpg\" style=\"border:px solid #000000\" title=\"习近平在中国共产党第十九次全国代表大会上作报告\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  习近平在中国共产党第十九次全国代表大会上作报告 \n</div>\n<p>　<strong>　强化备战打仗导向，提高打赢本领</strong></p>\n<p>　　2017年10月25日，习近平主持第一次军委常务会议时强调：</p>\n<p>　　对于军队高级干部来说，就是要不断提高谋划打仗、指挥打仗、带兵打仗能力。从我做起，从军委做起，强化备战打仗导向，提高打赢本领，抓实备战工作，带领我军真正做到能打仗、打胜仗，担当起党和人民赋予的新时代使命任务。</p>\n<div style=\"text-align:center\"> \n <img alt=\"2017年11月3日，习近平在军委联合作战指挥中心\" src=\"upload/image/4f67b39d-00bc-41c1-95b4-25a6ce88e1d8.jpg\" style=\"border:px solid #000000\" title=\"2017年11月3日，习近平在军委联合作战指挥中心\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  2017年11月3日，习近平在军委联合作战指挥中心 \n</div>\n<p>　<strong>　军事斗争是进行伟大斗争的重要方面，</strong></p>\n<p><strong> </strong></p>\n<p><strong>　　打赢能力是维护国家安全的战略能力。</strong></p>\n<p>　　2017年11月3日，习近平视察军委联合作战指挥中心时强调：</p>\n<p>　　我国正处在由大向强发展的关键阶段，前景十分光明，挑战也十分严峻，中华民族伟大复兴绝不是轻轻松松、敲锣打鼓就能实现的。军事斗争是进行伟大斗争的重要方面，打赢能力是维护国家安全的战略能力。全军要强化忧患意识、危机意识、打仗意识，全部心思向打仗聚焦，各项工作向打仗用劲，尽快把备战打仗能力搞上去。</p>\n<div style=\"text-align:center\"> \n <img alt=\"2017年12月13日，习近平视察71集团军\" src=\"upload/image/2ae098a7-bd7e-405e-8fb5-dba8effb9013.jpg\" style=\"border:px solid #000000\" title=\"2017年12月13日，习近平视察71集团军\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  2017年12月13日，习近平视察71集团军 \n</div>\n<p><strong>　　大抓实战化军事训练</strong></p>\n<p><strong> </strong></p>\n<p><strong>　　使部队成为威武之师</strong></p>\n<p>　　2017年12月13日，习近平视察71集团军时强调：</p>\n<p>　　部队各级要聚精会神抓练兵备战，把战斗力标准立起来，努力提高打赢能力。要适应改革后新体制新编成，贯彻陆军转型建设要求，创新集团军建设管理和作战运用方式，提高机动作战、立体攻防能力。要坚持实战实训、联战联训，创新训练理念和方法手段，大抓实战化军事训练。要加强战斗精神培育和战斗作风训练，使部队成为威武之师。各级党委和领导干部要强化责任担当，带领部队把练兵备战各项任务完成好。</p>\n<div style=\"text-align:center\"> \n <img alt=\"1月3日上午，2018年开训动员大会，习近平向全军发布训令。\" src=\"upload/image/622cee80-bb60-4355-8e2c-f15ab47fe9b4.jpg\" style=\"border:px solid #000000\" title=\"1月3日上午，2018年开训动员大会，习近平向全军发布训令。\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  1月3日上午，2018年开训动员大会，习近平向全军发布训令。 \n</div>\n<p><strong>　　全面加强实战化军事训练，全面提高打赢能力。</strong></p>\n<p>　　2018年1月3日，习近平向全军发布训令：</p>\n<p>　　坚定不移把军事训练摆在战略位置、作为中心工作，抓住不放，抓出成效。</p>\n<p>　　要坚持领导带头、以上率下，坚持实战实训、联战联训，坚持按纲施训、从严治训。</p>\n<p>　　提高军事训练实战化水平，牢牢掌握能打仗、打胜仗的过硬本领。</p>\n<p>　　要锻造召之即来、来之能战、战之必胜的精兵劲旅，坚决完成党和人民赋予的新时代使命任务。</p>\n<div style=\"text-align:center\"> \n <img alt=\"1月3日，习近平视察中部战区陆军某师。图为习主席同该师侦察英雄杨子荣生前所在连官兵亲切交谈。\" src=\"upload/image/782f0f26-ee0e-4412-9f8a-76e71503aecd.jpg\" style=\"border:px solid #000000\" title=\"1月3日，习近平视察中部战区陆军某师。图为习主席同该师侦察英雄杨子荣生前所在连官兵亲切交谈。\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  1月3日，习近平视察中部战区陆军某师。图为习主席同该师侦察英雄杨子荣生前所在连官兵亲切交谈。 \n</div>\n<p><strong>　　我军历来是打精气神的，过去钢少气多，现在钢多了，气要更多，骨头要更硬。</strong></p>\n<p>　　2018年1月3日，习近平视察中部战区陆军某师时讲话：</p>\n<p>　　要强化练兵备战，全部心思向打仗聚焦，各项工作向打仗用劲，加强数字化部队作战研究，创新作战概念和战法，大抓实战化军事训练，不断提高训练水平和打赢能力。</p>\n<p>　　要强化体系建设，加强信息系统和作战数据建设，确保成体系形成作战能力，有机融入全军联合作战体系。</p>\n<p>　　要强化改革创新，积极推进创新型人民军队实践探索。</p>\n<p>　　要强化政治保证，开展“传承红色基因、担当强军重任”主题教育，确保部队坚决听从党中央和中央军委指挥。</p>\n<p>　　各级要高度重视数字化部队人才队伍建设，培养一批高素质新型军事人才。坚决纠治基层官兵身边的“微腐败”和不正之风，把广大官兵积极性、主动性、创造性充分调动起来，共同把部队建设推向前进。</p>\n<div style=\"text-align:center\"> \n <img alt=\"1月3日上午，2018年开训动员大会，主会场部队官兵宣誓。\" src=\"upload/image/3eb5957d-3fa1-4d43-9dba-96a5febbd21d.jpg\" style=\"border:px solid #000000\" title=\"1月3日上午，2018年开训动员大会，主会场部队官兵宣誓。\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  1月3日上午，2018年开训动员大会，主会场部队官兵宣誓。 \n</div>\n<p>　　“安不可以忘危，治不可以忘乱。”</p>\n<p>　　当前，中国正在前所未有地靠近世界舞台的中心，也前所未有地面临严峻的安全压力和风险挑战。“提高军队打胜仗能力”，这是习近平主席从实现中国梦强军梦战略高度深深思考的课题，也是全军将士履行使命任务必须答好的考题。</p>\n<div id=\"function_code_page\"></div>','upload/image/3eb5957d-3fa1-4d43-9dba-96a5febbd21d.jpg',NULL,990,'2018-01-06 11:01:00','2018-01-06 14:47:22','64',0),(3156,'挚爱生活 沃尔沃V40元旦假期内蒙游记','姜贞宇','中国新闻网','<div style=\"text-align:center\"> \n <img src=\"upload/image/a82b531d-c67e-423a-ba6b-e8b51aae7655.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>1月5日电 今年入冬以来，虽然朋友圈弥漫着各种雪景，但是北京多数地区还未见一片雪花。趁着晴朗的天气，适逢元旦假期，笔者特穆自驾回了一趟内蒙古。这趟行程，沃尔沃V40将会担当起自驾车的角色，在这往返近800公里的行程中，让特穆更深一步体验了新款沃尔沃V40。</p>\n<div style=\"text-align:center\"> \n <img alt=\"出发前，对车辆进行检查，以保证旅途安全\" src=\"upload/image/2121e316-93bd-46cc-ae88-7bc2fca189f4.jpg\" style=\"border:px solid #000000\" title=\"出发前，对车辆进行检查，以保证旅途安全\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  出发前，对车辆进行检查，以保证旅途安全 \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\"> \n <div style=\"text-align:center\"> \n  <img alt=\"简单的旅行，一个箱子足以\" src=\"upload/image/bfbd7455-fc95-4296-8953-453a17bd9c3f.jpg\" style=\"border:px solid #000000\" title=\"简单的旅行，一个箱子足以\"> \n </div> \n <div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n   简单的旅行，一个箱子足以 \n </div> \n <br> \n</div>\n<p>　　作为沃尔沃品牌系列的入门车型，沃尔沃V40一直被赋予“高性能”的印象，成为高档汽车品牌中的“小钢炮”。全新的沃尔沃V40有了一个明显的家族特征――“雷神之锤”大灯，整体的外观和内饰较上一代产品变化不大。</p>\n<div style=\"text-align:center\"> \n <img alt=\"元旦假期第一天，路况还以和以往一样，出城方向车流量比较大\" src=\"upload/image/8dfecb20-c5f4-4058-921e-da86ae794b1b.jpg\" style=\"border:px solid #000000\" title=\"元旦假期第一天，路况还以和以往一样，出城方向车流量比较大\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  元旦假期第一天，路况还以和以往一样，出城方向车流量比较大 \n</div>\n<div style=\"text-align:center\"> \n <img alt=\"不过出了城，往内蒙古方向的高速公路上路况就好了起来。画面为河北境内鸡鸣驿。\" src=\"upload/image/b1ccbe0e-2130-46d6-81b8-531edb06dcee.jpg\" style=\"border:px solid #000000\" title=\"不过出了城，往内蒙古方向的高速公路上路况就好了起来。画面为河北境内鸡鸣驿。\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  不过出了城，往内蒙古方向的高速公路上路况就好了起来。画面为河北境内鸡鸣驿。 \n</div>\n<p>　　收拾行囊，踏上这为期3天的短假旅行，出发前经过检查，车辆各部分都正常，这就踏上旅途。作为一款小型车，内部空间不那么紧凑，后排倒置一个儿童安全座椅基本没啥问题(为了确保颈部安全，婴幼儿的安全座椅一定倒置)，而且驾乘空间也足够，这非常可能是基于北欧人高大身材而设计。对此，相对日系品牌的小型车而言，沃尔沃V40的空间更胜一筹。</p>\n<div style=\"text-align:center\"> \n <img alt=\"带着宝宝出门，儿童安全座椅是非常非常非常必要的\" src=\"upload/image/342f7695-b2cf-4398-8641-891e14deac97.jpg\" style=\"border:px solid #000000\" title=\"带着宝宝出门，儿童安全座椅是非常非常非常必要的\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  带着宝宝出门，儿童安全座椅是非常非常非常必要的 \n</div>\n<p>　　驾驶的沃尔沃V40为T4智雅版，作为一款小尺寸车型，搭载了2.0升 /140kW涡轮增压发动机，就账面参数而言，动力性不言而喻。而在实际的驾驶过程中，无论高速公路上提速还是市区行驶，动力是没得说。</p>\n<div style=\"text-align:center\"> \n <img alt=\"服务区小歇一下，杜绝疲劳驾驶\" src=\"upload/image/a9dc2378-173b-42a5-92c7-f6df38f10274.jpg\" style=\"border:px solid #000000\" title=\"服务区小歇一下，杜绝疲劳驾驶\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  服务区小歇一下，杜绝疲劳驾驶 \n</div>\n<div style=\"text-align:center\"> \n <img alt=\"蓝天白云、一望无际等词语基本常被用于描写内蒙古的风景，看看图就知道了。\" src=\"upload/image/a6e6bd8a-63a5-4c4f-8010-bc36ab7dda99.jpg\" style=\"border:px solid #000000\" title=\"蓝天白云、一望无际等词语基本常被用于描写内蒙古的风景，看看图就知道了。\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  蓝天白云、一望无际等词语基本常被用于描写内蒙古的风景，看看图就知道了。 \n</div>\n<div style=\"text-align:center\"> \n <img alt=\"无论春夏秋冬，沿途的风景只是美的不同而已\" src=\"upload/image/6a3fd79c-364b-4fa6-9f51-1686cb953e7b.jpg\" style=\"border:px solid #000000\" title=\"无论春夏秋冬，沿途的风景只是美的不同而已\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  无论春夏秋冬，沿途的风景只是美的不同而已 \n</div>\n<p>　　在“一望无际”的高速公路上，没有那么多的车，完全可以让沃尔沃V40进入定速巡航状态，这样一方面解放双脚，另一方面可以节省燃油。</p>\n<div style=\"text-align:center\"> \n <img alt=\"冬季的内蒙古，今年这里也没又下雪，下雪将又是一番景象。\" src=\"upload/image/9f837d30-306c-4502-a29e-121324f2aab1.jpg\" style=\"border:px solid #000000\" title=\"冬季的内蒙古，今年这里也没又下雪，下雪将又是一番景象。\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  冬季的内蒙古，今年这里也没又下雪，下雪将又是一番景象。 \n</div>\n<p>　　不过，进入国道后，不仅要车速降下来，定速巡航尽量也不要再使用。因为会有穿过公路的牛羊等牲畜，对于它们而言不能提及什么“交规”，只能是我们尽最大努力保证行车安全。</p>\n<div style=\"text-align:center\"> \n <img alt=\"牛羊便是这幅风景画中不可缺少的元素\" src=\"upload/image/bc8b9d9f-5e6a-4216-86ed-74ebe190966c.jpg\" style=\"border:px solid #000000\" title=\"牛羊便是这幅风景画中不可缺少的元素\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  牛羊便是这幅风景画中不可缺少的元素 \n</div>\n<p>　　在大草原上行驶，心旷神怡，清晰的天际线，通透而又清新的空气，与城市中的繁荣嘈杂行成明显的对比。天空的云朵犹如会变的乐高积木，不时地切换图案。笔直的公路一望无际，这时特穆该提醒，一定要谨慎驾驶，数据表明，越是这样的路况反而越容易出大事故。</p>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/34dc66fa-229a-45bf-97f8-7d9f5b966d86.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　近400公里的行驶抵达目的地。出发时满箱油，此时油位显示还有半箱，由于一路高速行驶，对此油耗自然不会高。如此看来，虽然是2.0T的排量，但是燃油经济性表现非常优秀。想必配置1.5T动力的T3版本沃尔沃V40油耗会更低。</p>\n<div style=\"text-align:center\"> \n <img alt=\"手扒羊肉，最具特色的内蒙美食\" src=\"upload/image/8d5cc958-fbba-4784-9af0-c7234920359b.jpg\" style=\"border:px solid #000000\" title=\"手扒羊肉，最具特色的内蒙美食\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  手扒羊肉，最具特色的内蒙美食 \n</div>\n<div style=\"text-align:center\"> \n <img alt=\"当然，烤羊排也别一番风味\" src=\"upload/image/6ddde96e-ddb9-4177-9cc8-013d790e2168.jpg\" style=\"border:px solid #000000\" title=\"当然，烤羊排也别一番风味\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  当然，烤羊排也别一番风味 \n</div>\n<p>　　回到内蒙古，怎能不吃羊肉？手扒羊肉、烤羊排这类家常便饭必须要有的，作为出生在内蒙古的特穆，可以自信告诉大家，内蒙古的羊肉只有鲜香，没有什么膻味，白水煮的羊肉就鲜香四溢。一顿美美的酒肉，抵挡了寒意，更是一份乡情。</p>\n<p>　　一个段时间不长的旅程，换一种心情，沃尔沃V40不仅仅是旅途担当，更多是将生活格调进行转换。（完）</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/6ddde96e-ddb9-4177-9cc8-013d790e2168.jpg',NULL,358,'2018-01-05 15:56:00','2018-01-06 14:47:23','72',0),(3157,'2017华侨华人十大新闻、新闻人物将揭晓 谁会上榜？','叶攀','中国侨网','<p>　　中国侨网1月5日电 <strong>题：全球侨胞翘首企盼，16年铸就金牌口碑，这份即将揭晓的非凡荣誉，最终究竟花落谁家？</strong></p>\n<p>　　2017年，在6000万华侨华人身边，发生了数不清的大事小情，其中不少可谓影响深远，意义重大；</p>\n<p>　　2017年，为了海外华社发展壮大，为了中华民族伟大复兴之路，很多人用行动做出巨大努力，成就卓著；</p>\n<p>　　究竟有哪些事件，吸引了侨界最多的关注？</p>\n<p>　　又有哪些人，称得上公众心中的侨界榜样？</p>\n<p>　　1月10日，由中国新闻社主办、中国侨网承办的“2017全球华侨华人年度评选颁奖典礼”，将在钓鱼台国宾馆芳菲苑盛大举行。</p>\n<p>　　届时，备受侨界关注的，已连续举办16载的全球华侨华人十大新闻评选，将现场揭晓结果。</p>\n<p>　　中共十九大召开，对华侨华人提出新期待</p>\n<p>　　中国全运会首次邀请华侨华人参赛</p>\n<p>　　法国华侨遭警察射杀震惊华人社会</p>\n<p>　　章莹颖事件引发海外留学安全话题</p>\n<p>　　……</p>\n<p>　　你最关注的侨界大事，最终是否榜上有名？</p>\n<p>　　承载无数期待的另一重头戏，“年度新闻人物”评选，也将在国侨办、中国侨联、中国华侨华人研究所、侨鑫集团及<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>有关领导及百余位侨界嘉宾的共同见证下，最终花落十家。</p>\n<p>　　2017年，华侨华人及归侨们交出一份亮眼的“成绩单”：</p>\n<p>　　科研喜报频传，国际大奖屡现华人身影；</p>\n<p>　　参政全球开花，多人当选国家、地区等各级议员；</p>\n<p>　　文体表现亮眼，重大赛事、顶级舞台华人担当主角；</p>\n<p>　　还有越来越多海外英才载誉归来，为祖国发展添砖加瓦。</p>\n<p>　　侨界发展壮大，榜样力量至关重要。</p>\n<p>　　究竟是谁，能从无数精英中脱颖而出，揽得“年度新闻人物”殊荣？</p>\n<div class=\"pictext\" style=\"margin:5px auto; text-align:left;text-indent:2em; \"></div>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/a7fd5fae-d0e3-4718-9421-ec23898d13d4.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　<strong>黄大年</strong></p>\n<p>　　黄大年，世界著名地球物理学家，在祖国最需要的时候，他秉承科技报国的理想，把国家需要视为毕生追求，直到生命的最后一刻。</p>\n<p>　　黄大年曾经在英国奋斗了十八年，2009年，黄大年放弃英国优厚待遇回国，成为东北首位“千人计划”专家。</p>\n<p>　　他用卓著的成就，践行着科学家的本心。7年间，黄大年带领400多名科学家，克服国外严格禁运和技术封锁，创造了多项“中国第一”，为中国“巡天探地潜海”填补多项技术空白。</p>\n<p>　　他用无私的大爱，肩负起人民教师的使命。让学生成才是他的理想，倾尽心血只作等闲。他与学生共度的时间远超亲友，不是家人，胜似家人。</p>\n<p>　　他更用宝贵的生命，诠释了知识份子的担当。吉林大学地质宫“不灭的灯火”，往返于长春与各地的最后一架航班，午夜出租车上争分夺秒的乘客……回国7年，他像一枚高速运动的转子，将一个个高精尖项目推向世界前沿，直到58载年华的最后一息。</p>\n<p>　　“心有大我、至诚报国，教书育人、敢为人先，淡泊名利、甘于奉献”，2017年5月，中国国家主席习近平对黄大年先进事迹作出高度褒奖，中国国务院侨务办公室也追授黄大年“至诚报国归侨楷模”荣誉称号。</p>\n<div class=\"pictext\" style=\"margin:5px auto; text-align:left;text-indent:2em; \"></div>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/a7fc08b7-1660-472e-98a0-28f3f0928bc0.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　<strong>赵小兰</strong></p>\n<p>　　赵小兰，美籍华人。2017年1月31日，美国国会参议院以93票赞成、6票反对通过了赵小兰担任美国运输部长的提名，这意味着她成为第一位两次进入美国政府内阁的华裔女性。</p>\n<p>　　赵小兰1953年3月26日出生于中国台北。2001年1月11日，时任美国总统乔治•布什提名赵小兰出任劳工部长，成为美国史上首位进入内阁的华裔。赵小兰从2001年起担任劳工部长直至2009年，是美国自二战以来担任这个职位最长的部长，也是小布什政府中唯一任满8年的内阁成员。2016年11月，新任美国总统特朗普又提名赵小兰出任运输部长。</p>\n<p>　　接受媒体采访时，赵小兰曾说：“我知道我身上代表了许多华人参政的希望，我觉得这是一份责任，从来没觉得是一种负担。我知道我必须要做好，只有我做好了，外界才会对亚裔有信心”。</p>\n<div class=\"pictext\" style=\"margin:5px auto; text-align:left;text-indent:2em; \"></div>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/3ba32bca-7f4e-4d7a-b660-1f2e32741dd8.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　<strong>陈文雄</strong></p>\n<p>　　陈文雄，法国华人。2017年6月18日，陈文雄在法国立法选举中战胜对手，成为法国本土首位华人国会议员。</p>\n<p>　　陈文雄于2008年成为巴黎十三区政府议员，2014年，成为巴黎首位华裔市议员，2017年当选国会议员。这条看似一帆风顺的从政之路背后，是陈文雄不断奔走的汗水。</p>\n<p>　　2017年初，法国总统大选前，政坛局势不甚明朗。从政以来一直未加入任何党派的陈文雄，明确表示支持马克龙。</p>\n<p>　　当选华人议员后，陈文雄不仅勤于政务，还不遗余力地在各种场合推广中华文化，包括设立中医研究中心、争取中医在法国合法化、开设中文班乃至建立中文学校、利用中国节日组织活动……他说，“我的父母就是华人，为我们(华侨华人)的‘大家庭’做些事，是应该的”。</p>\n<p>　　陈文雄祖籍广东普宁，1975年随父母从柬埔寨移民法国。作为海外华人参政楷模，陈文雄以自身经历告诉大家，华人同样可以赢得主流社会的认可。</p>\n<div class=\"pictext\" style=\"margin:5px auto; text-align:left;text-indent:2em; \"></div>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/c9d5928e-0592-4397-a210-aee37285059a.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　<strong>南仁东</strong></p>\n<p>　　2017年9月15日夜，“中国天眼”首席科学家兼总工程师、中科院国家天文台研究员南仁东因罹患肺癌、病情恶化抢救无效逝世，享年72岁。</p>\n<p>　　作为“中国天眼”的500米口径球面射电望远镜工程的发起者及奠基人，南仁东自1994年起，便一直负责工程的选址、预研究、立项、可行性研究及初步设计，编订了科学目标，指导了各项关键技术的研究及试验。</p>\n<p>　　南仁东1945年出生，一生极富传奇色彩。他从清华大学毕业后，曾在东北一无线电厂工作十年。改革开放后，他曾在国外著名大学任客座教授、访问学者，他还参加过十国大射电望远镜计划。</p>\n<p>　　这位驰骋于国际天文界的归侨科学家，在上世纪90年代中期毅然舍弃高薪，回国就任中科院北京天文台副台长。当时他一年的工资，只等于国外一天的工资。</p>\n<p>　　南仁东逝世是“中国天眼”的一大损失，也是中国天文界的一大损失，而他对工作的热情与执着，也为后人留下了宝贵的精神财富。</p>\n<div class=\"pictext\" style=\"margin:5px auto; text-align:left;text-indent:2em; \"></div>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/93b0ab6d-0a05-49fc-970d-5d9b36d7fcc5.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　<strong>陈国基</strong></p>\n<p>　　陈国基，巴拿马总统顾问。在当地侨界乃至主流社会，他是一位家喻户晓的人物。</p>\n<p>　　2017年6月13日，中国与巴拿马正式建交。作为巴拿马总统顾问，陈国基在巴中建交过程中担任穿梭使者，亲身经历了两国建交全过程，并全程陪同巴拿马副总统兼外长德圣马洛在北京与中国外长王毅签署备忘录，两国正式建立外交关系。</p>\n<p>　　中巴两国建交后，陈国基的工作更加忙碌。他表示，今后将重点关注如何帮助巴拿马华侨华人从两国交往中受益，从而实现自身更大发展、进一步提升华侨华人的社会地位。</p>\n<p>　　陈国基籍贯江门台山，16岁移民巴拿马。因为深信“华人要在主流社会取得认同，必须参与政治”，他开始了政治生涯。2014年，巴雷拉赢得巴拿马总统选举，陈国基成为总统顾问团的一员；2015年，他又成为巴拿马“国家中华民族委员会”首届委员。</p>\n<div class=\"pictext\" style=\"margin:5px auto; text-align:left;text-indent:2em; \"></div>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/07d2b4cc-8fc2-47bf-882d-8f59a1a0488a.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　<strong>黄素梅</strong></p>\n<p>　　黄素梅，加拿大安大略省议会议员，在她的推动下，加拿大安大略省成为西方第一个通过设立“南京大屠杀遇难者纪念日”动议的地方行政区。</p>\n<p>　　从2014年开始，黄素梅就开始收集整理历史资料，她访问了多伦多二战史实维护会、省议会图书馆相关研究团队，此外，她还征集到十万人签名支持动议。2016年12月，黄素梅提议将每年的12月13日定为安大略省“南京大屠杀遇难者纪念日”，2017年10月26日，安大略省议会通过了黄素梅提出的设立“南京大屠杀遇难者纪念日”的动议，议会辩论的当天，她泪洒现场。</p>\n<p>　　黄素梅1962年出生于香港，8岁时随家人移民加拿大，2011年，当选安大略省自由党议员，2015年连任议员职务。黄素梅表示，2017年是南京大屠杀发生80周年，正式设立‘南京大屠杀遇难者纪念日’，将为安大略省人民提供了解这一事件的机会。</p>\n<div class=\"pictext\" style=\"margin:5px auto; text-align:left;text-indent:2em; \"></div>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/47e0a28f-b3f0-46f5-a27c-f5ccf83eff68.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　<strong>杨振宁、姚期智</strong></p>\n<p>　　2017年2月，此前已放弃外国国籍的科学家杨振宁和姚期智，正式由中科院外籍院士转为中科院院士。这在中科院学部历史上尚属首次。</p>\n<p>　　今年95岁的杨振宁，在上世纪五六十年代先后创立“杨-米尔斯规范场”论和提出“杨-巴克斯特方程”。1957年，因与李政道共同提出弱相互作用中宇称不守恒原理，获得1957年诺贝尔物理学奖。</p>\n<p>　　谈及恢复中国国籍的原因，杨振宁说：“我的身体里循环着的是父亲的血液，是中华文化的血液。”</p>\n<p>　　姚期智长期从事计算机科学和量子信息科学研究，2000年获得世界计算机科学领域的最高奖项“图灵奖”，是迄今为止，唯一获此殊荣的亚裔计算机科学家。</p>\n<p>　　2004年，姚期智辞去美国普林斯顿教职来到中国。2017年2月，在回答为何要放弃美国国籍时，姚期智说：能做回百分之百的中国人，我觉得万分的欣慰与骄傲！</p>\n<p>　　巢暖自有凤来栖，两位科学巨匠的归来，是中国“人才磁场”效应不断增强的最好彰显。</p>\n<div class=\"pictext\" style=\"margin:5px auto; text-align:left;text-indent:2em; \"></div>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/0d31c4ba-068c-4c37-839e-b703139d2054.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　<strong>集体归国的哈佛博士后</strong></p>\n<p>　　“八剑下哈佛，共聚科学岛。”2017年8月，这一颇具武侠色彩的说法，在网上不胫而走。故事的主人公便是集体回国的八位哈佛博士后。</p>\n<p>　　王俊峰、刘青松、刘静、王文超、张欣、张钠、林文楚、任涛，这八位原在哈佛大学从事研究工作的科学家，相继来到中科院合肥物质科学研究院所在地、合肥蜀山湖畔的“科学岛”。</p>\n<p>　　王俊峰8年前偶然来到“科学岛”，中科院合肥物质科学研究院匡光力院士发出邀请。在王俊峰带动下，其余七位哈佛大学博士后也先后回归。</p>\n<p>　　王俊峰、张钠研究结构生物学；张欣研究磁生物效应；林文楚研究动物模型；刘青松、刘静、王文超、任涛研究肿瘤药物。八人回归意味着一条依托强磁场大科学装置与技术，开展以重大疾病为导向的多学科交叉研究网络的“学术链”完成组合。</p>\n<p>　　他们用三个字解释回归原因：“强磁场”。这不仅指“科学岛”上的强磁场实验装置，还包括中国发展给他们的信心。</p>\n<p>　　王俊峰说：“祖国就是一个强磁场，对这个国家没信心的人，是不会选择主动回国的”。</p>\n<div class=\"pictext\" style=\"margin:5px auto; text-align:left;text-indent:2em; \"></div>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/05ee7944-0ed8-4aa8-8f53-f696236a27d2.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　<strong>董荷斌、程飞</strong></p>\n<p>　　董荷斌、程飞，华裔赛车手，他们挥舞五星红旗，代表中国车队多次站上了世界赛车最高领奖台。</p>\n<p>　　2017年6月17、18日，华裔车手程飞、董荷斌率耀莱成龙DC车队在勒芒24小时比赛中包揽LMP2组别世界冠、亚军，并获得全场比赛第2、3名，两次站上领奖台。赛场上两次分别升起两面五星红旗，创造了中国车队在世界最高级别比赛的最好成绩。</p>\n<p>　　董荷斌是荷兰籍华裔车手，也是F1历史上首位华裔车手。2008年董荷斌加盟三叉戟车队 ，2010年加入雷诺F1车队，并分别于2003年、2006年获得亚洲宝马方程式冠军、德国三级方程式冠军。</p>\n<p>　　程飞6岁时随父母移居美国西雅图，自高中起多次参加全美各级卡丁车及多项初级方程式汽车大赛，多次获得冠军。曾在2013年美国赛百灵12小时耐力赛获冠军。</p>\n<p>　　2013年，董荷斌与程飞在国务院侨办举行的“海外杰出华裔青年华夏行”活动中相遇。这一年起，两人开始合作参加亚洲勒芒系列赛并连续获得三年年度总冠军，随后多次参加世界性比赛并终获成功。</p>\n<p>　　对于评选结果，想必大家早已按捺不住好奇，想要先睹为快。</p>\n<p>　　那还不赶快关注侨网微信及侨宝APP，第一时间获取更多活动信息：</p>\n<div class=\"pictext\" style=\"margin:5px auto; text-align:left;text-indent:2em; \"></div>\n<div style=\"text-align:center\"> \n <img src=\"upload/image/4dd3c7dd-cd72-4932-b64e-a17b60f38abf.jpg\" style=\"border:px solid #000000\"> \n</div>\n<p>　　1月10日，让我们共同期待这一场侨界盛事的到来！</p>\n<p>　　<strong>关于“全球华侨华人年度评选”：</strong></p>\n<p>　　一年一度的全球华侨华人十大新闻评选，已连续举办了16年，广受国内外侨界关注，成为重要的涉侨品牌活动。</p>\n<p>　　为了进一步拓展这项工作，<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>在此基础上，从2017年开始增加年度新闻人物评选。通过对年度侨界大事和人物的回顾，展示一年来的侨务工作成就，对侨界先进或知名人物进行集中宣传和展示。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/4dd3c7dd-cd72-4932-b64e-a17b60f38abf.jpg',NULL,263,'2018-01-05 19:25:00','2018-01-06 14:47:23','73',0),(3158,'保监会发文规范保险资金设立股权投资计划业务','唐云云','中国新闻网','<div style=\"text-align:center\"> \n <img alt=\"中国保险监督管理委员会。(资料图)<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 王子瑞 摄 图片来源：CNSPHOTO\" src=\"upload/image/a4459f49-cc6d-4cdf-83fb-07d880fb447c.jpg\" title=\"中国保险监督管理委员会。(资料图)<a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>发 王子瑞 摄 图片来源：CNSPHOTO\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  中国保险监督管理委员会。(资料图) \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>发 王子瑞 摄 图片来源：CNSPHOTO \n</div>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>1月5日电 据保监会网站消息，为贯彻落实全国金融工作会议精神，更好发挥保险资金服务实体经济的作用，切实防范保险资金以通道、嵌套、名股实债等方式开展股权投资计划业务，遏制地方政府隐性债务增量，中国保监会近日印发《关于保险资金设立股权投资计划有关事项的通知》(以下简称《通知》)。</p>\n<p>　　《通知》重在对保险资产管理机构设立股权投资计划业务进行规范。一是要求股权投资计划的投资收益应当与投资标的经营业绩或收益挂钩，严防以“名股实债”方式，变相抬高实体企业融资成本。根据“定期付息、到期还本”的债性特征，《通知》规定，保险资产管理机构发起设立股权投资计划时，禁止设置明确的回报预期且定期向投资人支付固定投资回报，禁止约定股权计划到期强制性由被投资企业或关联第三方赎回投资本金，目的是切实体现股权投资计划业务的资本属性和权益投资特点，避免变相违规增加地方政府债务规模，避免增加实体企业融资成本。二是要求保险资产管理机构设立投资计划应当承担主动管理职责，不得直接或变相开展通道业务，不得开展嵌套投资。三是建立优先注册机制，切实引导保险资金发挥自身特色，更好服务实体经济发展。《通知》规定，对切实服务国家重大发展战略、重大改革举措、重点领域、重大建设工程，以及投资于实体经济项目的股权投资计划，中国保监会指定注册机构予以优先注册。</p>\n<p>　　股权投资计划是保险资金参与实体经济建设的重要途径和方式。下一步，中国保监会将根据全国金融工作会议精神和统一资产管理产品标准要求，进一步完善保险资产管理机构设立股权投资计划相关业务规则。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/a4459f49-cc6d-4cdf-83fb-07d880fb447c.jpg',NULL,1801,'2018-01-05 11:56:00','2018-01-06 14:47:26','67',0),(3159,'《愤怒的小鸟》动画短片强势归来 萌贱耍宝笑果升级','于晓','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>1月5日电 2018新年刚至，全球知名IP《愤怒的小鸟》便给中国观众带来了一份巨大的新年礼物。三部全新“小鸟”系列动画短片――《愤怒的小鸟之蓝弟弟》和《愤怒的小鸟之猪猪传》第三、四季将于1月5日正式登陆腾讯视频。</p>\n<div style=\"text-align:center\"> \n <img alt=\"《愤怒的小鸟之蓝弟弟》剧照\" src=\"upload/image/806fd92f-9a4c-48ba-90d3-02848cc324ba.jpg\" style=\"border:px solid #000000\" title=\"《愤怒的小鸟之蓝弟弟》剧照\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  《愤怒的小鸟之蓝弟弟》剧照 \n</div>\n<p>　　<strong>全新小鸟魔力十足 “呆萌耍贱”捕获人心</strong></p>\n<p>　　《愤怒的小鸟》系列动画短片延续了短小精悍的风格，没有对话，仅凭角色调皮逗趣的动作展开剧情，但成年观众却并不因此觉得无趣，反而被全系列贯穿始终的“呆萌贱”所深深吸引。从国外平台播放数据来看，这样的内容非常适合现代都市人快节奏、碎片化的娱乐习惯。国外网友评论说：“每次看到‘蓝弟弟’和‘猪猪’狼狈又坚强的模样，仿佛看到了自己。笑完之后莫名就获得了一种抵抗现实的神奇力量。” 还有网友称“现在每天最放松的时候，就是晚上泡在浴缸里看几集《愤怒的小鸟》动画短片，有一晚我笑得差点把IPAD掉进水里！”数据显示，除了少儿群体，每天疲惫收工后亟需放松的年轻白领们也是“小鸟”的受众群。对他们而言，“小鸟”便好似一锅提神补气的心灵鸡汤。</p>\n<div style=\"text-align:center\"> \n <img alt=\"《愤怒的小鸟之猪猪传》剧照\" src=\"upload/image/be270dea-5f60-4f63-953f-3dbd8968b78d.jpg\" style=\"border:px solid #000000\" title=\"《愤怒的小鸟之猪猪传》剧照\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  《愤怒的小鸟之猪猪传》剧照 \n</div>\n<p>　　<strong>重金炮制“毛茸茸”质感 延续电影3D动画形象</strong></p>\n<p>　　此前，《愤怒的小鸟》系列动画短片曾在中国推出过第一季至第三季，以及“Stella”系列。几个系列在国内各大视频网站的总播放量高达40亿次。不过，这些短片都是2D版动画，直到2016年同名大电影《愤怒的小鸟》问世，观众才第一次体验到“小鸟”的3D质感。</p>\n<p>　　《愤怒的小鸟之蓝弟弟》和《愤怒的小鸟之猪猪传》系列短片，Rovio花费重金沿用了3D电影版的角色与视效设计，使得可爱的三胞胎“蓝弟弟”拥有了毛茸茸的逼真质感。之前在2D短片里身材浑圆的“猪猪”则终于“长出”了自己的手和脚。短片中所有的场景和道具也都变得立体起来。</p>\n<p>　　<strong>谁在幕后“操纵”《愤怒的小鸟》走红中国？</strong></p>\n<p>　　说到《愤怒的小鸟》，不能不提它的创造者――芬兰娱乐公司Rovio。2009年年底，Rovio基于智能手机触摸屏开发出了一款现象级的休闲益智游戏《愤怒的小鸟》。游戏的创新性与前瞻性，使Rovio的名字迅速传遍全球。在之后的几年里，“小鸟”品牌迅速扩张，而其衍生品――《愤怒的小鸟》系列动画短片和同名3D大电影则更受瞩目，成为全球各国争抢的“爆款”。</p>\n<p>　　《愤怒的小鸟》系列动画短片在中国大陆地区的独家代理发行权花落1905影业公司。作为一家拥有全产业链的影视公司，1905影业已和《变形金刚》、《007》、《碟中谍》、《终结者》、《极限特工》等多个全球超级IP系列都有过深度合作。2016年5月中美同步上映的同名3D大电影，在1905影业的营销推广下，最终以5.13亿元的票房，创下了当时手游改编电影在国内的最高票房纪录。正是在大电影合作上的认可与信任，此次1905影业再次与Rovio携手合作，将新一季《愤怒的小鸟》动画短片引入中国和更多的粉丝见面。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/be270dea-5f60-4f63-953f-3dbd8968b78d.jpg',NULL,542,'2018-01-05 16:48:00','2018-01-06 14:47:27','68',0),(3160,'刘思伟推新专辑 挑战大银幕文艺电影当男主角','罗攀','红网','<div style=\"text-align:center\"> \n <img src=\"upload/image/b9d826b7-c396-424b-80bf-23b7685e48e3.jpg\" style=\"border:px solid #000000\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\"></div>\n<p>　　<strong>刘思伟开年双喜临门 电影新专辑齐盛放 　</strong></p>\n<p>　　1月4日刘思伟《只想好好和你在一起》电影首映暨同名专辑发布会在北京碧水大厦宴会厅举行。发布会举行了隆重的百位嘉宾共走红毯仪式。</p>\n<p>　　《只想好好和你在一起》电影首映暨同名专辑发布会由央视主持蔡明娜、肖贵宁共同主持，刘思伟携电影主创人员悉数亮相。发布会分为两个部分，刘思伟第二张大碟《只想好好和你在一起》首唱会，和刘思伟担当男一号的同名文艺电影首映礼。</p>\n<p>　　电影《只想好好和你在一起》将于2018年一月登陆全国各大院线。电影讲述了全职太太方心如缠绕在事业成功的暖男老公、支离破碎的旧日男友、日日相伴的亲昵闺蜜、时时出击的候补情敌这些令人难以捉摸的关系之中的故事。表现了婚姻中的猜忌、矛盾、无奈与爱的执着。人生很短暂，拥有都是幸福。</p>\n<p>　　多年来刘思伟以其高亢嘹亮、充满磁性的嗓音用音乐讲述着属于男人的故事，无论是红遍全国的《找个好人就嫁了吧》，还是脍炙人口《断翅的天使》、《男人》，都是以男人的视角去创作，因此刘思伟也被大家称男人代言人，歌曲更是被封为中国男人情歌。</p>\n<p>　　距离第一张专辑《找个好人就嫁了吧》已经有十年的时间，刘思伟多年来一直热心于公益事业，其余的时间，他都留给了创作，新歌、新专辑，筹拍新电影。当一切完成的时候，刘思伟迫不及待的想要与大家一同分享自己的成果，以新歌曲、新专辑、主演的首部同名电影，三维一体的全新方式，重磅推出属于男人的故事。</p>\n<p>　　发布会现场刘思伟演唱了电影同名音乐《只想好好和你在一起》，神秘嘉宾韦嘉更是惊喜现身与刘思伟对唱《找个好人就嫁了吧》，这也是十年之后刘思伟和韦嘉的再次牵手。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/b9d826b7-c396-424b-80bf-23b7685e48e3.jpg',NULL,1406,'2018-01-06 08:26:00','2018-01-06 14:47:31','68',0),(3161,'国内跑马跑得最快的“颜值女王”被检出兴奋剂违规','陈海峰','钱江晚报','<p><strong>　　杭州马拉松，国内跑得最快的美女吃药了</strong></p>\n<p>　　昨天的马拉松圈炸锅了！因为1月4日傍晚中国反兴奋剂中心官网上公布了一则《兴奋剂违规信息公开》公告，其中内容有：近期，反兴奋剂中心实施的兴奋剂检查与调查中，下列运动员构成兴奋剂违规，这次公示的兴奋剂违规运动员一共有两位，都是马拉松跑者，一位是曾担任黑龙江省马拉松队助理教练的专业选手侯艳民，另一位是李文杰，李文杰在业余跑圈有着“颜值女王”的称号。</p>\n<p>　　原本只是专业竞技领域的兴奋剂问题，第一次发生在业余马拉松选手身上。这份公告，顿时引发了空前的关注。</p>\n<p>　　24岁的李文杰在跑圈相当出名，有多年的专业队背景。2015年她退役后，开始跑马拉松，只用了不到一年的时间就在业余跑圈名声大噪，并成为国内相关运动品牌的代言人。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　2016年全年，李文杰更是横扫各大马拉松赛场，一共拿下了27块金牌，收获了20多万元的奖金。中国马拉松官网显示，李文杰在2016年女子全程项目的业余选手中排名全国第8，其中2017年的武汉马拉松、杭州马拉松等比赛中她都拿到了国内女子冠军，成绩相当不错。</p>\n<p>　　中国反兴奋剂中心发布的公告显示，李文杰在2017年11月19日举行的青岛马拉松(全称为2017青岛海上国际马拉松)的兴奋剂检测中呈阳性，随后的B瓶检测仍为阳性(注：B瓶检测相当于复检，若B瓶的检测分析结果仍为阳性，则该运动员的兴奋剂检查结果即被判定为阳性)，李文杰是青岛马拉松的女子全程组第三名。</p>\n<p>　　公告中还给出了李文杰涉及的禁用物质，为促红细胞生成素(EPO)。据了解，运动中人为增加EPO能促进肌肉中氧气生成，从而让肌肉更有力、工作时间更长，对提高运动成绩和提升运动耐力相当有效，前几年在吃药的专业运动员里属于大路货。</p>\n<p>　　对于李文杰这样的业余跑友被检出兴奋剂，记者咨询了浙江省田径队女子中长跑总教练童笑梅：“业余选手兴奋剂违规，我也没有听说过。”</p>\n<p>　　那是否存在误服的可能性？</p>\n<p>　　童笑梅说：“确实存在这种可能，毕竟业余选手在吃的方面不可能像专业运动员这么留意。另外，如果运动员因为身体不适服用了药物，也可能被检出违规药物。不过根据专业流程，她可以在尿检后提交一份‘治疗用药豁免’申请，提前报备就不会有问题。”</p>\n<p>　　童笑梅介绍说：“现在国内马拉松也越来越规范了，一般比较大型的马拉松都会进行兴奋剂检测，但就检测流程来说，业余选手和专业选手都是一样的。”</p>\n<p>　　业余选手兴奋剂违规将面临怎样的处罚？</p>\n<p>　　中国反兴奋剂中心官网上还没有公布对李文杰的处理意见，不过记者查询了与她类似情况的专业运动员案例，对于专业运动员的处罚方式一般是取消比赛成绩、禁赛或负担若干例兴奋剂检测费用，但针对业余选手违规将面临怎样的处罚，目前并没有先例可以参照。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1194,'2018-01-06 07:58:00','2018-01-06 14:47:31','74',0),(3162,'毛振华坦诚回应黑龙江调查结果','史建磊','新华网','<p>　　<strong>毛振华坦诚回应黑龙江调查结果</strong></p>\n<p>　　新华社哈尔滨1月6日电 “毛振华视频”反映问题调查结果公布后，6日记者电话采访毛振华董事长。他说，感谢黑龙江省委省政府主要领导对这件事情的高度重视，非常高效地进行了调查处理。其实，省政府在过去几年对亚布力的发展十分重视，做了很多工作，也取得了成效，我们也是得益者。这次对我反映的基层问题的快速查处，体现了省委省政府在改善营商环境方面的决心和努力。我对今后亚布力阳光度假村的经营很有信心，对投资黑龙江很有信心。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>',NULL,NULL,385,'2018-01-06 14:39:00','2018-01-06 14:47:31','65',0),(3163,'海峡两岸绿色食品研讨会在哈尔滨举行','陈海峰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>哈尔滨1月5日电 (记者 刘锡菊 杨拓)第五届海峡两岸绿色食品技术与产业合作研讨会5日在中国北疆城市哈尔滨启幕。会议期间，来自两岸农业及食品科技产业界的百余位专家学者、企业代表就农业及食品科技产业园发展、新进农业技术、加强海峡两岸绿色食品产业合作等问题进行了深入探讨。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　黑龙江作为中国大陆最早发展绿色食品产业的省份，资源丰富。台湾地区则有着雄厚的技术实力，推动两岸双方高端生态农业和绿色食品产业链的资源整合；促进海峡两岸农业及食品科技产业园区建设；打造绿色食品新标准与新品牌，成为本次研讨会的主要话题。</p>\n<p>　　“希望借此能全面提升海峡两岸高端生态农业和绿色食品产业的品牌建设、技术水平和产业竞争力，打造海峡两岸高端生态农业和绿色食品技术与产业国际合作大平台。”台湾新来源酱园股份有限公司总经理萧新论说。</p>\n<p>　　研讨会期间，与会代表就“海峡两岸农业及食品科技产业园发展战略”、“花莲县近年各项新进农业技术介绍”、“黑龙江省绿色稻米产业发展新业态”、“加强海峡两岸合作推动健康食品产业发展”、“优势互补共谋农业合作发展”、“两岸合作优势互补新战略”等问题展开了深入的交流与讨论。</p>\n<p>　　哈尔滨海峡两岸产业园有限公司董事长林明洲先生说：“本次研讨会的召开将进一步健全哈尔滨海峡两岸农业及食品科技产业园协调管理办公室的服务机制，进一步加快企业入园落地，发挥入驻企业集群效应，具有积极推动作用，为陆台合作推波助澜。”他还期望作为哈尔滨海峡两岸农业及食品科技产业园的牵头单位，哈尔滨市科技局能够协调国家科技部等国家部委以及当地政府，从国家及地方的政策上给予产业园大力支持，使更多好的台湾农业及食品企业落户哈尔滨产业园区，为入住企业打造更加优良的生态环境。</p>\n<p>　　哈尔滨市科技局相关负责人表示：“此次研讨会将通过海峡两岸生态农业及绿色食品技术与产业合作联盟促进两岸高端生态农业和绿色食品全产业链资源整合，开展联合研发、示范应用及产业化合作生产，并将吸引更多企业入驻海峡两岸农业及食品(哈尔滨)科技产业园。”(完)</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,906,'2018-01-05 22:33:00','2018-01-06 14:47:31','70',0),(3164,'香港一医生手术中途离开 医院调查报告认为不能接受','周驰','新华网','<p><strong>　　香港一医生肝移植手术中途离开 医院调查报告认为不能接受</strong></p>\n<p>　　新华社香港1月5日电(记者张雅诗)去年香港一名医生在监督一项换肝手术中途离开，导致手术中断3小时。由涉事医院组成的调查小组5日表示，事件不能接受，会检讨手术人员沟通和工作安排，并继续跟进责任问题。</p>\n<p>　　香港大学医学院肝胆胰外科兼职副教授吴国际，于去年10月13日在玛丽医院负责监督一项肝脏移植手术时中途离开，前往一所私家医院进行预约手术，导致换肝手术中断3小时。调查小组主席兼玛丽医院副行政总监唐汉军5日在记者会上表示，这是不必要及可以避免的，吴国际在事件中没有作出妥善安排，让手术可以继续进行，不能接受。</p>\n<p>　　唐汉军表示，这是单一事件，与人手和编制无关。调查小组建议加强手术室各团队的沟通、厘清荣誉任命医生的行为守则和责任，并检讨肝脏移植团队的工作安排。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　玛丽医院行政总监陆志聪表示，会研究责任问题，以及是否需要惩处。他提到，该名接受肝脏移植的病人完成手术后初期病情反复，但其后已康复出院，并没有作出投诉。</p>\n<p>　　报告指出，吴国际在手术中途向主刀医生黄楚琳交代需要暂时离开2至3小时，并期望黄楚琳能在一般情况下继续进行手术。但当用作移植的尸体肝脏运抵医院后，黄楚琳发现肝脏有特殊情况，于是决定等待吴国际返回才继续进行手术。</p>\n<p>　　香港大学医学院4日回应传媒查询时强调，会以病人�祉和公众利益为首要原则，按调查结果及既定程序处理人事安排。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,663,'2018-01-05 19:49:00','2018-01-06 14:47:31','69',0),(3165,'银监会拟加强商业银行授信集中度风险管理','魏巍','新华网','<p>　<strong>　银监会拟加强商业银行授信集中度风险管理</strong></p>\n<p>　　新华社北京1月5日电(记者 李延霞)中国银监会5日就《商业银行大额风险暴露管理办法》公开征求意见，拟提高单家银行对单个同业客户风险暴露的监管要求、明确单家银行对单个企业或集团的授信总量上限，旨在推动商业银行强化大额风险暴露管理，有效防控集中度风险。</p>\n<p>　　银监会有关部门负责人表示，授信集中度风险是银行面临的最主要风险之一。近年来随着我国银行业对客户的授信方式日趋多元化，客户集中度风险呈现出新特点，制订统一、规范的大额风险暴露监管规则势在必行。</p>\n<p>　　办法从组织架构、管理制度、内部限额、信息系统等方面对商业银行强化大额风险管控提出具体要求。对于非同业单一客户，办法重申商业银行法关于贷款不超过资本10%的要求，同时规定包括贷款在内的所有信用风险暴露不得超过一级资本15%。对于非同业关联客户，办法规定其风险暴露不得超过一级资本的20%。对于同业客户，办法规定其风险暴露不得超过一级资本25%。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　考虑到部分银行同业风险暴露超过了办法规定的监管标准，办法对同业客户风险暴露设置了三年过渡期。商业银行可在过渡期内逐步调整业务模式、分散同业资产、扩展客户群体，而无需简单压降同业业务总体规模。</p>\n<p>　　银监会有关部门负责人表示，提高单家银行对单个同业客户风险暴露的监管要求，有助于引导银行回归本源、专注主业，弱化对同业业务的依赖，将更多资金投向实体经济。明确单家银行对单个企业或集团的授信总量上限，有助于改变授信过程中“搭便车”“垒大户”等现象，提高中小企业信贷可获得性，降低系统性风险。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,50,'2018-01-05 19:51:00','2018-01-06 14:47:31','67',0),(3166,'联合国副秘书长：从一带一路倡议中学习中国经验','高辰','人民日报','<p><strong>　　从一带一路倡议中学习中国经验</strong></p>\n<p><strong>　　――访联合国副秘书长阿希姆・施泰纳</strong></p>\n<p>　　本报记者&nbsp;&nbsp;王海林</p>\n<p>　　“中国在南南合作中发挥了重要作用，‘一带一路’倡议预示着中国将更加积极地参与国际事务，中国为联合国作出的贡献也越来越大。” 联合国副秘书长、联合国开发计划署署长阿希姆・施泰纳日前在接受本报记者专访时如是表示。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　2017年，不论对中国还是世界都是具有标志性意义的一年――“一带一路”倡议惠及全球，“构建人类命运共同体”理念首次被写入联合国决议，中共十九大胜利召开，中国理念、中国方案聚焦世界目光。</p>\n<p>　　施泰纳指出，“一带一路”倡议是过去50年到70年以来，由一个国家提出的，在其他国家进行投资和开发并建立友好合作关系的最大单一项目。中国有丰富的资源以及独特的发展经验，联合国开发计划署在过去的几十年里一直是许多国家政府的合作伙伴。“我们愿意同中国合作，从‘一带一路’倡议中学习经验”。</p>\n<p>　　“联合国气候变化大会《巴黎协定》的签署等事件让我们看到了中国在引导应对气候变化国际合作等方面发挥的引领作用，习近平主席的承诺让中国更加积极地与世界接轨。”施泰纳认为中国在南南合作中发挥着重要作用，为联合国作出的贡献越来越大。</p>\n<p>　　自2017年6月履新联合国开发计划署署长以来，施泰纳把推动联合国2030年可持续发展议程作为自己的工作重点。这一议程包含的17个可持续发展目标，与经济增长、社会发展紧密相关，强调不能以牺牲环境为代价。施泰纳特别赞赏中国发展所遵循的一个原则：不能落下任何一个人，不论男女老少、残疾或健康与否。一个国家发展是否成功，最终取决于是否有人落下。“可以明确看到在中国共产党的领导下，中国在绿色发展和生态文明方面更多地参与国际事务，在防治污染方面所采取的主要措施已经取得了显著成效。”</p>\n<p>　　施泰纳表示，习近平主席提出的生态文明建设理念不仅对于中国自身是一个巨大突破，对国际社会而言也十分有意义：“这就是领导力。真正的领导者能够判断形势，认清道路，充分地调动人民的积极性去寻找当下问题的答案。”中国不仅充分拥抱了自身历史发展的新时期，也致力于解决联合国正在关注的问题，实现可持续发展目标、社会进步以及生态环境可持续。“这标志着我们正在朝着一个全新的时代发展。中国与世界，正在一起探索未来”。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,1293,'2018-01-06 09:07:00','2018-01-06 14:47:31','64',0),(3167,'香港公布智慧城��蓝图 引入虚拟银行作为新服务模式','王硕','人民日报海外版','<p><strong>　　打造智慧城市</strong></p>\n<p><strong>　　香港有了“顶层设计”</strong></p>\n<p>　　本报记者 王 平</p>\n<p>　　2017年底，香港特区政府公布香港智慧城��蓝图，勾划未来五年的发展愿景。香港拥有全球顶级的科技基础设施，多年来在城市智慧化方面也多有着力，但一直缺乏全局规划。现在，香港终于有了打造智慧城市的“顶层设计”。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<p></p>\n<p>　　亮点不少</p>\n<p>　　香港创新及科技局局长杨伟雄在公布智慧城��蓝图的记者会上表示，建立智慧城市是特区政府的重点政策方向之一，政府将践行以人为本的理念，以科技提升城市管理。</p>\n<p>　　蓝图中有不少让人眼前一亮的措施。比如在2020年，特区政府将为所有香港居民提供免费数码个人身份，以单一的数码身份认证，进行政府和商业的网上交易。比起眼下人们在不同网站建立不同账号密码的情形，数码个人身份显然要方便合理得多。</p>\n<p>　　特区政府还将在2018年开发“快速支付系统”，用户透过手机号码及电邮地址，作电子方式的支付。特区政府还会推出10亿港元资助计划，协助安老及康复服务单位试用及购置科技产品。特区政府表示，医管局2019年会设立大数据分析平台，在2020年或以前开始试行智能医院模式。</p>\n<p>　　此外，特区政府将推动智能交通管理，除整合交通信息的应用程序外，将制定计划安装车内传感器，让驾驶者可以接收实时交通信息、遥距方式缴交隧道费。其他措施还包括逐步在公共照明系统安装LED灯、使用遥测感应装置监察空气污染、推行多功能智能灯柱试点计划以收集实时城市数据等。</p>\n<p>　　六大范畴</p>\n<p>　　这份智慧城��蓝图包涵六大范畴，包括智慧出行、智慧生活、智慧环境、智慧��民、智慧政府和智慧经济。</p>\n<p>　　智慧出行，包括将现有的交通信息应用程序整合为一个综合应用程序，方便市民规划行程；于2020年在所有主要干线安装交通探测器，提供实时交通信息；安装新一代路旁停车收费表等。</p>\n<p>　　智慧生活最主要的内容，是于2018年开发“快速支付系统”，用户可随时随地以电子方式支付。此外，还有制定二维码标准，推动零售业使用移动支付方式等。</p>\n<p>　　智慧环境则包括逐步减少燃煤发电，以天然气及非化石能源取代燃煤发电；由公营界别率先使用可再生能源；逐步在公共照明系统安装LED灯等。</p>\n<p>　　智慧��民，主要指为中小学课程领导提供科学、科技、工程及数学培训课程；推出博士专才库和优化实习研究员计划等。</p>\n<p>　　智慧政府则指政府推动应用第五代(5G)流动网络技术，于2020年采用单一数码个人身份，并应用人工智能、聊天机器人和大数据分析，以提升电子服务用户的体验。</p>\n<p>　　如虎添翼</p>\n<p>　　香港智慧城��蓝图的第六大范畴智慧经济，主要内容之一是引入虚拟银行作为新服务模式。</p>\n<p>　　在创新经济已成为全球经济增长主要引擎的今天，香港要稳固世界金融中心地位，必须为在应用金融科技创新、发展智慧金融方面有所作为。如香港金融管理局总裁陈德霖所说，银行与科技的融合刻不容缓，提升香港银行体系达到更高层次的智慧银行阶段，不单纯是锦上添花，而是势在必行。</p>\n<p>　　香港金融管理局日前公布将推出七项措施，提升香港银行体系智慧水准：2018年9月推出快速支付系统；2017年底前推出金融科技监管升级版沙盒；引入虚拟银行；推出“银行易”，简化对客户数码银行体验可能造成不便的监管要求；促进开放应用程序界面在银行业的发展及广泛应用，通过银行与科技公司的合作，鼓励创新及加强金融服务；加强跨境金融科技合作；提升科研及人才培训。</p>\n<p>　　香港有背靠祖国、依托粤港澳大湾区、接通世界的优势，有廉洁高效的政府和聪明勤奋的市民。香港向来不缺执行力，现在有了立足长远、方向明确的“顶层设计”，如虎添翼，明日可期。</p>\n<div id=\"function_code_page\"></div>',NULL,NULL,188,'2018-01-05 16:00:00','2018-01-06 14:47:31','69',0),(3168,'科技混搭设计演绎美好生活 中国设计智造大奖展举行','左盛丹','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>杭州1月6日电(徐瀚文)只需一勺清水，就能实现清洗和去污渍；一拖手，就能自动翘起的安全熨斗……这并不是科幻电影中的画面，而是发生在《中国设计智造大奖・美好生活展》中的实景。5日晚，《中国设计智造大奖・美好生活展》在杭州启幕，凭借科技滤镜的融入与设计感的加持，该展在现场演绎了一场别致的最炫“科技风”。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为《中国设计智造大奖・美好生活展》一角。　徐瀚文 摄\" src=\"upload/image/57d271e5-3800-4532-b56c-4699d1db9bce.jpg\" title=\"图为《中国设计智造大奖・美好生活展》一角。　徐瀚文 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为《中国设计智造大奖・美好生活展》一角。　徐瀚文 摄 \n</div>\n<p>　　据了解，中国设计智造大奖于2015年创立，系中国工业设计领域首个学院奖，既是当代创新设计评价、推广与合作的平台，也是艺术、科技与商业跨界创新的全球竞赛，更是一个创意转向财富与未来的实体创新加速器。大奖以“东方智性、国力智能、生活智慧、创新智库”为核心价值观，倡导设计回归“智造”本源。</p>\n<p>　　截至目前，中国设计智造大奖聚集了来自全球26个国家和地区的300余位涉及学界、设计协会、设计公司、设计博物馆及专业策展等领域的专家，而此次展出的产品，正是由专家根据独创的“金智塔”评价体系评选出的优秀作品。</p>\n<p>　　中国设计智造大奖组委会秘书长、中国美术学院工业设计研究院院长王昀介绍，本次展览呈现历届佳作近百件，其中包含文化创新、生活智慧、前沿科技、产业装备等几类优秀作品，以“设计+体验+互动”的形式，让创新设计走进千家万户，共塑美好生活。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为《中国设计智造大奖・美好生活展》一角。　徐瀚文 摄\" src=\"upload/image/4f9b4adf-6d25-4f84-be88-be0a10e87b74.jpg\" title=\"图为《中国设计智造大奖・美好生活展》一角。　徐瀚文 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为《中国设计智造大奖・美好生活展》一角。　徐瀚文 摄 \n</div>\n<p>　　众多展品中，获得“金智奖”的世界上第一款消费级自拍相机吸引了众多参展观众的目光。该相机可实现指尖放飞、空中悬停、智能定位，以独特的视角跟随拍摄，如影随形，记录生活中的片段。</p>\n<p>　　目前，该自拍无人机已投入生产，走进了消费者的家庭，改变着人们的生活。</p>\n<p>　　而这也正是此次《中国设计智造大奖・美好生活展》的主题，通过展现结合创新、艺术、科技、设计为一体的生活美学，让创新科技和设计真真切切地走进美好生活，满足老百姓对未来生活的向往和期待。</p>\n<p>　　近年来，随着文化创意中心和“工艺与民建艺术之都”建设的推进，中国美术学院国家大学科技园等一批国字号平台产业带动力持续增强，杭州以设计、影视、动漫、数字内容等为重点的文创优势行业发展在全国领跑；加上杭州高层次文化人才引进步伐的不断加快，使杭州成为大众创业万众创新的乐土。</p>\n<div style=\"text-align:center\"> \n <img alt=\"图为参展观众体验展览作品。　徐瀚文 摄\" src=\"upload/image/0025d334-60fb-4a17-a244-97c5cb43729a.jpg\" title=\"图为参展观众体验展览作品。　徐瀚文 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  图为参展观众体验展览作品。　徐瀚文 摄 \n</div>\n<p>　　杭州市文创办副主任孙磊称，设计产业是杭州文创产业重点培育的优势行业之一。而随着互联网+、文化+、发展新趋势的不断深入，文创产业、科技与制造业之间的融合发展步伐不断加速，创意设计作为深化供给侧结构性改革，推动产业结构转型，和提升现代制造业能级的关键要素，其地位和作用将越来越重要。</p>\n<p>　　孙磊表示，未来，杭州将进一步完善这一举措，从资金、项目、人才多个维度支持杭州创意设计业的发展，并积极响应十九大号召，加快推进中华优秀传统文化创造性转化，创新性发展，更好地满足人民群众对美好生活的需要。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/0025d334-60fb-4a17-a244-97c5cb43729a.jpg',NULL,479,'2018-01-06 14:22:00','2018-01-06 14:47:32','65',0),(3169,'摆脱图形验证码恐惧 今年8成火车票无需验证码','史建磊','浙江在线','<p>　　<strong>还记得被图形验证码统治的恐惧吗？今年八成火车票无需验证码</strong></p>\n<p>　　<strong>(浙江在线编辑 杨静涛)</strong>春运火车票于日前正式开始发售，今年春运自2月1日开始，3月12日结束。“返乡”无疑是未来一个月的关键词。</p>\n<p>　　铁路部门近日公布了春运火车票预售时间表，互联网售票、电话订票的预售期为30天，火车站售票窗口、代售点的购票预售期为28天。与去年相比，今年春运火车票首日开售后，购票速度相对没那么快。不过，热门方向的车票依然紧张。</p>\n<div style=\"text-align:center\"> \n <img alt=\"1月3日，2018年春运首日火车票正式开售，旅客在兰州火车站购票出行。今年春运从2月1日至3月12日结束。即日起，旅客可以提前预订到农历腊月十六(2月1日)的火车票，除夕当天(2月15日)火车票将从1月17日开售。据铁路部门消息，今年春运火车票的预售期仍延续此前规定，网络、电话最远预售期为30天，车站窗口、代售点、自动售票机最远预售期为28天。 <a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 杨艳敏 摄\" src=\"upload/image/7e8371be-0945-4bb5-9d9a-0cd8ee7bf1da.jpg\" title=\"1月3日，2018年春运首日火车票正式开售，旅客在兰州火车站购票出行。今年春运从2月1日至3月12日结束。即日起，旅客可以提前预订到农历腊月十六(2月1日)的火车票，除夕当天(2月15日)火车票将从1月17日开售。据铁路部门消息，今年春运火车票的预售期仍延续此前规定，网络、电话最远预售期为30天，车站窗口、代售点、自动售票机最远预售期为28天。 <a target=\'_blank\' href=\'http://www.chinanews.com/\'>中新社</a>记者 杨艳敏 摄\"> \n <input id=\"f5e5dd920b934ac4b5c3cd59650c7bd5\" name=\"f5e5dd920b934ac4b5c3cd59650c7bd5\" type=\"hidden\" value=\"http://www.chinanews.com/part/561/2018/0103/5382.html\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：春运火车票窗口售票。 \n <a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>记者 杨艳敏 摄 \n</div>\n<p>　　每年春运，对于返乡的游子来说抢购火车票，不亚于一场战役。铁道部售票官网12306推出的图片验证码，曾经让无数旅客抓狂。今年有了好消息，八成以上的车票购买时不需要使用验证码了。</p>\n<p>　　<strong>为防黄牛结果考倒购票者</strong></p>\n<p>　　为了防止抢票插件和黄牛党恶意购票，铁路部门从2013年起，频繁升级网站验证码机制，图形验证码的种类逐渐增加，识别的难度不断加大。这一原本为屏蔽机器人的验证过程，倒是把不少真人给难住了。</p>\n<p>　　据检测发现，2015年年底， 12306网站网购火车票图形码达到581种，一次性输入正确的仅为8%。</p>\n<p>　　“图片看不清”、“奇葩物件不认识”、“选来选去都不对”……随着12306将“数字+字母”的验证码升级为图形验证码，不少网友反映购票变为了“考智商”。“图片几乎囊括了天文地理、历史政治等多方面，多验证一次就巩固了一个知识点，还可以德智体美劳全面发展。”</p>\n<p>　　<strong>八成以上网络购票不需验证码</strong></p>\n<p>　　今年，中国铁路总公司表示，八成以上的春运网络购票将不需要验证码。昨天， 360浏览器表示通过大数据分析，今年春运抢票初期，12306的图形验证码并没有比去年增加难度，他们发现图形验证码已经不是铁路防止黄牛倒票的主要技术手段，铁路正在通过更加严格的实名认证等其他方式来防黄牛，提高了用户的购票体验。</p>\n<p>　　2017年春运，铁路又优化了售票流程，曾经给旅客带来不便的网购车票验证码再次进行重要调整。当时近六成车票发售时旅客不再需要使用验证码。今年，铁总表示，春运网络购票需要验证码的比例将被压缩到15%以下。</p>\n<p>　　相关负责人证实，今年12306图形验证码的难度并没有提高，图形验证码是随每日热门线路的出现而动态出现，也就是说，当旅客都集中抢购一条线路的火车票时，为了减轻瞬时购票压力，图形验证码会随机出现，如是非热门路线，旅客可直接购票，不会出现图形验证码，这无疑提高了人们购票的速度。</p>\n<p>　　<strong>杭州上榜十大最难买票的城市</strong></p>\n<p>　　“今年各大高校基本上1月中下旬放假，到2月1日春运开始，学生返程基本结束了。”携程相关人士告诉记者。这意味着，去年因春节较早导致学生潮、务工潮和白领潮叠加而出现的集中购票情况，今年不会发生。</p>\n<p>　　同程旅游发布的《2018年春运大数据报告》显示，春运高峰时期，“广州、北京、上海、深圳、杭州、福州、宁波、温州、厦门、武汉”为火车票最难买票的十大出发城市。</p>\n<p>　　携程数据还显示，春运首日的出行客流中，90后的比例首度超过50%，是自2016年春运以来最高的。从车次选择看，90后更愿意选择高铁、动车组列车，仅不到25%的90后会选择K字头快车和T字头特快列车。</p>\n<p>　　<strong>关于在线订票，你需要知道这些</strong></p>\n<p>　　1、自2014年11月28日起，铁路部门对互联网、电话订票的起售时间进行调整。放票时间点从16个调整为21个，即8：00至18：00期间，每个整点和半点均有新票起售。(具体请查询中国铁路客户服务中心官网)</p>\n<p>　　2、各在线旅游企业均推出线上订票、抢票服务，用户可以按车次、时间段，提前预订车票。一旦相关车次开票，系统会自动为旅客购票。订票成功率还是比较高的。</p>\n<p>　　3、火车票起售后，如果抢到票的人没及时支付，30分钟后这些票源将返回票池；开车前15天以上退票不收手续费。所以，开车前第15天往往是一个退票高峰期，用户可以适时捡漏。</p>\n<p>　　4、在没有符合出行需求的车票时，可以通过12306网站或手机客户端的接续换乘功能，查询途中换乘一次的部分列车余票情况。</p>\n<p>　　(综合北京青年报、浙江在线、杭州日报、新华网、澎湃新闻消息)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/7e8371be-0945-4bb5-9d9a-0cd8ee7bf1da.jpg',NULL,1758,'2018-01-06 13:56:00','2018-01-06 14:47:32','65',0),(3170,'中国人民解放军驻澳门部队司令员调整','陈海峰','新华网','<p><strong>　　中国人民解放军驻澳门部队司令员调整</strong></p>\n<p>　　新华社澳门1月5日电 中国人民解放军驻澳门部队发布消息：根据中央军委主席习近平签发的命令，驻澳门部队司令员王文少将轮换交流到省军区任职，驻香港部队副司令员廖正荣少将接任驻澳门部队司令员。</p>\n<p>　　日前，解放军南部战区司令员袁誉柏中将宣布了驻澳门部队司令员调整任职命令。</p>\n<p>　　廖正荣表示，将与驻澳门部队政委周吴刚一起率驻军全体官兵，坚定贯彻“一国两制”伟大方针，严格依据《中华人民共和国澳门特别行政区基本法》和《中华人民共和国澳门特别行政区驻军法》，忠诚使命、履职尽责、爱澳亲民，推动驻军建设向着新时代强军目标迈进，为维护澳门长期繁荣稳定作出积极贡献。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>',NULL,NULL,1391,'2018-01-05 21:58:00','2018-01-06 14:47:32','69',0),(3171,'业界报告称三成中国在校大学生创业意愿强','罗攀','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>北京1月6日电 (记者 马海燕)6日在北京发布的《2017中国大学生创业报告》指出，中国大学生的创业意愿持续高涨，30%的在校大学生创业意愿强烈。此外，有一定创业意愿的学生占57.9%，从未想过创业的只占12.1%。</p>\n<div style=\"text-align:center\"> \n <img alt=\"3月4日，招聘会吸引了众多即将毕业的大学生前来应聘。当日，由福州台湾创业创新创客基地委员会主办，福州市台胞投资企业协会协办的“首届福州台湾创业创新创客基地大专院校人才招聘会”在福州马尾举行，47家入驻基地的台资或陆台合资企业，抛出了包括产品美编、动画设计、电子商务、产品营销、食品调制、机械设计、产品临床医学在内的22类岗位，吸引了众多即将毕业的大学生前来应聘。 张斌 摄\" src=\"upload/image/83555fe4-d2e4-4f56-8c3c-48f6dcb359ae.jpg\" title=\"3月4日，招聘会吸引了众多即将毕业的大学生前来应聘。当日，由福州台湾创业创新创客基地委员会主办，福州市台胞投资企业协会协办的“首届福州台湾创业创新创客基地大专院校人才招聘会”在福州马尾举行，47家入驻基地的台资或陆台合资企业，抛出了包括产品美编、动画设计、电子商务、产品营销、食品调制、机械设计、产品临床医学在内的22类岗位，吸引了众多即将毕业的大学生前来应聘。 张斌 摄\"> \n <input id=\"254ae650ad304973b3c2126c2610aa54\" name=\"254ae650ad304973b3c2126c2610aa54\" type=\"hidden\" value=\"http://www.chinanews.com/tp/hd2011/2017/03-04/720710.shtml\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：招聘会吸引了众多即将毕业的大学生前来应聘。 张斌 摄 \n</div>\n<p>　　该报告由中国人民大学牵头，上海交通大学、浙江大学、厦门大学、武汉大学、中山大学等30余家高校、企业和社会组织联合发布，调查范围覆盖全国52所高校。</p>\n<p>　　报告显示，大学生创业主要出于追求自由的生活方式，在受访者中占31%；其次是赚钱，占26%；实现个人理想排在第三位，占18%。</p>\n<p>　　排在创业领域前五位的是消费电商、教育、信息科技、餐饮住宿和体育文化。大学生创业的切入点中，发现某种技术的商业潜质占58%，发现某个市场痛点占42%。</p>\n<p>　　调查还发现，创业正呈现低龄化趋势，很多大学生从本科之前就对创业产生兴趣，七成创业者在本科期间开始创业。大学生普遍认同创造力(24.5%)、风险承担(16.5%)和警觉性(13%)是创业者极为重要的素质。</p>\n<p>　　中国人民大学商学院院长、创业学院副院长毛基业指出，深化创新创业教育改革正在深度融入高校人才培养体系，成为高等教育综合改革的突破口。同时，虽然中国高校创业生态系统建设已经取得巨大成绩，但也存在不平衡和不充分的发展状况。</p>\n<p>　　大学生最希望高校提供的创业扶持政策包括“创业算学分”(24%)、“实验设备向学生开放”(17%)和“学校科研成果优先向创业学生转让”(16%)。而高校提供的主要扶持政策为“休学创业”、“实验设备向学生开放”以及“创业算学分”。</p>\n<p>　　超过50%的受访者认为资金短缺是创业最大障碍。报告认为，未来有必要进一步强化高校创业生态系统环境要素的培育和发展，特别是发展大学生创业融资体系，缓解大学生创业的资金瓶颈约束。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/83555fe4-d2e4-4f56-8c3c-48f6dcb359ae.jpg',NULL,1582,'2018-01-06 14:20:00','2018-01-06 14:47:32','65',0),(3172,'全国汽修冠军：对一辆车上万个零件部位深谙于心','高辰','钱江晚报','<p align=\"center\"><img src=\"upload/image/00ab0b35-bf71-44de-a08c-03bc3fc11e76.jpg\"></p>\n<p><strong>　　沉在一线13年，这回还拿了一个全国汽修行业的第一</strong></p>\n<p><strong>　　一辆车有上万个零件</strong></p>\n<p><strong>　　每个零件的部位他都深谙于心</strong></p>\n<p>　　本报记者 孙燕 本报实习生 俞吟之 摄影：杨晓轩</p>\n<p>　　“下午，我会来讲课。”江海荣接起电话，跟对方确认了2017年12月28日的行程。电话来自他的母校――杭州市交通职业高级中学。他从这里开始接触汽修。多年来，他的同学早已退出汽修一线，而他，却从未离开。</p>\n<p>　　在哥哥的指引下入行</p>\n<p>　　见到江海荣的时候，他正在二楼的修理车间里忙碌，一身灰色的工作服，白白净净，阳光清爽。</p>\n<p>　　“江哥，你过来看看，这是什么问题？”后辈在修理中有问题，爱请教他。虽然身为技术总监，但从来没人喊他江总，都爱喊“江哥”。</p>\n<p>　　江海荣的办公室，与其说是办公室，还不如说是间工作室。办公室靠墙放着两只架子，上面摆满了各种汽车零件。他的桌子上除了书，还有厚厚的一沓卷子。这里是他教学徒弟、研究汽修技术的小天地。</p>\n<p>　　在前不久结束的“德科杯”汽车维修工职业技能竞赛上，江海荣从全国28个省(区、市)的56名决赛选手中脱颖而出，拔得头筹。</p>\n<p>　　大大小小的冠军，江海荣得过不少，但这一次，是分量最重的一个，这是全国汽修界最权威的比赛。</p>\n<p>　　很小的时候，江海荣就对汽车表现出了极大的兴趣，“喜欢玩具车，小时曾经研究过拖拉机，把若干零件拼起来后发现会往前跑，就很好奇、喜欢。”“我有个哥哥，大我7岁，从小看我对汽车很有兴趣，建议我学这个。”江海荣回忆道。</p>\n<p>　　朝着这条路，他先考入杭州市交通职业高级中学汽车检测与维修专业，随后直接升入了浙江省交通职业技术学院汽车检测与维修专业。毕业后，他进了元通集团。</p>\n<p>　　如今他的同学都已不在汽修一线了，坚持到现在的只剩下他。想过换份工作吗？江海荣摇摇头，“在这里我深深体会到，有技术能得到尊重，会被礼待。”而且“每攻克掉一个汽修方面的‘疑难杂症’，我会高兴上一整天。”</p>\n<p>　　仍然觉得修车有点难</p>\n<p>　　虽然拿到了全国汽修界的第一，但问他修车究竟难不难时，他会不假思索地告诉你：“有点难。”江海荣记得有一次，店里收了一辆别克新君越车。车主说，他这辆车的收音机不知怎地就黑屏了。按照常规处理办法，同事对照维修手册排查线路，换上了新的屏幕、主机，奇怪的是问题并未得到解决。</p>\n<p>　　江海荣发现，“这辆车的娱乐系统是当时最新的MOST网络，会不会是系统某个部件干扰引起的。”江海荣猜想。但这套系统里面有五六个模块，连接的线路非常复杂。江海荣不打算放弃。他花了一天的时间，终于排查出故障原因。一辆车有上万个零件，江海荣已经把每个零件的部位深谙于心。但对层出不穷的新科技，他坦言还是需要费点脑筋，“目前为止，排除汽车故障方面还是比较有信心的！”</p>\n<p>　　自己制的测漏工具很实用</p>\n<p>　　在空调修理中，常常会有车主反映，自己的空调不制冷了。一般情况下，空调不制冷都是因为氟利昂泄漏了，但具体的泄漏部位查找却不那么容易。有一次，他们反反复复找了好几遍，就是找不到泄漏的部位。江海荣和同事商量了一下，突发奇想，不如做个工具，将蒸发箱与外部管路连接的地方堵住，然后加压来检测，这样检查就会变得容易了。</p>\n<p>　　说干就干，江海荣先画好图纸，再量尺寸，然后去五金市场买耗材，最后在自己的工作室制作。几天后，这把蒸发箱万能测漏工具就诞生了。“我们做过一次统计，应用新‘武器’在空调维修上后，返修率下降了80%以上。”江海荣说，当时他还想给这个工具去申请个专利，后来事情多了就给耽搁了。</p>\n<p>　　多年来，浙江物产元通汽车集团以技术立企、人才兴企，大力弘扬工匠精神。公司的青年中高级技术人在数量上与覆盖面上有了较大幅度的提升。以江海荣等为代表的一大批技术尖子脱颖而出，成为本岗位独领风骚的行家里手。自三年前首家省级汽修行业的技能大师工作室落户元通以来，现已拥有四家省级的技能大师工作室，涵盖汽修机电、钣金、油漆等领域，为公司高技能人才培养、服务流程标准制定，推动行业技术水平的进步提升作出巨大贡献。</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/00ab0b35-bf71-44de-a08c-03bc3fc11e76.jpg',NULL,952,'2018-01-06 09:41:00','2018-01-06 14:47:32','72',0),(3173,'甘肃手工造纸传承“七十二道”手艺','高辰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新社</a>兰州1月6日电 题：<strong>甘肃手工造纸传承“七十二道”手艺</strong></p>\n<p>　　作者 闫姣</p>\n<div style=\"text-align:center\"> \n <img alt=\"甘肃省陇南市西和县境内的西高山乡，至今有13户人家延续着已流传千年的手工造纸技艺。在当地，方言中将制作麻纸形象地称为“捞纸”。手工做这种麻纸，需要把枸树皮收集回来后，经过浸泡、砸穰、淘穰、刀切、麻钱记数、柞水、上墙等72道工序，才能蒸煮锤切出土纸。南如卓玛 摄 二十世纪五十年代，麻纸主要用于村民包装挂面、糊顶棚、倦炮、糊墙以及殡葬用。近些年来，麻纸受到书画爱好者的青睐，因为麻纸纸质柔韧、耐磨，极富弹性，有“千年不朽”等特点，适用于传统书画装裱、裱褙糊垫、纸活工艺制作、包装等用途。\" src=\"upload/image/d9de7aab-63df-4bd1-ab4b-370b22961a1c.jpg\" title=\"甘肃省陇南市西和县境内的西高山乡，至今有13户人家延续着已流传千年的手工造纸技艺。在当地，方言中将制作麻纸形象地称为“捞纸”。手工做这种麻纸，需要把枸树皮收集回来后，经过浸泡、砸穰、淘穰、刀切、麻钱记数、柞水、上墙等72道工序，才能蒸煮锤切出土纸。南如卓玛 摄 二十世纪五十年代，麻纸主要用于村民包装挂面、糊顶棚、倦炮、糊墙以及殡葬用。近些年来，麻纸受到书画爱好者的青睐，因为麻纸纸质柔韧、耐磨，极富弹性，有“千年不朽”等特点，适用于传统书画装裱、裱褙糊垫、纸活工艺制作、包装等用途。\"> \n <input id=\"4eb0c3cc7ef34ed98eea034c23e6d91a\" name=\"4eb0c3cc7ef34ed98eea034c23e6d91a\" type=\"hidden\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left;text-indent: 2em; \">\n  资料图：手工造纸 \n</div>\n<p>　　“七十二道手，上墙还要吹一口。”寒冬里，55岁的李生武忙碌在造纸作坊里。2017年一年，他用传统手工造纸技艺生产构树皮纸10万张，这门手艺传到他已是第六代。</p>\n<p>　　工业化冲击下使中国传统造纸术“日渐式微”。眼看曾经养家糊口的手艺活儿即将失传，李生武如坐针毡。申报非物质文化遗产、建立造纸传习所、借助互联网线上线下双销路……李生武忙前忙后，村里仅有的一户造纸艺人逐渐发展为6户。</p>\n<p>　　明清两代，甘肃康县大堡镇李家山村里的造纸业兴盛，当地人凭祖传的手艺勤劳致富。虽然岁月更迭，但李生武仍坚守这门手艺。</p>\n<p>　　在康县造纸传习所内，抄纸槽里放满了纸浆和水，纸浆纤维浮在表面，李生武双手抓住竹帘，从槽中“抄”起，竹帘上便形成厚薄均匀的一层纸，随后他把纸扣放在纸榨上去除水分，晾干后“吹”在墙上。李生武说，初学手艺时，一到夏天，就常遇到纸浆粘和的情况，经父亲指导才解决了这个难题。</p>\n<p>　　李生武说，造纸工序共七十二道。极为重要的有“浸泡、蒸煮、打碓、碾穰、淘穰、入药、抄捞、计数、扫纸”九道工序，这些李生武早已熟稔在心。抄捞是手艺活，抄捞纸张的薄厚、均匀全凭工匠的经验，纸太薄或太厚都影响使用。</p>\n<p>　　村子里漫山遍野种植的构树，其韧皮纤维是造纸的高级原料。李生武说，构树皮纸有纹理清晰，柔韧度高，耐保存百年不腐。当地造纸艺人说，“清明前砍折，三年砍折两次，构树就会越长越茂盛。”</p>\n<p>　　构树皮晒干后，在池子中用清水浸泡一周时间，然后在石灰水中浸泡10多分钟，再用直径两米的蒸锅蒸煮，即得到纸浆。为此，李生武要忙碌两个多月。</p>\n<p>　　目前李生武的作坊生产两类纸，一类为祭祀用纸，通过商贩供往甘肃西和县、徽县、两当县等地。另一类为书法绘画纸，2016年，李生武改良造纸技艺推出可“私人定制”的四种规格。</p>\n<p>　　现在，李生武学着用“新玩意儿”微信推广传统造纸术。此后，不断有顾客慕名线上购买构树皮纸。同时，李生武还与6家网店合作。他说，接下来计划邀请书画家在构树皮纸上创作，逐步打开传统造纸业的市场。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/d9de7aab-63df-4bd1-ab4b-370b22961a1c.jpg',NULL,517,'2018-01-06 11:18:00','2018-01-06 14:47:33','75',0),(3174,'中澳合拍第一部迷你剧在墨尔本开机(组图)','高辰','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>墨尔本1月6日电 (记者 陶社兰)在澳大利亚的中国留学生的学习、生活是什么样的？他们有着怎样的梦想和奋斗？5日在墨尔本开机的中澳合拍的第一部迷你剧《追光》，将以网剧形式展现这一题材。</p>\n<div style=\"text-align:center\"> \n <img alt=\"中澳合拍第一部迷你剧在墨尔本举行开机仪式。　陶社兰 摄\" src=\"upload/image/fbc2c6c0-4901-48fe-8ea7-3b8724711aa1.jpg\" title=\"中澳合拍第一部迷你剧在墨尔本举行开机仪式。　陶社兰 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  中澳合拍第一部迷你剧在墨尔本举行开机仪式。　陶社兰 摄 \n</div>\n<p>　　《追光》剧本创意来源于真实生活，从留学生为主的新一代移民与改革开放初期来澳的老一代移民的对比中，探讨中澳文化差异交融的过程。</p>\n<div style=\"text-align:center\"> \n <img alt=\"制片人、编剧、主演李潇介绍创作情况。　陶社兰 摄\" src=\"upload/image/6f26ca38-1fe7-4984-b4e2-61f79830a131.jpg\" title=\"制片人、编剧、主演李潇介绍创作情况。　陶社兰 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  制片人、编剧、主演李潇介绍创作情况。　陶社兰 摄 \n</div>\n<p>　　该剧制片人、编剧、主演李潇本人就是一名曾经的留学生。1989年出生的他，7年前从重庆来到墨尔本留学。初来时，由于文化上的不适应，百无聊赖中写下第一个剧本，并在墨尔本大学创立了《凹凸》剧社，还编导演过多部话剧。他表示，重新回到自己喜欢的文化事业中时，感到如鱼得水，于是开始享受在澳大利亚每一天的生活。后来，机缘巧合，他相继认识了该剧导演严雪、投资人李燕鹏，大家慢慢磨合，形成共识，要做与文化相关的、非常本地化的文化产品，表现中国年轻一代在澳大利亚奋斗的故事。</p>\n<div style=\"text-align:center\"> \n <img alt=\"澳大利亚演员Roy Barker参演《追光》。　陶社兰 摄\" src=\"upload/image/c261cd75-002d-40b2-85cd-9c8eaf9d6fcd.jpg\" title=\"澳大利亚演员Roy Barker参演《追光》。　陶社兰 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  澳大利亚演员Roy Barker参演《追光》。　陶社兰 摄 \n</div>\n<p>　　该剧主创人员都是80后、90后的在澳中国年轻人。投资人李燕鹏生于1992年，16岁时到墨尔本留学，2017年取得硕士学位。在此之前，曾一边读书一边投资饭店，慢慢积累了一些资本。3年前，他开始和朋友李潇合作做《追光》这个项目，计划最终投资100万澳元。他说，不能说这种投资没有风险，但从目前各方的反应来看，都很积极。这让他对该剧的前景充满信心。</p>\n<div style=\"text-align:center\"> \n <img alt=\"导演严雪介绍情况。　陶社兰 摄\" src=\"upload/image/f4911393-10b0-4268-9b58-71cbbf8e1190.jpg\" title=\"导演严雪介绍情况。　陶社兰 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  导演严雪介绍情况。　陶社兰 摄 \n</div>\n<p>　　导演严雪毕业于中央戏剧学院导演系，曾经是中央戏剧学院的教师，参与过电影《太极张三丰》、《高楼边》、《重庆森林》，话剧《枕头人》、《天堂之门》的拍摄和制作。该剧在演员、美术、灯光设计等方面，都不乏澳大利亚本土人士的参与。</p>\n<p>　　《追光》共分10集，每集30分钟，拟于今年9月上线播出。(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/f4911393-10b0-4268-9b58-71cbbf8e1190.jpg',NULL,1130,'2018-01-06 14:24:00','2018-01-06 14:47:33','75',0),(3175,'5岁男童失踪29年 众人相助一家人终团圆','罗攀','中国新闻网','<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>丽水1月6日电(见习记者 周禹龙 通讯员 胡昌清)浙江省丽水市公安局二楼会议室，冯明南开心地哭了――在二女儿刚刚出生之际，他找到了失散29年的亲生父母。</p>\n<div style=\"text-align:center\"> \n <img alt=\"冯明南 叶益平 摄\" src=\"upload/image/265f7b80-015e-4fe6-8b75-0d72ae5c37b3.jpg\" title=\"冯明南 叶益平 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  冯明南 叶益平 摄 \n</div>\n<p>　　1989年开春，湖南省新化县5岁的冯明南跟随母亲到河南省郑州市找工作时不慎走失，从此杳无音信；2018年新年之时，在浙江丽水、湖南新化警方和志愿者的共同努力下，这个失散的家庭重新走到了一起……</p>\n<div style=\"text-align:center\"> \n <img alt=\"冯明南认亲 叶益平 摄\" src=\"upload/image/2707e479-1100-4246-9c34-dffe1a17ab2b.jpg\" title=\"冯明南认亲 叶益平 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  冯明南认亲 叶益平 摄 \n</div>\n<p>　　“我想找到自己的亲生父母”</p>\n<p>　　今年34岁的冯明南，是河南省尉氏县人，可他一直知道，养他的并不是亲生父母。</p>\n<p>　　“到了养父母家，我听不懂村里人说的话，经常被村里的同龄孩子欺负，说我不是亲生的，是抱养来的。”冯明南告诉记者，他小时候经常会哭，想要找到亲生父母，“夜里还时常做噩梦。”</p>\n<div style=\"text-align:center\"> \n <img alt=\"冯明南DNA采集 叶益平 摄\" src=\"upload/image/60db8fcd-f07c-4175-a445-0c38e386c51a.jpg\" title=\"冯明南DNA采集 叶益平 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  冯明南DNA采集 叶益平 摄 \n</div>\n<p>　　到了养父母家两三年后，养父母告诉他，他是从郑州火车站被抱养来的。养父母还把刚被抱养回来时冯明南穿的两件衣服(毛衣和军绿色外衣)、一件缝补过的裤子和一双发白的解放鞋清洗干净，一直放在家里保存着。</p>\n<div style=\"text-align:center\"> \n <img alt=\"心情忐忑的冯明南 叶益平 摄\" src=\"upload/image/94cc53d3-19bd-4f51-855d-d36503f46059.jpg\" title=\"心情忐忑的冯明南 叶益平 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  心情忐忑的冯明南 叶益平 摄 \n</div>\n<p>　　“弟弟的丢失，对一家人打击都很大，我们老家湖南每年过年都要买鱼，我父母为了寻找弟弟，都没钱买鱼……”冯明南的哥哥刘长兵说。</p>\n<p>　　自从孩子丢失后，只要一听到哪里有孩子信息，亲生父亲刘华秋就会赶过去。29年来，刘华秋先后去了湖南、湖北、河南、陕西等众多地方寻找儿子，随身带的中国地图都被翻破了。</p>\n<p>　　但是，每一次的寻找都是抱着希望而去，失望而归。</p>\n<div style=\"text-align:center\"> \n <img alt=\"冯明南小时候的衣服 叶益平 摄\" src=\"upload/image/c580523b-ff5e-4ee6-bc71-86f0a39087ba.jpg\" title=\"冯明南小时候的衣服 叶益平 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  冯明南小时候的衣服 叶益平 摄 \n</div>\n<p>　　也许是特别的经历，“丢失”后的冯明南从小叛逆心很强。1997年就从养父母家跑到河南开封、广东广州、珠海、东莞、肇庆等地打工，他做过餐饮、面点师、针织。</p>\n<p>　　2007年经朋友介绍在广东东莞认识小他5岁的广西姑娘李亮玲。两人相恋后，2009年在河南养父母家举办了婚礼，同年生了一个女儿。</p>\n<p>　　2010年下半年夫妻俩带着女儿来到浙江丽水打工。看到女儿一天天成长，冯明南更想找到自己的亲生父母。于是他就抱着试试看在网上发布寻找亲生父母。但是消息发出就像石沉大海，没有回音。</p>\n<div style=\"text-align:center\"> \n <img alt=\"接待走失男子亲生父母 叶益平 摄\" src=\"upload/image/cb8343ec-8cba-4d9a-8b80-83dbfbaadb70.jpg\" title=\"接待走失男子亲生父母 叶益平 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  接待走失男子亲生父母 叶益平 摄 \n</div>\n<p>　　转眼间，好几年过去，事情出现了转机。2014年3月26日，“简单”的志愿者在网上看到了冯明南的寻亲消息后，立即跟他取得了联系，并帮助他到所在的丽水市公安局经济开发区分局采集DNA样本。同年4月3日，丽水市公安局刑事科学技术研究所DNA室将DNA信息录入全国公安机关查找被拐卖/失踪儿童DNA数据库。</p>\n<p>　　“我的名字‘原来’是刘龙兵”</p>\n<p>　　2017年，丽水市公安局刑事科学技术研究所DNA室民警比对发现冯明南的DNA与湖南省新化县的刘华秋夫妇的DNA符合亲属关系。</p>\n<div style=\"text-align:center\"> \n <img alt=\"亲生父母当年外出寻亲乡村出具的证明 叶益平 摄\" src=\"upload/image/07e5fe67-ddfc-4030-bee9-bbf43be89ba0.jpg\" title=\"亲生父母当年外出寻亲乡村出具的证明 叶益平 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  亲生父母当年外出寻亲乡村出具的证明 叶益平 摄 \n</div>\n<p>　　得知结果后，丽水市公安局刑事侦查支队副支队长鲍奕斌通过湖南警方的协助，电话联系到新化县刘华秋夫妇。“电话是刘华秋的长子接的，他确认确实有一个弟弟刘龙兵在小时候走失，不过他怀疑我是骗子，没有过多说话就把电话挂了。”鲍奕斌说。</p>\n<p>　　刘长兵对丽水警察的“热情服务”表示怀疑，特地在百度搜索出名字与照片到辖区派出所核查身份。</p>\n<p>　　通过辖区派出所的核实，才没有怀疑丽水警察是假警察。2017年12月27日，湖南省新化县公安局横阳派出所所长杨劲松向丽水公安反馈，元旦过后，刘华秋夫妇到丽水市公安局刑事科学技术研究所采集DNA复核。</p>\n<p>　　1月5日，丽水市公安局刑事科学技术研究所DNA民警现场采集了刘华秋夫妇的DNA样本，经工作人员加班加点，通过与冯明南DNA样本比对，复核结果确认刘华秋、伍春连是冯明南的生物学父母亲。</p>\n<p>　　最终，刘华秋夫妇在其长子刘长兵与媳妇的陪同下，在丽水市公安局与走失29年小儿子刘龙兵见面。</p>\n<p>　　“你看他的上下嘴唇多像他父亲的嘴唇。”见面后，伍春连紧紧拉着儿子的手，看见刘龙兵双手掌的“断掌”还在，心情激动，“儿子找到，我们又团聚了，真是太高兴了。”</p>\n<div style=\"text-align:center\"> \n <img alt=\"刑科所技术员对亲生父母亲按规定重新进行抽血DNA复核 叶益平 摄\" src=\"upload/image/85ee70b3-d5e5-4d7b-ba88-1e4b7ecd730c.jpg\" title=\"刑科所技术员对亲生父母亲按规定重新进行抽血DNA复核 叶益平 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  刑科所技术员对亲生父母亲按规定重新进行抽血DNA复核 叶益平 摄 \n</div>\n<p>　　临近年关，冯明南所在的公司生意特别忙，他是一名针织技术骨干，为人憨厚且工作勤快，现在每月收入万元左右。“我要多赚钱，孝敬父母，好好养孩子，以后等我空一点，要经常回湖南老家看看自己的父母和其他亲人，同时与哥哥一起照顾亲生父母。”(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/85ee70b3-d5e5-4d7b-ba88-1e4b7ecd730c.jpg',NULL,963,'2018-01-06 14:14:00','2018-01-06 14:47:33','65',0),(3176,'华尔街选出全球“最值得投资企业”：阿里巴巴夺冠','唐云云','中国日报网','<p><strong>　　华尔街“用钱投票”：全球“最值得投资企业”大赛阿里巴巴夺冠</strong></p>\n<p>　　经过六轮投票对决，华尔街的投资经理们从全球64家大企业中最终选出了“最值得投资的企业”：阿里巴巴。</p>\n<p>　　美国投资界知名风险投资数据分析公司CBInsights组织了这次大赛。CBInsights在全球范围内挑出了64家各个领域的最热门公司，包括苹果、谷歌、Facebook、特斯拉等互联网新贵，德国大众、高盛、IBM等传统巨头，以及阿里巴巴、腾讯等中国公司，以类似NBA季后赛对决的赛制对投资机构和基金经理们开放投票，最终选出最值得投资并长线持有十年的公司。</p>\n<p align=\"center\"><img alt=\"华尔街“用钱投票”：全球“最值得投资企业”大赛阿里巴巴夺冠\" src=\"upload/image/27d9bf00-9d46-4e58-8f36-3b16f005f910.jpg\"></p>\n<p class=\"pictext\" align=\"center\">(最终的投票页面显示，胜者是-----阿里巴巴)</p>\n<p>　　阿里巴巴一路淘汰诺华制药、迪斯尼、特斯拉和苹果，并在1月5日的最终投票轮中击败亚马逊赢得总决赛。</p>\n<p>　　此前，美国财政部刚刚否决阿里巴巴对美国转账平台速汇金的收购。相比美国政府对中国及中国公司的防范姿态，华尔街倒显得“身体相当诚实”，投资经理们对中国经济的看好，以及视阿里巴巴为中国经济代表的态度，通过总共六轮投票显露无疑。</p>\n<p>　　除阿里巴巴外，还有6家中国公司入围此次大赛，分别是小米，腾讯，今日头条，京东，百度和滴滴。其中，腾讯在八进四中败于亚马逊，百度在半决赛中败于老冤家谷歌，滴滴、小米在十六进八中分别败给优步、苹果，京东和今日头条则负于迪斯尼与Airbnb惨遭首轮出局。</p>\n<p>　　事实上，华尔街从未掩饰视阿里巴巴为全球最值得投资的公司的一贯态度。彭博社在2017年11月底的统计的数据显示，在华尔街的215个大型基金中，有三分之一的基金买入阿里巴巴的股票，年化回报率达到了33%，远高于市场平均水平。</p>\n<p>　　阿里巴巴不仅在主营电商业务上持续打破华尔街预期，更在科技和全球化领域加快布局，在全球范围内成为中国互联网经济后来居上的名片。</p>\n<p align=\"center\"><img alt=\"华尔街“用钱投票”：全球“最值得投资企业”大赛阿里巴巴夺冠\" src=\"upload/image/e7f8a0f4-1369-42f2-a8c6-044349ecd3bc.jpg\"></p>\n<p class=\"pictext\" align=\"center\">(决赛结果引发美国网友纷纷恭喜阿里巴巴。)</p>\n<p>　　自2016年10月启动“五新”战略以来，阿里巴巴引领的新零售变革，开辟出独具一格的中国零售新格局，并为解决全球零售业升级和消费问题提供了“中国方案”。</p>\n<p>　　在科技前沿探索领域，2017年10月阿里巴巴宣布两大前瞻性科研计划，包括3年内投入1000亿元成立“达摩院”，以及在全球7大城市设立实验室。</p>\n<p>　　更为重要的是，阿里巴巴及其领导者马云在全球化方面的努力，在过去一年得到了全世界的认可。马云的eWTP理念不仅在中国和马来西亚顺利落地，更受到WTO和世界经济论坛的拥抱和欢迎。2017年12月，在阿根廷举办的世界贸易组织部长级会议上，WTO发布联合声明鼓励所有成员拥抱eWTP，帮助小微企业全球化。</p>\n<p>　　过去两年来，包括《经济学人》、《巴伦周刊》、《纽约时报》、《金融时报》等在内的欧美主流媒体越来越多的关注以阿里巴巴为代表的中国互联网经济发展，甚至向美国硅谷巨头们发出警告：不要忽视中国力量！</p>\n<p align=\"center\"><img alt=\"华尔街“用钱投票”：全球“最值得投资企业”大赛阿里巴巴夺冠\" src=\"upload/image/a100449b-7898-49fe-90ba-3e688f93b627.jpg\"></p>\n<p>　　(2017年9月末出版的《经济学人》杂志，阿里巴巴被形容为中国创新领域的“王炸”)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/a100449b-7898-49fe-90ba-3e688f93b627.jpg',NULL,1099,'2018-01-05 15:01:00','2018-01-06 14:47:33','82',0),(3177,'甘肃钢笔画家创作《陇上腊月图》再现“陇上乡愁”','高辰','中国新闻网','<div style=\"text-align:center\"> \n <img alt=\"参加此次钢笔画展的18位艺术家平均年龄为60岁，他们自发组成一支“夕阳红”艺术家队伍，一同追忆往昔。　李治林 摄\" src=\"upload/image/c98e1c0f-16f9-4d8c-8574-6bb6af33f138.jpg\" title=\"参加此次钢笔画展的18位艺术家平均年龄为60岁，他们自发组成一支“夕阳红”艺术家队伍，一同追忆往昔。　李治林 摄\"> \n</div>\n<div class=\"pictext\" style=\"margin-top: 5px; margin-bottom: 5px; text-indent: 2em; text-align: left;\">\n  参加此次钢笔画展的18位艺术家平均年龄为60岁，他们自发组成一支“夕阳红”艺术家队伍，一同追忆往昔。　李治林 摄 \n</div>\n<p>　　<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>兰州1月6日电 (张婧)眼前的这幅《陇上腊月图》再现上世纪90年代甘肃各地农贸市场过年时热闹景象：卖糖葫芦、吹糖人、捏泥人、剪纸贺春、制作风车、卖炮竹、挑担人，还有骑着毛驴的赶集者……这幅作品在近日兰州市委宣传部主办的“淬钢漉墨――丝路风韵钢笔画展”展出。</p>\n<p>　　上世纪90年代，甘肃多地农贸集市人来人往、熙熙攘攘的场景是58岁的甘肃省美术家协会会员周新平创作钢笔画《陇上腊月图》的“灵感源泉”。他向<a target=\"_blank\" href=\"http://www.chinanews.com/\">中新网</a>记者讲述这幅作品的创作历程，“我很欣赏北宋画家张择端的存世精品《清明上河图》，这种对北宋城市经济情况的写照，给我很大的创作启发。”</p>\n<p>　　“画中共有258位各色人物。”周新平说，作品原形的发生地是甘肃陇西县通安镇集市。“当年这里民风淳朴，我在现场作了很多速写，拍了照片。”为达到人物神态各异的效果，周新平还专程到兰州的大坝早市、西部市场等集市找人物创造灵感。他说，在创作过程中借鉴了《清明上河图》的人物形象塑造理念，以及人物之间的组合关系，历时三个月完成了《陇上腊月图》。</p>\n<div style=\"text-align:center\"> \n <img alt=\"1月5日，“淬钢漉墨――丝路风韵钢笔画展”在甘肃兰州举办，甘肃甘肃省美术家协会会员周新平创作钢笔画《陇上腊月图》再现上世纪90年代农贸市场景象。　李治林　摄\" src=\"upload/image/a6f94454-7e5b-4129-b67d-2e2c4547becf.jpg\" style=\"border:px solid #000000\" title=\"1月5日，“淬钢漉墨――丝路风韵钢笔画展”在甘肃兰州举办，甘肃甘肃省美术家协会会员周新平创作钢笔画《陇上腊月图》再现上世纪90年代农贸市场景象。　李治林　摄\"> \n</div>\n<div class=\"pictext\" style=\"text-align: left; text-indent: 2em;\">\n  1月5日，“淬钢漉墨――丝路风韵钢笔画展”在甘肃兰州举办，甘肃甘肃省美术家协会会员周新平创作钢笔画《陇上腊月图》再现上世纪90年代农贸市场景象。　李治林　摄 \n</div>\n<p>　　周新平介绍，《陇上腊月图》是再现人们过去的集市生活，这种被现代超市慢慢取而代之的生活现象，对于老一辈人来说都是回忆和乡愁。</p>\n<p>　　家在陇西县通安镇的栾建军是周新平的学生。《陇上腊月图》就是以通安镇集市为主要背景构思创作。陇西县位于甘肃省东南部，地理位置重要是古丝绸之路和新亚欧大路桥的必经之地，自古以来商贾云集、集市繁荣。</p>\n<p>　　在陇西县长大的栾建军童年时光充满了对农贸集市生活的记忆，“每当新春将至，童年时的一幕幕浮现在我眼前。”栾建军说，那个年代的老百姓兜里揣上几块钱就可以像个“土豪”一样，底气十足地把集市上的小吃统统吃个遍。</p>\n<p>　　参加此次钢笔画展的18位艺术家平均年龄为60岁，他们自发组成一支“夕阳红”艺术家队伍，一同追忆往昔。参展作品还有国家一级美术师孙红创作的《乌镇水乡》、《写生平村》、甘肃省美术家协会会员胡蓉生的作品《天网》、《水车》等。胡蓉生说，“用画笔记录一些慢慢退出我们生活的东西，这都是我们的记忆。”(完)</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/a6f94454-7e5b-4129-b67d-2e2c4547becf.jpg',NULL,317,'2018-01-06 09:18:00','2018-01-06 14:47:33','75',0),(3178,'新版人民币即将发行？ 别再被这个视频忽悠了','左盛丹','央视新闻客户端','<p>　<strong>　新版人民币即将发行？别再被这个视频忽悠了！　</strong></p>\n<p>　　近日，网上流传一段视频，拍摄者拿出两叠崭新50元和100元钞票，称这是即将发行的“第六套人民币”。至于究竟什么时候发行，“还没有明确时间”。</p>\n<p>　　这是真的吗？</p>\n<p align=\"center\"><img src=\"upload/image/4a95a1f0-7049-42aa-b277-4e11ceceedc6.gif\"></p>\n<p align=\"center\"><img src=\"upload/image/6bfbcf03-7065-4763-ac80-664480224de8.gif\"></p>\n<p class=\"pictext\" align=\"center\">视频截图</p>\n<p>　　是纪念钞 不是“第六版人民币”！</p>\n<p>　　记者调查后发现，视频中的50元纸币其实是发行于1999年的庆祝中华人民共和国成立50周年的纪念钞，而100元纸币是发行于2015年的中国航天纪念钞。</p>\n<p>　　记者把网传视频播放速度放慢并定格，发现50元钞票上面写着“庆祝中华人民共和国成立50周年”的字样。 　　</p>\n<p>　　资料显示，中国人民银行在1999年曾面向全国发行“中华人民共和国成立五十周年”纪念钞，面额为50元。该纪念钞正面主景为开国大典盛况，背面为地球、和平鸽、铜狮、华表等组合图景，这是我国首次发行纪念钞。</p>\n<p align=\"center\"><img src=\"upload/image/36e0278a-fc62-49fd-bb1f-209817d2b49f.jpg\"></p>\n<p>　　网传视频的定格画面还显示，蓝色的100元纸币上印着卫星图案，正是中国人民银行于2015年11月26日发行的中国航天纪念钞，面额为100元。 　　</p>\n<p>　　该纪念钞主色调为蓝色，图案包括东方红一号卫星、神舟九号、天宫一号、嫦娥卫星等。</p>\n<p align=\"center\"><img src=\"upload/image/a240e97e-5d05-4e08-8b26-b079bbb9f171.jpg\"></p>\n<p>　　这两款纪念钞都与现行流通人民币职能相同，与同面额人民币等值流通。虽然从视频并不能判定这些纪念钞的真伪，但可以确定的一点是，这绝对不是所谓的“第六版人民币”。关于新一版人民币面世的谣言，此前也曾在网上出现过。</p>\n<p>　　人民币发行关系国计民生，大家一定要通过权威渠道获取相关信息，切勿轻信网络谣言！同时，对违法行为要及时举报，相关部门应追究其法律责任！</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\" style=\"padding-right:10px;\"> \n <tbody> \n  <tr> \n   <td> \n    <div id=\"adhzh\" name=\"hzh\"> \n     <!-- 广告位：画中画2 --> \n     <script>\r\n(function() {\r\n    var s = \"_\" + Math.random().toString(36).slice(2);\r\n    document.write(\'<div id=\"\' + s + \'\"></div>\');\r\n    (window.slotbydup=window.slotbydup || []).push({\r\n        id: \'2475336\',\r\n        container: s,\r\n        size: \'300,250\',\r\n        display: \'inlay-fix\'\r\n    });\r\n})();\r\n</script> \n     <script src=\"//dup.baidustatic.com/js/os.js\"></script> \n    </div> </td> \n  </tr> \n </tbody> \n</table>\n<div id=\"function_code_page\"></div>','upload/image/a240e97e-5d05-4e08-8b26-b079bbb9f171.jpg',NULL,1243,'2018-01-06 14:47:00','2018-01-06 14:47:54','66',0);

